  [111200201] = {
    id = 111200201,
    name = "一定是这里了，去看看！"
  },
  [111200202] = {
    id = 111200202,
    name = "莱娜小姐，走反了，往这边。"
  },
  [111200203] = {
    id = 111200203,
    name = "咳咳，我只是在观察有没有魔物追上来，看来一切安全，出发吧。"
  },
  [111200204] = {
    id = 111200204,
    name = "看来藏宝图所指的位置就是这里了。"
  },
  [111200205] = {
    id = 111200205,
    name = "宝物就在这里面吗？竟然是这么破旧的据点。"
  },
  [111200206] = {
    id = 111200206,
    name = "看样子，是魔物造成的。有的废弃据点，会成为魔物的新巢穴。"
  },
  [111200207] = {
    id = 111200207,
    name = "这里很安静啊，哪里有魔物？"
  },
  [111200208] = {
