  [111200234] = {
    id = 111200234,
    name = "你后面！"
  },
  [111200235] = {
    id = 111200235,
    name = "魔法余波未尽，头目级魔物痛苦地嘶吼着。"
  },
  [111200236] = {
    id = 111200236,
    name = "莱娜用力推开了你，提剑冲了上去给予了魔物最后一击。魔物凄厉地哀嚎出最后的吼叫，终于没了气息。"
  },
  [111200237] = {
    id = 111200237,
    name = "呼......头目级的魔物，也不过如此嘛。"
