  [111200256] = {
    id = 111200256,
    name = "少恭维我，这种脏活本小姐才不干！"
  },
  [111200257] = {
    id = 111200257,
    name = "所以就得我来做是吧？"
  },
  [111200258] = {
    id = 111200258,
    name = "哈欠~本小姐刚刚杀了头魔物，已经很累了。"
  },
  [111200259] = {
    id = 111200259,
    name = "（这尸体很诡异，还是暂时不要用手触碰比较好。）"
  },
  [111200260] = {
    id = 111200260,
    name = "喂~你看再久，线索也不会自己跑出来的。"
  },
  [111200261] = {
    id = 111200261,
    name = "......这种腐烂的程度，没有调查的必要。"
  },
  [111200262] = {
    id = 111200262,
    name = "呵呵，还以为是个靠谱的搭档，没想到连翻看尸体的勇气都没有。"
  },
  [111200263] = {
    id = 111200263,
    name = "莱娜小姐勇气可嘉，为什么不亲自查看呢？"
  },
  [111200264] = {
    id = 111200264,
    name = "我让利一成给你，你去翻尸体。这总行了吧？"
  },
  [111200265] = {
    id = 111200265,
    name = "只让一成？"
  },
  [111200266] = {
    id = 111200266,
    name = "那就再加一成！"
  },
  [111200267] = {
    id = 111200267,
    name = "不行，这个——"
  },
  [111200268] = {
    id = 111200268,
    name = "你别太贪心！"
  },
  [111200269] = {
    id = 111200269,
    name = "莱娜小姐误会了，我可没有说我会去翻尸体。"
  },
  [111200270] = {
    id = 111200270,
    name = "你耍我？！"
  },
  [111200271] = {id = 111200271, name = "嗤......"},
  [111200272] = {
    id = 111200272,
    name = "什么声音？"
  },
  [111200273] = {
    id = 111200273,
    name = "——快躲开！"
  },
  [111200274] = {
    id = 111200274,
    name = "你一把推开莱娜，同时抽剑挡下了一双腐烂的手！"
  },
  [111200275] = {
    id = 111200275,
    name = "那双手紧紧抓住了剑身，骨头上粘连的腐烂皮肉悬在半空摇摇晃晃，冲击着你的视觉。"
  },
  [111200276] = {
    id = 111200276,
    name = "堕落者？！"
  },
  [111200277] = {
    id = 111200277,
    name = "堕落者的身躯大部分已经腐烂，它直勾勾地看着你们，脸部溃烂呈青黑色。动起来的时候更加森冷、面容狰狞。"
  },
  [111200278] = {
    id = 111200278,
    name = "这个堕落者也太恶心了吧！整张皮都要脱落了！"
  },
  [111200279] = {
    id = 111200279,
    name = "堕落者被你一脚踹开，身上的血肉还在滴答滴答地往下淌，眼球也几乎快掉了。"
  },
  [111200280] = {
    id = 111200280,
    name = "本小姐......本小姐......有点不舒服......"
  },
  [111200281] = {
    id = 111200281,
    name = "看她秀气的脸皱成一团，你心一软。"
  },
  [111200282] = {
    id = 111200282,
    name = "莱娜小姐，你去旁边休息，我来应付。"
  },
  [111200283] = {
    id = 111200283,
    name = "不用，这种程度的堕落者......我，唔——"
  },
  [111200284] = {
    id = 111200284,
    name = "莱娜跑去一旁干呕，你迅速解决了濒死的堕落者。待它尸身消散后，掉下了另一张羊皮卷。"
  },
  [111200285] = {
    id = 111200285,
    name = "莱娜余光时不时瞟过来，看到堕落者彻底消失，才喘出了一口大气。"
  },
  [111200286] = {
    id = 111200286,
    name = "果然有线索。"
  },
  [111200287] = {
    id = 111200287,
    name = "给我看看！"
  },
  [111200288] = {
    id = 111200288,
    name = "莱娜小姐，此处标明了宝藏的位置，看来这里是真正的宝藏地。"
  },
  [111200289] = {
    id = 111200289,
    name = "太好了！"
  },
  [111200290] = {
    id = 111200290,
    name = "莱娜激动地一把扯过你手中的地图，指向了宝藏所在的方向。"
  },
  [111200291] = {
    id = 111200291,
    name = "莱娜秘宝探险组，出发！"
  },
  [111200292] = {
    id = 111200292,
    name = "到啦！就是这里！"
  },
  [111200293] = {
    id = 111200293,
    name = "你确定吗？"
  },
  [111200294] = {
    id = 111200294,
    name = "你在质疑本小姐的能力吗？"
  },
  [111200295] = {
    id = 111200295,
    name = "真的有人会把宝藏放在河边？"
  },
  [111200296] = {
    id = 111200296,
    name = "最容易发现的地方就是最安全的地方！正是因为这里有很多人往来，所以谁也想不到，这里会有顶级的宝藏。这就是埋下宝藏之人的高明之处！"
  },
  [111200297] = {
    id = 111200297,
    name = "你说的有道理，那我们在这附近找找看吧。"
  },
  [111200298] = {
    id = 111200298,
    name = "一段时间后......"
  },
  [111200299] = {
    id = 111200299,
    name = "到底藏在了哪里？"
  },
  [111200300] = {id = 111200300, name = "......"},
  [111200301] = {
    id = 111200301,
    name = "喂，为什么沉默？"
  },
  [111200302] = {
    id = 111200302,
    name = "啊，我在想，如果是我要放宝藏在这个地方，那一定......"
  },
  [111200303] = {
    id = 111200303,
    name = "藏在哪儿？"
  },
  [111200304] = {
    id = 111200304,
    name = "是个恶作剧。"
  },
  [111200305] = {id = 111200305, name = "什么？"},
  [111200306] = {
    id = 111200306,
    name = "我觉得很大的可能是，你被耍了。"
  },
  [111200307] = {id = 111200307, name = "......"},
  [111200308] = {
    id = 111200308,
    name = "也许从头到尾这个宝藏就不存在。"
  },
  [111200309] = {
    id = 111200309,
    name = "不可能。既然上一张图上标注了堕落者，我们又在堕落者身上找到了这个藏宝图，那么宝藏就一定存在！"
  },
  [111200310] = {
    id = 111200310,
    name = "......也对。如果只是为了骗你，没有必要特意标注那个堕落者，还给出这一张地图。"
  },
  [111200311] = {
    id = 111200311,
    name = "没错！宝藏一定就在这里，也许我们忽略了哪些地方。"
  },
  [111200312] = {
    id = 111200312,
    name = "嗯，那就再搜查一次吧。"
  },
  [111200313] = {
    id = 111200313,
    name = "又一段时间后......"
  },
  [111200314] = {
    id = 111200314,
    name = "为什么到处都找不到？难道有人捷足先登，提前拿走了宝物？那本小姐岂不是亏大了？！"
  },
  [111200315] = {
    id = 111200315,
    name = "我这边也没什么收获。"
  },
  [111200316] = {
    id = 111200316,
    name = "哼！我要去找他退钱！！"
  },
  [111200317] = {
    id = 111200317,
    name = "莱娜一气之下扔掉了地图，你叹口气，拾起来看了一眼。"
  },
  [111200318] = {
    id = 111200318,
    name = "......原来如此，也许宝藏还在。"
  },
  [111200319] = {
    id = 111200319,
    name = "不用安慰我。这次让你白跑一趟，跟我一起倒霉......真是抱歉了。"
  },
  [111200320] = {
    id = 111200320,
    name = "是真的，可能还在。莱娜小姐，你应该是走错了路。"
  },
  [111200321] = {
    id = 111200321,
    name = "你说什么？！"
  },
  [111200322] = {
    id = 111200322,
    name = "按照这张地图的指示，我们应该从相反的方向出发，目的地在一个山谷之中，而不是现在的......河道边。"
  },
  [111200323] = {id = 111200323, name = "......"},
  [111200324] = {id = 111200324, name = "......"},
  [111200325] = {id = 111200325, name = "......"},
  [111200326] = {
    id = 111200326,
    name = "啊，是吗，让本小姐再看看。"
  },
  [111200327] = {
    id = 111200327,
    name = "莱娜拿走地图，眼珠左右晃动，耳朵泛红。"
  },
  [111200328] = {
    id = 111200328,
    name = "嗯，是这个地图的误导性太强了，还好你发现了。"
  },
  [111200329] = {
    id = 111200329,
    name = "地图上标注的很清楚，莱娜小姐，是你带错路了。"
  },
  [111200330] = {id = 111200330, name = "......"},
  [111200331] = {
    id = 111200331,
    name = "说起来，之前在山洞也是。对了，你是不是方向感不太好？"
  },
  [111200332] = {
    id = 111200332,
    name = "怎、怎么可能！赶、赶快出发吧！"
  },
  [111200333] = {
    id = 111200333,
    name = "莱娜耳后的红蔓延到双颊，径自跑了。"
  },
  [111200334] = {
    id = 111200334,
    name = "莱娜小姐，走反了，是这边！"
  },
  [111200335] = {id = 111200335, name = "......"},
  [111200336] = {
    id = 111200336,
    name = "万仞绝壁间，你与莱娜沿着谷底河流旁的山路而上。山坳僻静幽暗，阳光也难以穿透这层层浊炁的雾霭。"
  },
  [111200337] = {
    id = 111200337,
    name = "等一下，前面是什么？！"
  },
  [111200338] = {
    id = 111200338,
    name = "（探险者？）"
  },
  [111200339] = {
    id = 111200339,
    name = "前方人影绰绰，你朝莱娜看了一眼。她使了个眼色，接着放慢脚步，尽量自然地和你并肩而行。"
  },
  [111200340] = {
    id = 111200340,
    name = "老大，在那边！"
  },
  [111200341] = {
    id = 111200341,
    name = "一阵脚步声响起，近十个探险者走出了雾霭，他们手中都拿着武器。"
  },
  [111200342] = {
    id = 111200342,
    name = "莱娜瞬间神经紧绷，手按紧了腰上的剑。你看到她的样子，拍了拍她握剑的手。"
  },
  [111200343] = {id = 111200343, name = "别怕。"},
  [111200344] = {
    id = 111200344,
    name = "莱娜像被突然烫到一般把手抽开。"
  },
  [111200345] = {
    id = 111200345,
    name = "谁......谁怕了！"
  },
  [111200346] = {
    id = 111200346,
    name = "这群人突然出来，我以为是魔物。"
  },
  [111200347] = {
    id = 111200347,
    name = "那群探险者看见了你们。领队的走了出来，他手一摆，身后的探险者们纷纷收起了武器。"
  },
  [111200348] = {
    id = 111200348,
    name = "这位小姐说的不错，野外听到异常的声响，提高警惕是理所应当的，尤其是在这种荒山野岭中。"
  },
  [111200349] = {
    id = 111200349,
    name = "不知道两位来这里做什么？"
  },
  [111200350] = {
    id = 111200350,
    name = "先说说你们来这里干嘛的？"
  },
  [111200351] = {
    id = 111200351,
    name = "这条山坳连通两边的据点，我们经常从这路过，有时也会碰见危险的魔物，不怪兄弟们多少都有点紧张。"
  },
  [111200352] = {
    id = 111200352,
    name = "听到那群探险者只是路过，莱娜的紧绷感明显松懈了下来。"
  },
  [111200353] = {
    id = 111200353,
    name = "没错，这里的雾气真是太大了，对面是人是魔物都看不清了。"
  },
  [111200354] = {
    id = 111200354,
    name = "那么两位到这里来，是因为知道去山那边据点的近路吗？"
  },
  [111200355] = {
    id = 111200355,
    name = "我们——"
  },
  [111200356] = {
    id = 111200356,
    name = "我早就说了，这雾蒙蒙的山谷，根本没有那种美景，他们一定是骗你的。"
  },
  [111200357] = {id = 111200357, name = "啊？"},
  [111200358] = {
    id = 111200358,
    name = "噢~我才不信，我就要去！那什么的景色，我一定要亲自见到！"
  },
  [111200359] = {
    id = 111200359,
    name = "老大，他们说的什么美景，咱们怎么没看到过？"
  },
  [111200360] = {
    id = 111200360,
    name = "人家小情侣的情趣，你懂什么。"
  },
  [111200361] = {id = 111200361, name = "......"},
  [111200362] = {
    id = 111200362,
    name = "那就快走吧。真不知道这些奇奇怪怪的风景有什么好看的~"
  },
  [111200363] = {
    id = 111200363,
    name = "哼，你不愿意我就自己去！"
  },
  [111200364] = {
    id = 111200364,
    name = "莱娜朝你使了个眼色，一头往山道上走去。可道路狭窄，那几人却站在原地阻拦着一动也不动。"
  },
  [111200365] = {
    id = 111200365,
    name = "莱娜预感不妙，回头看了你一眼，你快步走到她身边。"
  },
  [111200366] = {
    id = 111200366,
    name = "几位，麻烦让个路吧。"
  },
  [111200367] = {
    id = 111200367,
    name = "不着急，既然来了就别那么快走，大家相逢一场，也算是有缘分。"
  },
  [111200368] = {
    id = 111200368,
    name = "我看你们也很着急的样子，就不耽误你们赶路了，有缘自然会再见。"
  },
  [111200369] = {
    id = 111200369,
    name = "有些事其实是能坐下来谈一谈的，比如你要是肯把你的东西和女人都留下，我可以考虑放你一条生路。"
  },
  [111200370] = {
    id = 111200370,
    name = "大言不惭！你干什么——"
  },
  [111200371] = {
    id = 111200371,
    name = "见莱娜神色激动，你一把抓住了她抓魔法道具的手，突然将她虚环在一边，悄声地说了一句话。"
  },
  [111200372] = {
    id = 111200372,
    name = "（小声）别慌，听我安排，一起杀出去。"
  },
  [111200373] = {
    id = 111200373,
    name = "在她失神的一瞬间，你毫无声息地夺走了她手上的魔法道具，莱娜瞪大眼睛，看着你接下来的表演。"
  },
  [111200374] = {
    id = 111200374,
    name = "这位领队，实不相瞒，我们其实是得到了消息，这山里有宝物要诞生了。"
  },
  [111200375] = {
    id = 111200375,
    name = "宝物？老大！"
  },
  [111200376] = {
    id = 111200376,
    name = "哦？什么意思？"
  },
  [111200377] = {
    id = 111200377,
    name = "如果你肯放我们离开，我就告诉你们宝物在哪儿，否则，这个老师留下的宝物，据说有炸掉一个据点的威力。"
  },
  [111200378] = {id = 111200378, name = "......"},
  [111200379] = {
    id = 111200379,
    name = "（小声）你在胡说些什么？这就是普通的烟雾魔法弹而已。"
  },
  [111200380] = {
    id = 111200380,
    name = "小娜，你害怕吗？"
  },
  [111200381] = {
    id = 111200381,
    name = "小......小......怕什么！本姑娘才不会害怕！"
  },
  [111200382] = {
    id = 111200382,
    name = "好，能和你一起葬在一起，就是我毕生的愿望。"
  },
  [111200383] = {
    id = 111200383,
    name = "（小声）哈？"
  },
  [111200384] = {
    id = 111200384,
    name = "你高高地举起了手，探险者们没有看到莱娜的白眼，纷纷朝后退了几步，撤出了一个口子。"
  },
  [111200385] = {
    id = 111200385,
    name = "你，你不要乱来啊！"
  },
  [111200386] = {
    id = 111200386,
    name = "等等！我答应你！告诉我们地方，放你们走！"
  },
  [111200387] = {
    id = 111200387,
    name = "（小声）趁现在，动手！"
  },
  [111200388] = {
    id = 111200388,
    name = "轰！！莱娜反应了过来，在烟雾弹炸起的一瞬间，和你拔剑冲杀出去。"
  },
  [111200389] = {
    id = 111200389,
    name = "妈的碰到了疯子，先后退！"
  },
  [111200390] = {
    id = 111200390,
    name = "魔法烟雾加上天然的雾气，探险者们分不清敌我，只能在混乱中找人。你和莱娜一路扫清了周围的障碍，将那些喽啰踢回了狭窄的山道上。"
  },
  [111200391] = {
    id = 111200391,
    name = "被骗了！根本不是什么魔法炸弹！"
  },
  [111200392] = {
    id = 111200392,
    name = "哎哟，老大是我，你打错人啦......"
  },
  [111200393] = {id = 111200393, name = "走！"},
  [111200394] = {id = 111200394, name = "......"},
  [111200395] = {
    id = 111200395,
    name = "逃出重围后，你拉着莱娜一直奔跑，直到身后的声音彻底消失。"
  },
  [111200396] = {
    id = 111200396,
    name = "这里应该安全了。"
  },
  [111200397] = {
    id = 111200397,
    name = "你放开了她的手，发现莱娜的脸颊微红。"
  },
  [111200398] = {
    id = 111200398,
    name = "累了吗，那就休息一下吧。"
  },
  [111200399] = {
    id = 111200399,
    name = "才、才不累。几个小毛贼，怎么不让我好好教训他们！"
  },
  [111200400] = {
    id = 111200400,
    name = "他们人多势众，又熟悉这里的地形，说不定在这里专门抢劫过路人。这座山坳雾气浓郁，正好为他们作掩护，如果真的打起来，也许还不止这些人。"
  },
  [111200401] = {
    id = 111200401,
    name = "竟然是个贼窝！迟早解决掉他们，竟然敢觊觎本小姐！"
  },
  [111200402] = {
    id = 111200402,
    name = "对了，还有你！......小娜，谁允许你这么叫的？"
  },
  [111200403] = {
    id = 111200403,
    name = "莱娜小姐，这只是为了骗过他们......"
  },
  [111200404] = {
    id = 111200404,
    name = "哼，那你找的那个理由呢？什么景色，什么宝物，也是骗他们的？"
  },
  [111200405] = {
    id = 111200405,
    name = "宝物是我瞎编的，至于景色......"
  },
  [111200406] = {
    id = 111200406,
    name = "一般雾气缭绕的山里，在山顶上就能看到被簇拥的云海。如果这时有阳光从阴云里照射下来，就会像金色的魔法液倾泻进云雾里，形成天光万顷的绝美景象。"
  },
  [111200407] = {
    id = 111200407,
    name = "但我们这次不去山顶，自然是看不见的。"
  },
  [111200408] = {
    id = 111200408,
    name = "哼，谁问你这个......满口胡言乱语。"
  },
  [111200409] = {
    id = 111200409,
    name = "莱娜嘀咕了一句，视线望着山顶，像是期待着什么。"
  },
  [111200410] = {
    id = 111200410,
    name = "莱娜小姐，我们该出发了，以后有机会的话，可以看到的。"
  },
  [111200411] = {
    id = 111200411,
    name = "谁稀罕看呀，走啦！"
  },
  [111200415] = {
    id = 111200415,
    name = "地图的终点指向了山坳的低谷处，于是你和莱娜来到了谷底。"
  },
  [111200416] = {
    id = 111200416,
    name = "谷底极其荒凉，有一条杂草丛生的路，两旁是茂密的树林。路的尽头是一座废弃的祭台，祭台上趴着一只正在沉睡的魔物，莱娜看见立刻兴奋了起来。"
  },
  [111200417] = {
    id = 111200417,
    name = "小心，前面的魔物是头目级。"
  },
  [111200418] = {
    id = 111200418,
    name = "到了到了，没错，一定是这里！"
  },
  [111200419] = {
    id = 111200419,
    name = "神秘的痕迹，废弃的祭台，看门的魔物，还有令人心跳的直觉......这些都在告诉我，宝藏就在前面！"
  },
  [111200420] = {
    id = 111200420,
    name = "眼前的目标，是要解决掉这只头目级魔物。"
  },
  [111200421] = {
    id = 111200421,
    name = "交给我吧！宝藏，我来了！"
  },
  [111200422] = {
    id = 111200422,
    name = "眼看莱娜又要冲上去，你急忙拉住了她。"
  },
  [111200423] = {
    id = 111200423,
    name = "又怎么了？难道又要用你的什么作战计划？这种魔物对我们来说不是轻而易举？"
  },
  [111200424] = {
    id = 111200424,
    name = "......总觉得没这么简单。我先去勘察一下周围的情况，你等一下。"
  },
  [111200425] = {
    id = 111200425,
    name = "知道啦，麻烦。"
  },
  [111200426] = {
    id = 111200426,
    name = "一段时间后......"
  },
  [111200427] = {
    id = 111200427,
    name = "我回来了。"
  },
  [111200428] = {
    id = 111200428,
    name = "有什么发现吗？"
  },
  [111200429] = {
    id = 111200429,
    name = "没有，这里就只有这一只魔物，但这更让人疑心了。"
  },
  [111200430] = {
    id = 111200430,
    name = "什么都怀疑，你怎么不怀疑这里没有宝藏呢？"
  },
  [111200431] = {
    id = 111200431,
    name = "你说有，作为你的搭档，我当然相信你。"
  },
  [111200432] = {
    id = 111200432,
    name = "是临时搭档~哼，这下可以上了吧？"
  },
  [111200433] = {
    id = 111200433,
    name = "嗯，我们先杀掉这只魔物，再找宝藏。"
  },
  [111200434] = {
    id = 111200434,
    name = "那么，看我的！"
  },
  [111200435] = {
    id = 111200435,
    name = "莱娜再次拿出了一块魔法道具，在你面前得意地炫耀了一番，随后直接朝魔物的脑袋上砸了过去！"
  },
  [111200436] = {id = 111200436, name = "......"},
  [111200437] = {
    id = 111200437,
    name = "魔物被击倒后，莱娜绕着魔物的尸体走了好几圈。"
  },
  [111200438] = {
    id = 111200438,
    name = "宝藏到底在哪儿呢？难道在魔物肚子里？"
  },
  [111200439] = {
    id = 111200439,
    name = "被魔物吃进去了吗？"
  },
  [111200440] = {
    id = 111200440,
    name = "有可能，到处都找不到......你快把它剖开看看！"
  },
  [111200441] = {id = 111200441, name = "......"},
  [111200442] = {
    id = 111200442,
    name = "我给你分成，快点快点！"
  },
  [111200443] = {id = 111200443, name = "......"},
  [111200444] = {
    id = 111200444,
    name = "一段时间后......"
  },
  [111200445] = {
    id = 111200445,
    name = "你拿着血淋淋的刀，搜肠刮肚地找了好一阵，魔物的血淌了一地，顺着祭台上的纹路蔓延。"
  },
  [111200446] = {
    id = 111200446,
    name = "这里也没有，不可能啊，难道忽略了什么？"
  },
  [111200447] = {
    id = 111200447,
    name = "你擦拭着武器上的血，这时，祭台上有模糊的光亮起。"
  },
  [111200448] = {
    id = 111200448,
    name = "莱娜小姐，快看！"
  },
  [111200449] = {
    id = 111200449,
    name = "祭台上的纹路散发着微光，点亮了四周的石柱，整个祭台瞬间被一层薄光笼罩。"
  },
  [111200450] = {
    id = 111200450,
    name = "我就知道！宝藏一定在这里！"
  },
  [111200451] = {
    id = 111200451,
    name = "莱娜还没来得及激动，就发现魔物的身躯血肉正逐渐被祭台吞噬。"
  },
  [111200452] = {
    id = 111200452,
    name = "那个...要不我们还是出去等宝藏出现吧。"
  },
  [111200453] = {
    id = 111200453,
    name = "嗯，我也觉得有些不对劲。"
  },
  [111200454] = {
    id = 111200454,
    name = "走下祭台的时候，你们触碰到了一层光幕，那层光幕将你们困在了祭台上！"
  },
  [111200455] = {
    id = 111200455,
    name = "怎么回事？！！"
  },
  [111200456] = {
    id = 111200456,
    name = "是魔法阵！魔物的血开启了魔法阵，我们被当成了祭品！"
  },
  [111200457] = {
    id = 111200457,
    name = "怎么会？！"
  },
  [111200458] = {
    id = 111200458,
    name = "你想尽办法也没能打开那层光幕，此时魔物的血肉已经几乎被吞噬殆尽。"
  },
  [111200459] = {
    id = 111200459,
    name = "你感觉无尽的寒意从脚下升起，似乎有无数的丝线缠绕着你的身体，不断吸取你的生命力。意识正在被抽空，整个身体被束缚，仿佛胸腔快要停止跳动。"
  },
  [111200460] = {
    id = 111200460,
    name = "莱娜痛苦地皱着眉，跌跌撞撞地四处摸索，尝试着抓住点什么。那一瞬间，你艰难地抓住了她的手。"
  },
  [111200461] = {
    id = 111200461,
    name = "（如果再不做点什么，我和她都会死在这里吧？）"
  },
  [111200462] = {
    id = 111200462,
    name = "正当你思考的时候，莱娜猛然睁开了眼睛、坚决而明亮。"
  },
  [111200463] = {
    id = 111200463,
    name = "她用力地咬破嘴角，恢复了一些意识。接着拿出一样掌心大小的宝物，狠狠朝光幕砸了过去！"
  },
  [111200464] = {
    id = 111200464,
    name = "宝物与光幕相撞，圣光瞬间溢满了山谷。巨大的魔法震波将你们冲击到了祭台外部。祭台被炸得四分五裂，碎石满地，所有的魔法和吞噬的光都消散了。"
  },
  [111200465] = {
    id = 111200465,
    name = "......呼......呼......什么破宝藏，气死我了！"
  },
  [111200466] = {
    id = 111200466,
    name = "好一会儿，莱娜才缓过劲。"
  },
  [111200467] = {
    id = 111200467,
    name = "呵呵，是啊。"
  },
  [111200468] = {
    id = 111200468,
    name = "找了这么久的宝藏，竟然是一个献祭的祭台。莱娜小姐，到底是谁告诉你这个宝藏的？"
  },
  [111200469] = {
    id = 111200469,
    name = "是一个黑衣人。"
  },
  [111200470] = {
    id = 111200470,
    name = "黑衣人？"
  },
  [111200471] = {
    id = 111200471,
    name = "你想起之前也碰见过一个黑衣人，就把这件事告诉了莱娜。"
  },
  [111200472] = {
    id = 111200472,
    name = "没错！！！就是他！！"
  },
  [111200473] = {
    id = 111200473,
    name = "他看我一个人，悄悄地找到我，问我要不要买消息，我还花了一大笔钱！"
  },
  [111200474] = {id = 111200474, name = "......"},
  [111200475] = {
    id = 111200475,
    name = "哼，鬼鬼祟祟，一定不是什么好东西！！"
  },
  [111200476] = {
    id = 111200476,
    name = "（先前说他的宝藏一定没问题的也是你啊......）"
  },
  [111200477] = {
    id = 111200477,
    name = "骗子！气死我了！"
  },
  [111200478] = {
    id = 111200478,
    name = "不仅没有找到宝藏，还将自己的宝物也赔了进去，你非常能理解莱娜的愤怒。"
  },
  [111200479] = {
    id = 111200479,
    name = "轰隆隆——"
  },
  [111200480] = {
    id = 111200480,
    name = "什么声音！！"
  },
  [111200481] = {
    id = 111200481,
    name = "糟了，这个山谷要塌了！！"
  },
  [111200482] = {
    id = 111200482,
    name = "碎石簌簌地滚落，你拉着莱娜一路狂奔，她不时地回头张望。"
  },
  [111200483] = {
    id = 111200483,
    name = "我的宝物竟然这么厉害的吗？！"
  },
  [111200484] = {
    id = 111200484,
    name = "对，很厉害！！先逃离这里再说！"
  },
  [111200485] = {
    id = 111200485,
    name = "总算逃出来了。"
  },
  [111200486] = {
    id = 111200486,
    name = "莱娜喘着大气，侧头看了你一眼。忽然，她闷声笑了起来，脏兮兮的脸上，劫后余生的笑显得格外动人。"
  },
  [111200487] = {
    id = 111200487,
    name = "哈哈哈哈......你的脸......哈哈哈......好丑......"
  },
  [111200488] = {
    id = 111200488,
    name = "你跟着笑了起来，摸出一张手帕递给莱娜。"
  },
  [111200489] = {
    id = 111200489,
    name = "擦一擦吧。"
  },
  [111200490] = {
    id = 111200490,
    name = "莱娜的笑容戛然而止。"
  },
  [111200491] = {id = 111200491, name = "哼。"},
  [111200492] = {
    id = 111200492,
    name = "她一把扯过手帕，转身到一边擦拭脸颊。"
  },
  [111200493] = {
    id = 111200493,
    name = "咳咳，既然宝藏没有了，我们之间的合作协议也作废了。"
  },
  [111200494] = {id = 111200494, name = "是吗。"},
  [111200495] = {
    id = 111200495,
    name = "别伤心，你也没有白跑这一趟啊。"
  },
  [111200496] = {
    id = 111200496,
    name = "和本小姐一起历练的经验可是很宝贵的。"
  },
  [111200497] = {
    id = 111200497,
    name = "莱娜擦完了脸，将手帕扔回给你。"
  },
  [111200498] = {
    id = 111200498,
    name = "不过，你这个临时搭档还不错，下次等我有了线索再叫你吧。现在我要去找到那个黑衣人，然后好好地给他一个教训！"
  },
  [111200499] = {
    id = 111200499,
    name = "哼，再会了。"
  },
  [111200500] = {
    id = 111200500,
    name = "好啊，下次再一起寻宝。"
  },
  [111200501] = {
    id = 111200501,
    name = "莱娜看着你的微笑愣了一下。"
  },
  [111200502] = {
    id = 111200502,
    name = "接着，她眉毛一抬，转过身，头也不回地离开了。"
  },
  [111270001] = {
    id = 111270001,
    name = "行至一处山谷风口，风夹杂着潮湿的味道向你袭来。根据地图所示，最近的圈落就在风口前方的不远处，你深吸一口气继续前行。"
  },
  [111270002] = {
    id = 111270002,
    name = "把圈落建造在风谷外，就能构成天然的屏障。看来这座圈落相对安全，终于可以睡个好觉了。"
  },
  [111270003] = {
    id = 111270003,
    name = "连续几日的幕天席地，加上时刻警惕着魔物来袭，此时你紧绷的神经有所缓解，随即有了些困意。"
  },
  [111270004] = {
    id = 111270004,
    name = "救......救......"
  },
  [111270005] = {
    id = 111270005,
    name = "风中传来细碎的呼救声。"
  },
  [111270006] = {
    id = 111270006,
    name = "似乎是从前方传来的？"
  },
  [111270007] = {
    id = 111270007,
    name = "突然，前方的草丛中伸出一只颤巍巍的手，艰难地爬行着。那是一个浑身是伤的男人，看到你的时候，他眼中迸发出希望的光。"
  },
  [111270008] = {
    id = 111270008,
    name = "救我......拜托了......"
  },
  [111270009] = {
    id = 111270009,
    name = "（放着不管真的会死吧。）"
  },
  [111270010] = {
    id = 111270010,
    name = "你将他扶起靠在山壁旁，给了他一个水袋，他接过灌下了好几口。"
  },
  [111270011] = {
    id = 111270011,
    name = "谢......谢......"
  },
  [111270012] = {
    id = 111270012,
    name = "在你递食物的时候，他谢绝了。"
  },
  [111270013] = {
    id = 111270013,
    name = "这个不用。"
  },
  [111270014] = {
    id = 111270014,
    name = "可以的话，能给我一些止疼的药剂吗？"
  },
  [111270015] = {
    id = 111270015,
    name = "他在衣服里搜寻了许久，终于找到了一块欧泊递给了你。"
  },
  [111270016] = {
    id = 111270016,
    name = "获得欧泊*1"
  },
  [111270017] = {
    id = 111270017,
    name = "只靠药剂没用，还是尽快去圈落治疗吧。"
  },
  [111270018] = {
    id = 111270018,
    name = "止疼药剂是每个探险者常备的物品，你将药剂递给他，也没有拒绝那块欧泊。"
  },
  [111270019] = {
    id = 111270019,
    name = "我......我的左腿受了伤，暂时无法行走了。我在圈落有居所，能不能麻烦您......送我去......我一定，一定有重谢。"
  },
  [111270020] = {
    id = 111270020,
    name = "（这个人来历不明，如果惹上什么麻烦的话......）"
  },
  [111270021] = {
    id = 111270021,
    name = "可以先回答我一个问题吗，你这一身的伤是怎么回事？"
  },
  [111270022] = {
    id = 111270022,
    name = "我被一群魔物追赶——"
  },
  [111270023] = {
    id = 111270023,
    name = "不想说的话，就不用说了。"
  },
  [111270024] = {id = 111270024, name = "！！！"},
  [111270025] = {
    id = 111270025,
    name = "您......看出来了吗？"
  },
  [111270026] = {
    id = 111270026,
    name = "这么明显的人为伤痕，谁都能看得出来。"
  },
  [111270027] = {
    id = 111270027,
    name = "请放心，我已经逃出来了，他们都以为我死了，不会给您带来麻烦的。"
  },
  [111270028] = {
    id = 111270028,
    name = "逃出来？"
  },
  [111270029] = {
    id = 111270029,
    name = "是的。其实，我是地下决斗场的人。"
  },
  [111270030] = {
    id = 111270030,
    name = "原来如此，你是惹到了什么人？"
  },
  [111270031] = {
    id = 111270031,
    name = "你听说过地下决斗场。那是特殊的竞技场，举办者通常会拿出高额的奖励来吸引参与的比斗者，听说那种比斗还伴随着各种形式的赌博，有很多人愿意花钱观赛和报名参赛。"
  },
  [111270032] = {
    id = 111270032,
    name = "不，我只是比斗输了，他们想抓我回去继续比，我不愿意，才逃出来的。"
  },
  [111270033] = {
    id = 111270033,
    name = "还有这种规矩？"
  },
  [111270034] = {
    id = 111270034,
    name = "你看着他欲言又止的神色，叹了口气。"
  },
  [111270035] = {
    id = 111270035,
    name = "圈落离这里不远，我带你过去吧。"
  },
  [111270036] = {
    id = 111270036,
    name = "他露出欣喜又感激的表情。"
  },
  [111270037] = {
    id = 111270037,
    name = "谢谢，谢谢您！"
  },
  [111270038] = {
    id = 111270038,
    name = "根据伤者的叙述，你将他送到了圈落的居所。"
  },
  [111270039] = {
    id = 111270039,
    name = "他对你再三感谢，并拿出了这一次比斗的所有奖金，你没推辞他的谢意。"
  },
  [111270040] = {
    id = 111270040,
    name = "你收下了他的谢礼。"
  },
  [111270041] = {
    id = 111270041,
    name = "看他腿脚不便的样子，你将食物留给了他。离开伤者的住所后，你看到身着公会服饰的人朝你走来。"
  },
  [111270042] = {
    id = 111270042,
    name = "哎呀呀，好久不见呢。"
  },
  [111270043] = {
    id = 111270043,
    name = "你认识我？"
  },
  [111270044] = {
    id = 111270044,
    name = "呵呵，以您的知名度，公会的人怎会不认识呢？一直以来，您相助公会良多，公会一直铭记于心。"
  },
  [111270045] = {
    id = 111270045,
    name = "哦，这一次又有什么麻烦？"
  },
  [111270046] = {
    id = 111270046,
    name = "啊，真是什么都瞒不过您呢。"
  },
  [111270047] = {
    id = 111270047,
    name = "是这样的，我们发现了一个奇怪的地方，需要调查一些事，可是怎么也调查不出个结果......"
  },
  [111270048] = {
    id = 111270048,
    name = "是吗，真是辛苦你们了。"
  },
  [111270049] = {
    id = 111270049,
    name = "不辛苦，这也是为了给探险者们提供相对安全的旅程。"
  },
  [111270050] = {
    id = 111270050,
    name = "好的，那你继续加油吧，我走了。"
  },
  [111270051] = {
    id = 111270051,
    name = "......诶？那个，请等一下！"
  },
  [111270052] = {
    id = 111270052,
    name = "嗯？有什么事吗？"
  },
  [111270053] = {
    id = 111270053,
    name = "那件事，想请您帮忙......"
  },
  [111270054] = {
    id = 111270054,
    name = "就是你说的调查的事吗？"
  },
  [111270055] = {
    id = 111270055,
    name = "对对对，是关于地下决斗场的事。"
  },
  [111270056] = {
    id = 111270056,
    name = "地下决斗场？出了什么问题？"
  },
  [111270057] = {
    id = 111270057,
    name = "先前有流言说地下决斗场出现了堕落者，公会现在一直在调查这件事，但一直没有确凿的证据......"
  },
  [111270058] = {
    id = 111270058,
    name = "暗中调查这种事，公会应该很熟练了吧。"
  },
  [111270059] = {
    id = 111270059,
    name = "是这样没错，可是此次目标是地下决斗场。他们好像知道我们在调查什么，公会成员进去之后都被识破了身份。"
  },
  [111270060] = {
    id = 111270060,
    name = "他们没有见过您，一定不会特意提防你的。"
  },
  [111270061] = {
    id = 111270061,
    name = "哦，那你们找一个生面孔去就好啦。"
  },
  [111270062] = {id = 111270062, name = "这......"},
  [111270063] = {
    id = 111270063,
    name = "我相信以公会的财力，想帮忙的探险者要多少有多少。"
  },
  [111270064] = {
    id = 111270064,
    name = "可是，此事事关堕落者，有些调查需要秘密进行。您是公会能够信任的人，除您以外没有更合适的人选了。"
  },
  [111270065] = {
    id = 111270065,
    name = "所以这一次拜托了！"
  },
  [111270066] = {
    id = 111270066,
    name = "看他诚挚又忐忑的样子，你决定不再逗他。"
  },
  [111270067] = {
    id = 111270067,
    name = "......有堕落者的地下决斗场，到底是个什么地方，我确实有点兴趣。看在你这么诚心的份上，我就去看看吧。不过——"
  },
  [111270068] = {
    id = 111270068,
    name = "您请放心，一路上的花销最后都由公会结算，至于报酬方面......您看老价钱怎么样？"
  },
  [111270069] = {
    id = 111270069,
    name = "成交，查到结果我再告诉你。"
  },
  [111270070] = {
    id = 111270070,
    name = "那就静候佳音了。"
  },
  [111270071] = {
    id = 111270071,
    name = "他感激涕零地离开了。"
  },
  [111270072] = {
    id = 111270072,
    name = "啊......忘了问在哪儿。地下决斗场的话，那个人肯定知道一些情报吧。"
  },
  [111270073] = {
    id = 111270073,
    name = "你回头走向了伤者的居所。你敲了好几声的门，屋里都没有动静。"
  },
  [111270074] = {
    id = 111270074,
    name = "（他的脚受伤了，应该还在家才对。）"
  },
  [111270075] = {id = 111270075, name = "是我。"},
  [111270076] = {
    id = 111270076,
    name = "在你说话后，里面果然传出了人声。"
  },
  [111270077] = {
    id = 111270077,
    name = "是恩人先生吗？"
  },
  [111270078] = {
    id = 111270078,
    name = "嗯，放心吧，只有我一个人，想跟你打听一些事。"
  },
  [111270079] = {
    id = 111270079,
    name = "好，好的，请稍等。"
  },
  [111270080] = {
    id = 111270080,
    name = "里面传出了窸窸窣窣的声音，隔了好一阵门打开了，他露出歉意的笑让你进了屋。"
  },
  [111270081] = {
    id = 111270081,
    name = "这么谨慎，是怕地下决斗场的人找到你吗？"
  },
  [111270082] = {
    id = 111270082,
    name = "......你猜到了啊。"
  },
  [111270083] = {
    id = 111270083,
    name = "嗯，我也是想来打听一些情报。关于地下决斗场，你知道多少？"
  },
  [111270084] = {
    id = 111270084,
    name = "你想去地下决斗场？"
  },
  [111270085] = {
    id = 111270085,
    name = "嗯，听你说起来很有意思，没去过，想去看看。"
  },
  [111270086] = {
    id = 111270086,
    name = "这样啊，恩人先生知道决斗吗？"
  },
  [111270087] = {
    id = 111270087,
    name = "和大陆的斗技场差不多吧？有私怨的挑战者进行一对一比斗，胜者会得到败者的全部赌注。"
  },
  [111270088] = {
    id = 111270088,
    name = "和这个差不多，不过地下决斗场有一些特别的规矩。"
  },
  [111270089] = {
    id = 111270089,
    name = "比如上了决斗台的人生死自负，直到一方认输为止。如果没人认输，不小心将人打死的话......也不用负责的。甚至，还可以获得决斗场高额的奖金。"
  },
  [111270090] = {
    id = 111270090,
    name = "唔，听起来很危险啊。"
  },
  [111270091] = {
    id = 111270091,
    name = "我也是听人说那里有得赚才去的，打一场下来够我几天的吃喝了，没想到......后来遇到个厉害的对手，差点就下不来了。"
  },
  [111270092] = {
    id = 111270092,
    name = "既然认输就没事，那你为什么要逃跑？"
  },
  [111270093] = {
    id = 111270093,
    name = "我本来打算走了，可他们把我和一群人关在了一个屋子里，里面全是之前输掉决斗的人。我以为他们离开了决斗场，没想到是被关起来了！"
  },
  [111270094] = {
    id = 111270094,
    name = "关起来？"
  },
  [111270095] = {id = 111270095, name = "没错。"},
  [111270096] = {
    id = 111270096,
    name = "决斗场的人会拿药剂治好他们的伤，然后让他们继续出去决斗，直到被打死，就像......对，就像斗兽场里的野兽一样！"
  },
  [111270097] = {
    id = 111270097,
    name = "我不想变成那样！于是我假装喝下药剂，然后逃出了决斗场。我被他们一路追杀，最后逃到了森林里，他们以为我被魔物杀掉了，我才得以脱身......"
  },
  [111270098] = {
    id = 111270098,
    name = "看来这个决斗场的秘密有很多啊。"
  },
  [111270099] = {
    id = 111270099,
    name = "是啊，你如果想去的话，去看看就好了。"
  },
  [111270100] = {
    id = 111270100,
    name = "千万不要像我一样一时兴起成为参赛者，输了什么都会赔光，只有诈死才得以摆脱。"
  },
  [111270101] = {
    id = 111270101,
    name = "嗯，谢谢提醒。"
  },
  [111270102] = {
    id = 111270102,
    name = "伤者看你兴趣并不浓厚，放心地点了点头。"
  },
  [111270103] = {
    id = 111270103,
    name = "对了，决斗场晚上才开放，你可以在我这里暂时休息。"
  },
  [111270104] = {
    id = 111270104,
    name = "那就打扰了。"
  },
  [111270105] = {
    id = 111270105,
    name = "伤者高兴地挪到厨房台前弄得丁零当啷，你继续和他有一句没一句地聊着，顺便得知了地下决斗场的具体地址。"
  },
  [111270106] = {
    id = 111270106,
    name = "几个小时后......"
  },
  [111270107] = {
    id = 111270107,
    name = "谢谢你的招待，我走了。"
  },
  [111270108] = {
    id = 111270108,
    name = "请一定保重！"
  },
  [111270109] = {id = 111270109, name = "站住。"},
  [111270110] = {
    id = 111270110,
    name = "根据伤者说的地点，你来到了圈落附近的一间小屋。可正要进去就被守卫拦住了，他看你一脸无辜的样子，照例盘问。"
  },
  [111270111] = {
    id = 111270111,
    name = "干什么的？"
  },
  [111270112] = {
    id = 111270112,
    name = "这位大哥，听说这里有比赛，想来见识一下。"
  },
  [111270113] = {
    id = 111270113,
    name = "这个门是给参赛者准备的。"
  },
  [111270114] = {
    id = 111270114,
    name = "想观赛去那边买票。"
  },
  [111270115] = {
    id = 111270115,
    name = "哦哦好的。"
  },
  [111270116] = {
    id = 111270116,
    name = "守门人百无聊赖地指了指一边，你朝他指的方向走去，看到门口同样有一个守门人。在交了足够欧泊的“观赏费”后，你进入了小屋。"
  },
  [111270117] = {
    id = 111270117,
    name = "小屋内并没有比赛场地，而是一个壮汉走了过来，你不由地提高警惕。"
  },
  [111270118] = {
    id = 111270118,
    name = "啧，弱鸡。把你的武器拿出来，决斗场禁止带入。"
  },
  [111270119] = {
    id = 111270119,
    name = "幸好伤者提醒过你，你早早做好了准备，你拿出一把极为普通的低级武器，壮汉收缴了过去，将你推进了一道门里。"
  },
  [111270120] = {
    id = 111270120,
    name = "——哐！"
  },
  [111270121] = {
    id = 111270121,
    name = "身后铁门关闭，前方通道的尽头，是昏黄的光和此起彼伏的呐喊。"
  },
  [111270122] = {
    id = 111270122,
    name = "看来这里的出口和入口是分开的。比赛已经开始了吗？"
  },
  [111270123] = {
    id = 111270123,
    name = "啊啊啊——！！"
  },
  [111270124] = {
    id = 111270124,
    name = "走出通道，映入你眼帘的是一个巨大的凹形场地，正下方有一座巨大的笼子，笼子置于决斗场中央，笼中两人正激烈地拼斗着。"
  },
  [111270125] = {
    id = 111270125,
    name = "四周围观群众的声浪几乎要将整个场地掀翻。"
  },
  [111270126] = {
    id = 111270126,
    name = "干翻那个废物！！"
  },
  [111270127] = {
    id = 111270127,
    name = "——叮铃！"
  },
  [111270128] = {
    id = 111270128,
    name = "你听到一阵铃声。"
  },
  [111270129] = {
    id = 111270129,
    name = "那是什么？"
  },
  [111270130] = {
    id = 111270130,
    name = "那是在加赌注啊！加了赌注就拉铃，这样才够刺激！"
  },
  [111270131] = {
    id = 111270131,
    name = "突然出现一道解释的声音，随后你感觉肩膀被拍了一下。"
  },
  [111270132] = {
    id = 111270132,
    name = "喂，小子。你是第一次来吧，以前没见过你。"
  },
  [111270133] = {
    id = 111270133,
    name = "是啊。听说这里好玩又有趣，就来看看，果然让人热血沸腾啊~"
  },
  [111270134] = {
    id = 111270134,
    name = "那你来对地方了！"
  },
  [111270135] = {
    id = 111270135,
    name = "杀魔物有什么意思，这里才是最刺激的！你永远都猜不到到底谁才是最后的赢家！"
  },
  [111270136] = {
    id = 111270136,
    name = "说的我也想参与了。"
  },
  [111270137] = {
    id = 111270137,
    name = "那就等下一场好了，这场已经快要结束了，押了也是赔钱。"
  },
  [111270138] = {
    id = 111270138,
    name = "你望向台上，胜败已无悬念。"
  },
  [111270139] = {
    id = 111270139,
    name = "败者被打得满身是血。而看台旁，围在他附近的观众显得非常地愤怒。"
  },
  [111270140] = {
    id = 111270140,
    name = "起来打啊，你这个废物！老子的钱都压在你身上了！"
  },
  [111270141] = {
    id = 111270141,
    name = "可惜败局已定。他几乎昏迷在台上，嗫喏地认输，接着便被人匆匆拖了下去，宛如在拖一具尸体。"
  },
  [111270142] = {
    id = 111270142,
    name = "胜利者——风暴剑士！！"
  },
  [111270143] = {
    id = 111270143,
    name = "啊啊啊——！！"
  },
  [111270144] = {
    id = 111270144,
    name = "四周充斥着兴奋的吼叫声，一场赌博就能让他们一攫千金，比起冒险和魔物厮杀的危险，这样的赚钱方式显然更加吸引人。"
  },
  [111270145] = {
    id = 111270145,
    name = "目光穿过那群还在激动的人群，你看到败者正在被拖往后台，一路上那些押注了的赌徒凑了上去，浓痰唾沫吐了他一身。"
  },
  [111270146] = {
    id = 111270146,
    name = "如果不是有决斗场的打手拦着，他们也许会打死那个让他们倾家荡产的人。"
  },
  [111270147] = {
    id = 111270147,
    name = "后台吗......"
  },
  [111270148] = {
    id = 111270148,
    name = "正当你思考怎么样潜入后台的时候，身边爆发出了更疯狂的尖叫。"
  },
  [111270149] = {
    id = 111270149,
    name = "啊啊啊——！！"
  },
  [111270150] = {
    id = 111270150,
    name = "啊啊啊来了来了！！"
  },
  [111270151] = {
    id = 111270151,
    name = "怎么了？"
  },
  [111270152] = {
    id = 111270152,
    name = "是姬玛！！姬玛！！"
  },
  [111270153] = {id = 111270153, name = "谁？"},
  [111270154] = {
    id = 111270154,
    name = "周围震耳欲聋的吼叫声几乎要刺穿你的耳膜，亏得那赌徒在这种情况下还能听到你的话，他一脸兴奋地向你指了一个方向，和对其他决斗者的反应完全不同。"
  },
  [111270155] = {
    id = 111270155,
    name = "他像一个崇拜者在看着自己的偶像，激动之情溢于言表。"
  },
  [111270156] = {
    id = 111270156,
    name = "看到没，就在那！那就是姬玛！"
  },
  [111270157] = {
    id = 111270157,
    name = "你看了过去，那名挑战者看起来十分娇小，身旁的大剑几乎要高过她的身体，但她握剑的手非常稳，傲气的双眸扫了扫在场的人。"
  },
  [111270158] = {
    id = 111270158,
    name = "她看过来了 ！姬玛，我在这里！"
  },
  [111270159] = {
    id = 111270159,
    name = "啊啊啊啊！！姬玛战无不胜，打倒对面的！！"
  },
  [111270160] = {
    id = 111270160,
    name = "她很厉害吗？"
  },
  [111270161] = {
    id = 111270161,
    name = "她的实力在这里绝对是最强的！我一直在等她，她终于出场了！！"
  },
  [111270162] = {
    id = 111270162,
    name = "——矫捷的身姿，暴力的大剑，绝对的力量控制！"
  },
  [111270163] = {
    id = 111270163,
    name = "下一个将要出场的，就是——"
  },
  [111270164] = {
    id = 111270164,
    name = "血色蝴蝶兰——姬玛！！"
  },
  [111270165] = {
    id = 111270165,
    name = "啊啊啊——！！"
  },
  [111270166] = {
    id = 111270166,
    name = "姬玛身着蝴蝶兰镶边的裙子缓缓走入了满是血腥味的笼子，她的神色倨傲，如果不是拿着一把巨大的长剑，说是来参加宴会的也不为过。"
  },
  [111270167] = {
    id = 111270167,
    name = "对，就是这样，就是这个眼神！！毫不犹豫地把对手干翻吧！"
  },
  [111270168] = {
    id = 111270168,
    name = "你很喜欢她？"
  },
  [111270169] = {
    id = 111270169,
    name = "当然！！我在她身上压了很多钱！她每次都能赢，她已经连胜二十三次了！"
  },
  [111270170] = {
    id = 111270170,
    name = "二十三次！没有人像我运气这么好！"
  },
  [111270171] = {id = 111270171, name = "......"},
  [111270172] = {
    id = 111270172,
    name = "她简直就是我的福星，一本万利的宝贝！"
  },
  [111270173] = {
    id = 111270173,
    name = "还记得三个月前，她第一次出场的时候，别人都不看好她。我想着往她对手身上押再多也没得赚，就干脆押了她。"
  },
  [111270174] = {
    id = 111270174,
    name = "你知道吗？这种感觉就像我是拿到了一块小小的蚌壳，打开后却是一颗完美的珍珠！她赢了，赢得非常轻松！所以她绝对不可能就此止步！"
  },
  [111270175] = {
    id = 111270175,
    name = "她从来不会让我失望！就这样一直一直赢下去吧！"
  },
  [111270176] = {
    id = 111270176,
    name = "到底是擎天撼地残忍的风暴剑士再挫对手，还是席卷决斗场没有败绩的血色蝴蝶兰保持胜绩呢！让我们拭目以待！！"
  },
  [111270177] = {
    id = 111270177,
    name = "姬玛注视着对面的风暴剑士，对方朝她露出一个嘲讽的笑。"
  },
  [111270178] = {
    id = 111270178,
    name = "老子不打女人和小孩。你趁早滚蛋，否则......"
  },
  [111270179] = {
    id = 111270179,
    name = "姬玛昂着下巴，蔑视地看了他一眼。她无声地张了张嘴，口型似乎是“垃圾”。"
  },
  [111270180] = {
    id = 111270180,
    name = "那么，让我们用行动来支持你喜欢的选手吧！"
  },
  [111270181] = {
    id = 111270181,
    name = "围观群众又掀起了一阵疯狂的呐喊，两边铃声也不断地响起，高台上的管事拿着魔力扩音器大喊："
  },
  [111270182] = {
    id = 111270182,
    name = "比赛——正式开始！！！"
  },
  [111270183] = {
    id = 111270183,
    name = "啊啊啊，加油——！！"
  },
  [111270184] = {
    id = 111270184,
    name = "口令发出的瞬间，风暴剑士就怒不可遏地朝姬玛冲了上去！"
  },
  [111270185] = {
    id = 111270185,
    name = "等老子打到你哭着向我求饶，也许我会考虑放过你！"
  },
  [111270186] = {
    id = 111270186,
    name = "他的剑势如闪电，一下就冲到了姬玛眼前，姬玛不知什么时候拿起了大剑横扫而去，巨大的力量直接震开了那把剑。"
  },
  [111270187] = {
    id = 111270187,
    name = "剑士甚至还没来得及抬起左手的盾，腰上就被结实地踹了一脚，倒在笼子的铁栅上哐啷作响。"
  },
  [111270188] = {
    id = 111270188,
    name = "垃圾就应该去垃圾所在的垃圾场，"
  },
  [111270189] = {
    id = 111270189,
    name = "而不是在台上倒人胃口。"
  },
  [111270190] = {
    id = 111270190,
    name = "姬玛踹飞的力道恰到好处，她用了些巧劲，实际并没有用多少力气。那剑客趴在地上懵了好几秒，更加愤怒地爬了起来，脸上布满杀意。"
  },
  [111270191] = {
    id = 111270191,
    name = "围观的人群突然鸦雀无声，随即爆发出呐喊。"
  },
  [111270192] = {
    id = 111270192,
    name = "噢噢噢噢！风暴剑士，冲啊，碾压这个女人！"
  },
  [111270193] = {
    id = 111270193,
    name = "啊啊啊，姬玛你好帅！！我喜欢你！！！"
  },
  [111270194] = {id = 111270194, name = "......"},
  [111270195] = {
    id = 111270195,
    name = "风暴剑士再也不敢小瞧眼前的女人，而这时，姬玛开始了她的进攻——大剑砸向风暴剑士。"
  },
  [111270196] = {
    id = 111270196,
    name = "决斗场的经验让剑士有了敏锐的直觉。除了第一次大意轻敌，之后每一次他都能猜到姬玛的攻击轨迹。盾牌不断受击，防御滴水不漏，待到对手攻势疲软之际，就是他进攻的时机。"
  },
  [111270197] = {
    id = 111270197,
    name = "防守反击吗......"
  },
  [111270198] = {
    id = 111270198,
    name = "姬玛的攻势连绵不绝，你不由地也被精彩的打斗吸引了，但身边的赌徒却显得焦虑，因为剑士这样的对手，恰好是姬玛这种纯攻击类型的克星。"
  },
  [111270199] = {
    id = 111270199,
    name = "上啊！姬玛！打败他！！"
  },
  [111270200] = {
    id = 111270200,
    name = "砰！砰！砰！"
  },
  [111270201] = {
    id = 111270201,
    name = "姬玛接连不断地出剑，然而每一剑都落在了那面盾牌上。即便如此，姬玛依旧神色镇定，嘴角还带着讥讽的笑。"
  },
  [111270202] = {
    id = 111270202,
    name = "躲在龟壳里的家伙，不敢出来了吗！"
  },
  [111270203] = {
    id = 111270203,
    name = "砰！砰！砰！"
  },
  [111270204] = {
    id = 111270204,
    name = "姬玛加大了力量，直接以盾牌为攻击目标，剑士一时被击退了好几步。"
  },
  [111270205] = {
    id = 111270205,
    name = "身边的群众吵闹得更厉害了，他们要看的不是这种平和的对决。"
  },
  [111270206] = {
    id = 111270206,
    name = "怎么回事！绕后砍他啊！我他妈押了那么多钱在你身上，不是看你砸盾牌的！"
  },
  [111270207] = {
    id = 111270207,
    name = "（一力降十会，用绝对的力量打破对手最得意的计划，这个风暴剑士还真可怜啊......）"
  },
  [111270208] = {
    id = 111270208,
    name = "打啊！风暴剑士，你这个懦夫！！"
  },
  [111270209] = {
    id = 111270209,
    name = "场上不满的声音一浪高过一浪，剑士又退了一步，姬玛突然发力，以极快的速度掠去，在场的观众只看到了残影。"
  },
  [111270210] = {
    id = 111270210,
    name = "残影落定，那大剑从天而落，狠狠劈在了剑士慌忙举起的盾牌上。承受了数百次攻击的盾牌，终于不堪重负出现了裂痕。"
  },
  [111270211] = {
    id = 111270211,
    name = "——劈咔！盾牌炸裂了。"
  },
  [111270212] = {
    id = 111270212,
    name = "噢噢噢！正面打起来，打起来！"
  },
  [111270213] = {
    id = 111270213,
    name = "坚守了很久的剑士，看着正在轻喘的姬玛。想必最后那一击姬玛用尽了全力，已经露出了破绽，现在就是他取胜的好时机！"
  },
  [111270214] = {
    id = 111270214,
    name = "就是现在！"
  },
  [111270215] = {
    id = 111270215,
    name = "他的剑向着姬玛的面门突袭而去，可没想竭力的姬玛竟然在这时候借大剑的力量向后一仰，脚尖猛踢正中剑士的手腕，长剑脱手飞出，直奔剑士的咽喉而去！"
  },
  [111270216] = {id = 111270216, name = "！！！"},
  [111270217] = {
    id = 111270217,
    name = "剑尖停了下来。面对扑面而来的死亡恐惧，剑士不由地咽了咽口水，滚动的咽喉出现一丝血丝。"
  },
  [111270218] = {
    id = 111270218,
    name = "杀了他！姬玛！别犹豫！！！"
  },
  [111270219] = {
    id = 111270219,
    name = "看不起女人的失败者，还不认输吗？"
  },
  [111270220] = {
    id = 111270220,
    name = "呵，你敢杀人吗？我看过你的比赛，你不敢！连人都不敢杀的女人还来这里比武！你来杀我啊，杀了我你就赢了！"
  },
  [111270221] = {
    id = 111270221,
    name = "诶？第一次听到这种请求诶。"
  },
  [111270222] = {
    id = 111270222,
    name = "既然你诚心地请求，那我就满足你好了。"
  },
  [111270223] = {
    id = 111270223,
    name = "脖子上那把剑抵进了血肉，剑士恐慌地大叫了起来。"
  },
  [111270224] = {
    id = 111270224,
    name = "不！我认输！我认输！我认输！"
  },
  [111270225] = {
    id = 111270225,
    name = "胜利者——血色蝴蝶兰！！"
  },
  [111270226] = {
    id = 111270226,
    name = "决斗场老板声音一落，铁笼的大门随即打开。台下爆发出了山呼海啸般的欢呼声，姬玛在众人的簇拥下走向胜利者的休息室。"
  },
  [111270227] = {
    id = 111270227,
    name = "噢噢噢噢！！！"
  },
  [111270228] = {
    id = 111270228,
    name = "啊啊啊啊！我的女神姬玛赢了！！啊啊啊太精彩了！！我又赢了我又赢了！！"
  },
  [111270229] = {
    id = 111270229,
    name = "四周都沉浸在欢呼的声浪里，失败的剑士和押着他离开的打手去了后台，你悄悄地跟了上去。"
  },
  [111270230] = {
    id = 111270230,
    name = "进入后台，身后的铁门被落下，将喧闹隔绝在外。你来到了宛如迷宫的地下走廊，前面有几间明亮的房间，却没有人看守。"
  },
  [111270231] = {
    id = 111270231,
    name = "老板，再给我一次机会吧！我真的很需要那笔钱！"
  },
  [111270232] = {
    id = 111270232,
    name = "其中一个房间传来剑士苦苦哀求的声音。"
  },
  [111270233] = {
    id = 111270233,
    name = "可是你连武器都失去了，根本没有力量对付下一个对手吧。"
  },
  [111270234] = {
    id = 111270234,
    name = "我，我知道你们有那种药！我可以接受任何条件，只要能赢，只要能赢！"
  },
  [111270235] = {
    id = 111270235,
    name = "呵呵呵......令人敬佩的斗志，决斗场是不会放弃你的。"
  },
  [111270236] = {
    id = 111270236,
    name = "谢谢，谢谢！"
  },
  [111270237] = {
    id = 111270237,
    name = "喝了药之后，这里就不太适合——"
  },
  [111270238] = {
    id = 111270238,
    name = "咕噜噜.....玻璃瓶在地上滚动的声音在走廊回响。你猛地抬头，一个矫健的身影消失在前方拐角处。"
  },
  [111270239] = {id = 111270239, name = "谁！"},
  [111270240] = {
    id = 111270240,
    name = "你追着那身影过去，身后却传来管事和打手追来的声音。"
  },
  [111270241] = {
    id = 111270241,
    name = "站住！小子！敢跑到后台来，找死！"
  },
  [111270242] = {id = 111270242, name = "......"},
  [111270243] = {
    id = 111270243,
    name = "你左右躲藏，但是那些打手熟悉路途，很快就追上来了。当无处可躲的时候，你猛地被人拉进一个小巷子里，定睛一看，竟然是“血色蝴蝶兰”。"
  },
  [111270244] = {
    id = 111270244,
    name = "附近的打手跑远后，你跟她说了句“谢谢”。"
  },
  [111270245] = {
    id = 111270245,
    name = "（小声）不想死就别说话。"
  },
  [111270246] = {
    id = 111270246,
    name = "姬玛压低了声音，接着在前方带路。她非常熟悉地下走道，没过多久就找到一扇铁门，轻松推开后，外界耀眼的光倾泻而入。"
  },
  [111270247] = {
    id = 111270247,
    name = "呼，好了，安全了。说吧，你是谁？鬼鬼祟祟的，来这儿有什么目的？！"
  },
  [111270248] = {id = 111270248, name = "我......"},
  [111270249] = {
    id = 111270249,
    name = "在那边！别让那家伙跑了！！"
  },
  [111270250] = {
    id = 111270250,
    name = "哇啊，怎么又追来了？！"
  },
  [111270251] = {
    id = 111270251,
    name = "没等你回答，姬玛拔腿就跑。你思虑了一下，紧追姬玛而去。"
  },
  [111270252] = {
    id = 111270252,
    name = "呼，这里总算安全了。"
  },
  [111270253] = {
    id = 111270253,
    name = "姬玛回头看了一眼，发现你跟在身后。"
  },
  [111270254] = {
    id = 111270254,
    name = "你怎么也过来了？呼，你这个家伙，老跟着我干什么？"
  },
  [111270255] = {id = 111270255, name = "我——"},
  [111270256] = {
    id = 111270256,
    name = "等等，让我猜猜......你不会是我的崇拜者吧？"
  },
  [111270257] = {
    id = 111270257,
    name = "别赌了。看你也是个探险者，怎么不好好探险偏要走捷径呢？"
  },
  [111270258] = {
    id = 111270258,
    name = "姬玛小姐，其实，我想打听一些事。"
  },
  [111270259] = {
    id = 111270259,
    name = "难道，是我的私生活？"
  },
  [111270260] = {
    id = 111270260,
    name = "不，你误会了，我只是想打听关于决斗场的事。"
  },
  [111270261] = {
    id = 111270261,
    name = "难道是我连胜的秘诀？唔，我自己还没有达到巅峰，暂时不想收学生。"
  },
  [111270262] = {
    id = 111270262,
    name = "那个，姬玛小姐来决斗场三个月，有没有发现这家决斗场异常的地方，比如——"
  },
  [111270263] = {
    id = 111270263,
    name = "你果然很关注我啊~竟然知道我来了三个月。"
  },
  [111270264] = {
    id = 111270264,
    name = "比如......药物什么的？"
  },
  [111270265] = {id = 111270265, name = "......"},
  [111270266] = {
    id = 111270266,
    name = "姬玛随性的神色突然变得严肃。"
  },
  [111270267] = {
    id = 111270267,
    name = "你问这个干什么，你也想用那种药物变强？"
  },
  [111270268] = {
    id = 111270268,
    name = "受人所托，想调查一些事而已。"
  },
  [111270269] = {
    id = 111270269,
    name = "我知道那个药物，可以暂时提高人的力量。不过我对这种借助药物获得的力量没兴趣。"
  },
  [111270270] = {
    id = 111270270,
    name = "而且，我来决斗场也不是为了钱，只是想找人而已。"
  },
  [111270271] = {
    id = 111270271,
    name = "找人的话，为什么会到这里？"
  },
  [111270272] = {
    id = 111270272,
    name = "有人看到她来过这里，决斗场老板跟我说，打败决斗场所有的人，他就告诉我西比尔的消息。"
  },
  [111270273] = {
    id = 111270273,
    name = "哼，我不相信他，我要自己调查。"
  },
  [111270274] = {
    id = 111270274,
    name = "战胜所有人吗...对了，刚才那场打斗非常精彩。"
  },
  [111270275] = {
    id = 111270275,
    name = "你果然很关注我啊。"
  },
  [111270276] = {id = 111270276, name = "......"},
  [111270277] = {
    id = 111270277,
    name = "那姬玛小姐有线索了吗？"
  },
  [111270278] = {
    id = 111270278,
    name = "没有。她就像凭空消失了一样，难道我真的要打败所有的对手才行？"
  },
  [111270279] = {
    id = 111270279,
    name = "会不会，和那个药物有关？"
  },
  [111270280] = {
    id = 111270280,
    name = "不可能！西比尔绝对不可能使用那种药物！"
  },
  [111270281] = {
    id = 111270281,
    name = "那个药物到底是什么？"
  },
  [111270282] = {
    id = 111270282,
    name = "你知道决斗场的规矩吗？输了比赛的话，败者就会输掉他加入决斗场以来的所有欧泊，也就是说，之前的胜利都化为乌有。"
  },
  [111270283] = {
    id = 111270283,
    name = "但是有一种办法可以赎回欧泊，就是重新连胜十场。"
  },
  [111270284] = {
    id = 111270284,
    name = "那个风暴剑士已经输给你一次了，还是以他最擅长的作战方式输掉的。"
  },
  [111270285] = {
    id = 111270285,
    name = "哼，那只是我的战术之一，击溃心理防线才能让对手知道我们之间的差距！"
  },
  [111270286] = {
    id = 111270286,
    name = "所以，他需要药物来提升自己。"
  },
  [111270287] = {
    id = 111270287,
    name = "一旦服下那种药物，相当于和决斗场签订了终生契约。所以西比尔是绝对不会把自己卖给决斗场的。"
  },
  [111270288] = {
    id = 111270288,
    name = "原来如此。"
  },
  [111270289] = {
    id = 111270289,
    name = "别跑，站住！！"
  },
  [111270290] = {
    id = 111270290,
    name = "那群打手追上来了！"
  },
  [111270291] = {
    id = 111270291,
    name = "姬玛赶紧把你拖到树后躲避。"
  },
  [111270292] = {
    id = 111270292,
    name = "真是臭虫啊，甩都甩不掉！"
  },
  [111270293] = {
    id = 111270293,
    name = "你们刚藏好，就有三个人出现了。最前面那人疯疯癫癫、叽哩哇啦地大喊着，衣衫破烂，面色痛苦，还在不停地逃跑，仿佛身后追他的是地狱中的恶鬼。然而他很快就被抓住了。"
  },
  [111270294] = {
    id = 111270294,
    name = "我看你是活腻了！"
  },
  [111270295] = {
    id = 111270295,
    name = "那人疯狂地挣扎着，打手正想一巴掌打下去，却被另一个人制止了。疯疯癫癫的人被喂下了一管药剂，瞬间安静下来，闭眼昏睡了过去。"
  },
  [111270296] = {
    id = 111270296,
    name = "喂喂喂，别打死老大的货啊。"
  },
  [111270297] = {
    id = 111270297,
    name = "真是晦气。每天抓这些疯子，老子都要疯了！"
  },
  [111270298] = {
    id = 111270298,
    name = "行了，先带回去再说吧！"
  },
  [111270299] = {
    id = 111270299,
    name = "两人拖着昏睡的那人从你和姬玛身边走过，并没有注意到你们。你静静注视着那个被拖曳的人，他的身上隐隐散发出了一股黑气，浊炁欢快地跳跃着，丝丝缕缕进入他的身体中。"
  },
  [111270300] = {
    id = 111270300,
    name = "（堕落者的气息......地下决斗场果然和堕落者有关。）"
  },
  [111270301] = {
    id = 111270301,
    name = "等三人离开后，姬玛起身舒展了一下身体。"
  },
  [111270302] = {
    id = 111270302,
    name = "呼，原来不是追我们的，不过这地方也不能待了，你还有什么想问的，跟我去据点再说。"
  },
  [111270303] = {
    id = 111270303,
    name = "咳，那个......我是看在你是我的崇拜者的份上，才大发慈悲地告诉你的。"
  },
  [111270304] = {
    id = 111270304,
    name = "啊，那真是万分感谢。"
  },
  [111270305] = {
    id = 111270305,
    name = "你微笑着，没有揭穿她。"
  },
  [111270306] = {
    id = 111270306,
    name = "你跟着姬玛来到了据点，她将你带进了她的房间。"
  },
  [111270307] = {
    id = 111270307,
    name = "凌乱的房间里到处是她赢来的武器和战利品，几乎没有下脚的地方，姬玛从角落里掏出一把干净的椅子坐下了。"
  },
  [111270308] = {
    id = 111270308,
    name = "随便坐，不用客气。"
  },
  [111270309] = {id = 111270309, name = "......"},
  [111270310] = {
    id = 111270310,
    name = "那个，我只是想打听一些情报，并不打算长时间打扰你。"
  },
  [111270311] = {
    id = 111270311,
    name = "嗯......你看起来和那些聒噪的家伙不一样。"
  },
  [111270312] = {
    id = 111270312,
    name = "她的眉尾扬了扬，傲然地望着你。"
  },
  [111270313] = {
    id = 111270313,
    name = "喂，你想问什么，抓紧问吧。"
  },
  [111270314] = {
    id = 111270314,
    name = "其实，刚才我在那个逃跑的人身上感受到了堕落者的气息。"
  },
  [111270315] = {
    id = 111270315,
    name = "姬玛的眉头皱了起来。"
  },
  [111270316] = {
    id = 111270316,
    name = "这件事对附近造成了不小的影响，我是专门来决斗场调查这件事的。"
  },
  [111270317] = {
    id = 111270317,
    name = "所以那个被抓回去的人......如果西比尔......不行，这件事必须尽快查清楚！"
  },
  [111270318] = {
    id = 111270318,
    name = "啊，对了，你说你关注了我三个月，你有没有见过一个叫西比尔的女孩？！"
  },
  [111270319] = {id = 111270319, name = "......"},
  [111270320] = {
    id = 111270320,
    name = "你摇了摇头，姬玛围着椅子团团转，将那些战利品踢来踢去，发出叮当的响声。"
  },
  [111270321] = {id = 111270321, name = "......"},
  [111270322] = {
    id = 111270322,
    name = "（之前走廊里的声音，不会就是她制造的吧？）"
  },
  [111270323] = {
    id = 111270323,
    name = "姬玛停了下来，对你扬起头。"
  },
  [111270324] = {
    id = 111270324,
    name = "你知道地下决斗场的规矩吧？"
  },
  [111270325] = {
    id = 111270325,
    name = "你将从赌徒那里获得的情报告诉了她。"
  },
  [111270326] = {
    id = 111270326,
    name = "这个地下决斗场已经成立很久了，我来的时候，这里就有一百多个决斗者，所以那个人说“打败所有的决斗者才能告诉我”的时候，我就已经知道是在耍我。"
  },
  [111270327] = {
    id = 111270327,
    name = "我只能在每次决斗后悄悄调查。我发现后台有很多房间，每个房间都是封闭的，听不到也看不见里面是什么。不过在其中的一个房间里，我发现了大量的药物，应该就是能提升力量的那种药。"
  },
  [111270328] = {
    id = 111270328,
    name = "提升力量......如果药物有副作用，那么服用者很可能被负面情绪占据，继而被浊炁侵蚀身体......"
  },
  [111270329] = {
    id = 111270329,
    name = "浊炁侵蚀？那不就成了堕落者？？！"
  },
  [111270330] = {
    id = 111270330,
    name = "如果真的像你说的那样，那西比尔也一定知道......"
  },
  [111270331] = {
    id = 111270331,
    name = "诶，这只是我的一种猜测而已。"
  },
  [111270332] = {
    id = 111270332,
    name = "我们现在就去——"
  },
  [111270333] = {
    id = 111270333,
    name = "啊啊不行不行，我的剑破损了，还要去修磨，需要三天。"
  },
  [111270334] = {
    id = 111270334,
    name = "（看来斩坏那面盾用了不小的力气呢。）"
  },
  [111270335] = {
    id = 111270335,
    name = "三天后，你来这里找我。我们一起再去决斗场，找到真相！"
  },
  [111270336] = {
    id = 111270336,
    name = "姬玛小姐真有正义感啊。"
  },
  [111270337] = {
    id = 111270337,
    name = "我只是为了找人罢了。"
  },
  [111270338] = {
    id = 111270338,
    name = "对了。我给你提供了这么多珍贵的情报，作为交换，你这三天，也别忘了帮我打听西比尔的消息。"
  },
  [111270339] = {
    id = 111270339,
    name = "当然，得姬玛小姐邀请同行，荣幸之至。"
  },
  [111270340] = {
    id = 111270340,
    name = "三天后，你们如约来到了决斗场的入口附近。"
  },
  [111270341] = {
    id = 111270341,
    name = "呃，怎么一个人也没有？"
  },
  [111270342] = {
    id = 111270342,
    name = "姬玛踢了踢毫无反应的门。"
  },
  [111270343] = {
    id = 111270343,
    name = "这是参赛者走的门吧？观众会走另外一边。"
  },
  [111270344] = {
    id = 111270344,
    name = "没错，我就是参赛者啊。"
  },
  [111270345] = {
    id = 111270345,
    name = "姬玛又朝木门踢了两脚，门框岌岌可危。"
  },
  [111270346] = {
    id = 111270346,
    name = "那个，你们在那里干什么？"
  },
  [111270347] = {
    id = 111270347,
    name = "听说这里有比赛，想来观摩观摩。"
  },
  [111270348] = {
    id = 111270348,
    name = "哦，你们想进决斗场啊~真是不巧，他们前几天临时关闭了。"
  },
  [111270349] = {
    id = 111270349,
    name = "出了什么事？"
  },
  [111270350] = {
    id = 111270350,
    name = "听人说好像是发生了什么事。这几天都关着的，具体是什么就不知道了。"
  },
  [111270351] = {
    id = 111270351,
    name = "那有没有说什么时候再开放决斗场？"
  },
  [111270352] = {
    id = 111270352,
    name = "这个我也不清楚。"
  },
  [111270353] = {
    id = 111270353,
    name = "路人只是路过，闲聊几句后就离开了。"
  },
  [111270354] = {
    id = 111270354,
    name = "竟然临时关闭了......"
  },
  [111270355] = {
    id = 111270355,
    name = "那就不客气啦！"
  },
  [111270356] = {
    id = 111270356,
    name = "——砰！！"
  },
  [111270357] = {
    id = 111270357,
    name = "本就颤巍巍的木门被一脚踹破，姬玛大摇大摆地走了进去。"
  },
  [111270358] = {id = 111270358, name = "......"},
  [111270359] = {
    id = 111270359,
    name = "你们从参赛者通道走入了决斗场，一片黑暗中，姬玛点燃了火石照路。"
  },
  [111270360] = {
    id = 111270360,
    name = "眼前依然是一条通道，两边是属于参赛者独自的房间，上面写有数字编号，再往前隐约能看到铁笼和宽敞的决斗台。"
  },
  [111270361] = {
    id = 111270361,
    name = "竟然一个人也没有。"
  },
  [111270362] = {
    id = 111270362,
    name = "有药物的房间在哪里？"
  },
  [111270363] = {
    id = 111270363,
    name = "在这边。"
  },
  [111270364] = {
    id = 111270364,
    name = "姬玛轻车熟路地找到一间房屋，却发现打不开。她直接踹开了门，结果里面空空如也。"
  },
  [111270365] = {
    id = 111270365,
    name = "空的？不可能！"
  },
  [111270366] = {
    id = 111270366,
    name = "这里有堕落者残留的气息，果然......"
  },
  [111270367] = {
    id = 111270367,
    name = "谁啊？都说了关门了......"
  },
  [111270368] = {
    id = 111270368,
    name = "隔壁门被打开，从里面走出来一个打着哈欠的人，看起来是留守在这里的决斗场职员，他在看到姬玛的那一刻瞪大了眼睛。"
  },
  [111270369] = {
    id = 111270369,
    name = "姬玛？！！你竟然没有——"
  },
  [111270370] = {
    id = 111270370,
    name = "留守人意识到什么，立刻住了嘴，转身狂奔而去。"
  },
  [111270371] = {
    id = 111270371,
    name = "没有什么？喂，别跑，把话说完！！"
  },
  [111270372] = {
    id = 111270372,
    name = "这个人一定知道些什么，不能让他逃了！"
  },
  [111270373] = {
    id = 111270373,
    name = "留守人跑得飞快，你提步疾追，就在快追上他的时候，一个空药瓶从身后飞来，准确无误地砸在了留守人的后脑勺上。"
  },
  [111270374] = {
    id = 111270374,
    name = "瓶子落在地上骨碌碌作响，留守人抱头滚地嗷嗷直叫，随后被你提了起来。"
  },
  [111270375] = {
    id = 111270375,
    name = "饶命啊，姬玛大人！！我只是个守门的，什么都不知道，呜呜呜......"
  },
  [111270376] = {
    id = 111270376,
    name = "姬玛慢悠悠走到他面前。"
  },
  [111270377] = {
    id = 111270377,
    name = "那你跑什么？一定是心里有鬼！"
  },
  [111270378] = {
    id = 111270378,
    name = "我问你，这里怎么一个人都没有？！"
  },
  [111270379] = {
    id = 111270379,
    name = "我不就是人吗......"
  },
  [111270380] = {id = 111270380, name = "嗯？"},
  [111270381] = {
    id = 111270381,
    name = "啊！他们全都走了，就留我在这守着。"
  },
  [111270382] = {
    id = 111270382,
    name = "全走了？"
  },
  [111270383] = {
    id = 111270383,
    name = "我们老板......那个，他让我留下来看门。说，说等事儿过了，还会回来。"
  },
  [111270384] = {
    id = 111270384,
    name = "为什么会突然撤离？"
  },
  [111270385] = {
    id = 111270385,
    name = "我也不知道啊，老大让撤大家就赶紧撤了......"
  },
  [111270386] = {
    id = 111270386,
    name = "不知道？那我的剑能不能帮你想起点什么？"
  },
  [111270387] = {
    id = 111270387,
    name = "姬玛单手甩了甩那把大剑，剑锋在留守人的鼻尖晃荡了好几次，他惊恐地大喊，但被你挟持着无法挣脱。"
  },
  [111270388] = {
    id = 111270388,
    name = "我记起来了！记起来了！前几天，决斗场出了些事，老大说要关闭这里，就让大家都走了。不过老板走的时候还在问姬玛大人去哪儿了，不是故意不通知你的！"
  },
  [111270389] = {
    id = 111270389,
    name = "决斗场出了什么事？"
  },
  [111270390] = {
    id = 111270390,
    name = "好像是意外死了个送饭的......老板就让大家都走了。其实决斗者杀个人什么的，以前也是常有的事，不知道这次为什么这么着急。"
  },
  [111270391] = {
    id = 111270391,
    name = "我，我知道的就这么多了，放我走吧！"
  },
  [111270392] = {
    id = 111270392,
    name = "别着急啊，你还没说他们撤到哪儿去了。"
  },
  [111270393] = {
    id = 111270393,
    name = "这个我是真的不知道啊！"
  },
  [111270394] = {
    id = 111270394,
    name = "不想要鼻子了？"
  },
  [111270395] = {
    id = 111270395,
    name = "大人饶命啊！如果我知道，也不会待在这了！"
  },
  [111270396] = {
    id = 111270396,
    name = "这里既然已经被发现，他们就不会再回来，你被抛弃了。"
  },
  [111270397] = {
    id = 111270397,
    name = "留守人哽咽了一下，惊醒地看着你。"
  },
  [111270398] = {
    id = 111270398,
    name = "他们没有告诉你新的地点，只想留个人，隔段时间再看你死了没有，这样就能更好地掌握这边的情况。"
  },
  [111270399] = {
    id = 111270399,
    name = "喂，想想他们可能会去的地方在哪儿，否则，只能等你的老板回来给你装新的鼻子！"
  },
  [111270400] = {
    id = 111270400,
    name = "我，真的不......啊，我，我想起来了！绕过前面的森林，有一个废弃的据点！他们应该在那里！"
  },
  [111270401] = {
    id = 111270401,
    name = "这不是能想起来吗？"
  },
  [111270402] = {
    id = 111270402,
    name = "那是你的剑挥舞得好。"
  },
  [111270403] = {
    id = 111270403,
    name = "那当然。"
  },
  [111270404] = {
    id = 111270404,
    name = "你放了留守人，和姬玛转身离开，他脚一软瘫坐在地上大声喘气。"
  },
  [111270405] = {
    id = 111270405,
    name = "诶，等等。"
  },
  [111270406] = {
    id = 111270406,
    name = "姬玛回过头，留守人立马坐直了身体，全身汗毛倒立。"
  },
  [111270407] = {
    id = 111270407,
    name = "你，你说了会放过我的！"
  },
  [111270408] = {
    id = 111270408,
    name = "没说不放啊，只不过我还有个问题，你知道西比尔这个人吗？"
  },
  [111270409] = {
    id = 111270409,
    name = "留守人摇摇头，姬玛露出怀疑的眼神。"
  },
  [111270410] = {
    id = 111270410,
    name = "真的不知道？"
  },
  [111270411] = {
    id = 111270411,
    name = "千真万确啊！姬玛大人，我不该告诉你的都告诉你了！这个真不知道......"
  },
  [111270412] = {
    id = 111270412,
    name = "姬玛失望地看了他一眼，招呼你一起离开了。"
  },
  [111270413] = {
    id = 111270413,
    name = "这里就是那家伙说的地方了。"
  },
  [111270414] = {
    id = 111270414,
    name = "眼前是七八座废弃的房屋，破碎的木板和陶罐躺在地上无人问津，遍地的杂草和青苔，扑面而来的荒废气息...说明这里已经久无人居。"
  },
  [111270415] = {
    id = 111270415,
    name = "那家伙是不是骗我？这种地方怎么会有决斗场？"
  },
  [111270416] = {
    id = 111270416,
    name = "大多数人都会这样想，才能更好地隐蔽吧。"
  },
  [111270417] = {
    id = 111270417,
    name = "咳咳。你说的这个正是我刚想说的，那我们怎么找？"
  },
  [111270418] = {
    id = 111270418,
    name = "既然决斗场在这里，那总会有人来。我们等等看吧。"
  },
  [111270419] = {
    id = 111270419,
    name = "唔，我也是这么打算的。"
  },
  [111270420] = {
    id = 111270420,
    name = "一段时间后......"
  },
  [111270421] = {
    id = 111270421,
    name = "那家伙一定是骗我的，我要回去找他算账！"
  },
  [111270422] = {
    id = 111270422,
    name = "来了，那边有人！"
  },
  [111270423] = {
    id = 111270423,
    name = "远处传来脚步声，姬玛精神一震，就要走上去，而你拉住了她。"
  },
  [111270424] = {
    id = 111270424,
    name = "她看懂了你的眼神示意，悄悄跟你一起躲了起来，看着来人慢慢地靠近。"
  },
  [111270425] = {
    id = 111270425,
    name = "唔唔唔......！！！"
  },
  [111270426] = {
    id = 111270426,
    name = "兄弟，这家伙醒了！"
  },
  [111270427] = {
    id = 111270427,
    name = "安静点，不然我现在就宰了你！"
  },
  [111270428] = {
    id = 111270428,
    name = "唔唔唔......！！！"
  },
  [111270429] = {
    id = 111270429,
    name = "两个人抬着一个麻袋朝废墟走了过来，麻袋的形状在不断变化，可见里面的人挣扎得很厉害。这时其中一个打手朝麻袋狠狠打了一拳，里面传来痛苦的低吼声。"
  },
  [111270430] = {
    id = 111270430,
    name = "这是给你一点小小的教训，既然和决斗场签了契约，就乖乖地别想着逃跑了。"
  },
  [111270431] = {
    id = 111270431,
    name = "两个打手走进了废弃的据点，离你和姬玛越来越近。"
  },
  [111270432] = {
    id = 111270432,
    name = "（小声）现在可以上了吗？"
  },
  [111270433] = {
    id = 111270433,
    name = "再等等。"
  },
  [111270434] = {
    id = 111270434,
    name = "两人把麻袋扔到了一边，接着拨开附近的杂草，露出下面的石板。其中一人拿出了一块形状特殊的魔法石轻轻放上在了石板上，一条隧道訇然中开。"
  },
  [111270435] = {
    id = 111270435,
    name = "出现了！"
  },
  [111270436] = {
    id = 111270436,
    name = "是谁？！"
  },
  [111270437] = {
    id = 111270437,
    name = "见姬玛如此迫不及待，你也只好跟着走了出去。"
  },
  [111270438] = {
    id = 111270438,
    name = "血色蝴蝶兰？！！是你？！"
  },
  [111270439] = {
    id = 111270439,
    name = "怎么，不欢迎我啊？"
  },
  [111270440] = {
    id = 111270440,
    name = "哼，想打比赛的话，决斗场已经关闭了。"
  },
  [111270441] = {
    id = 111270441,
    name = "你们管事的，在这里面吧？"
  },
  [111270442] = {
    id = 111270442,
    name = "你们进去也没用，老板对你这种决斗者已经没兴趣了。"
  },
  [111270443] = {
    id = 111270443,
    name = "喂，你在做什么？！"
  },
  [111270444] = {
    id = 111270444,
    name = "发现你想要解开麻袋救出被绑起来的人，打手惊怒地出手，却被你反手打晕了过去。"
  },
  [111270445] = {
    id = 111270445,
    name = "你们这两个家伙！"
  },
  [111270446] = {
    id = 111270446,
    name = "我，我要去告诉老板大人，呃——！"
  },
  [111270447] = {
    id = 111270447,
    name = "厚重的剑背“咚”地一声砸在他的后脑勺上，他顿时晕了过去。姬玛跨过他走了过来。"
  },
  [111270448] = {
    id = 111270448,
    name = "居然当着我的面说想告状，第一次见到这种蠢货。"
  },
  [111270449] = {
    id = 111270449,
    name = "咳咳咳，是你救了我吗？"
  },
  [111270450] = {
    id = 111270450,
    name = "谢谢，谢谢。"
  },
  [111270451] = {
    id = 111270451,
    name = "你是决斗者？"
  },
  [111270452] = {
    id = 111270452,
    name = "被绑起来的人点了点头，看到姬玛的时候愣了一下。"
  },
  [111270453] = {
    id = 111270453,
    name = "这么看我干什么，本小姐知道自己很好看。"
  },
  [111270454] = {
    id = 111270454,
    name = "是你，你赢过我。"
  },
  [111270455] = {
    id = 111270455,
    name = "哦，原来是手下败将啊。"
  },
  [111270456] = {
    id = 111270456,
    name = "你，你来这里做什么？"
  },
  [111270457] = {
    id = 111270457,
    name = "管事的欠了我一点东西，我找他要回来。"
  },
  [111270458] = {
    id = 111270458,
    name = "咳咳，趁你现在还没进去，赶快逃走吧。"
  },
  [111270459] = {
    id = 111270459,
    name = "莫名其妙。"
  },
  [111270460] = {
    id = 111270460,
    name = "姬玛没再理会，转身进入了隧道，被绑的人着急地喊了一声。"
  },
  [111270461] = {
    id = 111270461,
    name = "你会死的！"
  },
  [111270462] = {
    id = 111270462,
    name = "怎么回事？"
  },
  [111270463] = {
    id = 111270463,
    name = "他们都疯了！他们用堕落者参赛！我不想变成堕落者，才想尽办法地逃跑！远离那里，越远越好！千万别进去！"
  },
  [111270464] = {
    id = 111270464,
    name = "那就更要进去了。"
  },
  [111270465] = {
    id = 111270465,
    name = "姬玛脚步未停，你连忙解开了绑着那人的绳子。"
  },
  [111270466] = {
    id = 111270466,
    name = "谢谢你提供的情报，快走吧，没有人能刚好救你第二次。"
  },
  [111270467] = {
    id = 111270467,
    name = "你拍了拍他哆嗦的肩，也进入了深不见底的地下隧道中。"
  },
  [111270468] = {
    id = 111270468,
    name = "姬玛走得很快，你在快到地下隧道出口处时才看到了她的身影。"
  },
  [111270469] = {
    id = 111270469,
    name = "等一下！"
  },
  [111270470] = {
    id = 111270470,
    name = "幸好隧道尽头混乱嘈杂的吼叫没有完全掩盖你的声音，姬玛回头看了你一眼。"
  },
  [111270471] = {
    id = 111270471,
    name = "啧，我还以为你听那个家伙的话，不来了呢。"
  },
  [111270472] = {
    id = 111270472,
    name = "我的调查还没结束，而且，答应你的事也没完成。"
  },
  [111270473] = {
    id = 111270473,
    name = "嗯......你还挺守信的。"
  },
  [111270474] = {
    id = 111270474,
    name = "看起来前面就是他们新的聚集点了。"
  },
  [111270475] = {
    id = 111270475,
    name = "啊，真是令人讨厌的味道。"
  },
  [111270476] = {
    id = 111270476,
    name = "扑面而来的是浓厚的血腥味，还有体味汗水混杂的奇怪味道，姬玛嫌弃地皱紧眉头。"
  },
  [111270477] = {
    id = 111270477,
    name = "这里比之前的地下空间大更多，看起来像是人工挖凿的巨大山洞。"
  },
  [111270478] = {
    id = 111270478,
    name = "看客们也大不相同，他们脸上满是不正常的疯癫和狂热，朝着擂台嘶声呐喊。姬玛恰巧被前面高壮的男人挡住了，跳了好几次什么也看不见。"
  },
  [111270479] = {
    id = 111270479,
    name = "在她不耐烦的时候，你点了点她的肩。你们找到一处角落，能清晰地看到中心擂台。"
  },
  [111270480] = {
    id = 111270480,
    name = "宽大的擂台四周同样布置着铁笼，而地板和栅栏上印满了血红以及发黑的痕迹。"
  },
  [111270481] = {
    id = 111270481,
    name = "噢噢噢噢！！"
  },
  [111270482] = {
    id = 111270482,
    name = "你和姬玛看了过去，决斗的双方已经厮打在了一起。"
  },
  [111270483] = {
    id = 111270483,
    name = "在鲜血四溅和痛苦吼声中，你看清楚了其中一个是风暴剑士——曾经输给姬玛的探险者。"
  },
  [111270484] = {
    id = 111270484,
    name = "而另外一个面相怪异，全身肌肉虬结，扭曲的面容露出嗜杀的凶光——你已经可以确认这人的身份。"
  },
  [111270485] = {
    id = 111270485,
    name = "是堕落者。"
  },
  [111270486] = {
    id = 111270486,
    name = "这是一场过分血腥的战斗。"
  },
  [111270487] = {
    id = 111270487,
    name = "风暴剑士抛弃了他的武器，用手臂抵挡堕落者的撕咬，腥臭的口涎和鲜血飞洒，刺激着所有观众的神经，围观群众不断爆发出浪潮般的欢呼和尖叫！"
  },
  [111270488] = {
    id = 111270488,
    name = "尖叫声在堕落者把风暴剑士扔出去的瞬间达到了高潮。"
  },
  [111270489] = {
    id = 111270489,
    name = "啊啊啊啊——！"
  },
  [111270490] = {
    id = 111270490,
    name = "杀了他，杀了他，杀了他！"
  },
  [111270491] = {
    id = 111270491,
    name = "太过分了，太过分了，太过分了！"
  },
  [111270492] = {
    id = 111270492,
    name = "姬玛还记得风暴剑士和他对战时桀骜不驯的样子，而现在他被扔在血笼边上，满身血痕生死不知。如果一个失败者会沦落到这样的下场，那么......"
  },
  [111270493] = {
    id = 111270493,
    name = "西比尔......"
  },
  [111270494] = {
    id = 111270494,
    name = "眼看姬玛就要冲出人群，却被你扶住了肩。"
  },
  [111270495] = {
    id = 111270495,
    name = "冷静。这场比赛结束，我和你一起去。"
  },
  [111270496] = {
    id = 111270496,
    name = "撕碎他！上啊！愚蠢的堕落者！！"
  },
  [111270497] = {
    id = 111270497,
    name = "风暴剑士摇摇晃晃地站了起来，他朝血笼外伸了伸手。围在四周的打手似乎知道他要什么，递给他一管药剂。"
  },
  [111270498] = {
    id = 111270498,
    name = "吼——！！"
  },
  [111270499] = {
    id = 111270499,
    name = "堕落者扑了上去，风暴剑士在堕落者的攻击下，艰难地将药剂倒入了口中......"
  },
  [111270500] = {
    id = 111270500,
    name = "我不会输，我不会输！啊啊啊——！"
  },
  [111270501] = {
    id = 111270501,
    name = "风暴剑士的衣衫崩裂，血色的臂膀变得青紫，长出了怪异的肢体，反手将眼前堕落者的肩膀捅穿了！"
  },
  [111270502] = {
    id = 111270502,
    name = "噢噢噢噢！精彩！！上啊！"
  },
  [111270503] = {id = 111270503, name = "！！！"},
  [111270504] = {
    id = 111270504,
    name = "观众席上骤然爆发出欢呼声。"
  },
  [111270505] = {
    id = 111270505,
    name = "血笼中，风暴剑士新生的异肢抓住了堕落者的双臂。他爆喝一声，竟然生生将堕落者整个撕成了两半。猩红的血如瀑布浇淋在他的身上，整个人被染得血红。"
  },
  [111270506] = {
    id = 111270506,
    name = "我赢了，我赢了......哈哈哈哈......呵呵呵呵......"
  },
  [111270507] = {
    id = 111270507,
    name = "打手们将竭力的风暴剑士拖出擂台，然后将他锁进另一个矮小的笼子里，抬向了后台。"
  },
  [111270508] = {
    id = 111270508,
    name = "......他赢了。"
  },
  [111270509] = {
    id = 111270509,
    name = "可是代价......"
  },
  [111270510] = {
    id = 111270510,
    name = "曾经的风暴剑士双眼中的锐光正慢慢消失，逐渐狂化堕落，成为了新生的堕落者。"
  },
  [111270511] = {
    id = 111270511,
    name = "斗场洋溢着狂欢的氛围。赢得大把赌资的观众更加期待下一场比赛，而你与姬玛已经跟随着一群打手，悄悄溜进了后台。"
  },
  [111270512] = {
    id = 111270512,
    name = "这些该死的家伙，真的把人变成了堕落者！"
  },
  [111270513] = {
    id = 111270513,
    name = "是的，证实了这一点，我的调查也结束了。"
  },
  [111270514] = {
    id = 111270514,
    name = "你一点都不生气吗？他们做出这种灭绝人性的事？！"
  },
  [111270515] = {
    id = 111270515,
    name = "没有办法改变这个事实的话，怎么生气都是没用的。"
  },
  [111270516] = {
    id = 111270516,
    name = "只要找到幕后的人......"
  },
  [111270517] = {
    id = 111270517,
    name = "既然这个决斗场有了这样的规模，说明深渊其他地方也存在着同样的决斗场。"
  },
  [111270518] = {
    id = 111270518,
    name = "这是一项暴利。你可以摧毁一个，但无法摧毁所有......"
  },
  [111270519] = {
    id = 111270519,
    name = "以及人性的贪婪。"
  },
  [111270520] = {
    id = 111270520,
    name = "姬玛沉默了一阵，径自走了。"
  },
  [111270521] = {
    id = 111270521,
    name = "这里的后台比之前的简陋了许多，很多地方只是用厚厚的帘布遮挡着，一路上都是决斗者流下的鲜血痕迹。"
  },
  [111270522] = {
    id = 111270522,
    name = "一个老人正在麻木地清扫，姬玛停下了脚步。"
  },
  [111270523] = {
    id = 111270523,
    name = "要不要问问他？"
  },
  [111270524] = {id = 111270524, name = "啊？"},
  [111270525] = {id = 111270525, name = "什么？"},
  [111270526] = {
    id = 111270526,
    name = "看到姬玛失神的样子，你意识到她是来找人的。"
  },
  [111270527] = {
    id = 111270527,
    name = "目睹这样的场景，难免会担心自己要找的人的安危。"
  },
  [111270528] = {
    id = 111270528,
    name = "不是要打听西比尔的消息吗？"
  },
  [111270529] = {
    id = 111270529,
    name = "你调查都结束了，还跟来干嘛？"
  },
  [111270530] = {
    id = 111270530,
    name = "我是个守信的人，这是你说的。"
  },
  [111270531] = {
    id = 111270531,
    name = "哼。这种事，我一个人就能做到。"
  },
  [111270532] = {
    id = 111270532,
    name = "姬玛上前问了老人几句话。得到消息后，她掀开了第二个帘幕，快步走了进去。"
  },
  [111270533] = {
    id = 111270533,
    name = "你跟紧脚步，进入了帘幕——锁链和牢笼碰撞发出了嘈杂的声响，堕落者痛苦绝望的吼叫此起彼伏。"
  },
  [111270534] = {
    id = 111270534,
    name = "真想把这些东西放出来咬死那个幕后之人。"
  },
  [111270535] = {
    id = 111270535,
    name = "自作孽不可活，他们会受到惩罚的。"
  },
  [111270536] = {
    id = 111270536,
    name = "姬玛听到你的话愣了愣，走向了尽头处最大的房间。"
  },
  [111270537] = {
    id = 111270537,
    name = "帘幕掀开后，看见的是一个中年男人。他坐在木质的椅子上，看起来像圈落中的普通商人。他浑浊狡猾的眼神在姬玛身上扫了两圈，又落在了你身上。"
  },
  [111270538] = {
    id = 111270538,
    name = "哦呀，姬玛~我的血色蝴蝶兰，好久不见。"
  },
  [111270539] = {
    id = 111270539,
    name = "少废话，我要的答案呢。"
  },
  [111270540] = {
    id = 111270540,
    name = "什么答案？哦，你说西比尔的消息啊~姬玛，你忘记我们之间的交易了吗？没有打败所有挑战者之前，我不能告诉你呢。"
  },
  [111270541] = {
    id = 111270541,
    name = "姬玛回头指着走廊两旁那些似人非人的怪物，一把扯住老板的领子。"
  },
  [111270542] = {
    id = 111270542,
    name = "你管那些家伙叫挑战者？！！"
  },
  [111270543] = {
    id = 111270543,
    name = "有话好好说，好好说~你先放手......我告诉你......"
  },
  [111270544] = {
    id = 111270544,
    name = "姬玛松开了手。决斗场老板在椅子扶手处按下一颗魔法石，整个房间瞬间爆发出刺目的白光！"
  },
  [111270545] = {
    id = 111270545,
    name = "把他们给我抓起来！"
  },
  [111270546] = {
    id = 111270546,
    name = "你一手捂住姬玛的眼睛，一手抓起椅子朝决斗场老板逃跑的身影扔了过去。"
  },
  [111270547] = {
    id = 111270547,
    name = "哇啊！！！"
  },
  [111270548] = {
    id = 111270548,
    name = "哗啦一声，椅子四分五裂！"
  },
  [111270549] = {
    id = 111270549,
    name = "白光逐渐消失，而那些打手也在听到决斗场老板的召唤后匆匆跑来。姬玛挣脱了你的手，怒气冲冲地踹了决斗场老板一脚。"
  },
  [111270550] = {id = 111270550, name = "快说！"},
  [111270551] = {
    id = 111270551,
    name = "我，我们的交易还在......我的人就要来了......你要是乖乖地继续参赛，我——"
  },
  [111270552] = {
    id = 111270552,
    name = "废话少说！"
  },
  [111270553] = {
    id = 111270553,
    name = "此时，附近的打手赶到。你一脚踹翻最近的那个打手，挟持决斗场老板作为人质。"
  },
  [111270554] = {
    id = 111270554,
    name = "都别动手！"
  },
  [111270555] = {
    id = 111270555,
    name = "住手！都给我住手！"
  },
  [111270556] = {
    id = 111270556,
    name = "你们押着决斗场老板慢慢移动，前方围住的打手整齐地向两边散开。揭开帘幕，人群的尖叫声再次充斥着耳膜。"
  },
  [111270557] = {
    id = 111270557,
    name = "救我！快救我！不然你们别想知道药剂在哪儿！！"
  },
  [111270558] = {
    id = 111270558,
    name = "闭嘴！想死吗？"
  },
  [111270559] = {
    id = 111270559,
    name = "我死了你也永远找不到西比尔了！！"
  },
  [111270560] = {
    id = 111270560,
    name = "姬玛愣了愣。而此时，打手一个又一个出现，各种武器朝你们袭来。你见姬玛束手束脚，便拿起她的大剑一挥，剑身掀翻了打手，也撞倒了一旁的赌资。"
  },
  [111270561] = {
    id = 111270561,
    name = "啊啊啊好多欧泊——！！！！我的！那是我的！！"
  },
  [111270562] = {
    id = 111270562,
    name = "闪亮的欧泊撒了一地，遭到人群哄抢，你和姬玛趁乱带着决斗场老板逃出了决斗场。"
  },
  [111270563] = {
    id = 111270563,
    name = "你们逃到了附近的森林。"
  },
  [111270564] = {
    id = 111270564,
    name = "姬玛气愤地踢了一脚，决斗场老板狼狈地在地上滚了一圈。"
  },
  [111270565] = {
    id = 111270565,
    name = "你，你不能杀我！！"
  },
  [111270566] = {
    id = 111270566,
    name = "反正你不打算告诉我，大不了你死了之后，我再继续找。深渊这么大，总有人认识西比尔！"
  },
  [111270567] = {
    id = 111270567,
    name = "我的手下要是知道你们杀了我！一定不会放过你们的！"
  },
  [111270568] = {
    id = 111270568,
    name = "他们现在被观众们拦着自身难保，再说，我们只需要把你丢在森林里就好了。是想被魔物咬死，还是想好好活着，你自己选吧。"
  },
  [111270569] = {id = 111270569, name = "......"},
  [111270570] = {
    id = 111270570,
    name = "西比尔......西比尔很早之前就离开了。"
  },
  [111270571] = {
    id = 111270571,
    name = "什么意思？"
  },
  [111270572] = {
    id = 111270572,
    name = "那个叫西比尔的女人来到决斗场之后，也报名参加了挑战，她说她只是缺钱用。可我没想到她竟然能打败所有的人。"
  },
  [111270573] = {
    id = 111270573,
    name = "是吗......咳咳，能成为我的对手，当然很厉害。然后呢？"
  },
  [111270574] = {
    id = 111270574,
    name = "她赢了很多钱。我当然不可能放她那么轻松地离开。"
  },
  [111270575] = {
    id = 111270575,
    name = "你把她怎么样了？！"
  },
  [111270576] = {
    id = 111270576,
    name = "没有！！我，我只是想留下她！哪知道她竟然单枪匹马杀出了决斗场，那一次之后，决斗场伤筋动骨，停业了一个多月，西比尔的名声也被那些观众传出去了。"
  },
  [111270577] = {
    id = 111270577,
    name = "我不信。"
  },
  [111270578] = {
    id = 111270578,
    name = "真的！就是这样，西比尔已经不在这里了！我都告诉你了！你可以放了我吧？！"
  },
  [111270579] = {
    id = 111270579,
    name = "决斗场老板挣扎着坐了起来。"
  },
  [111270580] = {
    id = 111270580,
    name = "锵——！"
  },
  [111270581] = {
    id = 111270581,
    name = "大剑随后在他眼前落下，深陷入地，剑光衬得他脸色惨白。"
  },
  [111270582] = {
    id = 111270582,
    name = "我只知道这么多了！你说过要放了我的！"
  },
  [111270583] = {
    id = 111270583,
    name = "我什么时候说过？！像你这种人渣，就应该被扔进魔物堆里！"
  },
  [111270584] = {
    id = 111270584,
    name = "姬玛又狠狠地踢了他好几脚，决斗场老板连连惨叫。"
  },
  [111270585] = {
    id = 111270585,
    name = "如果当初，西比尔被抓回来了，是不是会和那些堕落者一样？嗯？！"
  },
  [111270586] = {
    id = 111270586,
    name = "饶......饶命......"
  },
  [111270587] = {
    id = 111270587,
    name = "决斗场老板昏迷了过去，姬玛还想再锤两拳的时候被你制止了。"
  },
  [111270588] = {
    id = 111270588,
    name = "既然那些药剂只有他知道，那我还得把他送去公会交差。"
  },
  [111270589] = {
    id = 111270589,
    name = "你在帮公会调查？"
  },
  [111270590] = {
    id = 111270590,
    name = "哎......本来只是想确认地下决斗场是否有堕落者的存在，现在不小心把老板抓了。唔，看来得加价。"
  },
  [111270591] = {
    id = 111270591,
    name = "......看在你帮了我的份上，这家伙就送给你了。"
  },
  [111270592] = {
    id = 111270592,
    name = "看着姬玛拔剑转身而去，你叫住了她。"
  },
  [111270593] = {
    id = 111270593,
    name = "走这边，那边可能会遇到他的那些手下。"
  },
  [111270594] = {
    id = 111270594,
    name = "哼，我会怕他们？"
  },
  [111270595] = {
    id = 111270595,
    name = "虽是这样说，姬玛还是换了个方向。突然，她似乎又想起了什么。"
  },
  [111270596] = {
    id = 111270596,
    name = "说起来，刚才你出手很利落啊，什么时候比一比？"
  },
  [111270597] = {
    id = 111270597,
    name = "以后会有机会的，你还要继续寻找西比尔吗？"
  },
  [111270598] = {
    id = 111270598,
    name = "嗯，我必须找到她。"
  },
  [111270599] = {
    id = 111270599,
    name = "那么，再会了。"
  },
  [111270600] = {id = 111270600, name = "喂！"},
  [111270601] = {id = 111270601, name = "嗯？"},
  [111270602] = {
    id = 111270602,
    name = "好歹也是我的崇拜者。难道你没有其他想说的了？"
  },
  [111270603] = {
    id = 111270603,
    name = "其他的？"
  },
  [111270604] = {
    id = 111270604,
    name = "好吧，再见了，我的崇拜者。"
  },
  [111270605] = {
    id = 111270605,
    name = "森林中的雾气逐渐笼罩了姬玛远去的身影。"
  },
  [111270606] = {
    id = 111270606,
    name = "阳光散发着昏黄的光芒，振翅的蝴蝶飞入了丛林，剑身反射而来的微弱光点时而闪烁，直到消失。"
  },
  [111310001] = {
    id = 111310001,
    name = "密林上空，乌云盖顶，裹挟着浊炁的风在深渊肆意狂舞，刮在人身上生疼。"
  },
  [111310002] = {
    id = 111310002,
    name = "看起来会有雷阵雨，先找一处避雨的地方吧。"
  },
  [111310003] = {
    id = 111310003,
    name = "你尽量避开魔物与变异的蚊蝇，很快走到了密林的边陲。"
  },
  [111310004] = {
    id = 111310004,
    name = "妈的，这天气也变幻太快了吧。那家伙到底会不会来？"
  },
  [111310005] = {
    id = 111310005,
    name = "（那边有人？）"
  },
  [111310006] = {
    id = 111310006,
    name = "我说，你是不是搞错了？"
  },
  [111310007] = {
    id = 111310007,
    name = "你在质疑老子的情报？"
  },
  [111310008] = {
    id = 111310008,
    name = "问一下又怎么了？要是耽误了事儿，老大得弄死我们。"
  },
  [111310009] = {
    id = 111310009,
    name = "声音是从一棵大树后传来的，你走近后看到身着黑衣斗篷的两个人。他们手持武器，却没有背包，不太像普通的探险者。"
  },
  [111310010] = {
    id = 111310010,
    name = "看到你的瞬间，他们忽然停止了争吵。"
  },
  [111310011] = {
    id = 111310011,
    name = "你们在这里做什么？"
  },
  [111310012] = {
    id = 111310012,
    name = "两人直直盯着你，你心中油然而生一股怪异的感觉，但仍然好心提醒了一句。"
  },
  [111310013] = {
    id = 111310013,
    name = "快下雨了。这里很危险，还是尽快离开吧。"
  },
  [111310014] = {
    id = 111310014,
    name = "一个人。"
  },
  [111310015] = {
    id = 111310015,
    name = "拿剑的。"
  },
  [111310016] = {
    id = 111310016,
    name = "带着一个包袱。"
  },
  [111310017] = {
    id = 111310017,
    name = "是他吧？"
  },
  [111310018] = {
    id = 111310018,
    name = "是的吧。"
  },
  [111310019] = {
    id = 111310019,
    name = "情报是这么说的。"
  },
  [111310020] = {
    id = 111310020,
    name = "也没有看见其他人了。"
  },
  [111310021] = {
    id = 111310021,
    name = "两人的表情同时一变，狞笑地朝你走来。"
  },
  [111310022] = {
    id = 111310022,
    name = "你来得正好！乖乖把你身上的东西全都交出来吧！"
  },
  [111310023] = {
    id = 111310023,
    name = "原来是劫匪啊，唉，早该预料到的。"
  },
  [111310024] = {
    id = 111310024,
    name = "我们可是......在这等你很久了啊！"
  },
  [111310025] = {
    id = 111310025,
    name = "黑衣人拍了拍手，四周潜伏的七八个人纷纷走了出来。"
  },
  [111310026] = {
    id = 111310026,
    name = "居然还有埋伏......"
  },
  [111310027] = {
    id = 111310027,
    name = "没错。别想着抵抗，否则你这条小命——"
  },
  [111310028] = {
    id = 111310028,
    name = "——嘭！你连武器都没拿出来，一拳就捶翻了眼前的黑衣人，他在地上打了好几个滚。"
  },
  [111310029] = {
    id = 111310029,
    name = "如果是打劫的话，我想你们找错了人。"
  },
  [111310030] = {
    id = 111310030,
    name = "紧接着，拳脚搏斗声在密林边缘回荡，夹杂着黑衣人声嘶力竭的咆哮声。"
  },
  [111310031] = {
    id = 111310031,
    name = "竟然敢反抗，兄弟们抓住他！你，你们后退什么！"
  },
  [111310032] = {
    id = 111310032,
    name = "唔！这个家伙......好厉害啊！和情报上讲的不一样啊！"
  },
  [111310033] = {
    id = 111310033,
    name = "啊......啊......你他妈的！竟然敢丢下老子先跑了！"
  },
  [111310034] = {
    id = 111310034,
    name = "我想起来了！他没有拿盾，不是这个人！"
  },
  [111310035] = {
    id = 111310035,
    name = "清脆的武器声响起，而后是一群人落荒而逃的景象。"
  },
  [111310036] = {
    id = 111310036,
    name = "妈的，你坑我！"
  },
  [111310037] = {
    id = 111310037,
    name = "看到你又准备出手，鼻青脸肿的黑衣人立刻求饶。"
  },
  [111310038] = {
    id = 111310038,
    name = "这......这位大人！你说的对。我们找错人了......真的找错人了！"
  },
  [111310039] = {
    id = 111310039,
    name = "是吗，可是你们本来也不是什么好人吧，能打劫到我的头上，算你们倒霉。"
  },
  [111310040] = {
    id = 111310040,
    name = "他慌张地扔出一颗魔法弹，砰地一声炸开！"
  },
  [111310041] = {
    id = 111310041,
    name = "好臭......"
  },
  [111310042] = {
    id = 111310042,
    name = "随着恶臭的黑色烟雾蔓延，你失去了黑衣人的踪影。"
  },
  [111310043] = {
    id = 111310043,
    name = "竟然逃掉了，真狡猾啊。"
  },
  [111310044] = {
    id = 111310044,
    name = "滴答滴答——"
  },
  [111310045] = {
    id = 111310045,
    name = "下雨了。"
  },
  [111310046] = {
    id = 111310046,
    name = "雨滴伴随着越刮越猛烈的大风，在暴风雨来临之前，你迅速离开了这片密林。"
  },
  [111310047] = {
    id = 111310047,
    name = "暴雨滂沱，你披着避雨斗篷在雨中快速前进。"
  },
  [111310048] = {
    id = 111310048,
    name = "看这雨势一时半会儿不会停了，这附近有一个据点，先去那边吧....."
  },
  [111310049] = {
    id = 111310049,
    name = "去往据点的途中，你看到前方出现了打斗的身影。有几个人围成了一个圈，与圈里的人对峙着。"
  },
  [111310050] = {
    id = 111310050,
    name = "闪电划过的刹那，那个女人的脸清晰地显现出来，白皙而刚毅。"
  },
  [111310051] = {
    id = 111310051,
    name = "是他们？"
  },
  [111310052] = {
    id = 111310052,
    name = "那些围着女人的正是之前打劫你的人。"
  },
  [111310053] = {
    id = 111310053,
    name = "难道说，她才是那些人的目标？"
  },
  [111310054] = {
    id = 111310054,
    name = "......还给我。"
  },
  [111310055] = {
    id = 111310055,
    name = "女人的声音淹没在大雨和雷电的嘈杂声中。"
  },
  [111310056] = {
    id = 111310056,
    name = "她一个盾击挡掉了身后黑衣人的偷袭，大剑划破雨幕，接着拍飞了另一个黑衣人。"
  },
  [111310057] = {
    id = 111310057,
    name = "黑衣人摔在地上，整个人在泥水里滚了一圈，半张脸都被淤泥糊住了。他抹了一把脸，才勉强看清眼前站着的一双脚。"
  },
  [111310058] = {
    id = 111310058,
    name = "好巧，又见面了啊。"
  },
  [111310059] = {
    id = 111310059,
    name = "你俯视他沾满泥泞的脸，微笑着抽出了武器。"
  },
  [111310060] = {
    id = 111310060,
    name = "不、不要啊——！！！"
  },
  [111310061] = {
    id = 111310061,
    name = "他试图从地上爬起来，被你一脚踢翻在地踩着脖子。"
  },
  [111310062] = {
    id = 111310062,
    name = "——铛！"
  },
  [111310063] = {
    id = 111310063,
    name = "前方，四五个人再次冲向了那个女人。只见她抬起大剑抵挡，大雨冲刷着映射在剑身上面的双眼，在倾盆而下的暴雨里映照着微亮的光。"
  },
  [111310064] = {
    id = 111310064,
    name = "下一刻，几人被齐齐震开。"
  },
  [111310065] = {
    id = 111310065,
    name = "是你！！"
  },
  [111310066] = {
    id = 111310066,
    name = "其中一个人看到了你，脸色一变。"
  },
  [111310067] = {
    id = 111310067,
    name = "快走，大家快走！"
  },
  [111310068] = {
    id = 111310068,
    name = "不行！站住！"
  },
  [111310069] = {
    id = 111310069,
    name = "女人厉声呵斥，可那些黑衣人如同入水的鱼，一溜烟不见了踪影。"
  },
  [111310070] = {
    id = 111310070,
    name = "她背起了剑盾，追了几步再也看不到那群人的影子。"
  },
  [111310071] = {
    id = 111310071,
    name = "又丢下我，这群该死的家伙！！"
  },
  [111310072] = {
    id = 111310072,
    name = "听到人声，女人回过头，看到被你踩在脚下的黑衣人，加快脚步走了过来。"
  },
  [111310073] = {
    id = 111310073,
    name = "把东西还给我。"
  },
  [111310074] = {
    id = 111310074,
    name = "什么东西，我不知——啊！！！"
  },
  [111310075] = {
    id = 111310075,
    name = "你脚底一用力，黑衣人叫出了声。"
  },
  [111310076] = {
    id = 111310076,
    name = "我真的不知道，都是我们老大让我们干的啊......"
  },
  [111310077] = {
    id = 111310077,
    name = "把你知道的都告诉我。"
  },
  [111310078] = {
    id = 111310078,
    name = "女人站在你的对面，微微低着头，五指张开撩起她额前湿漉漉的头发，水珠从发尾滴落在睫毛上。"
  },
  [111310079] = {
    id = 111310079,
    name = "你抢了她的东西？"
  },
  [111310080] = {
    id = 111310080,
    name = "不是我，是老大！他让我们今天在这里埋伏一个人，抢到东西就带回去。其他的我真的什么都不知道啊......"
  },
  [111310081] = {
    id = 111310081,
    name = "所以你们伏击了我。"
  },
  [111310082] = {
    id = 111310082,
    name = "误会啊，那都是误会......"
  },
  [111310083] = {
    id = 111310083,
    name = "女人听到你们的对话后，抬起了琥珀色的双眼。"
  },
  [111310084] = {
    id = 111310084,
    name = "这个人可否交给我？被夺走的物品，我需要他找回来。"
  },
  [111310085] = {
    id = 111310085,
    name = "对了，我是深渊航线的辉夜。"
  },
  [111310086] = {
    id = 111310086,
    name = "深渊航线？那个很有名的雇佣组织啊。"
  },
  [111310087] = {
    id = 111310087,
    name = "你将黑衣人提了起来，他露出绝望的神色。"
  },
  [111310088] = {
    id = 111310088,
    name = "其实没那么麻烦，只要问出他们的驻地就好了。"
  },
  [111310089] = {
    id = 111310089,
    name = "辉夜认真地点点头，表示认同。"
  },
  [111310090] = {
    id = 111310090,
    name = "你说的不错，带上他只会耽误更多的时间。"
  },
  [111310091] = {
    id = 111310091,
    name = "喂喂！！你们不要把我当成工具用完就丢啊！"
  },
  [111310092] = {
    id = 111310092,
    name = "可是你目前的作用就是这个吧，再耽误时间的话，连工具的作用也会失去了。"
  },
  [111310093] = {id = 111310093, name = "！！！"},
  [111310094] = {
    id = 111310094,
    name = "这是恐吓吧！"
  },
  [111310095] = {
    id = 111310095,
    name = "黑衣人看辉夜并不像说笑的样子，连忙向你求救。"
  },
  [111310096] = {
    id = 111310096,
    name = "我把知道的都告诉你们，可是你们得答应不能杀我！"
  },
  [111310097] = {
    id = 111310097,
    name = "辉夜一脸疑惑地望着你。"
  },
  [111310098] = {
    id = 111310098,
    name = "你要杀他？"
  },
  [111310099] = {id = 111310099, name = "......"},
  [111310100] = {
    id = 111310100,
    name = "难道不是你想要杀我吗？！"
  },
  [111310101] = {
    id = 111310101,
    name = "我没想过杀你，如果你不告诉我的话......"
  },
  [111310102] = {
    id = 111310102,
    name = "你就怎么样？！"
  },
  [111310103] = {
    id = 111310103,
    name = "我就不问了。"
  },
  [111310104] = {id = 111310104, name = "......"},
  [111310105] = {
    id = 111310105,
    name = "说完就放你走。"
  },
  [111310106] = {
    id = 111310106,
    name = "黑衣人说了一个地点，就在附近据点的位置。"
  },
  [111310107] = {
    id = 111310107,
    name = "老大说了，拿到东西之后就去那汇合，这个任务失败了，我们也得死。两位大人，我也只是听命令行事。"
  },
  [111310108] = {
    id = 111310108,
    name = "黑衣人还在解释，辉夜已经转身而去。你离开前又看了一眼黑衣人。"
  },
  [111310109] = {
    id = 111310109,
    name = "干什么？你答应过不杀我的！"
  },
  [111310110] = {
    id = 111310110,
    name = "——唔啊！"
  },
  [111310111] = {
    id = 111310111,
    name = "这是为了防止你通风报信。"
  },
  [111310112] = {
    id = 111310112,
    name = "打晕了黑衣人，你看向离开的辉夜。"
  },
  [111310113] = {
    id = 111310113,
    name = "那种地方，竟然敢一个人去，不愧是深渊航线的护卫者啊。反正离得不远，顺便去看看吧。"
  },
  [111310114] = {
    id = 111310114,
    name = "进入据点后，雨渐渐小了，但街道上还是没什么人，你很快就追上了辉夜。"
  },
  [111310115] = {id = 111310115, name = "......"},
  [111310116] = {
    id = 111310116,
    name = "丢失的东西看起来对她很重要，但她依然走得不徐不疾。"
  },
  [111310117] = {
    id = 111310117,
    name = "嗯？你也来据点了。"
  },
  [111310118] = {
    id = 111310118,
    name = "其实，我打算跟你一起去。"
  },
  [111310119] = {
    id = 111310119,
    name = "不用了，我身上没有欧泊支付给你。"
  },
  [111310120] = {id = 111310120, name = "......"},
  [111310121] = {
    id = 111310121,
    name = "没关系，我也在找那群人。"
  },
  [111310122] = {
    id = 111310122,
    name = "原来是这样。"
  },
  [111310123] = {
    id = 111310123,
    name = "她没有再问下去，但依然以不徐不疾的速度向前走去。"
  },
  [111310124] = {
    id = 111310124,
    name = "（继续以这种速度走过去的话，人早跑光了吧。）"
  },
  [111310125] = {
    id = 111310125,
    name = "到达目的地后，辉夜在门前站定，敲了两下门。"
  },
  [111310126] = {
    id = 111310126,
    name = "......跟劫匪不用这么讲礼貌的。"
  },
  [111310127] = {
    id = 111310127,
    name = "——哐！！你一脚踹开了门，不出意外地叹了口气。"
  },
  [111310128] = {id = 111310128, name = "果然。"},
  [111310129] = {
    id = 111310129,
    name = "一个人也没有。"
  },
  [111310130] = {
    id = 111310130,
    name = "他们应该是知道我们会找上门，提前撤离了。"
  },
  [111310131] = {
    id = 111310131,
    name = "辉夜皱了皱眉，非常困扰的样子。"
  },
  [111310132] = {
    id = 111310132,
    name = "你......丢的那个东西是什么？很重要吗？"
  },
  [111310133] = {
    id = 111310133,
    name = "很重要。不过我不能说。"
  },
  [111310134] = {
    id = 111310134,
    name = "那接下来你打算怎么办？"
  },
  [111310135] = {
    id = 111310135,
    name = "如果丢失了护送的货物，要竭尽所能找回，确保货物完好无损地送至目的地，这是我们深渊航线的规则。"
  },
  [111310136] = {
    id = 111310136,
    name = "我出去看看有什么线索。"
  },
  [111310137] = {
    id = 111310137,
    name = "辉夜湿润的头发贴在脸上，衬得她的脸很白，却没有半点狼狈的感觉。她抹去下颌滴落的雨水，正要转身出门，就被你叫住了。"
  },
  [111310138] = {
    id = 111310138,
    name = "等一下，我来的时候已经确认过了，雨下得很大，外面没有留下痕迹。"
  },
  [111310139] = {
    id = 111310139,
    name = "没办法了吗......"
  },
  [111310140] = {
    id = 111310140,
    name = "不用着急，也许这间屋子能找到线索呢？"
  },
  [111310141] = {
    id = 111310141,
    name = "辉夜点点头，却又很为难地看着你。"
  },
  [111310142] = {
    id = 111310142,
    name = "你是在帮我吗？我没有欧泊，也不能替组织暂时雇佣你。"
  },
  [111310143] = {
    id = 111310143,
    name = "......这都是举手之劳而已。"
  },
  [111310144] = {
    id = 111310144,
    name = "我还是写个欠条给你吧。"
  },
  [111310145] = {
    id = 111310145,
    name = "为什么？"
  },
  [111310146] = {
    id = 111310146,
    name = "免费的才是最贵的。"
  },
  [111310147] = {
    id = 111310147,
    name = "......那就当作是你帮我吧。"
  },
  [111310148] = {id = 111310148, name = "什么？"},
  [111310149] = {
    id = 111310149,
    name = "那群家伙打劫了我，我要找他们报仇。"
  },
  [111310150] = {
    id = 111310150,
    name = "那个人说是误会。"
  },
  [111310151] = {
    id = 111310151,
    name = "反正他们不是好人......啊，不。"
  },
  [111310152] = {
    id = 111310152,
    name = "我是为了正义，才想找你合作，这些人在深渊横行霸道，以多欺少。他们这些劫匪专门打劫那些独行的探险者，很多人也因此丧命。"
  },
  [111310153] = {
    id = 111310153,
    name = "既然让我遇到了，我一定要找到他们的据点，除恶务尽！"
  },
  [111310154] = {
    id = 111310154,
    name = "竟然是这样吗？你真是一个善良高尚的人。"
  },
  [111310155] = {
    id = 111310155,
    name = "不过，像这样的恶行在深渊太多，秉持公理和正义的太少，你一定要好好保护自己。如果需要帮助的话，我们组织会为雇主提供最安心的防护。"
  },
  [111310156] = {
    id = 111310156,
    name = "组织的成员们都像你一样，是强力又有信念的探险者。"
  },
  [111310157] = {
    id = 111310157,
    name = "辉夜一本正经地认同了你的说辞，介绍组织的时候，她的双眼闪着自信的亮光。看她还想继续说下去，你赶紧转移了话题。"
  },
  [111310158] = {
    id = 111310158,
    name = "啊......总之，我们先找找这间屋子有什么线索吧。"
  },
  [111310159] = {id = 111310159, name = "嗯。"},
  [111310160] = {
    id = 111310160,
    name = "片刻后......"
  },
  [111310161] = {
    id = 111310161,
    name = "这是什么？"
  },
  [111310162] = {
    id = 111310162,
    name = "辉夜将剑盾放在一边，蹲下看你手中的东西。"
  },
  [111310163] = {
    id = 111310163,
    name = "这几把铁制的工具，不像是武器。"
  },
  [111310164] = {
    id = 111310164,
    name = "是铁铲，很多拾荒者会用这个去山崖峭壁采集莹草。"
  },
  [111310165] = {
    id = 111310165,
    name = "可是他们是劫匪，不是拾荒者。难道，拾荒者只是他们用来掩饰自己身份的？"
  },
  [111310166] = {
    id = 111310166,
    name = "确实有这种可能性。"
  },
  [111310167] = {
    id = 111310167,
    name = "只凭这个无法得知他们会去哪里，但我们也不能就这样放弃。"
  },
  [111310168] = {
    id = 111310168,
    name = "那我们就去找找这些“拾荒者”会去哪儿。"
  },
  [111310169] = {
    id = 111310169,
    name = "辉夜点头同意。你见她身边宽大的盾，顺手去提。"
  },
  [111310170] = {
    id = 111310170,
    name = "现在暂时不用战斗，我帮你拿吧，唔！！"
  },
  [111310171] = {
    id = 111310171,
    name = "那一瞬间，你露出了扭曲的表情。"
  },
  [111310172] = {
    id = 111310172,
    name = "（好重！）"
  },
  [111310173] = {
    id = 111310173,
    name = "下一刻，辉夜单手从你手里接过了盾，轻松地背在背上。"
  },
  [111310174] = {
    id = 111310174,
    name = "谢谢。不过还是我自己来拿吧。剑与盾承载着我的意志和信念，不能让别人来背负。"
  },
  [111310175] = {
    id = 111310175,
    name = "辉夜走向门口，屋外大雨已经停了。交叠的乌云间隙中透出金箔般的光，洒落在她身上。"
  },
  [111310176] = {id = 111310176, name = "......"},
  [111310177] = {
    id = 111310177,
    name = "屋外有三两个因为雨停而出行的探险者，辉夜不厌其烦地依次询问，总算是打听到了一些消息。"
  },
  [111310178] = {
    id = 111310178,
    name = "啊，说起那些拾荒者，平时都会出现在附近的。这几天确实没有见到了，你们要想找他们只有去贫民窟吧。"
  },
  [111310179] = {
    id = 111310179,
    name = "贫民窟吗？"
  },
  [111310180] = {
    id = 111310180,
    name = "路人给你们指了贫民窟的方向，是在据点外的一个小山坡上。"
  },
  [111310181] = {
    id = 111310181,
    name = "辉夜道谢之后立刻动身了，但依旧是不徐不疾的步伐。你两步追上了她，和她并肩而行。"
  },
  [111310182] = {
    id = 111310182,
    name = "你们来到了贫民窟——残缺的门板横在地上，损坏的屋顶，腐烂的墙面，被大雨冲刷过的院落破败又荒凉。"
  },
  [111310183] = {
    id = 111310183,
    name = "有人在吗？"
  },
  [111310184] = {
    id = 111310184,
    name = "辉夜提高了声音，里面无人应答。"
  },
  [111310185] = {
    id = 111310185,
    name = "情况不对劲。就算这里不是黑衣人组织的营地，总该有真的拾荒者才对。"
  },
  [111310186] = {
    id = 111310186,
    name = "我去前面探查一下。"
  },
  [111310187] = {
    id = 111310187,
    name = "辉夜说完便进入了贫民窟。你本想一起去，却在门口地面上发现了些奇怪的纹路。"
  },
  [111310188] = {
    id = 111310188,
    name = "嗯？这是什么？"
  },
  [111310189] = {
    id = 111310189,
    name = "你拂开地面的杂草。雕刻的一条条浅浅沟壑里，还有大雨未能冲刷干净的、暗红色的血迹。"
  },
  [111310190] = {
    id = 111310190,
    name = "......有魔法的波动。这是魔法阵？！"
  },
  [111310191] = {
    id = 111310191,
    name = "那些宛如黑泥的血渍在大雨之后露出了原本的红色，缓缓地顺着纹路流淌。"
  },
  [111310192] = {
    id = 111310192,
    name = "快过来！"
  },
  [111310193] = {
    id = 111310193,
    name = "辉夜仓促的声音从院里传出，你冲了进去。"
  },
  [111310194] = {
    id = 111310194,
    name = "他们、他们......"
  },
  [111310195] = {
    id = 111310195,
    name = "辉夜愣愣地站在一间推开的屋门前，神情紧绷，甚至......带有愤怒。"
  },
  [111310196] = {
    id = 111310196,
    name = "四四方方的狭窄的空间里，横七竖八摆满了拾荒者的尸体。他们中有老人、女人、甚至小孩，这些人生前都受到了不同程度的割伤或刺伤。"
  },
  [111310197] = {
    id = 111310197,
    name = "很显然，都是被利器放干了血液。"
  },
  [111310198] = {
    id = 111310198,
    name = "这就是......为什么拾荒者没有再出现......"
  },
  [111310199] = {
    id = 111310199,
    name = "辉夜弯下腰，拾起了门前落下的一节破烂的铃铛，轻轻放在了离她最近的孩子的手心。"
  },
  [111310200] = {
    id = 111310200,
    name = "为什么？为什么弱者就一定要被欺凌，被牺牲和践踏？"
  },
  [111310201] = {
    id = 111310201,
    name = "正因为这样，他们才聚在一起，放弃尊严也想要活下来，这就是他们拾荒者的生存之道。"
  },
  [111310202] = {
    id = 111310202,
    name = "虽然懦弱，但这却是他们唯一可以选择的活下去的方式。可惜，总有一些人，为谋取自己的利益而不择手段。"
  },
  [111310203] = {id = 111310203, name = "利益？"},
  [111310204] = {
    id = 111310204,
    name = "我在门口发现了魔法阵，他们把拾荒者的鲜血注入了魔法阵。那个魔法阵，一定不简单。"
  },
  [111310205] = {
    id = 111310205,
    name = "辉夜沉默了好一阵，呼出了一口气。"
  },
  [111310206] = {
    id = 111310206,
    name = "......你说的不错，绝对，不能放过那群人。"
  },
  [111310207] = {
    id = 111310207,
    name = "辉夜小姐，冒昧问一下，他们究竟拿走了什么东西？"
  },
  [111310208] = {
    id = 111310208,
    name = "为什么问这个？"
  },
  [111310209] = {
    id = 111310209,
    name = "那些黑衣人的目的不简单，如果是交给深渊航线护送的话，一定是非常重要的东西吧。"
  },
  [111310210] = {
    id = 111310210,
    name = "我也不知道。"
  },
  [111310211] = {id = 111310211, name = "诶？"},
  [111310212] = {
    id = 111310212,
    name = "深渊航线的雇主不问来路，所要护送的物品由组织登记，护送者不会知道护送的是什么。不得觊觎雇主的任何物品，是我们的准则之一。"
  },
  [111310213] = {
    id = 111310213,
    name = "很少有人面对财富不动心，这样也是为了约束组织成员，只当他是一件物品就好。不管是食物还是圣物，一视同仁。"
  },
  [111310214] = {
    id = 111310214,
    name = "你就不好奇吗？"
  },
  [111310215] = {
    id = 111310215,
    name = "我没兴趣。只要我接受了雇佣任务，就一定会全力以赴。"
  },
  [111310216] = {
    id = 111310216,
    name = "这样啊，那看来也无法知道雇主是谁了。"
  },
  [111310217] = {
    id = 111310217,
    name = "我知道雇主是谁，但我不能告诉你。"
  },
  [111310218] = {id = 111310218, name = "......"},
  [111310219] = {
    id = 111310219,
    name = "这是深渊航线的规定。"
  },
  [111310220] = {
    id = 111310220,
    name = "还真是让人放心的规定呢。"
  },
  [111310221] = {
    id = 111310221,
    name = "是的，深渊航线拥有着绝对的秩序。我加入这个组织的原因，也是因为它和我想守护正义和善良的目的是一致的。"
  },
  [111310222] = {
    id = 111310222,
    name = "所以，这一次......我不光要找回物品，更要斩除罪恶！"
  },
  [111310223] = {
    id = 111310223,
    name = "她的表情认真而坚定，你心底忽然有什么被触动了。"
  },
  [111310224] = {
    id = 111310224,
    name = "能在黑暗的深渊中守住这难得的信念，真的令我非常敬佩。"
  },
  [111310225] = {
    id = 111310225,
    name = "深渊风雨如晦，恶行不止。辉夜小姐，这一次你我并肩作战，消除罪恶。如何？"
  },
  [111310226] = {id = 111310226, name = "当然。"},
  [111310227] = {
    id = 111310227,
    name = "辉夜斩钉截铁地回答，展开了一个微笑。意识到什么，她忽然又回到了平日里一本正经的样子。"
  },
  [111310228] = {
    id = 111310228,
    name = "这里其他地方我都看过了，暂时没有别的线索。"
  },
  [111310229] = {
    id = 111310229,
    name = "我再仔细找找，也许会发现蛛丝马迹。"
  },
  [111310230] = {
    id = 111310230,
    name = "沙沙、沙沙......"
  },
  [111310231] = {
    id = 111310231,
    name = "什么声音？"
  },
  [111310232] = {
    id = 111310232,
    name = "缓慢的脚步声由远及近，你们立即警觉起来。"
  },
  [111310233] = {
    id = 111310233,
    name = "辉夜小姐，线索来了。"
  },
  [111310234] = {
    id = 111310234,
    name = "一个黑衣人打着哈欠走进了贫民窟，你还没来得及思考，辉夜已经冲出去了。"
  },
  [111310235] = {
    id = 111310235,
    name = "接招吧！"
  },
  [111310236] = {
    id = 111310236,
    name = "——是你！！怎么又是你们！！"
  },
  [111310237] = {
    id = 111310237,
    name = "黑衣人瞪大眼睛，拔腿就逃！"
  },
  [111310238] = {id = 111310238, name = "站住！"},
  [111310239] = {
    id = 111310239,
    name = "嘴上说站住，但辉夜跑动的速度并不快，看着黑衣人越来越远，你一个箭步追了上去。"
  },
  [111310240] = {
    id = 111310240,
    name = "我去抓他！"
  },
  [111310241] = {
    id = 111310241,
    name = "呼，跑得真快。"
  },
  [111310242] = {
    id = 111310242,
    name = "黑衣人轻车熟路地狂奔，然后一头扎进了山林里不见了。"
  },
  [111310243] = {
    id = 111310243,
    name = "你听到山林中隐约传来的魔物声，犹豫了一下，暂时停了下来。隔了好一阵，辉夜才赶了过来，你指了指山上。"
  },
  [111310244] = {
    id = 111310244,
    name = "他进去了。"
  },
  [111310245] = {
    id = 111310245,
    name = "山里吗......那我们也进去。"
  },
  [111310246] = {
    id = 111310246,
    name = "等等，那个人显然对这里很熟悉。我们贸然进去，也许会出现不可控的情况。"
  },
  [111310247] = {
    id = 111310247,
    name = "但那个人，是目前唯一的线索吧。就算前方有危险，我也要试一试。"
  },
  [111310248] = {
    id = 111310248,
    name = "辉夜声音清冽，缓慢却又沉着，如同她的步伐一样没有半点动摇。"
  },
  [111310249] = {id = 111310249, name = "呜——"},
  [111310250] = {
    id = 111310250,
    name = "魔物叫声回荡在山林间，令人毛骨悚然。"
  },
  [111310251] = {
    id = 111310251,
    name = "那就由我先去探探路吧。"
  },
  [111310252] = {
    id = 111310252,
    name = "山林里密集地布满了宽叶植物和茂盛的树木，黑衣人早就失去了踪影。"
  },
  [111310253] = {
    id = 111310253,
    name = "你小心翼翼地走在前方，辉夜紧随在你身后。进入备战状态后，你们二人之间再无言语，全身心警戒着四周。"
  },
  [111310254] = {
    id = 111310254,
    name = "果然没多久，周围的树丛就传出了一阵窸窸窣窣的骚动。就像有什么匍匐在泥土上，快速地刨动肢体朝你们靠近。"
  },
  [111310255] = {
    id = 111310255,
    name = "你警惕地攥紧了手中的武器，盯紧了那片正在颤动的树丛。颤动逐渐逼近，在离你们不远的地方停了下来。"
  },
  [111310256] = {id = 111310256, name = "......"},
  [111310257] = {
    id = 111310257,
    name = "你朝辉夜比了个手势，与她背靠背站立。"
  },
  [111310258] = {
    id = 111310258,
    name = "远处发出一声怪异的“咻”声。静默了半晌，树丛中的东西猛地跃起，飞扑向你们二人！"
  },
  [111310259] = {id = 111310259, name = "吼——"},
  [111310260] = {
    id = 111310260,
    name = "十余只最常见的、毫无智力的低等魔物沾满了树叶与泥土，露出坚硬的爪牙，整齐划一俯身而下攻击！"
  },
  [111310261] = {
    id = 111310261,
    name = "——锵！！你和辉夜同时出手，没多久就将其斩杀殆尽。"
  },
  [111310262] = {
    id = 111310262,
    name = "解决了吗？"
  },
  [111310263] = {
    id = 111310263,
    name = "算是吧。"
  },
  [111310264] = {
    id = 111310264,
    name = "那继续出发吧。"
  },
  [111310265] = {
    id = 111310265,
    name = "等一下，我们忽略了一件事。我刚才跟着黑衣人的路线一直走，但很明显中途他将魔物和自己的线路重合，然后改变了方向。"
  },
  [111310266] = {
    id = 111310266,
    name = "之后这些痕迹，都是魔物留下的。也许，我们已经进入了魔物栖息的领地了。"
  },
  [111310267] = {
    id = 111310267,
    name = "又让那个人跑了吗？"
  },
  [111310268] = {
    id = 111310268,
    name = "我们先离开这里吧，其他魔物很快会闻声而来的。"
  },
  [111310269] = {
    id = 111310269,
    name = "此时，远处再次响起了怪异的“咻”声。仿佛是鸟叫，又像是口哨般，越来越多魔物如波浪一般涌了过来！"
  },
  [111310270] = {
    id = 111310270,
    name = "来不及了，跑！"
  },
  [111310271] = {id = 111310271, name = "吼！！"},
  [111310272] = {
    id = 111310272,
    name = "你拉着辉夜加快奔跑的速度。"
  },
  [111310273] = {id = 111310273, name = "！！！"},
  [111310274] = {
    id = 111310274,
    name = "辉夜感觉脚下的土地在晃动，霎时，无数的根茎触须冲出地面，缠上她的脚！"
  },
  [111310275] = {id = 111310275, name = "小心！"},
  [111310276] = {
    id = 111310276,
    name = "辉夜果断用力放下了盾，瞬间斩断了触须，格挡住了它们的进攻。但下一秒，那些根须爬上了盾面，妄图将盾面吞噬。"
  },
  [111310277] = {
    id = 111310277,
    name = "以我之盾，护卫光明！"
  },
  [111310278] = {
    id = 111310278,
    name = "盾上光芒暴涨，缠绕的根须瞬间化为了灰烬。其他根须立刻钻进地下，从另一方攻击而来。"
  },
  [111310279] = {
    id = 111310279,
    name = "小心这边！"
  },
  [111310280] = {
    id = 111310280,
    name = "你用剑接连斩断根须，那些潜伏着的魔物也冲了出来，与你们二人展开了激烈的拼杀！"
  },
  [111310281] = {
    id = 111310281,
    name = "一段时间后......"
  },
  [111310282] = {
    id = 111310282,
    name = "你们踩在成堆的魔物尸体上，被存活的魔物团团包围，而远方还有更多的魔物正在靠近。"
  },
  [111310283] = {
    id = 111310283,
    name = "呼，不行。这样不仅杀不完魔物，我们还会耗死在这里。"
  },
  [111310284] = {id = 111310284, name = "咻——"},
  [111310285] = {
    id = 111310285,
    name = "仿佛接收到某种讯号，那些魔物又兴奋地加快了速度。辉夜和你对视一眼，察觉到了异常。"
  },
  [111310286] = {
    id = 111310286,
    name = "有什么在控制魔物！"
  },
  [111310287] = {
    id = 111310287,
    name = "魔物不是狼群，他们没有头狼，只屈服于阶级压制。"
  },
  [111310288] = {
    id = 111310288,
    name = "可是这些都是低级魔物，并没有头目级魔物。"
  },
  [111310289] = {
    id = 111310289,
    name = "唔，控制他们的，看来并不是魔物啊......"
  },
  [111310290] = {
    id = 111310290,
    name = "循着哨声的方向，你们同时看到了有黑影一闪而过。"
  },
  [111310291] = {
    id = 111310291,
    name = "在那边！你去追！"
  },
  [111310292] = {
    id = 111310292,
    name = "那你——"
  },
  [111310293] = {
    id = 111310293,
    name = "我没事。"
  },
  [111310294] = {
    id = 111310294,
    name = "她一挥大剑，为你斩出了一条路。"
  },
  [111310295] = {
    id = 111310295,
    name = "我的速度不如你，但这些低级的魔物想要拦下我，绝不可能！"
  },
  [111310296] = {
    id = 111310296,
    name = "辉夜眼神坚定，令你不自禁地信任。"
  },
  [111310297] = {
    id = 111310297,
    name = "好，我先去了！"
  },
  [111310298] = {
    id = 111310298,
    name = "将后背交给辉夜，你追踪着那抹黑色的身影，距离不断拉近。"
  },
  [111310299] = {id = 111310299, name = "别跑！"},
  [111310300] = {
    id = 111310300,
    name = "你拾起一根细长的树枝，赋予了魔法与力量的树枝穿破浊炁的迷障，朝黑影飞刺而去！"
  },
  [111310301] = {id = 111310301, name = "呃——"},
  [111310302] = {
    id = 111310302,
    name = "前方传出一声闷哼，那黑影跌落在地，随后周围归于平静。"
  },
  [111310303] = {
    id = 111310303,
    name = "你谨慎地拨开树叶，发现一处血迹，但没有见到黑衣人。"
  },
  [111310304] = {
    id = 111310304,
    name = "逃得真快啊。"
  },
  [111310305] = {
    id = 111310305,
    name = "身后不急不缓的脚步声响起，你回头看到辉夜走了过来。"
  },
  [111310306] = {
    id = 111310306,
    name = "魔物都撤退了。"
  },
  [111310307] = {
    id = 111310307,
    name = "她平静地说着话，仿佛只是做了一件十分平常的事。"
  },
  [111310308] = {
    id = 111310308,
    name = "抱歉，又让那家伙逃走了。"
  },
  [111310309] = {
    id = 111310309,
    name = "你不必道歉。如果不是你截断了他的哨声，魔物会一直攻击下去。"
  },
  [111310310] = {
    id = 111310310,
    name = "可惜，线索在这里中断了。"
  },
  [111310311] = {
    id = 111310311,
    name = "那就重新再找吧。"
  },
  [111310312] = {
    id = 111310312,
    name = "辉夜小姐好像不会气馁诶。"
  },
  [111310313] = {
    id = 111310313,
    name = "有时间气馁，不如想办法解决。"
  },
  [111310314] = {
    id = 111310314,
    name = "她的双眸半敛了一瞬，像是回忆起了什么，接着转过身朝山下走去。"
  },
  [111310315] = {
    id = 111310315,
    name = "以前我路过这座山时，记得附近有一座据点离这里不远，那里有深渊航线的临时歇脚处，我们先去那边整顿一下吧。"
  },
  [111310316] = {
    id = 111310316,
    name = "就在这里，我们进去吧。"
  },
  [111310317] = {
    id = 111310317,
    name = "你拖着疲惫的双腿，跟在辉夜身后，停下来喘了好几口气。"
  },
  [111310318] = {
    id = 111310318,
    name = "辉夜没有半点疲态，背着重剑和大盾疑惑地看了过来。"
  },
  [111310319] = {
    id = 111310319,
    name = "很累吗？"
  },
  [111310320] = {
    id = 111310320,
    name = "哈哈没事，就是比想象中的路程远了点。"
  },
  [111310321] = {
    id = 111310321,
    name = "辉夜带着你来到深渊航线的临时歇脚处。"
  },
  [111310322] = {
    id = 111310322,
    name = "这里探险者进进出出，人人都很忙碌的样子。柜台前有穿着制服的成员看到辉夜，双眼一亮，朝她打招呼。"
  },
  [111310323] = {
    id = 111310323,
    name = "辉夜小姐！"
  },
  [111310324] = {
    id = 111310324,
    name = "你好，我们认识吗？"
  },
  [111310325] = {
    id = 111310325,
    name = "啊，忘了自我介绍，我是深渊航线第37号中转接引员，很高兴能为辉夜小姐服务！"
  },
  [111310326] = {
    id = 111310326,
    name = "诶？这位是......"
  },
  [111310327] = {
    id = 111310327,
    name = "这一次任务的同行人。"
  },
  [111310328] = {
    id = 111310328,
    name = "是新人吗？没有见过呢，不好意思。因为辉夜小姐实在太有名气了，所以忽略了你。"
  },
  [111310329] = {id = 111310329, name = "......"},
  [111310330] = {
    id = 111310330,
    name = "不知道辉夜小姐需要什么呢？我们可以为正在执行任务的成员提供少量食物、水以及药物，当然这需要从您这一次的报酬里提前扣除。"
  },
  [111310331] = {
    id = 111310331,
    name = "我想打听一些情报。"
  },
  [111310332] = {
    id = 111310332,
    name = "辉夜将黑衣人的情况告诉了他，但隐瞒了丢失物品这件事。"
  },
  [111310333] = {
    id = 111310333,
    name = "噢，除了这个，辉夜小姐还需要其他的吗？"
  },
  [111310334] = {
    id = 111310334,
    name = "暂时没有了。"
  },
  [111310335] = {
    id = 111310335,
    name = "好的，辉夜小姐请先休息一下。"
  },
  [111310336] = {
    id = 111310336,
    name = "组织成员微笑着进入了内间，辉夜回头和你示意，带你走到了附近的休息区。"
  },
  [111310337] = {
    id = 111310337,
    name = "旁边有几个探险者，他们看起来都是组织内的成员。有的躺在沙发上睡大觉，有的正坐着吃东西，也有的桌上堆放了很多资料在不停地埋头苦干......"
  },
  [111310338] = {
    id = 111310338,
    name = "你和辉夜找了个空位坐下，等待接引员的消息。"
  },
  [111310339] = {
    id = 111310339,
    name = "你需要什么补给吗？刚才没问你就拒绝了他提供的东西。"
  },
  [111310340] = {
    id = 111310340,
    name = "你平常都是一个人吗？"
  },
  [111310341] = {
    id = 111310341,
    name = "如果说执行任务的话，是的。"
  },
  [111310342] = {
    id = 111310342,
    name = "组织里会以个人能力分配合适的任务，除非遇到S级以上的任务，组织会为我分配人手，其他时候一般都是独立完成任务。"
  },
  [111310343] = {
    id = 111310343,
    name = "好厉害，辉夜小姐是组织中很重要的成员吧。"
  },
  [111310344] = {
    id = 111310344,
    name = "辉夜摇摇头。"
  },
  [111310345] = {
    id = 111310345,
    name = "能独立完成任务的不止我一个，组织中很多人都比我优秀。我很明白自己的缺点是什么，所以只有努力提升自己来弥补。"
  },
  [111310346] = {
    id = 111310346,
    name = "这样，在下一次、下下次的任务里，才能更好地完成。"
  },
  [111310347] = {
    id = 111310347,
    name = "辉夜小姐真的很喜欢这份工作呢。"
  },
  [111310348] = {
    id = 111310348,
    name = "因为组织和我的信念是相同的。虽然像这样的组织有很多，但深渊航线是不同的，它坚守着秩序与正义。"
  },
  [111310349] = {
    id = 111310349,
    name = "不管成员有怎样的追求，在这一点上，大家都是一样的。"
  },
  [111310350] = {
    id = 111310350,
    name = "附近有谈笑声传来。辉夜侧过头看着附近的成员们，每个人都在做着自己喜欢的事，像回到了久远的大陆，那些安稳宁静的时光。"
  },
  [111310351] = {
    id = 111310351,
    name = "每个人都不遗余力地去改变，这个深渊才会一点一点的变好。也正是因为他们，才让我觉得我不是一个人在坚守。"
  },
  [111310352] = {
    id = 111310352,
    name = "所以，我会一直做下去。守护善良与正义，这就是我选择这份工作的原因。"
  },
  [111310353] = {
    id = 111310353,
    name = "你正想开口，接引员已经拿着一根指节长的木筒走了过来。"
  },
  [111310354] = {
    id = 111310354,
    name = "辉夜小姐，您要的情报已经全部记载在这里了，如果还需要什么，请尽管吩咐我。"
  },
  [111310355] = {
    id = 111310355,
    name = "谢谢你。"
  },
  [111310356] = {
    id = 111310356,
    name = "接引员的脸红了，摸着后脑勺，而后开心地回到了柜台。"
  },
  [111310357] = {
    id = 111310357,
    name = "是你的崇拜者啊~"
  },
  [111310358] = {id = 111310358, name = "什么？"},
  [111310359] = {
    id = 111310359,
    name = "辉夜专心看着木筒里的情报，根本没听清你说了什么，她神色凝重，你直觉不妙。"
  },
  [111310360] = {
    id = 111310360,
    name = "有什么发现吗？"
  },
  [111310361] = {id = 111310361, name = "嗯。"},
  [111310362] = {
    id = 111310362,
    name = "辉夜指着羊皮卷的一处，是一个山谷的标志。"
  },
  [111310363] = {
    id = 111310363,
    name = "这个山谷......在这个据点附近，也离我们之前去过的那座山很近。"
  },
  [111310364] = {
    id = 111310364,
    name = "是的。先前有好几批探险者误入过这个山谷，都有去无回。"
  },
  [111310365] = {
    id = 111310365,
    name = "因为没有宝物和大型魔物的信息，就没有派专人探查里面的危险程度。组织也特意将这里标注了，让成员绕道而行。"
  },
  [111310366] = {
    id = 111310366,
    name = "但是近期他们发现了附近有人活动的痕迹，正是我们一直在寻找的黑衣人。"
  },
  [111310367] = {
    id = 111310367,
    name = "原来躲在这里啊。"
  },
  [111310368] = {
    id = 111310368,
    name = "根据时间来看，他们藏在山谷里很久了。"
  },
  [111310369] = {
    id = 111310369,
    name = "狡兔三窟，总算找到了他们的大本营了。"
  },
  [111310370] = {
    id = 111310370,
    name = "嗯，这一次，要让他们为自己的所作所为付出代价。"
  },
  [111310371] = {
    id = 111310371,
    name = "你们进入了山谷。"
  },
  [111310372] = {
    id = 111310372,
    name = "天色暗沉下来，浊炁越来越浓厚，但你们一路都没有发现魔物的身影。"
  },
  [111310373] = {
    id = 111310373,
    name = "这里的浊炁很重，不适合人类长期停驻，想不到他们竟然会选择这里。"
  },
  [111310374] = {
    id = 111310374,
    name = "或许这些浓厚的浊炁只是表象。"
  },
  [111310375] = {
    id = 111310375,
    name = "其实，这样危险的环境，可以多叫几个人来吧？我相信他们也会乐意帮忙。"
  },
  [111310376] = {
    id = 111310376,
    name = "你是指刚才那些成员吗？"
  },
  [111310377] = {
    id = 111310377,
    name = "到据点来的都是身负任务的，他们都有各自的任务。"
  },
  [111310378] = {
    id = 111310378,
    name = "辉夜抬头看向前方暗沉沉的雾气，毫不犹豫地继续前行。突然前方吹来一阵风，风吹散了部分雾气，露出了泥泞的山路。"
  },
  [111310379] = {
    id = 111310379,
    name = "路上是一些残破的武器和爬满小虫的人类白骨，看样子死了不止几个月。"
  },
  [111310380] = {
    id = 111310380,
    name = "看样子，我们找对了地方。"
  },
  [111310381] = {
    id = 111310381,
    name = "......嗯。"
  },
  [111310382] = {
    id = 111310382,
    name = "这群人的罪恶，到此为止了。"
  },
  [111310383] = {
    id = 111310383,
    name = "你们继续前行，看到越来越多人类活动的痕迹，以及远处的炊烟。"
  },
  [111310384] = {
    id = 111310384,
    name = "果然是像老鼠一样躲在阴沟里了啊。"
  },
  [111310385] = {
    id = 111310385,
    name = "快靠近黑衣人的营地时，十几个人围着篝火一边聊天一边烤着食物，高声谈论着粗俗不堪的话题。"
  },
  [111310386] = {
    id = 111310386,
    name = "见辉夜又像上次一样要走出去，你赶紧拉住了她。"
  },
  [111310387] = {id = 111310387, name = "嗯？"},
  [111310388] = {
    id = 111310388,
    name = "他们还没发现我们，我们可以先做一些准备。"
  },
  [111310389] = {id = 111310389, name = "......"},
  [111310390] = {
    id = 111310390,
    name = "看见你在草丛里布置各式各样的陷阱，辉夜陷入了沉思。一段时间后，你结束了陷阱的布置。"
  },
  [111310391] = {
    id = 111310391,
    name = "可以了。"
  },
  [111310392] = {
    id = 111310392,
    name = "......哦。好。"
  },
  [111310393] = {
    id = 111310393,
    name = "辉夜的眼里有一丝茫然，但很快又恢复了镇定。"
  },
  [111310394] = {
    id = 111310394,
    name = "接下来，我们要怎么做？"
  },
  [111310395] = {
    id = 111310395,
    name = "你是不习惯用这样阴损的方式吗？"
  },
  [111310396] = {
    id = 111310396,
    name = "不，我只是在想，这或许是他们总是逃跑的原因吧。固执地以为堂堂正正就可以战胜敌人，却连自己要护送的物品都无法守护。"
  },
  [111310397] = {
    id = 111310397,
    name = "当然不是。辉夜小姐光明磊落，和不择手段也要达到目的人当然不一样。"
  },
  [111310398] = {
    id = 111310398,
    name = "对于我来说，只要能抓住他们，不在乎用什么方法，因为我们的目的是正义的，这样就够了。"
  },
  [111310399] = {id = 111310399, name = "......"},
  [111310400] = {
    id = 111310400,
    name = "你不用去改变什么，保持你自己就很好。"
  },
  [111310401] = {
    id = 111310401,
    name = "辉夜若有所思，在听你讲每个陷阱的用处时，神色异常认真。"
  },
  [111310402] = {
    id = 111310402,
    name = "等会儿就由我去将他们引过来，剩下的就交给你了。"
  },
  [111310403] = {
    id = 111310403,
    name = "没问题。"
  },
  [111310404] = {
    id = 111310404,
    name = "——轰！一颗魔法弹砸进了火堆中，引得周围一片惊呼。"
  },
  [111310405] = {
    id = 111310405,
    name = "什么东西！"
  },
  [111310406] = {
    id = 111310406,
    name = "看、看不见了，老大！"
  },
  [111310407] = {
    id = 111310407,
    name = "有人袭击！"
  },
  [111310408] = {
    id = 111310408,
    name = "啊——！！"
  },
  [111310409] = {
    id = 111310409,
    name = "浓浓的烟雾中，黑衣人接二连三传来了惨叫。"
  },
  [111310410] = {
    id = 111310410,
    name = "你刚将一个黑衣人打倒，感觉身后有人，遂转身曲起小臂举剑格挡，接下了力量极大的一斩。你整个人被巨大的推力推出了很长一段距离，正好停在了烟雾之外。"
  },
  [111310411] = {id = 111310411, name = "找死！"},
  [111310412] = {
    id = 111310412,
    name = "黑衣人头目追着你前往陷阱的方向，身后的那群黑衣人也紧随着跟了上来。"
  },
  [111310413] = {
    id = 111310413,
    name = "（来得好！）"
  },
  [111310414] = {
    id = 111310414,
    name = "只要引进陷阱的范围，他们插翅也难飞。你且战且退，引得黑衣人攻了上来。"
  },
  [111310415] = {
    id = 111310415,
    name = "然而一个人对抗多人还是有些吃力，尽管陷阱就在不远处，但你总被拦截，这让藏在暗处的辉夜也有些着急。"
  },
  [111310416] = {
    id = 111310416,
    name = "你保持着高强度的战斗，眼看锐利的武器就要落在你的肩头，你侧身一滚，到达了约定的位置，立刻启动了陷阱！"
  },
  [111310417] = {id = 111310417, name = "！！！"},
  [111310418] = {
    id = 111310418,
    name = "同时数不清的武器也从你的头顶落下！"
  },
  [111310419] = {
    id = 111310419,
    name = "砰！千钧一发之际，辉夜的盾从天而降，挡开了那些武器。"
  },
  [111310420] = {
    id = 111310420,
    name = "啊啊啊，老大，救我！"
  },
  [111310421] = {
    id = 111310421,
    name = "老大！这里都是陷阱，我们上当了！"
  },
  [111310422] = {
    id = 111310422,
    name = "分布在四周的陷阱起了作用，那群黑衣人暂时失去了视野。你和辉夜果断反击，打了他们一个措手不及，而头目撤退得及时，没有掉入陷阱中。"
  },
  [111310423] = {
    id = 111310423,
    name = "竟然找到这里来了。"
  },
  [111310424] = {
    id = 111310424,
    name = "那些拾荒者和失踪者，都是你杀的吧。"
  },
  [111310425] = {
    id = 111310425,
    name = "那又怎么样，你们也会是他们一样的下场。"
  },
  [111310426] = {
    id = 111310426,
    name = "黑衣人头目将手放在嘴边，吹响了特殊的哨声。"
  },
  [111310427] = {
    id = 111310427,
    name = "这个声音......"
  },
  [111310428] = {
    id = 111310428,
    name = "我要抓住他，带他回去审判。"
  },
  [111310429] = {
    id = 111310429,
    name = "随着哨声的响起，更多的黑衣人涌了上来，你与辉夜对视一眼，做好了艰苦一战的准备。"
  },
  [111310430] = {
    id = 111310430,
    name = "一段时间后......"
  },
  [111310431] = {
    id = 111310431,
    name = "地上到处是黑衣人的血，大部分的黑衣人失去了战力。接连不断的战斗使你疲惫不堪，先前被困在陷阱中的黑衣人也快要脱困。"
  },
  [111310432] = {
    id = 111310432,
    name = "而辉夜的呼吸虽然急促，但神色未变，拿剑的手依然很稳。"
  },
  [111310433] = {
    id = 111310433,
    name = "有点意思啊~可惜，不能用来作为养料真是可惜。"
  },
  [111310434] = {id = 111310434, name = "什么？"},
  [111310435] = {
    id = 111310435,
    name = "我说，去~死~吧~！"
  },
  [111310436] = {
    id = 111310436,
    name = "黑衣人头目在魔法陷阱打开的那一刻，将一个人推了出来，一道光牢从天而降！"
  },
  [111310437] = {
    id = 111310437,
    name = "原地，一道猩红的魔法阵启动了。那个人惊恐地挣扎，不停地拍打光牢壁！"
  },
  [111310438] = {
    id = 111310438,
    name = "老大，不要啊！不要杀我，饶命！"
  },
  [111310439] = {
    id = 111310439,
    name = "出卖了组织的人，必须去死，我是让你死得更有价值。"
  },
  [111310440] = {
    id = 111310440,
    name = "不是......是他们逼我的！我什么都没说，我什么都没说！啊——！！！"
  },
  [111310441] = {
    id = 111310441,
    name = "那个人瞬间被魔法阵吞噬，化成一滩血水，辉夜瞳孔微缩。"
  },
  [111310442] = {
    id = 111310442,
    name = "——敌人将自己的下属献祭了，在眨眼之间。"
  },
  [111310443] = {id = 111310443, name = "小心！"},
  [111310444] = {
    id = 111310444,
    name = "与此同时，辉夜脚下的土地变成了一个大泥潭，她及时用剑抵住泥潭边缘，才没有直接坠落下去。"
  },
  [111310445] = {
    id = 111310445,
    name = "她想逃出去，却发现动作越大，被吞噬得越快。泥潭下有一双无形的手向下拉扯，她借助所有的力量也无法挣脱，只能减缓下沉的速度。"
  },
  [111310446] = {
    id = 111310446,
    name = "就这样去死吧。对了，你的东西我也笑纳了，我会好好利用它的。"
  },
  [111310447] = {
    id = 111310447,
    name = "黑衣人的嘴角咧到了耳根，看起来异常狰狞。"
  },
  [111310448] = {
    id = 111310448,
    name = "辉夜的下半身已经陷入泥沼中，你伸手去拉她，试了好几次都纹丝不动，单凭力量根本无法将她救上来。"
  },
  [111310449] = {
    id = 111310449,
    name = "呵呵呵呵......魔法阵得到了献祭，只能在目标被完全吞噬的时候才会结束它的力量。"
  },
  [111310450] = {id = 111310450, name = "该死。"},
  [111310451] = {
    id = 111310451,
    name = "泥沼里有绝对禁锢的魔法，你先别管我。"
  },
  [111310452] = {
    id = 111310452,
    name = "哈哈哈哈，她说的不错，她会就这样死掉，再强的探险者也逃不过我的阵法。"
  },
  [111310453] = {
    id = 111310453,
    name = "但一定是有限制的吧，否则你一开始就会用这个办法。现在一直没有献祭第二个人的原因，是有什么内情吧？"
  },
  [111310454] = {
    id = 111310454,
    name = "哼，不需要魔法阵杀你也是易如反掌，给我把他干掉！"
  },
  [111310455] = {
    id = 111310455,
    name = "那些黑衣人看起来也非常惧怕泥沼，只从一个方向袭击而来，你身后的辉夜正越陷越深。"
  },
  [111310456] = {
    id = 111310456,
    name = "在打退了好几人后，你发现泥沼已经没过了辉夜的胸口，那把撑在边缘的剑也没过了剑身。"
  },
  [111310457] = {id = 111310457, name = "辉夜！"},
  [111310458] = {
    id = 111310458,
    name = "你跪在泥沼边徒手挖出淤泥，一边挖一边对抗来袭击的黑衣人。"
  },
  [111310459] = {
    id = 111310459,
    name = "黑衣人头目自从使用魔法阵后，就站在远处默默地运行着魔法阵，看着你们走向死亡之门。"
  },
  [111310460] = {
    id = 111310460,
    name = "你知道如果再不将辉夜救上来，她会被泥潭一点一点彻底吞噬。"
  },
  [111310461] = {
    id = 111310461,
    name = "你坚持住，我救你出来！"
  },
  [111310462] = {
    id = 111310462,
    name = "你......走......吧。"
  },
  [111310463] = {id = 111310463, name = "不。"},
  [111310464] = {
    id = 111310464,
    name = "愤怒与恐惧交错地充斥着你的大脑，但在这一刻你忽然冷静了下来，猛然盯住那个黑衣人头目。"
  },
  [111310465] = {
    id = 111310465,
    name = "是他在控制魔法阵！我去杀了那个人！"
  },
  [111310466] = {
    id = 111310466,
    name = "看着你一路冲了过去，黑衣人头目的脸色一变，接着大吼起来。"
  },
  [111310467] = {
    id = 111310467,
    name = "你杀了我也没用！魔法阵已经启动，她还是要死！！"
  },
  [111310468] = {
    id = 111310468,
    name = "身上到处被刺伤，你却无所畏惧。黑衣人冲上来的时候，你夺走了他的武器。"
  },
  [111310469] = {
    id = 111310469,
    name = "你爆发出了仅剩的力气，把武器朝头目的方向投掷而去！"
  },
  [111310470] = {
    id = 111310470,
    name = "唔，该死！"
  },
  [111310471] = {
    id = 111310471,
    name = "黑衣人的脸上多了一道鲜红的血迹，魔法阵也在这一刻有了松动！你回头，发现辉夜的小半张脸已经陷入了泥潭。"
  },
  [111310472] = {
    id = 111310472,
    name = "死吧！她死定了！哈哈哈哈......"
  },
  [111310473] = {
    id = 111310473,
    name = "突然，黑衣人的表情凝固了，辉夜正杵着那把沉重的大剑，缓缓地挣脱而上。"
  },
  [111310474] = {
    id = 111310474,
    name = "不可能，怎么可能！魔法阵怎么会失败？！怎么会？！"
  },
  [111310475] = {
    id = 111310475,
    name = "欲望，是你们的牢笼。"
  },
  [111310476] = {
    id = 111310476,
    name = "血债，是你们的罪恶。"
  },
  [111310477] = {
    id = 111310477,
    name = "而这片山谷，会是你们的墓地。"
  },
  [111310478] = {
    id = 111310478,
    name = "辉夜沐浴着淤泥和血渍从沼泽里一步步走了出来，神色肃然。"
  },
  [111310479] = {
    id = 111310479,
    name = "为了正义与光明——"
  },
  [111310480] = {
    id = 111310480,
    name = "以我之剑，斩破长夜！"
  },
  [111310481] = {
    id = 111310481,
    name = "白金色的剑芒暴涨，劈在山谷之上，整个地面震动，众人被强大的余波震晕了过去。黑衣人头目目露惧色，转身就逃。"
  },
  [111310482] = {
    id = 111310482,
    name = "他要逃了！"
  },
  [111310483] = {
    id = 111310483,
    name = "我们追！"
  },
  [111310484] = {
    id = 111310484,
    name = "消失了。"
  },
  [111310485] = {
    id = 111310485,
    name = "不会，他受了伤，走不了多远。"
  },
  [111310486] = {
    id = 111310486,
    name = "追出山谷后便失去了黑衣人头目的踪影。你们停在他消失的附近，并没有离开。"
  },
  [111310487] = {
    id = 111310487,
    name = "真是一只狡猾的老鼠，我先去附近看看。"
  },
  [111310488] = {
    id = 111310488,
    name = "我留在这里。"
  },
  [111310489] = {
    id = 111310489,
    name = "大约一刻钟后，你回到了原地，看见辉夜朝你摇了摇头。"
  },
  [111310490] = {
    id = 111310490,
    name = "难道真的走了？"
  },
  [111310491] = {
    id = 111310491,
    name = "我没有听到任何动静。"
  },
  [111310492] = {
    id = 111310492,
    name = "突然，前方传来了说话声，一群探险队从你们身边走过。"
  },
  [111310493] = {
    id = 111310493,
    name = "其中有个人瞧着你们满身泥泞的样子，不由地多看了你们几眼。他在观察你们的同时，你也在观察他们。"
  },
  [111310494] = {
    id = 111310494,
    name = "黑衣人的同伙吗？看起来并不像。"
  },
  [111310495] = {id = 111310495, name = "等等。"},
  [111310496] = {
    id = 111310496,
    name = "辉夜叫住了那几个人。"
  },
  [111310497] = {
    id = 111310497,
    name = "你在叫我们吗？"
  },
  [111310498] = {id = 111310498, name = "是的。"},
  [111310499] = {
    id = 111310499,
    name = "我想请问一下，你们刚刚有没有看到什么奇怪的人。"
  },
  [111310500] = {
    id = 111310500,
    name = "探险者在你和辉夜之间来回扫视，用一种难以言喻的表情回答。"
  },
  [111310501] = {
    id = 111310501,
    name = "你们......就挺奇怪的。"
  },
  [111310502] = {
    id = 111310502,
    name = "除了我们。"
  },
  [111310503] = {id = 111310503, name = "......"},
  [111310504] = {
    id = 111310504,
    name = "除了我们，还有看到别人吗？"
  },
  [111310505] = {
    id = 111310505,
    name = "没了，没了。"
  },
  [111310506] = {
    id = 111310506,
    name = "他摆着双手，一脸劫后余生的样子，几步追上了同伴。"
  },
  [111310507] = {id = 111310507, name = "......"},
  [111310508] = {
    id = 111310508,
    name = "辉夜回到你身边，这时前方身着公会的制服的男人匆匆走来。"
  },
  [111310509] = {
    id = 111310509,
    name = "请等一下。"
  },
  [111310510] = {id = 111310510, name = "？？？"},
  [111310511] = {
    id = 111310511,
    name = "公会成员疑惑地望着你们二人。"
  },
  [111310512] = {
    id = 111310512,
    name = "你来的路上有看到其他人吗？"
  },
  [111310513] = {
    id = 111310513,
    name = "这位小姐是同伴走失了吗？"
  },
  [111310514] = {
    id = 111310514,
    name = "我们在找一个非常危险的人，需要立刻找到他。"
  },
  [111310515] = {
    id = 111310515,
    name = "先前我有看到一个穿黑衣服的，但看不清他的脸。他走得很匆忙，像是什么在追赶他。"
  },
  [111310516] = {
    id = 111310516,
    name = "没错，就是那个黑衣人。"
  },
  [111310517] = {
    id = 111310517,
    name = "他往那边去了。"
  },
  [111310518] = {
    id = 111310518,
    name = "公会成员似乎也着急赶路，来不及道别就走了。"
  },
  [111310519] = {id = 111310519, name = "谢谢。"},
  [111310520] = {
    id = 111310520,
    name = "辉夜有所察觉地回头，看着他匆匆离去。"
  },
  [111310521] = {
    id = 111310521,
    name = "怎么了，我们快去追他吧。"
  },
  [111310522] = {id = 111310522, name = "你看。"},
  [111310523] = {
    id = 111310523,
    name = "你顺着辉夜的方向看去。"
  },
  [111310524] = {id = 111310524, name = "他......"},
  [111310525] = {
    id = 111310525,
    name = "他走路踉跄，鲜血从制服里滴落，同时露出了里面的黑衣。"
  },
  [111310526] = {id = 111310526, name = "是他！"},
  [111310527] = {
    id = 111310527,
    name = "竟然装作公会的人！"
  },
  [111310528] = {
    id = 111310528,
    name = "“公会成员”此时回头看了一眼身后，立刻加快了速度奔跑。"
  },
  [111310529] = {
    id = 111310529,
    name = "这次你跑不掉了！"
  },
  [111310530] = {
    id = 111310530,
    name = "又不见了。"
  },
  [111310531] = {
    id = 111310531,
    name = "在那里！"
  },
  [111310532] = {
    id = 111310532,
    name = "你指向前方，山林的罅隙间，有一个隐藏的山洞，你和辉夜对视一眼，径直追了进去。"
  },
  [111310533] = {
    id = 111310533,
    name = "没想到躲在这里也被你们找到了。"
  },
  [111310534] = {
    id = 111310534,
    name = "你没想到的事有很多。"
  },
  [111310535] = {
    id = 111310535,
    name = "你这个该死的女人！如果不是你，我的兄弟们也不会死。"
  },
  [111310536] = {
    id = 111310536,
    name = "你怕死吗？"
  },
  [111310537] = {
    id = 111310537,
    name = "呵呵呵，你在说什么，你难道不怕死？"
  },
  [111310538] = {
    id = 111310538,
    name = "你的同伴不想死，为什么你要杀死他？"
  },
  [111310539] = {
    id = 111310539,
    name = "献祭了他，就有机会杀了你！只需要死一个，我们都能继续活着！有什么不对？"
  },
  [111310540] = {
    id = 111310540,
    name = "那你以前杀的那些人，拾荒者也好，探险者也好，都是为了活着吗？"
  },
  [111310541] = {
    id = 111310541,
    name = "当然是为了变强！呵呵......你是想知道变强的办法吗？那个魔法阵很有吸引力吧？你放了我，我告诉你全部。"
  },
  [111310542] = {
    id = 111310542,
    name = "我不想知道。当你为了私欲剥夺别人生命的时候，你已经失去了人性。你不配为人。"
  },
  [111310543] = {
    id = 111310543,
    name = "呸！什么算是人？"
  },
  [111310544] = {
    id = 111310544,
    name = "那些风餐露宿衣不蔽体，吃了上顿没下顿，随时被人抢被人杀的弱者，那算人吗？弱肉强食，才是这个深渊的本质！"
  },
  [111310545] = {
    id = 111310545,
    name = "混乱、饥饿、绝望、憎恨......"
  },
  [111310546] = {
    id = 111310546,
    name = "与其苟延残喘活在这个没有明天的深渊里，不如让我来给他们解脱，还能为我们这些人变强做出贡献！"
  },
  [111310547] = {
    id = 111310547,
    name = "冥顽不灵！"
  },
  [111310548] = {
    id = 111310548,
    name = "——轰！大剑垂直而下！"
  },
  [111310549] = {
    id = 111310549,
    name = "辉夜这一次出剑极其迅速毫无犹豫，瞬间就斩断了他的一只臂膀。"
  },
  [111310550] = {
    id = 111310550,
    name = "啊啊啊啊——！"
  },
  [111310551] = {
    id = 111310551,
    name = "惨叫声回荡在山谷。"
  },
  [111310552] = {
    id = 111310552,
    name = "辉夜仍然是平静凛然的表情。你在她身旁站定，以防黑衣人的反击。"
  },
  [111310553] = {
    id = 111310553,
    name = "该死该死该死该死！！"
  },
  [111310554] = {
    id = 111310554,
    name = "黑衣人头目不断地流出鲜血，他用剩余的一只手拿出了一瓶药，疯狂地倒入了嘴里，一颗不剩。"
  },
  [111310555] = {
    id = 111310555,
    name = "啊啊啊啊啊！"
  },
  [111310556] = {
    id = 111310556,
    name = "紧接着，流血的断臂止住了血，但并没有愈合，反而像鳞片一瓣一瓣地长了出来，就像是凭空长出了一根魔物的肢体。"
  },
  [111310557] = {
    id = 111310557,
    name = "他整个身子扭曲着，变化着，在他痛苦的叫声中，彻底变成了一只人形怪物。"
  },
  [111310558] = {
    id = 111310558,
    name = "吼——！！"
  },
  [111310559] = {
    id = 111310559,
    name = "堕落者！"
  },
  [111310560] = {
    id = 111310560,
    name = "堕落者每走一步，身上颤巍巍的鳞片也跟着一颤，断臂上长长的尖刺以迅雷之势疾袭而来，被辉夜的坚盾及时挡下！"
  },
  [111310561] = {id = 111310561, name = "唔！"},
  [111310562] = {
    id = 111310562,
    name = "强大的力道让辉夜向后滑了好几步。此时堕落者用白森森的牙嘶吼着，辉夜的脸上却出现了怜悯的神色。"
  },
  [111310563] = {
    id = 111310563,
    name = "原来是堕落者吗......"
  },
  [111310564] = {
    id = 111310564,
    name = "只能杀掉了。"
  },
  [111310565] = {
    id = 111310565,
    name = "在堕落者不断的强攻下，你和辉夜在盾后暂避。"
  },
  [111310566] = {
    id = 111310566,
    name = "堕落者每一次攻击都落在了那面盾牌上，引得他更为暴躁，发起了更猛烈的进攻！"
  },
  [111310567] = {
    id = 111310567,
    name = "好机会！"
  },
  [111310568] = {
    id = 111310568,
    name = "趁堕落者露出了破绽，你攻上前去，剑影在空中交织成网，瞄准了尖刺的根部，再一次生生将“臂膀”斩落了下来！"
  },
  [111310569] = {
    id = 111310569,
    name = "吼！！！"
  },
  [111310570] = {
    id = 111310570,
    name = "失去尖刺的堕落者攻击力大减，辉夜及时加入，带着锐利无比的气势，与狂暴的堕落者缠斗。"
  },
  [111310571] = {
    id = 111310571,
    name = "片刻后，满身血痕的堕落者朝天嘶吼，以同归于尽的方式扑向辉夜。辉夜调动起全身的力气，迎着堕落者扑来的方向，脸上没有半分惧色。"
  },
  [111310572] = {
    id = 111310572,
    name = "——嗤！！在那只长爪即将扑至时，大剑如虹，贯穿了堕落者的心脏。"
  },
  [111310573] = {
    id = 111310573,
    name = "呃.....啊......啊......"
  },
  [111310574] = {
    id = 111310574,
    name = "扭曲变异的手在即将抵达辉夜面部一寸前无力地垂落。"
  },
  [111310575] = {
    id = 111310575,
    name = "为......什......么......我的力量......不如......你......"
  },
  [111310576] = {
    id = 111310576,
    name = "光明身后必有阴影，烈焰之下必有灰烬，善恶在人心蔓延。"
  },
  [111310577] = {
    id = 111310577,
    name = "辉夜抽出大剑，失去支撑的堕落者跪坐在地，徐徐抬头，逐渐化为灰烬。"
  },
  [111310578] = {
    id = 111310578,
    name = "在彻底泯灭人性之前，就由我们来改变。"
  },
  [111310579] = {
    id = 111310579,
    name = "你......改变......不了......什么......都......"
  },
  [111310580] = {
    id = 111310580,
    name = "即使希望渺茫，我也会努力让这个世界变得更美好。"
  },
  [111310581] = {
    id = 111310581,
    name = "消散之前，堕落者恢复了本来的脸，他露出了讥讽的笑。"
  },
  [111310582] = {
    id = 111310582,
    name = "你......给公会卖命......你怎么......知道......他们......让你护送的......是什么......"
  },
  [111310583] = {
    id = 111310583,
    name = "（原来深渊航线这次的雇主是公会。）"
  },
  [111310584] = {
    id = 111310584,
    name = "一个精致的铁盒从堕落者身上掉出。彻底湮灭前，山洞中回荡着堕落者的声音。"
  },
  [111310585] = {
    id = 111310585,
    name = "打开看看......你就知道了......公会可没你想得......那么干净......"
  },
  [111310586] = {
    id = 111310586,
    name = "他说的是什么意思？"
  },
  [111310587] = {
    id = 111310587,
    name = "挑拨离间。你看不出来吗？"
  },
  [111310588] = {id = 111310588, name = "......"},
  [111310589] = {
    id = 111310589,
    name = "辉夜拾起盒子，仔细检查了一番，松了口气。"
  },
  [111310590] = {
    id = 111310590,
    name = "就是它，还好没有被打开过。"
  },
  [111310591] = {
    id = 111310591,
    name = "你真的不好奇里面是什么？"
  },
  [111310592] = {
    id = 111310592,
    name = "仅凭他的话，我没有理由破坏组织的规则，况且他也根本不值得信任。"
  },
  [111310593] = {
    id = 111310593,
    name = "话是这么说，但很多人都会忍不住想打开看看吧。你很有原则呢。"
  },
  [111310594] = {
    id = 111310594,
    name = "原则吗。我只是坚信我所坚信的。"
  },
  [111310595] = {
    id = 111310595,
    name = "你们走出山洞，外面已是夜色深深，云破月来。"
  },
  [111310596] = {
    id = 111310596,
    name = "这一次，非常感谢你。"
  },
  [111310597] = {
    id = 111310597,
    name = "难道不是我们一起完成了正义的使命吗？"
  },
  [111310598] = {
    id = 111310598,
    name = "我知道，那是你的借口。"
  },
  [111310599] = {
    id = 111310599,
    name = "呵呵，被识破了吗？"
  },
  [111310600] = {
    id = 111310600,
    name = "......我不知道该怎么说，任何财富和话语的感谢都很苍白。总之，以后需要帮忙，到深渊航线找我。"
  },
  [111310601] = {
    id = 111310601,
    name = "我知道了。"
  },
  [111310602] = {
    id = 111310602,
    name = "辉夜露出了轻柔的微笑。"
  },
  [111310603] = {
    id = 111310603,
    name = "嗯。时间不多了，我继续上路了。"
  },
  [111310604] = {
    id = 111310604,
    name = "这么晚也要走吗？"
  },
  [111310605] = {
    id = 111310605,
    name = "正好有月光，可以为我指明路途。那么，保重了。"
  },
  [111310606] = {
    id = 111310606,
    name = "你向辉夜挥手道别。"
  },
  [111310607] = {
    id = 111310607,
    name = "明月透过深林细碎地洒在她的剑盾之上，如披了一件银色的披风，成为了深渊目及之处唯一的光亮。"
  },
  [111460001] = {
    id = 111460001,
    name = "救命，救命啊！！"
  },
  [111460002] = {
    id = 111460002,
    name = "前面是......魔物？！"
  },
  [111460003] = {
    id = 111460003,
    name = "隐隐约约的咆哮声从前方传来，你疾跑赶去，见一个少年朝你跑来。"
  },
  [111460004] = {
    id = 111460004,
    name = "少年跌跌撞撞，身后跟着一头硕大的低级魔物。这时，魔物一爪子将少年拍在地上，你立即出手，直击魔物要害！"
  },
  [111460005] = {
    id = 111460005,
    name = "吼......呜......"
  },
  [111460006] = {
    id = 111460006,
    name = "魔物被你轻松斩杀，少年有气无力地爬起来，整个身子还在颤抖。"
  },
  [111460007] = {
    id = 111460007,
    name = "呼......呼......我......我没死......我得救了？大哥哥，谢谢！谢谢您！"
  },
  [111460008] = {
    id = 111460008,
    name = "你是......探险者？"
  },
  [111460009] = {
    id = 111460009,
    name = "少年身上的服饰由拼凑的布料制成，衣服磨损了好几处，腕带上附有魔纹，背上挂了一把破烂的弓箭，腰上挎着一个大包。看起来是个探险者，却又不像一个探险者。"
  },
  [111460010] = {
    id = 111460010,
    name = "不是......哦不，我是！这次只是意外！总之，谢谢您了！"
  },
  [111460011] = {
    id = 111460011,
    name = "他捏了捏腰间鼓鼓的挂包，朝你鞠了一躬，飞快地跑开了。"
  },
  [111460012] = {id = 111460012, name = "喂！"},
  [111460013] = {
    id = 111460013,
    name = "天色已近黄昏，不管那个少年是不是探险者，在这附近都不安全，你想提醒一声，可他已经跑远了。"
  },
  [111460014] = {
    id = 111460014,
    name = "沙沙......沙沙......"
  },
  [111460015] = {
    id = 111460015,
    name = "你离开的脚步一顿，只见一棵树后，一个影子灵活地窜了过去。"
  },
  [111460016] = {
    id = 111460016,
    name = "女孩子？"
  },
  [111460017] = {
    id = 111460017,
    name = "那女孩走了几步，探头探脑地看向少年消失的方向，女孩随之跟了上去。"
  },
  [111460018] = {
    id = 111460018,
    name = "嗯？跟踪？"
  },
  [111460019] = {
    id = 111460019,
    name = "你一时好奇，悄悄跟在了后面。"
  },
  [111460020] = {
    id = 111460020,
    name = "你跟着两人进入了一片黑森林。少年在最前方走着，时不时回头望一眼，而女孩灵活地躲避着他的视线，没被发现。"
  },
  [111460021] = {id = 111460021, name = "......"},
  [111460022] = {
    id = 111460022,
    name = "少年在四周徘徊了好一阵，确认没人后一头钻进了一棵树中。"
  },
  [111460023] = {id = 111460023, name = "！！！"},
  [111460024] = {
    id = 111460024,
    name = "女孩走了出来，围着那棵树惊叹地绕了好几圈。"
  },
  [111460025] = {
    id = 111460025,
    name = "消失了？！是秘密入口？！"
  },
  [111460026] = {
    id = 111460026,
    name = "她试探性地将手放在树上，看着手掌消失，惊讶地睁大了眼睛。然后她又向前跨了一步，彻底融入了树中。"
  },
  [111460027] = {
    id = 111460027,
    name = "......结界吗？"
  },
  [111460028] = {
    id = 111460028,
    name = "你靠近了那棵树。"
  },
  [111460029] = {
    id = 111460029,
    name = "不是屏障......"
  },
  [111460030] = {
    id = 111460030,
    name = "你触碰到那层伪装的树皮表面，没有感受到魔法的痕迹。"
  },
  [111460031] = {
    id = 111460031,
    name = "也不是结界。"
  },
  [111460032] = {
    id = 111460032,
    name = "在手伸入的瞬间，你明显感觉到一道狭窄的缝隙，恰好够一人通过。"
  },
  [111460033] = {
    id = 111460033,
    name = "那就进去看看吧。"
  },
  [111460034] = {
    id = 111460034,
    name = "你穿过了那道缝隙。"
  },
  [111460035] = {
    id = 111460035,
    name = "进入后，你在漆黑的通道行走了好一会儿。"
  },
  [111460036] = {
    id = 111460036,
    name = "前面有光......"
  },
  [111460037] = {
    id = 111460037,
    name = "走到尽头处，豁然开朗。入目是一条清澈的溪流，潺潺流动，冲刷着河底的鹅卵石，发出好听的碰撞声。"
  },
  [111460038] = {
    id = 111460038,
    name = "浊炁......变弱了......"
  },
  [111460039] = {
    id = 111460039,
    name = "你感受着周围清新的气息，溪流的另一方，树林茂密，随风摇曳，发出了好似下雨一般的飒飒声。"
  },
  [111460040] = {
    id = 111460040,
    name = "远处是一排排高低不一的小红房子，延绵至前方的山脚下。其间隐隐有炊烟升起，一切都像大陆上的村庄，幽静自然，安宁祥和。"
  },
  [111460041] = {
    id = 111460041,
    name = "很显然，这里是一个不受深渊魔物所侵扰的、被隔绝的世界。"
  },
  [111460042] = {
    id = 111460042,
    name = "埃拉，原来你住在这里啊~哇，这个地方也太棒了吧！"
  },
  [111460043] = {
    id = 111460043,
    name = "少女出现在你的视野中，那个被跟踪的少年也在，神情还带着些慌张。"
  },
  [111460044] = {
    id = 111460044,
    name = "小声一点！你怎么也来了？你不能在这里，你快走！"
  },
  [111460045] = {
    id = 111460045,
    name = "诶？为什么？"
  },
  [111460046] = {
    id = 111460046,
    name = "不为什么，总之你赶紧走吧！"
  },
  [111460047] = {
    id = 111460047,
    name = "喂喂，好歹我也帮过你~告诉我为什么啊~"
  },
  [111460048] = {
    id = 111460048,
    name = "岫，你帮我找到了药，我很感激，我也希望你能来我家做客。可是，西默蒂瑞不是我一个人的，是大家的。"
  },
  [111460049] = {
    id = 111460049,
    name = "在得到大家允许之后，我一定......一定正式邀请你。"
  },
  [111460050] = {
    id = 111460050,
    name = "这样啊......"
  },
  [111460051] = {
    id = 111460051,
    name = "埃拉露出歉意的表情，可在视线转向你的时候，他大惊失色。"
  },
  [111460052] = {
    id = 111460052,
    name = "岫，你你你你你你......竟然还带人进来了！"
  },
  [111460053] = {
    id = 111460053,
    name = "什么？！"
  },
  [111460054] = {
    id = 111460054,
    name = "这时岫回头正好看到了你，她琥珀般的双目瞪大，一脸不可置信的样子。"
  },
  [111460055] = {
    id = 111460055,
    name = "你是谁？"
  },
  [111460056] = {
    id = 111460056,
    name = "怎么是你？"
  },
  [111460057] = {
    id = 111460057,
    name = "你认识他？"
  },
  [111460058] = {
    id = 111460058,
    name = "你不认识他？"
  },
  [111460059] = {
    id = 111460059,
    name = "两人同时问话，一时也反应了过来，齐刷刷地盯着你。"
  },
  [111460060] = {
    id = 111460060,
    name = "我看到你独自跑向这边，黄昏魔物活动又频繁，有点担心......"
  },
  [111460061] = {
    id = 111460061,
    name = "你有点心虚，但转念又想，如果他们真的遇到了危险，你也一定会出手相助。"
  },
  [111460062] = {id = 111460062, name = "哦。"},
  [111460063] = {
    id = 111460063,
    name = "诶，不对啊~你跟着他，我也跟着他......"
  },
  [111460064] = {
    id = 111460064,
    name = "我为什么没看到你？啊~难道你悄悄跟在我们两个后面？不可能啊......我明明检查了没人跟着才进来的。"
  },
  [111460065] = {id = 111460065, name = "......"},
  [111460066] = {
    id = 111460066,
    name = "检查了很多次还被两个人跟进来的埃拉，这时脸色涨红，但他很快反应了过来，摆正了神色。"
  },
  [111460067] = {
    id = 111460067,
    name = "你们快点出去吧，缝隙马上就要关闭了！"
  },
  [111460068] = {
    id = 111460068,
    name = "小埃拉，你回来了？！"
  },
  [111460069] = {
    id = 111460069,
    name = "......雷克！我这次带了一些外面的东西回来。一会儿可以分给大家！"
  },
  [111460070] = {
    id = 111460070,
    name = "一名青年从村庄处跑了上来，埃拉赶紧迎上前去，想把两人挡在身后。"
  },
  [111460071] = {
    id = 111460071,
    name = "而随着那个叫雷克的青年的呼喊，更多人出现了，其中还有抱着孩子的妇人。"
  },
  [111460072] = {
    id = 111460072,
    name = "你没事吧，看你这一身伤......回来就好。以后还是别出去了，外面多危险啊~"
  },
  [111460073] = {
    id = 111460073,
    name = "啧，你还回来干什么？那些乌七八糟的东西，我们用不着！"
  },
  [111460074] = {
    id = 111460074,
    name = "我的家在这里，我当然要回来。"
  },
  [111460075] = {
    id = 111460075,
    name = "埃拉，你竟然还带了外人进村子！"
  },
  [111460076] = {
    id = 111460076,
    name = "他们——"
  },
  [111460077] = {
    id = 111460077,
    name = "你知道他们是什么人吗，就带进来！"
  },
  [111460078] = {
    id = 111460078,
    name = "一个陌生的少年发现了你们，严肃地走向你和岫。"
  },
  [111460079] = {
    id = 111460079,
    name = "离开这里！西默蒂瑞不欢迎你们！埃拉，你赶紧带着这两个闯进来的人滚出去！"
  },
  [111460080] = {
    id = 111460080,
    name = "叔叔，他们帮过我，不是坏人！外面也没你想得那么可怕！"
  },
  [111460081] = {
    id = 111460081,
    name = "叔叔？！"
  },
  [111460082] = {
    id = 111460082,
    name = "岫震惊地看着眼前的少年。"
  },
  [111460083] = {
    id = 111460083,
    name = "年纪看起来和埃拉差不多大，怎么就成叔叔了？"
  },
  [111460084] = {
    id = 111460084,
    name = "你既然喜欢外面，就带着他们一起滚吧！"
  },
  [111460085] = {
    id = 111460085,
    name = "不！叔叔，在你们的病没有治好之前我是绝对不会放弃的！而且这一次，我又带了新的药回来，你们可以试试！"
  },
  [111460086] = {
    id = 111460086,
    name = "又带了药吗？"
  },
  [111460087] = {
    id = 111460087,
    name = "以前都试过那么多次了，我们已经不抱希望了......"
  },
  [111460088] = {
    id = 111460088,
    name = "这回不一样！岫小姐说，你们的症状看起来像是被浊炁侵蚀了，我这次带回来的是净化浊炁的药！"
  },
  [111460089] = {
    id = 111460089,
    name = "说了多少次了，我们没病！"
  },
  [111460090] = {
    id = 111460090,
    name = "我们觉得现在这样很好，你不要再做这些没用的事浪费时间了，还有你在外面胡乱认识的人，也不许带到村子里来！"
  },
  [111460091] = {
    id = 111460091,
    name = "岫碰了碰你的手肘。"
  },
  [111460092] = {
    id = 111460092,
    name = "我们好像真的不受欢迎诶？"
  },
  [111460093] = {id = 111460093, name = "......"},
  [111460094] = {
    id = 111460094,
    name = "埃拉看了看你们，那些村民也被埃拉叔叔的话影响，看你们的神色也变了，有妇人抱紧了怀里的孩子。"
  },
  [111460095] = {
    id = 111460095,
    name = "埃拉呀，你怎么能随随便便就把外人带进来呢？你叔叔说的对，要是这些人把西默蒂瑞的事泄露出去，魔物什么的进来了，那可怎么办啊......"
  },
  [111460096] = {id = 111460096, name = "......"},
  [111460097] = {
    id = 111460097,
    name = "村长来了！"
  },
  [111460098] = {
    id = 111460098,
    name = "人群瞬间分开，从中间走出来了一个中年男人。在看到埃拉的时候，他脸上露出了慈爱的笑容。"
  },
  [111460099] = {
    id = 111460099,
    name = "小埃拉回来了啊，嗯？还带了朋友吗？"
  },
  [111460100] = {
    id = 111460100,
    name = "哼，不过就出去两三天，也不知道哪儿带回来的不三不四的人！"
  },
  [111460101] = {
    id = 111460101,
    name = "村长，这是岫，这次的药是她帮我找的。"
  },
  [111460102] = {
    id = 111460102,
    name = "这位是......"
  },
  [111460103] = {
    id = 111460103,
    name = "埃拉想介绍你，却愣了一下。因为与你不过只是一面之缘，他并不知道你的名字。"
  },
  [111460104] = {
    id = 111460104,
    name = "是我的救命恩人。"
  },
  [111460105] = {
    id = 111460105,
    name = "我差点被魔物吃掉的时候，是他救了我。"
  },
  [111460106] = {
    id = 111460106,
    name = "抱歉，无意冒犯这里，打扰了各位。来到这里也只是巧合，既然你们有你们的规矩，我们会立刻离开的。"
  },
  [111460107] = {
    id = 111460107,
    name = "岫不可置信地看着你，使劲拽你的衣袖。"
  },
  [111460108] = {
    id = 111460108,
    name = "（小声）别走啊，我还想留在这呢！"
  },
  [111460109] = {
    id = 111460109,
    name = "这里很久没有外人来过了啊，埃拉，你打算怎么办？"
  },
  [111460110] = {
    id = 111460110,
    name = "天快要黑了。外面一到天黑就会很危险，就让他们在这里休息一晚再走吧？"
  },
  [111460111] = {
    id = 111460111,
    name = "是啊，就让我们休息一晚吧？"
  },
  [111460112] = {
    id = 111460112,
    name = "村长转过身，打量了你们几眼，点了点头。"
  },
  [111460113] = {
    id = 111460113,
    name = "既然是埃拉的恩人，那就暂时留下来，明天就让他们离开吧。"
  },
  [111460114] = {
    id = 111460114,
    name = "谢谢村长！我带他们去找住的地方！"
  },
  [111460115] = {
    id = 111460115,
    name = "雷克，你带他们去。小埃拉，你先和我们回去。"
  },
  [111460116] = {
    id = 111460116,
    name = "......好。"
  },
  [111460117] = {
    id = 111460117,
    name = "埃拉遗憾地跟你们挥了挥手，和村长等人离开了。其他人也逐渐散去，那名叫雷克的青年站在你们面前，似乎不知道该怎么开口。"
  },
  [111460118] = {
    id = 111460118,
    name = "麻烦带路了。"
  },
  [111460119] = {
    id = 111460119,
    name = "走这边。"
  },
  [111460120] = {
    id = 111460120,
    name = "嗯？你们的房子不是在那边吗？"
  },
  [111460121] = {
    id = 111460121,
    name = "那里没有空的房间，我带你们去山脚下。"
  },
  [111460122] = {
    id = 111460122,
    name = "那谢谢啦~"
  },
  [111460123] = {
    id = 111460123,
    name = "雷克没有说话，只是加快了脚步。"
  },
  [111460124] = {
    id = 111460124,
    name = "（小声）他为什么这么紧张？"
  },
  [111460125] = {
    id = 111460125,
    name = "可能因为第一次见到外来的人吧。"
  },
  [111460126] = {
    id = 111460126,
    name = "哦哦，我知道了，他和我一个好朋友很像，表面冷冰冰的，其实是个善良的人呢。"
  },
  [111460127] = {
    id = 111460127,
    name = "日暮时分，你们沿路穿过了村庄，一排排房屋亮起灯，行人在和雷克打招呼，见到你们时惊奇的眼神保持着友好，一切都是安适平和的模样。"
  },
  [111460128] = {
    id = 111460128,
    name = "雷克将你们带进了山脚下，指着前方几间空屋子。"
  },
  [111460129] = {
    id = 111460129,
    name = "就是这里了，你们随便住吧。"
  },
  [111460130] = {
    id = 111460130,
    name = "啊，竟然有床！厨房！餐桌！花园！太棒了！"
  },
  [111460131] = {
    id = 111460131,
    name = "......哼，虽然你们救了小埃拉，但西默蒂瑞也不是你们能乱来的地方。"
  },
  [111460132] = {
    id = 111460132,
    name = "没问题，没问题，谢谢你呀~"
  },
  [111460133] = {
    id = 111460133,
    name = "雷克离开了，岫兴奋地忙东忙西，还招呼你一起帮忙。"
  },
  [111460134] = {
    id = 111460134,
    name = "我经过那条河看到鱼特别肥，味道一定很不错！"
  },
  [111460135] = {
    id = 111460135,
    name = "那个，还是不要乱走比较好。"
  },
  [111460136] = {
    id = 111460136,
    name = "哎呀，你真胆小，说起来这个地方叫......西默蒂瑞吗？简直就像传说里的理想世界，我就知道，跟着埃拉一定会有收获！"
  },
  [111460137] = {
    id = 111460137,
    name = "你是为了到这里，才跟着埃拉的？"
  },
  [111460138] = {id = 111460138, name = "对啊。"},
  [111460139] = {
    id = 111460139,
    name = "我从小就听家里人说，深渊里有这么一个香格里拉——与世无争，宁静美好，没有浊炁的侵蚀。但是后来，他们说这只是传说，是骗人的。"
  },
  [111460140] = {
    id = 111460140,
    name = "但我不信，深渊有那么多奇迹，这样人人向往的乌托邦，也一定会有的！"
  },
  [111460141] = {
    id = 111460141,
    name = "埃拉那个小家伙，善良得有点笨呢，帮他一个忙，什么都傻乎乎地说了。幸好是遇到了我，要是碰上坏人，恐怕会被吃干抹净扒得皮都不剩，还会牵连这里的人......"
  },
  [111460142] = {
    id = 111460142,
    name = "啊，真好啊，原来他说的西默蒂瑞，真是这样一个美好纯净的地方......"
  },
  [111460143] = {
    id = 111460143,
    name = "话说回来，你是什么人，怎么会跟着我们？"
  },
  [111460144] = {
    id = 111460144,
    name = "我只是恰巧路过，把他从魔物手里救了出来。"
  },
  [111460145] = {
    id = 111460145,
    name = "啊，是那个时候！我也听到魔物的声音了！那时我正在解决另一只呢。原来你也救了他~看来埃拉运气还真不错嘛~"
  },
  [111460146] = {
    id = 111460146,
    name = "月明星稀，耳边传来浅浅的虫鸣声。岫欢快地躺在草地上，一手伸向天空。"
  },
  [111460147] = {
    id = 111460147,
    name = "好安静。不用担心魔物的袭击，也没有人心的算计，真好啊。这种安宁又舒适的环境，能一直在这里就好了......"
  },
  [111460148] = {
    id = 111460148,
    name = "这个地方完全不受深渊的影响，似乎是被什么特意隔离了......你不觉得，这里很奇怪吗？"
  },
  [111460149] = {
    id = 111460149,
    name = "哪里奇怪了？我觉得这个地方很好，这里的人也很好~"
  },
  [111460150] = {
    id = 111460150,
    name = "就是因为太完美了。完美到这些村民都不清楚外面的世界，他们没有武技和魔法，却还在深渊这样危机重重的地方偏安一隅。"
  },
  [111460151] = {
    id = 111460151,
    name = "这样的地方，存在于深渊，就像狼群里的兔子窝。如果一旦被外面发现，或者出口被打开，对于他们而言，迎来的是灭顶之灾。"
  },
  [111460152] = {
    id = 111460152,
    name = "你说得有道理诶，但只要我们不说出去就好啦~兔子窝能藏在狼群里，本来就是一种奇迹。我们就让这个奇迹继续延续下去吧！"
  },
  [111460153] = {
    id = 111460153,
    name = "岫一直保持着兴奋的心情，对于她来讲说，西默蒂瑞就是她理想中的世界。"
  },
  [111460154] = {
    id = 111460154,
    name = "你很喜欢这个地方吗？"
  },
  [111460155] = {
    id = 111460155,
    name = "难道你不喜欢吗？这里没有任何争斗，人都是那么善良，就算对我们很凶的埃拉叔叔，我也没在他身上感觉到任何恶意。"
  },
  [111460156] = {
    id = 111460156,
    name = "啊，所有的人都像他们一样就好了。"
  },
  [111460157] = {
    id = 111460157,
    name = "这是不可能的吧。"
  },
  [111460158] = {
    id = 111460158,
    name = "我知道呀！我就是畅想一下~如果能够把这份幸福分享给别人，那其他人也会被这样的幸福所感染，而像我这样快乐吧......"
  },
  [111460159] = {
    id = 111460159,
    name = "你不忍打击岫天真的想法，转身进了空屋打扫房间，而她依旧在外面滔滔不绝。"
  },
  [111460160] = {
    id = 111460160,
    name = "喂！你——我还不知道你叫什么呢。重新介绍一下，我叫岫，是真理之钥的成员哦！"
  },
  [111460161] = {
    id = 111460161,
    name = "真理之钥，是探险队吗？"
  },
  [111460162] = {
    id = 111460162,
    name = "你竟然没有听过真理之钥吗？那可是深渊最伟大的组织！"
  },
  [111460163] = {
    id = 111460163,
    name = "岫很激动，你有预感接下来她会长篇大论地讲述这个组织。"
  },
  [111460164] = {id = 111460164, name = "......"},
  [111460165] = {
    id = 111460165,
    name = "什么人？！"
  },
  [111460166] = {
    id = 111460166,
    name = "屋外有响动，岫警觉的声音传来，你立即出去查看。"
  },
  [111460167] = {
    id = 111460167,
    name = "月光阴影处，走出来一个大约十岁左右的孩童。岫意识到这里并不是危机重重的深渊，收起武器，卸下了防备。"
  },
  [111460168] = {
    id = 111460168,
    name = "小朋友，你来这里做什么？"
  },
  [111460169] = {
    id = 111460169,
    name = "你们就是埃拉的朋友？"
  },
  [111460170] = {
    id = 111460170,
    name = "孩童一本正经地问话，表情冷淡。岫觉得很有趣，忍不住想逗一逗他。"
  },
  [111460171] = {
    id = 111460171,
    name = "对啊，是你埃拉哥哥叫你来的吗？"
  },
  [111460172] = {
    id = 111460172,
    name = "哥哥？我十岁的时候，那小子才刚刚出生。"
  },
  [111460173] = {
    id = 111460173,
    name = "不可能~你看起来也就十岁的样子吧！"
  },
  [111460174] = {
    id = 111460174,
    name = "......我来是想让你帮忙的。"
  },
  [111460175] = {id = 111460175, name = "哦？"},
  [111460176] = {
    id = 111460176,
    name = "是想求我们呀，你这个态度可不是求人的态度哦~"
  },
  [111460177] = {id = 111460177, name = "......"},
  [111460178] = {
    id = 111460178,
    name = "是关于埃拉的，他跑去山里了。你们要是他的朋友，就去找找他吧。"
  },
  [111460179] = {id = 111460179, name = "山里？"},
  [111460180] = {
    id = 111460180,
    name = "岫回首看向身后的高山，此时犹如黑暗中沉睡的巨兽，随时会张开大口将村庄吞噬一般。"
  },
  [111460181] = {
    id = 111460181,
    name = "他为什么要去山上？"
  },
  [111460182] = {
    id = 111460182,
    name = "不知道。但你们不去把他带回来的话，他会有危险。"
  },
  [111460183] = {
    id = 111460183,
    name = "这里不是没有魔物吗？"
  },
  [111460184] = {
    id = 111460184,
    name = "夜晚更深露重。他如果没有探险者的经验，就算没有魔物，也很容易迷路或者冻死。"
  },
  [111460185] = {
    id = 111460185,
    name = "孩童看向你，认同地点点头。"
  },
  [111460186] = {
    id = 111460186,
    name = "所以拜托了。"
  },
  [111460187] = {
    id = 111460187,
    name = "那你快带路吧！我都能想象埃拉坐在树下瑟瑟发抖的样子了~"
  },
  [111460188] = {
    id = 111460188,
    name = "我不能去。"
  },
  [111460189] = {
    id = 111460189,
    name = "说得也是，你太小了，去找你们的大人，这样人多好找人~"
  },
  [111460190] = {
    id = 111460190,
    name = "他们也不能去。只有你们可以。"
  },
  [111460191] = {
    id = 111460191,
    name = "为什么？啊，难道是因为那个病？"
  },
  [111460192] = {id = 111460192, name = "！！！"},
  [111460193] = {
    id = 111460193,
    name = "他连这个都跟你们说了？！"
  },
  [111460194] = {
    id = 111460194,
    name = "他说你们很多年前遇到一场事故，之后就得了一种怪病，找了很多药都治不好。我就让他用净化浊炁的药试试，他这次把药带回来了，说要先给村长看看。"
  },
  [111460195] = {
    id = 111460195,
    name = "......算是这样。生病以后，我们就无法离开西默蒂瑞了。就连那座山，也成了绝对的禁区，因为进去之后，就会死。"
  },
  [111460196] = {
    id = 111460196,
    name = "这么严重......可是这座山这么大，我们要怎么找？"
  },
  [111460197] = {
    id = 111460197,
    name = "山上有一座神殿，已经荒废很久了，但埃拉经常独自去那里。"
  },
  [111460198] = {
    id = 111460198,
    name = "那赶紧出发吧！"
  },
  [111460199] = {
    id = 111460199,
    name = "岫回头，发现你站在原地不动，神色一怔。"
  },
  [111460200] = {
    id = 111460200,
    name = "让我们外人随便踏入你们的神殿，真的可以吗？"
  },
  [111460201] = {
    id = 111460201,
    name = "岫猛然转向了那个孩童，一种诡异的感觉从心底蔓延。"
  },
  [111460202] = {
    id = 111460202,
    name = "（小声）什么意思？"
  },
  [111460203] = {
    id = 111460203,
    name = "不过，既然埃拉有危险......作为埃拉的朋友，我们当然不能放弃他。对吧？"
  },
  [111460204] = {id = 111460204, name = "......"},
  [111460205] = {
    id = 111460205,
    name = "那，我们走吧？"
  },
  [111460206] = {id = 111460206, name = "好。"},
  [111460207] = {
    id = 111460207,
    name = "岫不知为什么突然长舒一口气，而那个孩童正面色凝重地看着你们，惊得岫差点一脚踩空。"
  },
  [111460208] = {
    id = 111460208,
    name = "小心脚下。"
  },
  [111460209] = {
    id = 111460209,
    name = "哦哦，好。"
  },
  [111460210] = {
    id = 111460210,
    name = "上山之后，浊炁逐渐浓郁，安宁静谧的山林中，连虫鸣都消失了，只听得见一前一后的脚步声。"
  },
  [111460211] = {
    id = 111460211,
    name = "......真安静啊......"
  },
  [111460212] = {
    id = 111460212,
    name = "你之前跟我说的那些话，是觉得那个小孩在骗我们吗？是我疏忽了，来到这个地方后，不自觉就放松警惕了。多谢你的提醒~"
  },
  [111460213] = {
    id = 111460213,
    name = "唔，这里的感觉和外面的深渊没什么区别啊，这就是那些村民不能上山的原因吗？"
  },
  [111460214] = {
    id = 111460214,
    name = "嗯，浊炁很浓，但普通人只要不在山上长时间待着，不会那么快被侵蚀，或许他们还有别的原因。"
  },
  [111460215] = {
    id = 111460215,
    name = "这样啊......普通人的话，埃拉应该也算普通人吧，那我们得尽快找到他。"
  },
  [111460216] = {
    id = 111460216,
    name = "哈哈，说起来他伪装成探险者的样子，就像小兔子戴着狼的爪子......哇，我看到神殿了！是那里吧！"
  },
  [111460217] = {
    id = 111460217,
    name = "岫指了指前方尖子塔的建筑，在夜幕中显得格外阴森。"
  },
  [111460218] = {
    id = 111460218,
    name = "你们加快脚步，很快到达了神殿门前。一个人影蜷缩在紧闭的神殿门口，此时听见声响，抬头惊讶地望向你们。"
  },
  [111460219] = {
    id = 111460219,
    name = "你们怎么......"
  },
  [111460220] = {
    id = 111460220,
    name = "你果然在这里啊！那个小孩说你在神殿，他还挺了解你的嘛~"
  },
  [111460221] = {
    id = 111460221,
    name = "......嗯。"
  },
  [111460222] = {
    id = 111460222,
    name = "怎么不回家？"
  },
  [111460223] = {
    id = 111460223,
    name = "是啊，这地方对你来说不安全，快回家吧。"
  },
  [111460224] = {
    id = 111460224,
    name = "我不想回去。"
  },
  [111460225] = {
    id = 111460225,
    name = "埃拉将头埋在膝盖里，仿佛受了天大的委屈。岫愣了愣，悄悄凑近你耳边。"
  },
  [111460226] = {
    id = 111460226,
    name = "好像离家出走的样子诶。"
  },
  [111460227] = {
    id = 111460227,
    name = "是和家里人吵架了吗？"
  },
  [111460228] = {
    id = 111460228,
    name = "叔叔，叔叔把我带回来的药全扔了......"
  },
  [111460229] = {id = 111460229, name = "！！！"},
  [111460230] = {
    id = 111460230,
    name = "他们都不让我出去了，说我做的都是无用功。我只是想治好他们，让他们也看看外面的世界啊......永无止境地被困在这里，还能算是活着吗？"
  },
  [111460231] = {
    id = 111460231,
    name = "困在这里......他们到底得了什么病？"
  },
  [111460232] = {
    id = 111460232,
    name = "埃拉摇了摇头。"
  },
  [111460233] = {
    id = 111460233,
    name = "在我出生那年，西默蒂瑞发生了一场变故，大量的黑气涌入了村庄，一时间死了好多好多人。"
  },
  [111460234] = {
    id = 111460234,
    name = "叔叔说，父亲为了把我送出去，也死在了这场变故里。就算侥幸活下来的人，也一个接着一个得了身染黑气的怪病死掉。"
  },
  [111460235] = {
    id = 111460235,
    name = "黑气......"
  },
  [111460236] = {
    id = 111460236,
    name = "村长为了不再有人死去，就来到了我身后这座神殿祈祷。祈祷奏效了，黑气被驱散了，大家都活了下来。"
  },
  [111460237] = {
    id = 111460237,
    name = "后来，是母亲把我从外面接了回来，而母亲回来后不久也死去了。那时候大家才知道，他们不再得病，却再也无法离开西默蒂瑞。"
  },
  [111460238] = {
    id = 111460238,
    name = "有人说是离开了神的庇护就会死，而我是唯一一个没有被神眷顾的人。"
  },
  [111460239] = {
    id = 111460239,
    name = "......神吗......"
  },
  [111460240] = {
    id = 111460240,
    name = "说起来，听说在我出生之前，这座神殿是很热闹的。可现在这里脱离了祂的庇护范围，就这样荒废了。"
  },
  [111460241] = {
    id = 111460241,
    name = "因为浊炁的原因吧？唔，这里的浊炁浓度甚至比深渊有的地方还重，是有什么力量把山上和山下隔开了......就像是、就像是......"
  },
  [111460242] = {
    id = 111460242,
    name = "把村庄的浊炁挪到了山上。"
  },
  [111460243] = {
    id = 111460243,
    name = "对、对！"
  },
  [111460244] = {
    id = 111460244,
    name = "是的。所谓的神不但封锁了西默蒂瑞，而且还让这里的人变得更奇怪了。"
  },
  [111460245] = {id = 111460245, name = "奇怪？"},
  [111460246] = {
    id = 111460246,
    name = "自从受到神的庇护后，他们的容貌就没有改变过。"
  },
  [111460247] = {id = 111460247, name = "！！！"},
  [111460248] = {id = 111460248, name = "！！！"},
  [111460249] = {
    id = 111460249,
    name = "一瞬间，这个村庄的异常在这一刻串联在了一起，你脑海中得到了一个初步的答案。"
  },
  [111460250] = {
    id = 111460250,
    name = "让你们上来的那个人，其实已经23岁了。"
  },
  [111460251] = {
    id = 111460251,
    name = "难怪......他不让我叫他小孩。"
  },
  [111460252] = {
    id = 111460252,
    name = "十多年了，只有我在变化，而他们的外貌一直没有改变过。他们所有人对我都很好，但我觉得自己就像一个异类。"
  },
  [111460253] = {
    id = 111460253,
    name = "竟然是不老不死吗？！那你的叔叔、还有村长......"
  },
  [111460254] = {
    id = 111460254,
    name = "岫的表情可以用惊叹来形容，这个地方竟然比她想象中的更加令人向往......"
  },
  [111460255] = {
    id = 111460255,
    name = "你好像很羡慕？"
  },
  [111460256] = {
    id = 111460256,
    name = "诶，我......"
  },
  [111460257] = {
    id = 111460257,
    name = "他们这样是不正常的！如果不是那场变故，他们会一直过着正常的生活。而不是现在这样，被永远、永远困在这里！"
  },
  [111460258] = {
    id = 111460258,
    name = "我要治好他们，让他们跟我一起，去看看外面的世界。可是、可是叔叔说，他现在得到了永生，是神给的恩赐，让我不要再白费力气了。"
  },
  [111460259] = {
    id = 111460259,
    name = "我不信，不信神！哪有神的恩赐是这样的？被困在这个逼仄的山谷里，日复一日地重复着一样的生活！就算能永生，这样活着，和行尸走肉又有什么区别啊？！"
  },
  [111460260] = {id = 111460260, name = "......"},
  [111460261] = {
    id = 111460261,
    name = "埃拉突然站起来，狠狠踢了一下神殿的门。"
  },
  [111460262] = {
    id = 111460262,
    name = "谁知道这个神是真是假？说不定是哪里来的恶魔，想把大家都困死在这里！"
  },
  [111460263] = {
    id = 111460263,
    name = "埃拉再次踹了一脚。"
  },
  [111460264] = {
    id = 111460264,
    name = "回答我啊，你不是能救人吗？怎么连回答我都做不到？！"
  },
  [111460265] = {
    id = 111460265,
    name = "埃拉！你冷静点。"
  },
  [111460266] = {
    id = 111460266,
    name = "我没事。"
  },
  [111460267] = {
    id = 111460267,
    name = "这种事，我做过很多次了，也没有受到任何处罚。你看，这个世界根本就没有神。"
  },
  [111460268] = {
    id = 111460268,
    name = "我一定会治好他们。就算这次叔叔扔掉了我的药，我还会再带回来！迟早有一天，说服他们做回正常人！"
  },
  [111460269] = {id = 111460269, name = "......"},
  [111460270] = {
    id = 111460270,
    name = "明天我跟你们一起出去。"
  },
  [111460271] = {
    id = 111460271,
    name = "你叔叔不是不让你再出去了吗？"
  },
  [111460272] = {
    id = 111460272,
    name = "我现在力气比他大，他阻止不了我。只要村长不阻止，我就能出去。"
  },
  [111460273] = {id = 111460273, name = "村长？"},
  [111460274] = {
    id = 111460274,
    name = "那个唯一的出口本来是打开的，母亲去世后，村长怕大家误闯送命，把那个出口也封了。在决定出去后，我花了一年多的时间，悄悄打开了通往外界的出口。"
  },
  [111460275] = {
    id = 111460275,
    name = "第一次出去回来的时候，他们担忧、责骂、庆幸......我看得出来，他们很羡慕我，羡慕我不用困在这里。村长也睁一只眼闭一只眼，只让我注意安全。"
  },
  [111460276] = {
    id = 111460276,
    name = "我是自由的，只有我是自由的，而这份自由，本该是大家的。"
  },
  [111460277] = {id = 111460277, name = "......"},
  [111460278] = {
    id = 111460278,
    name = "既然要出去，那么现在就下山吧。"
  },
  [111460279] = {
    id = 111460279,
    name = "哦对对，差点忘了，那个很严肃的小孩叮嘱我们，一定要让我们带你回去，他们......还是很担心你的。"
  },
  [111460280] = {
    id = 111460280,
    name = "埃拉露出了微笑。"
  },
  [111460281] = {
    id = 111460281,
    name = "嗯，他们都是我的家人啊。走吧，回去了。"
  },
  [111460282] = {
    id = 111460282,
    name = "三人下山后，你看到有个人影停留在路口。"
  },
  [111460283] = {
    id = 111460283,
    name = "有人在那里，好像是村长！"
  },
  [111460284] = {
    id = 111460284,
    name = "你仔细看了看，不得不赞叹岫的眼力。岫感受到你的目光，骄傲地拍了拍身上的箭筒，作为一个弓箭手，眼力当然不会差。"
  },
  [111460285] = {
    id = 111460285,
    name = "看他的样子，应该是在等人。"
  },
  [111460286] = {
    id = 111460286,
    name = "他......难道他知道我们的计划了？"
  },
  [111460287] = {
    id = 111460287,
    name = "不会吧，他不可能知道咱们在山上的谈话。别想了，过去问问就知道啦~"
  },
  [111460288] = {
    id = 111460288,
    name = "温和的中年男人等你们走下山，看着埃拉，目露关怀。"
  },
  [111460289] = {
    id = 111460289,
    name = "下次跟你叔叔生气了别去山上，那边不安全。"
  },
  [111460290] = {
    id = 111460290,
    name = "知道了，村长。"
  },
  [111460291] = {
    id = 111460291,
    name = "这么晚了还让你们帮忙，给你们添麻烦了。"
  },
  [111460292] = {
    id = 111460292,
    name = "没有啦，其实埃拉他——"
  },
  [111460293] = {
    id = 111460293,
    name = "村长，我明天要和他们一起出去。"
  },
  [111460294] = {id = 111460294, name = "诶？"},
  [111460295] = {
    id = 111460295,
    name = "埃拉不是说村长会封掉出口吗？"
  },
  [111460296] = {
    id = 111460296,
    name = "你找了那么多的药......还没有放弃吗？"
  },
  [111460297] = {
    id = 111460297,
    name = "当然。叔叔反应太大了，以前我找回来的药，他都会让我给大家试吃，可这一次竟然直接扔了我的药！"
  },
  [111460298] = {
    id = 111460298,
    name = "他要扔，我就继续找！总有一天，我会说服他们吃药。"
  },
  [111460299] = {
    id = 111460299,
    name = "其实，那个药是我让他扔掉的。"
  },
  [111460300] = {
    id = 111460300,
    name = "什么！！村长，为什么，为什么要扔掉那些药？！"
  },
  [111460301] = {
    id = 111460301,
    name = "会死。他们吃了，会死的。"
  },
  [111460302] = {id = 111460302, name = "！！！"},
  [111460303] = {
    id = 111460303,
    name = "十多年前，所有人都被突如起来的浊炁侵蚀，除了刚出生的你。你父亲把你包裹在了他的魔法罩里，送你离开了西默蒂瑞，避免了这次事故。"
  },
  [111460304] = {
    id = 111460304,
    name = "而你的父亲，也是因为用仅剩的魔法力保护你，被侵蚀而亡。我看着村子里的人日益虚弱，一个接一个地死去，只好去了神殿......"
  },
  [111460305] = {
    id = 111460305,
    name = "后......后来呢？"
  },
  [111460306] = {
    id = 111460306,
    name = "神殿里有我族的至宝——光阴沙漏。"
  },
  [111460307] = {
    id = 111460307,
    name = "（宝物？）"
  },
  [111460308] = {
    id = 111460308,
    name = "那本来是遗迹留给我们的唯一的东西，守护它是我们一生的使命。可在西默蒂瑞生死存亡之际，我不得不打开了它。"
  },
  [111460309] = {
    id = 111460309,
    name = "（遗迹......是那个传闻中的遗迹吗......）"
  },
  [111460310] = {
    id = 111460310,
    name = "光阴沙漏打开之后，西默蒂瑞的浊炁被转移到了山里，整个村庄与外界隔绝了。而我们的时间，也永远停留在了那一刻。"
  },
  [111460311] = {
    id = 111460311,
    name = "那为什么要把药扔掉？净化了浊炁，恢复了身体，你们就可以出去啊~"
  },
  [111460312] = {
    id = 111460312,
    name = "没用的。"
  },
  [111460313] = {
    id = 111460313,
    name = "浊炁在将死的身体里待了这么多年，早就成了维持身体的一部分能量，强行将浊炁净化的话，大家只会像你母亲一样，虚弱而亡。"
  },
  [111460314] = {id = 111460314, name = "！！！"},
  [111460315] = {
    id = 111460315,
    name = "在光阴沙漏被打开的时候，这里就形成了封闭的空间。我在身体垮掉之前，及时将沙漏转移到了村子的祭坛里。"
  },
  [111460316] = {
    id = 111460316,
    name = "这么多年了，也不是没有人想离开，可是离开的人都会恢复停滞的时间，最后都会死去。"
  },
  [111460317] = {
    id = 111460317,
    name = "我也尝试着找过很多药物，但都失败了。有时候我会想，当年是不是做错了，我们从神殿带走了光阴沙漏，遗弃了神......"
  },
  [111460318] = {
    id = 111460318,
    name = "神不再庇佑我们，永生永世地将我们囚禁在这里，就是对我们的惩罚。"
  },
  [111460319] = {
    id = 111460319,
    name = "村长长叹一口气。"
  },
  [111460320] = {
    id = 111460320,
    name = "原来......是这样......原来是这样......叔叔说的......都是对的......我真的在做无用功......"
  },
  [111460321] = {
    id = 111460321,
    name = "我救不了谁......我谁也救不了，谁也救不了......"
  },
  [111460322] = {
    id = 111460322,
    name = "一只手放在了埃拉毛毛躁躁的头上，埃拉抬头露出通红的双眼，看到村长慈爱的目光。"
  },
  [111460323] = {
    id = 111460323,
    name = "不，孩子，你是我们的希望。如果没有你不停地为大家找药，也许很多人已经厌倦了，这样一成不变、毫无自由地活着，是没有未来的。"
  },
  [111460324] = {
    id = 111460324,
    name = "而你，是大家活下去的希望......"
  },
  [111460325] = {
    id = 111460325,
    name = "埃拉抽了抽鼻子，用力抹了一把眼泪。"
  },
  [111460326] = {
    id = 111460326,
    name = "村长，深渊那么大，我一定会找到办法救你们的！"
  },
  [111460327] = {
    id = 111460327,
    name = "对。总会有希望的，别那么快放弃。我知道有个圈落！那里的医师非常有名，治了很多的疑难杂症，我们可以去找他！"
  },
  [111460328] = {
    id = 111460328,
    name = "嗯，好。"
  },
  [111460329] = {
    id = 111460329,
    name = "小埃拉......"
  },
  [111460330] = {
    id = 111460330,
    name = "村长，这里是我的家，我会让大家都变回正常的人，都可以去看看外面的世界！"
  },
  [111460331] = {
    id = 111460331,
    name = "嗯，你想去做的话，就去吧。"
  },
  [111460332] = {
    id = 111460332,
    name = "村长揉了揉埃拉的头，接着看向了你和岫，他微微弯下了腰。"
  },
  [111460333] = {
    id = 111460333,
    name = "在外面的话，小埃拉就拜托你们了。"
  },
  [111460334] = {
    id = 111460334,
    name = "放心吧，小埃拉是我们的朋友呢。"
  },
  [111460335] = {
    id = 111460335,
    name = "你点了点头，村长满意地笑了。正要离开时，埃拉突然出声。"
  },
  [111460336] = {
    id = 111460336,
    name = "村长，大家都知道吗？"
  },
  [111460337] = {id = 111460337, name = "......"},
  [111460338] = {
    id = 111460338,
    name = "大家都知道真相吗？知道自己不老不死，无法离开西默蒂瑞的原因吗？"
  },
  [111460339] = {id = 111460339, name = "......"},
  [111460340] = {
    id = 111460340,
    name = "村长！他们和我一样，有权知道这些！这个世界没有神，只有那个像诅咒一样的光阴沙漏。告诉他们，我埃拉会改变这一切！"
  },
  [111460341] = {
    id = 111460341,
    name = "......是，应该告诉他们......我会告诉他们的。"
  },
  [111460342] = {
    id = 111460342,
    name = "村长的背影停滞了一瞬，继而离开了。"
  },
  [111460343] = {id = 111460343, name = "......"},
  [111460344] = {
    id = 111460344,
    name = "目送村长远去，埃拉久久没有说话，岫一巴掌敲在了他的背上。"
  },
  [111460345] = {
    id = 111460345,
    name = "好啦，现在村长也支持你，我们明天就出发去圈落吧！"
  },
  [111460346] = {
    id = 111460346,
    name = "好好休息准备，明天才有精神出发。"
  },
  [111460347] = {
    id = 111460347,
    name = "......嗯。"
  },
  [111460348] = {
    id = 111460348,
    name = "这......就是圈落吗？！"
  },
  [111460349] = {
    id = 111460349,
    name = "当然啦~和你那附近的小据点是不是完全不一样！"
  },
  [111460350] = {
    id = 111460350,
    name = "我，我没有走过这么远的地方......"
  },
  [111460351] = {
    id = 111460351,
    name = "面对人潮涌动的大街，岫推了呆愣的埃拉一把。"
  },
  [111460352] = {
    id = 111460352,
    name = "走啦，我记得那个医师就在隔壁的街道。"
  },
  [111460353] = {
    id = 111460353,
    name = "三人来到岫印象中的医馆门前，发现此处大门敞开，相比附近拥挤的药店，这里门可罗雀。岫以为找错了，还特意看了好几眼。"
  },
  [111460354] = {
    id = 111460354,
    name = "就是这里吗？"
  },
  [111460355] = {
    id = 111460355,
    name = "呃，就是这里吧。"
  },
  [111460356] = {
    id = 111460356,
    name = "总觉得很不靠谱的样子......"
  },
  [111460357] = {
    id = 111460357,
    name = "埃拉求助地看着你，你朝他笑了笑。"
  },
  [111460358] = {
    id = 111460358,
    name = "不管怎么样，先进去看看吧。"
  },
  [111460359] = {
    id = 111460359,
    name = "你拍了拍他的肩，跟他叮嘱了几句话，他像小鸟啄食一样不断地点头。"
  },
  [111460360] = {
    id = 111460360,
    name = "你们走进了医馆......"
  },
  [111460361] = {
    id = 111460361,
    name = "干什么的？"
  },
  [111460362] = {
    id = 111460362,
    name = "趴在接待桌边的老头打了个哈欠，懒洋洋地坐了起来。"
  },
  [111460363] = {
    id = 111460363,
    name = "老先生，我们想找这里的医师。"
  },
  [111460364] = {
    id = 111460364,
    name = "你们走错了吧，看病吃药去隔壁。"
  },
  [111460365] = {
    id = 111460365,
    name = "不不，听说这里有一个专治奇怪病症的医师，我们就是来找他的。"
  },
  [111460366] = {
    id = 111460366,
    name = "什么病？"
  },
  [111460367] = {
    id = 111460367,
    name = "你就是那个有名的医师？"
  },
  [111460368] = {
    id = 111460368,
    name = "岫上下打量了他一番，看起来不太像。"
  },
  [111460369] = {
    id = 111460369,
    name = "不想看病就滚。"
  },
  [111460370] = {
    id = 111460370,
    name = "老医师精神矍铄，医术高明。我们闻名已久，这次是专程来拜访的。"
  },
  [111460371] = {
    id = 111460371,
    name = "是啊是啊~我都不敢相信这么容易就能见到您！"
  },
  [111460372] = {
    id = 111460372,
    name = "听说医师老先生擅长诊治疑难杂症，这孩子的家人得了一种奇怪的病，吃了很多药都没用。"
  },
  [111460373] = {
    id = 111460373,
    name = "岫拉过埃拉，埃拉立刻跟医师问了声好。"
  },
  [111460374] = {
    id = 111460374,
    name = "有什么就说吧，要是普通的病症，就滚出去，不要打扰我睡觉。"
  },
  [111460375] = {
    id = 111460375,
    name = "你们立刻将西默蒂瑞村民的情况讲了一遍......"
  },
  [111460376] = {
    id = 111460376,
    name = "......浊炁锁在了身体里......成了身体力量的一部分？"
  },
  [111460377] = {
    id = 111460377,
    name = "如果用净化浊炁的药物会怎么样？"
  },
  [111460378] = {
    id = 111460378,
    name = "想死可以用。"
  },
  [111460379] = {id = 111460379, name = "......"},
  [111460380] = {
    id = 111460380,
    name = "这个病有点意思，你再跟我说说具体的症状。"
  },
  [111460381] = {
    id = 111460381,
    name = "埃拉心中迸发出希望，原原本本地将情况一一说明。但他谨记了你的叮嘱，没有把光阴沙漏和永生的秘密讲出去。"
  },
  [111460382] = {
    id = 111460382,
    name = "嗯......病人真的来不了？"
  },
  [111460383] = {
    id = 111460383,
    name = "是的，那个地方在险恶峭壁的一个山谷里，周围魔物丛生，只有那一点空间相对浊炁稀薄一些。"
  },
  [111460384] = {
    id = 111460384,
    name = "诶，那个——"
  },
  [111460385] = {
    id = 111460385,
    name = "埃拉被岫及时阻止，他小声地对岫说。"
  },
  [111460386] = {
    id = 111460386,
    name = "西默蒂瑞周围并不危......"
  },
  [111460387] = {
    id = 111460387,
    name = "岫比了个噤声的手势，埃拉听话地闭嘴了。"
  },
  [111460388] = {
    id = 111460388,
    name = "呵，你们想多了，我这把老骨头才不会离开这里。我的意思是，看不到病人，价钱翻一倍。"
  },
  [111460389] = {
    id = 111460389,
    name = "你真的能治？！"
  },
  [111460390] = {
    id = 111460390,
    name = "没钱就别问了，老头子不看免费的病。"
  },
  [111460391] = {
    id = 111460391,
    name = "埃拉立刻倒出了他挂包里的东西，不止有囤积的欧泊，甚至还有从神殿壁上抠下来的几块源晶。"
  },
  [111460392] = {
    id = 111460392,
    name = "医师眨了眨眼，坐直了身子。"
  },
  [111460393] = {
    id = 111460393,
    name = "勉勉强强吧。"
  },
  [111460394] = {
    id = 111460394,
    name = "太好了！"
  },
  [111460395] = {
    id = 111460395,
    name = "小埃拉，你竟然这么有钱！"
  },
  [111460396] = {
    id = 111460396,
    name = "这是我所有的积蓄了。"
  },
  [111460397] = {
    id = 111460397,
    name = "咳咳，这个小家伙说的病症，除了要净化浊炁，同时还必须巩固身体，填补浊炁侵蚀的漏洞。制作这类药物，正好需要稀有的源晶，既然已经有了，我就不用到处找了。"
  },
  [111460398] = {
    id = 111460398,
    name = "那，那我什么时候可以拿到药？"
  },
  [111460399] = {
    id = 111460399,
    name = "医师瞥了一眼那满含期望的眼睛。"
  },
  [111460400] = {
    id = 111460400,
    name = "过几天再来，你们杵在这我怎么做药。"
  },
  [111460401] = {
    id = 111460401,
    name = "——砰！门关上后，你们三人面面相觑。"
  },
  [111460402] = {
    id = 111460402,
    name = "岫，你介绍的这个医师靠谱吗？"
  },
  [111460403] = {
    id = 111460403,
    name = "啊......我也是听真理之钥的朋友提到过~大概，也许，是没有问题的吧......"
  },
  [111460404] = {
    id = 111460404,
    name = "怎么了？你们怎么看起来都不是很高兴的样子。"
  },
  [111460405] = {
    id = 111460405,
    name = "没什么，我们先找个地方住吧。"
  },
  [111460406] = {
    id = 111460406,
    name = "啊，我一激动，把钱都花光了......"
  },
  [111460407] = {
    id = 111460407,
    name = "那不简单，你们两个住一个房间就行~"
  },
  [111460408] = {
    id = 111460408,
    name = "嗯？既然有了结果，后面就不需要我了吧。"
  },
  [111460409] = {id = 111460409, name = "......"},
  [111460410] = {id = 111460410, name = "......"},
  [111460411] = {
    id = 111460411,
    name = "你要走，开玩笑的吧？"
  },
  [111460412] = {
    id = 111460412,
    name = "两人用同样的表情看着你，仿佛要被抛弃一般。"
  },
  [111460413] = {
    id = 111460413,
    name = "......呵呵，当然是开玩笑的。我还等着埃拉的家人痊愈的那一天。"
  },
  [111460414] = {
    id = 111460414,
    name = "呼，我就说嘛，你一定是个好人！"
  },
  [111460415] = {
    id = 111460415,
    name = "你其实就只是想要个帮手吧？"
  },
  [111460416] = {
    id = 111460416,
    name = "没有那回事，我只是想跟你这个临时同伴一起探险而已~"
  },
  [111460417] = {
    id = 111460417,
    name = "三人说说笑笑找到了住宿的地方。"
  },
  [111460418] = {
    id = 111460418,
    name = "夜晚......"
  },
  [111460419] = {
    id = 111460419,
    name = "幸好有大哥哥的提醒，不然我就把西默蒂瑞的秘密说出去了。"
  },
  [111460420] = {
    id = 111460420,
    name = "你运气不错。"
  },
  [111460421] = {id = 111460421, name = "嗯？"},
  [111460422] = {
    id = 111460422,
    name = "对呀，看着源晶都面不改色，我有预感，那个老医师是个靠谱的。"
  },
  [111460423] = {
    id = 111460423,
    name = "啊，你是说那个亮亮的石头。神殿里有很多啊~我生气的时候，会从神殿墙壁上抠下来两颗，小的时候，也会用来打弹珠玩。"
  },
  [111460424] = {
    id = 111460424,
    name = "这是什么神仙村庄......"
  },
  [111460425] = {
    id = 111460425,
    name = "所以，那个医师能成功对吧？"
  },
  [111460426] = {
    id = 111460426,
    name = "他收下了你的东西，想必会拿出像样的药物。"
  },
  [111460427] = {
    id = 111460427,
    name = "是啊，如果治不好，我们再回来找他，反正他一把年纪了也跑不了。"
  },
  [111460428] = {id = 111460428, name = "......"},
  [111460429] = {
    id = 111460429,
    name = "如果治好了他们，你有什么打算？"
  },
  [111460430] = {
    id = 111460430,
    name = "他们有无穷无尽的时间，想怎么样都行。不离开西默蒂瑞，他们就不会变老。但以后有了更多选择，才是真正的自由啊~"
  },
  [111460431] = {
    id = 111460431,
    name = "一道黑色的影子从门前掠过。"
  },
  [111460432] = {id = 111460432, name = "谁？！"},
  [111460433] = {
    id = 111460433,
    name = "你开门没有发现任何人影，岫四下望了望，确认没人来过。"
  },
  [111460434] = {
    id = 111460434,
    name = "疑神疑鬼的，没有人呀~会不会是你听错了？"
  },
  [111460435] = {
    id = 111460435,
    name = "希望是吧。"
  },
  [111460436] = {
    id = 111460436,
    name = "五天后......"
  },
  [111460437] = {
    id = 111460437,
    name = "真的成功了？！"
  },
  [111460438] = {
    id = 111460438,
    name = "这种药虽然能固本培元，净化浊炁，但在服用以后身体会很虚弱，短时间内不能过度接触浊炁，否则药就白吃了，还会有生命危险。"
  },
  [111460439] = {
    id = 111460439,
    name = "知道了！谢谢，谢谢老先生！"
  },
  [111460440] = {
    id = 111460440,
    name = "没想到这么顺利就拿到了药，我们快点回去吧！"
  },
  [111460441] = {
    id = 111460441,
    name = "到啦，就在这里等日落吧！"
  },
  [111460442] = {
    id = 111460442,
    name = "三人连夜赶路，总算在日落之前到达了黑森林。一路斩杀了好几只大型魔物，岫此时靠在树上闭目休息。"
  },
  [111460443] = {
    id = 111460443,
    name = "这次真的不知道该怎么感谢你们。"
  },
  [111460444] = {
    id = 111460444,
    name = "让我在村庄里多住一阵怎么样？"
  },
  [111460445] = {
    id = 111460445,
    name = "我很喜欢西默蒂瑞。"
  },
  [111460446] = {
    id = 111460446,
    name = "嗯！住多久都行！大哥哥，你呢？"
  },
  [111460447] = {
    id = 111460447,
    name = "唔，给我一块源晶吧，就当是报酬了。"
  },
  [111460448] = {
    id = 111460448,
    name = "哇，贪财的家伙。"
  },
  [111460449] = {
    id = 111460449,
    name = "难道你不心动吗？"
  },
  [111460450] = {
    id = 111460450,
    name = "那，那也没你这样直接开口要的。"
  },
  [111460451] = {
    id = 111460451,
    name = "那些东西对我来说，没什么用途。你们喜欢，想拿多少拿多少！"
  },
  [111460452] = {
    id = 111460452,
    name = "喂，你可别这么大方！如果被外界知道了，这些源晶会给西默蒂瑞招来祸端的，千万别再这样傻乎乎地拿出来了。"
  },
  [111460453] = {id = 111460453, name = "嗯。"},
  [111460454] = {
    id = 111460454,
    name = "沙沙......"
  },
  [111460455] = {id = 111460455, name = "？！"},
  [111460456] = {
    id = 111460456,
    name = "岫睁开了双眼，警惕地看向了声音来源的方向。你朝她比了个手势，岫灵巧地跃步，悄悄接近了那个地方。"
  },
  [111460457] = {id = 111460457, name = "出来！"},
  [111460458] = {
    id = 111460458,
    name = "嗯？没人？难道是风？"
  },
  [111460459] = {
    id = 111460459,
    name = "你们先进。"
  },
  [111460460] = {id = 111460460, name = "哦。"},
  [111460461] = {
    id = 111460461,
    name = "两人飞快地钻进了树里，你站在原地许久都没见到其他人影，于是缓缓退步，转身快速进了结界空间。"
  },
  [111460462] = {
    id = 111460462,
    name = "小埃拉？"
  },
  [111460463] = {
    id = 111460463,
    name = "雷克，我回来了！"
  },
  [111460464] = {
    id = 111460464,
    name = "埃拉兴奋地朝雷克招手，雷克匆匆赶了过来。"
  },
  [111460465] = {
    id = 111460465,
    name = "小埃拉，你这次有没有受伤？"
  },
  [111460466] = {
    id = 111460466,
    name = "没有哦~一路上的魔物都被岫和大哥哥杀掉了。我们还带回来了可以治好大家的药！"
  },
  [111460467] = {
    id = 111460467,
    name = "真的？！"
  },
  [111460468] = {
    id = 111460468,
    name = "嗯，是真的！吃了这个药，你们就不用被困在西默蒂瑞了！"
  },
  [111460469] = {
    id = 111460469,
    name = "那太好了！"
  },
  [111460470] = {
    id = 111460470,
    name = "小埃拉回来啦？这次你走了好久。"
  },
  [111460471] = {
    id = 111460471,
    name = "嗯，以前只能在附近走走，这次去了比较远的地方。"
  },
  [111460472] = {
    id = 111460472,
    name = "哎呀，我们可担心坏了，就怕你出了什么事，找不到药就算了，你平安无事就好。"
  },
  [111460473] = {
    id = 111460473,
    name = "对不起，让你们担心了。"
  },
  [111460474] = {
    id = 111460474,
    name = "小埃拉。"
  },
  [111460475] = {
    id = 111460475,
    name = "村长！村长我找到药了！"
  },
  [111460476] = {
    id = 111460476,
    name = "埃拉取下身上的挂包，放在村长的手里。"
  },
  [111460477] = {
    id = 111460477,
    name = "不过服药之后会虚弱一段时间，还有这段时间千万不能被浊炁再次侵蚀。也就是说，只要休息一段时间就能出去了！"
  },
  [111460478] = {
    id = 111460478,
    name = "好，好，好。"
  },
  [111460479] = {
    id = 111460479,
    name = "小埃拉又带药回来啦。"
  },
  [111460480] = {
    id = 111460480,
    name = "嗯，这次的药一定有用的！"
  },
  [111460481] = {
    id = 111460481,
    name = "没关系，不就是试药嘛，成不成都习惯了。"
  },
  [111460482] = {
    id = 111460482,
    name = "这次我先来。"
  },
  [111460483] = {
    id = 111460483,
    name = "村长倒出一颗像糖豆一样的药丸，深吸了一口气，整个吞了下去。"
  },
  [111460484] = {
    id = 111460484,
    name = "怎么样？"
  },
  [111460485] = {
    id = 111460485,
    name = "所有人都和岫一样紧张地盯着村长，此时一股浓烈的黑气从村长体内散发出来。"
  },
  [111460486] = {id = 111460486, name = "！！！"},
  [111460487] = {
    id = 111460487,
    name = "村长突然弯下了腰，你不自觉地将手放在了武器上。"
  },
  [111460488] = {id = 111460488, name = "......"},
  [111460489] = {
    id = 111460489,
    name = "村长，你感觉怎么样？！"
  },
  [111460490] = {
    id = 111460490,
    name = "村长缓缓抬头，露出了虚弱而苍白的脸，浮现了发自真心的笑意。"
  },
  [111460491] = {
    id = 111460491,
    name = "身体......突然变轻了......就像重新活过来了一样......"
  },
  [111460492] = {
    id = 111460492,
    name = "埃拉欣喜地大喊“太好了”，周围人群愣了一秒才发出了欢呼声。"
  },
  [111460493] = {
    id = 111460493,
    name = "村长！这个药真的有用？！"
  },
  [111460494] = {
    id = 111460494,
    name = "嗯。只不过确实会感觉到疲累，我已经很久很久没有这样的感受了，这才是真正的活着啊......"
  },
  [111460495] = {
    id = 111460495,
    name = "小埃拉，你找到真正的药了，谢谢你，谢谢你。"
  },
  [111460496] = {
    id = 111460496,
    name = "我就知道，我能做到，我做到了！太好了，太好了......"
  },
  [111460497] = {
    id = 111460497,
    name = "嗯，雷克，你去把这些药分给大家吧。"
  },
  [111460498] = {id = 111460498, name = "好！"},
  [111460499] = {
    id = 111460499,
    name = "叔叔没来吗，我拿一颗药给他。"
  },
  [111460500] = {
    id = 111460500,
    name = "不用了。"
  },
  [111460501] = {
    id = 111460501,
    name = "一脸严肃的少年直挺地走了过来。"
  },
  [111460502] = {
    id = 111460502,
    name = "你又从哪里带来了乱七八糟的药，竟然还让村长试吃！"
  },
  [111460503] = {
    id = 111460503,
    name = "叔叔，村长已经好了，你也快吃吧！"
  },
  [111460504] = {
    id = 111460504,
    name = "我说过我没病，你看看村长，哪里像好了的样子？"
  },
  [111460505] = {
    id = 111460505,
    name = "埃拉叔叔一把打掉了埃拉的手，药咕噜噜地滚落，埃拉愣在原地。与此同时，震耳欲聋的幼童啼哭声响起。"
  },
  [111460506] = {
    id = 111460506,
    name = "哎呀，你们看，小哀的黑气也散了！"
  },
  [111460507] = {
    id = 111460507,
    name = "抱着孩子的妇人惊喜地大喊，大家纷纷凑了上去，脸上露出了笑容。"
  },
  [111460508] = {
    id = 111460508,
    name = "小哀，小哀~等你恢复了，妈妈就带你出去。白天看外面的世界，晚上回家休息，你要好好长大啊~"
  },
  [111460509] = {
    id = 111460509,
    name = "吃了药，就可以继续长大了？！"
  },
  [111460510] = {
    id = 111460510,
    name = "没错，只要服了药就能长大！"
  },
  [111460511] = {
    id = 111460511,
    name = "我也来一颗！"
  },
  [111460512] = {
    id = 111460512,
    name = "你们，你们怎么都那么相信他！"
  },
  [111460513] = {
    id = 111460513,
    name = "小埃拉不会害我们的呀~"
  },
  [111460514] = {
    id = 111460514,
    name = "你们这群目光短浅的人！你们有没有想过一旦我们出去，西默蒂瑞就不再是秘密！外面的人会进来，抢占我们的家园，而我们完全没有保护自己的能力！"
  },
  [111460515] = {
    id = 111460515,
    name = "可是我遇上的人都很好啊，外面没有那么可怕的。"
  },
  [111460516] = {
    id = 111460516,
    name = "（小声）我现在明白为什么你说他运气不错了。"
  },
  [111460517] = {id = 111460517, name = "......"},
  [111460518] = {
    id = 111460518,
    name = "叔叔，你不想出去吗？"
  },
  [111460519] = {
    id = 111460519,
    name = "出去有什么好？你不要被他们骗了！他们可能是看上了西默蒂瑞的财富！"
  },
  [111460520] = {
    id = 111460520,
    name = "你是说那些源晶吗？我们留着也没什么用，不如拿出去换更多的东西来改变大家的生活！"
  },
  [111460521] = {
    id = 111460521,
    name = "你，你竟然连源晶的事也告诉他们了！神殿是我们的信仰之地，是我们世代守护的神！而你竟然想用源晶做交易！你，你这个叛徒！"
  },
  [111460522] = {
    id = 111460522,
    name = "叔叔，你才是被骗的那一个。这个世界根本就没有神，或者说，神早就抛弃了我们！我们应该认清现实。"
  },
  [111460523] = {
    id = 111460523,
    name = "少年猛地扇了埃拉一巴掌！埃拉被打得半边脸通红。"
  },
  [111460524] = {
    id = 111460524,
    name = "西默蒂瑞现在就在神的庇佑下，我们能拥有永恒的生命都是因为神的力量，你不可以亵渎！"
  },
  [111460525] = {
    id = 111460525,
    name = "那是假的！村长都告诉我了，那是光阴沙漏的作用！"
  },
  [111460526] = {
    id = 111460526,
    name = "没错，村长不是把真相告诉我们了吗。当时如果村长没有及时用光阴沙漏护住我们，我们早就死了。"
  },
  [111460527] = {id = 111460527, name = "......"},
  [111460528] = {
    id = 111460528,
    name = "村长！村长！不好了！"
  },
  [111460529] = {
    id = 111460529,
    name = "一个孩子连滚带爬地跑了过来，神色惊惶。"
  },
  [111460530] = {
    id = 111460530,
    name = "怎么了？"
  },
  [111460531] = {
    id = 111460531,
    name = "有人，有外人闯入了祭坛！！"
  },
  [111460532] = {id = 111460532, name = "！！！"},
  [111460533] = {
    id = 111460533,
    name = "村长脚下一个踉跄，几乎要站不稳。"
  },
  [111460534] = {
    id = 111460534,
    name = "快去，你们快去阻止那个人！光阴沙漏就在祭坛！"
  },
  [111460535] = {
    id = 111460535,
    name = "还未来得及服药的村民急忙往祭坛赶去，剩下虚弱的人们坐在原地，怀疑的目光直直投向了你和岫。"
  },
  [111460536] = {
    id = 111460536,
    name = "怎么会？怎么会有外人......"
  },
  [111460537] = {
    id = 111460537,
    name = "如果光阴沙漏有什么差错，那你就滚出西默蒂瑞，永远不要回来！"
  },
  [111460538] = {
    id = 111460538,
    name = "埃拉叔叔怒火中烧，和人群一起往祭坛跑去。"
  },
  [111460539] = {
    id = 111460539,
    name = "我的直觉果然没有错，我们被跟踪了。"
  },
  [111460540] = {
    id = 111460540,
    name = "是那个家伙！他怎么跟着我们进来的？"
  },
  [111460541] = {id = 111460541, name = "......"},
  [111460542] = {
    id = 111460542,
    name = "埃拉，别发愣了！我们也赶紧过去！"
  },
  [111460543] = {
    id = 111460543,
    name = "岫拍了一下埃拉的肩，他忽地缓过了神，连忙点点头。"
  },
  [111460544] = {
    id = 111460544,
    name = "真的放心让我们去吗？如果我们和那个闯入者是同伙呢？"
  },
  [111460545] = {
    id = 111460545,
    name = "不，我相信你们！如果你们真的是坏人，就不会一而再再而三地帮我。我们一起去！"
  },
  [111460546] = {
    id = 111460546,
    name = "哈哈哈哈......本来看到那小子随随便便就拿出两颗源晶，想找机会下手，想不到意外发现了这么一个宝藏之地，还有让人不老不死的宝物！"
  },
  [111460547] = {
    id = 111460547,
    name = "祭坛上，黑衣人被村民团团围住，手上拿着一块晶莹的沙漏来回把玩，沙漏没有任何流动的迹象。他的眼中迸射出贪婪的光芒，丝毫不将村民放在眼中。"
  },
  [111460548] = {
    id = 111460548,
    name = "放下沙漏，不准你带走它！"
  },
  [111460549] = {
    id = 111460549,
    name = "哼，就凭你们这群弱小的普通人，还想阻拦我？！"
  },
  [111460550] = {
    id = 111460550,
    name = "村民一个个地冲上去，都被他轻而易举地踹飞。"
  },
  [111460551] = {
    id = 111460551,
    name = "这种珍贵的宝物，你们没有资格拥有它。"
  },
  [111460552] = {
    id = 111460552,
    name = "夜色中，黑衣人的掌心汇聚了一团魔法光圈，击中在村民们的身上，将他们束缚在原地。"
  },
  [111460553] = {
    id = 111460553,
    name = "不能动了！！！"
  },
  [111460554] = {
    id = 111460554,
    name = "哈哈哈哈！看到你们这些普通人无可奈何的样子，我可是愉悦得很呢。"
  },
  [111460555] = {
    id = 111460555,
    name = "把宝物还给他们！"
  },
  [111460556] = {
    id = 111460556,
    name = "一道箭矢破空而来，精准地刺穿了黑衣人的手腕！"
  },
  [111460557] = {
    id = 111460557,
    name = "啊啊啊！！"
  },
  [111460558] = {
    id = 111460558,
    name = "沙漏落地之前，被黑衣人用另一只手及时接住，放进了他的魔法袋里。"
  },
  [111460559] = {
    id = 111460559,
    name = "唔......探险者啊......你们想妨碍我？！"
  },
  [111460560] = {
    id = 111460560,
    name = "你这个卑鄙之人！竟然尾随我们！"
  },
  [111460561] = {
    id = 111460561,
    name = "岫再次射出一箭，黑衣人被逼退。你立即冲上前想夺回沙漏，却发现这个家伙企图趁乱伺机逃跑。岫灵机一动，又射出了几箭，箭矢封住了他撤退的路线，终于让他退无可退。"
  },
  [111460562] = {
    id = 111460562,
    name = "黑衣人自知不是你们的对手，趁机把埃拉叔叔抓了过来，用手扼住了他的咽喉。"
  },
  [111460563] = {id = 111460563, name = "叔叔！"},
  [111460564] = {
    id = 111460564,
    name = "不想要这个人死的话，就放我走！"
  },
  [111460565] = {
    id = 111460565,
    name = "不......能......放！"
  },
  [111460566] = {
    id = 111460566,
    name = "岫，大哥哥，怎么办？！"
  },
  [111460567] = {
    id = 111460567,
    name = "黑衣人趁你们分心，猛地将埃拉叔叔推向你们，随后朝赶来的村长而去！"
  },
  [111460568] = {id = 111460568, name = "唔——"},
  [111460569] = {
    id = 111460569,
    name = "村长！你竟然挟持村长！"
  },
  [111460570] = {
    id = 111460570,
    name = "哈哈哈哈！原来是村长啊，这下我们可以好好谈谈了吧。"
  },
  [111460571] = {
    id = 111460571,
    name = "喂，那边那两位探险者，这里可是一块宝地啊，不如咱们三个一起瓜分了它。我呢出点力气，把这些人清理了，这个宝物就归我。"
  },
  [111460572] = {
    id = 111460572,
    name = "至于那些源晶我也懒得找了，全归你们两个怎么样？"
  },
  [111460573] = {
    id = 111460573,
    name = "你做梦！他们是绝对不会同意的！"
  },
  [111460574] = {
    id = 111460574,
    name = "不行哦，你的提议不公平啊。"
  },
  [111460575] = {
    id = 111460575,
    name = "那你想怎么分？"
  },
  [111460576] = {
    id = 111460576,
    name = "这里既然有让人永生的宝物，那一定还有别的。这个宝物暂且不说，等找到其他的宝藏，找个拍卖行卖了。不过怎么个分法，当然是人越少，分得越多不是吗？"
  },
  [111460577] = {
    id = 111460577,
    name = "你施放一道魔法将岫和埃拉束缚住，两人瞪大眼睛看向了你。"
  },
  [111460578] = {
    id = 111460578,
    name = "大哥哥......"
  },
  [111460579] = {
    id = 111460579,
    name = "你......你放开我！"
  },
  [111460580] = {
    id = 111460580,
    name = "哈哈果然是同道中人！"
  },
  [111460581] = {
    id = 111460581,
    name = "本来就是萍水相逢的过客。遇到这样的好事，怎么能便宜了别人？"
  },
  [111460582] = {
    id = 111460582,
    name = "不错不错，等我先杀了这些人，我们兄弟再慢慢来分。"
  },
  [111460583] = {
    id = 111460583,
    name = "等等，这个村长说不定知道这个沙漏的用法。村长先生，那就麻烦你告知一下用法了。"
  },
  [111460584] = {
    id = 111460584,
    name = "没想到你竟然是这种人！与这种盗贼同流合污，你简直......"
  },
  [111460585] = {
    id = 111460585,
    name = "安静一点。村长，你考虑得怎么样？"
  },
  [111460586] = {id = 111460586, name = "呵呵。"},
  [111460587] = {
    id = 111460587,
    name = "——嗤！！"
  },
  [111460588] = {
    id = 111460588,
    name = "趁黑衣人松懈，你一剑刺穿了他的手臂。"
  },
  [111460589] = {id = 111460589, name = "村长！"},
  [111460590] = {id = 111460590, name = "岫！"},
  [111460591] = {
    id = 111460591,
    name = "岫和埃拉同时解除了束缚，成功救下了村长。黑衣人一时慌乱，你急速地补了好几剑，切断了他的脚筋，废掉了他的行动力。"
  },
  [111460592] = {
    id = 111460592,
    name = "啊啊啊啊啊！啊啊啊......啊......你......你......你骗我！！"
  },
  [111460593] = {
    id = 111460593,
    name = "他狼狈地躺在地上，眉心只离剑尖半分。"
  },
  [111460594] = {
    id = 111460594,
    name = "大哥哥！我就知道，你一定是帮我们的！"
  },
  [111460595] = {
    id = 111460595,
    name = "他这种人，如果让他跑了，那这里一定会遭殃。所以，不光要把宝物拿回来，还得让他的尸体一起留下来才行啊。"
  },
  [111460596] = {
    id = 111460596,
    name = "吓死人啦！你这个家伙，怎么老是说一些让人心惊胆战的话！"
  },
  [111460597] = {
    id = 111460597,
    name = "所以要速战速决。"
  },
  [111460598] = {
    id = 111460598,
    name = "小心！！"
  },
  [111460599] = {
    id = 111460599,
    name = "黑衣人恶狠狠地将光阴沙漏拿了出来。"
  },
  [111460600] = {
    id = 111460600,
    name = "我得不到，你们也别想拿到，一起死吧！"
  },
  [111460601] = {
    id = 111460601,
    name = "他要毁了沙漏！！"
  },
  [111460602] = {
    id = 111460602,
    name = "啊啊啊......"
  },
  [111460603] = {
    id = 111460603,
    name = "你果断地杀掉了黑衣人。"
  },
  [111460604] = {
    id = 111460604,
    name = "但沙漏已经裂开了缝隙，流沙缓缓倾泻，仿佛萤火构建了一条浅浅的银河，银河停滞在空中，正在缓缓消散。"
  },
  [111460605] = {
    id = 111460605,
    name = "不，不要！"
  },
  [111460606] = {
    id = 111460606,
    name = "村长，怎么办！"
  },
  [111460607] = {
    id = 111460607,
    name = "......等流沙消失，这个结界就会破碎。"
  },
  [111460608] = {
    id = 111460608,
    name = "不，不行！来不及了！还有那么多人没有吃药！他们都会死的！"
  },
  [111460609] = {
    id = 111460609,
    name = "那就只有第二种办法了。"
  },
  [111460610] = {
    id = 111460610,
    name = "村长低声沉吟，念着古老的语言。光阴沙漏似乎感应到了，又重新流动了起来，最终凝聚成了虚空沙漏的模样。"
  },
  [111460611] = {
    id = 111460611,
    name = "好漂亮......"
  },
  [111460612] = {id = 111460612, name = "......"},
  [111460613] = {
    id = 111460613,
    name = "虚空沙漏缓缓升空，一束束光芒绽放，流沙飞向四周，彻底消失在空中。"
  },
  [111460614] = {id = 111460614, name = "！！！"},
  [111460615] = {
    id = 111460615,
    name = "村长做完一切，脸上已不见血色，众人赶紧过去扶着他。"
  },
  [111460616] = {
    id = 111460616,
    name = "我尽力了，我们只有一个小时的时间......让大家尽快服药，做好准备吧。"
  },
  [111460617] = {
    id = 111460617,
    name = "什么意思？"
  },
  [111460618] = {
    id = 111460618,
    name = "......我封印了光阴沙漏，再......再过一个小时，西默蒂瑞会被彻底封锁，这一次，时间会永远停滞。"
  },
  [111460619] = {id = 111460619, name = "！！！"},
  [111460620] = {
    id = 111460620,
    name = "彻底封锁......永远停滞......怎么会这样？"
  },
  [111460621] = {
    id = 111460621,
    name = "村民陆陆续续持着火把赶来了祭坛，得知这个消息后都失去了反应。"
  },
  [111460622] = {
    id = 111460622,
    name = "村长，即使我们治好了这个病，也不能离开了吗？"
  },
  [111460623] = {
    id = 111460623,
    name = "可以离开，但是西默蒂瑞会就此关闭，出去之后就再也无法回来。"
  },
  [111460624] = {
    id = 111460624,
    name = "呵呵呵呵......呵呵，我就说你小子做的无用功......不光差点把我们害死，这下连你也出不去了吧！"
  },
  [111460625] = {
    id = 111460625,
    name = "哎，没事啦，大不了就和以前一样。"
  },
  [111460626] = {
    id = 111460626,
    name = "阿姨，你不跟我一起出去吗？"
  },
  [111460627] = {
    id = 111460627,
    name = "想想还是算了。外面太危险了，我和小哀都是普通人，虽然我想看着她长大，但我更不想后悔......"
  },
  [111460628] = {
    id = 111460628,
    name = "雷克，你呢，你不是想出去吗？一起去外面探险，我们说好的......"
  },
  [111460629] = {
    id = 111460629,
    name = "不行，我不能离开我的父母，我做不到再也不见他们。"
  },
  [111460630] = {
    id = 111460630,
    name = "埃拉询问着每一个人，得到的答案如一盆又一盆的凉水倒在他身上。"
  },
  [111460631] = {
    id = 111460631,
    name = "他手脚冰凉，眼睛注视着前方。眼前火光烁烁，视野慢慢模糊......"
  },
  [111460632] = {
    id = 111460632,
    name = "哎呀，其实这里没什么不好的呀~女人一直保持青春美貌的样子，男人也永远身强力壮，都是多亏了光阴沙漏呢。"
  },
  [111460633] = {
    id = 111460633,
    name = "埃拉，只要在这里，我们就会像遗迹的神族一样，拥有永恒的生命了。"
  },
  [111460634] = {
    id = 111460634,
    name = "虽然我很想出去，但我害怕外面的世界。比起有魔物存在的世界，我更想在这里好好地活着。"
  },
  [111460635] = {
    id = 111460635,
    name = "......你们，都不想离开吗？"
  },
  [111460636] = {
    id = 111460636,
    name = "埃拉，你是不是舍不得你的朋友呀？"
  },
  [111460637] = {
    id = 111460637,
    name = "怀抱幼童的女人看向岫和你，跟村长建议。"
  },
  [111460638] = {
    id = 111460638,
    name = "这两位也是好人，如果埃拉舍不得他们，留下他们也是可以的。"
  },
  [111460639] = {
    id = 111460639,
    name = "嗯，两位治好了大家的病，还帮我们除掉了侵犯者，留下来，就不会变老，还能拥有永恒的生命。"
  },
  [111460640] = {
    id = 111460640,
    name = "小姑娘，你不是很喜欢西默蒂瑞吗？如果你们想加入西默蒂瑞这个大家族，我想没有人会反对的。"
  },
  [111460641] = {
    id = 111460641,
    name = "我......我......"
  },
  [111460642] = {
    id = 111460642,
    name = "你不是说这里是你理想中的完美世界吗？一直留在这里快乐地生活，是你的希望吧。"
  },
  [111460643] = {id = 111460643, name = "......"},
  [111460644] = {id = 111460644, name = "......"},
  [111460645] = {
    id = 111460645,
    name = "......嗯。"
  },
  [111460646] = {
    id = 111460646,
    name = "我一直在寻找这样一个地方，一开始，我以为我真的找到了。可是后来我才知道，原来你们也有烦恼。我想帮助你们，解决这个难题，这样一切就更完美了......"
  },
  [111460647] = {
    id = 111460647,
    name = "无忧无虑，与世无争......我一直以为这就是我所追求的完美世界。可对比外面整个世界的话，我果然还是舍不得。"
  },
  [111460648] = {
    id = 111460648,
    name = "我有家人，有朋友，也有理想......想让外面的世界变得和西默蒂瑞一样......"
  },
  [111460649] = {
    id = 111460649,
    name = "抱歉没办法答应你们的邀请了，但我很高兴哦。很高兴来到西默蒂瑞，很高兴认识大家，童话里的故事是假的，但西默蒂瑞是真实存在的。"
  },
  [111460650] = {
    id = 111460650,
    name = "一直以来的愿望终于实现了呢，谢谢你们。"
  },
  [111460651] = {
    id = 111460651,
    name = "大哥哥，你呢？"
  },
  [111460652] = {
    id = 111460652,
    name = "谢谢大家的挽留，虽然能拥有永恒的生命很吸引人，但我还有更重要的事，不能留在这里。"
  },
  [111460653] = {
    id = 111460653,
    name = "我也不要留在这里。"
  },
  [111460654] = {
    id = 111460654,
    name = "西默蒂瑞真的很小、很小......当我见过了外面的世界之后，就不想一生都只能待在这里。如果我留下，未来我一定会后悔......"
  },
  [111460655] = {
    id = 111460655,
    name = "我想活出自己的人生！"
  },
  [111460656] = {
    id = 111460656,
    name = "埃拉退后了一步，泪水顺着眼角滑落。"
  },
  [111460657] = {
    id = 111460657,
    name = "真的是我做错了吗？明明就差最后一点了，为什么，还是不行呢......是我太不小心了，泄露了源晶才害了——"
  },
  [111460658] = {
    id = 111460658,
    name = "别自责了，小埃拉。你已经做得很好了。"
  },
  [111460659] = {
    id = 111460659,
    name = "是呀，等大家都恢复健康了，就能去山上慢慢适应魔法和浊炁......"
  },
  [111460660] = {
    id = 111460660,
    name = "对了，还有你说的那个源晶也是很有用的，到时做几把魔导武器，不比你外面的武器差哦。"
  },
  [111460661] = {
    id = 111460661,
    name = "你要走，就走吧。"
  },
  [111460662] = {
    id = 111460662,
    name = "叔叔......"
  },
  [111460663] = {
    id = 111460663,
    name = "呵，怎么劝也不听，拿着你母亲的东西滚吧！"
  },
  [111460664] = {
    id = 111460664,
    name = "埃拉叔叔拿出一个陈旧的袋子扔在了埃拉身上，大步走出了人群。"
  },
  [111460665] = {id = 111460665, name = "叔叔！"},
  [111460666] = {
    id = 111460666,
    name = "你叔叔是不想在你面前掉眼泪呢，他其实很舍不得你离开。你能在外面找到治好我们的药，说不定哪天也能找到回来的方法呢？你永远是西默蒂瑞的一员啊。"
  },
  [111460667] = {
    id = 111460667,
    name = "埃拉抱着袋子泣不成声，周围的村民一个个地上前安慰他。"
  },
  [111460668] = {
    id = 111460668,
    name = "时间不多了，我们走吧。"
  },
  [111460669] = {
    id = 111460669,
    name = "离开村庄之前，所有的村民都来到了入口处，一个又一个袋子放在埃拉身上，他们像家人一般和他道别。"
  },
  [111460670] = {
    id = 111460670,
    name = "岫感动地和所有人挥手，拉着埃拉走出了甬道。"
  },
  [111460671] = {
    id = 111460671,
    name = "离开西默蒂瑞后，埃拉沉默了很久。他一直默默跟在你们身后，岫不习惯沉闷的氛围，跟你说起了话。"
  },
  [111460672] = {
    id = 111460672,
    name = "你看大家都很喜欢埃拉啊~把最好的都留给了他呢。"
  },
  [111460673] = {
    id = 111460673,
    name = "嗯，因为他是西默蒂瑞的希望啊。村长当时这样说过吧。"
  },
  [111460674] = {
    id = 111460674,
    name = "埃拉停下了脚步。"
  },
  [111460675] = {
    id = 111460675,
    name = "大哥哥，岫，谢谢你们。"
  },
  [111460676] = {
    id = 111460676,
    name = "啊，我也没做什么啊，还让事态变成了这样......"
  },
  [111460677] = {
    id = 111460677,
    name = "不是的，你们帮了我很多。我想通了，我不能一直依赖你们。我是西默蒂瑞的希望，我要去历练，成为一个像大哥哥这样优秀的探险者！"
  },
  [111460678] = {
    id = 111460678,
    name = "诶？为什么不是我这样的？"
  },
  [111460679] = {
    id = 111460679,
    name = "他看起来更可靠的样子......"
  },
  [111460680] = {
    id = 111460680,
    name = "什么呀，可靠的明明是我吧！他就像狐狸一样狡猾，每天都不知道在想什么~像我这样开心快乐难道不好吗？"
  },
  [111460681] = {
    id = 111460681,
    name = "埃拉狂摇脑袋。"
  },
  [111460682] = {
    id = 111460682,
    name = "那就加油吧，未来的探险者。"
  },
  [111460683] = {id = 111460683, name = "嗯！"},
  [111460684] = {
    id = 111460684,
    name = "埃拉和你们道别，离开的时候，岫一直挥着手，直到看不见他的身影。"
  },
  [111460685] = {
    id = 111460685,
    name = "好啦，接下来我也要出发了！"
  },
  [111460686] = {
    id = 111460686,
    name = "又去找童话故事里的某个地方？"
  },
  [111460687] = {
    id = 111460687,
    name = "会飞的花园、地下的城堡......又或者是真正的完美世界！"
  },
  [111460688] = {
    id = 111460688,
    name = "一定有很多奇迹会在前面等着我。那些自由的，没有任何束缚的，充满欢笑的世界......我一定会找到的！"
  },
  [111460689] = {
    id = 111460689,
    name = "祝你好运，再见。"
  },
  [111460690] = {
    id = 111460690,
    name = "下次再见啦，心思深沉的大哥哥~♪"
  },
  [111460691] = {
    id = 111460691,
    name = "你笑了笑，看着灵动的身影消失在视野中。"
  },
