    name = "他告诉你，他们有一件宝物遗失在了湖中，刚才在水下没来得及取。"
  },
  [200030008] = {
    id = 200030008,
    name = "如果你愿意帮他们找回宝物，他们愿意付出厚礼表达谢意。"
  },
  [200030009] = {
    id = 200030009,
    name = "你告诉他们魔物已死，湖中是安全的，让他们自己去搜寻，于是探险者再次下水，但往返数次皆空手而回。"
  },
  [200030010] = {
    id = 200030010,
    name = "宝物不会无故消失，你提议解剖魔物查看，两人的面色一僵，只能照你的话去做。"
  },
  [200030011] = {
    id = 200030011,
    name = "果然在魔物的体内发现了宝物。"
  },
  [200030012] = {
    id = 200030012,
    name = "探险者和少女再次对你表示感谢，送给了你一些物资作为谢礼。"
  },
  [200030013] = {
    id = 200030013,
    name = "你再次进入水中，并没有看到他们形容的宝物。"
  },
  [200030014] = {
    id = 200030014,
    name = "在气竭之前，你重新回到了水面，却发现岸边已空无一人。"
  },
  [200030015] = {
    id = 200030015,
    name = "你走到被剖开的魔物尸体身边，似乎明白了什么。"
  },
  [200030016] = {
    id = 200030016,
    name = "少女见你无动于衷，拿出了身上的财物。"
  },
  [200030017] = {
    id = 200030017,
    name = "她告诉你湖中有一头魔物，她的同伴进入湖中后就失去了讯息，只求你能帮忙，她愿意把所有的财物给你。"
  },
  [200030018] = {
    id = 200030018,
    name = "你见湖面虽然平静，但其中暗潮汹涌，于是捡起一块石头投入其中。"
  },
  [200030019] = {
    id = 200030019,
    name = "湖中瞬间波涛激荡，翻腾的水浪间，一个被魔物触手缠着的人影短暂露出水面。"
  },
  [200030020] = {
    id = 200030020,
    name = "「逃——」"
  },
  [200030021] = {
    id = 200030021,
    name = "探险者向少女发出了最后的警告。"
  },
  [200030022] = {
    id = 200030022,
    name = "少女惊慌失措，把唯一的希望寄托在了你身上。"
  },
  [200030023] = {
    id = 200030023,
    name = "你刚跳入湖中，魔物便直接奔你而来。"
  },
  [200030024] = {
    id = 200030024,
    name = "面对这头猛兽你不敢大意，全力施放魔法，终于在气竭前将其斩杀。"
  },
  [200030025] = {
    id = 200030025,
    name = "魔物死后，你并未发现方才看到的那名探险者，只在水底找到一枚发光的宝石。"
  },
  [200030026] = {
    id = 200030026,
    name = "待你上岸，才发现那名探险者不知何时已经上岸，少女正在为他疗伤。"
  },
  [200030027] = {
    id = 200030027,
    name = "少女向你问起湖中的情况，并问你是否看到一枚发光的宝石。"
  },
  [200030028] = {
    id = 200030028,
    name = "你告诉少女刚才战斗太过激烈，并未发现什么，少女的表情显得有些落寞、遗憾。"
  },
  [200030029] = {
    id = 200030029,
    name = "你正准备告辞，突然感受到包裹中的宝石出现了魔力波动。"
  },
  [200030030] = {
    id = 200030030,
    name = "你来不及做出反应，便晕了过去......"
  },
  [200030031] = {
    id = 200030031,
    name = "醒来后你发现自己的随身财物，以及那枚宝石均已消失不见。"
  },
  [200030032] = {
    id = 200030032,
    name = "你告诉了少女自己的发现，并将宝石交给了她。"
  },
  [200030033] = {
    id = 200030033,
    name = "少女显得非常高兴，她从包裹中翻找出各种有用的物品，一股脑地塞到了你的手中。"
  },
  [200030034] = {
    id = 200030034,
    name = "二人再次对你表示感谢后，便告辞离去。"
  },
  [200030035] = {
    id = 200030035,
    name = "少女见你拒绝，眼中露出绝望。"
  },
  [200030036] = {
    id = 200030036,
    name = "下一秒，她就跳入了湖泊。"
  },
  [200030037] = {
    id = 200030037,
    name = "湖中一阵浪潮汹涌，不多时一头巨大的魔物浮出湖面。"
  },
  [200030038] = {
    id = 200030038,
    name = "你来不及隐藏，魔物径直向你袭来。"
  },
  [200030039] = {
    id = 200030039,
    name = "你见已无法躲避，便全力出手，费了不少精力才将魔物斩杀。"
  },
  [200030040] = {
    id = 200030040,
    name = "随后，你在魔物的体内发现了两人的尸骨，不由叹了口气。"
  },
  [200030041] = {
    id = 200030041,
    name = "你离开了湖泊，隐隐察觉到身后少女的绝望。"
  },
  [200030042] = {
    id = 200030042,
    name = "没走几步，你听到身后传来重物落水的声音。"
  },
  [200030043] = {
    id = 200030043,
    name = "回头看去，那少女竟然跳入了湖泊。"
  },
  [200030044] = {
    id = 200030044,
    name = "湖中一阵浪潮汹涌，不多时一头巨大的魔物浮出湖面。"
  },
  [200030045] = {
    id = 200030045,
    name = "你来不及隐藏，魔物便径直向你袭来。"
  },
  [200030046] = {
    id = 200030046,
    name = "你见已无法躲避，便全力出手，费了不少精力才将魔物斩杀。"
  },
  [200030047] = {
    id = 200030047,
    name = "随后，你在魔物的体内发现了两人的尸骨，不由叹了口气。"
  },
  [200040001] = {
    id = 200040001,
    name = "你走在山道上，忽然听到前方传来争吵声。"
  },
  [200040002] = {
    id = 200040002,
    name = "你走近一看，几名凶恶的壮汉正将一名探险者逼到崖边。"
  },
  [200040003] = {
    id = 200040003,
    name = "你靠近了些，从他们的对话中得知了大概情况。"
  },
  [200040004] = {
    id = 200040004,
    name = "那名探险者原本是这几名壮汉的同伙，他们一直做着杀人抢劫的勾当，现在那名探险者想拆伙，不想再继续作恶了。"
  },
  [200040005] = {
    id = 200040005,
    name = "领头的壮汉告诉那人，一旦踏上了这条路，便只能一直走到黑，绝不存在退路。"
  },
  [200040006] = {
    id = 200040006,
    name = "探险者一直求饶，表示愿意拿出所有的财物换取自由。"
  },
  [200040007] = {
    id = 200040007,
    name = "壮汉威胁探险者必须跟他们回去。"
  },
  [200040008] = {
    id = 200040008,
    name = "「你要是在路上想通了，我们还是一起宰肥羊过日子的好兄弟。」"
  },
  [200040009] = {
    id = 200040009,
    name = "「要是想不开，我们就送你上路。」"
  },
  [200040010] = {
    id = 200040010,
    name = "（看样子这群劫匪是在逼迫这个想改邪归正的同伙......）"
  },
  [200040011] = {
    id = 200040011,
    name = "你站了出来，让这群劫匪给探险者一个机会，既然人各有志，就不该再勉强。"
  },
  [200040012] = {
    id = 200040012,
    name = "「你算什么东西，莫名其妙管闲事。」"
  },
  [200040013] = {
    id = 200040013,
    name = "这群壮汉将矛头对准了你。"
  },
  [200040014] = {
    id = 200040014,
    name = "探险者让你快走，他不想让陌生人受牵连。"
  },
  [200040015] = {
    id = 200040015,
    name = "「想走？让你知道多管闲事的后果！」"
  },
  [200040016] = {
    id = 200040016,
    name = "几名壮汉将你围住，向你发起了攻击。"
  },
  [200040017] = {
    id = 200040017,
    name = "对你而言，他们的攻势看似凌厉凶狠，实则破绽百出。"
  },
  [200040018] = {
    id = 200040018,
    name = "不一会儿便被你打得趴在地上哭爹喊娘，纷纷求饶。"
  },
  [200040019] = {
    id = 200040019,
    name = "探险者对你非常感激，向你讲述了往事。"
  },
  [200040020] = {
    id = 200040020,
    name = "「这一切都是为了我在大陆的妻子。」"
  },
  [200040021] = {
    id = 200040021,
    name = "「我一直很厌恶这样的生活，但为了满足我妻子的愿望，我一直在强迫自己。」"
  },
  [200040022] = {
    id = 200040022,
    name = "「我不断告诉自己再坚持一下，只要再多赚一点就可以回家了......」"
  },
  [200040023] = {
    id = 200040023,
    name = "「可是前些日子，我却收到了她和别的男人私奔的消息。」"
  },
  [200040024] = {
    id = 200040024,
    name = "「唉，所以我便决定离开，只是没想到这些曾经的兄弟们......」"
  },
  [200040025] = {
    id = 200040025,
    name = "「幸好有你出现，才让我重新拥有了自由。」"
  },
  [200040026] = {
    id = 200040026,
    name = "「那你以后打算做些什么呢？」"
  },
  [200040027] = {
    id = 200040027,
    name = "「我会烹饪一些食材，之前也做过工匠的活计。」"
  },
  [200040028] = {
    id = 200040028,
    name = "「所以一直在厨师与工匠中犹豫，不知道做哪个好。」"
  },
  [200040029] = {
    id = 200040029,
    name = "「深渊中有各种各样的材料，用这些材料制作的器具不少都有特殊功效。」"
  },
  [200040030] = {
    id = 200040030,
    name = "「探险者对这方面的需求量很大。」"
  },
  [200040031] = {
    id = 200040031,
    name = "「做一名工匠的话，养活自己不成问题。」"
  },
  [200040032] = {
    id = 200040032,
    name = "探险者非常感激你的提议。"
  },
  [200040033] = {
    id = 200040033,
    name = "他将包袱中的一些物资作为谢礼送给了你，笑着向你道别。"
  },
  [200040034] = {
    id = 200040034,
    name = "「深渊中有许多珍奇的魔物、植物，如果能深入研究、烹调，想必可以做出许多美味佳肴。」"
  },
  [200040035] = {
    id = 200040035,
    name = "「这也算是不错的生计。」"
  },
  [200040036] = {
    id = 200040036,
    name = "探险者非常感激你的提议。"
  },
  [200040037] = {
    id = 200040037,
    name = "作为报答，他将自己多余的物资全都送给了你。"
  },
  [200040038] = {
    id = 200040038,
    name = "从此轻装上阵，向着厨师之路进发。"
  },
  [200040039] = {
    id = 200040039,
    name = "探险者后退了一步，走向了崖边。"
  },
  [200040040] = {
    id = 200040040,
    name = "「像我这样的人，这一生不如就这样结束了吧。」"
  },
  [200040041] = {
    id = 200040041,
    name = "他转身跳下了悬崖。"
  },
  [200040042] = {
    id = 200040042,
    name = "崖边卷起飓风，壮汉们震惊地愣在原地，没有一个人敢上前。"
  },
  [200040043] = {
    id = 200040043,
    name = "有人提议去找他的尸体，终究兄弟一场。"
  },
  [200040044] = {
    id = 200040044,
    name = "但很快就被其他人否定了。"
  },
  [200040045] = {
    id = 200040045,
    name = "「收个屁！让那家伙喂魔物吧！」"
  },
  [200040046] = {
    id = 200040046,
    name = "「就是，就是！要不是他，我们那几桩生意还不会被搅黄。」"
  },
  [200040047] = {
    id = 200040047,
    name = "几人骂骂咧咧地离开了。"
  },
  [200040048] = {
    id = 200040048,
    name = "他们走后，你注意到崖边的石头上出现了一只手，跳崖的探险者半悬在空中。"
  },
  [200040049] = {
    id = 200040049,
    name = "你走上前将他拉了起来。"
  },
  [200040050] = {
    id = 200040050,
    name = "探险者很感激你的帮助，苦笑着说起了自己的往事。"
  },
  [200040051] = {
    id = 200040051,
    name = "「虽然当年我是被逼跟着他们一起杀人抢劫......」"
  },
  [200040052] = {
    id = 200040052,
    name = "「......但确实做了很多不可饶恕的事。」"
  },
  [200040053] = {
    id = 200040053,
    name = "「我也一直没有勇气离开。」"
  },
  [200040054] = {
    id = 200040054,
    name = "「直到今天，我其实一直都活在煎熬和痛苦之中。」"
  },
  [200040055] = {
