    name = "「哎哟......哎哟......」"
  },
  [200050002] = {
    id = 200050002,
    name = "前方有人呻吟，你循着声音走了过去。"
  },
  [200050003] = {
    id = 200050003,
    name = "一名背着包袱的男子靠在大石头边休息，他拿着一瓶药剂，正往自己的腿上涂药。"
  },
  [200050004] = {
    id = 200050004,
    name = "他看到了你，惊喜地和你打招呼。"
  },
  [200050005] = {
    id = 200050005,
    name = "他说他只是一个普通人，听闻深渊风景和大陆的完全不同，想来见识一番。"
  },
  [200050006] = {
    id = 200050006,
    name = "他一路都跟着一支探险队行动，还算安稳，没想到今天出了意外。"
  },
  [200050007] = {
    id = 200050007,
    name = "他在魔物袭击中负伤，而那支探险队在杀掉魔物之后却弃他而去。"
  },
  [200050008] = {
    id = 200050008,
    name = "「他们真是太可恶了，你说对不对？」"
  },
  [200050009] = {
    id = 200050009,
    name = "他向你寻求认同。"
  },
  [200050010] = {
    id = 200050010,
    name = "旅者沉默了一会儿，说起了自己的事。"
  },
  [200050011] = {
    id = 200050011,
    name = "「唉，其实我也很困扰。」"
  },
  [200050012] = {
    id = 200050012,
    name = "「我曾想成为一个探险者，为此做了许多努力，但全都失败了。」"
  },
  [200050013] = {
    id = 200050013,
    name = "「为了学习魔法，我花重金从一个魔法师那里买了这块石头，没想到毫无用处。」"
  },
  [200050014] = {
    id = 200050014,
    name = "你看着那块灰扑扑的石头，发现眼前这个人的确是冤大头。"
  },
  [200050015] = {
    id = 200050015,
    name = "（从某种程度来看，这人还挺顽强的。）"
  },
  [200050016] = {
    id = 200050016,
    name = "「呵呵，不用安慰我，其实我已经知道自己不是做探险者的料。」"
  },
  [200050017] = {
    id = 200050017,
    name = "旅者认为自己的确不适合深渊，这次回去后应该好好考虑一下以后的生活了。"
  },
  [200050018] = {
    id = 200050018,
    name = "「不过，听说公会的伊芙大人正在研制普通人也能使用的强力器具。」"
  },
  [200050019] = {
    id = 200050019,
    name = "「如果那些东西平民也能用的话，像我这样的普通人也能不依靠任何人了！」"
  },
  [200050020] = {
    id = 200050020,
    name = "（列蒂西雅家还在研究这些东西吗？）"
  },
  [200050021] = {
    id = 200050021,
    name = "旅者把那块灰扑扑的石头递给了你，希望你能送他去圈落。"
  },
  [200050022] = {
    id = 200050022,
    name = "你拿着那块石头，隐隐感觉到其中似乎存在魔力。"
  },
  [200050023] = {
    id = 200050023,
    name = "于是你将表层剥离，意外发现竟然是一块蕴含魔力的罕见矿物。"
  },
  [200050024] = {
    id = 200050024,
    name = "旅者一脸惊讶羡慕，对你满口称赞，但并未表露出想把这块矿物要回去的想法。"
  },
  [200050025] = {
    id = 200050025,
