    name = "二人确定了同伴是被魔物所杀，决定为同伴报仇。"
  },
  [200100081] = {
    id = 200100081,
    name = "「现在真相大白，可以放过我了吗？」"
  },
  [200100082] = {
    id = 200100082,
    name = "二人还在犹豫时，一头断了前肢的魔物突然跳出来，扑向你们。"
  },
  [200100083] = {
    id = 200100083,
    name = "「吼——」"
  },
  [200100084] = {
    id = 200100084,
    name = "你与二人联手，很快将魔物击杀。"
  },
  [200100085] = {
    id = 200100085,
    name = "「这样就算报仇了吗？」"
  },
  [200100086] = {
    id = 200100086,
    name = "「总觉得不甘心啊......」"
  },
  [200100087] = {
    id = 200100087,
    name = "两人向你致谢，带着同伴的尸体离开了。"
  },
  [200100088] = {
    id = 200100088,
    name = "「真可怜呢。」"
  },
  [200100089] = {
    id = 200100089,
    name = "大肚子男人望着远去的二人，嘀咕了几句。"
  },
  [200100090] = {
    id = 200100090,
    name = "「？？？」"
  },
  [200100091] = {
    id = 200100091,
    name = "「没什么。谢谢啦，好心人，这只魔物就留给你处理啦。」"
  },
  [200100092] = {
    id = 200100092,
    name = "说罢，男人也转身离去。"
  },
  [200100093] = {
    id = 200100093,
    name = "结局还算不错，但你总觉得似乎哪里不对。"
  },
  [200110001] = {
    id = 200110001,
    name = "「前面那位，请等一下！」"
  },
  [200110002] = {
    id = 200110002,
    name = "有人突然叫住了你。"
  },
  [200110003] = {
    id = 200110003,
    name = "你转头看去，叫住你的是一名眼眶通红的少女，她看起来非常悲伤。"
  },
  [200110004] = {
    id = 200110004,
    name = "「不好意思，请问可以帮我一个忙吗？」"
  },
  [200110005] = {
    id = 200110005,
    name = "「有什么事吗？」"
  },
  [200110006] = {
    id = 200110006,
    name = "少女自称是鹰猎探险队的成员。"
  },
  [200110007] = {
    id = 200110007,
    name = "她的队长前几日在探路时遇难，但他的遗骨却至今还未回收。"
  },
  [200110008] = {
    id = 200110008,
    name = "少女所在的队伍现在已经选出了新的队长，出于安全考虑，决定放弃回收。"
  },
  [200110009] = {
    id = 200110009,
    name = "只有少女仍在坚持，她知道希望渺茫，但依然想要将前队长的遗骨回收，让他安息。"
  },
  [200110010] = {
    id = 200110010,
    name = "「为什么你不去呢？」"
  },
  [200110011] = {
    id = 200110011,
    name = "「我实力低微，而且掌握的基本都是支援型魔法，难以独自战斗。」"
  },
  [200110012] = {
    id = 200110012,
    name = "「其他人都觉得太过危险，不想为了一个已经死去的人犯险。」"
  },
  [200110013] = {
    id = 200110013,
    name = "「所以没人愿意帮我。」"
  },
  [200110014] = {
    id = 200110014,
    name = "「你能帮帮我吗，拜托了。」"
  },
  [200110015] = {
    id = 200110015,
    name = "她非常感激你，告诉了你大概的位置、路线，以及周围的魔物信息。"
  },
  [200110016] = {
    id = 200110016,
    name = "「如果实在找不到也不用勉强，请一定注意安全。」"
  },
  [200110017] = {
    id = 200110017,
    name = "你根据少女的指引进入了密林。"
  },
  [200110018] = {
    id = 200110018,
    name = "沿着他们曾经的路线行进，在一棵大树下，你发现了散落的遗骸。"
  },
  [200110019] = {
    id = 200110019,
    name = "遗骸不远处，还有一柄断裂的长弓。结合少女说过的话，你确认了遗骸的身份。"
  },
  [200110020] = {
    id = 200110020,
    name = "「应该就是这个了。」"
  },
  [200110021] = {
    id = 200110021,
    name = "你收敛遗骸和遗物时，忽然感受到一道冰冷的视线。"
  },
  [200110022] = {
    id = 200110022,
    name = "你正欲有所动作，魔物已然扑了上来。"
  },
  [200110023] = {
    id = 200110023,
    name = "你见无可退避，便迅速展开防御，借着魔物的攻击顺势滚到一旁，再向魔物发起攻击。"
  },
  [200110024] = {
    id = 200110024,
    name = "费了些精力，你将魔物斩杀，重新收敛地上遗骸和遗物。"
  },
  [200110025] = {
    id = 200110025,
    name = "「嗯？这是什么？」"
  },
  [200110026] = {
    id = 200110026,
    name = "你发现了一封残缺的信，便捡了起来查看内容。"
  },
  [200110027] = {
    id = 200110027,
    name = "信是另一个探险队写给死者的。"
  },
  [200110028] = {
    id = 200110028,
    name = "大概内容是他们只接受死者一人加入，让死者找机会摆脱那些弱小的成员，前往指定地点与他们会合。"
  },
  [200110029] = {
    id = 200110029,
    name = "「......这是背叛了原来的队伍，前往与新队伍会合的途中遇到意外了吧。」"
  },
  [200110030] = {
    id = 200110030,
    name = "你摇了摇头，将收敛好的东西都包裹起来，沿着原路返回。"
  },
  [200110031] = {id = 200110031, name = "......"},
  [200110032] = {
    id = 200110032,
    name = "你将包裹交给了少女。"
  },
  [200110033] = {
    id = 200110033,
    name = "她尽管已经做好了心理准备，但在看到那柄断裂的长弓时，依然崩溃地哭了出来。"
  },
  [200110034] = {
    id = 200110034,
    name = "（......要不要把信件给她呢？）"
  },
  [200110035] = {
    id = 200110035,
    name = "「这就是他所有的遗物吗？」"
  },
  [200110036] = {
    id = 200110036,
    name = "你点点头，少女露出苦涩的笑容。"
  },
  [200110037] = {
    id = 200110037,
    name = "「总算把他带了回来，这下他也能安息了......」"
  },
  [200110038] = {
    id = 200110038,
    name = "「其实他是我的恋人。」"
  },
  [200110039] = {
    id = 200110039,
    name = "「他还说要鞭笞我成长，直到能和他并肩而行。」"
  },
  [200110040] = {
    id = 200110040,
    name = "「我一直这样憧憬着。」"
  },
  [200110041] = {
    id = 200110041,
    name = "「现在，一直憧憬的人死掉了，我活着还有什么意义呢。」"
  },
  [200110042] = {
    id = 200110042,
    name = "少女沉默良久，长舒了一口气，露出一个释然的笑容。"
  },
  [200110043] = {
    id = 200110043,
    name = "「以后我会一直带着这柄断弓，就像他从未离我远去。」"
  },
  [200110044] = {
    id = 200110044,
    name = "「非常感谢你帮我把他带了回来，这里有些物资，还请不要客气。」"
  },
  [200110045] = {
    id = 200110045,
    name = "少女带着她的恋人离去了，你看着她消失的背影，不禁有些感慨。"
  },
  [200110046] = {
    id = 200110046,
    name = "「这是......」"
  },
  [200110047] = {
    id = 200110047,
    name = "「这......他要抛弃我们......」"
  },
  [200110048] = {
    id = 200110048,
    name = "「怎么会这样......呵......」"
  },
  [200110049] = {
    id = 200110049,
    name = "「呵呵呵呵......」"
  },
  [200110050] = {
    id = 200110050,
    name = "「原来我们在他心里，是随时可以抛弃的、弱小的累赘......」"
  },
  [200110051] = {
    id = 200110051,
    name = "「他对我总是很严厉。」"
  },
  [200110052] = {
    id = 200110052,
    name = "「我以为他只是想让我快一些成长，让我能尽快与他并肩前行。」"
  },
  [200110053] = {
    id = 200110053,
    name = "「没想到......呵呵呵，真是没想到......」"
  },
  [200110054] = {
    id = 200110054,
    name = "「这位先生，谢谢你，让我知道了这一切。」"
  },
  [200110055] = {
    id = 200110055,
    name = "「这里有些物资，还请收下。我要去和队友们会合了。」"
  },
  [200110056] = {
    id = 200110056,
    name = "「......」"
  },
  [200110057] = {
    id = 200110057,
    name = "「已经没有什么值得留恋的了。」"
  },
  [200110058] = {
    id = 200110058,
    name = "少女露出一个惨淡的笑容，向你行了一礼，消失在了道路尽头。"
  },
  [200110059] = {
    id = 200110059,
    name = "你以武技低微的理由拒绝了少女，建议她找专业的队伍帮忙，如此也能保障安全。"
  },
  [200110060] = {
    id = 200110060,
    name = "少女愣了愣，短暂沉默后，她向你表示了感谢，转身离开了。"
  },
  [200110061] = {
    id = 200110061,
    name = "你没有搭理少女，径直朝着密林走去。"
  },
  [200110062] = {
    id = 200110062,
    name = "少女看着你离去的背影，嘴巴张了张，似乎想喊住你，但直到你从她视野消失，也没有出声。"
  },
  [200110063] = {
    id = 200110063,
    name = "进入密林不久，你突然感觉到一股寒意，像是有什么东西躲在暗处紧盯着你。"
  },
  [200110064] = {
    id = 200110064,
    name = "你不敢大意，小心翼翼地前行。"
  },
  [200110065] = {
    id = 200110065,
    name = "你走到一棵大树下，发现一些散落的骸骨，从痕迹来看，应该是不久前遇害的探险者。"
  },
  [200110066] = {
    id = 200110066,
    name = "正当你观察四周时，一头魔物突然窜出，径直扑你而来。"
  },
  [200110067] = {
    id = 200110067,
    name = "所幸你早有准备，在魔物出现的瞬间便闪躲开来，随即向魔物发起了攻击。"
  },
  [200110068] = {
    id = 200110068,
    name = "经过一番战斗，你成功将魔物击杀，取走有用素材后，你继续向远处走去。"
  },
  [200120001] = {
    id = 200120001,
    name = "「嗯？」"
  },
  [200120002] = {
    id = 200120002,
    name = "走至偏远的山脚，你被某个东西绊了一下，低头看去，发现是一段人类的腿骨。"
  },
  [200120003] = {
    id = 200120003,
    name = "仔细一看，这具骸骨表面大部分都被风沙侵蚀，不过整体还算完整。"
  },
  [200120004] = {
    id = 200120004,
    name = "该骸骨背靠山崖坐在角落，头部抬起，似乎正在仰望天空。"
  },
  [200120005] = {
    id = 200120005,
    name = "骸骨的胸腹部分，可以看到一团明显的黑紫色。"
  },
  [200120006] = {
    id = 200120006,
    name = "「看来是中毒死去的。」"
  },
  [200120007] = {
    id = 200120007,
    name = "你叹了口气，准备离开时，发现那骸骨身旁有一个不起眼的盒子。"
  },
  [200120008] = {
    id = 200120008,
    name = "你扫了扫尘泥，盒子表面露出了精致华美的图案。"
  },
  [200120009] = {
    id = 200120009,
    name = "（盒子里面装的什么？）"
  },
  [200120010] = {
    id = 200120010,
    name = "你打开盒子，里面猛地窜出一股烟雾，随即幻化为一头恐怖的魔物！"
  },
  [200120011] = {
    id = 200120011,
    name = "你与魔物缠斗了起来，费了些力气才将其杀掉。"
  },
  [200120012] = {
    id = 200120012,
    name = "「这是什么魔物？」"
  },
  [200120013] = {
    id = 200120013,
    name = "你打败魔物后，那东西又溃散为烟雾，消失在了空气中，随即盒子中显露出一本书籍。"
  },
  [200120014] = {
    id = 200120014,
    name = "你正打算查看，突然听到身后传来脚步声。"
  },
  [200120015] = {
    id = 200120015,
    name = "你回头看去，来人是一名年轻的探险者。"
  },
  [200120016] = {
    id = 200120016,
    name = "「我说，好东西是不是该见者有份啊。」"
  },
  [200120017] = {
    id = 200120017,
    name = "探险者也看到了那本书籍，书籍包装精美，甚至用魔法保护着外层。"
  },
  [200120018] = {
    id = 200120018,
    name = "「死前都要放在手边，一定是很有用的魔法秘籍。」"
  },
  [200120019] = {
    id = 200120019,
    name = "「或者失传已久的炼药秘方。」"
  },
  [200120020] = {
    id = 200120020,
    name = "「也有可能是极为珍贵的魔物笔记......」"
  },
  [200120021] = {
    id = 200120021,
    name = "他提出了诸多猜测，随后上前和你商量。"
  },
  [200120022] = {
    id = 200120022,
    name = "「既然我们同时发现了这本书——」"
  },
  [200120023] = {
    id = 200120023,
    name = "「打个商量，我给你钱，这东西归我怎么样？」"
  },
  [200120024] = {
    id = 200120024,
    name = "「好东西当然见者有份。」"
  },
  [200120025] = {
    id = 200120025,
    name = "「噢，兄弟真是慷慨啊！那我们快打开看看吧！」"
  },
  [200120026] = {
    id = 200120026,
    name = "在他期待的眼光中，你小心翼翼地翻开了书籍......"
  },
  [200120027] = {
    id = 200120027,
    name = "「wohoo......这、这真是一本奇书啊！」"
  },
  [200120028] = {
    id = 200120028,
    name = "你快速合上了书籍。"
  },
  [200120029] = {
    id = 200120029,
    name = "「喂，再给我看一眼，我刚才什么都没看见！」"
  },
  [200120030] = {
    id = 200120030,
    name = "「......」"
  },
  [200120031] = {
    id = 200120031,
    name = "「说好的见者有份啊！快再给我看一眼。」"
  },
  [200120032] = {
    id = 200120032,
    name = "「我没有在陌生人面前看这种书的习惯，不过，你好像很喜欢这本书？」"
  },
  [200120033] = {
    id = 200120033,
    name = "「谁能不喜欢呢，我可不自欺欺人。」"
  },
  [200120034] = {
    id = 200120034,
    name = "「在孤独的夜晚，这简直就是抚慰心灵的极品。」"
  },
  [200120035] = {
    id = 200120035,
    name = "「兄弟，我有个主意。」"
  },
  [200120036] = {
    id = 200120036,
    name = "「如果把这本书复刻发行，一定能成为深渊中的畅销书！」"
  },
  [200120037] = {
    id = 200120037,
    name = "你俩一拍即合，随即开始制定计划，联系商人制作贩售。"
  },
  [200120038] = {
    id = 200120038,
    name = "然而正当你们投入大价钱复刻印刷时，突然有人找了过来。"
  },
  [200120039] = {
    id = 200120039,
    name = "来人正是此书的作者，他要求你们立即终止贩书计划，否则要让你们赔偿。"
  },
  [200120040] = {
    id = 200120040,
    name = "你们极力与原作者沟通，希望他能同意甚至加入这个商业计划，但他始终不为所动。"
  },
  [200120041] = {
    id = 200120041,
    name = "无奈之下，你们只得放弃。"
  },
  [200120042] = {
    id = 200120042,
    name = "你认为他的想法很好，但你没有兴趣加入他的商业计划。"
  },
  [200120043] = {
    id = 200120043,
    name = "于是，他为了带走书本给了你一些补偿，并约定等到正式贩售后，再按比例给你分利。"
  },
  [200120044] = {
    id = 200120044,
    name = "然而一段时间后，你等来的并非是利润分配，而是他破产的消息。"
  },
  [200120045] = {
    id = 200120045,
    name = "据说原作者找上了他，无奈之下他只得终止已经投入了巨大资金的计划......"
  },
  [200120046] = {
    id = 200120046,
    name = "那一刻，你很庆幸当初没有答应他的提议。"
  },
  [200120047] = {
    id = 200120047,
    name = "「哼！你不要敬酒不吃吃罚酒！」"
  },
  [200120048] = {
    id = 200120048,
    name = "他伸手就要去拿盒子里的书籍，被你一把打掉，你反手将书籍牢牢握在手中。"
  },
  [200120049] = {
    id = 200120049,
    name = "探险者发现你身手不凡，立刻停手，嬉皮笑脸地看着你。"
  },
  [200120050] = {
    id = 200120050,
    name = "「开个玩笑嘛，别当真！」"
  },
  [200120051] = {
    id = 200120051,
    name = "「我就是想见识见识，这本奇书到底写了些什么。」"
  },
  [200120052] = {
    id = 200120052,
    name = "在他期待的眼光中，你小心翼翼地翻开了书籍......"
  },
  [200120053] = {
    id = 200120053,
    name = "「wohoo......这、这真是一本奇书啊！」"
  },
  [200120054] = {
    id = 200120054,
    name = "你默默收起书籍，他想继续说些什么，但又慑于你的实力，只能委屈地看着你。"
  },
  [200120055] = {
    id = 200120055,
    name = "「真遗憾啊~」"
  },
  [200120056] = {
    id = 200120056,
    name = "在探险者的叹息声中，你将书放回盒子，带着盒子离开了此地。"
  },
  [200120057] = {
    id = 200120057,
    name = "他见你答应得很果断，便多给了你些钱。"
  },
  [200120058] = {
    id = 200120058,
    name = "随后他小心翼翼地把书从盒子中取出，翻开查看。"
  },
  [200120059] = {
    id = 200120059,
    name = "你见他双眼瞪大，瞳孔紧缩，一脸的不可思议，引得你也想凑近查看一番。"
  },
  [200120060] = {
    id = 200120060,
    name = "然而你刚伸头，他便啪地一声将书合上。"
  },
  [200120061] = {
    id = 200120061,
    name = "他眼神瞟向你，露出一个意味深长的笑容，然后将书贴身藏在怀中，匆匆离去。"
  },
  [200120062] = {
    id = 200120062,
    name = "「难道我错过了什么吗？」"
  },
  [200120063] = {
    id = 200120063,
    name = "你觉得好奇害死猫，决定不去窥探，将盒子放回了原处，正当你准备离去时，迎面走来了一名年轻的探险者。"
  },
  [200120064] = {
    id = 200120064,
    name = "他探头望向你身后的骸骨。"
  },
  [200120065] = {
    id = 200120065,
    name = "「啧啧，多可怜啊，怎么没人给他收尸呢，那我来帮忙好了。」"
  },
  [200120066] = {
    id = 200120066,
    name = "他俯身捡起那盒子打开，霎时一股烟雾窜出，紧接着烟雾幻化成一头恐怖的魔物！"
  },
  [200120067] = {
    id = 200120067,
    name = "「哇......救、救命！」"
  },
  [200120068] = {
    id = 200120068,
    name = "探险者被魔物一爪击倒。"
  },
  [200120069] = {
    id = 200120069,
    name = "但他仍紧紧抱着盒子不放，不停地以随身武器刺向魔物。"
  },
  [200120070] = {
    id = 200120070,
    name = "过了好一会儿，魔物化作烟雾消失，年轻的探险者也累得气喘吁吁。"
  },
  [200120071] = {
    id = 200120071,
    name = "「啊啊，真是无情啊，竟然就这样冷漠地看着——」"
  },
  [200120072] = {
    id = 200120072,
    name = "「你，你该不会想趁人之危吧！」"
  },
  [200120073] = {
    id = 200120073,
    name = "「......」"
  },
  [200120074] = {
    id = 200120074,
    name = "「我警告你啊，你别打歪主意，这是你不要，我才拿的。」"
  },
  [200120075] = {
    id = 200120075,
    name = "他一步步后退，直到退出了你的攻击范围。"
  },
  [200120076] = {
    id = 200120076,
    name = "「算你识相。」"
  },
  [200120077] = {
    id = 200120077,
    name = "他长吁一口气，抱着盒子一溜烟跑走了。"
  },
  [200120078] = {
    id = 200120078,
    name = "在魔物即将击中年轻探险者的那一刻，你出手了。"
  },
  [200120079] = {
    id = 200120079,
    name = "年轻的探险者见你帮忙，也立即起身和你一起攻击魔物。"
  },
  [200120080] = {
    id = 200120080,
    name = "在你二人的攻击下，魔物很快被击杀，化作烟雾消散，随即盒子中显露出一本书籍。"
  },
  [200120081] = {
    id = 200120081,
    name = "「哇，这一定是什么好东西吧？」"
  },
  [200120082] = {
    id = 200120082,
    name = "「我说，我能看一眼吗？好歹是我先发现的。」"
  },
  [200120083] = {
    id = 200120083,
    name = "但是鉴于刚才的教训，探险者不敢去拿书籍，似乎害怕下一秒再窜出一只魔物。"
  },
  [200120084] = {
    id = 200120084,
    name = "于是在他期待的眼神中，你小心翼翼的翻开了书籍......"
  },
  [200120085] = {
    id = 200120085,
    name = "「wohoo......这、这真是一本奇书啊！」"
  },
  [200120086] = {
    id = 200120086,
    name = "「你喜欢这种书？」"
  },
  [200120087] = {
    id = 200120087,
    name = "你没有在陌生人面前看这种成人书本的习惯，将书快速合上。"
  },
  [200120088] = {
    id = 200120088,
    name = "「谁能不喜欢呢，我可不自欺欺人。」"
  },
  [200120089] = {
    id = 200120089,
    name = "「兄弟，打个商量。」"
  },
  [200120090] = {
    id = 200120090,
    name = "「魔物是你打的，这本书本该归你。」"
  },
  [200120091] = {
    id = 200120091,
    name = "「但这本书着实罕见，要不我给你些钱，你让给我怎么样？」"
  },
  [200120092] = {
    id = 200120092,
    name = "说着，他拿出一些钱递到你面前，满怀期待地看着你。"
  },
  [200120093] = {
    id = 200120093,
    name = "「在孤独的夜晚，这简直就是抚慰心灵的极品。」"
  },
  [200120094] = {
    id = 200120094,
    name = "「而且这种品质，如果找人复刻贩售，一定能畅销深渊吧？！」"
  },
  [200120095] = {
    id = 200120095,
    name = "「那得加价。」"
  },
  [200120096] = {
    id = 200120096,
    name = "「开玩笑的，兄弟。这种好书，我当然是私下收藏了。」"
  },
  [200120097] = {
    id = 200120097,
    name = "他急忙将钱袋塞给你，接过书和盒子，如获至宝般抱在了怀里。"
  },
  [200120098] = {
    id = 200120098,
    name = "「啊！真是可惜~」"
  },
  [200120099] = {
    id = 200120099,
    name = "他叹了口气，想继续说些什么，但又慑于你的实力，只能委屈地看着你。"
  },
  [200120100] = {
    id = 200120100,
    name = "「那就让我再看一眼吧。」"
  },
  [200120101] = {
    id = 200120101,
    name = "你没有理会他的请求，将书装进盒子收好，在他遗憾的叹息声中离开了。"
  },
  [200130001] = {
    id = 200130001,
    name = "你行至小溪，发现一个小孩正蹲在岸边，专心看着水中游来游去的小鱼。"
  },
  [200130002] = {
    id = 200130002,
    name = "你环顾四周无人，便走上前去。"
  },
  [200130003] = {
    id = 200130003,
    name = "「怎么一个人在这里，你家里人呢？」"
  },
  [200130004] = {
    id = 200130004,
    name = "小孩站了起来，露出天真的笑容看着你。"
  },
  [200130005] = {
    id = 200130005,
    name = "「大哥哥，我好无聊啊，你陪我玩吧？」"
  },
  [200130006] = {
    id = 200130006,
    name = "小孩开心地跳了起来。"
  },
  [200130007] = {
    id = 200130007,
    name = "「大哥哥，你最好了！」"
  },
  [200130008] = {
    id = 200130008,
    name = "他朝着小溪对岸的树林大喊。"
  },
  [200130009] = {
    id = 200130009,
    name = "「快出来，你们快出来啊！」"
  },
  [200130010] = {
    id = 200130010,
    name = "一旁的大树后钻出来两个小脑袋。"
  },
  [200130011] = {
    id = 200130011,
    name = "「怎么了？」"
  },
  [200130012] = {
    id = 200130012,
    name = "「你又惹事了？」"
  },
  [200130013] = {
    id = 200130013,
    name = "「我没有惹事，是这个大哥哥刚才欺负我，想打劫我！」"
  },
  [200130014] = {
    id = 200130014,
    name = "「喂！我没有——」"
  },
  [200130015] = {
    id = 200130015,
    name = "「他看我一个小孩子好欺负，呜呜呜......」"
  },
  [200130016] = {
    id = 200130016,
    name = "「你这个欠揍的小子！」"
  },
  [200130017] = {
    id = 200130017,
    name = "「你看，他就是要欺负我！哥哥，我们一起打败他吧！」"
  },
  [200130018] = {
    id = 200130018,
    name = "「敢欺负我们小溪三人组，是要给他一个教训！」"
  },
  [200130019] = {
    id = 200130019,
    name = "「真、真的没关系吗？」"
  },
  [200130020] = {
    id = 200130020,
    name = "「就用叔叔教我们的本事！」"
  },
  [200130021] = {
    id = 200130021,
    name = "「好啊好啊，正好可以练习一下！」"
  },
  [200130022] = {
    id = 200130022,
    name = "几名小孩冲了上来！"
  },
  [200130023] = {
    id = 200130023,
    name = "你没怎么用力，就把他们推到了一边。"
  },
  [200130024] = {
    id = 200130024,
    name = "他们三个跌在地上喘气。"
  },
  [200130025] = {
    id = 200130025,
    name = "「这个大哥哥好厉害啊，不知道和叔叔比谁更厉害呢。」"
  },
  [200130026] = {
    id = 200130026,
    name = "「为什么夸他？你不是说他欺负你？」"
  },
  [200130027] = {
    id = 200130027,
    name = "「哎呀，我就是看大家都很无聊，恰好大哥哥也说愿意陪我们玩啊。」"
  },
  [200130028] = {
    id = 200130028,
    name = "「......你是故意的啊。」"
  },
  [200130029] = {
    id = 200130029,
    name = "「可是，我们输得很惨诶。」"
  },
  [200130030] = {
    id = 200130030,
    name = "「我们该回去了，再这样贪玩下去，不但会输，还会死得很惨。」"
  },
  [200130031] = {
    id = 200130031,
    name = "「对噢，这一路很危险啊。」"
  },
  [200130032] = {
    id = 200130032,
    name = "「如果有魔物就死定了。」"
  },
  [200130033] = {
    id = 200130033,
    name = "「大哥哥，能送我们回去吗？」"
  },
  [200130034] = {
    id = 200130034,
    name = "「......」"
  },
  [200130035] = {
    id = 200130035,
    name = "「哎呀呀，大哥哥原来是胆小鬼啊。」"
  },
  [200130036] = {
    id = 200130036,
    name = "「如果真的是陷阱，哥哥敢不敢跳进来呢？」"
  },
  [200130037] = {
    id = 200130037,
    name = "「想激我，你还太嫩了。」"
  },
  [200130038] = {
    id = 200130038,
    name = "你转身离开，三人没有挽留你，吵吵闹闹往回走去。"
  },
  [200130039] = {
    id = 200130039,
    name = "突然，三人离开的方向响起了魔物的吼叫。"
  },
  [200130040] = {
    id = 200130040,
    name = "你听到声响，迅速返回救人。"
  },
  [200130041] = {
    id = 200130041,
    name = "可惜一切都晚了，三个小孩已经惨死在魔物的利爪之下。"
  },
  [200130042] = {
    id = 200130042,
    name = "你杀掉了魔物，却无法挽回三人的性命。"
  },
  [200130043] = {
    id = 200130043,
    name = "「快走吧，这一带魔物这么多，你们也是胆大。」"
  },
  [200130044] = {
    id = 200130044,
    name = "你护送着几人往回走，路上果然遇到零星的魔物。"
  },
  [200130045] = {
    id = 200130045,
    name = "好在你早有准备，将所遇到的魔物尽数消灭。"
  },
  [200130046] = {
    id = 200130046,
    name = "「大哥哥太厉害了，能不能教我刚才那一手？」"
  },
  [200130047] = {
    id = 200130047,
    name = "「这个简单。」"
  },
  [200130048] = {
    id = 200130048,
    name = "你将运转魔法的方式告诉了小孩。"
  },
  [200130049] = {
    id = 200130049,
    name = "他按照你说的做，手心亮起一团小小的光芒，顿时高兴地跳了起来。"
  },
  [200130050] = {
    id = 200130050,
    name = "「太好了，我以后也要变得像大哥哥一样能保护大家！」"
  },
  [200130051] = {
    id = 200130051,
    name = "「你们不是还有叔叔吗？」"
  },
  [200130052] = {
    id = 200130052,
    name = "「可是叔叔已经死了。」"
  },
  [200130053] = {
    id = 200130053,
    name = "「他是说过要守护我们，但他死了，没办法守护了吧。」"
  },
  [200130054] = {
    id = 200130054,
    name = "在三人的引领下，你将他们送到了一个贫民窟，小孩开心地和你道别，并把他们叔叔遗留的一些物资赠予了你。"
  },
  [200130055] = {
    id = 200130055,
    name = "「我不会再贪玩了，要成为出色的探险者，必须从今天就开始修行！」"
  },
  [200130056] = {
    id = 200130056,
    name = "「你说过很多次了，一次也没做到。」"
  },
  [200130057] = {
    id = 200130057,
    name = "「那是因为叔叔教的东西不适合我。」"
  },
  [200130058] = {
    id = 200130058,
    name = "「我现在学会了新的方法，等着瞧吧！」"
  },
  [200130059] = {
    id = 200130059,
    name = "多年后，三人组建了小溪探险队，因乐善好施，行侠仗义，在深渊中声名远扬。"
  },
  [200130060] = {
    id = 200130060,
    name = "「我没欺负你，别污蔑我！」"
  },
  [200130061] = {
    id = 200130061,
    name = "「呜哇，你看他好凶。哥哥，我们一起打败他！」"
  },
  [200130062] = {
    id = 200130062,
    name = "「敢欺负我们小溪三人组，是要给他一个教训！」"
  },
  [200130063] = {
    id = 200130063,
    name = "「真、真的没关系吗？」"
  },
  [200130064] = {
    id = 200130064,
    name = "「就用叔叔教我们的本事！」"
  },
  [200130065] = {
    id = 200130065,
    name = "「来吧，正好可以练习一下！」"
  },
  [200130066] = {
    id = 200130066,
    name = "几名小孩冲了上来！"
  },
  [200130067] = {
    id = 200130067,
    name = "你没怎么用力，就把他们推到了一边。"
  },
  [200130068] = {
    id = 200130068,
    name = "他们三个跌在地上喘气。"
  },
  [200130069] = {
    id = 200130069,
    name = "你觉得和小孩置气很无趣，正要离开，天真的小孩拦下了你。"
  },
  [200130070] = {
    id = 200130070,
    name = "「不许走！再打一场！」"
  },
  [200130071] = {
    id = 200130071,
    name = "「这点教训还不够？怎么，你就这么喜欢吃苦头吗？」"
  },
  [200130072] = {
    id = 200130072,
    name = "「你、你再有本事，也是个谁都讨厌的坏家伙！」"
  },
  [200130073] = {
    id = 200130073,
    name = "「别惹他了，我们该回去了。」"
  },
  [200130074] = {
    id = 200130074,
    name = "「我不想打......我害怕。」"
  },
  [200130075] = {
    id = 200130075,
    name = "「怕就乖乖回去找你家大人。」"
  },
  [200130076] = {
    id = 200130076,
    name = "「在外面溜达的话，不知道什么时候就会变成魔物的午餐哦。」"
  },
  [200130077] = {
    id = 200130077,
    name = "「你这个大坏人！」"
  },
  [200130078] = {
    id = 200130078,
    name = "「我看你是没见过真正的坏人吧。」"
  },
  [200130079] = {
    id = 200130079,
    name = "「你、你想要干什么？」"
  },
  [200130080] = {
    id = 200130080,
    name = "「坏人嘛，当然是敲诈勒索抢劫一条龙服务了。」"
  },
  [200130081] = {
    id = 200130081,
    name = "「你......」"
  },
  [200130082] = {
    id = 200130082,
    name = "「我们快走！」"
  },
  [200130083] = {
    id = 200130083,
    name = "见你沉着脸走过去，三人撒腿就跑。"
  },
  [200130084] = {
    id = 200130084,
    name = "「再不跑快点就追上了哦。」"
  },
  [200130085] = {
    id = 200130085,
    name = "「哇......呜呜！」"
  },
  [200130086] = {
    id = 200130086,
    name = "「吼......」"
  },
  [200130087] = {
    id = 200130087,
    name = "此时，一只魔物突然窜出，直扑三人！"
  },
  [200130088] = {
    id = 200130088,
    name = "你及时出手，挡在三人面前。"
  },
  [200130089] = {
    id = 200130089,
    name = "所幸这头魔物并不算强，被你轻松解决。"
  },
  [200130090] = {
    id = 200130090,
    name = "你看着躲在一旁，抱成一团瑟瑟发抖的三人，笑着摇了摇头。"
  },
  [200130091] = {
    id = 200130091,
    name = "趁你收集魔物身上素材的功夫，几个小孩一溜烟跑没了影。"
  },
  [200130092] = {
    id = 200130092,
    name = "你环顾四周，发现三人正跑向远处的贫民窟，确认他们安全抵达后，你才松了口气。"
  },
  [200130093] = {
    id = 200130093,
    name = "（希望这件事能给他们一个教训。）"
  },
  [200130094] = {
    id = 200130094,
    name = "（在不清楚情况的时候，最好不要胡乱行动。）"
  },
  [200130095] = {
    id = 200130095,
    name = "多年后，三人组建了专门的情报组织——小溪情报组。"
  },
  [200130096] = {
    id = 200130096,
    name = "因谨小慎微的行事作风，以及可靠的消息渠道受到许多探险者推崇。"
  },
  [200130097] = {
    id = 200130097,
    name = "「你们以为惹到了我，今天可以轻易离开这里吗？」"
  },
  [200130098] = {
    id = 200130098,
    name = "「你、你想要干什么？」"
  },
  [200130099] = {
    id = 200130099,
    name = "「要你们赔钱啊。」"
  },
  [200130100] = {
    id = 200130100,
    name = "「可是，你没有受到伤害啊，为什么要赔钱？你这是打劫！」"
  },
  [200130101] = {
    id = 200130101,
    name = "「废话少说，快赔钱！不然谁也别想离开！」"
  },
  [200130102] = {
    id = 200130102,
    name = "你尽量扮演得像一个真正的坏人，想给这群小孩一个深刻的教训。"
  },
  [200130103] = {
    id = 200130103,
    name = "小孩们站在原地发抖。"
  },
  [200130104] = {
    id = 200130104,
    name = "淡定的小孩鼓起勇气扔出了一块素材，随即拉着两人飞快地逃跑。"
  },
  [200130105] = {
    id = 200130105,
    name = "此时，一头魔物突然窜出，直扑三人！"
  },
  [200130106] = {
    id = 200130106,
    name = "你立即出手，将手中的素材扔出干扰魔物，随即趁魔物注意力转移的瞬间，给予它致命一击。"
  },
  [200130107] = {
    id = 200130107,
    name = "你轻松解决了魔物，打扫战场时瞥见了远处的贫民窟，瞬间对几个小孩的来历有了判断。"
  },
  [200130108] = {
    id = 200130108,
    name = "「为什么救我们？」"
  },
  [200130109] = {
    id = 200130109,
    name = "「当然是为了下一次啊。难道你们没听过，羊要养肥之后再杀吗？」"
  },
  [200130110] = {
    id = 200130110,
    name = "「你、你......」"
  },
  [200130111] = {
    id = 200130111,
    name = "「嘘~不想和那头魔物一样的话就闭嘴。对了，下次再来这边，记得多带点东西，不然别怪我不客气。」"
  },
  [200130112] = {
    id = 200130112,
    name = "三个小孩恐惧地看了你一眼，逃一样地跑回了贫民窟。"
  },
  [200130113] = {
    id = 200130113,
    name = "多年后，三人成了这一带无恶不作的劫匪，"
  },
  [200130114] = {
    id = 200130114,
    name = "许多来此的探险者都遭了殃。"
  },
  [200130115] = {
    id = 200130115,
    name = "「可是我朋友还在这里啊。」"
  },
  [200130116] = {
    id = 200130116,
    name = "他指了指附近的树林。"
  },
  [200130117] = {
    id = 200130117,
    name = "「朋友？」"
  },
  [200130118] = {
    id = 200130118,
    name = "「是啊！喂，你们快出来！」"
  },
  [200130119] = {
    id = 200130119,
    name = "随着小孩的呼喊，一旁的大树后钻出两个小脑袋。"
  },
  [200130120] = {
    id = 200130120,
    name = "「你又惹事了？」"
  },
  [200130121] = {
    id = 200130121,
    name = "「是有吃的了吗？」"
  },
  [200130122] = {
    id = 200130122,
    name = "「我没有惹事，是这个大哥哥说要送我们回家，还会给我们吃的。」"
  },
  [200130123] = {
    id = 200130123,
    name = "「喂！你讹我啊——」"
  },
  [200130124] = {
    id = 200130124,
    name = "「大哥哥，你刚才说过的不算数了吗，呜呜呜......」"
  },
  [200130125] = {
    id = 200130125,
    name = "你见三人面黄肌瘦的模样，于心不忍，决定送他们回去。"
  },
  [200130126] = {
    id = 200130126,
    name = "在几个小孩的引领下，你将他们送回了附近的贫民窟。"
  },
  [200130127] = {
    id = 200130127,
    name = "「怎么一个人都没有？」"
  },
  [200130128] = {
    id = 200130128,
    name = "「因为都死了，叔叔出去找食物就再也没回来。」"
  },
  [200130129] = {
    id = 200130129,
    name = "「他说过要守护我们的。」"
  },
  [200130130] = {
    id = 200130130,
    name = "「但他死了，已经没办法守护了吧。」"
  },
  [200130131] = {
    id = 200130131,
    name = "「好饿......」"
  },
  [200130132] = {
    id = 200130132,
    name = "你拿出了干粮，三个小孩吃得很开心。"
  },
  [200130133] = {
    id = 200130133,
    name = "「好好吃......谢谢你，大哥哥你真是一个好人。」"
  },
  [200130134] = {
    id = 200130134,
    name = "「这里很容易被魔物袭击，你们为什么不去附近的圈落呢？」"
  },
  [200130135] = {
    id = 200130135,
    name = "「那个圈落不要拾荒者。」"
  },
  [200130136] = {
    id = 200130136,
    name = "「真拿你们没办法。」"
  },
  [200130137] = {
    id = 200130137,
    name = "放任这三个小孩在这里，很可能随时会死掉，你决定帮人帮到底。"
  },
  [200130138] = {
    id = 200130138,
    name = "与三人交流后，你将他们带到了附近圈落。"
  },
  [200130139] = {
    id = 200130139,
    name = "你找到了当地公会成员，在你的恳求以及三名孩子的努力下，他们得以在公会中从事简单的工作，换取公会的庇护。"
  },
  [200130140] = {
    id = 200130140,
    name = "「谢谢，谢谢你！」"
  },
  [200130141] = {
    id = 200130141,
    name = "「我们会认真工作的。这个送给你，谢谢你帮我们。」"
  },
  [200130142] = {
    id = 200130142,
    name = "小孩拿出了一些探险者的物资送给你，据说是那位叔叔留下的。"
  },
  [200130143] = {
    id = 200130143,
    name = "「我不会再贪玩了，为了能守护同伴，我要从现在开始认真学习。」"
  },
  [200130144] = {
    id = 200130144,
    name = "「你说过很多次了。」"
  },
  [200130145] = {
    id = 200130145,
    name = "「这次是认真的！」"
  },
  [200130146] = {
    id = 200130146,
    name = "「大哥哥，我不会辜负你为我们做的一切。」"
  },
  [200130147] = {
    id = 200130147,
    name = "「嗯，一起加油。」"
  },
  [200130148] = {
    id = 200130148,
    name = "多年后，三人成为了公会的正式成员。"
  },
  [200130149] = {
    id = 200130149,
    name = "因精明能干、热情友善受到许多人称赞。"
  },
  [200140001] = {
    id = 200140001,
    name = "前行途中，你发现附近树下一名男子正蹲坐在地上休息。"
  },
  [200140002] = {
    id = 200140002,
    name = "他身旁有一辆小木车，上面装满了药草。"
  },
  [200140003] = {
    id = 200140003,
    name = "你走近时，听见他唉声叹气。"
  },
  [200140004] = {
    id = 200140004,
    name = "他察觉到了你，顿时双瞳紧缩，露出恐惧的姿态，往后连退了好几步。"
  },
  [200140005] = {
    id = 200140005,
    name = "随即他急忙向你求饶，祈求你能放过他最后的货物。"
  },
  [200140006] = {
    id = 200140006,
    name = "你安抚了这个看起来像游商的男子。"
  },
  [200140007] = {
    id = 200140007,
    name = "男子见你没有抢劫的心思，松了一口气。"
  },
  [200140008] = {
    id = 200140008,
    name = "他向你倒苦水，诉说这一路的艰辛。"
  },
  [200140009] = {
    id = 200140009,
    name = "原来他并不是深渊游商，而是为了找寻一种药草，特意来到深渊的普通人。"
  },
  [200140010] = {
    id = 200140010,
    name = "在雇佣的探险者都逃走之后，他独自继续前进，发誓要找到传说中的药草。"
  },
  [200140011] = {
    id = 200140011,
    name = "「那是一株包治百病的药草。」"
  },
  [200140012] = {
    id = 200140012,
    name = "男子说起了药草的功效，原来他这次的目的是把药草带回去救她的女儿。"
  },
  [200140013] = {
    id = 200140013,
    name = "那株药草就长在他身后的山顶上。"
  },
  [200140014] = {
    id = 200140014,
    name = "但山顶有魔物的巡视，作为一个普通人，他根本无法靠近。"
  },
  [200140015] = {
    id = 200140015,
    name = "「如果没有药草的话，我女儿的命也就......」"
  },
  [200140016] = {
    id = 200140016,
    name = "你告诉男人世界上不可能存在那种药草，让他认清现实。"
  },
  [200140017] = {
    id = 200140017,
    name = "男人非常生气，认为你是胆小鬼，不想和你继续交流，推车跑向了山顶。"
  },
  [200140018] = {
    id = 200140018,
    name = "山顶有魔物出没，非常危险，你急忙追了上去，然而却没有见到男子的踪影。"
  },
  [200140019] = {
    id = 200140019,
    name = "就在你沿途搜寻时，一头魔物突然窜了出来，你不敢分心，全力迎战。"
  },
  [200140020] = {
    id = 200140020,
    name = "打败了魔物后，你继续寻找男子的踪影。"
  },
  [200140021] = {
    id = 200140021,
    name = "然而你在山顶找了一圈都一无所获，只在山崖附近发现了一辆破损的、趋近于腐烂的小推车。"
  },
  [200140022] = {
    id = 200140022,
    name = "推车旁倚着一副枯骨，枯骨边放着一张有些腐朽的羊皮纸。"
  },
  [200140023] = {
    id = 200140023,
    name = "「女神在上，请赐予我的爱女祝福，消弭她所有的病痛。」"
  },
  [200140024] = {
    id = 200140024,
    name = "枯骨的手中拿着一棵干枯的草，里面虽然还有少量魔力残留，但已失去价值。"
  },
  [200140025] = {
    id = 200140025,
    name = "从外形来看，这人已经死去了很长的时间。"
  },
  [200140026] = {
    id = 200140026,
    name = "「这是怎么回事？」"
  },
  [200140027] = {
    id = 200140027,
    name = "你沿原路下山，在山下看到了一个女探险者。"
  },
  [200140028] = {
    id = 200140028,
    name = "她主动跟你打招呼，问你在山顶上有没有看到包治百病的药草。"
  },
  [200140029] = {
    id = 200140029,
    name = "你将男子上山的事告诉了她。"
  },
  [200140030] = {
    id = 200140030,
    name = "所谓的药草不过只是蕴含了魔力的植物。"
  },
  [200140031] = {
    id = 200140031,
    name = "「是这样吗，我早就说过不可能了啊......」"
  },
  [200140032] = {
    id = 200140032,
    name = "「他还没有付给你的报酬，就由我来补偿吧。」"
  },
  [200140033] = {
    id = 200140033,
    name = "女探险者从包裹中取出一些物资交给了你。"
  },
  [200140034] = {
    id = 200140034,
    name = "随后她和你告别，继续朝山上走去。"
  },
  [200140035] = {
    id = 200140035,
    name = "「......果然还是想亲眼确认吗？」"
  },
  [200140036] = {
    id = 200140036,
    name = "你拿着报酬离开，没有看见之后发生的一切——"
  },
  [200140037] = {
    id = 200140037,
    name = "女探险者到达山崖附近时，行动突然变得迟缓，身躯容颜以肉眼可见的速度老去......"
  },
  [200140038] = {
    id = 200140038,
    name = "你转身离开，迎面走来一名女探险者。"
  },
  [200140039] = {
    id = 200140039,
    name = "她见到你眼睛一亮，朝你走了过来。"
  },
  [200140040] = {
    id = 200140040,
    name = "她问你有没有见到一个采药草的男子。"
  },
  [200140041] = {
    id = 200140041,
    name = "你将男子上山的事告诉了她。"
  },
  [200140042] = {
    id = 200140042,
    name = "她叹了口气，口中嘀咕几句，决定继续上山，寻找那个摘药草的男子。"
  },
  [200140043] = {
    id = 200140043,
    name = "「如果是那家伙的话，一定会被魔物吃掉吧。」"
  },
  [200140044] = {
    id = 200140044,
    name = "你们分别不久，突然从山上传来一声魔物的嘶吼。"
  },
  [200140045] = {
    id = 200140045,
    name = "你听到声音立即向山上赶去，然而只看到一具魔物的枯骨。"
  },
  [200140046] = {
    id = 200140046,
    name = "山崖附近，还有一辆破损的小推车倒在地上。"
  },
  [200140047] = {
    id = 200140047,
    name = "你走近一看，那小推车看起来和之前男子的推车很像，只是车身早已腐朽。"
  },
  [200140048] = {
    id = 200140048,
    name = "你沿途寻找，男子和女探险者都不见了踪影。"
  },
  [200140049] = {
    id = 200140049,
    name = "「人呢？」"
  },
  [200140050] = {
    id = 200140050,
    name = "你巡视一周没有任何发现，也并没看到传说中的药草。"
  },
  [200140051] = {
    id = 200140051,
    name = "「看来他们不知道用什么办法，已经离开了。」"
  },
  [200140052] = {
    id = 200140052,
    name = "男子感激你的帮助，但他想跟你一起。"
  },
  [200140053] = {
    id = 200140053,
    name = "你隐隐感觉到山顶的魔物并不好对付，如果带上他必定是一件十分危险的事。"
  },
  [200140054] = {
    id = 200140054,
    name = "你告知了男子可能的危险，但他仍然坚持，你只得带着他一起上山。"
  },
  [200140055] = {
    id = 200140055,
    name = "你们刚到山顶，魔物便窜了出来，你将男子护到身后，与魔物激战。"
  },
  [200140056] = {
    id = 200140056,
    name = "费了些功夫，你将魔物击败，男子在你的保护下也并无大碍。"
  },
  [200140057] = {
    id = 200140057,
    name = "你们继续在山顶寻找，最终在山崖附近发现一株药草。"
  },
  [200140058] = {
    id = 200140058,
    name = "男子欣喜若狂地跑上去摘取了药草，这株药草内蕴含着奇特的魔力。"
  },
  [200140059] = {
    id = 200140059,
    name = "或许真有传说中包治百病的效果也说不定。"
  },
  [200140060] = {
    id = 200140060,
    name = "男子说他终于可以医治女儿的病了，从包裹中拿出一堆物资塞给你，希望你能收下他的谢礼。"
  },
  [200140061] = {
    id = 200140061,
    name = "此时，一个女探险者出现在了你们的面前，男子叫他妹妹。"
  },
  [200140062] = {
    id = 200140062,
    name = "原来她担忧哥哥的安全，追寻着哥哥的踪迹也来到了这里。"
  },
  [200140063] = {
    id = 200140063,
    name = "「没想到你真的做到了啊。」"
  },
  [200140064] = {
    id = 200140064,
    name = "「为什么你们看起来没有变化？」"
  },
  [200140065] = {
    id = 200140065,
    name = "女探险者说着莫名其妙的话。"
  },
  [200140066] = {
    id = 200140066,
    name = "「只是摘药草而已，能有什么变化。」"
  },
  [200140067] = {
    id = 200140067,
    name = "「......总之，没有发生意外真是太好了。」"
  },
  [200140068] = {
    id = 200140068,
    name = "男子急着回去救女儿，两人与你挥手道别。"
  },
  [200140069] = {
    id = 200140069,
    name = "你告诉男人山顶可能非常危险，让他在此等候，自己如果拿到药草一定送回来。男子得到你的许诺，同意在原地等你归来。"
  },
  [200140070] = {
    id = 200140070,
    name = "你刚到山顶，魔物便突然窜了出来。"
  },
  [200140071] = {
    id = 200140071,
    name = "你早有准备，也没有后顾之忧，全力迎战魔物，不多时便将其击败。"
  },
  [200140072] = {
    id = 200140072,
    name = "在山顶寻找了一圈，你终于在山崖边见到一株和男子描述相符的药草。"
  },
  [200140073] = {
    id = 200140073,
    name = "你得到药草后沿原路返回，那名男子却已消失不见。"
  },
  [200140074] = {
    id = 200140074,
    name = "「人呢？」"
  },
  [200140075] = {
    id = 200140075,
    name = "你继续向山脚走去，看到了一名女探险者和一位年纪很大的老人。"
  },
  [200140076] = {
    id = 200140076,
    name = "老人见到你，露出了惊恐的眼神。"
  },
  [200140077] = {
    id = 200140077,
    name = "「你为什么没有改变？这个药草......药草......」"
  },
  [200140078] = {
    id = 200140078,
    name = "他说着莫名其妙的话，指着你手中的药草，突然晕了过去。"
  },
  [200140079] = {
    id = 200140079,
    name = "女探险者叹息着将他安顿在一边，朝你走了过来。"
  },
  [200140080] = {
    id = 200140080,
    name = "「你竟然没有遭遇任何意外？真是奇迹......」"
  },
  [200140081] = {
    id = 200140081,
    name = "「非常感谢你帮我们拿到了这株药草！」"
  },
  [200140082] = {
    id = 200140082,
    name = "随后，她和你讲起了她所知道的一切。"
  },
  [200140083] = {
    id = 200140083,
    name = "原来这名老人就是之前找药草的男子。"
  },
  [200140084] = {
    id = 200140084,
    name = "而这株传说中的药草，的确能治百病，但它的生长区域十分恐怖诡异。"
  },
  [200140085] = {
    id = 200140085,
    name = "任何接近的人或物，都可能突然衰老，甚至死去，就如同自身的时间被剥夺一般。"
  },
  [200140086] = {
    id = 200140086,
    name = "只有这株药草周围本就存在的一切，才不会受到影响。"
  },
  [200140087] = {
    id = 200140087,
    name = "这名老人，也就是她的哥哥，在你上山后不知道出于什么原因，也跟着上了山，所以变成了现在的模样。"
  },
  [200140088] = {
    id = 200140088,
    name = "所幸她及时赶到，将他带了下来，否则他现在可能已经化作枯骨。"
  },
  [200140089] = {
    id = 200140089,
