  [200170011] = {
    id = 200170011,
    name = "大叔见你想去上游，拿出一双手套，希望你能交给他儿子。"
  },
  [200170012] = {
    id = 200170012,
    name = "他说他儿子围着一条红围巾，很容易辨认。"
  },
  [200170013] = {
    id = 200170013,
    name = "你向河流上游行进，沿途看到许多和中年男子一样的采石人。"
  },
  [200170014] = {
    id = 200170014,
    name = "这些人大部分都是上了年纪的普通人，如果遇到魔物，恐怕凶多吉少。"
  },
  [200170015] = {
    id = 200170015,
    name = "不过越往上游走，普通人越少，取而代之的是一些探险者。"
  },
  [200170016] = {
    id = 200170016,
    name = "你又走了一段，已看不到普通人的身影，你边走边观察，寻找大叔的儿子。"
  },
  [200170017] = {
    id = 200170017,
    name = "突然，前方传来战斗的声音，你赶过去发现一头魔物正和一名围着红围巾的男子战斗。"
  },
  [200170018] = {
    id = 200170018,
    name = "你立即出手，与他一起对付魔物。"
  },
  [200170019] = {
    id = 200170019,
    name = "在你的帮助下，魔物很快被击败，不过男子却对你表现得很警惕。"
  },
  [200170020] = {
    id = 200170020,
    name = "他看向你身后，脸上露出一丝惊恐，随即一瘸一拐地逃走了。"
  },
  [200170021] = {
    id = 200170021,
    name = "你正想叫住他，却听身后传来微弱的呼救声。"
  },
  [200170022] = {
    id = 200170022,
    name = "回头看去，一名浑身是血的少年不知何时来到了你身后。"
  },
  [200170023] = {
    id = 200170023,
    name = "刚才的呼救声似乎耗尽了他最后一丝力气，在你回头瞬间他直直地倒了下去。"
  },
  [200170024] = {
    id = 200170024,
    name = "你没想到那名戴红围巾的男子可以跑那么快，追了好一段路才赶上他。"
  },
  [200170025] = {
    id = 200170025,
    name = "他哆嗦地扔下了红围巾，嘴里大喊大叫让你别杀他，他这就把围巾还给你们。"
  },
  [200170026] = {
    id = 200170026,
    name = "你捡起红围巾，意识到此人并不是大叔的儿子。"
  },
  [200170027] = {
    id = 200170027,
    name = "待你再赶回原处，那名倒在地上的少年已经奄奄一息。"
  },
  [200170028] = {
    id = 200170028,
    name = "你虽然全力施救，但还是晚了一步。"
  },
  [200170029] = {
    id = 200170029,
    name = "临死前，他牢牢抓住了你带回来的红围巾，对着你拿出的手套，露出了最后的微笑。"
  },
  [200170030] = {
    id = 200170030,
    name = "你背起少年向下游走去，准备将他送回大叔身边，然而刚走到下游，河里已不见了大叔的踪影，只有几名路人围在岸边。"
  },
  [200170031] = {
    id = 200170031,
    name = "你走过去，发现大叔躺在血泊之中，他双眼瞪圆，满是不甘与愤怒。"
  },
  [200170032] = {
    id = 200170032,
    name = "路人说着大叔的死因。"
  },
  [200170033] = {
    id = 200170033,
    name = "原来附近有一些专门打劫这些采石人的劫匪，平时还好，基本只会抢劫不会杀人。"
  },
  [200170034] = {
    id = 200170034,
    name = "但这次只因大叔斥责了他们几句，便被残忍杀害......"
  },
  [200170035] = {
    id = 200170035,
    name = "待人群散去，你收敛了大叔的遗体，将他和儿子安葬到了一起。"
  },
  [200170036] = {
    id = 200170036,
    name = "你将手套留在了他们身边，将那红色的围巾系到一截木材上，立在了他们的墓前。"
  },
  [200170037] = {
    id = 200170037,
    name = "你给了少年一些伤药，让他在此疗伤，自己去帮他夺回围巾。"
  },
  [200170038] = {
    id = 200170038,
    name = "在一个洞窟前，你找到了之前戴着红围巾的男子。"
  },
  [200170039] = {
    id = 200170039,
    name = "男子见你追了上来，嘴里大喊大叫让你别杀他，哆嗦地扔下了红围巾跑远了。"
  },
  [200170040] = {
    id = 200170040,
    name = "你捡起红围巾，回到受伤的少年身边。"
  },
  [200170041] = {
    id = 200170041,
    name = "他非常感激你的帮助，将围巾重新围在了脖子上，并拜托你将他送回父亲身边。"
  },
  [200170042] = {
    id = 200170042,
    name = "你们回到下游时，看到几个路人正围在岸边，地上有一具尸体。"
  },
  [200170043] = {
    id = 200170043,
    name = "你定睛一看，死者正是之前的大叔。"
  },
  [200170044] = {
    id = 200170044,
    name = "他躺在血泊之中，双眼瞪圆，满是不甘与愤怒。"
  },
  [200170045] = {
    id = 200170045,
    name = "少年疯一样地跑了上去抱紧了他父亲的尸体。"
  },
  [200170046] = {
    id = 200170046,
    name = "路人说你们来晚了，要是早一步，或许可以阻止悲剧发生。"
  },
  [200170047] = {
    id = 200170047,
    name = "据他们所说，几名劫匪和往常一样来打劫这些采石人。"
  },
  [200170048] = {
    id = 200170048,
    name = "大叔被抢劫后有些气愤，斥责了他们几句，结果就被残忍杀害......"
  },
  [200170049] = {
    id = 200170049,
    name = "少年悲痛欲绝，过了很久才平复了情绪。"
  },
  [200170050] = {
    id = 200170050,
    name = "你感到非常遗憾，想安慰他却不知如何开口。"
  },
  [200170051] = {
    id = 200170051,
    name = "少年再次向你致谢后，带着父亲的遗体离开了。"
  },
  [200170052] = {
    id = 200170052,
    name = "你全力救治这名浑身是血的少年，终于将他从死亡边缘拉了回来。"
  },
  [200170053] = {
    id = 200170053,
    name = "少年稍微好转之后，又强撑着站起来，表示自己要去追刚刚那名抢自己围巾的人。"
  },
  [200170054] = {
    id = 200170054,
    name = "与少年了解更多信息后，你确认他就是大叔的儿子，于是拿出手套交给他。"
  },
  [200170055] = {
    id = 200170055,
    name = "你劝少年先回去，他伤势太重，必须好好修养。"
  },
  [200170056] = {
    id = 200170056,
    name = "少年对你表示了感谢，不过眼下他仍决定继续去追赶那人。"
  },
  [200170057] = {
    id = 200170057,
    name = "少年告诉你，那条围巾对他有着特别的意义，所以绝不允许别人将它夺去。"
  },
  [200170058] = {
    id = 200170058,
    name = "你看着少年步履蹒跚的身影，不禁叹了口气，追了上去。"
  },
  [200170059] = {
    id = 200170059,
    name = "少年已受重伤，如果勉强追下去十分危险，你决定先将他送回去。"
  },
  [200170060] = {
    id = 200170060,
    name = "但少年却执意要追回自己的围巾，挣扎着走了几步后又晕了过去。"
  },
  [200170061] = {
    id = 200170061,
    name = "你带着少年回到下游河边，看到两人正逼着大叔交出他采集的月亮石。"
  },
  [200170062] = {
    id = 200170062,
    name = "你大喝一声冲了过去，二人见来者不善，抓起已经到手的月亮石，撒腿就跑。"
  },
  [200170063] = {
    id = 200170063,
    name = "大叔看到你们，无暇顾及刚才的损失，急忙问起儿子的情况。"
  },
  [200170064] = {
    id = 200170064,
    name = "你将发生的事都告诉了大叔。"
  },
  [200170065] = {
    id = 200170065,
    name = "他非常感激你，想再次拜托你帮忙找回那条红围巾。"
  },
  [200170066] = {
    id = 200170066,
    name = "他说那条红围巾，是他死去的妻子留给儿子的唯一物品。"
  },
  [200170067] = {
    id = 200170067,
    name = "对他儿子来说，那是比他生命还重要的东西。"
  },
  [200170068] = {
    id = 200170068,
    name = "你让大叔在此照顾他的儿子，自己去帮忙夺回围巾。"
  },
  [200170069] = {
    id = 200170069,
    name = "在一个洞窟前，你找到了之前戴着红围巾的男子。"
  },
  [200170070] = {
    id = 200170070,
    name = "男子自知不敌，知道你的来意后便将围巾拱手相让。"
  },
  [200170071] = {
    id = 200170071,
    name = "你带着围巾回到下游，见到几名路人围在岸边，大叔父子二人已倒在了血泊之中。"
  },
  [200170072] = {
    id = 200170072,
    name = "原来你走以后，刚才的劫匪回来报复，不但将财物一抢而空，还杀害了他们。"
  },
  [200170073] = {
    id = 200170073,
    name = "你收敛了二人的遗体，将他们简单安葬后，沉默着离开了。"
  },
  [200170074] = {
    id = 200170074,
    name = "你告诉大叔比起丢失的围巾，眼下他儿子的情况更加紧急，他儿子已身受重伤，必须及时找个安全的地方治疗修养。"
  },
  [200170075] = {
    id = 200170075,
    name = "大叔看着昏迷的儿子，简单思考后接受了你的提议。"
  },
  [200170076] = {
    id = 200170076,
    name = "你将两人送到了附近据点。"
  },
  [200170077] = {
    id = 200170077,
    name = "大叔非常感激你的帮助，送给你一些物资作为谢礼。"
  },
  [200170078] = {
    id = 200170078,
    name = "你没有立刻出手。"
  },
  [200170079] = {
    id = 200170079,
    name = "红围巾男子虽然看起来一瘸一拐的，但已将魔物压制，只需再等些时间，必然可以解决魔物。"
  },
  [200170080] = {
    id = 200170080,
    name = "果然，又过了一会儿，红围巾男子抓住魔物闪躲的间隙，给予了它致命一击。"
  },
  [200170081] = {
    id = 200170081,
    name = "你正准备走过去确认他的身份时，旁边传来一阵声响。"
  },
  [200170082] = {
    id = 200170082,
    name = "你回头看去，一名浑身是血的少年不知何时走了过来，冲着与魔物搏斗的男子大喊，让他把围巾还给自己。"
  },
  [200170083] = {
    id = 200170083,
    name = "你顿住了脚步，听到男子谩骂嘲讽着少年，甚至准备对其出手。"
  },
  [200170084] = {
    id = 200170084,
    name = "就在男子准备攻击少年之际，你阻止了他。"
  },
  [200170085] = {
    id = 200170085,
    name = "男子警告你别多管闲事，但见你不为所动，旋即向你发起攻击。"
  },
  [200170086] = {
    id = 200170086,
    name = "然而一交手，他便被你打趴。"
  },
  [200170087] = {
    id = 200170087,
    name = "男子意识到你的强大，不停地道歉求饶，并取下围巾还给了少年。"
  },
  [200170088] = {
    id = 200170088,
    name = "随后生怕你反悔一般，一溜烟跑没了影。"
  },
  [200170089] = {
    id = 200170089,
    name = "少年拿着围巾，露出一个安心的微笑，紧接着如同耗尽了最后一丝力气，直直地倒在了地上。"
  },
  [200170090] = {
    id = 200170090,
    name = "你急忙将他扶起，全力救治，终于将他从死亡边缘拉了回来。"
  },
  [200170091] = {
    id = 200170091,
    name = "少年苏醒后，你确认了他就是大叔寻找的儿子，便将手套交给了他。"
  },
  [200170092] = {
    id = 200170092,
    name = "你见他伤重，难以独自行动，便将他送回下游，和他父亲团聚。"
  },
  [200170093] = {
    id = 200170093,
    name = "你带着少年回到下游河边，看到两人正逼着大叔交出他采集的月亮石。"
  },
  [200170094] = {
    id = 200170094,
    name = "你大喝一声冲了过去，二人见来者不善，抓起已经到手的月亮石撒腿就跑。"
  },
  [200170095] = {
    id = 200170095,
    name = "大叔看到你们，无暇顾及刚才的损失，急忙问起儿子的情况。"
  },
  [200170096] = {
    id = 200170096,
    name = "你将发生的事都告诉了大叔，同时提议他们离开这里，寻找一个更安稳的区域生活。"
  },
  [200170097] = {
    id = 200170097,
    name = "大叔表示想等儿子恢复后再考虑，眼下需要优先照顾儿子。"
  },
  [200170098] = {
    id = 200170098,
    name = "你将他们送回了现在的住所。"
  },
  [200170099] = {
    id = 200170099,
    name = "大叔塞给你一堆物资，感谢你拯救了他们父子，无论你如何推辞，他都不愿收回。"
  },
  [200170100] = {
    id = 200170100,
    name = "你叹了口气，收下了那些物资，给他们留下了一些伤药后便动身离去。"
  },
  [200170101] = {
    id = 200170101,
    name = "你拒绝了大叔的提议，他也并未多言，笑呵呵地和你道别。"
  },
  [200170102] = {
    id = 200170102,
    name = "离开不久，你遇到两个人。"
  },
  [200170103] = {
    id = 200170103,
    name = "二人用不怀好意的目光上下打量着你，待你走近，竟将你拦了下来。"
  },
  [200170104] = {
    id = 200170104,
    name = "原来这二人是附近的劫匪，专门打劫来河边寻找月亮石的人。"
  },
  [200170105] = {
    id = 200170105,
    name = "见你从河边过来，便将你当成了采石的人，要你留下所有月亮石。"
  },
  [200170106] = {
    id = 200170106,
    name = "你游刃有余地和二人战斗着，将他们当作猎物戏耍。"
  },
  [200170107] = {
    id = 200170107,
    name = "过了一会儿，二人被你戏弄得筋疲力尽，抛下几句狠话之后，一溜烟逃没了影。"
  },
  [200170108] = {
    id = 200170108,
    name = "你摇了摇头，继续踏上旅途。"
  },
  [200170109] = {
    id = 200170109,
    name = "深渊中这种寻宝人太常见了，你对此并无兴趣，只是简单看了一眼便离开了。"
  },
  [200180001] = {
    id = 200180001,
    name = "行走在路上，你听到有人在呼救。"
  },
  [200180002] = {
    id = 200180002,
    name = "仔细一看，一个受伤的男子靠在岩石旁边。"
  },
  [200180003] = {
    id = 200180003,
    name = "他见你出现，急忙叫住了你，他说自己的脚受伤了，希望你能帮他一个忙。"
  },
  [200180004] = {
    id = 200180004,
    name = "受伤的男子说在附近的森林里有一支商队，他需要将一句话带给他们。"
  },
  [200180005] = {
    id = 200180005,
    name = "男子很感激你，告诉你只需要说「面包糊了」，他的伙伴就会明白一切。"
  },
  [200180006] = {
    id = 200180006,
    name = "你按照他的指引到达指定地点，却见好几支商队同时驻扎在那里。"
  },
  [200180007] = {
    id = 200180007,
    name = "这些商队看起来并无太大差别，你一时分不清哪支商队才是你要找的，陷入了犹豫。"
  },
  [200180008] = {
    id = 200180008,
    name = "你一支队伍一支队伍挨个悄悄询问，虽然遭到了有些人的质疑，但最后总算找到了接头人。"
  },
  [200180009] = {
    id = 200180009,
    name = "他向你了解了同伴的状况，得出了同伴失手的判断。"
  },
  [200180010] = {
    id = 200180010,
    name = "他给了你一些金币作为谢礼，同时提出想让你代替那位受伤同伴的想法。"
  },
  [200180011] = {
    id = 200180011,
    name = "邀请你参与「烤面包行动」。"
  },
  [200180012] = {
    id = 200180012,
    name = "接头人看了看周围，把你拉到一处僻静之地，和你说起了任务内容。"
  },
  [200180013] = {
    id = 200180013,
    name = "原来接头人和他的同伴都是公会的情报人员，商人只是他们平常的伪装。"
  },
  [200180014] = {
    id = 200180014,
    name = "这次他们的任务，是从一名探险者手中取得一份特殊的藏宝图。"
  },
  [200180015] = {
    id = 200180015,
    name = "为了不让高塔的人察觉，所以才采用了特殊的代号。"
  },
  [200180016] = {
    id = 200180016,
    name = "而「面包糊了」预示着那位同伴行动失败，他们的计划很可能已经被高塔知晓。"
  },
  [200180017] = {
    id = 200180017,
    name = "如果计划真的暴露，他的身份，说不定也已经被高塔的人察觉。"
  },
  [200180018] = {
    id = 200180018,
    name = "所以他必须尽快采取行动，而且为了确保成功，他希望你这个生面孔能帮他一把。"
  },
  [200180019] = {
    id = 200180019,
    name = "你答应了他的请求，随后他向你详细讲述了整个计划，并调整了其中一些环节。"
  },
  [200180020] = {
    id = 200180020,
    name = "根据计划，你到达一处树林潜伏起来。"
  },
  [200180021] = {
    id = 200180021,
    name = "据你们判断，如果藏宝图真的被高塔截获，那么只需要在此等候，便能遇到他们，到时候再抢回来即可。"
  },
  [200180022] = {
    id = 200180022,
    name = "你们蛰伏了许久，终于等到了目标人物。"
  },
  [200180023] = {
    id = 200180023,
    name = "他看起来很不起眼，如果不是接头人提醒，你甚至将他当成了路过的普通探险者。"
  },
  [200180024] = {
    id = 200180024,
    name = "你们拦下了他，准备按计划假装劫匪打劫他时，却被他识破了身份。"
  },
  [200180025] = {
    id = 200180025,
    name = "这名高塔成员奉劝你不要掺和高塔和公会的事，如果你识相一点，老实退出，他愿意给你一些奖励。"
  },
  [200180026] = {
    id = 200180026,
    name = "你拒绝了他，这令他有些恼怒，随即便向你发起攻击。"
  },
  [200180027] = {
    id = 200180027,
    name = "就在你下意识躲避的瞬间，他突然稳住身形，反身向远处逃去。"
  },
  [200180028] = {
    id = 200180028,
    name = "你一愣，意识到被他骗了，刚想追击，却见那名高塔成员突然停了下来，摇摇晃晃地倒在了地上。"
  },
  [200180029] = {
    id = 200180029,
    name = "接头人从容地走了上去，拿出一个小药瓶朝你晃了晃。"
  },
  [200180030] = {
    id = 200180030,
    name = "原来刚刚那名高塔成员试图离间你们时，他就提前动了手脚。"
  },
  [200180031] = {
    id = 200180031,
    name = "你们成功拿回了藏宝图，接头人丢给你一袋金币，告诉你那是属于你的奖励。"
  },
  [200180032] = {
    id = 200180032,
    name = "看你爽快地收下酬金，他笑了笑，带着藏宝图离开了。"
  },
  [200180033] = {
    id = 200180033,
    name = "你答应了他，他对你的选择非常满意。"
  },
  [200180034] = {
    id = 200180034,
    name = "正当他向你走来，掏出一个袋子准备递给你时，你忽然感到一阵晕眩。"
  },
  [200180035] = {
    id = 200180035,
    name = "眼前的高塔成员也变得摇摇晃晃，随后你俩一起倒在了地上。"
  },
  [200180036] = {
    id = 200180036,
    name = "失去意识前，你听到了接头人的叹息。"
  },
  [200180037] = {
    id = 200180037,
    name = "等你醒来时，周围一片漆黑，不管是高塔成员还是接头人都已经消失不见。"
  },
  [200180038] = {
    id = 200180038,
    name = "你似乎失去了那位公会成员的信任。"
  },
  [200180039] = {
    id = 200180039,
    name = "接头人听懂了你的言外之意，他叹了口气，一边说着能理解你，一边打开了一个瓶子。"
  },
  [200180040] = {
    id = 200180040,
    name = "你还没反应过来，只感觉一阵晕眩，倒在了地上。"
  },
  [200180041] = {
    id = 200180041,
    name = "意识消失前，你听到接头人自言自语地说着，为了计划，暂时不能放你离开。"
  },
  [200180042] = {
    id = 200180042,
    name = "醒来后，你发现自己躺在一个陌生的地方，怀里多了一个卷轴。"
  },
  [200180043] = {
    id = 200180043,
    name = "你正想打开查看，却见几人向你围杀而来。"
  },
  [200180044] = {
    id = 200180044,
    name = "一番激战后，来人悉数被你击倒在地。"
  },
  [200180045] = {
    id = 200180045,
    name = "此时，先前向你求助的那名受伤男子走了出来，他向你道出了原委。"
  },
  [200180046] = {
    id = 200180046,
    name = "原来，他们为了确保行动成功，将你算计了进来，现在他们已如愿甩掉追兵，愿意承受你的怒火。"
  },
  [200180047] = {
    id = 200180047,
    name = "你很想教训他一顿，但看到他接下来的动作，压制住了怒火。"
  },
  [200180048] = {
    id = 200180048,
    name = "他拿出一大袋金币，表示是对你的补偿以及奖励。"
  },
  [200180049] = {
    id = 200180049,
    name = "看在金币的面子上，你没有和他计较。"
  },
  [200180050] = {
    id = 200180050,
    name = "他见你痛快收下了金币，也露出一个释然的笑容，带着藏宝图离开了。"
  },
  [200180051] = {
    id = 200180051,
    name = "你直接以不感兴趣为由拒绝了接头人，他很识趣地不再打扰你，转身离去。"
  },
  [200180052] = {
    id = 200180052,
    name = "你在人群前大声喊出了「面包糊了」，被人以为是劫匪，一群人拿着武器逼近你。"
  },
  [200180053] = {
    id = 200180053,
    name = "你再次大喊了一声「面包糊了」，依然无人应答，在被众人包围之前，你转身逃走了。"
  },
  [200180054] = {
    id = 200180054,
    name = "而在这之后，无论你用了什么办法，也没有一个人和你接头。"
  },
  [200180055] = {
    id = 200180055,
    name = "你答应的委托失败了。"
  },
  [200180056] = {
    id = 200180056,
    name = "如此简单的任务让你觉得不可思议，怀疑其中是否有诈。"
  },
  [200180057] = {
    id = 200180057,
    name = "男子皱了皱眉，露出一副警惕的神色，随后强撑着离去，任你如何呼喊也不再回应。"
  },
  [200180058] = {
    id = 200180058,
    name = "你以有事在身为由拒绝了他，男子叹息了一声，露出失落的目光，在你离开后，他也一瘸一拐地离开了原地。"
  },
  [200190001] = {
    id = 200190001,
    name = "一名少女在路边唉声叹气。"
  },
  [200190002] = {
    id = 200190002,
    name = "她衣着精致，背着一张长弓。"
  },
  [200190003] = {
    id = 200190003,
    name = "腰间挂着许多箭矢，此时正埋着头踢地上的石头。"
  },
  [200190004] = {
    id = 200190004,
    name = "一块被踢飞的小石头打中了你的腿，她急忙抬起头向你道歉。"
  },
  [200190005] = {
    id = 200190005,
    name = "她说自己陷入了人生中最大的麻烦，实在不知道该怎么办才好。"
  },
  [200190006] = {
    id = 200190006,
    name = "少女展颜一笑，夸你是一个好人，但是这件事只能她自己去解决。"
  },
  [200190007] = {
    id = 200190007,
    name = "少女说在她的家族，只有成为真正的探险者才能离开据点。"
  },
  [200190008] = {
    id = 200190008,
    name = "而她现在只是一个见习猎影，要想成为家族承认的探险者，必须要做一件事。"
  },
  [200190009] = {
    id = 200190009,
    name = "那就是猎杀前面森林里的一头魔物，并将魔物的头带回去。"
  },
  [200190010] = {
    id = 200190010,
    name = "可她对那头魔物不熟悉，不知道该怎么对付那头魔物。"
  },
  [200190011] = {
    id = 200190011,
    name = "她说自己已经进过很多次森林了，但每次都无功而返。"
  },
  [200190012] = {
    id = 200190012,
    name = "家族给她的期限也快到了，再完成不了这个仪式，就要等到明年。"
  },
  [200190013] = {
    id = 200190013,
    name = "「我不要做一辈子的见习猎影！」"
  },
  [200190014] = {
    id = 200190014,
    name = "看着烦恼的少女，你决定给她出个主意。"
  },
  [200190015] = {
    id = 200190015,
    name = "从少女的描述中，你发现了问题所在。"
  },
  [200190016] = {
    id = 200190016,
    name = "因为对魔物一无所知，才导致她想不到解决方案。"
  },
  [200190017] = {
    id = 200190017,
    name = "你提议让她先调查魔物情报，再针对性地拟定作战计划，于是你和她说起自己的想法。"
  },
  [200190018] = {
    id = 200190018,
    name = "少女想了想。"
  },
  [200190019] = {
    id = 200190019,
    name = "「我想起来了，有个探险者经常去前面的森林狩猎魔物，或许能告诉我一些情报！」"
  },
  [200190020] = {
    id = 200190020,
    name = "少女带着你找到了那个人。"
  },
  [200190021] = {
    id = 200190021,
    name = "他满身伤痕，正徒手和一头魔物搏斗。"
  },
  [200190022] = {
    id = 200190022,
    name = "少女正想帮忙，你阻止了她，指出那名探险者正在收集魔物的情报。"
  },
  [200190023] = {
    id = 200190023,
    name = "探险者用余光瞥了你们一眼，继续与魔物缠斗了好一阵。"
  },
  [200190024] = {
    id = 200190024,
    name = "最后终于拿出武器，击杀了魔物。"
  },
  [200190025] = {
    id = 200190025,
    name = "探险者发现你们还在原地，朝你们走了过来，疑惑地看着你们。"
  },
  [200190026] = {
    id = 200190026,
    name = "少女微笑着看着他，突然脸上泛起红晕，把你往前推了推。"
  },
  [200190027] = {
    id = 200190027,
    name = "你发现她突然变得羞涩，只好帮她询问魔物的情报。"
  },
  [200190028] = {
    id = 200190028,
    name = "「是的。」"
  },
  [200190029] = {
    id = 200190029,
    name = "「这片森林里的魔物信息我已经掌握得差不多了。」"
  },
  [200190030] = {
    id = 200190030,
    name = "探险者告诉你们，他很喜欢研究各种魔物，不过比起书本，他更喜欢采用战斗的方式了解魔物。"
  },
  [200190031] = {
    id = 200190031,
    name = "他认为，只有不断和魔物交手，才能发现它们真正的弱点。"
  },
  [200190032] = {
    id = 200190032,
    name = "他已经走过许多地方，每当研究得差不多了，便会去往新的区域。"
  },
  [200190033] = {
    id = 200190033,
    name = "说这话时，他看了一眼躲在你身后的少女。"
  },
  [200190034] = {
    id = 200190034,
    name = "「这片区域我已经调查得差不多了，明天就准备离开这里。」"
  },
  [200190035] = {
    id = 200190035,
    name = "听他这样说，少女身子微微一怔，随即抿着嘴开始流泪。"
  },
  [200190036] = {
    id = 200190036,
    name = "探险者见此情形一下慌乱起来，不知该如何安慰少女。"
  },
  [200190037] = {
    id = 200190037,
    name = "场面陷入僵持，直到少女突然跑入森林，探险者急急忙忙地追了上去。"
  },
  [200190038] = {
    id = 200190038,
    name = "「......」"
  },
  [200190039] = {
    id = 200190039,
    name = "这两人的事，就由他们自己解决吧。"
  },
  [200190040] = {
    id = 200190040,
    name = "「说起来，你是来打听魔物信息的吧？」"
  },
  [200190041] = {
    id = 200190041,
    name = "「没有情报，你怎么通过仪式，成为真正的探险者？」"
  },
  [200190042] = {
    id = 200190042,
    name = "「又怎么和心仪的人，一起去到更远的地方探险呢？」"
  },
  [200190043] = {
    id = 200190043,
    name = "「真、真的？」"
  },
  [200190044] = {
    id = 200190044,
    name = "「才......才不是。」"
  },
  [200190045] = {
    id = 200190045,
    name = "探险者立刻和你们说起了他掌握的所有信息。"
  },
  [200190046] = {
    id = 200190046,
    name = "并表示自己愿意充当诱饵，确保少女能成功猎杀魔物。"
  },
  [200190047] = {
    id = 200190047,
    name = "少女破涕为笑，擦干眼泪，决定和探险者一起完成这次转正仪式。"
  },
  [200190048] = {
    id = 200190048,
    name = "二人对你表达了感谢，随即携手向远方森林走去。"
  },
  [200190049] = {
    id = 200190049,
    name = "你看着两人的背影，不由发出了感叹。"
  },
  [200190050] = {
    id = 200190050,
    name = "「青春啊~」"
  },
  [200190051] = {
    id = 200190051,
    name = "少女醍醐灌顶，对你说了很多个谢谢，心中突然就有了底气。"
  },
  [200190052] = {
    id = 200190052,
    name = "「没错，我这去问通过仪式的人！」"
  },
  [200190053] = {
    id = 200190053,
    name = "少女正打算实施计划，忽然又泄了气。"
  },
  [200190054] = {
    id = 200190054,
    name = "她想起家族中除了长辈，那些通过转正仪式的探险者都离开了这里，去更远的地方探险了。"
  },
  [200190055] = {
    id = 200190055,
    name = "少女再次陷入了苦恼，找长辈要魔物的情报，在她看来非常的丢脸。"
  },
  [200190056] = {
    id = 200190056,
    name = "「不用怕丢脸。」"
  },
  [200190057] = {
    id = 200190057,
    name = "「既然家族的长辈提出了这样的仪式，那就是等你回去求助的。」"
  },
  [200190058] = {
    id = 200190058,
    name = "「长辈们不会平白安排超出见习者能力的事，故意为难晚辈的。」"
  },
  [200190059] = {
    id = 200190059,
    name = "少女想了想，觉得你的说法很有道理。"
  },
  [200190060] = {
    id = 200190060,
    name = "向你道谢后，少女急急忙忙地跑走了。"
  },
  [200190061] = {
    id = 200190061,
    name = "你让她看清楚自己的能力和极限，告诉她，世上无难事，只要肯放弃。"
  },
  [200190062] = {
    id = 200190062,
    name = "不要做徒劳无功的事，那样是自找苦吃。"
  },
  [200190063] = {
    id = 200190063,
    name = "她吃惊地看着你，指责你说的都是歪理，她是不会轻言放弃的。"
  },
  [200190064] = {
    id = 200190064,
    name = "「与其在这里苦恼怎么去完成自己做不到的事，不如干脆放弃。」"
  },
  [200190065] = {
    id = 200190065,
    name = "「放弃吧，暂时做一个见习猎影。」"
  },
  [200190066] = {
    id = 200190066,
    name = "「反正每个人都有自己做不到的事，放弃也没什么大不了的。」"
  },
  [200190067] = {
    id = 200190067,
    name = "你拍了拍她的肩膀，告诉她家族是对的。"
  },
  [200190068] = {
    id = 200190068,
    name = "这是对弱者的保护，如果用其他方式通过了仪式，那未来付出代价的只会是自己。"
  },
  [200190069] = {
    id = 200190069,
    name = "她呆呆地看着你，仿佛失去了梦想，恍惚地离开了原地。"
  },
  [200190070] = {
    id = 200190070,
    name = "你告诉少女，自己帮她去猎杀魔物，她直接带回去交差就行。"
  },
  [200190071] = {
    id = 200190071,
    name = "少女直接愣在了原地，急忙说这次考核严禁别人帮忙之类的。"
  },
  [200190072] = {
    id = 200190072,
    name = "不过过了一会，她又低声说道，如果你真的愿意帮她，就必须严格保密，而且不能攻击魔物的头。"
  },
  [200190073] = {
    id = 200190073,
    name = "在得到你肯定的答复后，少女便带你进入森林寻找魔物。"
  },
  [200190074] = {
    id = 200190074,
    name = "恰巧那头魔物正在休息，你和少女默契地对视一眼，随即展开攻击。"
  },
  [200190075] = {
    id = 200190075,
    name = "少女身手敏捷，几乎在你击中魔物的瞬间，她的攻击也紧随而至。"
  },
  [200190076] = {
    id = 200190076,
    name = "同时在魔物做出反应之前，少女已跳出魔物的攻击范围，准备再次进攻。"
  },
  [200190077] = {
    id = 200190077,
    name = "你见状，判断出少女的实力其实不弱，主要是缺乏实战经验以及对魔物的了解，于是你决定只做最低程度的协助。"
  },
  [200190078] = {
    id = 200190078,
    name = "在你的协助下，不多时魔物便失去了抵抗能力，少女见状，射出精准的一箭，结束了魔物的生命。"
  },
  [200190079] = {
    id = 200190079,
    name = "随即，她掏出一柄锋利的短刀，麻利地割下了魔物的头部。"
  },
  [200190080] = {
    id = 200190080,
    name = "「总觉得，事情太容易了。我真的符合转正仪式的要求吗？」"
  },
  [200190081] = {
    id = 200190081,
    name = "「别多想，能得到别人的帮助也是一种实力。」"
  },
  [200190082] = {
    id = 200190082,
    name = "少女觉得你说的似乎也有道理，取出了一些物资送给你，当作对你的谢礼。"
  },
  [200190083] = {
    id = 200190083,
    name = "你才不想冒险做诱饵，转身就走，少女大声叫喊着让你站住。"
  },
  [200190084] = {
    id = 200190084,
    name = "谁知无意间惊醒了魔物，魔物咆哮着向你们冲来。"
  },
  [200190085] = {
    id = 200190085,
    name = "眼见情况危急，你瞬间使出全力攻击，片刻便将魔物斩杀。"
  },
  [200190086] = {
    id = 200190086,
    name = "你刚松了口气，回头却看少女目瞪口呆地跪坐在了地上。"
  },
  [200190087] = {
    id = 200190087,
    name = "「没想到我实力这么强吗？」"
  },
  [200190088] = {
    id = 200190088,
    name = "少女突然放声大哭起来，要你赔她的魔物。"
  },
  [200190089] = {
    id = 200190089,
    name = "在她的哭诉中你才知道，原来这头魔物是她从见习猎影转正的考核目标。"
  },
  [200190090] = {
    id = 200190090,
    name = "她在附近寻找了很久才发现这一头，现在却被你杀了......"
  },
  [200190091] = {
    id = 200190091,
    name = "你思考片刻，给她出了个主意。"
  },
  [200190092] = {
    id = 200190092,
    name = "「这头魔物尸身还算完整，你要不然直接带回去？反正只有我俩知道真相。」"
  },
  [200190093] = {
    id = 200190093,
    name = "少女气鼓鼓地看着你。"
  },
  [200190094] = {
    id = 200190094,
    name = "「这、这是作弊！你果然是个无赖！」"
  },
  [200190095] = {
    id = 200190095,
    name = "少女又哭了一会儿，但似乎也想不到其他解决方法。"
  },
  [200190096] = {
    id = 200190096,
    name = "「你能保证不对任何人说吗？」"
  },
  [200190097] = {
    id = 200190097,
    name = "眼见少女回心转意，你急忙向她保证，并将一些显而易见的伤口处理了一下，让整个魔物看上去更像是被少女所杀。"
  },
  [200190098] = {
    id = 200190098,
    name = "少女嘟囔着说她只需要头就行，随即掏出一把锋利的短刀取下了魔物的头颅。"
  },
  [200190099] = {
    id = 200190099,
    name = "「不管怎样，这次谢谢你了，无赖先生。」"
  },
  [200190100] = {
    id = 200190100,
    name = "「......」"
  },
  [200190101] = {
    id = 200190101,
    name = "见少女离去，你也松了口气，内心暗暗告诉自己，下次还是少招惹这些少女吧。"
  },
  [200190102] = {
    id = 200190102,
    name = "「什么麻烦，说出来让我高兴一下？」"
  },
  [200190103] = {
    id = 200190103,
    name = "少女生气地拿出弓箭对准了你，认为你是个轻浮的无赖。"
  },
  [200190104] = {
    id = 200190104,
    name = "在你的解释下，她才稍微消了气，不过要求你帮她做件事才能原谅你。"
  },
  [200190105] = {
    id = 200190105,
    name = "否则她就告诉自己的家族，让他们找你麻烦。"
  },
  [200190106] = {
    id = 200190106,
    name = "你见少女似乎真的很烦恼，想着反正目前也没有要紧的事，于是便决定看看少女想要你做什么。"
  },
  [200190107] = {
    id = 200190107,
    name = "你跟着少女来到森林深处，她告诉你此处是一头魔物的领地。"
  },
  [200190108] = {
    id = 200190108,
    name = "她现在要解决那头魔物，而你负责在一旁护卫她的安全，防止其他魔物打扰到她。"
  },
  [200190109] = {
    id = 200190109,
    name = "你们又走了片刻，发现了她所说的那头魔物。"
  },
  [200190110] = {
    id = 200190110,
    name = "魔物正在休息，少女却并没有马上攻击，她显得非常犹豫，你疑惑地看着她。"
  },
  [200190111] = {
    id = 200190111,
    name = "「不行，我之前也在它休息时攻击过它，但无法一击致命。」"
  },
  [200190112] = {
    id = 200190112,
    name = "「它一旦被刺激便会马上暴起反击，我根本来不及进行第二次攻击。」"
  },
  [200190113] = {
    id = 200190113,
    name = "她皱着眉头看向你，似乎在思考着什么。"
  },
  [200190114] = {
    id = 200190114,
    name = "「这样好了，你去帮我吸引那只魔物吧！」"
  },
  [200190115] = {
    id = 200190115,
    name = "「记住，你不能随便发起攻击，必须由我来杀掉它！」"
  },
  [200190116] = {
    id = 200190116,
    name = "你哑然一笑，不过想着既然答应了少女，也就硬着头皮走了过去。"
  },
  [200190117] = {
    id = 200190117,
    name = "对你而言，这头魔物算不上强，不过考虑到少女说的话，你还是打起精神，谨慎应对。"
  },
  [200190118] = {
    id = 200190118,
    name = "一声清脆的箭鸣，拉开了战斗序幕，你不断挑衅着愤怒的魔物，给少女创造机会。"
  },
  [200190119] = {
    id = 200190119,
    name = "她也没有辜负你的苦心，一箭又一箭精准命中魔物。"
  },
  [200190120] = {
    id = 200190120,
    name = "过了一会儿，在少女全力一击中，魔物应声倒下。"
  },
  [200190121] = {
    id = 200190121,
    name = "她掏出一柄锋利的短刀，麻利地割下了魔物的头部，表示剩余部分随你处置。"
  },
  [200190122] = {
    id = 200190122,
    name = "「为什么只要头？」"
  },
  [200190123] = {
    id = 200190123,
    name = "她不好意思地笑了笑，说这都是为了完成自己的转正仪式。"
  },
  [200190124] = {
    id = 200190124,
    name = "在她的家族，要想成为独当一面的探险者，必须猎杀指定魔物完成考核。"
  },
  [200190125] = {
    id = 200190125,
    name = "否则就只能在家族据点附近活动，不得独自远行。"
  },
  [200190126] = {
    id = 200190126,
    name = "少女对你表示了感谢，因为你的出现才让她终于完成了考核。"
  },
  [200190127] = {
    id = 200190127,
    name = "现在她最大的烦恼已经消散，决定把带的物资全部送给你作为谢礼。"
  },
  [200200001] = {
    id = 200200001,
    name = "「嗯？前面有个人？」"
  },
  [200200002] = {
    id = 200200002,
    name = "野外。一个中年男人跪在一间小屋门口，双手合十念念有词。"
  },
  [200200003] = {
    id = 200200003,
    name = "「美丽的面包女神啊，我赞美您，感谢您，您是纯洁而至高的......」"
  },
  [200200004] = {
    id = 200200004,
    name = "「你在这里做什么啊？」"
  },
  [200200005] = {
    id = 200200005,
    name = "「没有人告诉你吗？打断别人祈祷，是非常不礼貌的行为。」"
  },
  [200200006] = {
    id = 200200006,
    name = "「呃，抱歉。」"
  },
  [200200007] = {
    id = 200200007,
    name = "「哼，告诉你也没关系，你知道吗，面包女神降临了！」"
  },
  [200200008] = {
    id = 200200008,
    name = "「啊？」"
  },
  [200200009] = {
    id = 200200009,
    name = "「最近我只要把刚做好的面包端到桌上，等我转身的时候，面包就会消失。」"
  },
  [200200010] = {
    id = 200200010,
    name = "「虽然刚开始的确吓了我一跳。」"
  },
  [200200011] = {
    id = 200200011,
    name = "「但后来我想到了面包女神，一定是她聆听到了我的祈祷，降临在了这里！」"
  },
  [200200012] = {
    id = 200200012,
    name = "「......」"
  },
  [200200013] = {
    id = 200200013,
    name = "（通常情况下，都是被偷了吧......）"
  },
  [200200014] = {
    id = 200200014,
    name = "「但是我不知道是什么原因。」"
  },
  [200200015] = {
    id = 200200015,
    name = "「她拿了我的面包，却没有留下任何信息。」"
  },
  [200200016] = {
    id = 200200016,
    name = "「这不应该啊。」"
  },
  [200200017] = {
    id = 200200017,
    name = "「你可真是个奇葩......」"
  },
  [200200018] = {
    id = 200200018,
    name = "「奇葩是什么意思？」"
  },
  [200200019] = {
    id = 200200019,
    name = "「意思就是......你非常的特别。」"
  },
  [200200020] = {
    id = 200200020,
    name = "「没错，或许这就是女神看中我的原因吧？！」"
  },
  [200200021] = {
    id = 200200021,
    name = "「小伙子，你这么博学多才，一定知道怎么和面包女神沟通吧！」"
  },
  [200200022] = {
    id = 200200022,
    name = "「能不能拜托你帮我问问看，女神为什么没有回应我呢？」"
  },
  [200200023] = {
    id = 200200023,
    name = "「是好吃，还是不好吃呢？」"
  },
  [200200024] = {
    id = 200200024,
    name = "「大叔，你简直是奇葩中的奇葩。」"
  },
  [200200025] = {
    id = 200200025,
    name = "「哈哈，小伙子，你这么有眼光，也一定是个奇葩！」"
  },
  [200200026] = {
    id = 200200026,
    name = "「......倒也不用这么说。」"
  },
  [200200027] = {
    id = 200200027,
    name = "「哈哈，这是对你的称赞啊！」"
  },
  [200200028] = {
    id = 200200028,
    name = "「称赞就不用了，我先帮你看看吧。」"
  },
  [200200029] = {
    id = 200200029,
    name = "男人笑呵呵地把你带进了小屋中。"
  },
  [200200030] = {
    id = 200200030,
    name = "小屋里很简陋，除了些简单的工具外，就只有些面粉黄油之类的食材。"
  },
  [200200031] = {
    id = 200200031,
    name = "「因为对面包女神的崇拜，我走到哪里都会带上做面包的材料。」"
  },
  [200200032] = {
    id = 200200032,
    name = "「这些食材都是我从附近圈落弄过来的。」"
  },
  [200200033] = {
    id = 200200033,
    name = "「女神只拿走了面包，却没有拿这些面粉呢。」"
  },
  [200200034] = {
    id = 200200034,
    name = "「当然！面包女神，当然只会对面包感兴趣。」"
  },
  [200200035] = {
    id = 200200035,
    name = "你勘查了房间的环境，发现一股很杂乱的力量在四周蔓延。"
  },
  [200200036] = {
    id = 200200036,
    name = "「这里的确有不寻常的气息，你先出去，我看一看。」"
  },
  [200200037] = {
    id = 200200037,
    name = "「好啊，如果需要我做面包，随时叫我！」"
  },
  [200200038] = {
    id = 200200038,
    name = "男人离开了房间。"
  },
  [200200039] = {
    id = 200200039,
    name = "「......这种气息，像是魔法阵，但这个魔法阵会在哪儿呢？」"
  },
  [200200040] = {
    id = 200200040,
    name = "你在屋里仔细搜寻了一番，最终在桌子下方找到一个魔法阵。"
  },
  [200200041] = {
    id = 200200041,
    name = "「找到了，在这里。」"
  },
  [200200042] = {
    id = 200200042,
    name = "「是单向传送的魔法阵啊。」"
  },
  [200200043] = {
    id = 200200043,
    name = "「目标区域竟然只是桌子上这一小块。」"
  },
  [200200044] = {
    id = 200200044,
    name = "「看起来这位面包女神真的只想要面包而已。」"
  },
  [200200045] = {
    id = 200200045,
    name = "你将手放在魔法阵上，感知魔力来源，发现转移出口，就在不远的地方。"
  },
  [200200046] = {
    id = 200200046,
    name = "你离开房间，看到男人还在旁边虔诚地祈祷。"
  },
  [200200047] = {
    id = 200200047,
    name = "你没有理会他，径自走到了小屋背后的山洞外。"
  },
  [200200048] = {
    id = 200200048,
    name = "还没进去，洞里走出来一个女孩，手中拿着刚出炉的面包咬了一口。"
  },
  [200200049] = {
    id = 200200049,
    name = "「嘻嘻，面包女神我就不客气啦！」"
  },
  [200200050] = {
    id = 200200050,
    name = "「唔，你就是面包女神啊。」"
  },
  [200200051] = {
    id = 200200051,
    name = "「被发现了！」"
  },
  [200200052] = {
    id = 200200052,
    name = "女孩拔腿就跑，但很快被你抓住。"
  },
  [200200053] = {
    id = 200200053,
    name = "「呜，我不是面包女神，我只是太饿了，饶了我吧！」"
  },
  [200200054] = {
    id = 200200054,
    name = "「可是面包师想听听女神的感想啊，怎么办？」"
  },
  [200200055] = {
    id = 200200055,
    name = "「不不不，啊，我以后不偷他的面包了。」"
  },
  [200200056] = {
    id = 200200056,
    name = "「你要是放了我，我给你一块稀有的宝石怎么样？」"
  },
  [200200057] = {
    id = 200200057,
    name = "「你身上能有什么好东西？」"
  },
  [200200058] = {
    id = 200200058,
    name = "「少看不起人了！」"
  },
  [200200059] = {
    id = 200200059,
    name = "「这是一个探险者送我的，我当时把面包给他吃，他作为回礼给我的，说是能卖很多钱！」"
  },
  [200200060] = {
    id = 200200060,
    name = "「只是我一直舍不得罢了。」"
  },
  [200200061] = {
    id = 200200061,
    name = "「是吗，让我看看。」"
  },
  [200200062] = {
    id = 200200062,
    name = "「啧，装好人，结果也是一个见钱眼开的家伙。」"
  },
  [200200063] = {
    id = 200200063,
    name = "女孩朝你扔过来一个石头，趁你注意力分散之际，撒腿逃走了。"
  },
  [200200064] = {
    id = 200200064,
    name = "你笑着摇了摇头，没有继续去追。"
  },
  [200200065] = {
    id = 200200065,
    name = "你回到小屋，男人正祈祷完毕。"
  },
  [200200066] = {
    id = 200200066,
    name = "「怎么样？面包女神怎么说？」"
  },
  [200200067] = {
    id = 200200067,
    name = "「你说的面包女神，其实只是一个偷面包的女孩而已。」"
  },
  [200200068] = {
    id = 200200068,
    name = "「她在这张桌子下方设置了魔法阵，用来转移你的面包。」"
  },
  [200200069] = {
    id = 200200069,
    name = "男人愤怒地摧毁了餐桌，并扬言要去抓面包小偷，说罢摔门而去。"
  },
  [200200070] = {
    id = 200200070,
    name = "你自觉无趣，转身离开。"
  },
  [200200071] = {
    id = 200200071,
    name = "（那女孩瘦弱不堪，看样子确实是饿了。既然没什么坏心思，这次就不揭发她吧。）"
  },
  [200200072] = {
    id = 200200072,
    name = "「女神说，你烤的面包太好吃了，她就忍不住取走了一些。」"
  },
  [200200073] = {
    id = 200200073,
    name = "「哈哈，那是当然，我对自己烤面包的手艺可是很有信心！」"
  },
  [200200074] = {
    id = 200200074,
    name = "「嗯，对对对。」"
  },
  [200200075] = {
    id = 200200075,
    name = "「来来，这些是我之前刚做的，你也尝尝这连女神都赞不绝口的面包吧！」"
  },
  [200200076] = {
    id = 200200076,
    name = "男人非常开心，不由哼起了小曲，决定以后要给面包女神供奉更多美味的面包。"
  },
  [200200077] = {
    id = 200200077,
    name = "你把女孩抓到了男人面前，告诉男人这就是他的“面包女神”。"
  },
  [200200078] = {
    id = 200200078,
    name = "男人非常愤怒，当场摧毁了餐桌，并警告女孩滚出这片区域，否则会剁掉她的手。"
  },
  [200200079] = {
    id = 200200079,
    name = "女孩绝望地离开了，最后看向你的眼神中带着几分怨恨。"
  },
  [200200080] = {
    id = 200200080,
    name = "男人对你非常感激，将一大袋上好的面包送给你，感谢你帮他查明了一切。"
  },
  [200200081] = {
    id = 200200081,
    name = "「虽然这个女神是假的，但如果我继续做面包，每天虔诚祈祷，真正的女神一定会降临！」"
  },
  [200200082] = {
    id = 200200082,
    name = "男人给自己打气，随即开始揉面，准备做新的面包。"
  },
  [200200083] = {
    id = 200200083,
    name = "你看了看男人塞到怀中的面包，叹了口气离开了。"
  },
  [200200084] = {
    id = 200200084,
    name = "「其实，她这么爱吃你做的面包，正是因为你的面包好吃吧。」"
  },
  [200200085] = {
    id = 200200085,
    name = "「就算是这样，我也无法原谅偷我面包的小贼！」"
  },
  [200200086] = {
    id = 200200086,
    name = "「啧，他的面包难吃死了，要不是饿了，谁会吃他做的面包！」"
  },
  [200200087] = {
    id = 200200087,
    name = "「你说什么？！」"
  },
  [200200088] = {
    id = 200200088,
    name = "你见二人争吵起来，急忙出言阻止。"
  },
  [200200089] = {
    id = 200200089,
    name = "你告诉男人，女孩吃了他的面包，就干脆让女孩给他打杂来偿还。"
  },
  [200200090] = {
    id = 200200090,
    name = "你又告诉女孩，如果成为男人的助手，好好干活，今后也不用为了生计而烦恼。"
  },
  [200200091] = {
    id = 200200091,
    name = "二人各自思索一番后，同意了你的提议，握手言和。"
  },
  [200200092] = {
    id = 200200092,
    name = "「如果能有个助手协助，我确实会轻松很多，而且要是能及时得到口感反馈，我的手艺一定能提升更快！」"
  },
  [200200093] = {
    id = 200200093,
    name = "「要是不好吃我可是会直说的，你最好能有心理准备！」"
  },
  [200200094] = {
    id = 200200094,
    name = "「只要能让真正的面包女神显灵，我才不会在意！不过你要是敢胡说，小心我教训你！」"
  },
  [200200095] = {
    id = 200200095,
    name = "「来呀来呀，就看你有没有本事抓住我了......」"
  },
  [200200096] = {
    id = 200200096,
    name = "你看着二人斗嘴吵闹的样子，不禁觉得这样的结局似乎也不错。"
  },
  [200200097] = {
    id = 200200097,
    name = "「你的面包应该是被偷了。」"
  },
  [200200098] = {
    id = 200200098,
    name = "男人非常惊讶，但思考以后认为你说的也有道理。"
  },
  [200200099] = {
    id = 200200099,
    name = "「虽然说是在我的面前消失了，但也不乏有这样的可能性。」"
  },
  [200200100] = {
    id = 200200100,
    name = "「不过那个小偷到底用了什么方法偷走了我的面包呢？」"
  },
  [200200101] = {
    id = 200200101,
    name = "你跟着他进入了小屋。"
  },
  [200200102] = {
    id = 200200102,
    name = "小屋里很简陋，除了些简单的工具外，就只有些面粉黄油之类的食材。"
  },
  [200200103] = {
    id = 200200103,
    name = "「因为对面包女神的崇拜，我走到哪里都会带上做面包的材料。」"
  },
  [200200104] = {
    id = 200200104,
    name = "「这些食材都是我从附近圈落弄过来的。」"
  },
  [200200105] = {
    id = 200200105,
    name = "你勘查了房间的环境，发现一股很杂乱的力量在四周蔓延。"
  },
  [200200106] = {
    id = 200200106,
    name = "你把情况告诉了男人，他很惊讶有人会对他这样破旧的房子做手脚。"
  },
  [200200107] = {
    id = 200200107,
    name = "于是男人请求你帮忙确定问题所在。"
  },
  [200200108] = {
    id = 200200108,
    name = "你仔细感应着屋内的魔力，最终在餐桌背面发现了一个小的单向传送阵。"
  },
  [200200109] = {
    id = 200200109,
    name = "「看起来小偷只想偷面包啊。」"
  },
  [200200110] = {
    id = 200200110,
    name = "「哇！竟敢在我眼前偷东西，还让我以为是面包女神！可恶！」"
  },
  [200200111] = {
    id = 200200111,
    name = "「可恶！我非抓到他不可！」"
  },
  [200200112] = {
    id = 200200112,
    name = "男人发泄怒气之后又陷入苦恼，他并不熟悉魔法，不清楚该怎样抓住小偷。"
  },
  [200200113] = {
    id = 200200113,
    name = "「这位兄弟，我一看你就是乐于助人的大好人，你帮我抓到那个家伙，我给你报酬怎么样？」"
  },
  [200200114] = {
    id = 200200114,
    name = "你将手放在魔法阵上，感知魔力来源，发现转移出口，就在不远的地方。"
  },
  [200200115] = {
    id = 200200115,
    name = "你带着男人沿着魔力传导的方向搜寻，最终来到男人房子后面的一个山洞中。"
  },
  [200200116] = {
    id = 200200116,
    name = "你们蹑手蹑脚地摸了进去，发现一个女孩正啃着面包。"
  },
  [200200117] = {
    id = 200200117,
    name = "「你这个小偷，让我给逮着了吧！」"
  },
  [200200118] = {
    id = 200200118,
    name = "女孩先是一惊，随即扑通一声跪在地上。"
  },
  [200200119] = {
    id = 200200119,
    name = "「不、不是的，我只是饿了。」"
  },
  [200200120] = {
    id = 200200120,
    name = "女孩一边求饶一边道歉，眼泪止不住地往外流。"
  },
  [200200121] = {
    id = 200200121,
    name = "「呜，我再也不偷了，饶了我吧。」"
  },
  [200200122] = {
    id = 200200122,
    name = "「......」"
  },
  [200200123] = {
    id = 200200123,
    name = "女孩的反应出乎了男人的预料，他有些不知所措地看着你，你冲他耸了耸肩。"
  },
  [200200124] = {
    id = 200200124,
    name = "最终男人叹了口气，整了整嗓子，带着些别扭的语气向女孩询问。"
  },
  [200200125] = {
    id = 200200125,
    name = "「我的面包，怎么样？」"
  },
  [200200126] = {
    id = 200200126,
    name = "「呜呜呜......啊？」"
  },
  [200200127] = {
    id = 200200127,
    name = "「回答我啊！我的面包，味道怎么样？」"
  },
  [200200128] = {
    id = 200200128,
    name = "「......」"
  },
  [200200129] = {
    id = 200200129,
    name = "「哇，你这个小偷，吃了我的面包，连句好话都不知道说吗？」"
  },
  [200200130] = {
    id = 200200130,
    name = "「你每天都做同样的面包，口味太单调了......」"
  },
  [200200131] = {
    id = 200200131,
    name = "「！！！」"
  },
  [200200132] = {
    id = 200200132,
    name = "看着眼前的二人，你突然想到个主意。"
  },
  [200200133] = {
    id = 200200133,
    name = "你告诉男人，既然他现在在钻研面包手艺，不如干脆收这女孩为助手。"
  },
  [200200134] = {
    id = 200200134,
    name = "平时不仅可以帮忙做些杂事，做出的面包也能让她试吃，及时反馈味道，如此一来他的手艺或许能有明显的提升。"
  },
  [200200135] = {
    id = 200200135,
    name = "你又告诉女孩，作为她偷吃男人面包的惩罚，需要她给男人做助手，负责帮他精进手艺。"
  },
  [200200136] = {
    id = 200200136,
    name = "二人各自思索一番后，同意了你的提议。"
  },
  [200200137] = {
    id = 200200137,
    name = "「如果能有个助手协助，我确实会轻松很多，而且要是能及时得到反馈，我的手艺一定能提升更快！」"
  },
  [200200138] = {
    id = 200200138,
    name = "「如果做得不好吃，我可以直说吗？」"
  },
  [200200139] = {
    id = 200200139,
    name = "「哼，当然可以，不过我对我的手艺可是很自信的！你要是胡说我可是会教训你！」"
  },
  [200200140] = {
    id = 200200140,
    name = "女孩可怜巴巴地看着你们，眼泪似乎又要盈出眼眶。"
  },
  [200200141] = {
    id = 200200141,
    name = "「......」"
  },
  [200200142] = {
    id = 200200142,
    name = "「啊，真是麻烦啊！只要你不是胡说，我就不会教训你的！」"
  },
  [200200143] = {
    id = 200200143,
    name = "女孩破涕为笑，表示自己以后有什么就说什么，努力帮男人提升做面包的手艺。"
  },
  [200200144] = {
    id = 200200144,
    name = "见事情也算圆满结束，你便准备离去。"
  },
  [200200145] = {
    id = 200200145,
    name = "男人却拉着你，执意要做一些美味的面包来感谢你，并且表示这回的面包女神虽然是假的，但如果继续坚持，说不真的女神就会降临！"
  },
  [200200146] = {
    id = 200200146,
    name = "你笑了笑，与他一起回到了他的小屋。"
  },
  [200200147] = {
    id = 200200147,
    name = "在女孩和男人的嬉闹声中，袅袅炊烟再次升起。"
  },
  [200200148] = {
    id = 200200148,
    name = "你以还有要事为由拒绝了男人的请求，男人叹了口气，只能先将桌子毁掉。"
  },
  [200200149] = {
    id = 200200149,
    name = "你离开之后又过了几天，男人终于做好了一张新的桌子。"
  },
  [200200150] = {
    id = 200200150,
    name = "在他外出购买食材之际，一个女孩出现在了房子外面。"
  },
  [200200151] = {
    id = 200200151,
    name = "确认屋内无人后，女孩钻进了屋内，在那张新桌子下方刻画起来......"
  },
  [200200152] = {
    id = 200200152,
    name = "「还是不要打扰他了。」"
  },
  [200210001] = {
    id = 200210001,
    name = "你走到一处山脚下，发现有人正蹲在路旁挖掘什么。"
  },
  [200210002] = {
    id = 200210002,
    name = "你靠近了些，看到他正小心翼翼地翻动土壤，剔除杂草，想挖出埋在下面的药草。"
  },
  [200210003] = {
    id = 200210003,
    name = "在他身边放着一个小筐，里面有几株新鲜的药草。"
  },
  [200210004] = {
    id = 200210004,
    name = "「哇，吓我一跳！」"
  },
  [200210005] = {
    id = 200210005,
    name = "「......我有这么吓人吗？」"
  },
  [200210006] = {
    id = 200210006,
    name = "「啊，不是，我还以为是魔物。」"
  },
  [200210007] = {
    id = 200210007,
    name = "「你在这里干什么？」"
  },
  [200210008] = {
    id = 200210008,
    name = "「采药啊，不说了，一会儿时间快到了。」"
  },
  [200210009] = {
    id = 200210009,
    name = "采药人说完后继续开始挖药草。"
  },
  [200210010] = {
    id = 200210010,
    name = "「竟然对药草这么了解，太厉害了！」"
  },
  [200210011] = {
    id = 200210011,
    name = "「呵呵，我指望着干这个赚钱，自然得懂些才行。」"
  },
  [200210012] = {
    id = 200210012,
    name = "「我说的是实话，我很佩服像你这样把一件事做到极致的人。」"
  },
  [200210013] = {
    id = 200210013,
    name = "「呵呵，是嘛。小兄弟，你要是想要药草，旁边筐里有，随便拿。」"
  },
  [200210014] = {
    id = 200210014,
    name = "你笑着道了谢，从一旁的筐里拿起一株药草。"
  },
  [200210015] = {
    id = 200210015,
    name = "「这药草是做什么的？」"
  },
  [200210016] = {
    id = 200210016,
    name = "「......」"
  },
  [200210017] = {
    id = 200210017,
    name = "「你......我今天只采到这么些，真没有多的了。」"
  },
  [200210018] = {
    id = 200210018,
    name = "「喂，别一副这种表情，我不是在打劫你。」"
  },
  [200210019] = {
    id = 200210019,
    name = "「你不是要这个药草吗？」"
  },
  [200210020] = {
    id = 200210020,
    name = "「这不是你送我的吗？」"
  },
  [200210021] = {
    id = 200210021,
    name = "「......噢，你不是劫匪啊。」"
  },
  [200210022] = {
    id = 200210022,
    name = "「......」"
  },
  [200210023] = {
    id = 200210023,
    name = "「那你把这个拿着。」"
  },
  [200210024] = {
    id = 200210024,
    name = "采药人又递给你一株药草。"
  },
  [200210025] = {
    id = 200210025,
    name = "「？？？」"
  },
  [200210026] = {
    id = 200210026,
    name = "「你手上那株有毒，这一株是解毒的。」"
  },
  [200210027] = {
    id = 200210027,
    name = "你立刻把手中的药草放回了他的药筐，把解毒药草握在了手中。"
  },
  [200210028] = {
    id = 200210028,
    name = "「你坑人啊？」"
  },
  [200210029] = {
    id = 200210029,
    name = "「真想坑你就不会告诉你了。」"
  },
  [200210030] = {
    id = 200210030,
    name = "采药人把新挖出的药草放进了筐里，起身准备离开。"
  },
  [200210031] = {
    id = 200210031,
    name = "「时间也差不多了，再晚魔物就该来了。」"
  },
  [200210032] = {
    id = 200210032,
    name = "采药人提上药筐，和你告别后，快速向据点方向跑去。"
  },
  [200210033] = {
    id = 200210033,
    name = "「真是个奇怪的采药人。」"
  },
  [200210034] = {
    id = 200210034,
    name = "「不不，那是你用来卖的吧，我不能拿你的。」"
  },
  [200210035] = {
    id = 200210035,
    name = "「......噢，你不是劫匪啊。」"
  },
  [200210036] = {
    id = 200210036,
    name = "「你以为我是劫匪？」"
  },
  [200210037] = {
    id = 200210037,
    name = "「算是友善的劫匪吧。」"
  },
  [200210038] = {
    id = 200210038,
    name = "「什么叫友善的......」"
  },
  [200210039] = {
    id = 200210039,
    name = "「有的劫匪就像你这样，这种劫匪只要不伤害我们，拿就拿吧。」"
  },
  [200210040] = {
    id = 200210040,
    name = "「但有的......不管人还是货物，他们都不会放过，真遇上就得赶紧跑。」"
  },
  [200210041] = {
    id = 200210041,
    name = "「所以你一开始才那么害怕......」"
  },
  [200210042] = {
    id = 200210042,
    name = "「你没想过惩治劫匪吗？」"
  },
  [200210043] = {
    id = 200210043,
    name = "「没用的。」"
  },
  [200210044] = {
    id = 200210044,
    name = "「像你这样今天还是探险者，但明天说不定就成了劫匪的人太多了。」"
  },
  [200210045] = {
    id = 200210045,
    name = "「惩治一两个，又有什么用呢？」"
  },
  [200210046] = {
    id = 200210046,
    name = "「......」"
  },
  [200210047] = {
    id = 200210047,
    name = "采药人把新挖出的药草放进了筐里，起身准备离开。"
  },
  [200210048] = {
    id = 200210048,
    name = "「时间也差不多了，再晚魔物就该来了。」"
  },
  [200210049] = {
    id = 200210049,
    name = "采药人提上药筐，和你告别后，快速向据点方向跑去。"
  },
  [200210050] = {
    id = 200210050,
    name = "「原来时间快到了，不是说的药草啊......」"
  },
  [200210051] = {
    id = 200210051,
    name = "「什么意思？这药草还有时间限制？」"
  },
  [200210052] = {
    id = 200210052,
    name = "「不是药草限制时间，是魔物。」"
  },
  [200210053] = {
    id = 200210053,
    name = "「只有这段时间，魔物才不会出现。」"
  },
  [200210054] = {
    id = 200210054,
    name = "「我可不像你们探险者，能轻易对付魔物。」"
  },
  [200210055] = {
    id = 200210055,
    name = "「原来是这样。」"
  },
  [200210056] = {
    id = 200210056,
    name = "「探险者，你在这里干嘛？」"
  },
  [200210057] = {
    id = 200210057,
    name = "「我路过而已。」"
  },
  [200210058] = {
    id = 200210058,
    name = "「呼，我还以为是要抢我药草的劫匪呢。」"
  },
  [200210059] = {
    id = 200210059,
    name = "「......」"
  },
  [200210060] = {
    id = 200210060,
    name = "「所以你刚刚被吓到并不是因为魔物。」"
  },
  [200210061] = {
    id = 200210061,
    name = "「既然这样，你没想过换一份生计吗？」"
  },
  [200210062] = {
    id = 200210062,
    name = "「想过，但对我而言，现在这样算是最好的生活了。」"
  },
  [200210063] = {
    id = 200210063,
    name = "「虽然偶尔会被打劫，偶尔会因魔物一无所获，但至少能保障我和家人的生活，我已经很满足了。」"
  },
  [200210064] = {
    id = 200210064,
    name = "采药人把新挖出的药草放进了筐里，起身准备离去。"
  },
  [200210065] = {
    id = 200210065,
    name = "「时间也差不多了，再不走魔物就该来了。」"
  },
  [200210066] = {
    id = 200210066,
    name = "「小兄弟，你要是想猎杀魔物，可以在这附近等一会儿。」"
  },
  [200210067] = {
    id = 200210067,
    name = "「不过它们经常成群出现，你一个人的话，记得小心一点。」"
  },
  [200210068] = {
    id = 200210068,
    name = "你向采药人道谢，看着他离开了。"
  },
  [200210069] = {
    id = 200210069,
    name = "要不要等魔物出现呢？"
  },
  [200210070] = {
    id = 200210070,
    name = "采药人离开不久，好几头魔物就出现在了你的视野中。"
  },
  [200210071] = {
    id = 200210071,
    name = "对你而言，这些魔物都是行走的素材，你动了动臂膀，向着魔物冲了过去。"
  },
  [200210072] = {
    id = 200210072,
    name = "你对这些魔物并没什么兴趣，采药人离开后，你也离开了此地。"
  },
  [200210073] = {
    id = 200210073,
    name = "「你这药草怎么卖呢？」"
  },
  [200210074] = {
    id = 200210074,
    name = "「不好意思啊，这些药草都被据点的药商收购了。」"
  },
  [200210075] = {
    id = 200210075,
    name = "「你想买的话，可以去据点里最大的那家药店。」"
  },
  [200210076] = {
    id = 200210076,
    name = "「这样啊，不过你直接卖给探险者，不是比低价给药商更好吗？」"
  },
  [200210077] = {
    id = 200210077,
    name = "「这不一样啊，小兄弟。」"
  },
  [200210078] = {
    id = 200210078,
    name = "「直接卖给你确实会多赚一点，但你并不会每天都买我的药草。」"
  },
  [200210079] = {
    id = 200210079,
    name = "「收购商则不一样，只要我们的协议还有效，不管我什么时候送过去他都会买下。」"
  },
  [200210080] = {
    id = 200210080,
    name = "「你少卖一点也不会被发现吧？」"
  },
  [200210081] = {
    id = 200210081,
    name = "「协议就是用来遵守的啊，我不可能为了一点蝇头小利，损害我们的长期合作关系。」"
  },
  [200210082] = {
    id = 200210082,
    name = "「做生意，特别是想长久做下去的生意，凭的是良心啊小兄弟。」"
  },
  [200210083] = {
    id = 200210083,
    name = "采药人把新挖出的药草放进了筐里，起身离开。"
  },
  [200210084] = {
    id = 200210084,
    name = "「......」"
  },
  [200210085] = {
    id = 200210085,
    name = "你趁他不注意，偷偷从篮子里拿走了几株药草，随后快速离开了。"
  },
  [200210086] = {
    id = 200210086,
    name = "没过多久，你的手开始起红疹，并出现了腐烂的现象。"
  },
  [200210087] = {
    id = 200210087,
    name = "（竟然是有毒的药草！）"
  },
  [200210088] = {
    id = 200210088,
    name = "你赶紧扔掉了药草，立即治疗伤口。"
  },
  [200210089] = {
    id = 200210089,
    name = "看起来只是个普通的采药人，你继续赶路。"
  },
  [200220001] = {
    id = 200220001,
    name = "「麦克，别跑，快回来！」"
  },
  [200220002] = {
    id = 200220002,
    name = "你听到前方有人在喊叫。"
  },
  [200220003] = {
    id = 200220003,
    name = "不多时，视野中出现了一人一狗。"
  },
  [200220004] = {
    id = 200220004,
    name = "白色的大狗正朝你的方向狂奔，身后一个探险者上气不接下气地追赶着。"
  },
  [200220005] = {
    id = 200220005,
    name = "「前面的先生，快帮我拦住麦克！」"
  },
  [200220006] = {
    id = 200220006,
    name = "正在这时，大狗发出一声吼叫，朝你扑了过来！"
  },
  [200220007] = {
    id = 200220007,
    name = "你闪身躲过，反手抓住了狗的后颈，朝探险者扔了回去。"
  },
  [200220008] = {
    id = 200220008,
    name = "「麦克，冷静点！」"
  },
  [200220009] = {
    id = 200220009,
    name = "探险者刚把它接住，大狗却猛地一挣扎，再次向远处奔去。"
  },
  [200220010] = {
    id = 200220010,
    name = "探险者大喊着狗的名字，无力地坐在了地上。"
  },
  [200220011] = {
    id = 200220011,
    name = "「这是怎么回事？」"
  },
  [200220012] = {
    id = 200220012,
    name = "「那是我养的麦克，从小跟我一起长大。」"
  },
  [200220013] = {
    id = 200220013,
    name = "「麦克一直很乖的，它给了我很多的帮助，就像我的家人一样。」"
  },
  [200220014] = {
    id = 200220014,
    name = "「它从来不害怕魔物，还会在关键的时候保护我。」"
  },
  [200220015] = {
    id = 200220015,
    name = "「可是不知道怎么回事。」"
  },
  [200220016] = {
    id = 200220016,
    name = "「这两天它的脾气突然变得很暴躁，不允许任何人碰它。」"
  },
  [200220017] = {
    id = 200220017,
    name = "「它一直往森林里跑，我把它抓回来好几次，它精神好一点后又跑了。」"
  },
  [200220018] = {
    id = 200220018,
    name = "「真是麻烦啊，啊，对了。」"
  },
  [200220019] = {
    id = 200220019,
    name = "「探险者，我现在有更重要的事，你能帮我把它找回来吗？」"
  },
  [200220020] = {
    id = 200220020,
    name = "「太好了！麦克就在前方的森林里，它总是对着一棵树吼叫。」"
  },
  [200220021] = {
    id = 200220021,
    name = "「交给我吧。」"
  },
  [200220022] = {
    id = 200220022,
    name = "「拜托了。对了，这上面有我的味道，带上它，它不会攻击你。」"
  },
  [200220023] = {
    id = 200220023,
    name = "说着，探险者取下自己的手套递给你。"
  },
  [200220024] = {
    id = 200220024,
    name = "「我现在要去做的事十分紧急，很快就回来，我们待会儿就在这碰头！」"
  },
  [200220025] = {
    id = 200220025,
    name = "探险者说完就离开了。"
  },
  [200220026] = {
    id = 200220026,
    name = "你刚进入森林，就听见了麦克的叫声。"
  },
  [200220027] = {
    id = 200220027,
    name = "麦克正冲着一棵大树龇牙咧嘴，喉咙里发出咕噜咕噜的声音。"
  },
  [200220028] = {
    id = 200220028,
    name = "你正思考怎么接近它，旁边突然走出一个黑衣人。"
  },
  [200220029] = {
    id = 200220029,
    name = "他拄着一根法杖，看起来很疲惫，显然也是刚刚赶到这里。"
  },
  [200220030] = {
    id = 200220030,
    name = "「哼，你这条疯狗，终于找到你了！」"
  },
  [200220031] = {
    id = 200220031,
    name = "「竟然吃了我的晶石，我要把你剁成肉酱！」"
  },
  [200220032] = {
    id = 200220032,
    name = "麦克冲着黑衣人狂吠，龇牙咧嘴地与他对峙。"
  },
  [200220033] = {
    id = 200220033,
    name = "眼见黑衣人即将动手，你从暗处走了出来。"
  },
  [200220034] = {
    id = 200220034,
    name = "麦克看见你手上的手套，凑近闻了闻，站到了你身边。"
  },
  [200220035] = {
    id = 200220035,
    name = "见此一幕，黑衣人微微皱眉。"
  },
  [200220036] = {
    id = 200220036,
    name = "「你是它的主人？你的狗吞了我的东西，把它交出来吧！」"
  },
  [200220037] = {
    id = 200220037,
    name = "「这狗不是我的，所以不能交给你。」"
  },
  [200220038] = {
    id = 200220038,
    name = "「哼，满口胡言！」"
  },
  [200220039] = {
    id = 200220039,
    name = "黑衣人认为你在狡辩，话音刚落，便朝你发起攻击。"
  },
  [200220040] = {
    id = 200220040,
    name = "你后撤一步，摆开防御架势，将黑衣人第一波进攻化解，随即展开反击。"
  },
  [200220041] = {
    id = 200220041,
    name = "很快黑衣人落入下风，你抓住时机一脚将其踢翻在地。"
  },
  [200220042] = {
    id = 200220042,
    name = "「别，别杀我！晶石我不要了，狗我也不要了，都给你都给你！」"
  },
  [200220043] = {
    id = 200220043,
    name = "你并不想置他于死地，便挥了挥手，黑衣人见状连滚带爬地逃走了。"
  },
  [200220044] = {
    id = 200220044,
    name = "走之前还憎恨地看了你一眼。"
  },
  [200220045] = {
    id = 200220045,
    name = "「这只狗的肚子里有晶石？会不会就是它暴躁的原因？」"
  },
  [200220046] = {
    id = 200220046,
    name = "你安抚着麦克，使用魔法让它暂时安静下来，躺在地上。"
  },
  [200220047] = {
    id = 200220047,
    name = "通过探查，你发现麦克体内果然有一枚晶石。"
  },
  [200220048] = {
    id = 200220048,
    name = "费了些功夫，你终于将晶石从麦克体内取了出来。"
  },
  [200220049] = {
    id = 200220049,
    name = "待你解除魔法后，它也不像之前那般狂躁，只是恹恹地躺在地上。"
  },
  [200220050] = {
    id = 200220050,
    name = "你带着狗回到了约定的地方，发现探险者已经等在了那里。"
  },
  [200220051] = {
    id = 200220051,
    name = "「你找到它了，它怎么了？发生了什么？」"
  },
  [200220052] = {
    id = 200220052,
    name = "「我去的时候遇到了一个黑衣人，我从他手里夺回来的。」"
  },
  [200220053] = {
    id = 200220053,
    name = "「天哪，幸亏有你。这些是我的心意，请收下吧。」"
  },
  [200220054] = {
    id = 200220054,
    name = "为了感谢你救了麦克，探险者给了你一些金币作为报酬，随即一人一狗离开了。"
  },
  [200220055] = {
    id = 200220055,
    name = "你继续前行，在途中遭遇了一大群魔物。"
  },
  [200220056] = {
    id = 200220056,
    name = "你寡不敌众，逃跑时丢失了不少物资。"
  },
  [200220057] = {
    id = 200220057,
    name = "你把狗带回了约定的地方，那名探险者也刚好返回。"
  },
  [200220058] = {
    id = 200220058,
    name = "你将晶石和黑衣人相关的事告诉了他，他露出震惊的神色，随后拿出了一些金币酬谢你。"
  },
  [200220059] = {
    id = 200220059,
    name = "「原来是这样，回去我就把麦克肚子里的东西取出来。」"
  },
  [200220060] = {
    id = 200220060,
    name = "「真是太感谢你了！这些是我的心意，请收下。」"
  },
  [200220061] = {
    id = 200220061,
    name = "探险者带着他的狗离开了。你继续前行，在途中遭遇了一大群魔物。"
  },
  [200220062] = {
    id = 200220062,
    name = "你寡不敌众，败退而逃，损失了不少物资。"
  },
  [200220063] = {
    id = 200220063,
    name = "黑衣人见你仍不依不饶，急忙从身上掏出各种物资。"
  },
  [200220064] = {
    id = 200220064,
    name = "「我、我还有很多东西，都给你，都给你！」"
  },
  [200220065] = {
    id = 200220065,
    name = "他表现得非常慌乱，趁你看向那堆物资之际，突然一记魔法向你袭来。"
  },
  [200220066] = {
    id = 200220066,
    name = "你挡下了魔法，正准备还击，却见此人已消失了踪影。"
  },
  [200220067] = {
    id = 200220067,
    name = "你带着狗回到了约定的地方，发现探险者已经等在了那里。"
  },
  [200220068] = {
    id = 200220068,
    name = "「我看它的症状很奇怪，会不会是吃坏了什么东西。」"
  },
  [200220069] = {
    id = 200220069,
    name = "「或许是的，可怜的麦克，我回去就给它检查。」"
  },
  [200220070] = {
    id = 200220070,
    name = "「谢谢你帮我找回了它，这是我的心意，还请收下。」"
  },
  [200220071] = {
    id = 200220071,
    name = "探险者递给你一些金币作为谢礼。"
  },
  [200220072] = {
    id = 200220072,
    name = "「对了，我们收到消息，前面据点附近有大量魔物袭击的可能。」"
  },
  [200220073] = {
    id = 200220073,
    name = "「大家都已经准备撤离了，你也不要去那种危险的地方了。」"
  },
  [200220074] = {
    id = 200220074,
    name = "「知道了，谢谢提醒。」"
  },
  [200220075] = {
    id = 200220075,
    name = "等探险者离开后，你思考了一下，最终选择避开前方的据点，绕道而行。"
  },
  [200220076] = {
    id = 200220076,
    name = "你拒绝了他的请求，他犹豫了一会儿，然后向狗逃走的反方向跑去。"
  },
  [200220077] = {
    id = 200220077,
    name = "「到底是什么事，连“家人”也顾不上。」"
  },
  [200220078] = {
    id = 200220078,
    name = "你虽有些疑惑，但也不愿多管闲事，继续赶路。"
  },
  [200220079] = {
    id = 200220079,
    name = "没走多远，你在途中遭遇了一大群魔物。"
  },
  [200220080] = {
    id = 200220080,
    name = "你寡不敌众，败退而逃，损失了不少物资。"
  },
  [200220081] = {
    id = 200220081,
    name = "「你为什么要杀它？」"
  },
  [200220082] = {
    id = 200220082,
    name = "「这只狗抢走了我的东西，我现在要它还回来！」"
  },
  [200220083] = {
    id = 200220083,
    name = "「难怪他看起来很暴躁。」"
  },
  [200220084] = {
    id = 200220084,
    name = "「我有办法让它把你的东西吐出来。」"
  },
  [200220085] = {
    id = 200220085,
    name = "「也许没有必要杀它。」"
  },
  [200220086] = {
    id = 200220086,
    name = "「不，它必须死！」"
  },
  [200220087] = {
    id = 200220087,
    name = "「它是恶魔养的狗！」"
  },
  [200220088] = {
    id = 200220088,
    name = "「那些劫匪，装作探险者，把路过的人骗到前面的据点。」"
  },
  [200220089] = {
    id = 200220089,
    name = "「再引诱魔物袭击据点，坑害那些路人！」"
  },
  [200220090] = {
    id = 200220090,
    name = "「我和我的队友就是之前的受害者！」"
  },
  [200220091] = {
    id = 200220091,
    name = "「它肚子里的晶石就是我爱人的遗物！」"
  },
  [200220092] = {
    id = 200220092,
    name = "「这样吗？我刚才还看到了它的主人。」"
  },
  [200220093] = {
    id = 200220093,
    name = "「什么？！糟了，他们一定又开始计划袭击据点了！」"
  },
  [200220094] = {
    id = 200220094,
    name = "「我得马上去通知大家！」"
  },
  [200220095] = {
    id = 200220095,
    name = "「感谢你告诉我这些事！」"
  },
  [200220096] = {
    id = 200220096,
    name = "黑衣人说着递给你一袋金币，随即向着据点的方向狂奔而去。"
  },
  [200220097] = {
    id = 200220097,
    name = "「......」"
  },
  [200220098] = {
    id = 200220098,
    name = "你不想多管闲事，转身离开。"
  },
  [200220099] = {
    id = 200220099,
    name = "不多时，你再次看到了那条狗。"
  },
  [200220100] = {
    id = 200220100,
    name = "这次追赶它的不是先前的探险者，而是一名黑衣人。"
  },
  [200220101] = {
    id = 200220101,
    name = "「哼，这次看你往哪儿跑！」"
  },
  [200220102] = {
    id = 200220102,
    name = "「吃了我的晶石，我现在就把你宰了剁成肉酱！」"
  },
  [200220103] = {
    id = 200220103,
    name = "狗看到你出现在前方，匆忙掉头，正好被黑衣人抓了个正着。"
  },
  [200220104] = {
    id = 200220104,
    name = "「你死定了，疯狗！」"
  },
  [200220105] = {
    id = 200220105,
    name = "黑衣人打晕了狗，正打算动手。"
  },
  [200220106] = {
    id = 200220106,
    name = "他发现你看着他，露出了警惕的神色。"
  },
  [200220107] = {
    id = 200220107,
    name = "黑衣人见你并无敌意，遂干净利落地杀掉了那条狗，从狗肚子里取出一枚晶石。"
  },
  [200220108] = {
    id = 200220108,
    name = "你撇了撇嘴，继续向远处走去。"
  },
  [200230001] = {
    id = 200230001,
    name = "「唉，这可怎么办才好啊......」"
  },
  [200230002] = {
    id = 200230002,
    name = "途经树林的时候，你听到有人在叹息。"
  },
  [200230003] = {
    id = 200230003,
    name = "「发生什么事了？」"
  },
  [200230004] = {
    id = 200230004,
    name = "你走到那人面前，发现他捧着一本书，一脸忧愁。"
  },
  [200230005] = {
    id = 200230005,
    name = "「啊，抱歉，被你听到了啊。」"
  },
  [200230006] = {
    id = 200230006,
    name = "「我是一名魔物学者，最近正在修正一本魔物笔记。」"
  },
  [200230007] = {
    id = 200230007,
    name = "「但前两天，我遇到了一头恐怖的魔物。」"
  },
  [200230008] = {
    id = 200230008,
    name = "「逃跑途中，不小心丢失了那本笔记。」"
  },
  [200230009] = {
    id = 200230009,
    name = "「啊，到底该怎么办啊......」"
  },
  [200230010] = {
    id = 200230010,
    name = "「那本笔记对你很重要吗？」"
  },
  [200230011] = {
    id = 200230011,
    name = "「当然！那上面记载着许多魔物信息，是我的心血。」"
  },
  [200230012] = {
    id = 200230012,
    name = "「这样啊。」"
  },
  [200230013] = {
    id = 200230013,
    name = "「既然如此重要，那我帮你找回来吧。」"
  },
  [200230014] = {
    id = 200230014,
    name = "「唉，难道真的只能拜托那家伙吗......啊！等等，你说你愿意帮忙？！真、真的吗？！」"
  },
  [200230015] = {
    id = 200230015,
    name = "「这怎么好意思呢。」"
  },
  [200230016] = {
    id = 200230016,
    name = "学者有些不好意思地看着你，见你再次点头，瞬间扑倒在你面前。"
  },
  [200230017] = {
    id = 200230017,
    name = "「那就拜托你了！」"
  },
  [200230018] = {
    id = 200230018,
    name = "「那样珍贵的宝物，要是就这样丢失了，对我而言，不，对人类来说都是巨大的损失！」"
  },
  [200230019] = {
    id = 200230019,
    name = "「......」"
  },
  [200230020] = {
    id = 200230020,
    name = "「抱歉，没想到你愿意帮助我，我实在太激动了。」"
  },
  [200230021] = {
    id = 200230021,
    name = "「笔记就丢失在后面的山上，那里有非常强大的魔物出没，请务必小心。」"
  },
  [200230022] = {
    id = 200230022,
    name = "你走上山，发现你曾经来过这里，那时这里活跃的还都只是一些普通魔物。"
  },
  [200230023] = {
    id = 200230023,
    name = "但按那位学者所说，现在这里竟然出现了实力强大的魔物......"
  },
  [200230024] = {
    id = 200230024,
    name = "你思考了一阵，决定——"
  },
  [200230025] = {
    id = 200230025,
    name = "为了尽快找回笔记，你决定采用最简单的方式——按学者逃跑路线寻找。"
  },
  [200230026] = {
    id = 200230026,
    name = "这样虽然可能遇到较多魔物，但无疑是最有效率的选择。"
  },
  [200230027] = {
    id = 200230027,
    name = "一头、两头......一波、两波......你将沿途遇到的魔物悉数击败。"
  },
  [200230028] = {
    id = 200230028,
    name = "魔物的数量超乎你的预料，正当你思考是否该转换策略时，四周突然陷入死寂。"
  },
  [200230029] = {
    id = 200230029,
    name = "你的直觉告诉你，强敌出现了。"
  },
  [200230030] = {
    id = 200230030,
    name = "吼——！"
  },
  [200230031] = {
    id = 200230031,
    name = "伴随一声咆哮，一头巨大的魔物出现在离你不远的巨石上，居高临下地盯着你。"
  },
  [200230032] = {
    id = 200230032,
    name = "你见此情景，原地站定，体内魔力快速运转，随时准备出手。"
  },
  [200230033] = {
    id = 200230033,
    name = "魔物似乎也看出你有些实力，目光变得愈发凌厉，并未轻易发起进攻。"
  },
  [200230034] = {
    id = 200230034,
    name = "一人一魔物静静对峙，犹如两尊雕像。"
  },
  [200230035] = {
    id = 200230035,
    name = "呼~一阵大风吹来，卷起地面的落叶，就在叶片腾空瞬间，你和魔物同时出手。"
  },
  [200230036] = {id = 200230036, name = "轰——"},
  [200230037] = {
    id = 200230037,
    name = "你知道这次的敌人不同寻常，不敢有任何保留，而那魔物也感受到了危机，攻击变得愈发猛烈。"
  },
  [200230038] = {
    id = 200230038,
    name = "一次又一次的碰撞，一波又一波的爆炸，山道周围的草木被你们的战斗波及，毁坏无数。"
  },
  [200230039] = {
    id = 200230039,
    name = "轰——！！！"
  },
  [200230040] = {
    id = 200230040,
    name = "伴随着最后一声巨响，你踉跄着稳住了身形，巨大的魔物倒在了你的身后。"
  },
  [200230041] = {
    id = 200230041,
    name = "「呼，真是一场恶战啊。」"
  },
  [200230042] = {
    id = 200230042,
    name = "你休息了片刻，才继续动身搜寻，终于在一棵树下发现一本笔记。"
  },
  [200230043] = {
    id = 200230043,
    name = "你把笔记带回给了学者。"
  },
  [200230044] = {
    id = 200230044,
    name = "「天哪，你真的拿回了我的笔记！」"
  },
  [200230045] = {
    id = 200230045,
    name = "「让我看看，噢！可恶，那家伙果然......」"
  },
  [200230046] = {
    id = 200230046,
    name = "「有什么不对吗？」"
  },
  [200230047] = {
    id = 200230047,
    name = "「喔，没有，对不起，我太激动了。」"
  },
  [200230048] = {
    id = 200230048,
    name = "「是这个没错，这就是我的笔记，实在太感谢你了！」"
  },
  [200230049] = {
    id = 200230049,
    name = "「对了，我还丢了一些其他东西，那里除了这本笔记，还有别的吗？」"
  },
  [200230050] = {
    id = 200230050,
    name = "你将过程告诉了学者。"
  },
  [200230051] = {
    id = 200230051,
    name = "「这样啊......我知道了。」"
  },
  [200230052] = {
    id = 200230052,
    name = "「我还是先回据点去好好研究......哦不，补全这本笔记。」"
  },
  [200230053] = {
    id = 200230053,
    name = "「那报酬方面？」"
  },
  [200230054] = {
    id = 200230054,
    name = "「什么，你想要报酬？」"
  },
  [200230055] = {
    id = 200230055,
    name = "「可是你自愿帮我的，不是吗？」"
  },
  [200230056] = {
    id = 200230056,
    name = "「不说了，我先走了。」"
  },
  [200230057] = {
    id = 200230057,
    name = "学者拿着笔记快步离开了。"
  },
  [200230058] = {
    id = 200230058,
    name = "「......」"
  },
  [200230059] = {
    id = 200230059,
    name = "你打开了笔记。"
  },
  [200230060] = {
    id = 200230060,
    name = "笔记内并无任何有关于魔物的记录，更像是某人的日记。"
  },
  [200230061] = {
    id = 200230061,
    name = "每一页都倾诉着作者对一名叫做凯蒂的女孩的爱意。"
  },
  [200230062] = {
    id = 200230062,
    name = "「日记？情书？」"
  },
  [200230063] = {
    id = 200230063,
    name = "你大概翻看了一下，发现竟然还是暗恋，里面详细记录了作者与那名女孩的点点滴滴。"
  },
  [200230064] = {
    id = 200230064,
    name = "「唔，还真是浪漫，也有够肉麻......」"
  },
  [200230065] = {
    id = 200230065,
    name = "你又在周围找了一番，但并无任何发现，便沿原路返回与学者会合。"
  },
  [200230066] = {
    id = 200230066,
    name = "「抱歉，我没找到你的笔记。」"
  },
  [200230067] = {
    id = 200230067,
    name = "「难道已经被魔物撕碎了？哦，不......」"
  },
  [200230068] = {
    id = 200230068,
    name = "「不过我发现一本日记。」"
  },
  [200230069] = {
    id = 200230069,
    name = "「日记？」"
  },
  [200230070] = {
    id = 200230070,
    name = "你拿出了捡到的日记。"
  },
  [200230071] = {
    id = 200230071,
    name = "「只是一本有关爱情的日记。」"
  },
  [200230072] = {
    id = 200230072,
    name = "「这个，好眼熟......等等，让我看看！」"
  },
  [200230073] = {
    id = 200230073,
    name = "「这是你的？」"
  },
  [200230074] = {
    id = 200230074,
    name = "「不是我的，但是——」"
  },
  [200230075] = {
    id = 200230075,
    name = "「那就不能给你了。」"
  },
  [200230076] = {
    id = 200230076,
    name = "「不，虽然不是我的，但这是我朋友的。」"
  },
  [200230077] = {
    id = 200230077,
    name = "「没想到他竟然把这个弄丢了。」"
  },
  [200230078] = {
    id = 200230078,
    name = "「哦对了，他的名字叫伊文。」"
  },
  [200230079] = {
    id = 200230079,
    name = "你看了下，笔记第一页的确写着伊文，于是你将日记交给了学者。"
  },
  [200230080] = {
    id = 200230080,
    name = "「让我看看他写了些什......什么！」"
  },
  [200230081] = {
    id = 200230081,
    name = "「这个家伙果然喜欢凯蒂！」"
  },
  [200230082] = {
    id = 200230082,
    name = "「可恶的家伙，居然和我争夺喜欢的女孩，凯蒂绝对不会喜欢他的！」"
  },
  [200230083] = {
    id = 200230083,
    name = "「哦，那你要加油。」"
  },
  [200230084] = {
    id = 200230084,
    name = "「当然！我可是......不对，谁、谁说我也喜欢凯蒂了。」"
  },
  [200230085] = {
    id = 200230085,
    name = "「哼，既然伊文也去过山上，说不定发现了我的笔记，我得去找他。」"
  },
  [200230086] = {
    id = 200230086,
    name = "「顺便警告他，让他赶紧打消这份心思！」"
  },
  [200230087] = {
    id = 200230087,
    name = "学者抱着笔记快步离开了。"
  },
  [200230088] = {
    id = 200230088,
    name = "「......」"
  },
  [200230089] = {
    id = 200230089,
    name = "你觉得自己只是来找笔记的，没必要浪费精力对付那些强大的魔物。"
  },
  [200230090] = {
    id = 200230090,
    name = "于是你收敛了气息，沿着小路绕行，尽量避免和魔物相遇。"
  },
  [200230091] = {
    id = 200230091,
    name = "终于，你在一棵大树下发现了笔记，但旁边正酣睡着一头巨大的魔物。"
  },
  [200230092] = {
    id = 200230092,
    name = "为了不惊扰魔物，你将自己的气息收敛至极限，同时屏住呼吸，蹑手蹑脚地行动。"
  },
  [200230093] = {
    id = 200230093,
    name = "终于你拿到了笔记，随即全身蓄力，嗖的一声窜了出去。"
  },
  [200230094] = {
    id = 200230094,
    name = "突然的动静惊醒了魔物，但它只是睁眼看了看，没有感受危险后又闭眼睡去......"
  },
  [200230095] = {
    id = 200230095,
    name = "下山路上，你遇见了一名探险者。"
  },
  [200230096] = {
    id = 200230096,
    name = "他见到你手中的笔记，露出惊喜的神色。"
  },
  [200230097] = {
    id = 200230097,
    name = "「你，你捡到了我的日记！我还以为它丢了！」"
  },
  [200230098] = {
    id = 200230098,
    name = "「太好了，我找了它好久......你，你没有看过这本日记吧？」"
  },
  [200230099] = {
    id = 200230099,
    name = "「这是你的日记？」"
  },
  [200230100] = {
    id = 200230100,
    name = "「当然，我叫伊文，这本日记上有我的名字！」"
  },
  [200230101] = {
    id = 200230101,
    name = "你翻开了笔记，发现第一页确实写着伊文的名字。"
  },
  [200230102] = {
    id = 200230102,
    name = "当你准备再往下翻阅时，伊文阻止了你。"
  },
  [200230103] = {
    id = 200230103,
    name = "「我说的没错吧，这本就是我的东西，请还给我吧。」"
  },
  [200230104] = {
    id = 200230104,
    name = "「......」"
  },
  [200230105] = {
    id = 200230105,
    name = "「那你说说日记的内容，我需要确认这是你的笔记。」"
  },
  [200230106] = {
    id = 200230106,
    name = "「你看过这本日记了？！」"
  },
  [200230107] = {
    id = 200230107,
    name = "「你，你这个混蛋，竟然偷窥别人的隐私！快把日记还给我！」"
  },
  [200230108] = {
    id = 200230108,
    name = "探险者突然伸手抢你手中的笔记，你灵活闪过，反手制住了他。"
  },
  [200230109] = {
    id = 200230109,
    name = "探险者羞愤难当，脸涨得通红。"
  },
  [200230110] = {
    id = 200230110,
    name = "「你这个下流无耻的家伙，你是不是也喜欢凯蒂？！」"
  },
  [200230111] = {
    id = 200230111,
    name = "探险者语无伦次地骂着，你觉得你们好像有些误会，于是你翻阅起了那本笔记。"
  },
  [200230112] = {
    id = 200230112,
    name = "「......如果失去你，就如同夜晚失去月亮，白天失去太阳......」"
  },
  [200230113] = {
    id = 200230113,
    name = "「你的脸颊，就如同被美神雕刻过，每一刻都闪耀着光芒，照在我心上......」"
  },
  [200230114] = {
    id = 200230114,
    name = "「一天不在你身旁，我就失去了前行的力量......」"
  },
  [200230115] = {
    id = 200230115,
    name = "「啊，原来是情书啊，看来是我误会了。」"
  },
  [200230116] = {
    id = 200230116,
    name = "「啊啊啊啊！太、太羞耻了啊！」"
  },
  [200230117] = {
    id = 200230117,
    name = "「你写的时候不觉得羞耻吗？」"
  },
  [200230118] = {
    id = 200230118,
    name = "你把笔记还给了他，他抱在怀里视若珍宝，突然想到了什么。"
  },
  [200230119] = {
    id = 200230119,
    name = "「对了，你从哪里找到这本日记的？」"
  },
  [200230120] = {
    id = 200230120,
    name = "「实际上......」"
  },
  [200230121] = {
    id = 200230121,
    name = "你将所有的事告诉了他。"
  },
  [200230122] = {
    id = 200230122,
    name = "「原来是他啊，他的魔物笔记在我这里。」"
  },
  [200230123] = {
    id = 200230123,
    name = "「我每天都会来这边修行。」"
  },
  [200230124] = {
    id = 200230124,
    name = "「不过这段时间突然出现了一头实力强大的魔物，弄得大家都措手不及。」"
  },
  [200230125] = {
    id = 200230125,
    name = "「那家伙的笔记也弄丢了，一定十分烦恼吧。」"
  },
  [200230126] = {
    id = 200230126,
    name = "「没想到我找回了他的笔记，却把自己的日记弄丢了。」"
  },
  [200230127] = {
    id = 200230127,
    name = "「......」"
  },
  [200230128] = {
    id = 200230128,
    name = "「怎么了？」"
  },
  [200230129] = {
    id = 200230129,
    name = "「我把他的笔记给你，关于这本日记的事，你可不能告诉他。」"
  },
  [200230130] = {
    id = 200230130,
    name = "「我没有传播别人隐私的爱好。」"
  },
  [200230131] = {
    id = 200230131,
    name = "「哼，要是被我知道了，我不会放过你的。」"
  },
  [200230132] = {
    id = 200230132,
    name = "探险者红着脸说着恶狠狠的话，把笔记丢给你后便跑开了，但没几步又停了下来。"
  },
  [200230133] = {
    id = 200230133,
    name = "「对了，我才不是特意去帮他找笔记的，别跟那家伙说见过我！」"
  },
  [200230134] = {
    id = 200230134,
    name = "你把笔记带回给了学者。"
  },
  [200230135] = {
    id = 200230135,
    name = "「天哪，你真的拿回了我的笔记！」"
  },
  [200230136] = {
    id = 200230136,
    name = "「让我看看，太棒了！」"
  },
  [200230137] = {
    id = 200230137,
    name = "「如果没有它，我都不知道未来该怎样活下去。」"
  },
  [200230138] = {
    id = 200230138,
    name = "「托你的福，真的非常感谢你，这是我的一点心意，还请收下！」"
  },
  [200230139] = {
    id = 200230139,
    name = "学者说着掏出一堆物资递给你。"
  },
  [200230140] = {
    id = 200230140,
    name = "（要不要告诉他伊文的事呢？）"
  },
  [200230141] = {
    id = 200230141,
    name = "「我得先回据点去，好好补全这本笔记，有缘再见。」"
  },
  [200230142] = {
    id = 200230142,
    name = "学者抱着笔记快步离开了。"
  },
  [200230143] = {
    id = 200230143,
    name = "「我上山的时候，正好看到一名探险者，是他捡到了你的笔记。」"
  },
  [200230144] = {
    id = 200230144,
    name = "「他知道我的目的后，就让我把笔记带给你。」"
  },
  [200230145] = {
    id = 200230145,
    name = "「哦！你说的是伊文啊！」"
  },
  [200230146] = {
    id = 200230146,
    name = "「那是我的队友，经常独自去山里修行。」"
  },
  [200230147] = {
    id = 200230147,
    name = "「我都警告过他山里出现了一头强大的魔物，连我的笔记都丢在那里，让他今后别去了。」"
  },
  [200230148] = {
    id = 200230148,
    name = "「没想到他竟然还去......真是让人担忧啊。」"
  },
  [200230149] = {
    id = 200230149,
    name = "「你们的关系真好啊。」"
  },
  [200230150] = {
    id = 200230150,
    name = "「唔......那个，十分感谢你告诉了我这件事，我先告辞了。」"
  },
  [200230151] = {
    id = 200230151,
    name = "学者抱着笔记快步离开了。"
  },
  [200230152] = {
    id = 200230152,
    name = "（原来是挚友啊，真好。）"
  },
  [200230153] = {
    id = 200230153,
    name = "「你也太倒霉了吧。」"
  },
  [200230154] = {
    id = 200230154,
    name = "「是啊......难道真的要找那个家伙帮忙吗？」"
  },
  [200230155] = {
    id = 200230155,
    name = "「找那个可能喜欢凯蒂的家伙，真是......不甘心啊......」"
  },
  [200230156] = {
    id = 200230156,
    name = "学者一脸沮丧地离开了。"
  },
  [200240001] = {
    id = 200240001,
    name = "「快看那边！」"
  },
  [200240002] = {
    id = 200240002,
    name = "「啊，是漂亮的魔法，哇！」"
  },
  [200240003] = {
    id = 200240003,
    name = "「哈哈哈，我赢了！」"
  },
  [200240004] = {
    id = 200240004,
    name = "「嗯嗯，你赢啦！」"
  },
  [200240005] = {
    id = 200240005,
    name = "「笨蛋！」"
  },
  [200240006] = {
    id = 200240006,
    name = "前方一高一矮两个少年，正在追逐打闹。"
  },
  [200240007] = {
    id = 200240007,
    name = "「你们在做什么？」"
  },
  [200240008] = {
    id = 200240008,
    name = "「我们在玩游戏啦。」"
  },
  [200240009] = {
    id = 200240009,
    name = "「没错，这个游戏很好玩。」"
  },
  [200240010] = {
    id = 200240010,
    name = "「我们再来一次吧。」"
  },
  [200240011] = {
    id = 200240011,
    name = "「好呀好呀。」"
  },
  [200240012] = {
    id = 200240012,
    name = "「看着，一会儿我会抛两个东西出去，他喜欢哪个就去捡哪个。」"
  },
  [200240013] = {
    id = 200240013,
    name = "「只要在规定时间内拿到东西，东西就归他了。」"
  },
  [200240014] = {
    id = 200240014,
    name = "「当然，我会在路上设置障碍，想拿到东西，可没那么容易。」"
  },
  [200240015] = {
    id = 200240015,
    name = "「预备，开始！」"
  },
  [200240016] = {
    id = 200240016,
    name = "高个子少年高高抛出了两样东西。"
  },
  [200240017] = {
    id = 200240017,
    name = "一个是金币，而另一个则是廉价的魔物硬壳。"
  },
  [200240018] = {
    id = 200240018,
    name = "高个子少年施放魔法，裹挟着两件物品向远处飘去。"
  },
  [200240019] = {
    id = 200240019,
    name = "矮个子少年看了一眼，飞快地朝魔物硬壳冲了过去。"
  },
  [200240020] = {
    id = 200240020,
    name = "「哈哈哈，陷阱来了！」"
  },
  [200240021] = {
    id = 200240021,
    name = "「呜哇啊......」"
  },
  [200240022] = {
    id = 200240022,
    name = "「接着是火圈和落石！」"
  },
  [200240023] = {
    id = 200240023,
    name = "高个子少年施展着不痛不痒的基础魔法，不断地给矮个子少年制造障碍。"
  },
  [200240024] = {
    id = 200240024,
    name = "不过最终矮个子少年顺利地拿到了魔物硬壳。"
  },
  [200240025] = {
    id = 200240025,
    name = "「哈哈，你看他是不是很好笑！」"
  },
  [200240026] = {
    id = 200240026,
    name = "「这样也太过分了。」"
  },
  [200240027] = {
    id = 200240027,
    name = "「嘁，是他自愿跟我玩的。」"
  },
  [200240028] = {
    id = 200240028,
    name = "「我可没有强迫他。」"
  },
  [200240029] = {
    id = 200240029,
    name = "「喂，你要不要再玩一次啊？」"
  },
  [200240030] = {
    id = 200240030,
    name = "「好啊，好啊！」"
  },
  [200240031] = {
    id = 200240031,
    name = "「你看，是他自己想玩。」"
  },
  [200240032] = {
    id = 200240032,
    name = "「我是在陪他玩呢。」"
  },
  [200240033] = {
    id = 200240033,
    name = "「......」"
  },
  [200240034] = {
    id = 200240034,
    name = "「这一次，我们玩点大的怎么样？」"
  },
  [200240035] = {
    id = 200240035,
    name = "「我就不扔金币了，只扔魔物硬壳，只是障碍会变得更多哦。」"
  },
  [200240036] = {
    id = 200240036,
    name = "「有没有兴趣啊？」"
  },
  [200240037] = {
    id = 200240037,
    name = "「没问题！」"
  },
  [200240038] = {
    id = 200240038,
    name = "「那么，开始了！」"
  },
  [200240039] = {
    id = 200240039,
    name = "高个子少年把魔物硬壳扔了出去。"
  },
  [200240040] = {
    id = 200240040,
    name = "魔法包裹着魔物硬壳，飘向远方。"
  },
  [200240041] = {
    id = 200240041,
    name = "矮个子少年毫不犹豫地冲了出去。"
  },
  [200240042] = {
    id = 200240042,
    name = "高个子少年开始挥动魔法，比先前那次更加频繁。"
  },
  [200240043] = {
    id = 200240043,
    name = "矮个子少年艰难地躲避，还是被火球烧掉了一些头发。"
  },
  [200240044] = {
    id = 200240044,
    name = "「哈哈哈哈太好玩了，是我玩过最好玩的捡垃圾游戏！」"
  },
  [200240045] = {
    id = 200240045,
    name = "矮个子少年终于到达了终点，接下了那块魔物硬壳。"
  },
  [200240046] = {
    id = 200240046,
    name = "他回头朝你们笑了笑。"
  },
  [200240047] = {
    id = 200240047,
    name = "「......」"
  },
  [200240048] = {
    id = 200240048,
    name = "「真是......没救了。」"
  },
  [200240049] = {
    id = 200240049,
    name = "「哈哈，他就这样笨笨的也不错嘛。」"
  },
  [200240050] = {
    id = 200240050,
    name = "「至少不让人讨厌。」"
  },
  [200240051] = {
    id = 200240051,
    name = "「讨厌？」"
  },
  [200240052] = {
    id = 200240052,
    name = "「就算再笨的人，也知道金币和垃圾的价值。」"
  },
  [200240053] = {
    id = 200240053,
    name = "「他故意选择垃圾，我也就勉为其难配合一下啦。」"
  },
  [200240054] = {
    id = 200240054,
    name = "「反正我也不需要这些垃圾，用来取乐也是不错的。」"
  },
  [200240055] = {
    id = 200240055,
    name = "「他以为我不知道他把那些垃圾拿去卖掉换取食物。」"
  },
  [200240056] = {
    id = 200240056,
    name = "「实际上那些不过是我对他的施舍而已。」"
  },
  [200240057] = {
    id = 200240057,
    name = "「呼......呼，我拿到了！」"
  },
  [200240058] = {
    id = 200240058,
    name = "「嗯，干得不错。」"
  },
  [200240059] = {
    id = 200240059,
    name = "「这块魔物硬壳也是你的了。」"
  },
  [200240060] = {
    id = 200240060,
    name = "「它实在是太帅了！」"
  },
  [200240061] = {
    id = 200240061,
    name = "「今天的游戏就到此为止吧，你也该回去了。」"
  },
  [200240062] = {
    id = 200240062,
    name = "「嗯嗯！」"
  },
  [200240063] = {
    id = 200240063,
    name = "矮个子少年蹦蹦跳跳地离开了。"
  },
  [200240064] = {
    id = 200240064,
    name = "「看吧，这么容易就满足了，这样的人，真是蠢得不可救药。」"
  },
  [200240065] = {
    id = 200240065,
    name = "「......为什么不告诉他？」"
  },
  [200240066] = {
    id = 200240066,
    name = "「因为我觉得有趣啊，他要是改变了，那多没意思。」"
  },
  [200240067] = {
    id = 200240067,
    name = "「不跟你说了，我要去修行了。」"
  },
  [200240068] = {
    id = 200240068,
    name = "「我还得去猎杀更多的魔物来‘逗宠物’，好好享受游戏呢。」"
  },
  [200240069] = {
    id = 200240069,
    name = "「......」"
  },
  [200240070] = {
    id = 200240070,
    name = "「欺负一个弱者，很有趣吗？」"
  },
  [200240071] = {
    id = 200240071,
    name = "「哈哈，你懂什么？你以为他什么都不知道吗？」"
  },
  [200240072] = {
    id = 200240072,
    name = "「什么意思？」"
  },
  [200240073] = {
    id = 200240073,
    name = "「当然是他自愿的。」"
  },
  [200240074] = {
    id = 200240074,
    name = "「就算再笨的人，也知道金币和垃圾的价值。」"
  },
  [200240075] = {
    id = 200240075,
    name = "「他自愿被我欺负，因为我可以给他魔物硬壳。」"
  },
  [200240076] = {
    id = 200240076,
    name = "「这样，他就能靠那些垃圾换取食物，填饱肚子。」"
  },
  [200240077] = {
    id = 200240077,
    name = "「对他这样的人来说，就算是这些垃圾，也不可能轻易获得。」"
  },
  [200240078] = {
    id = 200240078,
    name = "「想要生存，就要付出代价。」"
  },
  [200240079] = {
    id = 200240079,
    name = "「于我们而言，是与魔物厮杀拼命。」"
  },
  [200240080] = {
    id = 200240080,
    name = "「对他而言，只需要被我当作宠物取乐而已，不是更轻松吗？」"
  },
  [200240081] = {
    id = 200240081,
    name = "「但你这样做，是践踏他的尊严。」"
  },
  [200240082] = {
    id = 200240082,
    name = "「尊严？哈哈哈，这位先生，尊严能让他活下去吗？」"
  },
  [200240083] = {
    id = 200240083,
    name = "「呼......呼，我拿到了！」"
  },
  [200240084] = {
    id = 200240084,
    name = "「嗯，干得不错。」"
  },
  [200240085] = {
    id = 200240085,
    name = "「这块魔物硬壳也是你的了。」"
  },
  [200240086] = {
    id = 200240086,
    name = "「它实在是太帅了！」"
  },
  [200240087] = {
    id = 200240087,
    name = "「喂，这位先生说，让你别再捡垃圾了，他觉得这种游戏会让你失去尊严。」"
  },
  [200240088] = {
    id = 200240088,
    name = "「！！！」"
  },
  [200240089] = {
    id = 200240089,
    name = "「什、什么意思......我不太懂你们说的......」"
  },
  [200240090] = {
    id = 200240090,
    name = "「哈哈哈，是吧？对弱小的人来说，能不被强者抛弃就不错了，只要能被庇护，尊严算什么？」"
  },
  [200240091] = {
    id = 200240091,
    name = "「嗯，我......我先回去了。」"
  },
  [200240092] = {
    id = 200240092,
    name = "矮个子少年匆忙离开，似乎在逃避什么。"
  },
  [200240093] = {
    id = 200240093,
    name = "「看吧，这就是他的答案。」"
  },
  [200240094] = {
    id = 200240094,
    name = "「愚蠢的家伙。」"
  },
  [200240095] = {
    id = 200240095,
    name = "「不跟你说了，我要继续去修行了，到时候再搞些更有趣的东西回来玩。」"
  },
  [200240096] = {
    id = 200240096,
    name = "高个子少年也慢悠悠地离开了。"
  },
  [200240097] = {
    id = 200240097,
    name = "（......尊严与生存吗？）"
  },
  [200240098] = {
    id = 200240098,
    name = "「他为什么不选金币？」"
  },
  [200240099] = {
    id = 200240099,
    name = "「因为他笨啊，哈哈！」"
  },
  [200240100] = {
    id = 200240100,
    name = "「呼，呼，我回来了。」"
  },
  [200240101] = {
    id = 200240101,
    name = "「我太喜欢这个硬壳了。」"
  },
  [200240102] = {
    id = 200240102,
    name = "「你不喜欢这个金币吗？」"
  },
  [200240103] = {
    id = 200240103,
    name = "「它闪闪发光的诶。」"
  },
  [200240104] = {
    id = 200240104,
    name = "「不，还是硬壳更好看。」"
  },
  [200240105] = {
    id = 200240105,
    name = "「你看，他是不是很奇怪？」"
  },
  [200240106] = {
    id = 200240106,
    name = "「竟然会有人喜欢这种不值钱的垃圾。」"
  },
  [200240107] = {
    id = 200240107,
    name = "「哈哈！」"
  },
  [200240108] = {
    id = 200240108,
    name = "「等我下次猎到魔物的时候，再来和你玩游戏啊。」"
  },
  [200240109] = {
    id = 200240109,
    name = "「好啊，实在太期待了。」"
  },
  [200240110] = {
    id = 200240110,
    name = "「哈哈，笨蛋。」"
  },
  [200240111] = {
    id = 200240111,
    name = "高个子少年离开了。"
  },
  [200240112] = {
    id = 200240112,
    name = "「......」"
  },
  [200240113] = {
    id = 200240113,
    name = "「我说，你知道那是金币吧？」"
  },
  [200240114] = {
    id = 200240114,
    name = "「嗯？」"
  },
  [200240115] = {
    id = 200240115,
    name = "「我当然知道啊，可我真的喜欢这个硬壳，看上去太酷了。」"
  },
  [200240116] = {
    id = 200240116,
    name = "「你有魔物硬壳的话，我们也能一起做游戏哦。」"
  },
  [200240117] = {
    id = 200240117,
    name = "（......）"
  },
  [200240118] = {
    id = 200240118,
    name = "「那我们也来玩游戏吧。」"
  },
  [200240119] = {
    id = 200240119,
    name = "你拿出了一枚金币和一块魔物材料。"
  },
  [200240120] = {
    id = 200240120,
    name = "矮个子少年眼睛一亮，露出笑容。"
  },
  [200240121] = {
    id = 200240121,
    name = "「我准备好了。」"
  },
  [200240122] = {
    id = 200240122,
    name = "你和高个子少年做出了同样的动作，把两件物品抛向了两个方向。"
  },
  [200240123] = {
    id = 200240123,
    name = "他看了一眼，意外地跑向了金币。"
  },
  [200240124] = {
    id = 200240124,
    name = "「......」"
  },
  [200240125] = {
    id = 200240125,
    name = "「呼，我拿到了！」"
  },
  [200240126] = {
    id = 200240126,
    name = "你没有设置障碍为难他，他很快就拿回了那枚金币。"
  },
  [200240127] = {
    id = 200240127,
    name = "「怎么这次你选择拿金币了？」"
  },
  [200240128] = {
    id = 200240128,
    name = "「因为，因为这一次的金币更好看啊！」"
  },
  [200240129] = {
    id = 200240129,
    name = "「按照游戏规则，这枚金币归我了是吧？」"
  },
  [200240130] = {
    id = 200240130,
    name = "「当然不是。」"
  },
  [200240131] = {
    id = 200240131,
    name = "你拿回了那枚金币。"
  },
  [200240132] = {
    id = 200240132,
    name = "「我才是制定游戏规则的人。」"
  },
  [200240133] = {
    id = 200240133,
    name = "「我可没说你拿到金币，金币就是你的。」"
  },
  [200240134] = {
    id = 200240134,
    name = "「你这个人，实在太恶劣了！」"
  },
  [200240135] = {
    id = 200240135,
    name = "「可没有你这个小骗子恶劣哦。」"
  },
  [200240136] = {
    id = 200240136,
    name = "「你......你在说什么？」"
  },
  [200240137] = {
    id = 200240137,
    name = "「你是故意的吧？」"
  },
  [200240138] = {
    id = 200240138,
    name = "「面对刚刚那种经常找你玩的人，就故意选择魔物硬壳。」"
  },
  [200240139] = {
    id = 200240139,
    name = "「对我这种只是好奇，玩一次就离开的人，你就选择金币。」"
  },
  [200240140] = {
    id = 200240140,
    name = "「看来你知道什么东西更有价值啊。」"
  },
  [200240141] = {
    id = 200240141,
    name = "「不，不是你想的这样！」"
  },
  [200240142] = {
    id = 200240142,
    name = "「我、我也只是为了生存而已。」"
  },
  [200240143] = {
    id = 200240143,
    name = "「如果我在他面前选了金币，他以后就不会跟我一起玩了。」"
  },
  [200240144] = {
    id = 200240144,
    name = "「到时候我连魔物硬壳都得不到，我会饿死的。」"
  },
  [200240145] = {
    id = 200240145,
    name = "「我不要这枚金币了，你别告诉别人这件事，求你了。」"
  },
  [200240146] = {
    id = 200240146,
    name = "「......真可怜啊。」"
  },
  [200240147] = {
    id = 200240147,
    name = "你把金币还给了他。"
  },
  [200240148] = {
    id = 200240148,
    name = "像他这样没有什么战斗力的人，在深渊里很难存活下去吧。"
  },
  [200240149] = {
    id = 200240149,
    name = "「那就悄悄地，别被人瞧见了。」"
  },
  [200240150] = {
    id = 200240150,
    name = "「嗯！你真是个好人。」"
  },
  [200240151] = {
    id = 200240151,
    name = "「对了，这个给你。」"
  },
  [200240152] = {
    id = 200240152,
    name = "少年偷偷塞给你一件素材。"
  },
  [200240153] = {
    id = 200240153,
    name = "「这是有个老家伙跟我玩游戏时给我的，我感觉和那些硬壳不一样，所以就收藏了起来，没有卖掉。」"
  },
  [200240154] = {
    id = 200240154,
    name = "「我得走了，这个金币可不能让他们看见。」"
  },
  [200240155] = {
    id = 200240155,
    name = "矮个子少年跑开了。"
  },
  [200240156] = {
    id = 200240156,
    name = "「......」"
  },
  [200240157] = {
    id = 200240157,
    name = "「不让你这个小骗子吃点苦头，以后你会成为大骗子。」"
  },
  [200240158] = {
    id = 200240158,
    name = "「那你想怎么样？」"
  },
  [200240159] = {
    id = 200240159,
    name = "「我家里还有很多这种魔物硬壳，我都给你。」"
  },
  [200240160] = {
    id = 200240160,
    name = "「你卖给材料商就能赚一笔，然后拿着作为封口费怎么样？」"
  },
  [200240161] = {
    id = 200240161,
    name = "「你没有想过别的生存方式吗？」"
  },
  [200240162] = {
    id = 200240162,
    name = "「想过啊，可是......我得先活下来，才有机会选择其他的生存方式。」"
  },
  [200240163] = {
    id = 200240163,
    name = "「我家里那些硬壳，就是为了学习技艺积攒的，为了换取学费。」"
  },
  [200240164] = {
    id = 200240164,
    name = "「而且我也知道这种游戏长久不了，不管我怎样表演，他们也总有腻的那天。」"
  },
  [200240165] = {
    id = 200240165,
    name = "「所以我得抓紧时间，还差一点，我就能攒够了。」"
  },
  [200240166] = {
    id = 200240166,
    name = "「......」"
  },
  [200240167] = {
    id = 200240167,
    name = "「......我想成为一名猎影！」"
  },
  [200240168] = {
    id = 200240168,
    name = "「呵呵，是不是太异想天开了？其实我知道自己没什么天赋，体格也不行——」"
  },
  [200240169] = {
    id = 200240169,
    name = "「不，很不错的目标！只要有明确的目标，就一定会有实现的那天。」"
  },
  [200240170] = {
    id = 200240170,
    name = "「嗯，谢谢你的鼓励！我会继续努力的，希望能来得及。」"
  },
  [200240171] = {
    id = 200240171,
    name = "「那么就此别过啦~」"
  },
  [200240172] = {
    id = 200240172,
    name = "矮个子少年蹦蹦跳跳地离开了，你沉思片刻，心中有了计划。"
  },
  [200240173] = {
    id = 200240173,
    name = "几天后，矮个子少年刚和别人玩完游戏，你出现在了他面前，少年有些惊讶。"
  },
  [200240174] = {
    id = 200240174,
    name = "「我们来玩一个游戏吧。」"
  },
  [200240175] = {
    id = 200240175,
    name = "「好啊，玩什么呢？」"
  },
  [200240176] = {
    id = 200240176,
    name = "你拿出一封信函，告诉他持有这封信函的人，可以根据信上的指引，前往附近圈落免费进行探险者学习。"
  },
  [200240177] = {
    id = 200240177,
    name = "少年瞪大了双眼，呆呆地看着你。"
  },
  [200240178] = {
    id = 200240178,
    name = "「那么还是老规矩，在这封信落地前你能抓住它，它就是你的了。」"
  },
  [200240179] = {
    id = 200240179,
    name = "说罢你催动魔法，信轻轻向远处飘去，少年猛地转身向信飘落的方向跑去，生怕你看见他夺眶而出的泪水。"
  },
  [200240180] = {
    id = 200240180,
    name = "「为什么要这样做？」"
  },
  [200240181] = {
    id = 200240181,
    name = "「你在说什么？我不太明白。」"
  },
  [200240182] = {
    id = 200240182,
    name = "「你是故意这样做的吧？」"
  },
  [200240183] = {
    id = 200240183,
    name = "「嗯？」"
  },
  [200240184] = {
    id = 200240184,
    name = "「选择没有价值的魔物硬壳，而不选择值钱的金币，为什么？」"
  },
  [200240185] = {
    id = 200240185,
    name = "「哈哈，被你发现了啊。」"
  },
  [200240186] = {
    id = 200240186,
    name = "「答案很简单啦。」"
  },
  [200240187] = {
    id = 200240187,
    name = "「如果我一直选魔物硬壳，他们就会认为很好玩，一直给我对吧。」"
  },
  [200240188] = {
    id = 200240188,
    name = "「这样我每天都可以得到很多魔物硬壳。」"
  },
  [200240189] = {
    id = 200240189,
    name = "「把那些硬壳卖给材料商，便足以维持我的生活。」"
  },
  [200240190] = {
    id = 200240190,
    name = "「只要这种游戏能长期玩下去，我获得的远比一个金币更多。」"
  },
  [200240191] = {
    id = 200240191,
    name = "「如果我选择了金币，便不会再有人跟我玩啦。」"
  },
  [200240192] = {
    id = 200240192,
    name = "「......」"
  },
  [200240193] = {
    id = 200240193,
    name = "「大家都说我是笨蛋，其实我早就明白这些了。」"
  },
  [200240194] = {
    id = 200240194,
    name = "「对了，这件事记得帮我保密。」"
  },
  [200240195] = {
    id = 200240195,
    name = "「我得继续被当作笨蛋才行。」"
  },
  [200240196] = {
    id = 200240196,
    name = "矮个子少年蹦蹦跳跳地离开了。"
  },
  [200240197] = {
    id = 200240197,
    name = "「聪明人总以为自己很聪明，笨蛋却不一定是真的笨蛋呢。」"
  },
  [200250001] = {
    id = 200250001,
    name = "「......」"
  },
  [200250002] = {
    id = 200250002,
    name = "「前面有什么声音？」"
  },
  [200250003] = {
    id = 200250003,
    name = "你发现角落处有一团黑黝黝的雾气，散发着危险的气息。"
  },
  [200250004] = {
    id = 200250004,
    name = "「这是？」"
  },
  [200250005] = {
    id = 200250005,
    name = "就在你专心观察之际，雾气突然蹦了起来。"
  },
  [200250006] = {
    id = 200250006,
    name = "「魔物！」"
  },
  [200250007] = {
    id = 200250007,
    name = "眼前的魔物虽然看起来威胁不大，但你依然准备将其除掉。"
  },
  [200250008] = {
    id = 200250008,
    name = "「等一下！」"
  },
  [200250009] = {
    id = 200250009,
    name = "一个女人突然叫住了你。"
  },
  [200250010] = {
    id = 200250010,
    name = "「别杀它！」"
  },
  [200250011] = {
    id = 200250011,
    name = "身着公会服饰的女人跑到了你的面前。"
  },
  [200250012] = {
    id = 200250012,
    name = "那团黑黝黝的雾气飞快朝女人飞了过去，在女人周围磨蹭，看上去很是亲昵。"
  },
  [200250013] = {
    id = 200250013,
    name = "「我是公会支援部的研究员，小黑是我们的研究对象。」"
  },
  [200250014] = {
    id = 200250014,
    name = "「拜托了，请不要杀它。」"
  },
  [200250015] = {
    id = 200250015,
    name = "「它是从研究所偷跑出来的。」"
  },
  [200250016] = {
    id = 200250016,
    name = "「偷跑？」"
  },
  [200250017] = {
    id = 200250017,
    name = "「是的，研究所最近新捕获了一批魔物，我忙着照顾那些魔物，没注意就......」"
  },
  [200250018] = {
    id = 200250018,
    name = "「这种魔物看起来并不像成年魔物，一般研究不都会选择成年魔物吗？」"
  },
  [200250019] = {
    id = 200250019,
    name = "「呵呵，其实这是我们最新的一个研究方向。」"
  },
  [200250020] = {
    id = 200250020,
    name = "「主要研究这些魔物幼崽的成长过程、变化，以及是否能驯化。」"
  },
  [200250021] = {
    id = 200250021,
    name = "「驯化？」"
  },
  [200250022] = {
    id = 200250022,
    name = "「是的，如果能驯化或者交流，说不定人类与魔物也能和睦相处——」"
  },
  [200250023] = {
    id = 200250023,
    name = "「不，请忘了我说的话。」"
  },
  [200250024] = {
    id = 200250024,
    name = "「......总感觉你们的研究很危险。」"
  },
  [200250025] = {
    id = 200250025,
    name = "「请放心，我们已经做好了风险管控。」"
  },
  [200250026] = {
    id = 200250026,
    name = "「另外，还希望你不要把这件事告诉别人，否则可能会为我们带来不必要的麻烦。」"
  },
  [200250027] = {
    id = 200250027,
    name = "「麻烦？具体是什么呢？」"
  },
  [200250028] = {
    id = 200250028,
    name = "「由于是全新的研究课题，如果泄露很可能引来同行的觊觎吧？」"
  },
  [200250029] = {
    id = 200250029,
    name = "「而且，这种类型的研究课题，对一些狂热探险者而言，是绝不允许的。」"
  },
  [200250030] = {
    id = 200250030,
    name = "「好吧，我知道了。」"
  },
  [200250031] = {
    id = 200250031,
    name = "「非常感谢您！幸亏遇到的是你这样通情达理的探险者！」"
  },
  [200250032] = {
    id = 200250032,
    name = "研究员和你道别，小黑乖乖地跟在她的身后。"
  },
  [200250033] = {
    id = 200250033,
    name = "你突然觉得它像一个听话的宠物。"
  },
  [200250034] = {
    id = 200250034,
    name = "「......我怎么会有这种奇怪的想法。」"
  },
  [200250035] = {
    id = 200250035,
    name = "「请放心，我不会透露给任何人。」"
  },
  [200250036] = {
    id = 200250036,
    name = "「不过这里，时常还是会有人路过吧？」"
  },
  [200250037] = {
    id = 200250037,
    name = "「是的，所以我们的研究所也正准备搬迁，到时候看管也会更严格。」"
  },
  [200250038] = {
    id = 200250038,
    name = "「能遇到你这样通情达理的探险者真是太好了，非常感谢你的理解。」"
  },
  [200250039] = {
    id = 200250039,
    name = "女研究员招呼了一句，小黑便乖乖跟在她身后离去了。"
  },
  [200250040] = {
    id = 200250040,
    name = "「魔物竟然真的能听懂？！」"
  },
  [200250041] = {
    id = 200250041,
    name = "「这难道不是魔物吗？」"
  },
  [200250042] = {
    id = 200250042,
    name = "「是，但它非常特殊，是我们研究所的重要观察对象。」"
  },
  [200250043] = {
    id = 200250043,
    name = "「研究所？在这附近？」"
  },
  [200250044] = {
    id = 200250044,
    name = "「啊......这个......我们的研究目前还处于保密阶段，所以不便透露......」"
  },
  [200250045] = {
    id = 200250045,
    name = "「你......真的是公会的研究员？」"
  },
  [200250046] = {
    id = 200250046,
    name = "「当然了。我、我还有其他重要的事，我先走了。」"
  },
  [200250047] = {
    id = 200250047,
    name = "「等一下，你很可疑啊。」"
  },
  [200250048] = {
    id = 200250048,
    name = "「这种魔物，为什么允许它到外面活动？」"
  },
  [200250049] = {
    id = 200250049,
    name = "「如果袭击人类怎么办？」"
  },
  [200250050] = {
    id = 200250050,
    name = "「它，它是逃出来的。」"
  },
  [200250051] = {
    id = 200250051,
    name = "「逃出来？」"
  },
  [200250052] = {
    id = 200250052,
    name = "「那就更危险了，竟然已经做出这种举动，必须及时除掉吧？！」"
  },
  [200250053] = {
    id = 200250053,
    name = "「不行！」"
  },
  [200250054] = {
    id = 200250054,
    name = "见你准备动手，研究员挺身挡在了魔物面前。"
  },
  [200250055] = {
    id = 200250055,
    name = "（拼死也要保护魔物？这只魔物有这么重要？）"
  },
  [200250056] = {
    id = 200250056,
    name = "你和她谁也不肯让步，一时陷入僵局。"
  },
  [200250057] = {
    id = 200250057,
    name = "正在这时，小魔物蹭了蹭女人的手臂，似乎想带她走。"
  },
  [200250058] = {
    id = 200250058,
    name = "「你看，它不会伤人，而且已经能明白一些简单的指令了。」"
  },
  [200250059] = {
    id = 200250059,
    name = "「对你是无害，那对别人呢？你就不考虑其他人的安危吗？」"
  },
  [200250060] = {
    id = 200250060,
    name = "「它、它真的不会伤人，求求你放过它吧，我今后一定会严加看管的。」"
  },
  [200250061] = {
    id = 200250061,
    name = "看着研究员苦苦哀求的样子，你叹了口气，决定给她一次机会。"
  },
  [200250062] = {
    id = 200250062,
    name = "「如果我再遇到它，我不会手下留情。」"
  },
  [200250063] = {
    id = 200250063,
    name = "说罢你转身离去，研究员急忙抱着魔物跑走了，生怕你反悔。"
  },
  [200250064] = {
    id = 200250064,
    name = "眼见研究员如此袒护那魔物，你认为更不能留它，否则真酿出祸患，后悔就迟了。"
  },
  [200250065] = {
    id = 200250065,
    name = "你决定无论如何也要趁现在将那魔物除掉。"
  },
  [200250066] = {
    id = 200250066,
    name = "于是你假意收起攻击姿态，女研究员以为你准备放过魔物，果然放松下来。"
  },
  [200250067] = {
    id = 200250067,
    name = "趁她转身的瞬间，你以迅雷之势出手，一击将魔物击杀。"
  },
  [200250068] = {
    id = 200250068,
    name = "「不！」"
  },
  [200250069] = {
    id = 200250069,
    name = "「你竟然杀了它！」"
  },
  [200250070] = {
    id = 200250070,
    name = "「哼，我这是为所有人类除害。」"
  },
  [200250071] = {
    id = 200250071,
    name = "「如果再让我发现类似的事，我会亲自拜访你们研究所。」"
  },
  [200250072] = {
    id = 200250072,
    name = "「你、你......」"
  },
  [200250073] = {
    id = 200250073,
    name = "研究员忍不住掩面哭泣，你不再理会，径直转身离去。"
  },
  [200250074] = {
    id = 200250074,
    name = "「......噗噗！」"
  },
  [200250075] = {
    id = 200250075,
    name = "魔物没有攻击你，而是像受到惊吓一般，躲到了一块小石头后面。"
  },
  [200250076] = {
    id = 200250076,
    name = "「......噗噗！」"
  },
  [200250077] = {
    id = 200250077,
    name = "「竟然还知道藏起来......这魔物挺有意思的。」"
  },
  [200250078] = {
    id = 200250078,
    name = "「小黑！」"
  },
  [200250079] = {
    id = 200250079,
    name = "「......噗噗！」"
  },
  [200250080] = {
    id = 200250080,
    name = "「小黑，你又偷跑出来了。」"
  },
  [200250081] = {
    id = 200250081,
    name = "「小黑？」"
  },
  [200250082] = {
    id = 200250082,
    name = "身着公会服饰的女人跑到了你的面前。"
  },
  [200250083] = {
    id = 200250083,
    name = "那团黑黝黝的雾气飞快朝女人飞了过去，不停磨蹭着女人，看上去很是亲昵。"
  },
  [200250084] = {
    id = 200250084,
    name = "「你好，我是公会支援部的研究员，小黑是我们的研究对象。」"
  },
  [200250085] = {
    id = 200250085,
    name = "「很少见你这样温柔的人了呢。」"
  },
  [200250086] = {
    id = 200250086,
    name = "「很多人看到魔物，第一反应都是杀掉它。」"
  },
  [200250087] = {
    id = 200250087,
    name = "「我见这小家伙没什么恶意，所以才......」"
  },
  [200250088] = {
    id = 200250088,
    name = "「是的，小黑从不伤人，反而很喜欢待在人类身边。」"
  },
  [200250089] = {
    id = 200250089,
    name = "「所以它是我们的重点研究对象，我们希望能从它身上，找到人类与魔物的相处——」"
  },
  [200250090] = {
    id = 200250090,
    name = "「不，找到能掌控魔物的方法。」"
  },
  [200250091] = {
    id = 200250091,
    name = "「掌控魔物？据我所知还没人能做到吧？」"
  },
  [200250092] = {
    id = 200250092,
    name = "「是的，所以我们捕获了一些魔物幼崽，观察它们的成长、变化，看能否有所突破。」"
  },
  [200250093] = {
    id = 200250093,
    name = "「一些？难道你们还饲养着其他魔物幼崽？」"
  },
  [200250094] = {
    id = 200250094,
    name = "研究员意识到自己和你透露了太多信息，一时语塞。"
  },
  [200250095] = {
    id = 200250095,
    name = "「呵呵，放心，我并无恶意，只是对你们的研究感到好奇。」"
  },
  [200250096] = {
    id = 200250096,
    name = "「能拥有如此远大的目标，让我不禁有些佩服。」"
  },
  [200250097] = {
    id = 200250097,
    name = "研究员见你称赞她的事业，显得非常开心，如同遇见知己一般，又和你聊了许多魔物的话题。"
  },
  [200250098] = {
    id = 200250098,
    name = "你的应答令她非常满意，仿佛遇到了知音。"
  },
  [200250099] = {
    id = 200250099,
    name = "「没想到我们竟然如此投缘，不知你现在是否方便，方便的话可以去我们研究所看看那些小家伙。」"
  },
  [200250100] = {
    id = 200250100,
    name = "你原本只是出于礼貌应付一番，没想到她竟然如此上心，令你隐隐有些不安，于是以有要事在身为由拒绝了她。"
  },
  [200250101] = {
    id = 200250101,
    name = "「真是遗憾啊，看来只能等以后有机会再说了。」"
  },
  [200250102] = {
    id = 200250102,
    name = "「是啊，等有机会，我也想好好接触一下这些小家伙。」"
  },
  [200250103] = {
    id = 200250103,
    name = "「那就这么说定了，等你忙完可以随时到这边来找我。」"
  },
  [200250104] = {
    id = 200250104,
    name = "「嗯，没问题。」"
  },
  [200250105] = {
    id = 200250105,
    name = "看着她带着魔物渐行渐远的身影，你长舒了一口气。"
  },
  [200250106] = {
    id = 200250106,
    name = "你跟着她到达了研究所，这里设施简陋，和你印象中那些研究所完全不同。"
  },
  [200250107] = {
    id = 200250107,
    name = "而且与其说是研究所，不如说更像一个简单布置过的山洞。"
  },
  [200250108] = {
    id = 200250108,
    name = "洞内灯光昏暗，仅保持着最低限度的照明。"
  },
  [200250109] = {
    id = 200250109,
    name = "你跟在研究员身后走了一会儿，到达一处相对开阔的区域，她告诉你，此处便是魔物幼崽生活的区域。"
  },
  [200250110] = {
    id = 200250110,
    name = "然而你环顾四周，一片漆黑，什么也看不到。"
  },
  [200250111] = {
    id = 200250111,
    name = "在你有些疑惑时，研究员走到一个石柱旁，轻轻转动石柱。"
  },
  [200250112] = {
    id = 200250112,
    name = "洞窟上方突然亮起数块水晶，将整个洞穴照亮。"
  },
  [200250113] = {
    id = 200250113,
    name = "洞窟周围的石壁上，被凿出一个个大小不一的孔洞，每一个孔洞中都关着魔物，见水晶亮起，魔物纷纷嚎叫起来。"
  },
  [200250114] = {
    id = 200250114,
    name = "你被眼前这一幕震惊了，几十？上百？这里关押的魔物数量超乎你的想象。"
  },
  [200250115] = {
    id = 200250115,
    name = "见你一脸惊讶，女研究员非常得意地告诉你，这里关押着各种生长阶段的魔物。"
  },
  [200250116] = {
    id = 200250116,
    name = "同时更加细致地和你介绍起她的研究，以及这里的设施......"
  },
  [200250117] = {
    id = 200250117,
    name = "但你一句话也没听进去，数量如此庞大的魔物群让你感到忧心，你打断了她的话。"
  },
  [200250118] = {
    id = 200250118,
    name = "「这么多魔物，你们能够管控住吗？」"
  },
  [200250119] = {
    id = 200250119,
    name = "女研究员听你这样说，愣了一下，随即告诉你她们的应对措施如何完备，这里的防护如何坚固。"
  },
  [200250120] = {
    id = 200250120,
    name = "你从她所说的内容来判断，这里的防护确实非常牢固，不禁觉得自己想太多了。"
  },
  [200250121] = {
    id = 200250121,
    name = "正当你稍微放松，准备仔细观察这些魔物时，洞窟顶部的水晶突然闪烁了两下，随后彻底熄灭。"
  },
  [200250122] = {
    id = 200250122,
    name = "研究员又旋转了几下石柱，却并未能让水晶重新亮起。"
  },
  [200250123] = {
    id = 200250123,
    name = "「奇怪，难道又有魔力泄露了吗？嗯......不过不用担心，这是正常现象......」"
  },
  [200250124] = {
    id = 200250124,
    name = "研究员的话像一颗炸弹般突然在你的脑海中炸裂开来，对！魔力，就是魔力！"
  },
  [200250125] = {
    id = 200250125,
    name = "研究员刚刚所说的那些防护措施确实足够完备，但需要庞大的魔力供应。"
  },
  [200250126] = {
    id = 200250126,
    name = "可是这里简陋的设施，寥寥可数的人员......魔力供应迟早会出问题。"
  },
  [200250127] = {
    id = 200250127,
    name = "你瞬间觉得冷汗直冒，如果这么多魔物失控......"
  },
  [200250128] = {id = 200250128, name = "轰——"},
  [200250129] = {
    id = 200250129,
    name = "突然洞窟内响起一声巨响，有魔物破洞而出！"
  },
  [200250130] = {
    id = 200250130,
    name = "你顾不上身旁吓呆了的研究员，对着洞窟周围全力施放魔法，挡下了第一波破洞而出的魔物。"
  },
  [200250131] = {
    id = 200250131,
    name = "紧接着你拖着她一路狂奔，一边逃一边在洞窟石壁上留下魔法印记。"
  },
  [200250132] = {
    id = 200250132,
    name = "你将研究所内的人员全部遣散到了外面，随即全力施放魔法，引爆设置的印记。"
  },
  [200250133] = {
    id = 200250133,
    name = "轰——！！！"
  },
  [200250134] = {
    id = 200250134,
    name = "伴随着阵阵轰鸣，山洞轰然坍塌，将尚未逃出来的魔物全部掩埋......"
  },
  [200260001] = {
    id = 200260001,
    name = "「哇！救......救我！」"
  },
  [200260002] = {
    id = 200260002,
    name = "前方传来呼救声。"
  },
  [200260003] = {
    id = 200260003,
    name = "一个背着画架的中年男人看见你，就像看到了救命稻草，飞快地朝你冲了过来。"
  },
  [200260004] = {
    id = 200260004,
    name = "在他身后，一头普通魔物正咆哮着逼近。"
  },
  [200260005] = {
    id = 200260005,
    name = "「普通人？」"
  },
  [200260006] = {
    id = 200260006,
    name = "你抬手便是一击，轻松解决掉了魔物。"
  },
  [200260007] = {
    id = 200260007,
    name = "「谢谢......谢谢！」"
  },
  [200260008] = {
    id = 200260008,
    name = "「你的运气不错。」"
  },
  [200260009] = {
    id = 200260009,
    name = "「我已经很小心了，没想到还是遇到了魔物。」"
  },
  [200260010] = {
    id = 200260010,
    name = "「你看起来不像探险者，竟敢在这种地方活动？」"
  },
  [200260011] = {
    id = 200260011,
    name = "「我是一个画师，到这边来是为了寻找一种花。」"
  },
  [200260012] = {
    id = 200260012,
    name = "「花？」"
  },
  [200260013] = {
    id = 200260013,
    name = "「是的，一种几乎能称得上完美的花。」"
  },
  [200260014] = {
    id = 200260014,
    name = "说着画师向你展示了他的画作，上面是一个闭眼微笑的少女，双手置于胸前，似乎握着什么。"
  },
  [200260015] = {
    id = 200260015,
    name = "「我想找到那种花，补全这幅画。」"
  },
  [200260016] = {
    id = 200260016,
    name = "「喔，很美的画啊。」"
  },
  [200260017] = {
    id = 200260017,
    name = "「谢谢。」"
  },
  [200260018] = {
    id = 200260018,
    name = "「我找了很久也毫无线索，直到不久前，有人告诉我曾在这边见过，所以我就赶了过来。」"
  },
  [200260019] = {
    id = 200260019,
    name = "「然后一无所获，还被魔物追？」"
  },
  [200260020] = {
    id = 200260020,
    name = "「不！我找到了！」"
  },
  [200260021] = {
    id = 200260021,
    name = "「竟然真有？！」"
  },
  [200260022] = {
    id = 200260022,
    name = "「嗯，那种花叫做空之花，就长在后面的一棵大树顶端。」"
  },
  [200260023] = {
    id = 200260023,
    name = "「我刚才就是想爬上去采摘时遇到了魔物，所以才......」"
  },
  [200260024] = {
    id = 200260024,
    name = "「这位探险者，非常感谢你刚刚救了我，你的实力如此强大，不知你能否......」"
  },
  [200260025] = {
    id = 200260025,
    name = "「帮你去摘花？？」"
  },
  [200260026] = {
    id = 200260026,
    name = "「嗯，我知道这个请求很冒昧，但我现在也想不到其他办法，只能拜托你了！」"
  },
  [200260027] = {
    id = 200260027,
    name = "你按画师的指引，成功找到了绽放于大树顶端的花。"
  },
  [200260028] = {
    id = 200260028,
    name = "然而正当你准备采摘时，几头魔物从高空向你冲来。"
  },
  [200260029] = {
    id = 200260029,
    name = "你身处树上，难以全力对付魔物，几番交手后让魔物逃走了。"
  },
  [200260030] = {
    id = 200260030,
    name = "你将花带给了画师，他显得无比激动。"
  },
  [200260031] = {
    id = 200260031,
    name = "「你、你真的做到了！」"
  },
  [200260032] = {
    id = 200260032,
    name = "「天啊，你看它的形态和色泽，这世间再也找不到比它更完美的存在了！」"
  },
  [200260033] = {
    id = 200260033,
    name = "画师说着，急忙从包裹中取出绘画工具。"
  },
  [200260034] = {
    id = 200260034,
    name = "「你能就这样帮我拿着它吗，我现在就将这幅画补全！」"
  },
  [200260035] = {
    id = 200260035,
    name = "「这里并不安全，不如换个地方......」"
  },
  [200260036] = {
    id = 200260036,
    name = "画师并未理会你，他已沉浸在了绘画之中。"
  },
  [200260037] = {
    id = 200260037,
    name = "你叹了口气，只得站在原地，等待画师补全他的画作。"
  },
  [200260038] = {
    id = 200260038,
    name = "不一会儿，你忽然看到远处有一群黑影正向你们飞来。"
  },
  [200260039] = {
    id = 200260039,
    name = "你定睛一看，正是先前摘花时击退的魔物，看来是召集族群前来寻仇了！"
  },
  [200260040] = {
    id = 200260040,
    name = "你心中一惊，急忙招呼画师撤退，然而他依然沉浸在绘画中，不想就此放弃。"
  },
  [200260041] = {
    id = 200260041,
    name = "你见多说无用，便一把抓起他的衣领向远处逃去。"
  },
  [200260042] = {
    id = 200260042,
    name = "「不！我的画！我的画还没拿！」"
  },
  [200260043] = {
    id = 200260043,
    name = "画师不停地叫喊着。"
  },
  [200260044] = {
    id = 200260044,
    name = "「如果失去了那幅画，我活着也没有意义，你自己逃吧，我要去拿我的画！」"
  },
  [200260045] = {
    id = 200260045,
    name = "画师不停地挣扎严重影响了你的行动，你索性放开了他，任由他跑回去拿画。"
  },
  [200260046] = {
    id = 200260046,
    name = "画师大喊着冲向自己的画作，完全不在乎逼近的魔物。"
  },
  [200260047] = {
    id = 200260047,
    name = "你心里暗骂一声，咬了咬牙，迅速运转全身魔力，紧随着画师冲了过去。"
  },
  [200260048] = {
    id = 200260048,
    name = "就在画师抱起自己画作之时，魔物也已逼至他身前。"
  },
  [200260049] = {
    id = 200260049,
    name = "轰——！"
  },
  [200260050] = {
    id = 200260050,
    name = "你及时赶到，用魔法击退了距他最近的几头魔物，随即全力与魔物群交战。"
  },
  [200260051] = {
    id = 200260051,
    name = "这些魔物个体实力并不强，但数量实在太多，让你有些应接不暇。"
  },
  [200260052] = {
    id = 200260052,
    name = "你大喊着让画师赶紧撤离，然而却无人回应。"
  },
  [200260053] = {
    id = 200260053,
    name = "你回头看去，发现他已倒在血泊之中，生命垂危。"
  },
  [200260054] = {
    id = 200260054,
    name = "你当机立断，全力施放出一个巨大的魔法，短暂扰乱了这群魔物。"
  },
  [200260055] = {
    id = 200260055,
    name = "随即扛起死死抱住画作的画师，一个箭步冲了出去。"
  },
  [200260056] = {
    id = 200260056,
    name = "身后的魔物群紧追不舍，你魔力运转极快，但这群飞行魔物依然在不断拉近你们之间的距离。"
  },
  [200260057] = {
    id = 200260057,
    name = "危急之时，你发现了一个山洞，于是再次蓄积魔力，全力轰向地面。"
  },
  [200260058] = {
    id = 200260058,
    name = "瞬间地面溅起大量烟尘，让魔物群暂时失去了视野，你则趁机钻进山洞，并将入口封住。"
  },
  [200260059] = {
    id = 200260059,
    name = "你全力救治着画师，然而他的情况不断恶化。"
  },
  [200260060] = {
    id = 200260060,
    name = "「咳咳咳，不......不用救我了......」"
  },
  [200260061] = {
    id = 200260061,
    name = "「你先别说话。」"
  },
  [200260062] = {
    id = 200260062,
    name = "「不、不要再浪费魔力了，不然......你也可能陷入危险。」"
  },
  [200260063] = {
    id = 200260063,
    name = "「能见到那个花，我的旅途......也接近终点了。」"
  },
  [200260064] = {
    id = 200260064,
    name = "「......啊......是你吗，爱理......你来接我了吗......」"
  },
  [200260065] = {
    id = 200260065,
    name = "「爱理，我好想你......终于......终于又能在一起了......」"
  },
  [200260066] = {
    id = 200260066,
    name = "在生命的最后一刻，画师的脸上浮现出一抹满足的微笑。"
  },
  [200260067] = {
    id = 200260067,
    name = "「不行，我不可能让你去送死。」"
  },
  [200260068] = {
    id = 200260068,
    name = "画师根本不听你说，仍然不断地挣扎，为了能成功逃走，你将他打晕了过去。"
  },
  [200260069] = {
    id = 200260069,
    name = "逃跑途中你发现了一个山洞，立即躲了进去，并将洞口暂时封住。"
  },
  [200260070] = {id = 200260070, name = "......"},
  [200260071] = {
    id = 200260071,
    name = "「唔......这是哪里？」"
  },
  [200260072] = {
    id = 200260072,
    name = "「你醒了？我们差点就没能逃掉。先在这躲一会儿吧。」"
  },
  [200260073] = {
    id = 200260073,
    name = "「逃？啊！画！我的画！」"
  },
  [200260074] = {
    id = 200260074,
    name = "「等魔物走了我们再回去找吧，这会儿外面全是魔物。」"
  },
  [200260075] = {
    id = 200260075,
    name = "「不......不要啊......」"
  },
  [200260076] = {
    id = 200260076,
    name = "画师说着，双手抱头陷入了痛苦之中，蜷缩在山洞角落。"
  },
  [200260077] = {
    id = 200260077,
    name = "又过了一段时间，你发现魔物已散去，便和画师一起返回寻找他的画作。"
  },
  [200260078] = {
    id = 200260078,
    name = "然而在刚才的骚乱中，画作已被损毁。"
  },
  [200260079] = {
    id = 200260079,
    name = "画师这次没有再哭嚎，他面如死灰，静静走到那幅画作旁边，将它捡了起来抱在怀中，如同抱着心爱的人。"
  },
  [200260080] = {
    id = 200260080,
    name = "他步履蹒跚地向森林方向走去，你告诉他森林中存在危险，让他赶紧回据点。"
  },
  [200260081] = {
    id = 200260081,
    name = "他回头朝你露出一个惨淡而绝望的笑容。"
  },
  [200260082] = {
    id = 200260082,
    name = "「谢谢你的提醒，谢谢你救了我，但我现在，知道自己该去哪里了......」"
  },
  [200260083] = {
    id = 200260083,
    name = "说罢，他便不再理会你的呼喊，径直走入了森林。"
  },
  [200260084] = {
    id = 200260084,
    name = "「别着急，我们先摆脱这群魔物，之后我再去帮你拿回来！」"
  },
  [200260085] = {
    id = 200260085,
    name = "你发现一个山洞，立即躲了进去，并将洞口暂时封住。"
  },
  [200260086] = {
    id = 200260086,
    name = "「你就在这里等我，别乱跑。」"
  },
  [200260087] = {
    id = 200260087,
    name = "「那、那你快点回来......」"
  },
  [200260088] = {
    id = 200260088,
    name = "你全力隐匿自己的气息，借着草木山石的掩护，成功躲开了魔物群。"
  },
  [200260089] = {
    id = 200260089,
    name = "终于，你发现了画作，不过刚才的骚乱已将它损毁。"
  },
  [200260090] = {
    id = 200260090,
    name = "你只能努力将残破的部分全部收集起来，带回给了画师。"
  },
  [200260091] = {
    id = 200260091,
    name = "当画师看到画作的惨状时，期待的目光瞬间黯淡了下来。"
  },
  [200260092] = {
    id = 200260092,
    name = "「不、不，不该是这样的！不该是这样的！」"
  },
  [200260093] = {
    id = 200260093,
    name = "画师崩溃了，瞬间起身向一侧的岩壁撞去，想要轻生。"
  },
  [200260094] = {
    id = 200260094,
    name = "你虽然及时出手，但他还是撞到了石壁，昏了过去。"
  },
  [200260095] = {
    id = 200260095,
    name = "你为他处理了伤口，确认并无大碍后，将画作的残片全部展开，准备将画作拼合、修补。"
  },
  [200260096] = {
    id = 200260096,
    name = "时间一点点流逝，你终于将残破的画作拼接得差不多了，却发现画作中手的部位还有缺失。"
  },
  [200260097] = {
    id = 200260097,
    name = "正当你思考着该怎么办时，画师低沉的声音从你身后传来。"
  },
  [200260098] = {
    id = 200260098,
    name = "「她是我的妻子，叫做爱理。」"
  },
  [200260099] = {
    id = 200260099,
    name = "「她曾是个探险者，但我们结婚后她就不再探险，全心全意支持我的事业。」"
  },
  [200260100] = {
    id = 200260100,
    name = "「可是，就在我刚有起色之际，她却永远地离我而去。」"
  },
  [200260101] = {
    id = 200260101,
    name = "「她离开前，告诉我她还有心愿未能实现。」"
  },
  [200260102] = {
    id = 200260102,
    name = "「她希望我能将一种她曾见过的最完美的花，画进她的画像之中。」"
  },
  [200260103] = {
    id = 200260103,
    name = "「并且让我务必完成她的这个心愿。」"
  },
  [200260104] = {
    id = 200260104,
    name = "「但现在......一切都结束了......」"
  },
  [200260105] = {
    id = 200260105,
    name = "「那幅画像是我为她绘制的最完美的一幅。」"
  },
  [200260106] = {
    id = 200260106,
    name = "「但是现在，它却毁了......」"
  },
  [200260107] = {
    id = 200260107,
    name = "「谢谢你帮我找到了花，也谢谢你救了我，但我可能没有机会报答你了。」"
  },
  [200260108] = {
    id = 200260108,
    name = "画师从你手中接过修补好的画作，轻轻地抚摸了一番，便向你告辞。"
  },
  [200260109] = {
    id = 200260109,
    name = "你大概猜到了他接下来的选择，但也想不到其他办法了。"
  },
  [200260110] = {
    id = 200260110,
    name = "对于已经绝望的人，能拯救他们的，只有他们自己。"
  },
  [200260111] = {
    id = 200260111,
    name = "画师紧紧抱着画作，步履蹒跚地向洞外走去。"
  },
  [200260112] = {
    id = 200260112,
    name = "你再次叫住了他，说了些想要他帮忙绘画，希望能看到他的画展之类的话。"
  },
  [200260113] = {
    id = 200260113,
    name = "他回头朝你露出一个惨淡而绝望的笑容。"
  },
  [200260114] = {
    id = 200260114,
    name = "「请不用再为我费心了。」"
  },
  [200260115] = {
    id = 200260115,
    name = "说罢他便头也不回地向外走去。"
  },
  [200260116] = {
    id = 200260116,
    name = "你叫住了画师，告诉他他还没有完成妻子的心愿，如果就此放弃，他的妻子是不会安宁的。"
  },
  [200260117] = {
    id = 200260117,
    name = "「但现在画毁了，花也没有了，我已经什么也做不到了。」"
  },
  [200260118] = {
    id = 200260118,
    name = "「不，既然找到过一次，那么深渊中一定还有其他地方也能找到！」"
  },
  [200260119] = {
    id = 200260119,
    name = "「至于这幅画，虽然拼接完后存在残缺，但想必你这些年早已记下了这幅画的每一个细节，只要肯花些时间，你一定能重新绘制出来的。」"
  },
  [200260120] = {
    id = 200260120,
    name = "「而且最关键的一点，你真的理解你妻子的遗愿了吗？」"
  },
  [200260121] = {
    id = 200260121,
    name = "「什么意思？这是我妻子最后告诉我的话，我怎么可能记错。」"
  },
  [200260122] = {
    id = 200260122,
    name = "你叹了口气，告诉画师他所忽略的内容。"
  },
  [200260123] = {
    id = 200260123,
    name = "他的妻子知道自己死后画师很可能自寻短见，所以才给他留了一个几乎不可能完成的事作为遗愿。"
  },
  [200260124] = {
    id = 200260124,
    name = "她知道画师深爱着自己，一定会听自己的话，所以与其说希望他去完成这件事，不如说是希望他在追寻的过程中，找到继续活下去的意义。"
  },
  [200260125] = {
    id = 200260125,
    name = "「怎、怎么可能......不，不会是这样的......没有她，我根本没有活下去的意义。」"
  },
  [200260126] = {
    id = 200260126,
    name = "「所以她才希望你去寻找。」"
  },
  [200260127] = {
    id = 200260127,
    name = "画师陷入了沉默，你的话无疑让他产生了动摇。"
  },
  [200260128] = {
    id = 200260128,
    name = "「我想起来了，她刚病倒的时候，不肯好好吃药。」"
  },
  [200260129] = {
    id = 200260129,
    name = "「我就说如果她再不好好吃药，等她病死我就立刻赴死！」"
  },
  [200260130] = {
    id = 200260130,
    name = "「呵呵，呵呵哈哈，是因为这句话吗......爱理她...竟然在人生的最后时光也在为我考虑...」"
  },
  [200260131] = {
    id = 200260131,
    name = "「呵呵哈哈哈，我真是、真是太笨了啊！」"
  },
  [200260132] = {
    id = 200260132,
    name = "「对我来说，爱理就是我的一切。」"
  },
  [200260133] = {
    id = 200260133,
    name = "「谢谢你让我明白了爱理真正的用意。」"
  },
  [200260134] = {
    id = 200260134,
    name = "「我会重新为她绘制画像，重新寻找空之花......」"
  },
  [200260135] = {
    id = 200260135,
    name = "「然后遵从她的遗愿，寻找活下去的意义。寻找即使她不在我身边，也足以让我活下去的意义。」"
  },
  [200260136] = {
    id = 200260136,
    name = "「这样么......」"
  },
  [200260137] = {
    id = 200260137,
    name = "画师见你不愿帮忙，自顾自地朝不远处的森林走去。"
  },
  [200260138] = {
    id = 200260138,
    name = "「喂！」"
  },
  [200260139] = {
    id = 200260139,
    name = "你担心他再出危险，也跟了上去。"
  },
  [200260140] = {
    id = 200260140,
    name = "你看见了画师所说的那朵花，它正绽放于一棵大树顶端。"
  },
  [200260141] = {
    id = 200260141,
    name = "画师努力地想爬上大树，但每次都以失败告终。"
  },
  [200260142] = {
    id = 200260142,
    name = "你摇了摇头，走上前去，准备帮他一把。"
  },
  [200260143] = {
    id = 200260143,
    name = "然而正当你爬上树准备摘花时，几头魔物突然向你袭来。"
  },
  [200260144] = {
    id = 200260144,
    name = "你身处树上，难以发挥全力，与空中的魔物缠斗起来。"
  },
  [200260145] = {
    id = 200260145,
    name = "画师有些担心你们的打斗毁掉空之花，便在树下大声叫喊着让你小心别伤到花。"
  },
  [200260146] = {
    id = 200260146,
    name = "他的声音吸引了一头魔物的注意，魔物直扑他而去，你见状顾不得树上的花和魔物，纵身跳下树，挡在了他身前。"
  },
  [200260147] = {
    id = 200260147,
    name = "来到地面的你终于可以全力施展，在你多番魔法的攻击下，不一会儿便将魔物全部解决。"
  },
  [200260148] = {
    id = 200260148,
    name = "你扶起刚刚战斗中被吓得蜷缩在一旁的画师，却见他一脸呆滞地望着树顶。"
  },
  [200260149] = {
    id = 200260149,
    name = "你抬头望去，才发现树顶的空之花被刚才的战斗波及，只剩下一枝残缺的花茎。"
  },
  [200260150] = {
    id = 200260150,
    name = "「完了......一切都完了......」"
  },
  [200260151] = {
    id = 200260151,
    name = "「......嗯？」"
  },
  [200260152] = {
    id = 200260152,
    name = "「我找了这么多年......」"
  },
  [200260153] = {
    id = 200260153,
    name = "「呵呵，呵呵哈哈哈，命运还真是残酷啊......」"
  },
  [200260154] = {
    id = 200260154,
    name = "画师垂下了头，耷拉着双肩，显得极其沮丧，拖着沉重的步履向远处走去。"
  },
  [200260155] = {
    id = 200260155,
    name = "任你如何呼喊，他都如同听不见一般。"
  },
  [200270001] = {id = 200270001, name = "嗖——"},
  [200270002] = {
    id = 200270002,
    name = "一支箭矢射落在你脚下。"
  },
  [200270003] = {
    id = 200270003,
    name = "「站住！此路不通！」"
  },
  [200270004] = {
    id = 200270004,
    name = "声音的主人是一名少年。"
  },
  [200270005] = {
    id = 200270005,
    name = "你正欲抬脚，又是嗖嗖几声，箭矢拦住了你的去路。"
  },
  [200270006] = {
    id = 200270006,
    name = "「这座山是本大王的！」"
  },
  [200270007] = {
    id = 200270007,
    name = "「要想从这里过，要么留下干粮......」"
  },
  [200270008] = {
    id = 200270008,
    name = "「要么留下金币，要么——」"
  },
  [200270009] = {
    id = 200270009,
    name = "「就滚远点。」"
  },
  [200270010] = {
    id = 200270010,
    name = "「劫匪？」"
  },
  [200270011] = {
    id = 200270011,
    name = "「错错错，劫匪什么的真难听，我可是这里的山大王！」"
  },
  [200270012] = {
    id = 200270012,
    name = "「那你想干什么？」"
  },
  [200270013] = {
    id = 200270013,
    name = "「我不是说过了吗？」"
  },
  [200270014] = {
    id = 200270014,
    name = "「这座山现在是本王的地盘！」"
  },
  [200270015] = {
    id = 200270015,
    name = "「想过可以，得交过路费！」"
  },
  [200270016] = {
    id = 200270016,
    name = "「可我不想给啊。」"
  },
  [200270017] = {
    id = 200270017,
    name = "「啊啊，蠢货！没看见我手里拿着武器吗？！」"
  },
  [200270018] = {
    id = 200270018,
    name = "「武器，我也有啊。」"
  },
  [200270019] = {
    id = 200270019,
    name = "「你——」"
  },
  [200270020] = {
    id = 200270020,
    name = "「哼，看你的样子也是个探险者吧。」"
  },
  [200270021] = {
    id = 200270021,
    name = "「还真是不见棺材不掉泪，本王给你个机会。」"
  },
  [200270022] = {
    id = 200270022,
    name = "「和本王比试一下吧，只要你能赢，本王就免费让你过！」"
  },
  [200270023] = {
    id = 200270023,
    name = "「但你要是输了，身上的装备物资全归本王，然后哪来的滚哪儿去，你敢吗？」"
  },
  [200270024] = {
    id = 200270024,
    name = "「有何不敢？说吧，怎么比试？」"
  },
  [200270025] = {
    id = 200270025,
    name = "「呵，竟然敢答应？比起那些扭头就走的家伙，你还算有胆量。」"
  },
  [200270026] = {
    id = 200270026,
    name = "「比试内容嘛......这山上有个魔物巢穴，里面有数量庞大的魔物群，咱们比一下相同时间内谁杀的魔物多怎么样？」"
  },
  [200270027] = {
    id = 200270027,
    name = "「没问题。」"
  },
  [200270028] = {
    id = 200270028,
    name = "「哼，那就跟我来吧。」"
  },
  [200270029] = {
    id = 200270029,
    name = "少年把你带到一个山洞前，拿出了一个黑色的瓶子。"
  },
  [200270030] = {
    id = 200270030,
    name = "「就是这里了，这些魔物正常情况下不会离开山洞，但我小瓶中的烟雾可以将它们驱赶出来。」"
  },
  [200270031] = {
    id = 200270031,
    name = "「烟雾持续时间大概是三分钟左右，这段时间内我们谁斩杀的魔物更多就算谁赢，没问题吧？」"
  },
  [200270032] = {
    id = 200270032,
    name = "你点了点头。"
  },
  [200270033] = {
    id = 200270033,
    name = "「对了，比试一旦开始，就别想着逃跑了。要么乖乖认输交出装备物资，要么......就尝尝本王的利箭！」"
  },
  [200270034] = {
    id = 200270034,
    name = "你微微一笑，和少年击掌约定，随即少年打开黑色瓶子，扔进了洞窟。"
  },
  [200270035] = {
    id = 200270035,
    name = "你决定给他点颜色瞧瞧，于是全力运转魔力，凝神静气，紧紧盯着洞口。"
  },
  [200270036] = {
    id = 200270036,
    name = "随着烟雾在洞内扩散，第一头魔物探出了洞口，少年尚未来得及松开弓弦，你已然将其斩杀......"
  },
  [200270037] = {
    id = 200270037,
    name = "几分钟后，洞口再无魔物涌出。"
  },
  [200270038] = {
    id = 200270038,
    name = "少年在一旁大口大口地喘着气，看向你的眼神中满是不可思议。"
  },
  [200270039] = {
    id = 200270039,
    name = "在刚才的比试中，无论同时涌出多少魔物，几乎都被你一瞬间解决。"
  },
  [200270040] = {
    id = 200270040,
    name = "到了后来，少年完全放弃了自己擅长的弓箭，抽出佩刀冲向洞口拼命往里乱刺，才在你的攻击下抢到几头魔物。"
  },
  [200270041] = {
    id = 200270041,
    name = "然而纵使他竭尽全力，所杀的魔物和你比起来，仍然连一个零头都比不上。"
  },
  [200270042] = {
    id = 200270042,
    name = "「呵呵，所以我能走了吗？」"
  },
  [200270043] = {
    id = 200270043,
    name = "你朝他露出一个轻松的笑容，便准备离去，他却又拦下了你。"
  },
  [200270044] = {
    id = 200270044,
    name = "「不、不——」"
  },
  [200270045] = {
    id = 200270045,
    name = "「嗯？想耍赖？」"
  },
  [200270046] = {
    id = 200270046,
    name = "「不！本王，不，我认输，我认输了！」"
  },
  [200270047] = {
    id = 200270047,
    name = "「可以把你的名字告诉我吗？」"
  },
  [200270048] = {
    id = 200270048,
    name = "「呵呵，只是一个普通探险者而已。」"
  },
  [200270049] = {
    id = 200270049,
    name = "「不可能！附近圈落据点中，所有有名气的探险者我都知道，但他们完全比不上你。」"
  },
  [200270050] = {
    id = 200270050,
    name = "「请告诉我你的名字吧！让我能知道自己到底输给了怎样的大人物！」"
  },
  [200270051] = {
    id = 200270051,
    name = "「......」"
  },
  [200270052] = {
    id = 200270052,
    name = "（真是头疼，早知道就不使出全力了。）"
  },
  [200270053] = {
    id = 200270053,
    name = "你思索了片刻，看着少年认真的目光，突然有了主意。"
  },
  [200270054] = {
    id = 200270054,
    name = "「那就叫我无名吧。」"
  },
  [200270055] = {
    id = 200270055,
    name = "「无名？好奇怪的名字。」"
  },
  [200270056] = {
    id = 200270056,
    name = "「不过我记下了，无名前辈！我会继续努力修行，总有一天会超越你的！」"
  },
  [200270057] = {
    id = 200270057,
    name = "说罢，少年向你行了一礼，随即告诉了你通过此处的近路，并与你约定，等以后有机会再和你比试。"
  },
  [200270058] = {
    id = 200270058,
    name = "你长呼一口气，运转魔力，决定陪少年玩玩。"
  },
  [200270059] = {
    id = 200270059,
    name = "随着烟雾在洞内扩散，第一头魔物探出了洞口......"
  },
  [200270060] = {
    id = 200270060,
    name = "嗖~一支利箭精准命中魔物，你内心不禁对少年的身手多了几分赞许。"
  },
  [200270061] = {
    id = 200270061,
    name = "随着涌出洞口的魔物增多，你也开始了攻击。"
  },
  [200270062] = {
    id = 200270062,
    name = "几分钟后，洞口再无魔物涌出。"
  },
  [200270063] = {
    id = 200270063,
    name = "乍一看，你二人所杀的魔物并无太多差别，少年遂上前逐一清点起来。"
  },
  [200270064] = {
    id = 200270064,
    name = "「比我多了一头？哼，看来你的实力不错嘛，竟然能和本王平分秋色。」"
  },
  [200270065] = {
    id = 200270065,
    name = "「不过也算你运气好，本王今天状态不佳，如果再来一次，本王可不会输。」"
  },
  [200270066] = {
    id = 200270066,
    name = "「呵呵，那么我能走了吗？」"
  },
  [200270067] = {
    id = 200270067,
    name = "「当然，本王一向信守承诺。」"
  },
  [200270068] = {
    id = 200270068,
    name = "「不过免费通行也仅限这一次，等你下次再从这经过，本王还会和你比试，做好心理准备吧！」"
  },
  [200270069] = {
    id = 200270069,
    name = "说罢，少年转身下山，不再拦你。"
  },
  [200270070] = {
    id = 200270070,
    name = "但他没走几步，突然回头叫住了你。"
  },
  [200270071] = {
    id = 200270071,
    name = "「对了，这山后面的魔物实力不容小觑，你最好当心点。」"
  },
  [200270072] = {
    id = 200270072,
    name = "「当然，本王不是担心你，只是不想还没赢回来，你就先被魔物吃了而已。」"
  },
  [200270073] = {
    id = 200270073,
    name = "看着少年离去的背影，你笑着摇了摇头，继续踏上旅程。"
  },
  [200270074] = {
    id = 200270074,
    name = "随着烟雾在洞内扩散，第一头魔物探出了洞口......"
  },
  [200270075] = {
    id = 200270075,
    name = "嗖~一支利箭精准命中魔物，你内心不禁对少年的身手多了几分赞许。"
  },
  [200270076] = {
    id = 200270076,
    name = "不过既然决定输给他，你也开始了胡乱攻击。"
  },
  [200270077] = {
    id = 200270077,
    name = "在你有意控制下，你施放的魔法虽然多，但命中率极低，被你击杀的魔物几乎都是逃跑时慌不择路，误打误撞被你击中。"
  },
  [200270078] = {
    id = 200270078,
    name = "几分钟后，洞口再无魔物涌出。"
  },
  [200270079] = {
    id = 200270079,
    name = "你击杀的魔物远远低于少年，你正猜测他会如何处置你时，却看到他满脸愤怒。"
  },
  [200270080] = {
    id = 200270080,
    name = "「你、你看不起本王吗？！」"
  },
  [200270081] = {
    id = 200270081,
    name = "「啊？这话从何说起？」"
  },
  [200270082] = {
    id = 200270082,
    name = "「别以为我没看出你是故意输的，你的实力根本不止这样！」"
  },
  [200270083] = {
    id = 200270083,
    name = "「无论是魔法的施放速度、还是力量，你丝毫不逊色于我知道的那些有名探险者。」"
  },
  [200270084] = {
    id = 200270084,
    name = "「而且虽然你看似命中极低，实际上每一只即将逃掉的魔物都会被你精准击杀。」"
  },
  [200270085] = {
    id = 200270085,
    name = "「......」"
  },
  [200270086] = {
    id = 200270086,
    name = "你之前只是认为少年的身手不错，却没想到他的观察能力、分析能力竟然也如此优秀。"
  },
  [200270087] = {
    id = 200270087,
    name = "「我讨厌你们这些自以为是的大人！」"
  },
  [200270088] = {
    id = 200270088,
    name = "少年的语气中带着愤怒、委屈，以及些许的不甘。"
  },
  [200270089] = {
    id = 200270089,
    name = "「总觉得可以随便应付我们，在你们眼里，我们不管做什么都是胡闹。」"
  },
  [200270090] = {
    id = 200270090,
    name = "「抱歉......」"
  },
  [200270091] = {
    id = 200270091,
    name = "「我才不要你的道歉，你和其他大人一样，都只会糊弄我！」"
  },
  [200270092] = {
    id = 200270092,
    name = "少年带上自己的武器和工具跑走了，任你如何呼喊也没有停下。"
  },
  [200270093] = {
    id = 200270093,
    name = "看着他跑远的背影，你不禁怀疑起自己的选择。"
  },
  [200270094] = {
    id = 200270094,
    name = "你才不想和这个中二少年计较，绕道离去。"
  },
  [200270095] = {
    id = 200270095,
    name = "「呵呵，又是一个怂货。」"
  },
  [200280001] = {
    id = 200280001,
    name = "前面围了一群人。"
  },
  [200280002] = {
    id = 200280002,
    name = "「发生什么事了？」"
  },
  [200280003] = {
    id = 200280003,
    name = "「有人布下了一个擂台。」"
  },
  [200280004] = {
    id = 200280004,
    name = "「只要挑战者打败守擂的人，就能得到很多奖励！」"
  },
  [200280005] = {
    id = 200280005,
    name = "「哦，野外擂台啊。」"
  },
  [200280006] = {
    id = 200280006,
    name = "前方拥挤的人群围成了一个大圈，你好不容易才挤了进去。"
  },
  [200280007] = {
    id = 200280007,
    name = "「看样子又要输啦！」"
  },
  [200280008] = {
    id = 200280008,
    name = "「这家伙连一剑都接不了吧？」"
  },
  [200280009] = {
    id = 200280009,
    name = "「之前有个更强的都没撑住第一剑，这家伙肯定不行！」"
  },
  [200280010] = {
    id = 200280010,
    name = "「不过奖励是真丰厚啊！」"
  },
  [200280011] = {
    id = 200280011,
    name = "「那是自然，不然哪有这么多人挑战。」"
  },
  [200280012] = {
    id = 200280012,
    name = "「嗯？」"
  },
  [200280013] = {
    id = 200280013,
    name = "「丰厚的奖励？」"
  },
  [200280014] = {
    id = 200280014,
    name = "「是啊，你刚来的？」"
  },
  [200280015] = {
    id = 200280015,
    name = "「看见擂台中间的那个剑士没，只要击败他，后面桌子上的奖励就全是你的！」"
  },
  [200280016] = {
    id = 200280016,
    name = "你顺着围观者所指的方向看去，擂台后方桌子上堆积着不少财物，着实诱人。"
  },
  [200280017] = {
    id = 200280017,
    name = "正在此时，方才的挑战者被击飞了出去，引得一片惊呼。"
  },
  [200280018] = {
    id = 200280018,
    name = "趁中场休息，擂台的主办者又出来讲述规则。"
  },
  [200280019] = {
    id = 200280019,
    name = "「只需缴纳报名费，任何人都能成为挑战者！」"
  },
  [200280020] = {
    id = 200280020,
    name = "「大家缴纳的报名费一部分会投入后方的奖励中，另一部分则会用于慈善事业，资助深渊中的拾荒者、流浪者！」"
  },
  [200280021] = {
    id = 200280021,
    name = "「只要能击败擂主，便能带走后方的所有奖励！」"
  },
  [200280022] = {
    id = 200280022,
    name = "「勇敢的探险者们，不管是为了眼前的财富，还是为了奉献爱心，尽管来挑战吧！」"
  },
  [200280023] = {
    id = 200280023,
    name = "「喔喔喔喔喔！」"
  },
  [200280024] = {
    id = 200280024,
    name = "「告诉我，你们有梦想吗？！」"
  },
  [200280025] = {
    id = 200280025,
    name = "「有！」"
  },
  [200280026] = {
    id = 200280026,
    name = "「你们有爱心吗？！」"
  },
  [200280027] = {
    id = 200280027,
    name = "「有！」"
  },
  [200280028] = {
    id = 200280028,
    name = "「那么就来挑战吧！我们的擂主已经持续战斗了两小时，或许已有些疲惫，想要带走奖励的人可要抓紧咯。」"
  },
  [200280029] = {
    id = 200280029,
    name = "「已经报名的勇士稍安勿躁，请按缴纳费用的顺序，排队上场挑战。」"
  },
  [200280030] = {
    id = 200280030,
    name = "（似乎有点意思......）"
  },
  [200280031] = {
    id = 200280031,
    name = "你看了一眼四周，发现几名有些沮丧的人，遂上前和他们攀谈起来。"
  },
  [200280032] = {
    id = 200280032,
    name = "原来这几人是之前的挑战者，但无一例外都失败了。"
  },
  [200280033] = {
    id = 200280033,
    name = "你向他们问起擂主的情况，他们告诉你擂主力量极大，很少有人能挡下他的巨剑。"
  },
  [200280034] = {
    id = 200280034,
    name = "而且他看起来速度虽然不快，但浑身上下的铠甲极其坚硬，就算侥幸躲过第一剑，发起反击，也根本伤不了他。"
  },
  [200280035] = {
    id = 200280035,
    name = "「那家伙强得和怪物一样，不管如何攻防都没用！」"
  },
  [200280036] = {
    id = 200280036,
    name = "「就连被我最得意的重锤击中，他也根本没动弹一下！」"
  },
  [200280037] = {
    id = 200280037,
    name = "你看着此人背后巨大的长锤，不由一惊，被这样的武器全力一击却根本不动弹，这根本不是人类能做到的吧？！"
  },
  [200280038] = {
    id = 200280038,
    name = "你心里突然有了一个奇怪的想法，带着疑问和猜测，你观察起擂台上的战斗来。"
  },
  [200280039] = {
    id = 200280039,
    name = "挑战者一个接一个，有施展魔法的、有用剑的、用弓的......"
  },
  [200280040] = {
    id = 200280040,
    name = "被铠甲包裹住全身的擂主根本无惧，不管面对怎样的对手，他都只是挥剑、劈砍，根本不考虑防御。"
  },
  [200280041] = {
    id = 200280041,
    name = "而且无论对手怎样挑衅，他都巍然不动，等待挑战者先出手，可谓极其自信。"
  },
  [200280042] = {
    id = 200280042,
    name = "「原来如此。」"
  },
  [200280043] = {
    id = 200280043,
    name = "你已经明白了为何这名擂主能连续几个小时战斗也不疲惫，甚至无论承受怎样的攻击也不躲闪了。"
  },
  [200280044] = {
    id = 200280044,
    name = "它根本就不是人，而是一个极其精妙的人偶！所以才用铠甲包裹全身，所以才一句话也不说......"
  },
  [200280045] = {
    id = 200280045,
    name = "你决定揭穿主办者的骗术，于是大声喊道比赛是假的，擂主是一具人偶！"
  },
  [200280046] = {
    id = 200280046,
    name = "听到你的话，周围狂呼的人群一下安静了下来。"
  },
  [200280047] = {
    id = 200280047,
    name = "「胡说八道的王八蛋！你又没参与比赛，凭什么在这挑拨！」"
  },
  [200280048] = {
    id = 200280048,
    name = "「哼，你敢让擂主露脸吗？之所以遮盖起来，不就是怕人看到是个人偶吗？！」"
  },
  [200280049] = {
    id = 200280049,
    name = "「你、你你！」"
  },
  [200280050] = {
    id = 200280050,
    name = "「来人，协助斯盖尔大人解下头盔！」"
  },
  [200280051] = {
    id = 200280051,
    name = "主办者随即叫来两名助手，一边和擂主道歉，一边帮他取下巨大、厚重的头盔。"
  },
  [200280052] = {
    id = 200280052,
    name = "你心里隐隐感到不妙。"
  },
  [200280053] = {
    id = 200280053,
    name = "当头盔被两名助手取走的那一刻，你内心暗叹，完了......"
  },
  [200280054] = {
    id = 200280054,
    name = "头盔下是一张充满沧桑的中年人的脸，面部疤痕交错，一看就是从无数尸山血海中拼杀出来的战神。"
  },
  [200280055] = {
    id = 200280055,
    name = "他依然没有说话，只是静静地盯着你，让你不寒而栗。"
  },
  [200280056] = {
    id = 200280056,
    name = "（难道真的猜错了吗......）"
  },
  [200280057] = {
    id = 200280057,
    name = "「你们两个帮斯盖尔大人把头盔戴上。」"
  },
  [200280058] = {
    id = 200280058,
    name = "见头盔重新戴上后，主办者冲你破口大骂。"
  },
  [200280059] = {
    id = 200280059,
    name = "周围的人群也不断嘘你，甚至有几个冲动的准备冲过来教训你。"
  },
  [200280060] = {
    id = 200280060,
    name = "你见势不妙，匆匆逃离了此地。"
  },
  [200280061] = {
    id = 200280061,
    name = "你通过自己的观察和打听来的情报判断，这具人偶使用了极其精致的素材。"
  },
  [200280062] = {
    id = 200280062,
    name = "寻常攻击根本就奈何不了它，而且每次中场休息时，主办者都会出来串场，吸引众人的目光。"
  },
  [200280063] = {
    id = 200280063,
    name = "然后安排人员给擂主按摩、舒展身体，实际上是在维护人偶！"
  },
  [200280064] = {
    id = 200280064,
    name = "你本以为遇见了难得一见的高手，没想到却是这样，瞬间感觉非常扫兴。"
  },
  [200280065] = {
    id = 200280065,
    name = "你撇了撇嘴，目光扫了一圈狂呼的人群，默默离去。"
  },
  [200280066] = {
    id = 200280066,
    name = "通过观察，你有了对战计划，于是缴纳了报名费用，决定和这个人偶玩玩。"
  },
  [200280067] = {
    id = 200280067,
    name = "轰——！！"
  },
  [200280068] = {
    id = 200280068,
    name = "一名挑战者被一剑击飞，终于轮到你上场了。"
  },
  [200280069] = {
    id = 200280069,
    name = "你站在距离擂主三米之外，静静地与它对峙，据你之前的观察，这个距离便是他的攻击范围之外。"
  },
  [200280070] = {
    id = 200280070,
    name = "在围观的人看来，你与擂主似乎进入了高手决战时的状态，一瞬间人群安静下来，目不转睛地盯着你们。"
  },
  [200280071] = {
    id = 200280071,
    name = "然而几分钟后，你们却依然一动不动，观众们终于沉不住气了。"
  },
  [200280072] = {
    id = 200280072,
    name = "「你们还打不打？动手啊！」"
  },
  [200280073] = {
    id = 200280073,
    name = "「就是，装什么高手！」"
  },
  [200280074] = {
    id = 200280074,
    name = "「呵呵，看来这位挑战者也被我们的重甲剑士斯盖尔大人震慑住了。」"
  },
  [200280075] = {
    id = 200280075,
    name = "「不过这位挑战者，如果在十分钟内不出手，我们会判你输掉的哦。」"
  },
  [200280076] = {
    id = 200280076,
    name = "你自信一笑，既然已经验证了自己的想法，那么取胜的方法，你也已经找到。"
  },
  [200280077] = {
    id = 200280077,
    name = "你一脚向前踏出，擂主便举起巨剑向你挥来。"
  },
  [200280078] = {
    id = 200280078,
    name = "眼见擂主有了动作，你便将脚又撤了回来，擂主一剑挥空，随即停止了动作。"
  },
  [200280079] = {
    id = 200280079,
    name = "你又是一脚踏出，擂主再次挥剑......"
  },
  [200280080] = {
    id = 200280080,
    name = "如此反复几次后，擂主挥剑的动作很明显出现了一些不协调。"
  },
  [200280081] = {
    id = 200280081,
    name = "周围的观众开始嘈杂起来，对擂台上这奇怪的一幕感到好奇。"
  },
  [200280082] = {
    id = 200280082,
    name = "主办者则悄悄用毛巾擦拭着额头上的汗珠。"
  },
  [200280083] = {
    id = 200280083,
    name = "「挑战者，你再不出手，时间就快到了哦。」"
  },
  [200280084] = {
    id = 200280084,
    name = "你丝毫没理会他的催促，又是反复几次试探后，终于人偶的动作出现了差错。"
  },
  [200280085] = {
    id = 200280085,
    name = "你抓住时机一个闪身贴近人偶，对着他铠甲的缝隙，全力轰出一击魔法。"
  },
  [200280086] = {
    id = 200280086,
    name = "轰——！"
  },
  [200280087] = {
    id = 200280087,
    name = "伴随着巨响，擂主上身被你炸得四分五裂，手臂关节纷纷向四周飞去。"
  },
  [200280088] = {
    id = 200280088,
    name = "场下一片寂静。"
  },
  [200280089] = {
    id = 200280089,
    name = "「斯盖尔大人......败了？」"
  },
  [200280090] = {
    id = 200280090,
    name = "「噫，擂主的手臂怎么是金属——」"
  },
  [200280091] = {
    id = 200280091,
    name = "「啊！这个关节怎么是齿轮，也没血迹！」"
  },
  [200280092] = {
    id = 200280092,
    name = "全场陷入混乱，人们涌上擂台观察擂主，却见其内部竟然全部是由金属、矿物组成！"
  },
  [200280093] = {
    id = 200280093,
    name = "愤怒的人群冲向了主办者，冲向了摆放奖励的桌子......"
  },
  [200280094] = {
    id = 200280094,
    name = "你看着和主办者们扭打在一起的人群，不禁摇了摇头。"
  },
  [200280095] = {
    id = 200280095,
    name = "「总之揭穿了他们的把戏，希望这些人以后也能长点教训。」"
  },
  [200280096] = {
    id = 200280096,
    name = "你正欲离去，突然想到挑战前自己缴纳的巨额报名费，正打算取回，却见混乱的人群早已将桌上的财物哄抢一空。"
  },
  [200280097] = {
    id = 200280097,
    name = "而主办者也已经被扒掉衣服捆了起来，看样子他携带的财物也被抢光了。"
  },
  [200280098] = {
    id = 200280098,
    name = "你叹了口气，只能认栽。"
  },
  [200280099] = {
    id = 200280099,
    name = "擂主的实力引起了你的兴趣，你决定直接报名参加，和擂主切磋一番。"
  },
  [200280100] = {
    id = 200280100,
    name = "「我倒要看看，他有多厉害。」"
  },
  [200280101] = {
    id = 200280101,
    name = "你支付了高昂的报名费，静静等待着出场机会。"
  },
  [200280102] = {
    id = 200280102,
    name = "轰——！！"
  },
  [200280103] = {
    id = 200280103,
    name = "一名挑战者被一剑击飞，终于轮到你上场了。"
  },
  [200280104] = {
    id = 200280104,
    name = "你根据最近几个对手的情况来分析，觉得擂主应该是力量型对手，他全身包裹严密的铠甲极大地提升了他的防护能力。"
  },
  [200280105] = {
    id = 200280105,
    name = "所以他不需要思考防守，只需全力进攻，便无人能挡下他的巨剑。"
  },
  [200280106] = {
    id = 200280106,
    name = "你已然有了计划，这种类型的对手只要以速度压制他，便有极高的取胜可能性。"
  },
  [200280107] = {
    id = 200280107,
    name = "比试开始的哨声响起，你立即展开攻击，以灵活敏捷的身形应对着他的挥砍，根本不和他正面对抗。"
  },
  [200280108] = {
    id = 200280108,
    name = "然后趁他攻击之时，不断施放魔法进行轰炸，试图打乱他的进攻节奏，寻找制胜时机。"
  },
  [200280109] = {
    id = 200280109,
    name = "「竟然接连躲过了好几剑，他说不定有机会！」"
  },
  [200280110] = {
    id = 200280110,
    name = "「是啊，看上去普普通通，没想到是个高手！」"
  },
  [200280111] = {
    id = 200280111,
    name = "你根本不理会周围的闲言碎语，专心应对着眼前的对手。"
  },
  [200280112] = {
    id = 200280112,
    name = "你们的战斗又持续了一段时间，你感觉自己的体力消耗已经开始增大，但却依然感受不到擂主紊乱的气息。"
  },
  [200280113] = {
    id = 200280113,
    name = "不，是从一开始到现在也没感受到他的气息！"
  },
  [200280114] = {
    id = 200280114,
    name = "（绝对的高手！不过在这种地方，真的有人能修行到这种程度吗？）"
  },
  [200280115] = {
    id = 200280115,
    name = "你心里有些疑惑，但不敢分心，而此时擂主的攻击也变得越来越凌厉。"
  },
  [200280116] = {
    id = 200280116,
    name = "嗖，你躲过了擂主横挥的一剑。"
  },
  [200280117] = {
    id = 200280117,
    name = "（好机会！）"
  },
  [200280118] = {
    id = 200280118,
    name = "趁擂主尚无法调整身姿，你凝聚魔力，闪身至他身前，准备发动决胜的一击。"
  },
  [200280119] = {
    id = 200280119,
    name = "然而就在这关键之际，擂主的一只手，以一种常人绝对无法做到的姿势向你挥来。"
  },
  [200280120] = {
    id = 200280120,
    name = "轰！！！"
  },
  [200280121] = {
    id = 200280121,
    name = "一声巨响后，你已经在擂台之外了。"
  },
  [200280122] = {
    id = 200280122,
    name = "你大口喘着粗气，刚才的一幕仍令你心有余悸。"
  },
  [200280123] = {
    id = 200280123,
    name = "如果不是你及时进行防御，恐怕此刻已凶多吉少。"
  },
  [200280124] = {
    id = 200280124,
    name = "你向擂台看去，擂主仍无任何气息波动，似乎与你的战斗只是玩玩而已。"
  },
  [200280125] = {
    id = 200280125,
    name = "虽然关于擂主你还有诸多疑问，但此刻已难以得到答案。"
  },
  [200280126] = {
    id = 200280126,
    name = "在围观群众的呼喊声、以及主办者亢奋的叫声中，你默默离去。"
  },
  [200280127] = {
    id = 200280127,
    name = "主办者所说的内容总让你觉得不舒服，而且擂台上的擂主看起来也有些奇怪......"
  },
  [200280128] = {
    id = 200280128,
    name = "你决定不去趟这趟浑水，选择在一旁默默观战。"
  },
  [200280129] = {
    id = 200280129,
    name = "然而战斗并没有想象中那么激烈，挑战者几乎都是被轻易解决，这样的战斗让你觉得非常无趣。"
  },
  [200280130] = {
    id = 200280130,
    name = "你撇了撇嘴，目光扫了一圈狂欢的人群，默默离去。"
  },
  [200280131] = {
    id = 200280131,
    name = "对长期在深渊活跃的探险者而言，这种野外擂台并不陌生。"
  },
  [200280132] = {
    id = 200280132,
    name = "除了能打发枯燥的探险生活外，也算是一种不错的修行方式。"
  },
  [200280133] = {
    id = 200280133,
    name = "不过这种野外擂台也伴随着诸多问题，比如难以控制的伤亡、巨大的赌注等等。"
  },
  [200280134] = {
    id = 200280134,
    name = "若无必要，你不想卷入这些事件。"
  },
  [200290001] = {
    id = 200290001,
    name = "「哇，打起来，打起来！」"
  },
  [200290002] = {
    id = 200290002,
    name = "「......」"
  },
  [200290003] = {
    id = 200290003,
    name = "（那边在干什么？）"
  },
  [200290004] = {
    id = 200290004,
    name = "一群探险者围成了一个圈。"
  },
  [200290005] = {
    id = 200290005,
    name = "看起来都是些好事之徒。"
  },
  [200290006] = {
    id = 200290006,
    name = "他们不断起哄，挑唆着什么。"
  },
  [200290007] = {
    id = 200290007,
    name = "你走近了些，看见两个人正剑拔弩张。"
  },
  [200290008] = {
    id = 200290008,
    name = "一个是手持法杖的少年。"
  },
  [200290009] = {
    id = 200290009,
    name = "另一个是身穿软甲的剑士。"
  },
  [200290010] = {
    id = 200290010,
    name = "「你是聋子吗？还要我再说一次？」"
  },
  [200290011] = {
    id = 200290011,
    name = "「那我就再说一次。」"
  },
  [200290012] = {
    id = 200290012,
    name = "「听好啦！对不起啦！」"
  },
  [200290013] = {
    id = 200290013,
    name = "「下次遇见你，我一定不会这样说啦！」"
  },
  [200290014] = {
    id = 200290014,
    name = "「这不是道歉就能解决的事！」"
  },
  [200290015] = {
    id = 200290015,
    name = "「你侮辱我的剑术，就是侮辱我的家族传承！」"
  },
  [200290016] = {
    id = 200290016,
    name = "「这件事不能这么算了，我要和你堂堂正正地决斗！」"
  },
  [200290017] = {
    id = 200290017,
    name = "少年似乎并不想和这剑士过多纠缠，也没理会剑士丢过来的手套。"
  },
  [200290018] = {
    id = 200290018,
    name = "甚至在剑士已经极其愤怒的情况下，一记魔法将那手套给烧了。"
  },
  [200290019] = {
    id = 200290019,
    name = "「你欺人太甚！」"
  },
  [200290020] = {
    id = 200290020,
    name = "剑士拔剑就要冲上去，却又被少年偷袭点着了头发。"
  },
  [200290021] = {
    id = 200290021,
    name = "「我的头发！」"
  },
  [200290022] = {
    id = 200290022,
    name = "「哼，我都说了别再纠缠我了！」"
  },
  [200290023] = {
    id = 200290023,
    name = "「信不信我把你烤了拿去喂魔物！」"
  },
  [200290024] = {
    id = 200290024,
    name = "法杖顶部又出现了一团火球。"
  },
  [200290025] = {
    id = 200290025,
    name = "「哇，不会来真的吧？」"
  },
  [200290026] = {
    id = 200290026,
    name = "你走到二人中间。"
  },
  [200290027] = {
    id = 200290027,
    name = "「你干嘛？替他打抱不平啊？」"
  },
  [200290028] = {
    id = 200290028,
    name = "「没有。」"
  },
  [200290029] = {
    id = 200290029,
    name = "「只是小误会的话，没必要闹到这个地步吧。」"
  },
  [200290030] = {
    id = 200290030,
    name = "「是他一直在纠缠我诶。」"
  },
  [200290031] = {
    id = 200290031,
    name = "「打架打输了。」"
  },
  [200290032] = {
    id = 200290032,
    name = "「竟然不愿意承认自己的剑术垃圾。」"
  },
  [200290033] = {
    id = 200290033,
    name = "「你明明是偷袭！」"
  },
  [200290034] = {
    id = 200290034,
    name = "「剑士本来就会面临各种各样的战斗环境吧。」"
  },
  [200290035] = {
    id = 200290035,
    name = "「嗯？等等。」"
  },
  [200290036] = {
    id = 200290036,
    name = "「你身上也有魔力流动的气息！」"
  },
  [200290037] = {
    id = 200290037,
    name = "「被我发现了！」"
  },
  [200290038] = {
    id = 200290038,
    name = "「既然如此，你想替他打抱不平，就接受我的挑战吧！」"
  },
  [200290039] = {
    id = 200290039,
    name = "「哈哈，竟然这么自信，比魔法的话我可不会输给任何人！」"
  },
  [200290040] = {
    id = 200290040,
    name = "说罢，你二人摆出决斗姿态，准备动手。"
  },
  [200290041] = {
    id = 200290041,
    name = "——轰！远处突然响起一阵轰鸣声。"
  },
  [200290042] = {
    id = 200290042,
    name = "「天！那边是什么？」"
  },
  [200290043] = {
    id = 200290043,
    name = "「是魔物，一大群魔物！快逃！」"
  },
  [200290044] = {
    id = 200290044,
    name = "「什么？魔物！」"
  },
  [200290045] = {
    id = 200290045,
    name = "「既然如此，一击定胜负吧！」"
  },
  [200290046] = {
    id = 200290046,
    name = "他迅速吟唱起咒语，双手高举法杖，将无数魔力汇聚，你隐隐感受到了其中蕴含的巨大破坏力。"
  },
  [200290047] = {
    id = 200290047,
    name = "（竟然如此拼命，如果与他对攻，恐怕会波及周围混乱的人群，他也会受伤......）"
  },
  [200290048] = {
    id = 200290048,
    name = "趁他蓄力尚未完成，你一个加速闪身到他背后，对着他的腋窝抓挠。"
  },
  [200290049] = {
    id = 200290049,
    name = "「噗，你、你这家伙竟然——啊哈哈哈哈，停下，快停下，哈哈哈哈......」"
  },
  [200290050] = {
    id = 200290050,
    name = "你并未停手，直到少年认输你才罢休。"
  },
  [200290051] = {
    id = 200290051,
    name = "「你、你这是耍赖！」"
  },
  [200290052] = {
    id = 200290052,
    name = "「你既然已经认输，说这些还有什么用？而且你也没说不能使用这些手段。」"
  },
  [200290053] = {
    id = 200290053,
    name = "「你！哼！要是等我蓄力完成，你绝对无法接下我这一击！」"
  },
  [200290054] = {
    id = 200290054,
    name = "「已经结束了，快走吧，魔物马上到了。」"
  },
  [200290055] = {
    id = 200290055,
    name = "「呜哇，这么近了！」"
  },
  [200290056] = {
    id = 200290056,
    name = "你们紧跟着人群逃散，终于在魔物袭来前撤退到了安全的地方。"
  },
  [200290057] = {
    id = 200290057,
    name = "你见状立刻认输。"
  },
  [200290058] = {
    id = 200290058,
    name = "「哼，感受到我的力量了吗？真是明智的决定！」"
  },
  [200290059] = {
    id = 200290059,
    name = "「无敌真是寂寞啊！」"
  },
  [200290060] = {
    id = 200290060,
    name = "你看到远处的滚滚烟尘，转身就跑。"
  },
  [200290061] = {
    id = 200290061,
    name = "「快走吧，魔物来了！」"
  },
  [200290062] = {
    id = 200290062,
    name = "「哇，那个剑士竟然已经跑了？！嘁，我还以为有多勇敢呢！」"
  },
  [200290063] = {
    id = 200290063,
    name = "不多时，魔物群如山呼海啸般踏过此地。"
  },
  [200290064] = {
    id = 200290064,
    name = "所幸你们全都撤退到了安全的地方。"
  },
  [200290065] = {
    id = 200290065,
    name = "「嘁，不敢打就走开，别多管闲事。」"
  },
  [200290066] = {
    id = 200290066,
    name = "——轰！远处突然响起一阵轰鸣声。"
  },
  [200290067] = {
    id = 200290067,
    name = "「天！那边是什么？」"
  },
  [200290068] = {
    id = 200290068,
    name = "「是魔物，一大群魔物！快跑！」"
  },
  [200290069] = {
    id = 200290069,
    name = "「什么？魔物！」"
  },
  [200290070] = {
    id = 200290070,
    name = "「哼，算你们走运。」"
  },
  [200290071] = {
    id = 200290071,
    name = "魔法少年正准备撤离，却发现剑士拦住了自己。"
  },
  [200290072] = {
    id = 200290072,
    name = "「魔物群就要来了，有什么先离开这里再说吧！」"
  },
  [200290073] = {
    id = 200290073,
    name = "剑士十分固执，拦下少年不让他走。"
  },
  [200290074] = {
    id = 200290074,
    name = "「你今天必须和我堂堂正正地决斗！」"
  },
  [200290075] = {
    id = 200290075,
    name = "「否则大家都死在这里吧！」"
  },
  [200290076] = {
    id = 200290076,
    name = "「你他妈是个疯子吧？！」"
  },
  [200290077] = {
    id = 200290077,
    name = "——轰隆隆。"
  },
  [200290078] = {
    id = 200290078,
    name = "魔物群已经逼近，你们已经失去了逃跑的机会。"
  },
  [200290079] = {
    id = 200290079,
    name = "「你们两个，想活命的话就帮我！」"
  },
  [200290080] = {
    id = 200290080,
    name = "你全力施放魔法，轰击魔物群前方地面，二人见状瞬间明白了你的意图。"
  },
  [200290081] = {
    id = 200290081,
    name = "一时间无数剑光、魔法拼命在魔物前方的地面闪耀、爆炸，溅起漫天烟尘。"
  },
  [200290082] = {
    id = 200290082,
    name = "随即你们三人抓住时机向附近丛林逃去。"
  },
  [200290083] = {
    id = 200290083,
    name = "「呼哈......呼哈......总算到安全的地方了。」"
  },
  [200290084] = {
    id = 200290084,
    name = "「没想到你竟然有这样的实力，但你为什么不肯和我较量呢？」"
  },
  [200290085] = {
    id = 200290085,
    name = "「不想卷入麻烦。」"
  },
  [200290086] = {
    id = 200290086,
    name = "「麻烦吗......呵呵。」"
  },
  [200290087] = {
    id = 200290087,
    name = "「果然就像老头子说的，真正的强者，不需要强迫别人认同自己。」"
  },
  [200290088] = {
    id = 200290088,
    name = "「我清楚我和你的差距，但我不会放弃的。」"
  },
  [200290089] = {
    id = 200290089,
    name = "「总有一天，我会赢过你。」"
  },
  [200290090] = {
    id = 200290090,
    name = "魔法少年有些落寞地走了，剑士也陷入了沉思。"
  },
  [200290091] = {
    id = 200290091,
    name = "你见他眉头紧锁的模样，摇了摇头也离开了。"
  },
  [200290092] = {
    id = 200290092,
    name = "少年趁剑士不备，抬手便是一记魔法。"
  },
  [200290093] = {
    id = 200290093,
    name = "「你，你对我做了什么？！」"
  },
  [200290094] = {
    id = 200290094,
    name = "「全身好麻......不能动了......」"
  },
  [200290095] = {
    id = 200290095,
    name = "剑士被定在原地，魔法少年扬长而去。"
  },
  [200290096] = {
    id = 200290096,
    name = "「手下败将，别再来惹我了。」"
  },
  [200290097] = {
    id = 200290097,
    name = "说罢少年紧跟着逃散的人群离去，只留下剑士痛苦地伫立在原地。"
  },
  [200290098] = {
    id = 200290098,
    name = "你叹了口气，帮他解除了魔法。"
  },
  [200290099] = {
    id = 200290099,
    name = "不多时，魔物群逼近，卷起漫天烟尘。"
  },
  [200290100] = {
    id = 200290100,
    name = "你带着剑士到达了安全的地方。"
  },
  [200290101] = {
    id = 200290101,
    name = "目睹魔物群巨大破坏力的剑士仍心有余悸。"
  },
  [200290102] = {
    id = 200290102,
    name = "「刚才要不是你，我已经被魔物踩成肉酱了。」"
  },
  [200290103] = {
    id = 200290103,
    name = "「小事一桩，我走了。」"
  },
  [200290104] = {
    id = 200290104,
    name = "「嗯，我也该去做我的事了。」"
  },
  [200290105] = {
    id = 200290105,
    name = "「我会继续寻找他，和他堂堂正正地决斗！」"
  },
  [200290106] = {
    id = 200290106,
    name = "「依靠偷袭得来的胜利，我绝不会认同！」"
  },
  [200290107] = {
    id = 200290107,
    name = "——轰！远处突然响起一阵轰鸣声。"
  },
  [200290108] = {
    id = 200290108,
    name = "「天！那边是什么？」"
  },
  [200290109] = {
    id = 200290109,
    name = "「是魔物，一大群魔物！快逃！」"
  },
  [200290110] = {
    id = 200290110,
    name = "「什么？魔物！」"
  },
  [200290111] = {
    id = 200290111,
    name = "「哼，算你捡回一条命。」"
  },
  [200290112] = {
    id = 200290112,
    name = "魔法少年正准备走，又被剑士拦下。"
  },
  [200290113] = {
    id = 200290113,
    name = "「不许走！先和我决战！」"
  },
  [200290114] = {
    id = 200290114,
    name = "「我说过，别纠缠我！」"
  },
  [200290115] = {
    id = 200290115,
    name = "眼看剑士又要与少年打起来。"
  },
  [200290116] = {
    id = 200290116,
    name = "「魔物就要来了，有什么先离开这里再说吧！」"
  },
  [200290117] = {
    id = 200290117,
    name = "「哎呀，他说得没错，我先溜了！」"
  },
  [200290118] = {
    id = 200290118,
    name = "魔法少年钻进了人群，溜得飞快。"
  },
  [200290119] = {
    id = 200290119,
    name = "「喂！」"
  },
  [200290120] = {
    id = 200290120,
    name = "趁魔物来临之前，你们全都撤退到了安全的地方。"
  },
  [200290121] = {
    id = 200290121,
    name = "你被人流推搡着离开了。"
  },
  [200290122] = {
    id = 200290122,
    name = "「你要真有本事就先解决那群魔物证明自己吧！」"
  },
  [200290123] = {
    id = 200290123,
    name = "「本大爷不奉陪了！」"
  },
  [200290124] = {
    id = 200290124,
    name = "人们快速找到了安全的地方藏了起来。"
  },
  [200290125] = {
    id = 200290125,
    name = "不多时，魔物群如山呼海啸冲来，大地都为之颤动......"
  },
  [200290126] = {
    id = 200290126,
    name = "你再次回到了刚才的地方。"
  },
  [200290127] = {
    id = 200290127,
    name = "只见魔法少年呆呆地站在原地。"
  },
  [200290128] = {
    id = 200290128,
    name = "他正对着一具残破的软甲喃喃自语。"
  },
  [200290129] = {
    id = 200290129,
    name = "「真是又笨又傻。」"
  },
  [200290130] = {
    id = 200290130,
    name = "「那么大群魔物，你还真敢正面对抗......」"
  },
  [200290131] = {
    id = 200290131,
    name = "这时，他发现你在看他。"
  },
  [200290132] = {
    id = 200290132,
    name = "「干什么？想打一场吗？」"
  },
  [200290133] = {
    id = 200290133,
    name = "你摇摇头，转身离开了。"
  },
  [200300001] = {
    id = 200300001,
    name = "你走进一片密林，突然发现前方树下有个人影。"
  },
  [200300002] = {
    id = 200300002,
    name = "（......尸体？）"
  },
  [200300003] = {
    id = 200300003,
    name = "你靠近些，发现那人脸上满是尘土和干涸的血迹。"
  },
  [200300004] = {
    id = 200300004,
    name = "这时，那人动了动，吓了你一跳。"
  },
  [200300005] = {
    id = 200300005,
    name = "「还活着啊。」"
  },
  [200300006] = {
    id = 200300006,
    name = "眼前的人缓缓站了起来。"
  },
  [200300007] = {
    id = 200300007,
    name = "他的脖子晃了一下，头旋转一圈后耷拉在了肩膀上。"
  },
  [200300008] = {
    id = 200300008,
    name = "他跛着腿，一瘸一拐朝你走来，你谨慎地后退几步。"
  },
  [200300009] = {
    id = 200300009,
    name = "「怎么回事？」"
  },
  [200300010] = {
    id = 200300010,
