  [200320052] = {
    id = 200320052,
    name = "眼看魔物袭来，这具身体所感受到的绝望和恐惧都无比清晰地传递给了你。"
  },
  [200320053] = {
    id = 200320053,
    name = "「到此...为止了吗......」"
  },
  [200320054] = {
    id = 200320054,
    name = "你猛然惊醒，回到了森林。发现几头魔物正环伺在你周围，见你有所动静，瞬间全扑了上来！"
  },
  [200320055] = {
    id = 200320055,
    name = "你顾不得形象姿态，只能就地连滚几圈，虽然很狼狈，但躲过了魔物致命的攻击。"
  },
  [200320056] = {
    id = 200320056,
    name = "随即你调整身形，对魔物展开反击。"
  },
  [200320057] = {
    id = 200320057,
    name = "「好险，要是再晚一点，就成魔物的口粮了。」"
  },
  [200320058] = {
    id = 200320058,
    name = "刚才发生的一切令你心有余悸，不敢在此多做停留。"
  },
  [200320059] = {
    id = 200320059,
    name = "趁这会儿迷雾消散，你快速离开了森林。"
  },
  [200320060] = {
    id = 200320060,
    name = "「应该是某种幻境。」"
  },
  [200320061] = {
    id = 200320061,
    name = "「还是不要沉迷于此了，尽快找到出口更重要。」"
  },
  [200320062] = {
    id = 200320062,
    name = "你放弃了解读，四下寻找出口。"
  },
  [200320063] = {
    id = 200320063,
    name = "这时，巨剑开始扭曲、变化，最终化作尘埃，消散于天地之间。"
  },
  [200320064] = {
    id = 200320064,
    name = "遥远的地平线上雾霭茫茫。"
  },
  [200320065] = {
    id = 200320065,
    name = "从远方吹来的风夹带着紫蓝色的雪。"
  },
  [200320066] = {
    id = 200320066,
    name = "仔细看去那并不是雪。"
  },
  [200320067] = {
    id = 200320067,
    name = "晶莹的颗粒像雪一般，布满了整个天空。"
  },
  [200320068] = {
    id = 200320068,
    name = "你心中不安，伸手抓住那些颗粒，手心一阵刺痛。"
  },
  [200320069] = {
    id = 200320069,
    name = "眼前的景象瞬间消散，痛感让你从幻境中清醒了过来。"
  },
  [200320070] = {
    id = 200320070,
    name = "「呼，幸好，差点就迷失在幻境里了。」"
  },
  [200320071] = {
    id = 200320071,
    name = "「咦，这是......」"
  },
  [200320072] = {
    id = 200320072,
    name = "你摊开手心。"
  },
  [200320073] = {
    id = 200320073,
    name = "手心赫然躺着你刚在抓住的晶莹颗粒。"
  },
  [200320074] = {
    id = 200320074,
    name = "「三千勇士啊——」"
  },
  [200320075] = {
    id = 200320075,
    name = "「请守护这个国度。」"
  },
  [200320076] = {
    id = 200320076,
    name = "「愿这片土地永世昌盛。」"
  },
  [200320077] = {
    id = 200320077,
    name = "你听到有人在祈祷。"
  },
  [200320078] = {
    id = 200320078,
    name = "此刻，一股吸力猛地将你吸入剑中。"
  },
  [200320079] = {
    id = 200320079,
    name = "而你也看清了「外面」的景象。"
  },
  [200320080] = {
    id = 200320080,
    name = "千万人垂头跪在你的面前。"
  },
  [200320081] = {
    id = 200320081,
    name = "最前面的人，头顶着黄金冕旒。"
  },
  [200320082] = {
    id = 200320082,
    name = "他以敬重又怀念的目光仰望着。"
  },
  [200320083] = {
    id = 200320083,
    name = "他应该是这片土地的帝王。"
  },
  [200320084] = {
    id = 200320084,
    name = "而后，他转身宣布了一句话。"
  },
  [200320085] = {
    id = 200320085,
    name = "你并没有听清，但千万人开始沸腾了。"
  },
  [200320086] = {
    id = 200320086,
    name = "他们欢呼着，笑中带着泪水。"
  },
  [200320087] = {
    id = 200320087,
    name = "说不清是激动多一些，还是悲伤多一些。"
  },
  [200320088] = {
    id = 200320088,
    name = "王缓缓朝你走来。"
  },
  [200320089] = {
    id = 200320089,
    name = "你看到他的手伸向剑碑，就像要触摸到你一般——"
  },
  [200320090] = {
    id = 200320090,
    name = "「我的勇士啊，这就是......你所期望的吗......」"
  },
  [200320091] = {
    id = 200320091,
    name = "一滴泪从他眼角落下......"
  },
  [200320092] = {
    id = 200320092,
    name = "眼前的景象消失了。"
  },
  [200320093] = {
    id = 200320093,
    name = "你身处森林的边缘。"
  },
  [200320094] = {
    id = 200320094,
    name = "「刚才......是幻觉吗？」"
  },
  [200320095] = {
    id = 200320095,
    name = "「原来已经走出那片迷雾了啊。」"
  },
  [200320096] = {
    id = 200320096,
    name = "你发现手心多了一样东西，晶莹剔透，彷如凝结的泪珠。"
  },
  [200320097] = {
    id = 200320097,
    name = "巨剑开始扭曲、变化，最终化作尘埃，消散于天地之间。"
  },
  [200320098] = {
    id = 200320098,
    name = "遥远的地平线上雾霭茫茫。"
  },
  [200320099] = {
    id = 200320099,
    name = "从远方吹来的风夹带着紫蓝色的雪。"
  },
  [200320100] = {
    id = 200320100,
    name = "仔细看去那并不是雪。"
  },
  [200320101] = {
    id = 200320101,
    name = "晶莹的颗粒像雪一般，布满了整个天空。"
  },
  [200320102] = {
    id = 200320102,
    name = "脚边的路断断续续延伸向荒野尽头。"
  },
  [200320103] = {
    id = 200320103,
    name = "你沿着路一直走，走进了一片迷雾。"
  },
  [200320104] = {
    id = 200320104,
    name = "穿过迷雾后，你又回到了森林。"
  },
  [200320105] = {
    id = 200320105,
    name = "你刚恢复视野，便对上了几头魔物凶厉的目光......"
  },
  [200320106] = {
    id = 200320106,
    name = "你杀掉了魔物，离开了这片森林。"
  },
  [200320107] = {
    id = 200320107,
    name = "你一动不动，听见上空传来钟声。"
  },
  [200320108] = {
    id = 200320108,
    name = "孩童们合唱着人类的颂歌。"
  },
  [200320109] = {
    id = 200320109,
    name = "歌唱着曾经的荣耀与永恒；"
  },
  [200320110] = {
    id = 200320110,
    name = "歌唱着英雄的战斗；"
  },
  [200320111] = {
    id = 200320111,
    name = "歌唱着人类的牺牲；"
  },
  [200320112] = {
    id = 200320112,
    name = "歌唱着光明降临，世界团结——"
  },
  [200320113] = {
    id = 200320113,
    name = "最终建立了自由国度的叙事长诗。"
  },
  [200320114] = {
    id = 200320114,
    name = "而后，建筑坍塌，从天空坠落......"
  },
  [200320115] = {
    id = 200320115,
    name = "宛如末日流星。"
  },
  [200320116] = {
    id = 200320116,
    name = "你被黑暗笼罩，浑身剧痛。"
  },
  [200320117] = {
    id = 200320117,
    name = "「唔！」"
  },
  [200320118] = {
    id = 200320118,
    name = "再次睁眼，疼痛感瞬间消散，雾气也早已散去。"
  },
  [200320119] = {
    id = 200320119,
    name = "在天黑之前，你终于走出了这片诡异的森林。"
  },
  [200330001] = {
    id = 200330001,
    name = "「那边的，请过来一下。」"
  },
  [200330002] = {
    id = 200330002,
    name = "你看到有个探险者在朝你招手。"
  },
  [200330003] = {
    id = 200330003,
    name = "他蹲在地上，露出苦恼的表情。"
  },
  [200330004] = {
    id = 200330004,
    name = "「是在叫我？」"
  },
  [200330005] = {
    id = 200330005,
    name = "「附近没别人了，当然是叫你。」"
  },
  [200330006] = {
    id = 200330006,
    name = "「该死的！」"
  },
  [200330007] = {
    id = 200330007,
    name = "「那个可恶的盗贼偷走了我重要的魔法笔记。」"
  },
  [200330008] = {
    id = 200330008,
    name = "「还把我弄伤了！」"
  },
  [200330009] = {
    id = 200330009,
    name = "「那你还真倒霉啊。」"
  },
  [200330010] = {
    id = 200330010,
    name = "「别光顾着同情我，探险者之间互帮互助不是应该的吗？」"
  },
  [200330011] = {
    id = 200330011,
    name = "「既然盗贼已经走了，你在这里蹲着也找不回来吧。」"
  },
  [200330012] = {
    id = 200330012,
    name = "「那个家伙是惯偷！」"
  },
  [200330013] = {
    id = 200330013,
    name = "「我打听到好几个人在这里遭殃了！」"
  },
  [200330014] = {
    id = 200330014,
    name = "「那个家伙一定还会在这里出现！」"
  },
  [200330015] = {
    id = 200330015,
    name = "「你也别在那里站着。」"
  },
  [200330016] = {
    id = 200330016,
    name = "「快点过来蹲下，别被他发现了！」"
  },
  [200330017] = {
    id = 200330017,
    name = "探险者满意地点点头。"
  },
  [200330018] = {
    id = 200330018,
    name = "「有正义感的人。」"
  },
  [200330019] = {
    id = 200330019,
    name = "没过多久，前方出现了一个少年。"
  },
  [200330020] = {
    id = 200330020,
    name = "「来了，就是他！」"
  },
  [200330021] = {
    id = 200330021,
    name = "「竟然真等到了？！」"
  },
  [200330022] = {
    id = 200330022,
    name = "「就是这个家伙，夺走了我的魔法笔记！」"
  },
  [200330023] = {
    id = 200330023,
    name = "「你打算怎么拿回来？」"
  },
  [200330024] = {
    id = 200330024,
    name = "「你帮帮我......」"
  },
  [200330025] = {
    id = 200330025,
    name = "「？？？」"
  },
  [200330026] = {
    id = 200330026,
    name = "「我受伤了，没有足够的把握能抓住他。」"
  },
  [200330027] = {
    id = 200330027,
    name = "「放心，我会协助你的！」"
  },
  [200330028] = {
    id = 200330028,
    name = "你拒绝了探险者，他显得很失望。"
  },
  [200330029] = {
    id = 200330029,
    name = "「我还以为你是一个心怀正义的人。」"
  },
  [200330030] = {
    id = 200330030,
    name = "「没想到也是一个胆小鬼。」"
  },
  [200330031] = {
    id = 200330031,
    name = "「可我从一开始就没有答应帮你啊？」"
  },
  [200330032] = {
    id = 200330032,
    name = "「......」"
  },
  [200330033] = {
    id = 200330033,
    name = "「既然这样，我自己去！」"
  },
  [200330034] = {
    id = 200330034,
    name = "他一瘸一拐追了过去，少年很快注意到了他。"
  },
  [200330035] = {
    id = 200330035,
    name = "「嗤，原来是被宰过的肥羊啊。」"
  },
  [200330036] = {
    id = 200330036,
    name = "少年说罢迅速离去，根本不给他动手的机会。"
  },
  [200330037] = {
    id = 200330037,
    name = "「你给我站住！」"
  },
  [200330038] = {
    id = 200330038,
    name = "「......」"
  },
  [200330039] = {
    id = 200330039,
    name = "（这样也不可能抓住吧。）"
  },
  [200330040] = {
    id = 200330040,
    name = "你摇了摇头，离开了这里。"
  },
  [200330041] = {
    id = 200330041,
    name = "你看着他逃走了。"
  },
  [200330042] = {
    id = 200330042,
    name = "探险者气鼓鼓地赶了上来。"
  },
  [200330043] = {
    id = 200330043,
    name = "「你怎么不追啊？」"
  },
  [200330044] = {
    id = 200330044,
    name = "「你把他吓跑了，我怎么追？」"
  },
  [200330045] = {
    id = 200330045,
    name = "「......这个时候你不该追上去吗？」"
  },
  [200330046] = {
    id = 200330046,
    name = "「追上去做什么？」"
  },
  [200330047] = {
    id = 200330047,
    name = "「掉入你们的陷阱吗？」"
  },
  [200330048] = {
    id = 200330048,
    name = "「什么......陷阱？」"
  },
  [200330049] = {
    id = 200330049,
    name = "「你故意踩断树枝，就是想提醒他吧？」"
  },
  [200330050] = {
    id = 200330050,
    name = "「这样他就能引诱我追上去，然后掉进你们早就设好的陷阱里。」"
  },
  [200330051] = {
    id = 200330051,
    name = "「我不知道你在说什么。」"
  },
  [200330052] = {
    id = 200330052,
    name = "「不帮忙就算了，还质疑受害者！」"
  },
  [200330053] = {
    id = 200330053,
    name = "「怎么会有你这么可恶的人！」"
  },
  [200330054] = {
    id = 200330054,
    name = "你突然感觉到危险，将探险者一把拉过来，自己则迅速闪到一旁。"
  },
  [200330055] = {
    id = 200330055,
    name = "一个大网落在了你刚才站的位置。"
  },
  [200330056] = {
    id = 200330056,
    name = "此刻那探险者正在网中挣扎，他越挣扎，网就束缚得越紧。"
  },
  [200330057] = {
    id = 200330057,
    name = "「嘁，竟然失手了！」"
  },
  [200330058] = {
    id = 200330058,
    name = "少年见偷袭失败，便迅速消失了。"
  },
  [200330059] = {
    id = 200330059,
    name = "「怎么会这样，救我啊！」"
  },
  [200330060] = {
    id = 200330060,
    name = "「还真是自作自受。」"
  },
  [200330061] = {
    id = 200330061,
    name = "你将他搜刮了一番，得到不少东西，心情舒畅地离开了。"
  },
  [200330062] = {
    id = 200330062,
    name = "你点点头，探险者露出惊喜的目光。"
  },
  [200330063] = {
    id = 200330063,
    name = "「我就知道你是一个正义的人!」"
  },
  [200330064] = {
    id = 200330064,
    name = "「他要开始布置陷阱了！」"
  },
  [200330065] = {
    id = 200330065,
    name = "探险者催促你抓住那个少年。"
  },
  [200330066] = {
    id = 200330066,
    name = "「安静点。」"
  },
  [200330067] = {
    id = 200330067,
    name = "你偷偷靠近，身后突然响起树枝断裂的声音——"
  },
  [200330068] = {id = 200330068, name = "咔嚓。"},
  [200330069] = {
    id = 200330069,
    name = "听到动静，少年转过头看向你，露出狡黠的眼神。"
  },
  [200330070] = {
    id = 200330070,
    name = "（暴露了......）"
  },
  [200330071] = {
    id = 200330071,
    name = "少年似乎没有察觉到你的意图，甚至和你打了一声招呼。"
  },
  [200330072] = {
    id = 200330072,
    name = "「你这个小偷别跑！」"
  },
  [200330073] = {
    id = 200330073,
    name = "探险者在后面激动地大叫。"
  },
  [200330074] = {
    id = 200330074,
    name = "少年看向你身后，随后做了一个夸张的表情。"
  },
  [200330075] = {
    id = 200330075,
    name = "「哇，好怕怕，快来追我啊！」"
  },
  [200330076] = {
    id = 200330076,
    name = "少年拔腿就跑。"
  },
  [200330077] = {
    id = 200330077,
    name = "你追了上去。"
  },
  [200330078] = {
    id = 200330078,
    name = "少年身手不凡，凭借熟悉的地势环境，让你始终抓不住他，总是差那么一点。"
  },
  [200330079] = {
    id = 200330079,
    name = "「你也太逊啦！哈哈哈。」"
  },
  [200330080] = {
    id = 200330080,
    name = "少年大笑一声，而后你脚下一空。"
  },
  [200330081] = {
    id = 200330081,
    name = "你坠落到了一个深坑里，深坑中布满了让人昏迷的烟雾。"
  },
  [200330082] = {
    id = 200330082,
    name = "你晕倒了过去。"
  },
  [200330083] = {
    id = 200330083,
    name = "等你醒来后，发现自己已被洗劫一空。"
  },
  [200330084] = {
    id = 200330084,
    name = "周围早已见不到任何人影。"
  },
  [200330085] = {
    id = 200330085,
    name = "次日，你再来此地准备寻找少年和那名探险者，却一无所获。"
  },
  [200330086] = {
    id = 200330086,
    name = "「喂，你不帮忙抓盗贼吗？」"
  },
  [200330087] = {
    id = 200330087,
    name = "「加油，希望你今天能蹲到。」"
  },
  [200330088] = {
    id = 200330088,
    name = "「切，一点正义感都没有。」"
  },
  [200330089] = {
    id = 200330089,
    name = "探险者吐槽了你一句。"
  },
  [200330090] = {
    id = 200330090,
    name = "你无视了他，向着他所说的反方向走去。"
  },
  [200330091] = {
    id = 200330091,
    name = "还没走出他的视野外，一张巨大的网从天而降。"
  },
  [200330092] = {
    id = 200330092,
    name = "你被困在了里面，越挣扎网束缚得越紧，一会儿就将你牢牢捆缚住。"
  },
  [200330093] = {
    id = 200330093,
    name = "「哈哈！今天的肥羊！」"
  },
  [200330094] = {
    id = 200330094,
    name = "「......」"
  },
  [200330095] = {
    id = 200330095,
    name = "眼前出现了一个少年。"
  },
  [200330096] = {
    id = 200330096,
    name = "他正龇牙咧嘴地朝你笑着。"
  },
  [200330097] = {
    id = 200330097,
    name = "「放弃吧，你挣扎不掉的！」"
  },
  [200330098] = {
    id = 200330098,
    name = "趁你动弹不得，少年抢走了你的钱袋。"
  },
  [200330099] = {
    id = 200330099,
    name = "「你这个家伙！抓住你了！」"
  },
  [200330100] = {
    id = 200330100,
    name = "他一瘸一拐地追了上来。"
  },
  [200330101] = {
    id = 200330101,
    name = "「什么啊？」"
  },
  [200330102] = {
    id = 200330102,
    name = "「又来一只肥羊，可惜是被宰过的了。」"
  },
  [200330103] = {
    id = 200330103,
    name = "他伸手一拉，收走了你身上的网，趁你还来不及反应就快速逃走了。"
  },
  [200330104] = {
    id = 200330104,
    name = "「别跑！该死的盗贼！」"
  },
  [200330105] = {
    id = 200330105,
    name = "「略略略，抓不到我！」"
  },
  [200330106] = {
    id = 200330106,
    name = "少年的声音回荡在森林里。"
  },
  [200330107] = {
    id = 200330107,
    name = "「你也着了他的道吧！」"
  },
  [200330108] = {
    id = 200330108,
    name = "「谁让你不听我的！」"
  },
  [200330109] = {
    id = 200330109,
    name = "「你说的陷阱是在另一边。」"
  },
  [200330110] = {
    id = 200330110,
    name = "「这一片都是他的活动范围啊，我怎么可能精准判断出他在哪出没。」"
  },
  [200330111] = {
    id = 200330111,
    name = "「......」"
  },
  [200330112] = {
    id = 200330112,
    name = "你恢复了力气，起身站起。"
  },
  [200330113] = {
    id = 200330113,
    name = "「这次就当是个教训吧。」"
  },
  [200330114] = {
    id = 200330114,
    name = "「他今天已经成功打劫，就不会再回来了。」"
  },
  [200330115] = {
    id = 200330115,
    name = "「要不要明天和我一起蹲他啊？」"
  },
  [200330116] = {
    id = 200330116,
    name = "你答应了探险者。"
  },
  [200330117] = {
    id = 200330117,
    name = "第二天你再次来到了这个地方。"
  },
  [200330118] = {
    id = 200330118,
    name = "刚走到目的地，一张大网再次落下。"
  },
  [200330119] = {
    id = 200330119,
    name = "而探险者似乎早有准备，灵活地躲开了。"
  },
  [200330120] = {
    id = 200330120,
    name = "「哎呀呀，怎么还是同一个人？」"
  },
  [200330121] = {
    id = 200330121,
    name = "「我看他身上还有不少好东西啊。」"
  },
  [200330122] = {
    id = 200330122,
    name = "「你们两个合谋？！」"
  },
  [200330123] = {
    id = 200330123,
    name = "「知道也晚啦。」"
  },
  [200330124] = {
    id = 200330124,
    name = "两人再次将你洗劫一空，扬长而去。"
  },
  [200330125] = {
    id = 200330125,
    name = "你拒绝了探险者。"
  },
  [200330126] = {
    id = 200330126,
    name = "「你难道不想教训他吗？」"
  },
  [200330127] = {
    id = 200330127,
    name = "「不想追回你的金币吗？」"
  },
  [200330128] = {
    id = 200330128,
    name = "「不用追了。」"
  },
  [200330129] = {
    id = 200330129,
    name = "「哇，你的损失也不少吧，就这样算了？」"
  },
  [200330130] = {
    id = 200330130,
    name = "「谁说的？你这个同伙不是在这里吗？」"
  },
  [200330131] = {
    id = 200330131,
    name = "「你、你在说什么......」"
  },
  [200330132] = {
    id = 200330132,
    name = "「还要我说得明白些吗？」"
  },
  [200330133] = {
    id = 200330133,
    name = "「你就是那个盗贼的同谋，所以，快把东西交出来吧。」"
  },
  [200330134] = {
    id = 200330134,
    name = "「什么同谋，你污蔑我！」"
  },
  [200330135] = {
    id = 200330135,
    name = "你没兴趣听他狡辩，一脚绊倒了他。"
  },
  [200330136] = {
    id = 200330136,
    name = "「你以为你和那人挤眉弄眼的样子我没发现吗？」"
  },
  [200330137] = {
    id = 200330137,
    name = "「你的伤势根本不像是最近的！没猜错的话，你负责物色猎物，那少年负责动手吧？！」"
  },
  [200330138] = {
    id = 200330138,
    name = "「......你、你想干嘛？！」"
  },
  [200330139] = {
    id = 200330139,
    name = "「干嘛？哼，当然是找回损失了。」"
  },
  [200330140] = {
    id = 200330140,
    name = "你将探险者身上的财物搜刮了一番，没想到不仅找回了损失，竟然还有得赚。"
  },
  [200330141] = {
    id = 200330141,
    name = "这让你倍感愉悦，哼着小曲离开了。"
  },
  [200340001] = {
    id = 200340001,
    name = "你走到一个湖边。"
  },
  [200340002] = {
    id = 200340002,
    name = "发现湖中有鱼在游动。"
  },
  [200340003] = {
    id = 200340003,
    name = "「正好是可以食用的鱼。」"
  },
  [200340004] = {
    id = 200340004,
    name = "你利用附近的材料制作了一根钓竿，决定钓几条鱼满足口腹之欲望。"
  },
  [200340005] = {
    id = 200340005,
    name = "「用什么鱼饵呢？」"
  },
  [200340006] = {
    id = 200340006,
    name = "你在周围抓了些虫子，将其作为鱼饵挂在鱼钩上扔了出去。"
  },
  [200340007] = {
    id = 200340007,
    name = "鱼钩落入了水中。"
  },
  [200340008] = {
    id = 200340008,
    name = "你坐在一边，静静观察水面上的波纹。"
  },
  [200340009] = {
    id = 200340009,
    name = "此时，浮标开始沉入水中。"
  },
  [200340010] = {
    id = 200340010,
    name = "「难道有鱼上钩了？」"
  },
  [200340011] = {
    id = 200340011,
    name = "你使劲拉拽鱼竿，发现根本拉不动。"
  },
  [200340012] = {
    id = 200340012,
    name = "浮标在水面无规律地游动，你再次用力拉竿，一声呜咽从水底传来。"
  },
  [200340013] = {
    id = 200340013,
    name = "「！！！」"
  },
  [200340014] = {
    id = 200340014,
    name = "「好像是个了不得的东西啊！」"
  },
  [200340015] = {
    id = 200340015,
    name = "话刚说完，一头魔物钻出湖面，直扑你而来！"
  },
  [200340016] = {
    id = 200340016,
    name = "还好你有所准备，挡下了魔物的攻击，随即与魔物战斗。"
  },
  [200340017] = {
    id = 200340017,
    name = "击败魔物后，鱼竿饵料已尽数被毁，因为刚才的动静，湖中的鱼也早已散去。"
  },
  [200340018] = {
    id = 200340018,
    name = "你叹了口气，只得收拾起魔物，将其作为今天的食材。"
  },
  [200340019] = {
    id = 200340019,
    name = "你有节奏地收着线，一点点将鱼拉近。"
  },
  [200340020] = {
    id = 200340020,
    name = "「果然是一条肥美的大鱼，今天有口福了。」"
  },
  [200340021] = {
    id = 200340021,
    name = "你喜出望外，猛拉鱼竿。"
  },
  [200340022] = {
    id = 200340022,
    name = "——嘭！"
  },
  [200340023] = {
    id = 200340023,
    name = "大鱼挣脱了线，在游走前甚至用尾巴拍打了几下湖面，溅起巨大水花，似乎是对你的嘲笑。"
  },
  [200340024] = {
    id = 200340024,
    name = "「......」"
  },
  [200340025] = {
    id = 200340025,
    name = "你继续小心地收着线，待大鱼距离你很近之际，你拿起制作的简易鱼叉，猛地扎去！"
  },
  [200340026] = {
    id = 200340026,
    name = "夜晚，篝火的噼啪声和烤鱼的滋滋声交汇在一起，极其诱人。"
  },
  [200340027] = {
    id = 200340027,
    name = "你将随身携带的一些调料洒到了鱼身上。"
  },
  [200340028] = {
    id = 200340028,
    name = "一时香味四溢，更添了几分饥饿感。"
  },
  [200340029] = {
    id = 200340029,
    name = "「我不客气啦。」"
  },
  [200340030] = {
    id = 200340030,
    name = "你缓缓收线，发现水下生物一动不动。"
  },
  [200340031] = {
    id = 200340031,
    name = "你用了些力气，突然将一团东西拉出了水面。"
  },
  [200340032] = {
    id = 200340032,
    name = "你踉跄了几步才稳住身形。"
  },
  [200340033] = {
    id = 200340033,
    name = "仔细一看，竟然是一团水草缠住了鱼饵。"
  },
  [200340034] = {
    id = 200340034,
    name = "「......失败了啊。」"
  },
  [200340035] = {
    id = 200340035,
    name = "半晌过后，你的鱼饵已经用光，但却一条鱼也没钓上。"
  },
  [200340036] = {
    id = 200340036,
    name = "「今天运气不佳，等几天再来试试吧。」"
  },
  [200340037] = {
    id = 200340037,
    name = "你在周围猎杀了一头魔物，将其肉作为鱼饵。"
  },
  [200340038] = {
    id = 200340038,
    name = "鱼钩落入了水中。"
  },
  [200340039] = {
    id = 200340039,
    name = "你坐在一边，静静观察着水面上的波纹。"
  },
  [200340040] = {
    id = 200340040,
    name = "忽然，浮标沉入水中。"
  },
  [200340041] = {
    id = 200340041,
    name = "「难道有鱼上钩了？」"
  },
  [200340042] = {
    id = 200340042,
    name = "你使劲拉拽鱼竿，跃出水面的，竟然是一只巨大的靴子。"
  },
  [200340043] = {
    id = 200340043,
    name = "「......」"
  },
  [200340044] = {
    id = 200340044,
    name = "你扔掉了靴子，继续钓鱼。"
  },
  [200340045] = {
    id = 200340045,
    name = "隔了许久，依然一条鱼也没有钓到。"
  },
  [200340046] = {
    id = 200340046,
    name = "「肯定是这只靴子的原因，不然早钓上鱼了。」"
  },
  [200340047] = {
    id = 200340047,
    name = "你把靴子提了上来。"
  },
  [200340048] = {
    id = 200340048,
    name = "靴子异常沉重，你从中倒出了许多水藻，以及一小袋金币。"
  },
  [200340049] = {
    id = 200340049,
    name = "「......运气不错，竟然还有意外收获。」"
  },
  [200340050] = {
    id = 200340050,
    name = "你又钓了一会，还是没有鱼上钩。"
  },
  [200340051] = {
    id = 200340051,
    name = "「算了，反正今天的收获已足够大快朵颐了。」"
  },
  [200340052] = {
    id = 200340052,
    name = "说罢，你处理掉渔具，向附近据点走去。"
  },
  [200350001] = {
    id = 200350001,
    name = "你发现了一个废弃的矿洞。"
  },
  [200350002] = {
    id = 200350002,
    name = "走进去后，感受到了充沛的魔力。"
  },
  [200350003] = {
    id = 200350003,
    name = "「一定藏着些好东西。」"
  },
  [200350004] = {
    id = 200350004,
    name = "你沿四周摸索了一番，但并无发现。"
  },
  [200350005] = {
    id = 200350005,
    name = "正思索之际，你瞥见一把遗弃的矿镐，心中突然有了主意。"
  },
  [200350006] = {
    id = 200350006,
    name = "「那么从哪边开始挖呢？」"
  },
  [200350007] = {
    id = 200350007,
    name = "你在左侧的石壁上发现了以前矿工留下的开凿痕迹，决定沿着他们开凿的位置继续深挖试试。"
  },
  [200350008] = {
    id = 200350008,
    name = "没凿几下，你便发现了一些矿物特有的痕迹。"
  },
  [200350009] = {
    id = 200350009,
    name = "「这么容易吗？」"
  },
  [200350010] = {
    id = 200350010,
    name = "你继续深挖，矿物的痕迹越来越明显。"
  },
  [200350011] = {
    id = 200350011,
    name = "——轰！"
  },
  [200350012] = {
    id = 200350012,
    name = "你用力过猛，将石壁挖出了一个窟窿。"
  },
  [200350013] = {
    id = 200350013,
    name = "一堆石头砸落了下来。"
  },
  [200350014] = {
    id = 200350014,
    name = "待烟尘消散，你在其中发现了闪闪发光的矿物。"
  },
  [200350015] = {
    id = 200350015,
    name = "「运气不错。」"
  },
  [200350016] = {
    id = 200350016,
    name = "你收起矿物，正准备继续在周围挖掘时，听到有说话声传来。"
  },
  [200350017] = {
    id = 200350017,
    name = "你走出去查看，发现一队矿工正朝矿洞走来。"
  },
  [200350018] = {
    id = 200350018,
    name = "「啊，原来不是废弃的矿洞啊。」"
  },
  [200350019] = {
    id = 200350019,
    name = "你趁他们尚未发现，悄悄离开了。"
  },
  [200350020] = {
    id = 200350020,
    name = "你决定绕着矿物痕迹，在周围挖掘试试。"
  },
  [200350021] = {
    id = 200350021,
    name = "挖了一阵，碰到了一块坚硬的岩石。为了能继续挖下去，你决定暴力挖开岩石。"
  },
  [200350022] = {
    id = 200350022,
    name = "——咔嚓！"
  },
  [200350023] = {
    id = 200350023,
    name = "整面矿壁开始崩裂。"
  },
  [200350024] = {
    id = 200350024,
    name = "「糟了。」"
  },
  [200350025] = {
    id = 200350025,
    name = "随着裂纹蔓延，眼看矿洞就要坍塌。"
  },
  [200350026] = {
    id = 200350026,
    name = "你迅速逃了出去，伴随着轰鸣声，矿洞化为了废墟。"
  },
  [200350027] = {
    id = 200350027,
    name = "「好险。」"
  },
  [200350028] = {
    id = 200350028,
    name = "你心有余悸，拍拍胸口重新踏上旅途。"
  },
  [200350029] = {
    id = 200350029,
    name = "石壁正中间有一处凹槽，你决定从这个凹槽开始挖掘。"
  },
  [200350030] = {
    id = 200350030,
    name = "你什么都没有挖到。"
  },
  [200350031] = {
    id = 200350031,
    name = "不过，一丝不同寻常的气息从里面散发出来。"
  },
  [200350032] = {
    id = 200350032,
    name = "要继续挖吗？"
  },
  [200350033] = {
    id = 200350033,
    name = "你继续挖掘，异样的气味越来越浓烈。"
  },
  [200350034] = {
    id = 200350034,
    name = "突然，一只尖锐的触手从石缝中钻了出来！"
  },
  [200350035] = {
    id = 200350035,
    name = "「滋滋......」"
  },
  [200350036] = {
    id = 200350036,
    name = "你吓了一跳，随即冷静了下来。趁魔物尚未完全钻出来之际，全力发起攻击。"
  },
  [200350037] = {
    id = 200350037,
    name = "没费什么劲你便将魔物解决。"
  },
  [200350038] = {
    id = 200350038,
    name = "「呼，竟然还有魔物存在。」"
  },
  [200350039] = {
    id = 200350039,
    name = "「看来不能继续深挖了，不然不知道还会遇到什么。」"
  },
  [200350040] = {
    id = 200350040,
    name = "「而且里面竟然存在空洞，看样子这个地方随时可能坍塌，太危险了。」"
  },
  [200350041] = {
    id = 200350041,
    name = "你思考了一会儿，决定放弃继续探索，离开了矿洞。"
  },
  [200350042] = {
    id = 200350042,
    name = "「不好的预感啊。」"
  },
  [200350043] = {
    id = 200350043,
    name = "你虽然已经停了下来，却听见刚刚挖掘的地方传来石头崩裂的声音——咔嚓。"
  },
  [200350044] = {
    id = 200350044,
    name = "随即一道裂缝开始向周围蔓延。"
  },
  [200350045] = {
    id = 200350045,
    name = "「糟透了。」"
  },
  [200350046] = {
    id = 200350046,
    name = "矿洞即将坍塌。"
  },
  [200350047] = {
    id = 200350047,
    name = "你迅速逃了出去，伴随着轰鸣声，矿洞化为了废墟。"
  },
  [200350048] = {
    id = 200350048,
    name = "「好险。」"
  },
  [200350049] = {
    id = 200350049,
    name = "你心有余悸，拍拍胸口重新踏上新的旅途。"
  },
  [200350050] = {
    id = 200350050,
    name = "你环视周围，发现右边石壁相对完整，没有被开凿的痕迹，于是决定从右侧开挖。"
  },
  [200350051] = {
    id = 200350051,
    name = "然而你没挖几下——咔嚓，石壁裂开了一条巨大的缝！"
  },
  [200350052] = {
    id = 200350052,
    name = "「看起来有点危险。」"
  },
  [200350053] = {
    id = 200350053,
    name = "你继续挥动矿镐，裂缝也随之蔓延，很快便布满右侧石壁。"
  },
  [200350054] = {
    id = 200350054,
    name = "轰——一声巨响，右侧石壁整面坍塌，后方赫然是一个巨大的空洞。"
  },
  [200350055] = {
    id = 200350055,
    name = "「没想到这后方竟然还有如此大的空间。」"
  },
  [200350056] = {
    id = 200350056,
    name = "你走进去一看，里面竟然有一具白骨，旁边放着一把已经生锈的剑，看样子已经死去多时。"
  },
  [200350057] = {
    id = 200350057,
    name = "你在空洞内四下搜寻，最终只在白骨下方发现一本笔记。"
  },
  [200350058] = {
    id = 200350058,
    name = "你快速翻阅了一下，里面记载着许多奇怪的内容，一时看不懂。"
  },
  [200350059] = {
    id = 200350059,
    name = "关于白骨的身份已然无从查起。"
  },
  [200350060] = {
    id = 200350060,
    name = "你摇了摇头，收起锈剑和笔记离开了。"
  },
  [200350061] = {
    id = 200350061,
    name = "你换了个地方挖掘，不多时，几块散发微光的矿物出现在了你的面前。"
  },
  [200350062] = {
    id = 200350062,
    name = "「发现了。」"
  },
  [200350063] = {
    id = 200350063,
    name = "你将所有矿物收好，心满意足地离开了。"
  },
  [200360001] = {
    id = 200360001,
    name = "「有点饿了。」"
  },
  [200360002] = {
    id = 200360002,
    name = "「吃点东西吧。」"
  },
  [200360003] = {
    id = 200360003,
    name = "你生起篝火，拿出食物架在旁边，不多时便香气四溢。"
  },
  [200360004] = {
    id = 200360004,
    name = "一个壮硕的男人闻着味走了过来。"
  },
  [200360005] = {
    id = 200360005,
    name = "「我闻到了。」"
  },
  [200360006] = {
    id = 200360006,
    name = "男人走到你旁边坐下，看着火堆旁的食物皱紧了眉头。"
  },
  [200360007] = {
    id = 200360007,
    name = "「真是糟糕的处理。」"
  },
  [200360008] = {
    id = 200360008,
    name = "「你说这个？」"
  },
  [200360009] = {
    id = 200360009,
    name = "「嗯哼。」"
  },
  [200360010] = {
    id = 200360010,
    name = "「你简直在暴殄天物！」"
  },
  [200360011] = {
    id = 200360011,
    name = "「作为深渊美食家，我无法容忍你这种糟蹋食材的行为！」"
  },
  [200360012] = {
    id = 200360012,
    name = "「......那你有何高见？」"
  },
  [200360013] = {
    id = 200360013,
    name = "「既然你诚心诚意地向深渊美食家本人请教！」"
  },
  [200360014] = {
    id = 200360014,
    name = "「那我大大方方的告诉你。」"
  },
  [200360015] = {
    id = 200360015,
    name = "「不过在此之前，你得回答我几个问题。」"
  },
  [200360016] = {
    id = 200360016,
    name = "「你认为自己是一个内心温柔的人吗？」"
  },
  [200360017] = {
    id = 200360017,
    name = "「对了，别想撒谎，我会看出来的哦！」"
  },
  [200360018] = {
    id = 200360018,
    name = "「这和料理有什么关系？」"
  },
  [200360019] = {
    id = 200360019,
    name = "「哼，当然有关系！不过只知道果腹的人自然不会思考就是了。」"
  },
  [200360020] = {
    id = 200360020,
    name = "「你尽管回答我就好。」"
  },
  [200360021] = {
    id = 200360021,
    name = "「唔，内心温柔的人啊~」"
  },
  [200360022] = {
    id = 200360022,
    name = "「你的心中充满了爱和善良。」"
  },
  [200360023] = {
    id = 200360023,
    name = "「深渊中的食物无论怎么做都会难吃的。」"
  },
  [200360024] = {
    id = 200360024,
    name = "「因为它们本身就象征着绝望、恐惧、悲伤。」"
  },
  [200360025] = {
    id = 200360025,
    name = "「以温柔的心去烹饪，是无法让这些食物变美味的。」"
  },
  [200360026] = {
    id = 200360026,
    name = "「不过也并非毫无办法......」"
  },
  [200360027] = {
    id = 200360027,
    name = "男人说完便盯着你，似乎期待你的反应。"
  },
  [200360028] = {
    id = 200360028,
    name = "见你只是看着他却不说话，男人撇了撇嘴，继续谈起了自己的看法。"
  },
  [200360029] = {
    id = 200360029,
    name = "「这个世界上任何事物都有双面性。」"
  },
  [200360030] = {
    id = 200360030,
    name = "「比如黑与白、光与影，料理也不例外。」"
  },
  [200360031] = {
    id = 200360031,
    name = "「深渊中的食材大多都源于黑暗，想要将其变得美味......则需要辅以光明。」"
  },
  [200360032] = {
    id = 200360032,
    name = "男人摇头晃脑，侃侃而谈，说到最后，拿出了一瓶药剂。"
  },
  [200360033] = {
    id = 200360033,
    name = "「而所谓的光明，便是我手中这一瓶——究极美味剂！」"
  },
  [200360034] = {
    id = 200360034,
    name = "「这是我经过多年研究才调制出来的调味剂，已经第五代了！」"
  },
  [200360035] = {
    id = 200360035,
    name = "「只要有了它，便能让深渊中的任何食物变得美味！」"
  },
  [200360036] = {
    id = 200360036,
    name = "「......」"
  },
  [200360037] = {
    id = 200360037,
    name = "「说了那么多，你是卖药的？」"
  },
  [200360038] = {
    id = 200360038,
    name = "「话不能这么说，我和那些卖药的完全不同！他们是纯粹的商贩，是利益的奴隶！」"
  },
  [200360039] = {
    id = 200360039,
    name = "「而我，是在推广自己的产品。」"
  },
  [200360040] = {
    id = 200360040,
    name = "「现在正值推广期间，可以给你一瓶免费试用。」"
  },
  [200360041] = {
    id = 200360041,
    name = "「如果使用后觉得不错的话，可以帮我们宣传一下。」"
  },
  [200360042] = {
    id = 200360042,
    name = "「另外我们的店就在前方据点，欢迎随时来采购。」"
  },
  [200360043] = {
    id = 200360043,
    name = "「当然，如果对我的产品感兴趣，也欢迎来洽谈加盟事宜。」"
  },
  [200360044] = {
    id = 200360044,
    name = "「我们后续计划是在各个据点圈落开设分店，如此神奇的调味剂一定能风靡深渊的。」"
  },
  [200360045] = {
    id = 200360045,
    name = "「......好的，如果有需要我会找你。」"
  },
  [200360046] = {
    id = 200360046,
    name = "「那就这么说定了！」"
  },
  [200360047] = {
    id = 200360047,
    name = "「对了，现在正是我们扩张初期，如果想加盟，越早优惠越大！」"
  },
  [200360048] = {
    id = 200360048,
    name = "男人又对你劝告一番后才离去。"
  },
  [200360049] = {
    id = 200360049,
    name = "「这个世界上任何事物都有双面性。」"
  },
  [200360050] = {
    id = 200360050,
    name = "「比如黑与白、光与影，料理也不例外。」"
  },
  [200360051] = {
    id = 200360051,
    name = "「深渊中的食材大多都源于黑暗，想要将其变得美味......则需要辅以光明。」"
  },
  [200360052] = {
    id = 200360052,
    name = "男人摇头晃脑，侃侃而谈，说到最后，拿出了一瓶药剂。"
  },
  [200360053] = {
    id = 200360053,
    name = "「而所谓的光明，便是我手中这一瓶——究极美味剂！」"
  },
  [200360054] = {
    id = 200360054,
    name = "「这是我经过多年研究才做出的调味剂，已经第五代了！」"
  },
  [200360055] = {
    id = 200360055,
    name = "「只要有了它，便能让深渊中的任何食物变得美味！」"
  },
  [200360056] = {
    id = 200360056,
    name = "「真有那么神奇？」"
  },
  [200360057] = {
    id = 200360057,
    name = "「当然，不信的话你可以先买一瓶试试，我以成本价给你。」"
  },
  [200360058] = {
    id = 200360058,
    name = "「......」"
  },
  [200360059] = {
    id = 200360059,
    name = "「这么看着我干嘛？你还想免费试用吗？」"
  },
  [200360060] = {
    id = 200360060,
    name = "「这个调味剂的成本可是极其高昂，目前产量非常有限。」"
  },
  [200360061] = {
    id = 200360061,
    name = "「如果不是看你是个温柔的人，我才不会拿出来，甚至还以成本价给你。」"
  },
  [200360062] = {
    id = 200360062,
    name = "你婉拒了他。"
  },
  [200360063] = {
    id = 200360063,
    name = "他笑你没眼光，转身离开了。"
  },
  [200360064] = {
    id = 200360064,
    name = "壮硕的男人直夸你眼光独到，交易完成后还贴心的告诉了你一些烹饪技巧。"
  },
  [200360065] = {
    id = 200360065,
    name = "「真的有用吗？」"
  },
  [200360066] = {
    id = 200360066,
    name = "男人离开后，你带着怀疑和期待，将调味剂洒到了食物上......"
  },
  [200360067] = {
    id = 200360067,
    name = "「你很清楚这个世界的本质啊。」"
  },
  [200360068] = {
    id = 200360068,
    name = "「不过，也只有你这种知道世界本质、明白自己内心的人才能成为强者。」"
  },
  [200360069] = {
    id = 200360069,
    name = "「但对你们这样的人来说，想做出美味佳肴，那几乎是不可能的事。」"
  },
  [200360070] = {
    id = 200360070,
    name = "「你们是天生的强者，所以感受不到食材中所包含的丰富情感。」"
  },
  [200360071] = {
    id = 200360071,
    name = "「唉，这正是所谓的“有得便有失”吧。」"
  },
  [200360072] = {
    id = 200360072,
    name = "男人感叹了一会儿，突然凑到你跟前，一副神神秘秘的样子。"
  },
  [200360073] = {
    id = 200360073,
    name = "「但我有办法让你们这种强者也能做出美味佳肴！」"
  },
  [200360074] = {
    id = 200360074,
    name = "说罢，他一脸期待地看着你。"
  },
  [200360075] = {
    id = 200360075,
    name = "「我浸淫料理多年，深知各种食材的奥秘。」"
  },
  [200360076] = {
    id = 200360076,
    name = "「最近我根据自己多年经验，研制出了一款特效调味剂——究极美味剂！」"
  },
  [200360077] = {
    id = 200360077,
    name = "「它能将各种食材所包含的情感尽数释放，让食材充分呈现出自己的风味。」"
  },
  [200360078] = {
    id = 200360078,
    name = "「有了它，任何人都能成为料理大师，轻松做出美味佳肴。」"
  },
  [200360079] = {
    id = 200360079,
    name = "「有那么厉害？」"
  },
  [200360080] = {
    id = 200360080,
    name = "「当然，不信的话你可以先买一瓶试试，我以成本价给你。」"
  },
  [200360081] = {
    id = 200360081,
    name = "「......」"
  },
  [200360082] = {
    id = 200360082,
    name = "「这么看着我干嘛？你还想免费试用吗？」"
  },
  [200360083] = {
    id = 200360083,
    name = "「这个调味剂的成本可是极其高昂，目前产量非常有限。」"
  },
  [200360084] = {
    id = 200360084,
    name = "「如果不是看你是强者，我才不会拿出来，甚至还以成本价给你。」"
  },
  [200360085] = {
    id = 200360085,
    name = "「既然这么好，你自己留着用吧。」"
  },
  [200360086] = {
    id = 200360086,
    name = "直到吃完食物，你都没再理他。"
  },
  [200360087] = {
    id = 200360087,
    name = "他见无法说动你，气呼呼地离开了。"
  },
  [200360088] = {
    id = 200360088,
    name = "「是否美味根本不重要。」"
  },
  [200360089] = {
    id = 200360089,
    name = "「对我们长期在深渊探险的人来说，只要不难吃、能果腹就行了。」"
  },
  [200360090] = {
    id = 200360090,
    name = "「不管是恶心的虫子，还是美味的魔物，都只是满足生存需求罢了。」"
  },
  [200360091] = {
    id = 200360091,
    name = "男人长叹了一口气，似乎颇受打击。」"
  },
  [200360092] = {
    id = 200360092,
    name = "「怎么，难道不是这样吗？」"
  },
  [200360093] = {
    id = 200360093,
    name = "「没、没错，都是为了生存。」"
  },
  [200360094] = {
    id = 200360094,
    name = "「不过......你真的不考虑在满足生存的情况下，也充分享受生活吗？」"
  },
  [200360095] = {
    id = 200360095,
    name = "「不考虑。」"
  },
  [200360096] = {
    id = 200360096,
    name = "「追求享受，是修行的大忌，只会影响我拔剑的速度。」"
  },
  [200360097] = {
    id = 200360097,
    name = "「......」"
  },
  [200360098] = {
    id = 200360098,
    name = "「真是了不起的志气！」"
  },
  [200360099] = {
    id = 200360099,
    name = "男人对你的回答表示非常佩服，随即果断离去，似乎根本不想再多待一秒。"
  },
  [200360100] = {
    id = 200360100,
    name = "即便你招呼他吃点东西，他也没有回头。"
  },
  [200360101] = {
    id = 200360101,
    name = "（奇怪的人，该不会有什么别的企图吧？）"
  },
  [200360102] = {
    id = 200360102,
    name = "你拒绝回答男人的问题。"
  },
  [200360103] = {
    id = 200360103,
    name = "「你！哼，这样粗暴地对待食物，你迟早会遭天罚！」"
  },
  [200360104] = {
    id = 200360104,
    name = "他气呼呼地离开，走的时候还捂住了鼻子。"
  },
  [200360105] = {
    id = 200360105,
    name = "你感觉这人莫名其妙，草草吃完食物也离开了。"
  },
  [200370001] = {
    id = 200370001,
    name = "你走进一片森林。"
  },
  [200370002] = {
    id = 200370002,
    name = "地面铺满了厚厚的落叶，一些腐化的魔物枯骨散落四周，看样子已许久不曾有人踏足。"
  },
  [200370003] = {
    id = 200370003,
    name = "突然，有什么东西从你脚下钻了出来！"
  },
  [200370004] = {
    id = 200370004,
    name = "你纵身一跃，躲过了偷袭，同时展开反击，轻松解决了魔物。"
  },
  [200370005] = {
    id = 200370005,
    name = "你仔细观察四周，发现此地充斥着一股诡异的感觉。"
  },
  [200370006] = {
    id = 200370006,
    name = "直觉告诉你继续深入可能会有危险，你立即掉头准备离去，却见一个身穿长袍的陌生人出现在了前方。"
  },
  [200370007] = {
    id = 200370007,
    name = "「迷路的旅行者哟。」"
  },
  [200370008] = {
    id = 200370008,
    name = "「魔物在此肆虐疯长，暗黑的恶魔在吞噬旅人的灵魂。」"
  },
  [200370009] = {
    id = 200370009,
    name = "「你很幸运，拯救了你自己。」"
  },
  [200370010] = {
    id = 200370010,
    name = "他说完就走进了森林的迷雾中。"
  },
  [200370011] = {
    id = 200370011,
    name = "「......」"
  },
  [200370012] = {
    id = 200370012,
    name = "你悄悄跟在他身后。"
  },
  [200370013] = {
    id = 200370013,
    name = "走了一会儿，你见他在一处稍微开阔的区域停下。"
  },
  [200370014] = {
    id = 200370014,
    name = "长袍人口中念念有词，地上随即亮起红色的光。"
  },
  [200370015] = {
    id = 200370015,
    name = "「魔法阵？」"
  },
  [200370016] = {
    id = 200370016,
    name = "突然，长袍人身上燃起了苍白的火焰。"
  },
  [200370017] = {
    id = 200370017,
    name = "长袍在熊熊火焰中抖落，里面竟然空空如也！"
  },
  [200370018] = {
    id = 200370018,
    name = "「......怎么回事？」"
  },
  [200370019] = {
    id = 200370019,
    name = "你走近一看，什么也没发现。"
  },
  [200370020] = {
    id = 200370020,
    name = "无论火焰、灰烬，还是地面的魔法阵，都如同不曾存在过一般。"
  },
  [200370021] = {
    id = 200370021,
    name = "你没有管这名奇怪的长袍人，选择沿原路返回。"
  },
  [200370022] = {
    id = 200370022,
    name = "走了一会儿，你来到一个水潭边，那种诡异的感觉虽然仍未消散，但已微弱了许多，于是你决定在此休整一番。"
  },
  [200370023] = {
    id = 200370023,
    name = "就在你刚点燃篝火，准备烤制食物时，几头魔物悄无声息地出现在你眼前。"
  },
  [200370024] = {
    id = 200370024,
    name = "它们看上去像某种魔物的幼崽，虽然尚未成熟，但露出的凶戾感已让你不敢大意。"
  },
  [200370025] = {
    id = 200370025,
    name = "在你的全力攻击之下，几头魔物几乎在一瞬间便被你斩杀。"
  },
  [200370026] = {
    id = 200370026,
    name = "正当你松了一口气，一声震天巨吼从不远处的森林中传来。"
  },
  [200370027] = {
    id = 200370027,
    name = "随即一股强大至极的气息向着此处冲来。"
  },
  [200370028] = {
    id = 200370028,
    name = "「！！！」"
  },
  [200370029] = {
    id = 200370029,
    name = "你突然感觉到巨大的危机，面对来者，你没有必胜的把握。"
  },
  [200370030] = {
    id = 200370030,
    name = "你顾不得收拾物资，迅速向远处逃去。"
  },
  [200370031] = {
    id = 200370031,
    name = "你继续向森林深处走去，一阵歌声打破了林中寂静。"
  },
  [200370032] = {
    id = 200370032,
    name = "歌声从森林深处传来，悠扬动人。"
  },
  [200370033] = {
    id = 200370033,
    name = "你正想仔细聆听，突然感觉脚下落叶颤动，许多小型魔物从下方涌出，四散奔逃。"
  },
  [200370034] = {
    id = 200370034,
    name = "「竟然隐藏了这么多魔物......」"
  },
  [200370035] = {
    id = 200370035,
    name = "还要继续前进吗?"
  },
  [200370036] = {
    id = 200370036,
    name = "你顺着歌声一路前进。"
  },
  [200370037] = {
    id = 200370037,
    name = "道路旁歪歪扭扭的路牌上刻着些文字。"
  },
  [200370038] = {
    id = 200370038,
    name = "「幽灵游荡之所。」"
  },
  [200370039] = {
    id = 200370039,
    name = "你心中突然涌起一股不祥的预感。"
  },
  [200370040] = {
    id = 200370040,
    name = "你继续向深处走去，周围响起了窸窸窣窣的声音。"
  },
  [200370041] = {
    id = 200370041,
    name = "你向周围看去，赫然发现不知何时一些魔物也在朝着歌声方向前行。"
  },
  [200370042] = {
    id = 200370042,
    name = "它们似乎都没发现你，坚定不移地前行，犹如一个个朝圣者。"
  },
  [200370043] = {
    id = 200370043,
    name = "你又走了一会儿，路旁再次出现一个路牌，这次，上面刻着几个极其醒目的大字。"
  },
  [200370044] = {
    id = 200370044,
    name = "「灾殃降临，万物消亡。」"
  },
  [200370045] = {
    id = 200370045,
    name = "看到这行字的瞬间，一股极其恐怖的气息从森林深处袭来。"
  },
  [200370046] = {
    id = 200370046,
    name = "你的直觉告诉你，前方存在着难以抗衡的存在。"
  },
  [200370047] = {
    id = 200370047,
    name = "「......很强。」"
  },
  [200370048] = {
    id = 200370048,
    name = "你不再理会周围诡异的魔物和歌声，迅速向外撤离。"
  },
  [200370049] = {
    id = 200370049,
    name = "你越过路牌继续前行，终于见到了吟唱歌声之人。"
  },
  [200370050] = {
    id = 200370050,
    name = "灰白的长袍将他全身笼罩，他站在一个巨大的红圈之中，周围的魔物正争先恐后地跳入红圈。"
  },
  [200370051] = {
    id = 200370051,
    name = "魔物跳入红圈后便直接消失了，但并不像传送魔法那般，而是浑身燃烧，化作火星消散。"
  },
  [200370052] = {
    id = 200370052,
    name = "「......怎么回事？」"
  },
  [200370053] = {
    id = 200370053,
    name = "你停在了红圈边缘，警惕地向长袍人问到，但他并未回应你。"
  },
  [200370054] = {
    id = 200370054,
    name = "过了许久，周围的魔物已悉数跳入红圈消失，长袍人缓缓转过身，你感受到了前所未有的压迫感。"
  },
  [200370055] = {
    id = 200370055,
    name = "「那些魔物去哪儿了？」"
  },
  [200370056] = {
    id = 200370056,
    name = "「去该去的地方。」"
  },
  [200370057] = {
    id = 200370057,
    name = "说罢，他也化作一团火焰消散。"
  },
  [200370058] = {
    id = 200370058,
    name = "「等准备好了，再来探险吧。」"
  },
  [200370059] = {
    id = 200370059,
    name = "你转身离开了原地。"
  },
  [200370060] = {
    id = 200370060,
    name = "悠扬的歌声逐渐消失了，森林又陷入了死寂。"
  },
  [200370061] = {
    id = 200370061,
    name = "就在你离开不久，一声叹息从森林深处传来。"
  },
  [200370062] = {
    id = 200370062,
    name = "「真是的......」"
  },
  [200370063] = {
    id = 200370063,
    name = "「又被打扰了。」"
  },
  [200380001] = {
    id = 200380001,
    name = "一个落魄的女人在路边哭泣。"
  },
  [200380002] = {
    id = 200380002,
    name = "你从她身边走过时，她哭得更大声了。"
  },
  [200380003] = {
    id = 200380003,
    name = "「呜呜呜，我怎么这么凄凉啊......」"
  },
  [200380004] = {
    id = 200380004,
    name = "「众神啊，我做错了什么......」"
  },
  [200380005] = {
    id = 200380005,
    name = "你打算继续前行时，衣角被她扯住。"
  },
  [200380006] = {
    id = 200380006,
    name = "「......」"
  },
  [200380007] = {
    id = 200380007,
    name = "「这位......小姐，有什么事？」"
  },
  [200380008] = {
    id = 200380008,
    name = "「我身为贵族千金，却被探险者骗到了深渊......」"
  },
  [200380009] = {
    id = 200380009,
    name = "「我为了他舍弃了家族和尊严......」"
  },
  [200380010] = {
    id = 200380010,
    name = "「我以为找到了最美好的爱情，还和他结婚......」"
  },
  [200380011] = {
    id = 200380011,
    name = "「可他竟然把我的钱花光就跑了！」"
  },
  [200380012] = {
    id = 200380012,
    name = "「而现在，我唯一的栖身之所......」"
  },
  [200380013] = {
    id = 200380013,
    name = "「也被一群强盗占领了......呜呜呜......」"
  },
  [200380014] = {
    id = 200380014,
    name = "「你房屋在哪儿？」"
  },
  [200380015] = {
    id = 200380015,
    name = "「我的小屋就在前面......」"
  },
  [200380016] = {
    id = 200380016,
    name = "「我已经一天没回家了。」"
  },
  [200380017] = {
    id = 200380017,
    name = "「你是要帮我夺回我的房子吗？」"
  },
  [200380018] = {
    id = 200380018,
    name = "「不过，我已经没有什么可以给你的了......」"
  },
  [200380019] = {
    id = 200380019,
    name = "「你看起来一无所有。」"
  },
  [200380020] = {
    id = 200380020,
    name = "「呜呜呜，我好苦啊，故乡也早已回不去了。」"
  },
  [200380021] = {
    id = 200380021,
    name = "「从我任性离开那一刻，我的家族不会再接纳我了。」"
  },
  [200380022] = {
    id = 200380022,
    name = "「现在我仅有的也就只剩那个房子了......」"
  },
  [200380023] = {
    id = 200380023,
    name = "「如果能夺回房子，我......我就将房子中的财物都给你！」"
  },
  [200380024] = {
    id = 200380024,
    name = "你根据女人的指引，来到一栋木屋前，周围布置着不少陷阱。"
  },
  [200380025] = {
    id = 200380025,
    name = "你轻松躲过，来到窗外窥探，只见屋内有一道人影闪过。"
  },
  [200380026] = {
    id = 200380026,
    name = "你谨慎地进入房内，又躲开了一个极其巧妙的陷阱。"
  },
  [200380027] = {
    id = 200380027,
    name = "「阁下......果然很厉害啊。」"
  },
  [200380028] = {
    id = 200380028,
    name = "你看见木屋的角落坐着一个身穿公会服饰的人。"
  },
  [200380029] = {
    id = 200380029,
    name = "「阁下是来抓我的？」"
  },
  [200380030] = {
    id = 200380030,
    name = "「你是......强盗？」"
  },
  [200380031] = {
    id = 200380031,
    name = "「我还以为阁下接了悬赏来抓我。」"
  },
  [200380032] = {
    id = 200380032,
    name = "「确有人委托我，赶走这里的强盗。」"
  },
  [200380033] = {
    id = 200380033,
    name = "「你说的是那群强盗？」"
  },
  [200380034] = {
    id = 200380034,
    name = "「我已经把他们都赶走了。」"
  },
  [200380035] = {
    id = 200380035,
    name = "「有人正在悬赏我，我不得不找个地方躲起来。」"
  },
  [200380036] = {
    id = 200380036,
    name = "「你得罪了谁？」"
  },
  [200380037] = {
    id = 200380037,
    name = "「高塔。我发现了不该发现的东西。」"
  },
  [200380038] = {
    id = 200380038,
    name = "「不知道还有没有机会把消息带回公会。」"
  },
  [200380039] = {
    id = 200380039,
    name = "他忽然很认真地看着你。"
  },
  [200380040] = {
    id = 200380040,
    name = "「我只是来帮人拿回房子的。」"
  },
  [200380041] = {
    id = 200380041,
    name = "「一定要赶我走吗，我也算是赶走强盗的功臣。」"
  },
  [200380042] = {
    id = 200380042,
    name = "「要不你去把房屋主人找来，我和她商量一下。」"
  },
  [200380043] = {
    id = 200380043,
    name = "你想了想，同意了他的提议。"
  },
  [200380044] = {
    id = 200380044,
    name = "你回去寻找委托人，却见她浑身是血，正被一个男人掐住脖子按倒在地。"
  },
  [200380045] = {
    id = 200380045,
    name = "你一脚踹飞男人，这才发现女人身上的血来自于这个男人，他的胸口破开了一个大洞。"
  },
  [200380046] = {
    id = 200380046,
    name = "他连骂了好几声「贱人」，最后停止了呼吸。"
  },
  [200380047] = {
    id = 200380047,
    name = "「怎么回事？」"
  },
  [200380048] = {
    id = 200380048,
    name = "「一个强盗而已。」"
  },
  [200380049] = {
    id = 200380049,
    name = "「比起这个，我的房子......」"
  },
  [200380050] = {
    id = 200380050,
    name = "你把之前的事告诉了她。"
  },
  [200380051] = {
    id = 200380051,
    name = "「我知道了。」"
  },
  [200380052] = {
    id = 200380052,
    name = "「那我去和他商量吧。」"
  },
  [200380053] = {
    id = 200380053,
    name = "「对了，我正好从这强盗身上夺回一些财物，就送给你作为谢礼吧！」"
  },
  [200380054] = {
    id = 200380054,
    name = "女人递给你一个沾有血迹的袋子，随即向木屋走去。"
  },
  [200380055] = {
    id = 200380055,
    name = "你看了看死在一旁的强盗有些疑惑，但也并未多想，转身离开。"
  },
  [200380056] = {
    id = 200380056,
    name = "「我对你们的事不感兴趣。」"
  },
  [200380057] = {
    id = 200380057,
    name = "「那真是可惜。」"
  },
  [200380058] = {
    id = 200380058,
    name = "「嗯？有人来了？」"
  },
  [200380059] = {
    id = 200380059,
    name = "你跟随他的视线从窗户眺望。"
  },
  [200380060] = {
    id = 200380060,
    name = "「喂喂，好像不太妙。」"
  },
  [200380061] = {
    id = 200380061,
    name = "你看到一个陌生的男人走向了落魄的女人，想到了一个可能性。"
  },
  [200380062] = {
    id = 200380062,
    name = "「我的委托人可能有点麻烦，我去看看。」"
  },
  [200380063] = {
    id = 200380063,
    name = "你赶到的时候，男人正与女人互相推搡。"
  },
  [200380064] = {
    id = 200380064,
    name = "他嘴里骂出的语言粗鄙不堪。"
  },
  [200380065] = {
    id = 200380065,
    name = "你听出他的身份是女人的丈夫，这一次也是为了拿钱而来。"
  },
  [200380066] = {
    id = 200380066,
    name = "你上前一脚踹翻了男人。"
  },
  [200380067] = {
    id = 200380067,
    name = "他见女人有帮手，骂了两句就逃走了。"
  },
  [200380068] = {
    id = 200380068,
    name = "落魄的女人收起了藏在身后的匕首。"
  },
  [200380069] = {
    id = 200380069,
    name = "「我差一点就杀了他。」"
  },
  [200380070] = {
    id = 200380070,
    name = "「你的房子有人帮你拿回来了，去看看吧。」"
  },
  [200380071] = {
    id = 200380071,
    name = "你把之前的事告诉了她。"
  },
  [200380072] = {
    id = 200380072,
    name = "「我知道了，谢谢你们。」"
  },
  [200380073] = {
    id = 200380073,
    name = "「能帮我赶走强盗，真是太感谢了！」"
  },
  [200380074] = {
    id = 200380074,
    name = "「没想到你丈夫还会纠缠你......」"
  },
  [200380075] = {
    id = 200380075,
    name = "「嗯，不过我已经有打算了。」"
  },
  [200380076] = {
    id = 200380076,
    name = "女人朝你笑了笑，走向了小屋。"
  },
  [200380077] = {
    id = 200380077,
    name = "「有话直说。」"
  },
  [200380078] = {
    id = 200380078,
    name = "「就是这块石头，你送到据点就可以，拜托了！」"
  },
  [200380079] = {
    id = 200380079,
    name = "「看来你伤得很严重啊。」"
  },
  [200380080] = {
    id = 200380080,
    name = "「不过外面还有一个等着我拿回房子的女人，我会告诉她你在这里。」"
  },
  [200380081] = {
    id = 200380081,
    name = "「你把她带过来吧，我也算是帮她赶走强盗的恩人。」"
  },
  [200380082] = {
    id = 200380082,
    name = "等你赶回去找女人时，女人已经消失了。"
  },
  [200380083] = {
    id = 200380083,
    name = "回到据点后，你将石头交给了公会的人。"
  },
  [200380084] = {
    id = 200380084,
    name = "公会的人非常感激，决定找人把同伴接回据点。"
  },
  [200380085] = {
    id = 200380085,
    name = "不过，你却再也没见到过那个女人。"
  },
  [200380086] = {
    id = 200380086,
    name = "「可是就算夺回了房子，你未来又该怎么办呢？」"
  },
  [200380087] = {
    id = 200380087,
    name = "「我只要有个遮风挡雨的地方就够了。」"
  },
  [200380088] = {
    id = 200380088,
    name = "「实在不行，我还能做一些别的生意，虽然......」"
  },
  [200380089] = {
    id = 200380089,
    name = "「虽然会吃些苦头，但我已经没有别的选择。」"
  },
  [200380090] = {
    id = 200380090,
    name = "「这里太危险了，随时都可能被魔物袭击。」"
  },
  [200380091] = {
    id = 200380091,
    name = "「我送你去据点吧。」"
  },
  [200380092] = {
    id = 200380092,
    name = "「可是，我想拿回我的房子......」"
  },
  [200380093] = {
    id = 200380093,
    name = "「那不是一个好的去处。」"
  },
  [200380094] = {
    id = 200380094,
    name = "「这种建在野外的小屋无论如何隐蔽，也迟早会被魔物或者强盗发现。」"
  },
  [200380095] = {
    id = 200380095,
    name = "你说服了她，把她带到了附近的据点。"
  },
  [200380096] = {
    id = 200380096,
    name = "她的背影单薄，步履蹒跚，就那样孤独地走入了人群。"
  },
  [200380097] = {
    id = 200380097,
    name = "「我还是先去看看你的房子吧。」"
  },
  [200380098] = {
    id = 200380098,
    name = "你刚走没几步，女人突然发出尖叫。"
  },
  [200380099] = {
    id = 200380099,
    name = "「就是他！那个家伙又回来了！」"
  },
  [200380100] = {
    id = 200380100,
    name = "一个男人看到了你们，转身就跑。"
  },
  [200380101] = {
    id = 200380101,
    name = "你很快追上了他，将他打倒在地。"
  },
  [200380102] = {
    id = 200380102,
    name = "「这个就是让你一无所有的家伙？」"
  },
  [200380103] = {
    id = 200380103,
    name = "「他一定是没钱了，才又想找我索要！」"
  },
  [200380104] = {
    id = 200380104,
    name = "「他总是这样吗？」"
  },
  [200380105] = {
    id = 200380105,
    name = "男人向你求饶，被你一脚踢得晕头转向。"
  },
  [200380106] = {
    id = 200380106,
    name = "他又向女人求饶，甚至诉说起以前美好的时光。"
  },
  [200380107] = {
    id = 200380107,
    name = "「我可以原谅你的。」"
  },
  [200380108] = {
    id = 200380108,
    name = "「但只能在你死了以后。」"
  },
  [200380109] = {
    id = 200380109,
