    id = 200420078,
    name = "到了据点后，老人看着往来的人流停下了脚步。"
  },
  [200420079] = {
    id = 200420079,
    name = "「......」"
  },
  [200420080] = {
    id = 200420080,
    name = "「我来过这里。」"
  },
  [200420081] = {
    id = 200420081,
    name = "「谢谢你啊，年轻人。」"
  },
  [200420082] = {
    id = 200420082,
    name = "老人朝你笑，但看着却像哭一般。"
  },
  [200420083] = {
    id = 200420083,
    name = "「我好像......想起来了......」"
  },
  [200420084] = {
    id = 200420084,
    name = "「我儿子......应该回不来了......」"
  },
  [200420085] = {
    id = 200420085,
    name = "「......节哀。」"
  },
  [200420086] = {
    id = 200420086,
    name = "你把盒子递给老人，他摆了摆手拒绝了。"
  },
  [200420087] = {
    id = 200420087,
    name = "而后走向了茫茫人海。"
  },
  [200420088] = {
    id = 200420088,
    name = "「......」"
  },
  [200420089] = {
    id = 200420089,
    name = "失去了庇护的失独老人，该如何在深渊里活下去呢。"
  },
  [200420090] = {
    id = 200420090,
    name = "你觉得老人举止疯癫，不想惹祸上身。"
  },
  [200420091] = {
    id = 200420091,
    name = "你离开了小屋。"
  },
  [200420092] = {
    id = 200420092,
    name = "「还不快滚进来！」"
  },
  [200420093] = {
    id = 200420093,
    name = "「锅里剩了点汤，你爱喝不喝！」"
  },
  [200420094] = {
    id = 200420094,
    name = "「每次都走这么久，也不怕我死在这里！」"
  },
  [200420095] = {
    id = 200420095,
    name = "「......」"
  },
  [200420096] = {
    id = 200420096,
    name = "「哦，你当然不怕了。」"
  },
  [200420097] = {
    id = 200420097,
    name = "「我死了，你就没有负担了。」"
  },
  [200420098] = {
    id = 200420098,
    name = "「你就自由了。」"
  },
  [200420099] = {
    id = 200420099,
    name = "「要不然我现在就去死，这样你就自由啦——」"
  },
  [200420100] = {
    id = 200420100,
    name = "「......」"
  },
  [200420101] = {
    id = 200420101,
    name = "「你怎么不说话？啊？是默认吗？真是出息了啊，那我现在就去死！」"
  },
  [200420102] = {
    id = 200420102,
    name = "老人怒气冲冲地出门了。"
  },
  [200420103] = {
    id = 200420103,
    name = "（这里虽然种植了驱赶魔物的植物，但如果离开太远，也会有危险吧......）"
  },
  [200420104] = {
    id = 200420104,
    name = "你追上老人的时候，前面正好出现了一头魔物，老人吓得一动不动。"
  },
  [200420105] = {
    id = 200420105,
    name = "你见状立即冲了上去，不一会儿便将魔物解决。"
  },
  [200420106] = {
    id = 200420106,
    name = "老人似乎恢复了些许神智。"
  },
  [200420107] = {
    id = 200420107,
    name = "「太可怕了。」"
  },
  [200420108] = {
    id = 200420108,
    name = "「绝对不能留在这了。」"
  },
  [200420109] = {
    id = 200420109,
    name = "「谢谢你啊，年轻人！」"
  },
  [200420110] = {
    id = 200420110,
    name = "（看来恢复正常了。）"
  },
  [200420111] = {
    id = 200420111,
    name = "「你见过我的儿子吗？」"
  },
  [200420112] = {
    id = 200420112,
    name = "「他也是探险者，背着一柄长剑。」"
  },
  [200420113] = {
    id = 200420113,
    name = "「他很喜欢他的武器，经常拿出来炫耀......」"
  },
  [200420114] = {
    id = 200420114,
    name = "「哎，我说这些干什么。」"
  },
  [200420115] = {
    id = 200420115,
    name = "「你知道附近的据点吗？」"
  },
  [200420116] = {
    id = 200420116,
    name = "「就是西南方向那个。」"
  },
  [200420117] = {
    id = 200420117,
    name = "「我儿子去那个据点换物资了，让我等他回来。」"
  },
  [200420118] = {
    id = 200420118,
    name = "「年轻人，帮帮忙，你带我去那个据点吧。」"
  },
  [200420119] = {
    id = 200420119,
    name = "「我想快点找到我儿子。」"
  },
  [200420120] = {
    id = 200420120,
    name = "你想起来时的路上，路过了一个废弃的据点。"
  },
  [200420121] = {
    id = 200420121,
    name = "那个据点几年前被魔物袭击，早就废弃掉了......"
  },
  [200420122] = {
    id = 200420122,
    name = "你将据点的真实情况告诉了老人。"
  },
  [200420123] = {
    id = 200420123,
    name = "老人突然像小孩一样哭了。"
  },
  [200420124] = {
    id = 200420124,
    name = "「我想起来了......」"
  },
  [200420125] = {
    id = 200420125,
    name = "「他说去据点，他没回来......」"
  },
  [200420126] = {
    id = 200420126,
    name = "「......两年、三年，还是五年......我不记得了......」"
  },
  [200420127] = {
    id = 200420127,
    name = "「难道死在那边了？」"
  },
  [200420128] = {
    id = 200420128,
    name = "「不，不会的。」"
  },
  [200420129] = {
    id = 200420129,
    name = "老人颤巍巍地走向小屋的方向。"
  },
  [200420130] = {
    id = 200420130,
    name = "「老人家，这里毕竟是野外，你一个人很不安全的。」"
  },
  [200420131] = {
    id = 200420131,
    name = "「我送你去别的据点吧。」"
  },
  [200420132] = {
    id = 200420132,
    name = "「不行，他要我在这等他的。」"
  },
  [200420133] = {
    id = 200420133,
    name = "「他要是回来，看不见我怎么办？」"
  },
  [200420134] = {
    id = 200420134,
    name = "「他是一个孝顺的乖孩子。」"
  },
  [200420135] = {
    id = 200420135,
    name = "「不管什么都会优先考虑我。」"
  },
  [200420136] = {
    id = 200420136,
    name = "「他说会回来就一定会回来的。」"
  },
  [200420137] = {
    id = 200420137,
    name = "老人走进小屋，锁上了门。"
  },
  [200420138] = {
    id = 200420138,
    name = "你停住了半晌，叹了口气离开了。"
  },
  [200420139] = {
    id = 200420139,
    name = "你带他到了那个废弃的据点。"
  },
  [200420140] = {
    id = 200420140,
    name = "「这里已经荒废了，你儿子应该去了别的地方。」"
  },
  [200420141] = {
    id = 200420141,
    name = "「......」"
  },
  [200420142] = {
    id = 200420142,
    name = "「在那里！」"
  },
  [200420143] = {
    id = 200420143,
    name = "「是那个，我看到了！」"
  },
  [200420144] = {
    id = 200420144,
    name = "老人走进废墟里，佝偻着腰扒拉着。"
  },
  [200420145] = {
    id = 200420145,
    name = "他从废墟里找出了一把断剑。"
  },
  [200420146] = {
    id = 200420146,
    name = "「这是我儿子的剑。」"
  },
  [200420147] = {
    id = 200420147,
    name = "「哎呀，怎么断掉了。」"
  },
  [200420148] = {
    id = 200420148,
    name = "「他真是一点也不爱惜啊......」"
  },
  [200420149] = {
    id = 200420149,
    name = "（通常断掉的剑意味着......）"
  },
  [200420150] = {
    id = 200420150,
    name = "老人抱紧了剑，忽然哭了起来。"
  },
  [200420151] = {
    id = 200420151,
    name = "「......」"
  },
  [200420152] = {
    id = 200420152,
    name = "（看来他已经知道了。）"
  },
  [200420153] = {
    id = 200420153,
    name = "老人哭了很久，才想起你还在旁边。"
  },
  [200420154] = {
    id = 200420154,
    name = "「谢谢你......带我来这里。」"
  },
  [200420155] = {
    id = 200420155,
    name = "「这里不安全，我送你回去吧。」"
  },
  [200420156] = {
    id = 200420156,
    name = "「不用了......」"
  },
  [200420157] = {
    id = 200420157,
    name = "「儿子没了，我活着也没什么意思了。」"
  },
  [200420158] = {
    id = 200420158,
    name = "「本来我也活不久的。」"
  },
  [200420159] = {
    id = 200420159,
    name = "「都是他一直在努力赚钱给我治病。」"
  },
  [200420160] = {
    id = 200420160,
    name = "「......是我害了儿子......」"
  },
  [200420161] = {
    id = 200420161,
    name = "老人又开始哭，哭了一会儿突然没有了声音。"
  },
  [200420162] = {
    id = 200420162,
    name = "他充满皱纹的眼角还流着泪。"
  },
  [200420163] = {
    id = 200420163,
    name = "你叫了他两声，没有任何反应。"
  },
  [200420164] = {
    id = 200420164,
    name = "你探了探他的鼻息......"
  },
  [200420165] = {
    id = 200420165,
    name = "老人已经永远地离开了这个世界。"
  },
  [200420166] = {
    id = 200420166,
    name = "你叹息一声，将老人与断剑葬在了一起，离开了废墟。"
  },
  [200430001] = {
    id = 200430001,
    name = "「参加猎魔大会夺旗的探险者们在这里集合啦！」"
  },
  [200430002] = {
    id = 200430002,
    name = "圈落外，前方一阵人潮涌动，你听见人们欢呼的声音。"
  },
  [200430003] = {
    id = 200430003,
    name = "「猎魔大会？」"
  },
  [200430004] = {
    id = 200430004,
    name = "你挤进了人群，发现一些探险者正在招募成员，组建临时行动小组。"
  },
  [200430005] = {
    id = 200430005,
    name = "「各位探险者，分组时间即将结束，马上准备出发了！」"
  },
  [200430006] = {
    id = 200430006,
    name = "「等等，我们还少一个人！」"
  },
  [200430007] = {
    id = 200430007,
    name = "「我们队也少一个！」"
  },
  [200430008] = {
    id = 200430008,
    name = "「还有一分钟的时间，你们最好快点找到人。」"
  },
  [200430009] = {
    id = 200430009,
    name = "几组探险者齐刷刷地看向了落单的你。"
  },
  [200430010] = {
    id = 200430010,
    name = "「诶？这位探险者，你还没选好小组吗？」"
  },
  [200430011] = {
    id = 200430011,
    name = "「来我们这啊，攻防兼备，保证得第一！」"
  },
  [200430012] = {
    id = 200430012,
    name = "「不不，应该来我们这，你去夺宝，我们全力掩护你，绝对没问题！」"
  },
  [200430013] = {
    id = 200430013,
    name = "「来我们这一组吧，快些结束拿奖励，还不耽误参加下一轮活动。」"
  },
  [200430014] = {
    id = 200430014,
    name = "「喂，你们本来就多一个人，还和我们抢人！」"
  },
  [200430015] = {
    id = 200430015,
    name = "「别听他们的，他们只想让你冲锋可不会管你的死活！」"
  },
  [200430016] = {
    id = 200430016,
    name = "「......」"
  },
  [200430017] = {
    id = 200430017,
    name = "「比赛就是为了赢的，我当然选猎影的队伍。」"
  },
  [200430018] = {
    id = 200430018,
    name = "你的选择引起了另外两队的敌视，他们忿忿地看着你，仿佛将你视为此生大敌。"
  },
  [200430019] = {
    id = 200430019,
    name = "猎影小组的成员则露出势在必得的神色。"
  },
  [200430020] = {
    id = 200430020,
    name = "「嘿，明智的选择，第一名非我们莫属！」"
  },
  [200430021] = {
    id = 200430021,
    name = "「看起来不妙，他们两组好像联合起来了。」"
  },
  [200430022] = {
    id = 200430022,
    name = "你看到言灵探险者队找到了异刃探险者队，他们商量了一阵，决定合作对付你所在的小组。"
  },
  [200430023] = {
    id = 200430023,
    name = "「哼，他们不敢乱来！比赛禁止一切手段的互相伤害，组织者会全程监视我们的行动，一旦违规成绩就会作废。」"
  },
  [200430024] = {
    id = 200430024,
    name = "「看到前面那个山头了吗，只要把上面的旗帜抢回来就能获胜。我们已经发现一条近路，你小子跟紧点。」"
  },
  [200430025] = {
    id = 200430025,
    name = "组织成员吹响短笛，比赛开始，你身旁的猎影探险者以最快的速度冲了出去。"
  },
  [200430026] = {
    id = 200430026,
    name = "「新来的，一会儿到了山顶有只魔物，你去把它引开，我们夺旗！」"
  },
  [200430027] = {
    id = 200430027,
    name = "你点了点头，决定听从他的指挥。"
  },
  [200430028] = {
    id = 200430028,
    name = "「很好，那只魔物又蠢又硬，你无需和它死斗，引走以后尽快下山，我们拿了旗子去山下等你！」"
  },
  [200430029] = {
    id = 200430029,
    name = "你们率先到达山顶，山顶果然盘踞着一头魔物，看有人到来，它立刻直起了庞大的身躯朝你们冲来。"
  },
  [200430030] = {
    id = 200430030,
    name = "你果断出手，全力吸引魔物的注意，身旁几名猎影则迅速散开，直奔旗帜而去。"
  },
  [200430031] = {
    id = 200430031,
    name = "你将魔物引到远处，听到了山顶的欢呼声，知道队友夺旗成功便不再恋战，疾步下山与小组会合。"
  },
  [200430032] = {
    id = 200430032,
    name = "然而当你到达山下的时候，那里站着的胜利者竟然不是猎影小组。"
  },
  [200430033] = {
    id = 200430033,
    name = "「没想到吧，最后胜利的是我们。」"
  },
  [200430034] = {
    id = 200430034,
    name = "「只要不让你们得到第一，奖励分半也无所谓，总归胜利了不是吗？」"
  },
  [200430035] = {
    id = 200430035,
    name = "你望着他们志得意满的目光，惊觉自己的临时加入，似乎破坏了几个小组之间的平衡，不禁有些感慨。"
  },
  [200430036] = {
    id = 200430036,
    name = "你想了想，提出了一些建议。"
  },
  [200430037] = {
    id = 200430037,
    name = "「前有魔物，后有两个小组，这些都是我们的阻碍，如果一起遇上，情况会对我们很不利，不如给他们两队制造点障碍？」"
  },
  [200430038] = {
    id = 200430038,
    name = "「你可别乱来，到时成绩作废我们就扒了你的皮！」"
  },
  [200430039] = {
    id = 200430039,
    name = "「不，我的意思是利用魔物来拦住他们。」"
  },
  [200430040] = {
    id = 200430040,
    name = "「哈哈，你可真是个天才，这的确是个好主意，行，到时候驱赶魔物的事就交给你了！」"
  },
  [200430041] = {
    id = 200430041,
    name = "你们率先到达山顶，山顶果然盘踞着一头魔物，看有人到来，它立刻直起了庞大的身躯朝你们冲来。"
  },
  [200430042] = {
    id = 200430042,
    name = "你果断出手，全力吸引魔物的注意，身旁几名猎影则迅速散开，直奔旗帜而去。"
