  [200430058] = {
    id = 200430058,
    name = "魔物暴起攻击，成员们立刻展开防御，你让他们先走，独自拦下魔物。"
  },
  [200430059] = {
    id = 200430059,
    name = "你全力出手与魔物战斗，不多时便将其击败。"
  },
  [200430060] = {
    id = 200430060,
    name = "你迅速追上了其他成员，然而到达山顶时却发现猎影小组已经抢先一步夺走旗帜。"
  },
  [200430061] = {
    id = 200430061,
    name = "你们因在途中选择绕开魔物而放缓了前进速度，错失比赛胜利。"
  },
  [200430062] = {
    id = 200430062,
    name = "你快速评判了敌我双方的实力，决定除掉魔物。"
  },
  [200430063] = {
    id = 200430063,
    name = "于是你召集众人协助，果断向魔物发起突袭。"
  },
  [200430064] = {
    id = 200430064,
    name = "在你全力攻击之下，几乎瞬间将魔物击毙。"
  },
  [200430065] = {
    id = 200430065,
    name = "随即你们再无顾忌，全速前进，终于率先抵达终点，夺得了本次比赛的第一名。"
  },
  [200430066] = {
    id = 200430066,
    name = "你想了想，猎影小组缺少防护，而言灵小组步伐太缓慢，攻防兼备的队伍更适合你，你选择加入异刃的队伍，言灵小组丧气地摇了摇头。"
  },
  [200430067] = {
    id = 200430067,
    name = "「兄弟，我会证明你的选择没错！让我们一起拿回旗帜，夺得这次比赛的魁首！」"
  },
  [200430068] = {
    id = 200430068,
    name = "队友们跃跃欲试，听组织者宣读最后的比赛规则，谁先夺得旁边山头的旗帜返回，谁就能取得猎魔大会组织为大家准备的赏金。"
  },
  [200430069] = {
    id = 200430069,
    name = "一声短笛响起，比赛开始。猎影组成员率先冲出了起跑线，异刃探险者一下忍不住笑了起来。"
  },
  [200430070] = {
    id = 200430070,
    name = "「这群家伙仗着腿脚快，走了最危险的路，等会儿有他们苦头吃！」"
  },
  [200430071] = {
    id = 200430071,
    name = "「而且山顶还有魔物看守，他们就算先到也很难解决。」"
  },
  [200430072] = {
    id = 200430072,
    name = "「不过咱们这条路上也会有一些魔物干扰，而且......万一他们不和看守魔物死磕，而是将其引走就糟了......」"
  },
  [200430073] = {
    id = 200430073,
    name = "「咱们得想个法子才行，你有什么主意吗？」"
  },
  [200430074] = {
    id = 200430074,
    name = "「我们的目的是夺旗，不用管拦路的魔物，至于山顶的看守魔物，就让他们去引开，只要比他们更快抢到旗帜就行。」"
  },
  [200430075] = {
    id = 200430075,
    name = "「说得倒是轻松，拦路的魔物如果不管，很可能我们还没到终点就有人受伤了。」"
  },
  [200430076] = {
    id = 200430076,
    name = "「或者......你去对付魔物，我们去夺旗。」"
  },
  [200430077] = {
    id = 200430077,
    name = "「如果能获得胜利的话，这样也不是不行。」"
  },
  [200430078] = {
    id = 200430078,
    name = "异刃探险者同意了你的提议，你们加速前进，没过多久果然与魔物狭路相逢。"
  },
  [200430079] = {
    id = 200430079,
    name = "你率先出击，并将一路的魔物都吸引了过来，确保小组其他成员顺利通行。"
  },
  [200430080] = {
    id = 200430080,
    name = "战斗结束后，你加速赶至山顶。此时猎影小组已率先一步夺旗，异刃小组因中了猎影小组的圈套，错失了夺魁的机会。"
  },
  [200430081] = {
    id = 200430081,
    name = "「等着吧，下一次我们一定拿第一名！」"
  },
  [200430082] = {
    id = 200430082,
    name = "「有句话叫做兵贵神速，只要我们够快，不管是魔物还是人，都追不上我们。」"
  },
  [200430083] = {
    id = 200430083,
    name = "「可我们这条路上有不少魔物阻拦，依你看要怎样才能快得起来？」"
  },
  [200430084] = {
    id = 200430084,
    name = "「如果你们相信我的话，由我去夺旗，你们牵制途中魔物，顺便干扰其他小组的判断。」"
  },
  [200430085] = {
    id = 200430085,
    name = "「......好，如果成功了，那你就是我们的奇兵！」"
  },
  [200430086] = {
    id = 200430086,
    name = "异刃小组同意了你的提议，你们加速前进，没过多久果然与魔物狭路相逢，成员们悉数出击，你则趁机直奔山顶。"
  },
  [200430087] = {
    id = 200430087,
    name = "到达山顶时，你见猎影小组正试图引走看守的魔物，你抓住时机，成功夺走了旗帜。"
  },
  [200430088] = {
    id = 200430088,
    name = "「不可能！你怎么可能比我们还快？！」"
  },
  [200430089] = {
    id = 200430089,
    name = "在猎影小组成员不可思议的眼神中，你潇洒离去。"
  },
  [200430090] = {
    id = 200430090,
    name = "「哈哈哈哈，我就知道，我们的合作一定能成功！」"
  },
  [200430091] = {
    id = 200430091,
    name = "「这份奖励你拿好了！下次比赛再一起合作啊！」"
  },
  [200430092] = {
    id = 200430092,
    name = "你想了想，决定先去圈落整顿补给，等一切搞定后再来看看这个所谓的猎魔大会。"
  },
  [200430093] = {
    id = 200430093,
    name = "不过等你返回时，发现活动已经结束了。"
  },
  [200440001] = {
    id = 200440001,
    name = "「参加猎魔大会寻宝的探险者们在这里集合啦！」"
  },
  [200440002] = {
    id = 200440002,
    name = "圈落外，前方一阵人潮涌动，你听见人们欢呼的声音。"
  },
  [200440003] = {
    id = 200440003,
    name = "「猎魔大会？」"
  },
  [200440004] = {
    id = 200440004,
    name = "你挤入人群，听见组织者正在介绍猎魔大会的寻宝规则。"
  },
  [200440005] = {
    id = 200440005,
    name = "「本次寻宝的线索我们都以谜题的方式告诉了大家。在天黑前，第一个找到宝物并带回给我的，将获得丰厚奖励！」"
  },
  [200440006] = {
    id = 200440006,
    name = "「线索......谜题？」"
  },
  [200440007] = {
    id = 200440007,
    name = "「你是第一次参加这个活动吗？」"
  },
  [200440008] = {
    id = 200440008,
    name = "「是啊，我是路过遇到的。」"
  },
  [200440009] = {
    id = 200440009,
    name = "「那你运气真好啊！你如果想参加，就快去找裁判要线索吧！」"
  },
  [200440010] = {
    id = 200440010,
    name = "你按他所说，从裁判员那里得到一张纸条，上面写着——魔物翅膀的颜色。"
  },
  [200440011] = {
    id = 200440011,
    name = "「怎么样，拿到线索了吗？」"
  },
  [200440012] = {
    id = 200440012,
    name = "「嗯。」"
  },
  [200440013] = {
    id = 200440013,
    name = "「呵呵，只要能解开谜题，就能找到宝物！」"
  },
  [200440014] = {
    id = 200440014,
    name = "「不好，大家都出发了，你还不动身吗？还是说想找个同伴组队？」"
  },
  [200440015] = {
    id = 200440015,
    name = "「呵呵，你不也还没出发。」"
  },
  [200440016] = {
    id = 200440016,
    name = "「嘿嘿......我脑子笨，总是比别人慢一步，我先想想线索再出发。」"
  },
  [200440017] = {
    id = 200440017,
    name = "你向探险者表示了感谢，随即独自展开行动。"
  },
  [200440018] = {
    id = 200440018,
    name = "你分析着谜题，带翅膀的魔物常分布在山林溪谷，附近正好有一条河，于是你快步向河流跑去。"
  },
  [200440019] = {
    id = 200440019,
    name = "果然这里已经聚集了不少人，但似乎并无发现，有人甚至已经开始下水摸索河底。"
  },
  [200440020] = {
    id = 200440020,
    name = "见到这一幕，你再次思索起谜题。"
  },
  [200440021] = {
    id = 200440021,
    name = "你根据魔物翅膀这个线索来到了此地，那么颜色又指代什么呢？"
  },
  [200440022] = {
    id = 200440022,
    name = "你正思索着，忽然瞥见河滩上的几棵树，树上开满了蓝紫色的花。"
  },
  [200440023] = {
    id = 200440023,
    name = "你快步朝大树走去。"
  },
  [200440024] = {
    id = 200440024,
    name = "一阵大风吹来，河滩上的众人都闻到了一股幽香，其中一些人也纷纷向大树跑去。"
  },
  [200440025] = {
    id = 200440025,
    name = "这股香味让你觉得有些熟悉，不禁停下了脚步。"
  },
  [200440026] = {
    id = 200440026,
    name = "你忽然想起这种花香具有安眠麻痹的效果！于是立即后退一段距离，屏住呼吸等待风停。"
  },
  [200440027] = {
    id = 200440027,
    name = "冲在最前方的探险者已经被花香影响，逐渐瘫软在地上。"
  },
  [200440028] = {
    id = 200440028,
    name = "你环顾四周，发现被风吹散的花朵纷纷飘向附近一处石壁。"
  },
  [200440029] = {
    id = 200440029,
    name = "所有的线索忽然在你的脑海汇集，你知道宝物在哪里了！"
  },
  [200440030] = {
    id = 200440030,
    name = "你冲向石壁仔细搜寻，果然找到一个金属盒子，盒子上还刻画着圈落的标记。"
  },
  [200440031] = {
    id = 200440031,
    name = "你将盒子带回交给了组织者。"
  },
  [200440032] = {
    id = 200440032,
    name = "「这么快？！这次其实藏了比以往更多的盒子，但你是第一个将它带回的人，恭喜你！」"
  },
  [200440033] = {
    id = 200440033,
    name = "宣告胜者出现的钟声响起，在无数人羡慕的眼神下，你取走了丰厚的奖励。"
  },
  [200440034] = {
    id = 200440034,
    name = "为了抢占先机，你继续迎着花香跑了过去，但没走几步，一阵晕眩感袭来。"
  },
  [200440035] = {
    id = 200440035,
    name = "（想起来了！这种花香具有安眠麻痹的效果！）"
  },
  [200440036] = {
    id = 200440036,
    name = "你急忙屏住呼吸，但为时已晚，强烈的晕眩感让你们这些靠近的人都瘫软在了地上。"
  },
  [200440037] = {
    id = 200440037,
    name = "过了好一会儿你才缓过来，随即捂住口鼻前往大树附近搜寻。"
  },
  [200440038] = {
    id = 200440038,
    name = "你和众人找了很久，直到日落也没有找到所谓的宝物，只能败兴而归。"
  },
  [200440039] = {
    id = 200440039,
    name = "「我们一起找吧，多个人多一双眼睛，要是侥幸胜出，那我们就平分奖励。」"
  },
  [200440040] = {
    id = 200440040,
    name = "「呜哇，你真是个大好人，对了这是我的线索，你脑子聪明，你帮我看看吧。」"
  },
  [200440041] = {
    id = 200440041,
    name = "你接过了她递来的纸条，上面写的内容和你的不同。"
  },
  [200440042] = {
    id = 200440042,
    name = "「我的提示是魔物翅膀的颜色，而你得到的提示却是没有光明的地方......」"
  },
  [200440043] = {
    id = 200440043,
    name = "你陷入了思索，两条线索似乎毫无关联，热心的探险者突然一拍脑袋。"
  },
  [200440044] = {
    id = 200440044,
    name = "「魔物！没有光明！或许我知道！」"
  },
  [200440045] = {
    id = 200440045,
    name = "她告诉你周围有一个山谷，很符合没有光明的描述，而那个山谷中正好存在一些带翅膀的魔物。"
  },
  [200440046] = {
    id = 200440046,
    name = "你们决定去山谷看看，但刚走到附近，便发现此处已经有不少探险者了。"
  },
  [200440047] = {
    id = 200440047,
    name = "「哇，原来其他人也猜到了，咱们也得抓紧了！」"
  },
  [200440048] = {
    id = 200440048,
    name = "你们立即加入搜寻的人群，在山谷中四处摸索。"
  },
  [200440049] = {
    id = 200440049,
    name = "「啊！快、快跑啊！」"
  },
  [200440050] = {
    id = 200440050,
    name = "突然，热心的探险者尖叫着跑了回来，她身后是一大群魔物。"
  },
  [200440051] = {
    id = 200440051,
    name = "场面开始混乱。"
  },
  [200440052] = {
    id = 200440052,
    name = "眼见她陷入危机，你果断冲了出去，将她护在身后，并全力迎击魔物。"
  },
  [200440053] = {
    id = 200440053,
    name = "你的举动瞬间引起了周围人的注意，混乱的局面稍微安定了一点。"
  },
  [200440054] = {
    id = 200440054,
    name = "此时众人才发现来袭的都是些普通魔物，于是纷纷高喊着跟随你一起反击。"
  },
  [200440055] = {
    id = 200440055,
    name = "局面瞬间逆转，你带着众人很快便将所有魔物剿灭。"
  },
  [200440056] = {
    id = 200440056,
    name = "众人欢呼过后，寻宝继续进行。"
  },
  [200440057] = {
    id = 200440057,
    name = "「好险......谢谢你，呜呜呜......要不是你，我今天就要被魔物吃掉了。」"
  },
  [200440058] = {
    id = 200440058,
    name = "「不必客气，而且托你的福，我知道宝物在哪里了！」"
  },
  [200440059] = {
    id = 200440059,
    name = "「啊？在哪里？」"
  },
  [200440060] = {
    id = 200440060,
    name = "「刚才袭击的魔物中，飞行的魔物大部分翅膀都是紫色，而这个山谷中，紫色的地方就是——」"
  },
  [200440061] = {
    id = 200440061,
    name = "「山腰的大树！」"
  },
  [200440062] = {
    id = 200440062,
    name = "她顺着你指的方向看去，一棵枝叶为紫色的大树正耸立在那里。"
  },
  [200440063] = {
    id = 200440063,
    name = "「我知道一条小路可以快速过去，你跟我来！」"
  },
  [200440064] = {
    id = 200440064,
    name = "她显得非常激动，急忙带着你抄小路直奔大树。"
  },
  [200440065] = {
    id = 200440065,
    name = "你们在树下搜寻，果然在一个树缝里发现了一个盒子。"
  },
  [200440066] = {
    id = 200440066,
    name = "你们拿着盒子交给了组织者，幸运地获得了本次比赛的第一名。"
  },
  [200440067] = {
    id = 200440067,
    name = "你见魔物虽然跟在她身后，但还有一段安全距离，便决定先观察一下情况。"
  },
  [200440068] = {
    id = 200440068,
    name = "然而山谷中寻宝的众人听到魔物来袭，看见慌乱四散的人群后，纷纷逃窜，局面瞬间失控。"
  },
  [200440069] = {
    id = 200440069,
    name = "「没办法，先救人吧！」"
  },
  [200440070] = {
    id = 200440070,
    name = "你被逃窜的人群不停冲撞，好不容易才赶到了她身边。"
  },
  [200440071] = {
    id = 200440071,
    name = "虽然你将魔物快速解决，但她也在混乱中被魔物所伤，你只得将她背回圈落治疗。"
  },
  [200440072] = {
    id = 200440072,
    name = "「呜呜，对不起，我就知道我只会拖大家的后腿......」"
  },
  [200440073] = {
    id = 200440073,
    name = "她非常自责，你费了些时间才安慰好她，待她情绪稳定后，你也离开了。"
  },
  [200440074] = {
    id = 200440074,
    name = "你向探险者表示了感谢，随即准备找其他人合作。"
  },
  [200440075] = {
    id = 200440075,
    name = "经过观察，你发现了一对兄妹，他们也正在招募合作对象，于是你向二人走去。"
  },
  [200440076] = {
    id = 200440076,
    name = "「我们同意你加入，找到了奖励就平分吧。不过比起寻宝，我们需要优先保护我妹妹，没问题吧？」"
  },
  [200440077] = {
    id = 200440077,
    name = "这位兄长看起来不在乎什么奖励，对他而言，显然妹妹更加重要。"
  },
  [200440078] = {
    id = 200440078,
    name = "「那么我们先交换情报吧。」"
  },
  [200440079] = {
    id = 200440079,
    name = "随即你们互相展示了收到的线索。"
  },
  [200440080] = {
    id = 200440080,
    name = "「魔物翅膀的颜色、火焰无法升起的地方、温柔的所在......会是在哪里呢......」"
  },
  [200440081] = {
    id = 200440081,
    name = "「魔物的翅膀颜色太多了，但火焰无法升起，一定是水中吧？温柔的话，也是水吧？那么一定就在河里啦！」"
  },
  [200440082] = {
    id = 200440082,
    name = "哥哥立马同意了妹妹的意见，似乎不管她说什么，哥哥也不会有异议。"
  },
  [200440083] = {
    id = 200440083,
    name = "你们来到河边，此时这里已经聚集了不少人。"
  },
  [200440084] = {
    id = 200440084,
    name = "见有人已经进入河中搜寻，妹妹玩性大起，瞬间跳入河中玩起水来。"
  },
  [200440085] = {
    id = 200440085,
    name = "你告诉她水中可能有潜藏的危险，而且还要找寻宝物，将妹妹劝了上来。"
  },
  [200440086] = {
    id = 200440086,
    name = "哥哥皱了皱眉，对你的反应很不满意。"
  },
  [200440087] = {
    id = 200440087,
    name = "随即你们在河畔找了一会儿，但是没有任何发现。"
  },
  [200440088] = {
    id = 200440088,
    name = "妹妹对这种枯燥的寻找失去了耐性，发起脾气，为了哄她哥哥准备带她回圈落逛街，你们就此分开。"
  },
  [200440089] = {
    id = 200440089,
    name = "你独自又搜寻了一阵，但直到胜者出现的钟声响起，你也没有任何发现。"
  },
  [200440090] = {
    id = 200440090,
    name = "你想了想，妹妹的分析似乎也有道理，便也跟着下了水。"
  },
  [200440091] = {
    id = 200440091,
    name = "妹妹认为你也是下水游玩，立即捧起水向你洒来。"
  },
  [200440092] = {
    id = 200440092,
    name = "受到水花攻击的你不甘示弱，也捧起水向妹妹洒去，同时你们将本来没打算下水的哥哥也卷入了战局。"
  },
  [200440093] = {
    id = 200440093,
    name = "你们几人似乎完全忘记了寻宝这件事，尽情在水中嬉闹。"
  },
  [200440094] = {
    id = 200440094,
    name = "过了一会儿，一些周围寻宝的人也被你们带动，放弃了搜寻加入你们。"
  },
  [200440095] = {
    id = 200440095,
    name = "当远处胜者出现的钟声响起，你们才停止了嬉闹。"
  },
  [200440096] = {
    id = 200440096,
    name = "「啊！糟了！都忘记寻找宝物了！」"
  },
  [200440097] = {
    id = 200440097,
    name = "「呵呵，不过度过了一段不错的时间，不是吗？」"
  },
  [200440098] = {
    id = 200440098,
    name = "「反正参加活动也是为了能享受乐趣而已。」"
  },
  [200440099] = {
    id = 200440099,
    name = "「呵呵，你说的没错！不过玩了半天，感觉有点饿了，去吃点东西吧！」"
  },
  [200440100] = {
    id = 200440100,
    name = "「好啊。这位探险者要一起来吗？我知道圈落里有家很棒的餐厅。」"
  },
  [200440101] = {
    id = 200440101,
    name = "你跟随二人向圈落走去，虽然未能找到宝物，但也度过了一段愉悦的时光。"
  },
  [200440102] = {
    id = 200440102,
    name = "你想了想，决定先去圈落整顿补给，等一切搞定后再来看看这个所谓的猎魔大会。"
  },
  [200440103] = {
    id = 200440103,
    name = "不过等你返回时，发现活动已经结束了。"
  },
  [200450001] = {
    id = 200450001,
    name = "「参加猎魔大会诛戮的探险者们在这里集合啦！」"
  },
  [200450002] = {
    id = 200450002,
    name = "圈落外，前方一阵人潮涌动，你听见人们欢呼的声音。"
  },
  [200450003] = {
    id = 200450003,
    name = "「猎魔大会？」"
  },
  [200450004] = {
    id = 200450004,
    name = "你挤入人群，听见组织者正在介绍规则。"
  },
  [200450005] = {
    id = 200450005,
    name = "「......还是按照老规矩来，相同时间内谁击杀的魔物最多，谁就能带走今天的奖励！」"
  },
  [200450006] = {
    id = 200450006,
    name = "「比赛杀魔物？」"
  },
  [200450007] = {
    id = 200450007,
    name = "「噫，新面孔啊。」"
  },
  [200450008] = {
    id = 200450008,
    name = "「嗯，我今天刚到这里。」"
  },
  [200450009] = {
    id = 200450009,
    name = "「哇！那你可不能错过我们一年一度的盛典啊！无论亲自参加还是观赛，都能让你永生难忘！」"
  },
  [200450010] = {
    id = 200450010,
    name = "「我也能参赛吗？」"
  },
  [200450011] = {
    id = 200450011,
    name = "「当然！不管是谁，只要报名就能参赛。」"
  },
  [200450012] = {
    id = 200450012,
    name = "「今年有好几位明星选手，你要是参赛的话可得加油啦，哈哈哈。」"
  },
  [200450013] = {
    id = 200450013,
    name = "「既然如此，那我还是和你一起观赛吧。」"
  },
  [200450014] = {
    id = 200450014,
    name = "「好，那我们赶紧过去赛场，要是晚了，好位置就被抢光了。」"
  },
  [200450015] = {
    id = 200450015,
    name = "你跟着他来到一处山坡上，周围早已聚集了不少人。"
  },
  [200450016] = {
    id = 200450016,
    name = "此处确实如他所说是个观赛的好地方，能将下方猎场的情况尽收眼底。"
  },
  [200450017] = {
    id = 200450017,
    name = "探险者向你介绍起猎场的情况，魔物在哪里放出，选手在哪里入场他都一清二楚。"
  },
  [200450018] = {
    id = 200450018,
    name = "他正和你说着，赛场内响起三声钟声。"
  },
  [200450019] = {
    id = 200450019,
    name = "「哦哦！马上就要开始了！」"
  },
  [200450020] = {
    id = 200450020,
    name = "随着第一名选手入场，比赛正式开始。"
  },
  [200450021] = {
    id = 200450021,
    name = "在一波又一波的欢呼声中，前几名选手分别斩杀了一定数目的魔物。"
  },
  [200450022] = {
    id = 200450022,
    name = "「今年竞争果然激烈啊，不知道能不能打破之前的猎杀记录。」"
  },
  [200450023] = {
    id = 200450023,
    name = "又一位选手上场了，你看了他一眼，随即皱起眉头。"
  },
  [200450024] = {
    id = 200450024,
    name = "此人看上去并不强，但防护却是已经上场的选手中最简单的。"
  },
  [200450025] = {
    id = 200450025,
    name = "这样的装备确实可以充分发挥自身的灵活性，但如果敌人众多......"
  },
  [200450026] = {
    id = 200450026,
    name = "果然，开场不久，此人便被魔物群逼得节节败退，所谓的灵活性在众多魔物前毫无作用。"
  },
  [200450027] = {
    id = 200450027,
    name = "他的表现令周围观众非常不满，不时有嘘声传出，随着场内局面演变为参赛者一个劲地逃跑，魔物不停追赶时，周围更是爆发出阵阵哄笑。"
  },
  [200450028] = {
    id = 200450028,
    name = "参赛者已经负伤，但主办方似乎仍没有干预的打算。"
  },
  [200450029] = {
    id = 200450029,
    name = "「再这么下去，他会死的。」"
  },
  [200450030] = {
    id = 200450030,
    name = "「啊？」"
  },
  [200450031] = {
    id = 200450031,
    name = "你刚说完，参赛者又被魔物击中一次，这次主办方终于决定干预了。"
  },
  [200450032] = {
    id = 200450032,
    name = "但进场协助的人员似乎出了岔子，意外将另一波魔物放了出来......"
  },
  [200450033] = {
    id = 200450033,
    name = "赛场内一大群魔物追着几人狂奔，眼看惨剧即将上演，周围却爆发出一阵又一阵的热烈呼声。"
  },
  [200450034] = {
    id = 200450034,
    name = "你拨开人群向着赛场冲了过去，几名赛场管理人员想拦住你，却被你一个眼神瞪了回去。"
  },
  [200450035] = {
    id = 200450035,
    name = "你翻身进入赛场，一边轰击魔物，转移魔物的注意力，一边迂回旋转，将冲向你的魔物绕得团团转。"
  },
  [200450036] = {
    id = 200450036,
    name = "同时你也招呼着逃窜的人，让他们往你的方向逃。"
  },
  [200450037] = {
    id = 200450037,
    name = "几人见你相救，如同抓住了救命稻草，全力向你奔来。"
  },
  [200450038] = {
    id = 200450038,
    name = "其中跑得最快的人距离你仅几步之远时，你一个箭步冲出，抓起他的衣领，猛地向场外人群中丢去。"
  },
  [200450039] = {
    id = 200450039,
    name = "「哇！！！」"
  },
  [200450040] = {
    id = 200450040,
    name = "人群响起一阵惊呼，稳稳地将他接住了。"
  },
  [200450041] = {
    id = 200450041,
    name = "随即你采用相同的方式，又将其他几人全部扔出场外后，你才寻找机会跳了出去，留下一群魔物在场内嘶吼咆哮。"
  },
  [200450042] = {
    id = 200450042,
    name = "「谢、谢谢你。」"
  },
  [200450043] = {
    id = 200450043,
    name = "「你们的应急措施也太不可靠了！算了，赶紧救治伤员，解决场内魔物吧。」"
  },
  [200450044] = {
    id = 200450044,
    name = "「好、好的！我这就安排，这就安排。」"
  },
  [200450045] = {
    id = 200450045,
    name = "看着他一路小跑的样子，你不禁感到扫兴。"
  },
  [200450046] = {
    id = 200450046,
    name = "摇了摇头，你混入四散的人群离开了赛场。"
  },
  [200450047] = {
    id = 200450047,
    name = "参赛者陷入危机，营救人员也陷入危机，你见组织者正在下方跑来跑去召集人手，似乎在准备更大规模的救援。"
  },
  [200450048] = {
    id = 200450048,
    name = "但就在此时，人群中爆发出一阵惊呼。"
  },
  [200450049] = {
    id = 200450049,
    name = "你向赛场内看去，一名营救人员不小心被石头绊到，摔倒在地，身后的魔物已经一拥而上。"
  },
  [200450050] = {
    id = 200450050,
    name = "新的救援尚未到位，但场内的情况已不容乐观，你知道不能再等了。"
  },
  [200450051] = {
    id = 200450051,
    name = "随即，你一个纵身跃下山坡，踩着前方观众的头，在一声声惊呼中跳入了赛场。"
  },
  [200450052] = {
    id = 200450052,
    name = "你一记魔法击退了营救人员周围的魔物，他虽拼死抵抗，但此刻已是身受重伤。"
  },
  [200450053] = {
    id = 200450053,
    name = "你抓起他的衣领，猛地向场外人群中丢去。"
  },
  [200450054] = {
    id = 200450054,
    name = "（是生是死，就看你自己的造化了。）"
  },
  [200450055] = {
    id = 200450055,
    name = "人群响起一阵惊呼，稳稳地将他接住了。"
  },
  [200450056] = {
    id = 200450056,
    name = "随即你向着还在奔逃的几人冲去，将他们全部扔出场外后，你才寻找机会跳了出去，留下一群魔物在场内嘶吼咆哮。"
  },
  [200450057] = {
    id = 200450057,
    name = "此时，组织者才带着新组建的救援队出现。"
  },
  [200450058] = {
    id = 200450058,
    name = "你看他气喘吁吁的样子，没有多说什么，只是瞥了一眼场内的魔物。"
  },
  [200450059] = {
    id = 200450059,
    name = "他瞬间明白了你的意思。"
  },
  [200450060] = {
    id = 200450060,
    name = "「你们赶紧出动，把赛场内所有魔物全部剿灭！」"
  },
  [200450061] = {
    id = 200450061,
    name = "当他安排好一切，准备向你道谢时，你已经混入四散的人群，离开了赛场。"
  },
  [200450062] = {
    id = 200450062,
    name = "「来都来了，我也去试试吧。」"
  },
  [200450063] = {
    id = 200450063,
    name = "你按照探险者的指引找到了报名者，他递给你一个牌子，上面写着一个数字。"
  },
  [200450064] = {
    id = 200450064,
    name = "「你的号码是28，叫到这个数字的时候你就上场。」"
  },
  [200450065] = {
    id = 200450065,
    name = "报名者又和你讲了些比赛规则，比如每轮挑战都是单人进行。"
  },
  [200450066] = {
    id = 200450066,
    name = "如果在限定时间内击杀完放出来的所有魔物，裁判会暂停时间，继续放出新的魔物给参赛者继续挑战。"
  },
  [200450067] = {
    id = 200450067,
    name = "最终在指定时间内，击杀魔物最多的人获得优胜。"
  },
  [200450068] = {
    id = 200450068,
    name = "「嗯，我明白了。」"
  },
  [200450069] = {
    id = 200450069,
    name = "了解完规则，你根据他的指引走向赛场。"
  },
  [200450070] = {
    id = 200450070,
    name = "伴随着三声钟声响起，比赛正式开启。"
  },
  [200450071] = {
    id = 200450071,
    name = "你坐在候场席位上，观察着前面选手的挑战。"
  },
  [200450072] = {
    id = 200450072,
    name = "第一轮3头，第二轮5头，第三轮7头......"
  },
  [200450073] = {
    id = 200450073,
    name = "但直到11号选手登场前，也没有人能将前三轮魔物全部击杀。"
  },
  [200450074] = {
    id = 200450074,
    name = "「请直接放出前两轮的魔物。」"
  },
  [200450075] = {
    id = 200450075,
    name = "听着他的话你心里一惊，竟然还能这样？！但同时面对8头魔物......看来他很自信啊。"
  },
  [200450076] = {
    id = 200450076,
    name = "观众席爆发出热烈的欢呼，原来11号正是今天的明星选手之一。"
  },
  [200450077] = {
    id = 200450077,
    name = "经过一番精彩的战斗，他赶在计时结束前将第四轮怪物全部歼灭。"
  },
  [200450078] = {
    id = 200450078,
    name = "「哼，跳梁小丑罢了。」"
  },
  [200450079] = {
    id = 200450079,
    name = "和周围观众山呼海啸的欢呼声不同，24号选手似乎对11号很不满。"
  },
  [200450080] = {
    id = 200450080,
    name = "「阁下对他很熟悉吗？」"
  },
  [200450081] = {
    id = 200450081,
    name = "24号看了你一眼，眼中满是不屑，似乎与你们坐在一起已经是对你们的恩赐。"
  },
  [200450082] = {
    id = 200450082,
    name = "「熟悉？不，我的脑海中容不下小丑的存在。」"
  },
  [200450083] = {
    id = 200450083,
    name = "「哟，我以为是谁呢，原来是我之前破纪录时的背景板啊。」"
  },
  [200450084] = {
    id = 200450084,
    name = "「是你？」"
  },
  [200450085] = {
    id = 200450085,
    name = "「哎~几年没来参加，这记录竟然还是我当年打下的。」"
  },
  [200450086] = {
    id = 200450086,
    name = "你眉头微挑，看向了说话之人，此人外表看似普通，但举手投足间，都透露着一种强者独有的感觉。"
  },
  [200450087] = {
    id = 200450087,
    name = "（竟然也收敛了气息吗......）"
  },
  [200450088] = {
    id = 200450088,
    name = "「这位兄弟是第一次来吗？运气不错嘛。」"
  },
  [200450089] = {
    id = 200450089,
    name = "眼见你打量他，17号来到你身边，拍了拍你的肩膀，看似和善，但所说的话却无比傲慢。"
  },
  [200450090] = {
    id = 200450090,
    name = "「第一次来就能和这位背景板一起见证新纪录的诞生，哈哈哈，真是好运气啊。」"
  },
  [200450091] = {
    id = 200450091,
    name = "「......」"
  },
  [200450092] = {
    id = 200450092,
    name = "「啊，兄弟，我可没有贬低你的意思，不仅是你，今天在座的诸位，也都是背景板，啊哈哈哈哈~」"
  },
  [200450093] = {
    id = 200450093,
    name = "看着17号的嚣张模样，24号眼中似乎要喷出火来。"
  },
  [200450094] = {
    id = 200450094,
    name = "又过了一段时间，终于轮到你上场了。"
  },
  [200450095] = {
    id = 200450095,
    name = "此前，17号再次突破大赛记录，将第一名的斩杀记录重新定在了第七轮魔物。"
  },
  [200450096] = {
    id = 200450096,
    name = "而24号即使拼尽了全力，也还是差了一点。"
  },
  [200450097] = {
    id = 200450097,
    name = "你走进赛场，舒展了一番身体，此时组织者向你问起释放魔物的顺序。"
  },
  [200450098] = {
    id = 200450098,
    name = "你决定给现场这些人一点震撼。"
  },
  [200450099] = {
    id = 200450099,
    name = "「第一次放1-4轮魔物，第二次放5-7轮，第三次......再说吧。」"
  },
  [200450100] = {
    id = 200450100,
    name = "寂静，绝对的寂静。你话音一落，全场一片死寂。"
  },
  [200450101] = {
    id = 200450101,
    name = "「你、你确定吗？这么多，发生意外我们可来不及救援。」"
  },
  [200450102] = {
    id = 200450102,
    name = "「确定。」"
  },
  [200450103] = {
    id = 200450103,
    name = "又是一阵沉默，片刻后，随着一声哨响，一大群魔物嘶吼着冲出牢笼，向你扑来。"
  },
  [200450104] = {
    id = 200450104,
    name = "你在之前的观察中，早已知晓这些魔物的实力，洞悉了他们的弱点。"
  },
  [200450105] = {
    id = 200450105,
    name = "此时，你决定全力展现实力，让小看你的人闭嘴。"
  },
  [200450106] = {
    id = 200450106,
    name = "你原地站定，任由魔物冲来。"
  },
  [200450107] = {
    id = 200450107,
    name = "「喂喂，他就那样站着，这是在找死吧？！」"
  },
  [200450108] = {
    id = 200450108,
    name = "十米、八米、五米......"
  },
  [200450109] = {
    id = 200450109,
    name = "你默数着魔物们与你的距离，许多观众已经害怕地扭过头，紧闭双眼。"
  },
  [200450110] = {
    id = 200450110,
    name = "三米、两米、一米——"
  },
  [200450111] = {
    id = 200450111,
    name = "就在魔物即将撕碎你的前一秒，你一脚踏地，跃上高空，随即将早已准备完成的魔法术式向下全力施放。"
  },
  [200450112] = {id = 200450112, name = "轰——"},
  [200450113] = {
    id = 200450113,
    name = "巨大的爆炸席卷了整个赛场，爆炸的强风将近距离的观众也吹得倒退几步。"
  },
  [200450114] = {
    id = 200450114,
    name = "烟尘消散，你立于赛场中央。"
  },
  [200450115] = {
    id = 200450115,
    name = "短暂寂静之后，现场爆发出山呼海啸般地呼喊......"
  },
  [200450116] = {
    id = 200450116,
    name = "这一次，你将大赛的记录刷新到了一个前所未有的数字。"
  },
  [200450117] = {
    id = 200450117,
    name = "第二天，为了躲避上门采访、求助、握手、邀约等等的人群，你化妆扮成拾荒者悄悄离开了。"
  },
  [200450118] = {
    id = 200450118,
    name = "你决定稳扎稳打，尝试挑战自己的极限。"
  },
  [200450119] = {
    id = 200450119,
    name = "「一轮一轮地放吧，每当我解决完请立即放下一轮，直到我喊停。」"
  },
  [200450120] = {
    id = 200450120,
    name = "「这样你可就没有休息的时间了，确定吗？」"
  },
  [200450121] = {
    id = 200450121,
    name = "「确定。」"
  },
  [200450122] = {
    id = 200450122,
    name = "随着一声哨响，第一波魔物直扑你而来。"
  },
  [200450123] = {
    id = 200450123,
    name = "你全力运转魔法，抬手便是多段攻击，几乎在一瞬间便将几头魔物解决。"
  },
  [200450124] = {
    id = 200450124,
    name = "紧接着便是第二轮......"
  },
  [200450125] = {
    id = 200450125,
    name = "「喂喂，他这也太强了吧，说不定能打破17号的记录。」"
  },
  [200450126] = {
    id = 200450126,
    name = "「这不太可能，17号第一次可是直接放出了前三轮的魔物。」"
  },
  [200450127] = {
    id = 200450127,
    name = "「他看起来每一轮都很快，但时间就那么点，而且后面魔物越来越多，我才不信他能维持这样的速度。」"
  },
  [200450128] = {
    id = 200450128,
    name = "第三轮、第四轮、第五轮......"
  },
  [200450129] = {
    id = 200450129,
    name = "每一轮几乎都是魔物刚出来不久便被你剿灭......"
  },
  [200450130] = {
    id = 200450130,
    name = "观众们停下了欢呼，紧张地看着你，几乎忘记了呼吸，释放魔物的组织者手心也已出汗。"
  },
  [200450131] = {
    id = 200450131,
    name = "你仍忘情地战斗着，似乎忘记了时间，忘记了地点，肆意施放着魔法......"
  },
  [200450132] = {
    id = 200450132,
    name = "这种感觉你已经很久没感受过了，直到突然的一声长哨，将你拉回了现实。"
  },
  [200450133] = {
    id = 200450133,
    name = "你环顾四周，赛场内一片死寂。"
  },
  [200450134] = {
    id = 200450134,
    name = "「请问结束了吗？」"
  },
  [200450135] = {
    id = 200450135,
    name = "话音刚落，周围便响起山呼海啸般的喝彩声，组织者不断高喊着「新纪录」。"
  },
  [200450136] = {
    id = 200450136,
    name = "你这才反应过来。"
  },
  [200450137] = {
    id = 200450137,
    name = "庆祝会上，无数贵族才俊向你敬酒，和你攀谈，希望拉近你们的关系。"
  },
  [200450138] = {
    id = 200450138,
    name = "你实在难以应对，最终在庆祝会间隙，找了个时机偷偷溜走了。"
  },
  [200450139] = {
    id = 200450139,
    name = "你想了想，决定先去圈落整顿补给，等一切搞定后再来看看这个所谓的猎魔大会。"
  },
  [200450140] = {
    id = 200450140,
    name = "不过等你返回时，发现活动已经结束了。"
  },
  [200460001] = {
    id = 200460001,
    name = "你走在路途中，突然感觉到一阵眩晕。"
  },
  [200460002] = {
    id = 200460002,
    name = "当你发现不对劲时，已经手脚酸软，浑身无力，你凭着意志没有让自己晕过去。"
  },
  [200460003] = {
    id = 200460003,
    name = "此时前方出现了两个模糊的影子，正向你走来，你思忖着该如何应对。"
  },
  [200460004] = {
    id = 200460004,
    name = "你倒在地上假装昏迷，暗中以魔力刺激自己，以强烈的痛感让自己保持清醒，同时做好了随时拼死一搏的准备。"
  },
  [200460005] = {
    id = 200460005,
    name = "对方逐渐靠近。通过声音，你判断是两人......不，一人一宠物？"
  },
  [200460006] = {
    id = 200460006,
    name = "虽有疑惑，但你并未睁眼，静静等待对方的动作。"
  },
  [200460007] = {
    id = 200460007,
    name = "对方在你面前站了一会儿，随即周围响起一阵窸窸窣窣的声音。"
  },
  [200460008] = {
    id = 200460008,
    name = "又过了一会儿，你听到一个少女开始吟唱起咒语。"
  },
  [200460009] = {
    id = 200460009,
    name = "伴随着少女的咒语，你敏锐地觉察到地面的魔力开始以你为中心流动。"
  },
  [200460010] = {
    id = 200460010,
    name = "（似乎并无危险，是在治疗我吗？）"
  },
  [200460011] = {
    id = 200460011,
    name = "虽然并未感觉到身体有加速恢复的迹象，但流动的魔力让你觉得非常舒适。"
  },
  [200460012] = {
    id = 200460012,
    name = "少女停止了吟唱，又仔细观察了你一阵，似乎对你的情况感到疑惑。"
  },
  [200460013] = {
    id = 200460013,
    name = "少女陷入了沉默，过了一会儿她向你凑了过来，你甚至能感觉到她的鼻息。"
  },
  [200460014] = {
    id = 200460014,
    name = "你睁开了眼睛，少女正用她明亮清澈的大眼睛打量你，距离你的脸只有几厘米。"
  },
  [200460015] = {
    id = 200460015,
    name = "她被你突然睁眼吓了一大跳，怪叫着摔倒在地，倒爬了好几米。"
  },
  [200460016] = {
    id = 200460016,
    name = "你发现除了少女，之前感受到的另一个存在是一头魔物，不，长得像魔物的傀儡。"
  },
  [200460017] = {
    id = 200460017,
    name = "此刻它正乖巧地坐在一旁。"
  },
  [200460018] = {
    id = 200460018,
    name = "「你你你，你吓死我了！」"
  },
  [200460019] = {
    id = 200460019,
    name = "少女拍着胸口，好一阵才平息下来。"
  },
  [200460020] = {
    id = 200460020,
    name = "「既然没晕的话就早点说话啊，我还以为成功了呢......」"
  },
  [200460021] = {
    id = 200460021,
    name = "「阿妈给的迷烟完全没用嘛，还说什么可以轻松放倒魔物，哼。」"
  },
  [200460022] = {
    id = 200460022,
    name = "「你想做什么？」"
  },
  [200460023] = {
    id = 200460023,
    name = "「当然是把你炼成傀儡啦，如果刚才契约成功，你现在就已经是我的傀儡了！」"
  },
  [200460024] = {
    id = 200460024,
    name = "「契约？」"
  },
  [200460025] = {
    id = 200460025,
    name = "你看向地面，原来刚才的动静是少女在画魔法阵。"
  },
  [200460026] = {
    id = 200460026,
    name = "你动了动身体，发现之前的晕眩感已经减轻了很多，遂起身准备离去。"
  },
  [200460027] = {
    id = 200460027,
    name = "「喂！你要去哪里？」"
  },
  [200460028] = {
    id = 200460028,
    name = "「我没有回答你的义务，你偷袭我的事都还没和你计较，别得寸进尺。」"
  },
  [200460029] = {
    id = 200460029,
    name = "「你！我也不想啊，可是不偷袭我根本抓不住人嘛......」"
  },
  [200460030] = {
    id = 200460030,
    name = "「......」"
  },
  [200460031] = {
    id = 200460031,
    name = "「你抓人做什么？」"
  },
  [200460032] = {
    id = 200460032,
    name = "「我说过了啊，契约傀儡啊。」"
  },
  [200460033] = {
    id = 200460033,
    name = "「......」"
  },
  [200460034] = {
    id = 200460034,
    name = "眼见你又准备离开，少女立即朝你扑来，抱住了你的脚。"
  },
  [200460035] = {
    id = 200460035,
    name = "「不要走嘛，不要走嘛。」"
  },
  [200460036] = {
    id = 200460036,
    name = "少女抱着你的脚不放，开始撒娇，你继续追问她的目的。"
  },
  [200460037] = {
    id = 200460037,
    name = "「你假装我的傀儡好不好？」"
  },
  [200460038] = {
    id = 200460038,
    name = "「我拒绝。」"
  },
  [200460039] = {
    id = 200460039,
    name = "「求求你了！你一看就是好心人，好心的大哥哥，你帮帮我吧！」"
  },
  [200460040] = {
    id = 200460040,
    name = "「对了，我可以给你钱！阿秋！」"
  },
  [200460041] = {
    id = 200460041,
    name = "少女将名为阿秋的傀儡叫了过来，从它肚子里掏出一堆金币。"
  },
  [200460042] = {
    id = 200460042,
    name = "「只要你装作我的傀儡，帮我完成任务，这些钱我都给你！」"
  },
  [200460043] = {
    id = 200460043,
    name = "「我可不是看在钱的份上，只想看看到底是谁想出这种任务！」"
  },
  [200460044] = {
    id = 200460044,
    name = "眼见你答应，少女破涕为笑，立即与你商量了起来。"
  },
  [200460045] = {
    id = 200460045,
    name = "少女告诉你，她想外出游历，但母亲要她先证明自己，用契约炼制一个傀儡。"
  },
  [200460046] = {
    id = 200460046,
    name = "「契约炼制傀儡？」"
  },
  [200460047] = {
    id = 200460047,
    name = "「嗯！阿秋就是阿妈炼制的傀儡，别看它笨笨的，但普通魔物根本不是它的对手。」"
  },
  [200460048] = {
    id = 200460048,
    name = "你对她的母亲不禁感到好奇。"
  },
  [200460049] = {
    id = 200460049,
    name = "你跟随少女回到了她家。"
  },
  [200460050] = {
    id = 200460050,
    name = "你还在思索如何扮演傀儡，找机会从她母亲口中套话，没想到她母亲却一眼将你识破。"
  },
  [200460051] = {
    id = 200460051,
    name = "你只得将事情挑明，质问她为何要对探险者下手。"
  },
  [200460052] = {
    id = 200460052,
    name = "听完你的话，少女母亲愣住了，随即将少女叫到了跟前教训起来。"
  },
  [200460053] = {
    id = 200460053,
    name = "随后她才向你解释存在的误会。"
  },
  [200460054] = {
    id = 200460054,
    name = "原来那个魔法阵根本不是什么契约用的，只是一个限制魔物的阵法，她们的魔法对人类也没效果。"
  },
  [200460055] = {
    id = 200460055,
    name = "而且母亲根本没有让女儿对探险者动手，只是告诉女儿，想出去游历，先证明自己的能力再说。"
  },
  [200460056] = {
    id = 200460056,
    name = "随后又感慨到要是她外出游历了，这个家就只剩自己一个人，空荡荡的，想说话都找不到人。"
  },
  [200460057] = {
    id = 200460057,
    name = "结果少女完全误解了母亲的意思，不知道怎么想的，竟然理解为要去外面抓个探险者证明自己。"
  },
  [200460058] = {
    id = 200460058,
    name = "还想炼制一个探险者傀儡留在家中陪伴母亲......"
  },
  [200460059] = {
    id = 200460059,
    name = "知晓事情的真相后，你长舒了一口气。"
  },
  [200460060] = {
    id = 200460060,
    name = "在这位母亲不停的道歉、以及少女的哭声中，你离开了。"
  },
  [200460061] = {
    id = 200460061,
    name = "「我没有帮你的义务，而且我也不认识你，撒娇也没用。」"
  },
  [200460062] = {
    id = 200460062,
    name = "你甩开了少女向远处走去，身后再次传来少女的哭声和祈求。"
  },
  [200460063] = {
    id = 200460063,
    name = "你叹了口气，加快脚步离开了。"
  },
  [200460064] = {
    id = 200460064,
    name = "被迷烟偷袭让你觉得很不快，加之少女甚至对你使用奇奇怪怪的魔法......"
  },
  [200460065] = {
    id = 200460065,
    name = "你本想给她个教训，但你仔细观察后发现那个魔法阵虽然玄妙，但和契约什么的毫无关联。"
  },
  [200460066] = {
    id = 200460066,
    name = "加之少女本人看上去并无恶意，你决定不予理会。"
  },
  [200460067] = {
    id = 200460067,
    name = "对这种不知道哪来的任性自大的小姑娘，你不想和她牵扯到一起。"
  },
  [200460068] = {
    id = 200460068,
    name = "「喂！你不准走！」"
  },
  [200460069] = {
    id = 200460069,
    name = "你头也不回地走远了。"
  },
  [200460070] = {
    id = 200460070,
    name = "「我说了不许走啊！」"
  },
  [200460071] = {
    id = 200460071,
    name = "「呜呜呜，阿秋，我又失败了......」"
  },
  [200460072] = {
    id = 200460072,
    name = "少女抱着魔物傀儡伤心地哭了起来。"
  },
  [200460073] = {
    id = 200460073,
    name = "判断少女的魔法对你并无危害后，你继续假装昏迷，想看看她究竟想做什么。"
  },
  [200460074] = {
    id = 200460074,
    name = "少女陷入了长时间的沉默，似乎在思索什么难题。"
  },
  [200460075] = {
    id = 200460075,
    name = "你沉浸在魔力流转的舒适感中，不知不觉忘记了维系清醒，昏睡了过去。"
  },
  [200460076] = {
    id = 200460076,
    name = "等你苏醒时，发现自己已经躺在一张床上，房间内还有一对母女。"
  },
  [200460077] = {
    id = 200460077,
    name = "见你醒来，母亲急忙带着女儿上前道歉。"
  },
  [200460078] = {
    id = 200460078,
    name = "「非常抱歉，我女儿任性的行为给你添麻烦了。」"
  },
  [200460079] = {
    id = 200460079,
    name = "「对、对不起，我知道错了，请原谅我！」"
  },
  [200460080] = {
    id = 200460080,
    name = "在二人后续说明中，你知道了整个事情的经过。"
  },
  [200460081] = {
    id = 200460081,
    name = "原来女儿误解了母亲的话，才自作主张将你绑了回来。"
  },
  [200460082] = {
    id = 200460082,
    name = "你无奈地摇了摇头，在二人不停的道歉中离开了。"
  },
  [200460083] = {
    id = 200460083,
    name = "你暗掐掌心，辅以魔力刺激，通过痛觉维持着清醒。"
  },
  [200460084] = {
    id = 200460084,
    name = "两个身影逐渐靠近，你这才看清来者是一名少女和一头——魔物？！"
  },
  [200460085] = {
    id = 200460085,
    name = "但你仔细观察后发现，那魔物行动僵硬，毫无生机，看来是以魔物为素材制作的傀儡。"
  },
  [200460086] = {
    id = 200460086,
    name = "「咦？你为什么还醒着啊？！中了我的迷烟，应该会昏迷才对啊。」"
  },
  [200460087] = {
    id = 200460087,
    name = "「你是故意对我下手的？」"
  },
  [200460088] = {
    id = 200460088,
    name = "「当然啦！不过竟然失败了，难道是剂量太低了吗......」"
  },
  [200460089] = {
    id = 200460089,
    name = "少女看上去充满了朝气，一双眼睛尤为灵动，但她的回答却令你非常意外。"
  },
  [200460090] = {
    id = 200460090,
    name = "「你故意袭击我，究竟想做什么？」"
  },
  [200460091] = {
    id = 200460091,
    name = "「哎呀，你问题真多！当然是把你做成和阿秋一样的傀儡啊。」"
  },
  [200460092] = {
    id = 200460092,
    name = "少女拍了拍魔物傀儡的脑袋，傀儡立即乖乖地坐在了她脚下。"
  },
  [200460093] = {
    id = 200460093,
    name = "「你能把人做成傀儡？」"
