    id = 200460098,
    name = "「你、你竟敢小看我！」"
  },
  [200460099] = {
    id = 200460099,
    name = "「毕竟你看起来就是不会嘛。要不你说说具体的操作？」"
  },
  [200460100] = {
    id = 200460100,
    name = "「我......我才不告诉你！」"
  },
  [200460101] = {
    id = 200460101,
    name = "「哼，虽然这次失败了，但我是不会放弃的！」"
  },
  [200460102] = {
    id = 200460102,
    name = "少女带着魔物跑远了。"
  },
  [200460103] = {
    id = 200460103,
    name = "你虽然还有许多疑问，但此时身体也尚未恢复，只得之后再调查了。"
  },
