  [130060012] = {
    id = 130060012,
    name = "以辉夜的名望，想必能策应组织中的大部分成员吧？"
  },
  [130060013] = {
    id = 130060013,
    name = "没错，到时候辉夜大头目就将「深渊航线」的财政交给我来负责吧！"
  },
  [130060014] = {
    id = 130060014,
    name = "那么采访一下未来的财政干部星云小姐，您接手后准备怎样来改善成员们的待遇呢？"
  },
  [130060015] = {
    id = 130060015,
    name = "咳咳咳，最优先的嘛，当然是让所有人的酬金翻倍！"
  },
  [130060016] = {
    id = 130060016,
    name = "那对于我这样最先响应的成员，会有特别优待吗？"
  },
  [130060017] = {
    id = 130060017,
    name = "当然！咱们毕竟是共患难的同伴，美丽的月蚀小姐不管承接什么委托，酬金都加十倍！"
  },
  [130060018] = {
    id = 130060018,
    name = "那我就先感谢星云小姐和辉夜大头目了。"
  },
  [130060019] = {
    id = 130060019,
    name = "你们俩......"
  },
  [130060020] = {
    id = 130060020,
    name = "虽然现在是休息时间，但也别太放松了，越是临近最后关头越不能大意。"
  },
  [130060021] = {
    id = 130060021,
    name = "遵命，辉夜大头目。"
  },
  [130060022] = {
    id = 130060022,
    name = "噗，呵呵呵。"
  },
  [130070001] = {
    id = 130070001,
    name = "这趟委托已经完成，我们稍微休整一下便回去复命。"
  },
  [130070002] = {
    id = 130070002,
    name = "完成了吗，总感觉忽略了些什么......"
  },
  [130070003] = {
    id = 130070003,
    name = "我们确实有将物品亲手交到目标手中，所以这趟委托完成了。"
  },
  [130070004] = {
    id = 130070004,
    name = "这一路经历了不少事，回去后一起庆祝一下？"
  },
  [130070005] = {
    id = 130070005,
    name = "好，那就这么决定了！"
  },
  [130070006] = {
    id = 130070006,
    name = "......别擅自就决定了啊。"
  },
  [130070007] = {
    id = 130070007,
    name = "难道你有什么急事？"
  },
  [130070008] = {
    id = 130070008,
    name = "虽然确实有些想处理的事，但庆祝的时间还是有的。"
  },
  [130070009] = {
    id = 130070009,
    name = "这不就没问题了~大家一起行动这么久，我们已经是同生共死的同伴了，就别见外啦。"
  },
  [130070010] = {
    id = 130070010,
    name = "欸，我突然有个想法，既然我们已经如此默契，要不再一起承接些更有挑战的委托？"
  },
  [130070011] = {
    id = 130070011,
    name = "我记得组织中有几个高额悬赏的委托至今无人认领。"
  },
  [130070012] = {
    id = 130070012,
    name = "我可不想去挑战那些恐怖的存在。"
  },
  [130070013] = {
    id = 130070013,
    name = "我也并无兴趣。"
  },
  [130070014] = {
    id = 130070014,
    name = "你们也拒绝得太果断了......"
  },
  [130070015] = {
    id = 130070015,
    name = "我们不都是想要帮助更多人，践行自己的正义吗？如果有钱的话，实现起来会轻松很多吧？"
  },
  [130070016] = {
    id = 130070016,
    name = "财物只是生活的必要，并非我为之行动的理由。"
  },
  [130070017] = {
    id = 130070017,
    name = "如果想依靠钱财实现自己的正义，或许我回家继承父母的事业会更有效率。"
  },
  [130070018] = {
    id = 130070018,
    name = "而且他们也很希望我能作出那样的选择吧。"
  },
  [130070019] = {
    id = 130070019,
    name = "说起来你父母前段时间出版了新作品，里面有称赞你哦。"
  },
  [130070020] = {id = 130070020, name = "是吗？"},
  [130070021] = {
    id = 130070021,
    name = "嗯，下次有机会介绍我们认识一下吧，说不定我和他们会有很多共同话题，辉夜也一起！"
  },
  [130070022] = {
    id = 130070022,
    name = "我也要去吗？"
  },
  [130070023] = {
    id = 130070023,
    name = "当然，我们是同伴嘛。"
  },
  [130070024] = {
    id = 130070024,
    name = "又擅自决定了......"
  },
  [130080001] = {
    id = 130080001,
    name = "我虽然曾设想过无数种可能，但目标人物死去这种情况，完全在预料之外。"
  },
  [130080002] = {
    id = 130080002,
    name = "唉，我也从没想过会有这样的事。辉夜处理过的委托最多，有遇到过类似的吗？"
  },
  [130080003] = {
    id = 130080003,
    name = "我也几乎没遇到过，不过在这深渊中，出现任何情况都不奇怪。"
  },
  [130080004] = {
    id = 130080004,
    name = "话虽如此，但这种结果比起因魔物导致委托失败，或者运送物品丢失之类的情况更让人难受。"
  },
  [130080005] = {
    id = 130080005,
    name = "感觉心里空落落的，好像失去了执行委托的意义。"
  },
  [130080006] = {
    id = 130080006,
    name = "这可不像你，不过我也有类似的感觉。"
  },
  [130080007] = {
    id = 130080007,
    name = "现在不是消沉的时候，无论结果如何，我们都需要回去复命。"
  },
  [130080008] = {
    id = 130080008,
    name = "而且执行的委托多了，就会发现比这种结果更复杂、更令人沮丧的委托还有很多。"
  },
  [130080009] = {
    id = 130080009,
    name = "我们能做的，只有变得更强，让自己能更有效率地执行委托，赶在结果出现变化前解决一切。"
  },
  [130080010] = {
    id = 130080010,
    name = "这算是“前辈”的忠告吗？"
  },
  [130080011] = {
    id = 130080011,
    name = "不，我并没有说教的意思——"
  },
  [130080012] = {
    id = 130080012,
    name = "好啦，辉夜“前辈”，我们明白你的意思，不用解释哦。"
  },
  [130080013] = {
    id = 130080013,
    name = "确实如辉夜“前辈”所说，我们现在能做的，是努力将下一个委托做得更好！"
  },
  [130080014] = {id = 130080014, name = "......"},
  [130080015] = {
    id = 130080015,
    name = "好了星云，别逗辉夜了，她的脸都已经红了。"
  },
  [130080016] = {
    id = 130080016,
    name = "欸，明明是你先说的，真是狡猾啊，月蚀“前辈”。"
  },
  [130080017] = {id = 130080017, name = "......"},
  [130080018] = {
    id = 130080018,
    name = "我记得某人好奇这次护送的物品来着，如果组织不小心得知某人又出现“窥探客人隐私”的意图，会不会责罚呢？"
  },
  [130080019] = {
    id = 130080019,
    name = "月蚀，你你你！你也没少说组织吝啬！"
  },
  [130080020] = {
    id = 130080020,
    name = "可是「深渊航线」的条例中，并不存在“不能抱怨组织”之类的规定。"
  },
  [130080021] = {
    id = 130080021,
    name = "辉夜，你得帮我作证啊，买了那么多伴手礼，我真没钱交罚金了......"
  },
  [130080022] = {
    id = 130080022,
    name = "噗——咳咳，休整结束，该回去了。"
  },
  [130090001] = {
    id = 130090001,
    name = "这一片都找遍了，完全没有任何发现。"
  },
  [130090002] = {
    id = 130090002,
    name = "我这边也是，没发现目标人物踪迹。"
  },
  [130090003] = {
    id = 130090003,
    name = "那么基于现状，此次委托暂时判定为失败，我们回去复命吧。"
  },
  [130090004] = {
    id = 130090004,
    name = "失败......"
  },
  [130090005] = {
    id = 130090005,
    name = "辉夜，我们再找找吧，或许、或许是我们哪里疏漏了，不，肯定是我们哪里疏漏了。"
  },
  [130090006] = {
    id = 130090006,
    name = "嗯，我们再更加仔细地搜索一下，哪怕能找到一些线索都行。"
  },
  [130090007] = {
    id = 130090007,
    name = "已经搜索了好几次，无论再怎么寻找，也不会有结果。"
  },
  [130090008] = {
    id = 130090008,
    name = "可是都已经来到了目的地，就这样放弃，根本无法说服自己。"
  },
  [130090009] = {
    id = 130090009,
    name = "月蚀，你也曾处理过类似的委托吧？"
  },
  [130090010] = {
    id = 130090010,
    name = "对，所以我一想到复命后那些委托人失望的表情，就无法允许自己放弃。"
  },
  [130090011] = {
    id = 130090011,
    name = "我也一样，每次我都无法直视委托人失望的双眼，甚至有时候会觉得自己什么也守护不了。"
  },
  [130090012] = {
    id = 130090012,
    name = "但这并非我们滞留与此的理由，否则就真的什么做不到了。"
  },
  [130090013] = {id = 130090013, name = "......"},
  [130090014] = {
    id = 130090014,
    name = "好不甘心。"
  },
  [130090015] = {
    id = 130090015,
    name = "不过目标人物失踪，也并非代表他遇到了危险，说不定是遇上好事了呢，所以已不需要我们护送的物品。"
  },
  [130090016] = {
    id = 130090016,
    name = "毕竟这里是深渊，一切皆有可能。"
  },
  [130090017] = {
    id = 130090017,
    name = "你就是这样安慰自己的吗？"
  },
  [130090018] = {
    id = 130090018,
    name = "这也并非单纯的安慰，我只是阐述了一种可能。"
  },
  [130090019] = {
    id = 130090019,
    name = "如果按你所说，那我们护送的物品是可以解决危险的？"
  },
  [130090020] = {
    id = 130090020,
    name = "辉夜，你是不是也涉嫌“窥探客户隐私”了？"
  },
  [130090021] = {
    id = 130090021,
    name = "我、我只是说可能。"
  },
  [130090022] = {
    id = 130090022,
    name = "放心，我不会揭发你的，今后组织要再罚我钱，你记得帮我说好话呀。"
  },
  [130090023] = {id = 130090023, name = "......"},
  [130090024] = {
    id = 130090024,
    name = "我们放弃吧星云，就像辉夜所说，或许委托人已经去享受新的生活了。"
  },
  [130090025] = {
    id = 130090025,
    name = "嗯，一起回去吧。"
  },
  [130100001] = {
    id = 130100001,
    name = "没想到这次委托背后，竟然还存在着不为人知的故事。"
  },
  [130100002] = {
    id = 130100002,
    name = "是啊，又完成一个有意义的委托。你在写什么呢月蚀，难道想将这次经历写成故事发表？"
  },
  [130100003] = {
    id = 130100003,
    name = "只是日记而已，每次完成有特殊意义的委托，我都会记录下来。"
  },
  [130100004] = {
    id = 130100004,
    name = "没想到你还有这种习惯，不过这次委托背后的故事着实令人感慨，写成故事的话一定会很受欢迎。"
  },
  [130100005] = {
    id = 130100005,
    name = "要不你试试？我和辉夜实名加入，增加故事可信度，到时候故事热销，分我们一些出场费就行。"
  },
  [130100006] = {
    id = 130100006,
    name = "只要是事实，我并不介意自己的名字出现在任何地方。"
  },
  [130100007] = {
    id = 130100007,
    name = "辉夜，你这就太外行了，你知道对现在的探险者来说最重要的是什么吗？"
  },
  [130100008] = {
    id = 130100008,
    name = "战斗的理由，守护安宁的意志，追寻未知的决心。"
  },
  [130100009] = {
    id = 130100009,
    name = "你......说的也没错。"
  },
  [130100010] = {
    id = 130100010,
    name = "不过对有名气的探险者来说，姓名权、肖像权、名誉权都是重要的资产，绝不能随便处置。"
  },
  [130100011] = {
    id = 130100011,
    name = "我并不觉得自己能与那些传奇探险者相提并论。"
  },
  [130100012] = {
    id = 130100012,
    name = "而且比起你说的那些，能顺利完成委托，帮助到困扰的人，我会觉得更加满足。"
  },
  [130100013] = {
    id = 130100013,
    name = "不错，每一个不起眼的委托背后或许都有着曲折的故事，我们的举手之劳，也可能成为他人的救赎。"
  },
  [130100014] = {
    id = 130100014,
    name = "这并不冲突吧？"
  },
  [130100015] = {
    id = 130100015,
    name = "你要是真感兴趣就自己去写吧，至于出场费，就当我赞助你了。"
  },
  [130100016] = {
    id = 130100016,
    name = "你们俩还真是慷慨，就不怕我胡乱捏造吗？"
  },
  [130100017] = {
    id = 130100017,
    name = "比如什么辉夜推荐的药物，购买商品抽取月蚀的见面会门票之类的，一定能吸引不少人！"
  },
  [130100018] = {
    id = 130100018,
    name = "如果你那样做我会很困扰。"
  },
  [130100019] = {
    id = 130100019,
    name = "你如果想和我的剑亲近一番，就尽管去做。"
  },
  [130100020] = {
    id = 130100020,
    name = "你、你那是什么眼神，开玩笑嘛！"
  },
  [130100021] = {
    id = 130100021,
    name = "我只是觉得这次经历着实令人印象深刻，如果能分享给更多人就好了。"
  },
  [130100022] = {
    id = 130100022,
    name = "决定了，今后我也要多接一些委托，将类似的经历全部记录下来，让更多人了解深渊，了解这个世界中存在的美好和希望。"
  },
  [130110001] = {
    id = 130110001,
    name = "大丰收！这下所有人的伴手礼都足够了！"
  },
  [130110002] = {
    id = 130110002,
    name = "等等，伴手礼？这些东西你不是买回去自己收藏吗？"
  },
  [130110003] = {
    id = 130110003,
    name = "有少数几件我确实会用来收藏啦，但这么多全用来收藏——我看起来像是会做那种事的人吗？"
  },
  [130110004] = {id = 130110004, name = "像。"},
  [130110005] = {
    id = 130110005,
    name = "你刚刚购物时好几次问我们有没有喜欢的，难道是想准备我们的礼物？"
  },
  [130110006] = {
    id = 130110006,
    name = "对啊，正好你们俩也在，能自己挑选的话我就可以不用考虑该给你们准备什么了。"
  },
  [130110007] = {
    id = 130110007,
    name = "我还以为你只是单纯地想让我们帮你参考一下。"
  },
  [130110008] = {
    id = 130110008,
    name = "话说你买这么多礼物花费了不少吧？万一再被组织处罚，还有钱交罚金吗？"
  },
  [130110009] = {
    id = 130110009,
    name = "哼哼，这你就不懂了吧，我刚刚在这可赚了不少，除去购买礼物的钱，甚至还有盈余。"
  },
  [130110010] = {
    id = 130110010,
    name = "什么？！难道你去变卖了什么贵重物品？"
  },
  [130110011] = {
    id = 130110011,
    name = "怎么可能！我是去卖消息啦。"
  },
  [130110012] = {id = 130110012, name = "消息？"},
  [130110013] = {
    id = 130110013,
    name = "对！想要赚钱不一定需要货物，只靠头脑也能做到。"
  },
  [130110014] = {
    id = 130110014,
    name = "比如我们此前经过的据点因为魔物活动，普通物资和素材价格都比这边贵上不少。"
  },
  [130110015] = {
    id = 130110015,
    name = "而我们来的途中解决了不少魔物，道路已经通畅，所以我就将这个消息卖给这边的商人。"
  },
  [130110016] = {
    id = 130110016,
    name = "虽然再过不久他们也能发现，但如果能抢占先机，这些商人很乐意付出一点酬劳的。"
  },
  [130110017] = {
    id = 130110017,
    name = "原来如此......既然星云如此慷慨，要不我们再回去看看？"
  },
  [130110018] = {
    id = 130110018,
    name = "月蚀，我们本就是与星云一起出来的，还从她这里收取伴手礼，有失礼仪了。"
  },
  [130110019] = {
    id = 130110019,
    name = "星云都特意为大家准备了，我们如果不收才有愧她的好意，而且你刚才不是很中意一把匕首吗，不是正好——"
  },
  [130110020] = {
    id = 130110020,
    name = "算不上中意，只是稍微觉得有点特别而已。"
  },
  [130110021] = {
    id = 130110021,
    name = "特别也行，等星云送给你后慢慢把玩，我们就再去看看吧。"
  },
  [130110022] = {
    id = 130110022,
    name = "已经晚了。"
  },
  [130110023] = {
    id = 130110023,
    name = "嗯，什么意思？"
  },
  [130110024] = {
    id = 130110024,
    name = "刚才你们都不说话，我已经准备好了。"
  },
  [130110025] = {
    id = 130110025,
    name = "至于是什么，回去后再揭晓答案！"
  },
  [130120001] = {
    id = 130120001,
    name = "哇哦，是浮光的信！哎呀呀，她还是一如既往地认真呢。"
  },
  [130120002] = {
    id = 130120002,
    name = "她的任务已经完成了，真羡慕啊......哎，同样作为穷人家的孩子，一起努力吧！"
  },
  [130120003] = {id = 130120003, name = "......"},
  [130120004] = {
    id = 130120004,
    name = "话说回来，她在信中提到的情报很让人在意。"
  },
  [130120005] = {
    id = 130120005,
    name = "啊，你说火山地动的事，那个会有什么影响？"
  },
  [130120006] = {
    id = 130120006,
    name = "如果爆发过的火山发生地动，很可能会引起火山的再次爆发，熔岩会将周围的森林、草原，一切生物都化为灰烬。"
  },
  [130120007] = {
    id = 130120007,
    name = "好可怕，月蚀见过吗？"
  },
  [130120008] = {
    id = 130120008,
    name = "没有，我只在书里看到过。据说很多年前诺维奇帝国的北部火山苏醒吞噬了森林和房舍，至今那里仍然是一片无人区，成了罪大恶极的犯人才会被流放的死亡之地。"
  },
  [130120009] = {
    id = 130120009,
    name = "月蚀真是见多识广。那照这么说，咱们这一趟任务岂不是很危险？"
  },
  [130120010] = {
    id = 130120010,
    name = "嗯，只能更加小心行事了。"
  },
  [130120011] = {
    id = 130120011,
    name = "辉夜回答得很坚决啊，一点也没有放弃的样子。"
  },
  [130120012] = {
    id = 130120012,
    name = "难道你想放弃？"
  },
  [130120013] = {
    id = 130120013,
    name = "当然不会，我是那么容易放弃的人吗？这次任务委托人给得可是很多的。"
  },
  [130130001] = {
    id = 130130001,
    name = "哈哈哈哈哈！"
  },
  [130130002] = {
    id = 130130002,
    name = "在笑什么那么开心？咦，这是什么？长眼睛的萝卜？"
  },
  [130130003] = {
    id = 130130003,
    name = "哈哈哈，是浮光的画像，哦不对，浮光画的人像。"
  },
  [130130004] = {
    id = 130130004,
    name = "噗......呵呵。"
  },
  [130130005] = {
    id = 130130005,
    name = "哇哦，好难得看到月蚀笑了。"
  },
  [130130006] = {
    id = 130130006,
    name = "......浮光的画......很生动。"
  },
  [130130007] = {
    id = 130130007,
    name = "真有长得像萝卜的人吗？"
  },
  [130130008] = {
    id = 130130008,
    name = "也许吧。"
  },
  [130130009] = {
    id = 130130009,
    name = "唔......如果是长成这样，在哪里都很引人注目吧。"
  },
  [130130010] = {
    id = 130130010,
    name = "没错，可是我们没有在附近见过这人。"
  },
  [130130011] = {
    id = 130130011,
    name = "也没有听过有人谈论这个人。"
  },
  [130130012] = {
    id = 130130012,
    name = "或许他不是往这边逃走呢，因为火山的传言，已经很少人过来了。"
  },
  [130130013] = {
    id = 130130013,
    name = "是哦，而且他还带着一堆东西，是没必要逃到这边的，除非他想销赃。"
  },
  [130130014] = {
    id = 130130014,
    name = "销赃。你说得没错，星云。"
  },
  [130130015] = {id = 130130015, name = "啊？"},
  [130130016] = {
    id = 130130016,
    name = "他不能把偷来的东西在附近卖掉，只能去很远的地方，也许我们真的能碰上这个人。"
  },
  [130130017] = {
    id = 130130017,
    name = "好啊，那咱们把眼睛放亮点，决不能让这种小贼逃走了！"
  },
  [130140001] = {
    id = 130140001,
    name = "星云，浮光的信到了。"
  },
  [130140002] = {
    id = 130140002,
    name = "真的？！她说了什么？"
  },
  [130140003] = {
    id = 130140003,
    name = "她提到了我们两个。"
  },
  [130140004] = {
    id = 130140004,
    name = "啊......我猜不会说什么称赞的话啦，让我看看......果然！"
  },
  [130140005] = {
    id = 130140005,
    name = "她说得不错。"
  },
  [130140006] = {
    id = 130140006,
    name = "星云贪功冒进，辉夜循规蹈矩，浮光说得真是毫不留情。"
  },
  [130140007] = {
    id = 130140007,
    name = "月蚀，为什么你没有缺点啊。"
  },
  [130140008] = {
    id = 130140008,
    name = "呵呵，我的平庸就是最大的缺点了。"
  },
  [130140009] = {
    id = 130140009,
    name = "不要妄自菲薄啦，你做的每一件事都很完美啦。"
  },
  [130140010] = {
    id = 130140010,
    name = "......也许太过于追求完美就是我的缺点吧。"
  },
  [130140011] = {
    id = 130140011,
    name = "不管怎样，月蚀，在我固执的地方，你一定要提醒我。"
  },
  [130140012] = {
    id = 130140012,
    name = "还有我，要是有什么打乱计划的想法，也一定要阻止我啊。"
  },
  [130140013] = {
    id = 130140013,
    name = "嗯，我也有做得不好的地方，我们一起互相监督吧。"
  },
  [130140014] = {id = 130140014, name = "嗯。"},
  [130140015] = {id = 130140015, name = "好！"},
  [130150001] = {
    id = 130150001,
    name = "没想到在这里也能见到促销单，看起来的确很吸引人。"
  },
  [130150002] = {
    id = 130150002,
    name = "哼，竟然用这种卑鄙的促销方式与我家竞争！"
  },
  [130150003] = {
    id = 130150003,
    name = "差点忘了，星云家里的商会和行商们是竞争对手。"
  },
  [130150004] = {
    id = 130150004,
    name = "就是这群人扰乱市场，这下我家该怎么办啊，又要变得更穷了怎么办......"
  },
  [130150005] = {
    id = 130150005,
    name = "......不会的。"
  },
  [130150006] = {
    id = 130150006,
    name = "每次出现这种事，家里长辈们都忙得焦头烂额，虽然最后大家都没有饿死，但生活真的变得更艰难了啊。"
  },
  [130150007] = {
    id = 130150007,
    name = "......星云对自家的产业似乎还是不太了解啊。"
  },
  [130150008] = {id = 130150008, name = "嗯。"},
  [130150009] = {
    id = 130150009,
    name = "你们在嘀嘀咕咕说什么？"
  },
  [130150010] = {
    id = 130150010,
    name = "在说这份促销单，看起来很诱人，但没有看到实物之前，很难对品质下定义。"
  },
  [130150011] = {
    id = 130150011,
    name = "对哦，我父亲说做生意切记不可以以次充好，我家卖的东西虽然普普通通，但大家用了都觉得不错的。"
  },
  [130150012] = {
    id = 130150012,
    name = "......普......通吗？"
  },
  [130150013] = {
    id = 130150013,
    name = "咦，这背面真画了地图。让我看看，我们已经走到这个位置了吧。"
  },
  [130150014] = {
    id = 130150014,
    name = "离据点还有些距离，今晚得加紧赶路了。"
  },
  [130150015] = {id = 130150015, name = "走吧。"},
  [130150016] = {
    id = 130150016,
    name = "嗯，让我好好看看，那个卡卡玛的新店到底如何。"
  },
  [130160001] = {
    id = 130160001,
    name = "这封信是留给另一支商队的，看来他们已经进入沼泽地了。"
  },
  [130160002] = {
    id = 130160002,
    name = "果然是富贵险中求，这么危险的地方，他们还打算继续往前，难道有什么我们不知道情报？"
  },
  [130160003] = {
    id = 130160003,
    name = "比如说......宝物或者......圣物？"
  },
  [130160004] = {
    id = 130160004,
    name = "他们能成功到达此处，实力已经很强了，这是什么商会？"
  },
  [130160005] = {
    id = 130160005,
    name = "唔，你听过黑市吗？"
  },
  [130160006] = {
    id = 130160006,
    name = "黑市......"
  },
  [130160007] = {
    id = 130160007,
    name = "就是那些见不得人的交易，在黑市里，你可以找到魔法物品，禁忌的卷轴，失窃的宝物......以及公会禁止公开售卖的东西。"
  },
  [130160008] = {
    id = 130160008,
    name = "听起来不是什么好地方。"
  },
  [130160009] = {
    id = 130160009,
    name = "咳咳，确实是这样的，那个地方不受任何约束，但很少有人在黑市捣乱，有传言说是高塔在背后帮他们撑腰。"
  },
  [130160010] = {
    id = 130160010,
    name = "星云，你很熟悉黑市啊。"
  },
  [130160011] = {
    id = 130160011,
    name = "呵呵，偶然一次跟家里人去过，那一次父亲发了很大的火，后来我才知道那地方是黑市。"
  },
  [130160012] = {
    id = 130160012,
    name = "那里的商人都是亡命之徒，他们为了宝物可以做出任何事，也不会顾及自己的安危，这封信的疯劲，让我突然想起了这群人，尤其那个记号，我也只在黑市见到过。"
  },
  [130160013] = {
    id = 130160013,
    name = "......原来如此。"
  },
  [130160014] = {
    id = 130160014,
    name = "辉夜原本是打算提醒他们撤离吗？"
  },
  [130160015] = {
    id = 130160015,
    name = "嗯，毕竟这个地方已经不适合人类长留了。"
  },
  [130160016] = {
    id = 130160016,
    name = "生死有命，他们既然选择了这条路，就随他们去吧，我们还有更重要的任务。"
  },
  [130160017] = {
    id = 130160017,
    name = "嗯，走吧。"
  },
  [130170001] = {
    id = 130170001,
    name = "黑市入口？"
  },
  [130170002] = {
    id = 130170002,
    name = "在哪里？我看看......唔，果然很机灵啊，这种地方绝对不会在信上说明白的。"
  },
  [130170003] = {
    id = 130170003,
    name = "黎明要塞也有这样的黑市？"
  },
  [130170004] = {
    id = 130170004,
    name = "嗯，为了躲避公会的调查，黑市往往是流动的，需要有熟人带进去，毕竟交易的都是见不得人的东西嘛。"
  },
  [130170005] = {
    id = 130170005,
    name = "你上次说黑市的人都是亡命之徒，那他们会交易什么见不得人的东西？"
  },
  [130170006] = {
    id = 130170006,
    name = "啊哈哈，就是我们平常看不见的......我们不说这个了吧。"
  },
  [130170007] = {
    id = 130170007,
    name = "是有什么难言之隐吗？"
  },
  [130170008] = {
    id = 130170008,
    name = "或者是我们难以想象的宝物？道具？药品？"
  },
  [130170009] = {
    id = 130170009,
    name = "唔......我见过......人。"
  },
  [130170010] = {id = 130170010, name = "？？？"},
  [130170011] = {
    id = 130170011,
    name = "就是卖小孩子......把小孩子做成药人，那些血有治愈的效果，传言还能长生......"
  },
  [130170012] = {
    id = 130170012,
    name = "太过分了！"
  },
  [130170013] = {
    id = 130170013,
    name = "别生气，别生气，这件事被公会调查之后，就消停了很长的时间，现在大多是卖没有在商会记录的东西了。"
  },
  [130170014] = {
    id = 130170014,
    name = "这也不好，深渊里的很多东西，没有净化浊炁，很容易被侵蚀，太危险了。"
  },
  [130170015] = {
    id = 130170015,
    name = "这样的黑市就不应该存在。"
  },
  [130170016] = {
    id = 130170016,
    name = "唉，世间的恶事每一刻都在发生，我们也没有能力事事去阻止。"
  },
  [130170017] = {
    id = 130170017,
    name = "我知道，但我没办法习惯不管。"
  },
  [130170018] = {
    id = 130170018,
    name = "等这之后，我们找个时间去调查一下吧。"
  },
  [130170019] = {
    id = 130170019,
    name = "好，打听消息的事交给我。"
  },
  [130180001] = {
    id = 130180001,
    name = "是账本。"
  },
  [130180002] = {
    id = 130180002,
    name = "喔，这个我熟！这谁记的，比咱们的又清楚又详细啊！"
  },
  [130180003] = {
    id = 130180003,
    name = "我们大多自给自足，很少用到组织的东西。"
  },
  [130180004] = {
    id = 130180004,
    name = "嗯，但这个一目了然，完全可以作为探险手册来记啊。"
  },
  [130180005] = {id = 130180005, name = "手册？"},
  [130180006] = {
    id = 130180006,
    name = "比如说，我们今天走了哪些地方，杀了哪些魔物，吃了什么用了什么，都记下来，等到回去之后整理成册，不就是很好的一本探险日记嘛！"
  },
  [130180007] = {
    id = 130180007,
    name = "不错，只靠记忆，的确会忘记许多事，但记下之后又有什么用？"
  },
  [130180008] = {
    id = 130180008,
    name = "这可是深渊航线的宝贵经验啊，如果将这本日记拿出去卖，那咱们组织的经费就再也不用发愁了！"
  },
  [130180009] = {id = 130180009, name = "不行。"},
  [130180010] = {id = 130180010, name = "啊？"},
  [130180011] = {
    id = 130180011,
    name = "我们的路线与经历，都与委托人的任务息息相关，如果将这些告诉别人，和出卖委托人没有区别。"
  },
  [130180012] = {
    id = 130180012,
    name = "噢，知道了。"
  },
  [130180013] = {
    id = 130180013,
    name = "星云，别灰心，其实你如果不想忘记那些回忆，可以自己记下来。"
  },
  [130180014] = {
    id = 130180014,
    name = "我的父母都有写手记的习惯，他们会记录一切值得记下的事，他们也想我这样做，可惜我对这件事不感兴趣。"
  },
  [130180015] = {
    id = 130180015,
    name = "呵呵，不用啦，我也只是说说而已，每天写多累啊......有时候杀完魔物只想躺下，连眼睛都不想睁开。"
  },
  [130190001] = {
    id = 130190001,
    name = "飘友？很新的词汇。"
  },
  [130190002] = {
    id = 130190002,
    name = "这个写信的好有意思，可惜信的残余的魔法力不足，看完就自己销毁了。"
  },
  [130190003] = {
    id = 130190003,
    name = "怎么，你还想给他回一封信？"
  },
  [130190004] = {
    id = 130190004,
    name = "不愧是月蚀，真的很了解我啊，这种人走过很多地方，一定知道哪里有宝藏。"
  },
  [130190005] = {
    id = 130190005,
    name = "恐怕这个人并不想收到你的回信。"
  },
  [130190006] = {
    id = 130190006,
    name = "有来有往才可以叫朋友，这样单方面联系什么也做不到啊。"
  },
  [130190007] = {
    id = 130190007,
    name = "也许对他而言就足够了。"
  },
  [130190008] = {
    id = 130190008,
    name = "害怕陌生人，又想把自己的想法告诉别人，用这样的办法，也算对内心的一种宣泄。如果你回了信，他反而应该更害怕吧。"
  },
  [130190009] = {id = 130190009, name = "......"},
  [130190010] = {
    id = 130190010,
    name = "怎么一脸惋惜的样子？"
  },
  [130190011] = {
    id = 130190011,
    name = "这个人还是挺厉害的，如果我能知道这个魔法，是不是就可以很轻松给其他人写信了？"
  },
  [130190012] = {
    id = 130190012,
    name = "没那么简单。这样的信没有目的地，随风飞扬，不能送到指定的人手里。"
  },
  [130190013] = {
    id = 130190013,
    name = "你想写信吗？如果有紧急的事，可以让信使送去近区的据点。"
  },
  [130190014] = {
    id = 130190014,
    name = "只是说说而已啦，真有急事的话，家里人给了我东西，应该可以及时传达消息的吧。"
  },
  [130190015] = {
    id = 130190015,
    name = "......差点忘了，你身上的东西比这只千纸鹤更不简单。"
  },
  [130200001] = {
    id = 130200001,
    name = "湖泊......这哪儿有湖泊？"
  },
  [130200002] = {
    id = 130200002,
    name = "这些血迹，也许有人遭遇了危机。"
  },
  [130200003] = {
    id = 130200003,
    name = "可能是送消息的人，也可能是......赶来支援的人。"
  },
  [130200004] = {
    id = 130200004,
    name = "等等，看这地图......好像的确指的是这里，这是怎么回事？"
  },
  [130200005] = {
    id = 130200005,
    name = "地图画错了？"
  },
  [130200006] = {
    id = 130200006,
    name = "......应该不会错，可这里，并没有信上所说的禁锢之湖。"
  },
  [130200007] = {
    id = 130200007,
    name = "那就是人错了？"
  },
  [130200008] = {
    id = 130200008,
    name = "人错了？是什么意思？"
  },
  [130200009] = {
    id = 130200009,
    name = "辉夜的意思，是这个叫苏克的人，传了假消息。"
  },
  [130200010] = {
    id = 130200010,
    name = "可为什么要传假消息呢？还让他的队长白跑一趟。"
  },
  [130200011] = {
    id = 130200011,
    name = "如果你是队长，你收到信会这么做？"
  },
  [130200012] = {
    id = 130200012,
    name = "那还用想，肯定带上人去支援啊！"
  },
  [130200013] = {
    id = 130200013,
    name = "辉夜呢？"
  },
  [130200014] = {
    id = 130200014,
    name = "......我，应该会一个人去。"
  },
  [130200015] = {
    id = 130200015,
    name = "嗯。这里没有厮杀的痕迹，只有地图上沾了血，所以我猜想......"
  },
  [130200016] = {
    id = 130200016,
    name = "队长在这里遇到了魔物？他们一起把魔物杀掉，然后这个苏克就带队长去了那个湖泊。嗯，应该是这样吧......"
  },
  [130200017] = {
    id = 130200017,
    name = "这附近没有湖泊，再远的地方，也没有。"
  },
  [130200018] = {
    id = 130200018,
    name = "那就只有一种可能了。"
  },
  [130200019] = {id = 130200019, name = "......"},
  [130200020] = {
    id = 130200020,
    name = "咦？怎么不说话都走了。算了，我也不想了，你们等等我呀！"
  },
  [130210001] = {
    id = 130210001,
    name = "这就是最后一页......前面的，都撕掉了。"
  },
  [130210002] = {
    id = 130210002,
    name = "只为了一个宝物，连多年的情义都可以弃而不顾，也太不是人了吧！"
  },
  [130210003] = {
    id = 130210003,
    name = "不，不会只因为这个，既然是一起战斗，共同患难的伙伴，又怎么会轻易地把刀剑刺向对方呢？假如有一天我们之间有了争执，你会这么做吗？"
  },
  [130210004] = {
    id = 130210004,
    name = "当然不会！我怎么会是这种人！"
  },
  [130210005] = {
    id = 130210005,
    name = "也许一开始，他们也不是这种人。"
  },
  [130210006] = {
    id = 130210006,
    name = "......你是说，在这之前，他们的队伍里就已经出现了矛盾？"
  },
  [130210007] = {
    id = 130210007,
    name = "也许这个宝物，是压垮那份信任最后的稻草。"
  },
  [130210008] = {
    id = 130210008,
    name = "那也用不着动手啊......有什么坐下来慢慢谈不好吗？"
  },
  [130210009] = {
    id = 130210009,
    name = "你们猜，他在那个时候，有没有动手？"
  },
  [130210010] = {
    id = 130210010,
    name = "他应该是躲起来了吧，或者跑掉了？不然怎么会后悔......"
  },
  [130210011] = {
    id = 130210011,
    name = "是啊，他为什么会后悔，那件宝物的下落会在哪里？"
  },
  [130210012] = {
    id = 130210012,
    name = "难道他......"
  },
  [130210013] = {
    id = 130210013,
    name = "不知道，都只是我的猜测。"
  },
  [130210014] = {
    id = 130210014,
    name = "伙伴，是最亲密的战友，也是最容易刺伤对方的一把利刃。因为互相信任了解，一旦有了猜忌，只会万劫不复。"
  },
  [130210015] = {
    id = 130210015,
    name = "伙伴就是伙伴，不应该成为敌人，我永远也不会像他那样，等失去了一切才去后悔。"
  },
  [130210016] = {
    id = 130210016,
    name = "......希望他能在记忆的灰烬中，能找回他想要的光明吧，哪怕只有一瞬。"
  },
  [130220001] = {id = 130220001, name = "噗嗤。"},
  [130220002] = {
    id = 130220002,
    name = "笑什么？"
  },
  [130220003] = {
    id = 130220003,
    name = "原来吟游诗人也要吃饭睡觉打魔物的呀。"
  },
  [130220004] = {id = 130220004, name = "......"},
  [130220005] = {
    id = 130220005,
    name = "我还以为他们只会到处走走，抱着琴唱唱歌，然后找人索要金币。"
  },
  [130220006] = {
    id = 130220006,
    name = "你被索要过金币？"
  },
  [130220007] = {
    id = 130220007,
    name = "对啊，说金币是所谓的......聆听神的指引，传达神谕所需的媒介。"
  },
  [130220008] = {
    id = 130220008,
    name = "你给了吗？"
  },
  [130220009] = {
    id = 130220009,
    name = "给了。他在我家门口坐了一整天，一口饭也没吃，我看他太可怜了，就给了他金币。"
  },
  [130220010] = {
    id = 130220010,
    name = "后来他拿到钱也没着急走，那时我还以为他们这群神秘的人真的有什么力量能让自己不吃饭呢。"
  },
  [130220011] = {id = 130220011, name = "......"},
  [130220012] = {
    id = 130220012,
    name = "不过这家伙也挺有勇气的，要不是他们亲自走到这些地方来，我们在大陆也听不到深渊的许多传说吧。"
  },
  [130220013] = {
    id = 130220013,
    name = "「踏遍深渊之每一个角落，唱遍世间所有已知与未知之事」这就是他的决心。"
  },
  [130220014] = {
    id = 130220014,
    name = "靠旅行写作唱歌来赚钱，真是不容易啊，下一次我再见到诗人的话，就再多给一块金币吧。"
  },
  [130220015] = {
    id = 130220015,
    name = "为什么要多给一块？"
  },
  [130220016] = {
    id = 130220016,
    name = "这样他们就更有动力唱给更多的人，大家不用下深渊也能听到深渊的传奇和见闻啦。"
  },
  [130230001] = {
    id = 130230001,
    name = "啊，看起来真让人心动。"
  },
  [130230002] = {
    id = 130230002,
    name = "是的，如果财迷心窍的人，或许就真的进山洞找人了。"
  },
  [130230003] = {
    id = 130230003,
    name = "......呵呵，你不是在说我吧？"
  },
  [130230004] = {
    id = 130230004,
    name = "不是，你还是有分寸的。"
  },
  [130230005] = {
    id = 130230005,
    name = "当然，我时刻记得，这次有重要任务在身的。"
  },
  [130230006] = {
    id = 130230006,
    name = "嗯，你没心动就好。"
  },
  [130230007] = {
    id = 130230007,
    name = "......嘿嘿，话说回来，这洞穴和宝物的事，等咱们回来路上可以探一探啊。"
  },
  [130230008] = {
    id = 130230008,
    name = "探一探也不是不可以，前提是这封信的情报的准确的。"
  },
  [130230009] = {
    id = 130230009,
    name = "你怀疑事有玄机？"
  },
  [130230010] = {
    id = 130230010,
    name = "说不准，深渊里的情况复杂多变，一个探险者怎么可能单凭一己之力就找到宝物呢？"
  },
  [130230011] = {
    id = 130230011,
    name = "要是他只是想利用我们对抗守护魔物，然后独吞宝藏。那咱们就亏死了！"
  },
  [130230012] = {
    id = 130230012,
    name = "呵呵，不管真假，等我们任务结束之后，都可以来探一探。"
  },
  [130230013] = {
    id = 130230013,
    name = "没错，咱们几个在一起，再强的魔物也不是对手！"
  },
  [130230014] = {
    id = 130230014,
    name = "这样一来，既能完成任务，又能得到宝......历练，岂不是一举两得？"
  },
  [130230015] = {
    id = 130230015,
    name = "嗯，如果是额外挑战的话，我同意。"
  },
  [130230016] = {
    id = 130230016,
    name = "我也同意。"
  },
  [130230017] = {
    id = 130230017,
    name = "好耶，全票赞成，就这么说定了。"
  },
  [130240001] = {
    id = 130240001,
    name = "藏宝图，真的是藏宝图！"
  },
  [130240002] = {
    id = 130240002,
    name = "嗯，意外收获呢。"
  },
  [130240003] = {
    id = 130240003,
    name = "让我看看，宝藏在哪儿......啊，这个地方，好像在火山深处的位置。"
  },
  [130240004] = {
    id = 130240004,
    name = "那里......似乎与我们的目的地背道而驰吧？"
  },
  [130240005] = {
    id = 130240005,
    name = "是吗，如果从这边绕过去的话——"
  },
  [130240006] = {
    id = 130240006,
    name = "不行，太远了。"
  },
  [130240007] = {
    id = 130240007,
    name = "唉，我就是推测一下路线，果然还是不行啊。"
  },
  [130240008] = {
    id = 130240008,
    name = "这张藏宝图，他竟然就这样轻而易举地放弃了。"
  },
  [130240009] = {
    id = 130240009,
    name = "对啊，就算自己拿不到宝藏，卖给其他人也有赚啊。"
  },
  [130240010] = {
    id = 130240010,
    name = "也许他有其他的目的。"
  },
  [130240011] = {
    id = 130240011,
    name = "卖给其他人，就不会知道宝藏的样子和去处了。"
  },
  [130240012] = {
    id = 130240012,
    name = "没错，他还没找到宝藏，就想将这个机遇和这份希望寄予他人，由他人来达成他的心愿。"
  },
  [130240013] = {
    id = 130240013,
    name = "嗯，如果是我，我也会不甘心的，但伙伴的生命更重要。这样将藏宝图留下，也是求一个心理安慰吧。也许真的会遇到我这样的好人，找到宝藏会和他分享喜悦呢。"
  },
  [130240014] = {
    id = 130240014,
    name = "我们去不了。"
  },
  [130240015] = {
    id = 130240015,
    name = "我只是说说而已。"
  },
  [130250001] = {
    id = 130250001,
    name = "这这这，有宝藏欸！！"
  },
  [130250002] = {
    id = 130250002,
    name = "伽丝茉......是公会情报部部长吧？这封信竟然是公会的秘密，让咱们知道了，会不会不太好啊？"
  },
  [130250003] = {
    id = 130250003,
    name = "这公会的秘密......怎么跑进魔物肚子里去了？难道送信的人被吃了？"
  },
  [130250004] = {
    id = 130250004,
    name = "不一定，公会有许多传递情报的方式。比如利用飞鸟、魔偶、魔法阵、信号标记，总之不一定是人。"
  },
  [130250005] = {
    id = 130250005,
    name = "不用人传递情报，那中途被别人......或者这家伙截获了，那该怎么办？"
  },
  [130250006] = {
    id = 130250006,
    name = "也许他们有他们的规则吧，这个就不是我们考虑的事了。说起来，我们要把这个消息传回给公会吗？"
  },
  [130250007] = {
    id = 130250007,
    name = "我觉得可以先给公会传一封消息，告诉他们咱们拿到了一封情报。"
  },
  [130250008] = {
    id = 130250008,
    name = "然后呢？"
  },
  [130250009] = {
    id = 130250009,
    name = "然后就等他们开价啊，情报可是有市无价，这么好的赚钱机会，怎么能放过。"
  },
  [130250010] = {
    id = 130250010,
    name = "可这本就是公会的东西......你想待价而沽？"
  },
  [130250011] = {
    id = 130250011,
    name = "如果不是公会的情报，我还想送到组织里，给组织填补点家用呢。要不是咱们，他们是永远没机会知道这个消息了。月蚀，你说呢？"
  },
  [130250012] = {
    id = 130250012,
    name = "我快被你说服了，你让我想想，让我想想......"
  },
  [130250013] = {
    id = 130250013,
    name = "不用想了，看看这上面写的，刻不容缓！这魔物应该刚吃了它没多久就被我们干掉了，现在送信，一来一回，可要耽搁不少时间呢。"
  },
  [130250014] = {
    id = 130250014,
    name = "就不能直接送到公会手里吗？"
  },
  [130250015] = {
    id = 130250015,
    name = "我不是为了稳妥一点嘛，万一收到的......不是公会的人呢？当面交易更妥当。"
  },
  [130250016] = {id = 130250016, name = "交易？"},
  [130250017] = {
    id = 130250017,
    name = "啊哈哈，交不交易到时候再说吧，总之先把人叫来。"
  },
  [130250018] = {
    id = 130250018,
    name = "嗯，这个我同意。"
  },
  [130250019] = {
    id = 130250019,
    name = "好吧，我还是认为既然是别人的，就应该还给别人，而不是作为筹码。"
  },
  [130250020] = {
    id = 130250020,
    name = "知道啦。"
  },
  [130260001] = {
    id = 130260001,
    name = "是传讯用的魔法石，很少见......竟然会在这里。"
  },
  [130260002] = {
    id = 130260002,
    name = "好东西啊，虽然刻的文字是固定的，但输入一点魔法就能看到，可以反复观看，价格很贵的！"
  },
  [130260003] = {
    id = 130260003,
    name = "不过，我之前见过这类型的魔法石，通常里面刻的都是魔法书籍，或者魔法阵一类的，没想到还有人拿它来写信，真奢侈啊。"
  },
  [130260004] = {
    id = 130260004,
    name = "星云，辉夜，你们听过这个祭坛吗？"
  },
  [130260005] = {
    id = 130260005,
    name = "被星星照亮的祭坛......没听过。"
  },
  [130260006] = {
    id = 130260006,
    name = "我也没听过。"
  },
  [130260007] = {
    id = 130260007,
    name = "你看，他找这个克莱尔跟他一起去祭坛，说明这个克莱尔真是他最好的朋友，连这么隐秘的事都能告诉他，还愿意分享那份力量。"
  },
  [130260008] = {
    id = 130260008,
    name = "......是吗？他为什么请求克莱尔帮他拿到所谓的遗产？他说的挑战，为什么要让别人和他一起面对？"
  },
  [130260009] = {
    id = 130260009,
    name = "祭坛有危险，他一个人应付不来。"
  },
  [130260010] = {
    id = 130260010,
    name = "原......原来是这样的吗？！"
  },
  [130260011] = {
    id = 130260011,
    name = "他没有说明白那个挑战是什么，甚至......连落名都没有。"
  },
  [130260012] = {
    id = 130260012,
    name = "等待克莱尔的也许是挑战，也许是陷阱。"
  },
  [130260013] = {
    id = 130260013,
    name = "那那那......这个白骨会是谁？"
  },
  [130260014] = {
    id = 130260014,
    name = "我也不知道，被骗来的克莱尔，亦或是计划失败的写信人，最好谁也不是。"
  },
  [130260015] = {
    id = 130260015,
    name = "可是他们，明明应该是最好的朋友吧。"
  },
  [130260016] = {
    id = 130260016,
    name = "嗯，我也希望谁都不是。"
  },
  [130270001] = {
    id = 130270001,
    name = "信在这里就中断了。"
  },
  [130270002] = {
    id = 130270002,
    name = "他说的那个山洞......是不是我们刚才路过的那个？"
  },
  [130270003] = {id = 130270003, name = "嗯。"},
  [130270004] = {
    id = 130270004,
    name = "队员们在那里发现残缺的衣物，这个探险者应该已经......"
  },
  [130270005] = {id = 130270005, name = "......"},
  [130270006] = {
    id = 130270006,
    name = "虽然已经经历过很多次，但每当看到有人死去的时候，心里仍然觉得不安。"
  },
  [130270007] = {
    id = 130270007,
    name = "当他踏上寻宝之路时，就应该知道会有与魔物恶战的一天。"
  },
  [130270008] = {
    id = 130270008,
    name = "可惜很多人只看到眼前的利益，并没有相应的胆识和能力。"
  },
  [130270009] = {
    id = 130270009,
    name = "可他们只是想寻宝，并没有错。"
  },
  [130270010] = {
    id = 130270010,
    name = "嗯，我们只能用自己的力量，减少这样的悲剧发生。"
  },
  [130270011] = {
    id = 130270011,
    name = "对，你说得没错。"
  },
  [130270012] = {
    id = 130270012,
    name = "只是我们更幸运吧。"
  },
  [130280001] = {
    id = 130280001,
    name = "很美的画......"
  },
  [130280002] = {
    id = 130280002,
    name = "都被血弄脏了啊。唉，这血迹斑斑的，感觉不太吉利，可能卖不出什么好价钱。"
  },
  [130280003] = {
    id = 130280003,
    name = "不，它比我所见过的画都要美，它的价值不该用金钱来衡量。这是我从没见过的色彩......"
  },
  [130280004] = {
    id = 130280004,
    name = "有火山泥的味道。这画中的某些颜料，应该来自火山附近的泥土。"
  },
  [130280005] = {
    id = 130280005,
    name = "是吗？我闻闻？唔，确实有股怪味，但还挺有意思的。"
  },
  [130280006] = {
    id = 130280006,
    name = "可惜根据这幅画的磨损程度来看，已经过去至少一个月了。"
  },
  [130280007] = {
    id = 130280007,
    name = "那这个画家岂不是凶多吉少。"
  },
  [130280008] = {
    id = 130280008,
    name = "嗯。他在写下遗言的时候，应该已经遭遇不测。但他没有放弃这幅画，而是坚持完成了它。"
  },
  [130280009] = {
    id = 130280009,
    name = "啊？你怎么看出来的？"
  },
  [130280010] = {
    id = 130280010,
    name = "除了这些血迹，画上没有添补的痕迹，说明他是在短时间内完成了画作，而没有停下来疗伤。"
  },
  [130280011] = {
    id = 130280011,
    name = "我还是不能理解，一幅画而已，以后还可以再画，命没了就没了啊。"
  },
  [130280012] = {
    id = 130280012,
    name = "也许在他心里，这不仅是一幅画。"
  },
  [130280013] = {
    id = 130280013,
    name = "是意志，或者信仰，就像正义对于深渊航线一样。"
  },
  [130280014] = {
    id = 130280014,
    name = "我有点明白了，这么说，这幅画又变得很值钱了啊。"
  },
  [130280015] = {
    id = 130280015,
    name = "......星云，这不是我们的画。"
  },
  [130280016] = {
    id = 130280016,
    name = "咳咳，你说得对，这种东西不能据为己有，先收起来吧，也许能找到他的家人也说不定呢。"
  },
  [130280017] = {
    id = 130280017,
    name = "嗯，走吧。"
  },
  [130290001] = {
    id = 130290001,
    name = "火山花......"
  },
  [130290002] = {
    id = 130290002,
    name = "他被骗了啊......我们看到的火山花根本没有那种作用吧。"
  },
  [130290003] = {
    id = 130290003,
    name = "世界上真的有能治愈一切疾病的药吗？"
  },
  [130290004] = {
    id = 130290004,
    name = "也许有吧，比如还没出世的圣物？"
  },
  [130290005] = {
    id = 130290005,
    name = "真这种东西，又怎么会落在普通人手里呢，告诉他的那个人，肯定也知道没有这种东西，只是想骗他的钱吧。"
  },
  [130290006] = {
    id = 130290006,
    name = "为什么？"
  },
  [130290007] = {
    id = 130290007,
    name = "小辉夜呀，你想想，你会劝一个从未下过深渊的人，来火山脚下找什么火山花吗？"
  },
  [130290008] = {id = 130290008, name = "不会。"},
  [130290009] = {
    id = 130290009,
    name = "这就对咯，那个人骗光了他的钱，然后再让他进深渊，这样他死了，也没有人知道那个人是元凶，元凶又可以继续去骗别人......"
  },
  [130290010] = {
    id = 130290010,
    name = "不可原谅！"
  },
  [130290011] = {
    id = 130290011,
    name = "竟然利用弱者求药心切的心情来谋财害命，这种心肠歹毒的恶人，应该绳之以法，在广场审判。"
  },
