  [111550447] = {id = 111550447, name = "嗯？"},
  [111550448] = {
    id = 111550448,
    name = "鬼魅回头看你，你笑着将几块萤石扔进了灯罩里，大小不一的“星星”汇聚在一起，星辰仿佛活了起来。"
  },
  [111550449] = {
    id = 111550449,
    name = "因为星星不再孤单了。送给你。"
  },
  [111550450] = {id = 111550450, name = "！！！"},
  [111550451] = {
    id = 111550451,
    name = "这些萤石......"
  },
  [111550452] = {
    id = 111550452,
    name = "当时在湖泊里捞的，我留了几块。"
  },
  [111550453] = {
    id = 111550453,
    name = "呵呵呵......"
  },
  [111550454] = {
    id = 111550454,
    name = "鬼魅捧着灯笑了。你难得再次看到了这样的笑，没有伪装与假面，双眸投射出细碎而明亮的光点，在破晓的天色里随着笑意溢满了星河。"
  },
  [111550455] = {
    id = 111550455,
    name = "谢谢你，你是第一个送奴家礼物的人。这个礼物，奴家很喜欢。"
  },
  [111550456] = {
    id = 111550456,
    name = "你也是第一个，没有认定奴家是魔女恶鬼的人......尽管多一个人布置剧场是有些不习惯，但你真的令奴家很意外。"
  },
  [111550457] = {
    id = 111550457,
    name = "唔，能与鬼魅小姐共同演绎剧目，是我的荣幸。"
  },
  [111550458] = {
    id = 111550458,
    name = "鬼魅突然倾身靠了过来，一只手轻轻拂动了你耳边的发。"
  },
  [111550459] = {
    id = 111550459,
    name = "呵呵，那就期待我们的下次合作了。"
  },
  [111550460] = {
    id = 111550460,
    name = "声音轻柔而婉转，如一阵风，淡薄而不真实。"
  },
  [111550461] = {id = 111550461, name = "......"},
  [111550462] = {
    id = 111550462,
    name = "待你正想回应时，鬼魅已经消失了，空中旋转飘落了一张扑克牌，你接着翻看正面，神秘典雅的桃心纹饰上，描绘着漆黑的图案“Q”。"
  },
  [111800001] = {
    id = 111800001,
    name = "就是这里！队长，你看，前面的魔物，脑袋上发光的那个！"
  },
  [111800002] = {
    id = 111800002,
    name = "岩甲石灵？"
  },
  [111800003] = {
    id = 111800003,
    name = "这不就是普通的岩甲石灵？它们身上总有会发光的地方。"
  },
  [111800004] = {
    id = 111800004,
    name = "一点也不普通！不说个头大了一圈，队长，你仔细看它的脑袋，那里有一面镜子！"
  },
  [111800005] = {
    id = 111800005,
    name = "你放眼看去，岩石与水晶堆砌的甲壳上，有一处光芒与其他水晶不同，像镜面反射般，亮得刺眼。"
  },
  [111800006] = {
    id = 111800006,
    name = "看到了。不管怎样，先打下来再说。"
  },
  [111800007] = {
    id = 111800007,
    name = "欸嘿！交给小莎！"
  },
  [111800008] = {
    id = 111800008,
    name = "......没过多久，你们就顺利除掉了岩甲石灵，摘取了它头上的“镜子”。"
  },
  [111800009] = {
    id = 111800009,
    name = "哈哈，真的是一面镜子呢，安妮斯快来看！"
  },
  [111800010] = {
    id = 111800010,
    name = "尼莉莎把镜子往前一伸，里面映照出了安妮斯微笑的模样。"
  },
  [111800011] = {
    id = 111800011,
    name = "咦？明明有魔法的波动，这镜子怎么没反应？"
  },
  [111800012] = {
    id = 111800012,
    name = "尼莉莎揽镜自照，挨个走到你们面前，照到你的时候，你在镜子里看到一个黑衣白发的女人，正悄无声息地站在你身后。"
  },
  [111800013] = {
    id = 111800013,
    name = "呜哇！！！"
  },
  [111800014] = {
    id = 111800014,
    name = "尼莉莎当场吓得跳起来，镜子脱手被你稳稳接住，你回头看向那个女人，她什么时候出现的，你们之中竟然没有一个人发现。"
  },
  [111800015] = {
    id = 111800015,
    name = "这位是......"
  },
  [111800016] = {
    id = 111800016,
    name = "鬼牌，鬼侍。"
  },
  [111800017] = {
    id = 111800017,
    name = "（那个神秘组织......）"
  },
  [111800018] = {
    id = 111800018,
    name = "我想买你的镜子。"
  },
  [111800019] = {
    id = 111800019,
    name = "啊？这么快就有买家了？"
  },
  [111800020] = {
    id = 111800020,
    name = "鬼侍小姐，你为什么要买它？"
  },
  [111800021] = {
    id = 111800021,
    name = "只是想买而已。你卖吗？"
  },
  [111800022] = {
    id = 111800022,
    name = "这是我和同伴们一起获得的，得看他们的意见。"
  },
  [111800023] = {
    id = 111800023,
    name = "我没关系。"
  },
  [111800024] = {
    id = 111800024,
    name = "如果这位小姐需要的话......"
  },
  [111800025] = {
    id = 111800025,
    name = "价格可不便宜，这可是从......好吧，虽然不是什么稀奇的魔物，但也是难得的好东西。"
  },
  [111800026] = {
    id = 111800026,
    name = "正所谓宝镜配美人，这是队长和鬼侍小姐的缘分啊。"
  },
  [111800027] = {
    id = 111800027,
    name = "菲尼斯你闭嘴。"
  },
  [111800028] = {
    id = 111800028,
    name = "多少钱？"
  },
  [111800029] = {
    id = 111800029,
    name = "你和鬼侍说了一个合理的价格，她干脆地同意了，正当你将镜子递给鬼侍时，旁边的草丛猛然跳出了四五个黑衣人。"
  },
  [111800030] = {
    id = 111800030,
    name = "什么人？！"
  },
  [111800031] = {
    id = 111800031,
    name = "竟然慢了一步，那就一起杀了吧！"
  },
  [111800032] = {
    id = 111800032,
    name = "几名黑衣人悍然出手，还没等你说出口，鬼侍已经一手探出，横剑一挡，只听“砰”的一声，冲上前的一个黑衣人已经被大剑砸得远远的，翻了好几个跟头。"
  },
  [111800033] = {
    id = 111800033,
    name = "你找死！"
  },
  [111800034] = {
    id = 111800034,
    name = "面对凛凛刀光，鬼侍一言不发，抡起大剑挡了过去，黑衣人被撞得头晕眼花，鼻血直流。尼莉莎眨了眨眼，暗自竖起一根大拇指。"
  },
  [111800035] = {
    id = 111800035,
    name = "厉害啊，人狠话不多！"
  },
  [111800036] = {
    id = 111800036,
    name = "这群人来路不明，一来就干杀人夺宝的勾当，你也觉得没有交流的必要。"
  },
  [111800037] = {
    id = 111800037,
    name = "大家小心，专心应敌！"
  },
  [111800038] = {
    id = 111800038,
    name = "几只阴沟里的老鼠，也不知道在嚣张什么。"
  },
  [111800039] = {
    id = 111800039,
    name = "蒂娜也加入了战局，长剑呼啸，阻挡了偷袭鬼侍的一击，同时反手一剑，直奔黑衣人的面门，金铁交鸣声毫无节奏地响起。"
  },
  [111800040] = {id = 111800040, name = "多谢。"},
  [111800041] = {
    id = 111800041,
    name = "客气了。"
  },
  [111800042] = {
    id = 111800042,
    name = "面对黑衣人的围攻，菲尼斯也毫不犹豫地出手，你让安妮斯站在身边，尼莉莎早已翻上了树，瞄准了正在后方施展魔法的黑衣人。"
  },
  [111800043] = {
    id = 111800043,
    name = "来咯，看小莎的天外流星！"
  },
  [111800044] = {
    id = 111800044,
    name = "“嗖——”箭矢划破空气的声音尖锐而刺耳，黑衣人只觉眼前一花，还未等反应过来，箭矢已穿透了他的护臂，深深钉入了他的肩膀。"
  },
  [111800045] = {
    id = 111800045,
    name = "啊啊啊！"
  },
  [111800046] = {
    id = 111800046,
    name = "该死的家伙！先把东西抢过来！"
  },
  [111800047] = {
    id = 111800047,
    name = "一群人的目标从先杀人变成了先夺宝，几名黑衣人打算绕过阻碍，夺走你手里的那面镜子。你注意到其中一名黑衣人悄悄地退了一步，而此时另一人从背后袭来。"
  },
  [111800048] = {
    id = 111800048,
    name = "老师，这边！"
  },
  [111800049] = {
    id = 111800049,
    name = "交给我就好。"
  },
  [111800050] = {
    id = 111800050,
    name = "你无暇他顾，和菲尼斯共同御敌，几名黑衣人被打得踉跄后退。突然，一道光束从暗处倏然而至！"
  },
  [111800051] = {
    id = 111800051,
    name = "你还没来得及抵挡，鬼侍的剑已经挡住了光，恍若一瞬间将光芒吸入了大剑之中。"
  },
  [111800052] = {
    id = 111800052,
    name = "藏头露尾的东西。"
  },
  [111800053] = {
    id = 111800053,
    name = "她反手一挥，刚才凝聚在剑里的魔法力如魔法弹一般爆破开来，炸得黑衣人四处逃窜。"
  },
  [111800054] = {
    id = 111800054,
    name = "可恶！撤退！"
  },
  [111800055] = {
    id = 111800055,
    name = "不能放走他们！"
  },
  [111800056] = {
    id = 111800056,
    name = "这群人目的明确，下手熟练，明显有组织和预谋，且坏事做尽，不能再让他们去祸害无辜。"
  },
  [111800057] = {
    id = 111800057,
    name = "你们反守为攻，很快就制服了那群黑衣人。"
  },
  [111800058] = {
    id = 111800058,
    name = "你们到底是什么人？"
  },
  [111800059] = {
    id = 111800059,
    name = "领头的黑衣人并不回答，只是比了个手势，其余的黑衣人竟然齐齐举剑自尽。"
  },
  [111800060] = {id = 111800060, name = "！！！"},
  [111800061] = {
    id = 111800061,
    name = "嘿嘿嘿，想知道？就一起去地狱吧！"
  },
  [111800062] = {
    id = 111800062,
    name = "趁众人震惊之际，黑衣人抛出了一枚原形的魔法珠，还没靠近你已感受到其中蕴含的恐怖魔法力，当机立断将手中的镜子扔了出去抵挡！"
  },
  [111800063] = {id = 111800063, name = "散开！"},
  [111800064] = {
    id = 111800064,
    name = "所有人都快速躲闪，唯有鬼侍妄图将魔法珠打开，你一见拉住鬼侍，转身趴在地下，千钧一发之际，你听到魔法珠击中镜面的声音。"
  },
  [111800065] = {
    id = 111800065,
    name = "轰——你的脑中一片嗡鸣，低头看被你扑倒的鬼侍，她的眼中灼灼闪耀，那是镜子破碎后反射的光芒。"
  },
  [111800066] = {id = 111800066, name = "......"},
  [111800067] = {
    id = 111800067,
    name = "你想礼节性地问她有没有事，也想赶快起身，可你什么也做不了，下一秒你头晕目眩，瞬间失去了意识。"
  },
  [111800068] = {id = 111800068, name = "老师！"},
  [111800069] = {
    id = 111800069,
    name = "队长！怎么回事？"
  },
  [111800070] = {
    id = 111800070,
    name = "队长晕过去了！"
  },
  [111800071] = {
    id = 111800071,
    name = "我......我治疗了，不行。"
  },
  [111800072] = {
    id = 111800072,
    name = "不应该的。"
  },
  [111800073] = {
    id = 111800073,
    name = "镜子碎了，会不会和这个有关？"
  },
  [111800074] = {
    id = 111800074,
    name = "不管怎样，这里不能久留，先离开这里，再找治好队长的办法。"
  },
  [111800075] = {
    id = 111800075,
    name = "蒂娜安抚地拍了拍安妮斯，尼莉莎也罕见地没有和她拌嘴，慎重地带上了你和鬼侍。"
  },
  [111800076] = {
    id = 111800076,
    name = "嗯？这个是鬼侍小姐的东西。"
  },
  [111800077] = {
    id = 111800077,
    name = "安妮斯看着落在鬼侍身边光芒暗淡的一对吊坠，小心地将它们收了起来。"
  },
  [111800078] = {
    id = 111800078,
    name = "走吧，我知道有个人，也许有办法。"
  },
  [111800079] = {id = 111800079, name = "好。"},
  [111800080] = {
    id = 111800080,
    name = "意识回笼，你听见了鸟鸣声，似乎还感觉到阳光照在身上的温度。"
  },
  [111800081] = {
    id = 111800081,
    name = "（阳光？深渊里怎么会有阳光？）"
  },
  [111800082] = {
    id = 111800082,
    name = "你猛地睁眼，头顶树叶斑斓，午后的光星星点点地洒进阴翳，你眨了眨眼睛，抬手遮住了许久不见的日光。"
  },
  [111800083] = {
    id = 111800083,
    name = "（这里是？）"
  },
  [111800084] = {
    id = 111800084,
    name = "你的手指白皙修长，根骨分明，一看就是养尊处优的贵公子才能有的一双手。"
  },
  [111800085] = {
    id = 111800085,
    name = "不对，这根本不是你的手。"
  },
  [111800086] = {
    id = 111800086,
    name = "你豁然起身，脑中一阵胀痛，忽然涌入了一段不属于你的记忆，以新奇又离谱的方式，如走马灯一般闪烁着，强行灌入闹钟，直到疼痛减缓，你才缓缓回过神。"
  },
  [111800087] = {
    id = 111800087,
    name = "（幻境？）"
  },
  [111800088] = {
    id = 111800088,
    name = "（不，不像是幻境。）"
  },
  [111800089] = {
    id = 111800089,
    name = "幻境没有这样完全的真实，也不会将你变成另外一个人。"
  },
  [111800090] = {
    id = 111800090,
    name = "你身上穿着宝石蓝的锦缎外套，身旁挂着天鹅绒的披风，边缘的织金熠熠闪烁，披风上绣着似剑非剑的纹路，你觉得有一丝眼熟。"
  },
  [111800091] = {
    id = 111800091,
    name = "（当然眼熟了，这是普尔兰伯爵家族的家徽。）"
  },
  [111800092] = {
    id = 111800092,
    name = "你反应过来，自嘲了一声。"
  },
  [111800093] = {
    id = 111800093,
    name = "是的，你现在是普尔兰伯爵的继承人，16岁。你有忙碌的伯爵父亲，慈爱的母亲，还有一个可爱的妹妹。"
  },
  [111800094] = {
    id = 111800094,
    name = "你们普尔兰一族拥有属于自己的领地，有古老的城堡和庄园。"
  },
  [111800095] = {
    id = 111800095,
    name = "你从小养尊处优，所有的人都称你为少爷。家庭教师特意教导你骑术与剑术，德高望重的老者为你授课历史与哲学，他们都希望将你培养成为合格的下一任领主。"
  },
  [111800096] = {
    id = 111800096,
    name = "但你少年心性，散漫贪玩，趁着家里忙成一团的时候，见缝插针地躲到了这里睡觉。"
  },
  [111800097] = {
    id = 111800097,
    name = "（好真实的梦，呵呵，暂时当作是梦吧。）"
  },
  [111800098] = {
    id = 111800098,
    name = "上一刻你和同伴们还在为宝物争抢不休，这会儿竟然闲逸地躺在了树上睡大觉，你抿了抿嘴，难得地感受到了一瞬间不可思议的安宁。"
  },
  [111800099] = {
    id = 111800099,
    name = "哥哥，我就知道，你又来这里偷懒了！"
  },
  [111800100] = {
    id = 111800100,
    name = "你低头看下去，粉雕玉琢的小女孩抬头叉腰，嘟着嘴，脸颊鼓鼓囊囊地看着你，但你知道她并不是真的生气。"
  },
  [111800101] = {id = 111800101, name = "妹妹？"},
  [111800102] = {
    id = 111800102,
    name = "你看到她的第一眼，脑中就想起关于她的记忆，这是“你”的亲妹妹，柯莱特尔。"
  },
  [111800103] = {
    id = 111800103,
    name = "她骄横顽皮，也纯真善良，带着源自贵族的自信与傲娇。这时，她的眼中闪过狡黠的光。"
  },
  [111800104] = {
    id = 111800104,
    name = "哥哥，刚刚父亲大人好像在找你呢，你猜我怎么跟他说的？"
  },
  [111800105] = {
    id = 111800105,
    name = "你告诉他了？"
  },
  [111800106] = {
    id = 111800106,
    name = "哼哼，你猜呀？"
  },
  [111800107] = {
    id = 111800107,
    name = "你翻身跳下了树，朝柯莱特尔走了过去，一步一行中，感受到了这具少年身体的蓬勃朝气。"
  },
  [111800108] = {
    id = 111800108,
    name = "让哥哥偷懒不去，被我抓到了吧！"
  },
  [111800109] = {
    id = 111800109,
    name = "我的好妹妹，我现在就回去，你可别跟父亲大人告状。"
  },
  [111800110] = {
    id = 111800110,
    name = "你回想之前和柯莱特尔的相处方式，模仿着继承人的口吻应付着。"
  },
  [111800111] = {
    id = 111800111,
    name = "（在不知道为什么会出现这样的情况之前，还是不要改变现状吧。）"
  },
  [111800112] = {
    id = 111800112,
    name = "只有不动声色地获取周围的情报，也许才能找到办法离开这个“幻境”。果然，柯莱特尔对你的“求饶”习以为常，摆出了一副“你看着办”的表情。"
  },
  [111800113] = {
    id = 111800113,
    name = "你说不告诉我就不告诉吗？"
  },
  [111800114] = {
    id = 111800114,
    name = "你思考了一瞬，想起这个哥哥给妹妹准备了一个礼物，正打算找机会送出去。"
  },
  [111800115] = {
    id = 111800115,
    name = "呵呵，那哥哥送你一个东西，你帮我跟父亲大人瞒着。"
  },
  [111800116] = {
    id = 111800116,
    name = "什么东西？"
  },
  [111800117] = {
    id = 111800117,
    name = "小女孩的脸说变就变，柯莱特尔一点看不出刚才在生气，期待地睁着大眼睛望着你。"
  },
  [111800118] = {
    id = 111800118,
    name = "你拿出了一块方盒，蹲下身打开了它，果然听到了女孩子的惊叹声。"
  },
  [111800119] = {
    id = 111800119,
    name = "哇！好漂亮的吊坠！"
  },
  [111800120] = {
    id = 111800120,
    name = "方盒中静静安放着两块吊坠，一个白金镶嵌着月光石，如月华般皎洁，一个银边缠绕着紫水晶，似夜幕下的星辰。"
  },
  [111800121] = {
    id = 111800121,
    name = "咦，是两个吊坠？都是我的吗？"
  },
  [111800122] = {
    id = 111800122,
    name = "你挑一个，剩下那个留给哥哥。"
  },
  [111800123] = {
    id = 111800123,
    name = "哼，哪有送礼物送一半的。哥哥，你不会是舍不得吧？"
  },
  [111800124] = {
    id = 111800124,
    name = "当然不是。这是一对魔法吊坠，据说戴在两个人身上，不管两人相隔多远，也能听到对方的呼唤。"
  },
  [111800125] = {
    id = 111800125,
    name = "真的吗？！"
  },
  [111800126] = {
    id = 111800126,
    name = "嗯，你戴一个，哥哥戴一个。这样以后父亲找我的时候，你就可以在心里悄悄告诉我。"
  },
  [111800127] = {
    id = 111800127,
    name = "柯莱特尔有点发愣，随即露出了娇憨的模样。"
  },
  [111800128] = {
    id = 111800128,
    name = "原来送我魔法吊坠原来是这个目的！哼，我要去告诉父亲！"
  },
  [111800129] = {
    id = 111800129,
    name = "那你不要哥哥的礼物了吗？"
  },
  [111800130] = {
    id = 111800130,
    name = "......唔。"
  },
  [111800131] = {
    id = 111800131,
    name = "她盯着亮晶晶的吊坠，很舍不得的样子。"
  },
  [111800132] = {
    id = 111800132,
    name = "喜欢哪个？哥哥给你戴上。"
  },
  [111800133] = {
    id = 111800133,
    name = "小小的手指在方盒里滑来滑去，指了指左边的白金色，又指向了右边的银紫色。"
  },
  [111800134] = {
    id = 111800134,
    name = "我要这个紫色石头的吧！这个好像星星呀！"
  },
  [111800135] = {id = 111800135, name = "好。"},
  [111800136] = {
    id = 111800136,
    name = "你抽出盒中的银链，调整了适合柯莱特尔的长度，小心地挂在了她身上，她立刻抓起了吊坠，看到了其中流动的魔法符文，仿佛一场梦幻的流星雨。"
  },
  [111800137] = {
    id = 111800137,
    name = "真美啊......"
  },
  [111800138] = {
    id = 111800138,
    name = "收了哥哥的礼物，就要替哥哥保密哦。"
  },
  [111800139] = {
    id = 111800139,
    name = "柯莱特尔想压下嘴边的笑容，却怎么也做不出生气的假样，只能作罢。"
  },
  [111800140] = {
    id = 111800140,
    name = "好吧，这一次就饶过你了。哥哥，你也快戴上啊！"
  },
  [111800141] = {
    id = 111800141,
    name = "你将白金吊坠挂在了身上，那一瞬，心底有什么被触动了一下，接着你听到了柯莱特尔的声音。"
  },
  [111800142] = {id = 111800142, name = "哥哥？"},
  [111800143] = {id = 111800143, name = "哥哥？"},
  [111800144] = {id = 111800144, name = "嗯？"},
  [111800145] = {
    id = 111800145,
    name = "哥哥，你是不是被言灵骗了，我这样叫你，好像和之前没什么差别啊？"
  },
  [111800146] = {
    id = 111800146,
    name = "呵呵，你要在心里叫我。"
  },
  [111800147] = {
    id = 111800147,
    name = "心里叫？这样吗？"
  },
  [111800148] = {
    id = 111800148,
    name = "【哥哥。】"
  },
  [111800149] = {
    id = 111800149,
    name = "稚嫩的声音在耳边响起，仿若近在咫尺，但柯莱特尔的嘴巴并没有张开。"
  },
  [111800150] = {
    id = 111800150,
    name = "听不见吗？"
  },
  [111800151] = {
    id = 111800151,
    name = "【哥哥、哥哥、哥哥、哥哥。】"
  },
  [111800152] = {
    id = 111800152,
    name = "【听见了，妹妹。】"
  },
  [111800153] = {
    id = 111800153,
    name = "你扶额，阻止了这次魔音贯耳，柯莱特尔却开心地跳了起来。"
  },
  [111800154] = {
    id = 111800154,
    name = "啊！哥哥，刚才是你回答我了吗？"
  },
  [111800155] = {id = 111800155, name = "对。"},
  [111800156] = {
    id = 111800156,
    name = "太棒了！这个好好玩！"
  },
  [111800157] = {
    id = 111800157,
    name = "你看着她可爱的笑容，不由得也被感染了笑出声。正打算让她和你一起回家，柯莱特尔却一溜跑得远远的。"
  },
  [111800158] = {
    id = 111800158,
    name = "【哥哥，你听得到吗？】"
  },
  [111800159] = {
    id = 111800159,
    name = "你无奈地笑了笑，点点头。柯莱特尔大笑着转身跑了好几步，回头朝你招手。"
  },
  [111800160] = {
    id = 111800160,
    name = "【哥哥，你听得到吗？！】"
  },
  [111800161] = {
    id = 111800161,
    name = "【听到了。】"
  },
  [111800162] = {
    id = 111800162,
    name = "柯莱特尔原地蹦跳了起来，躲在了一棵树后。"
  },
  [111800163] = {
    id = 111800163,
    name = "【哥哥，你听得到吗？！！】"
  },
  [111800164] = {
    id = 111800164,
    name = "【听~到~了~】"
  },
  [111800165] = {
    id = 111800165,
    name = "【哈哈，哥哥，这个太好玩啦！】"
  },
  [111800166] = {
    id = 111800166,
    name = "柯莱特尔在原地转了几个圈圈，紫水晶随她飞起，熠熠生光。"
  },
  [111800167] = {
    id = 111800167,
    name = "【哥哥，不管我在哪里，你都能听到我的话吗？】"
  },
  [111800168] = {
    id = 111800168,
    name = "【嗯，能听到。】"
  },
  [111800169] = {
    id = 111800169,
    name = "【到君主的城堡那么远也可以吗？】"
  },
  [111800170] = {
    id = 111800170,
    name = "柯莱特尔跑了回来，晶晶亮亮地望着你。你笑了笑，牵起了她的手。"
  },
  [111800171] = {id = 111800171, name = "可以。"},
  [111800172] = {
    id = 111800172,
    name = "不管你在哪儿，只要你呼唤我，我就一定能找到你。"
  },
  [111800173] = {
    id = 111800173,
    name = "太好了！要是还有这样的吊坠，可以给父亲大人一个，母亲大人一个......"
  },
  [111800174] = {
    id = 111800174,
    name = "只有这一对哦，那个言灵已经不在了。"
  },
  [111800175] = {
    id = 111800175,
    name = "啊，这样呀......咦，哥哥，这边不是回家的路。"
  },
  [111800176] = {
    id = 111800176,
    name = "出来了这么久，总要找个再理由回去。"
  },
  [111800177] = {
    id = 111800177,
    name = "我之前发现宴会需要的鲜花不足，亲自去城里挑选了一些，现在该去拿货了。"
  },
  [111800178] = {
    id = 111800178,
    name = "【啊，狡猾的哥哥。】"
  },
  [111800179] = {
    id = 111800179,
    name = "【我听见了哦。】"
  },
  [111800180] = {id = 111800180, name = "哼。"},
  [111800181] = {
    id = 111800181,
    name = "你牵着柯莱特尔在城里转了一大圈，没有找到更多的线索，只能根据记忆从花农那运了一大车鲜花，和妹妹愉快地回了城堡。"
  },
  [111800182] = {
    id = 111800182,
    name = "少爷和小姐回来了！"
  },
  [111800183] = {
    id = 111800183,
    name = "众多仆人迎了上来，你愣了愣，对这个称呼还不太习惯。"
  },
  [111800184] = {
    id = 111800184,
    name = "（如果是菲尼斯，应该如鱼得水吧。）"
  },
  [111800185] = {
    id = 111800185,
    name = "【哎呀，父亲大人来了！哥哥我困了，先回房午睡啦！】"
  },
  [111800186] = {
    id = 111800186,
    name = "一回头，柯莱特尔已经不见了。"
  },
  [111800187] = {id = 111800187, name = "......"},
  [111800188] = {
    id = 111800188,
    name = "你看到远处走来的中年男人，身披绸缎长袍，银扣随着他稳健的步伐闪烁着微光，流露出不经意的优雅与从容。"
  },
  [111800189] = {
    id = 111800189,
    name = "父亲大人。"
  },
  [111800190] = {
    id = 111800190,
    name = "出门了？"
  },
  [111800191] = {
    id = 111800191,
    name = "是的，父亲大人，我看需要布置的鲜花不够了，就去城里挑选了一些，都在这里了。"
  },
  [111800192] = {
    id = 111800192,
    name = "你让侍卫把花车拉走，看向普尔兰面无表情的脸，转开了眼光。你不敢保证他发现不了你的异状。"
  },
  [111800193] = {
    id = 111800193,
    name = "行了。去把宾客名单再检查一遍，确保不能遗漏。"
  },
  [111800194] = {id = 111800194, name = "是。"},
  [111800195] = {
    id = 111800195,
    name = "侍卫的安保部署怎么样了？"
  },
  [111800196] = {
    id = 111800196,
    name = "你呆住了。“你”好像已经完全忘了这件事，可就是这犹豫的时间，普尔兰伯爵的眉头就皱了起来，你赶忙开口补救。"
  },
  [111800197] = {
    id = 111800197,
    name = "是这样的。之前计划的方案，我发现仍有漏洞，就打算在偏僻的角落安装一些简单的魔法陷阱，尊贵的宾客不会去那里，这样也可以防止宵小擅自闯入庄园。"
  },
  [111800198] = {
    id = 111800198,
    name = "嗯，你看着安排吧。"
  },
  [111800199] = {
    id = 111800199,
    name = "看普尔兰伯爵松开了眉头，你也松了一口气。这时，他伸出手，把你脑袋上的叶子拿了下来放在你手上。"
  },
  [111800200] = {id = 111800200, name = "......"},
  [111800201] = {
    id = 111800201,
    name = "你也不小了，不要整天想着出去玩，德温特教授已经在阁楼等你很久了。"
  },
  [111800202] = {
    id = 111800202,
    name = "他轻哼了一声，带着侍卫走远了，你赶紧把手里的叶子扔掉。"
  },
  [111800203] = {
    id = 111800203,
    name = "（还好不是真的，但为什么还是会觉得尴尬......）"
  },
  [111800204] = {
    id = 111800204,
    name = "你抬眼望着眼前巨大的庄园，仍然想不通为何会出现这样的“幻境”。"
  },
  [111800205] = {
    id = 111800205,
    name = "（要怎么样才能离开这个“幻境”？目前也没有发现其他同伴的存在......）"
  },
  [111800206] = {
    id = 111800206,
    name = "少爷，宾客名单都准备好了。您看看，是先去见德温特教授，还是......"
  },
  [111800207] = {
    id = 111800207,
    name = "先看名单。你去跟特温特教授说，这两日太忙，他布置的课业我之后会一同补上。"
  },
  [111800208] = {
    id = 111800208,
    name = "是的，少爷。"
  },
  [111800209] = {
    id = 111800209,
    name = "你暗自呼了一口气，印象里，那个老教授古板刚正，一去就耽搁一整天，还是能不见就不见吧。"
  },
  [111800210] = {
    id = 111800210,
    name = "（当务之急，还是寻找脱身的办法，也许这个“宴会”才是关键。）"
  },
  [111800211] = {
    id = 111800211,
    name = "你回到了属于自己的房间，随侍的仆人递上了一张金丝册子，你暗自吐槽了贵族的奢靡，仔细地看了起来。"
  },
  [111800212] = {
    id = 111800212,
    name = "（奥布里昂勋爵，格兰瑟姆公爵，德温特小姐，哦，是阁楼那个老爷子的孙女......啊，头晕脑胀。）"
  },
  [111800213] = {
    id = 111800213,
    name = "你帮我看看。"
  },
  [111800214] = {
    id = 111800214,
    name = "你把册子扔给了仆人，却见他眼神闪烁，低着头翻开了名单，隔了好一阵，才垂首回答。"
  },
  [111800215] = {
    id = 111800215,
    name = "回少爷，目前没有发现遗漏的宾客。"
  },
  [111800216] = {
    id = 111800216,
    name = "确定没有？"
  },
  [111800217] = {
    id = 111800217,
    name = "仆人表情凝固，又慌乱了一下。"
  },
  [111800218] = {
    id = 111800218,
    name = "诺瓦克家族虽然和大主教有亲缘，但伯爵大人一直没打算原谅他，少爷，您打算邀请诺瓦克家族参加宴会吗？"
  },
  [111800219] = {
    id = 111800219,
    name = "（什么跟什么啊？听他的意思，这个家还有个仇人？）"
  },
  [111800220] = {
    id = 111800220,
    name = "要不，你给我出出主意？"
  },
  [111800221] = {
    id = 111800221,
    name = "少爷，大主教在上个月试图说服陛下没收伯爵大人的领地，这时候激怒他们，并不是一件明智的事。"
  },
  [111800222] = {
    id = 111800222,
    name = "哦，你是指责父亲大人昏庸无能吗？还是，你有其他的想法？"
  },
  [111800223] = {
    id = 111800223,
    name = "仆人嘭地跪了下去，发出的撞击声吓了你一跳，也越来越觉得这个仆人有问题。"
  },
  [111800224] = {
    id = 111800224,
    name = "少爷，我向希望女神起誓，我对普尔兰家族忠心耿耿，但凡有一句虚言，我将万箭穿心，永堕地狱！"
  },
  [111800225] = {
    id = 111800225,
    name = "希望女神不管死后的事，永堕地狱这种无聊的誓言对我没有意义。"
  },
  [111800226] = {
    id = 111800226,
    name = "再说，我又没有说你背叛了家族，你紧张什么？"
  },
  [111800227] = {
    id = 111800227,
    name = "仆人的脸涨得通红。"
  },
  [111800228] = {
    id = 111800228,
    name = "除了诺瓦克家族，还有别的什么遗漏吗？"
  },
  [111800229] = {
    id = 111800229,
    name = "回少爷的话，没，没有了。"
  },
  [111800230] = {
    id = 111800230,
    name = "那你走吧，别吵我睡午觉。"
  },
  [111800231] = {
    id = 111800231,
    name = "少爷，德温特教授那边——"
  },
  [111800232] = {
    id = 111800232,
    name = "不是说了这两天很忙吗，宴会之后再说。"
  },
  [111800233] = {
    id = 111800233,
    name = "是。愿您有个好梦。"
  },
  [111800234] = {
    id = 111800234,
    name = "仆人离开之后，你揉了揉脑袋，走向窗台，尝试着敲了三下。果然，暗中保护你的侍卫出现在了树荫下，你指了指走远的仆人。"
  },
  [111800235] = {
    id = 111800235,
    name = "跟着他，有什么发现回来告诉我。"
  },
  [111800236] = {
    id = 111800236,
    name = "侍卫不言不语，朝你行了一礼后就藏入了阴影中。"
  },
  [111800237] = {
    id = 111800237,
    name = "（藏在影子里的魔法？有意思。）"
  },
  [111800238] = {
    id = 111800238,
    name = "将身边的人都支走后，你一头砸进了柔软的床铺里。"
  },
  [111800239] = {
    id = 111800239,
    name = "（呼，终于没人了。）"
  },
  [111800240] = {
    id = 111800240,
    name = "你的头埋在软软的天鹅绒中，这是你从未体验过的感受，只是梦境恐怕没有办法模拟出来，也绝对不会这样真实。"
  },
  [111800241] = {
    id = 111800241,
    name = "（难道，我和这位普尔兰少爷互换了身体？）"
  },
  [111800242] = {
    id = 111800242,
    name = "你笑了笑，甩开这种荒诞的想法，看向窗外的阳光，似乎出现了一条裂缝，又很快消失了。"
  },
  [111800243] = {
    id = 111800243,
    name = "果然，再真实也是假的。"
  },
  [111800244] = {
    id = 111800244,
    name = "你试想过告诉别人你的身份，但临到头却犹豫了。如果不能破境，那你面临的也许是无法逆转的灭顶之灾——这个世界不会饶恕抢夺他人灵魂的恶魔。"
  },
  [111800245] = {
    id = 111800245,
    name = "【哥哥！救命救命！】"
  },
  [111800246] = {
    id = 111800246,
    name = "耳边传来柯莱特尔着急的呼唤，你心中一紧。"
  },
  [111800247] = {
    id = 111800247,
    name = "【妹妹，怎么了？！你在哪儿？！】"
  },
  [111800248] = {
    id = 111800248,
    name = "【我在城里迷路啦，这里好多人，我不知道该往哪个方向走......】"
  },
  [111800249] = {
    id = 111800249,
    name = "【......】"
  },
  [111800250] = {
    id = 111800250,
    name = "【哥哥，你听见了吗？哥哥！】"
  },
  [111800251] = {
    id = 111800251,
    name = "【你把周围的建筑物告诉我，然后站在原地别动，我找人来接你。】"
  },
  [111800252] = {
    id = 111800252,
    name = "【不要不要，这次我是瞒着母亲大人出来的。】"
  },
  [111800253] = {
    id = 111800253,
    name = "【......】"
  },
  [111800254] = {
    id = 111800254,
    name = "【哥哥，你能不能亲自出来一趟？】"
  },
  [111800255] = {
    id = 111800255,
    name = "【唉，好吧。】"
  },
  [111800256] = {
    id = 111800256,
    name = "【耶，我最喜欢你啦！那哥哥，在你找到我之前，我能跟你一直说话吗？】"
  },
  [111800257] = {
    id = 111800257,
    name = "【吊坠里的魔法力总有用完的时候，你省着点。】"
  },
  [111800258] = {
    id = 111800258,
    name = "【嗯，那我乖乖在这里等你来接我吧。】"
  },
  [111800259] = {
    id = 111800259,
    name = "你回应了一声，突然后悔把这件礼物送给她了。"
  },
  [111800260] = {
    id = 111800260,
    name = "好麻烦。"
  },
  [111800261] = {
    id = 111800261,
    name = "原本你也只是暂时藏在这具身体里，明明可以不用管那个调皮的小女孩，但却又忍不住宠爱她。"
  },
  [111800262] = {
    id = 111800262,
    name = "算了，谁让她可爱呢。"
  },
  [111800263] = {
    id = 111800263,
    name = "根据柯莱特尔所说的地点，你在一个摊贩前找到了她。她的眼睛亮晶晶的，得意地将廉价的玩具送到你的手里，说花了两枚金币买下了它。"
  },
  [111800264] = {
    id = 111800264,
    name = "哥哥，这个可好玩啦，转一转就能飞起来哦！"
  },
  [111800265] = {
    id = 111800265,
    name = "你笑着把昂贵的风车收了起来，牵着她回到了家门口，正好迎上了普尔兰伯爵严厉的目光。"
  },
  [111800266] = {
    id = 111800266,
    name = "柯莱特尔见着却一点不惧，笑着拿出了另一支风车，跑上前去。"
  },
  [111800267] = {
    id = 111800267,
    name = "爸爸！你看，我给你买了风车！这样在你太热的时候，它能呼啦啦地吹风哦！"
  },
  [111800268] = {
    id = 111800268,
    name = "喔，好极了。爸爸很喜欢！"
  },
  [111800269] = {
    id = 111800269,
    name = "我给妈妈也买了一支哦！"
  },
  [111800270] = {
    id = 111800270,
    name = "好啊，你亲自送去给她吧。"
  },
  [111800271] = {
    id = 111800271,
    name = "柯莱特尔跑走后，专属于她的慈父转眼就变了。"
  },
  [111800272] = {id = 111800272, name = "......"},
  [111800273] = {
    id = 111800273,
    name = "日安，父亲大人。"
  },
  [111800274] = {
    id = 111800274,
    name = "柯莱特尔年纪尚幼，你作为兄长，有责任做出表率。"
  },
  [111800275] = {id = 111800275, name = "是。"},
  [111800276] = {
    id = 111800276,
    name = "然而，你似乎没有将我的话放在心上。"
  },
  [111800277] = {
    id = 111800277,
    name = "你开口想狡辩，想起“你”以前的样子，立刻低头道歉。"
  },
  [111800278] = {
    id = 111800278,
    name = "我知道错了，以后也不会再犯这种错误。"
  },
  [111800279] = {
    id = 111800279,
    name = "知道就好。"
  },
  [111800280] = {
    id = 111800280,
    name = "领完罚就早点休息，明天宴会就开始了，会更忙的。"
  },
  [111800281] = {
    id = 111800281,
    name = "是，父亲大人。"
  },
  [111800282] = {
    id = 111800282,
    name = "你答应得爽快，普尔兰伯爵意外地看了你一眼，少见地朝你笑了笑，转身离开。"
  },
  [111800283] = {
    id = 111800283,
    name = "（他笑什么？）"
  },
  [111800284] = {
    id = 111800284,
    name = "少爷，请跟我来。"
  },
  [111800285] = {
    id = 111800285,
    name = "干什么？"
  },
  [111800286] = {
    id = 111800286,
    name = "少爷，您忘了，地下牢房在这边。主人说这次您如果认错态度不错，就只罚您悬吊反省四个小时，背诵德温特教授的五节课文就可以出来了。"
  },
  [111800287] = {
    id = 111800287,
    name = "只？罚四小时？"
  },
  [111800288] = {
    id = 111800288,
    name = "你麻木地跟在管家身后，不由得深深怀疑了起来。"
  },
  [111800289] = {
    id = 111800289,
    name = "（怎么对儿子和对女儿的态度不一样？这家伙是他亲生的吗？）"
  },
  [111800290] = {id = 111800290, name = "......"},
  [111800291] = {id = 111800291, name = "......"},
  [111800292] = {
    id = 111800292,
    name = "从地下牢房出来的时候，已是月上中天，你浑身的力气都没了，整个人蔫巴巴的，顶着惨白的脸倒在华贵的床铺里。"
  },
  [111800293] = {
    id = 111800293,
    name = "（普尔兰伯爵真是教子有方，对贪玩的少爷用这种办法，就算是头猪也能培养成合格的继承人。）"
  },
  [111800294] = {
    id = 111800294,
    name = "不止是肉体和精神的折磨，整整四个小时一盏灯都看不见，黑漆漆的令人恐惧。地牢里不知被关过什么，总发出耐人寻味的声音，再加上腐烂的味道，真让人觉得头皮发麻。"
  },
  [111800295] = {
    id = 111800295,
    name = "也难怪你一看见地牢门口，就不自觉地想后退，竟然是被罚过，怕了。"
  },
  [111800296] = {
    id = 111800296,
    name = "（就连疼痛也是真实的......）"
  },
  [111800297] = {
    id = 111800297,
    name = "你排解着脑子里的不适，努力想怎么解决眼下的难题。"
  },
  [111800298] = {
    id = 111800298,
    name = "（如果不能离开，难道一辈子留在这里吗？）"
  },
  [111800299] = {
    id = 111800299,
    name = "【哥哥。】"
  },
  [111800300] = {
    id = 111800300,
    name = "耳边又传来了柯莱特尔的声音，这一次她的声音很轻，如同在你耳边说着悄悄话。"
  },
  [111800301] = {
    id = 111800301,
    name = "【哥哥，你睡了吗？】"
  },
  [111800302] = {
    id = 111800302,
    name = "【没有。】"
  },
  [111800303] = {
    id = 111800303,
    name = "【哥哥，对不起呀。是我让你出来，才被父亲罚了。】"
  },
  [111800304] = {
    id = 111800304,
    name = "柯莱特尔的心声竟然是哽咽的，你能想到她眼红红的可怜模样，一下就心软了。"
  },
  [111800305] = {
    id = 111800305,
    name = "【没关系，你也是想给家人们准备礼物才出去的是不是？】"
  },
  [111800306] = {
    id = 111800306,
    name = "【本来还想给你们一个惊喜，但我好笨，找不到回家的路了。】"
  },
  [111800307] = {
    id = 111800307,
    name = "【不会找不到的。你叫我，我不是就带你回家了吗？】"
  },
  [111800308] = {
    id = 111800308,
    name = "【嗯！】"
  },
  [111800309] = {
    id = 111800309,
    name = "【哥哥，我知道你没吃东西，晚餐之后，我悄悄藏了几块蛋糕放在你床底下啦！】"
  },
  [111800310] = {id = 111800310, name = "？？？"},
  [111800311] = {
    id = 111800311,
    name = "你垂下半个身子吊在床边，向床底望去，果然看到了摆着一盘小蛋糕，不由会心一笑。"
  },
  [111800312] = {
    id = 111800312,
    name = "【看见了，谢谢妹妹。】"
  },
  [111800313] = {
    id = 111800313,
    name = "你边吃边听柯莱特尔悄悄说话，蛋糕十分合你的口味。"
  },
  [111800314] = {
    id = 111800314,
    name = "【这个魔法吊坠真的太神奇了。】"
  },
  [111800315] = {
    id = 111800315,
    name = "【嗯？】"
  },
  [111800316] = {
    id = 111800316,
    name = "【我一叫哥哥，哥哥真的会回应我。中午的时候，我还以为会被父亲大人抓回去呢。】"
  },
  [111800317] = {
    id = 111800317,
    name = "【但是哥哥真的来了......太好了。】"
  },
  [111800318] = {
    id = 111800318,
    name = "【你喜欢这个礼物就好。】"
  },
  [111800319] = {
    id = 111800319,
    name = "【嘻嘻，哥哥，你吃完了吗？】"
  },
  [111800320] = {
    id = 111800320,
    name = "【嗯。】"
  },
  [111800321] = {
    id = 111800321,
    name = "休息了好一阵，你有了些精神，虽然地下牢房没让你沾染那些脏东西，但你还是打算沐浴之后再入睡。"
  },
  [111800322] = {id = 111800322, name = "来人。"},
  [111800323] = {
    id = 111800323,
    name = "仆人应声而进，听到你的要求，诧异地看了你一眼，随即立刻去准备了。"
  },
  [111800324] = {
    id = 111800324,
    name = "【哈欠......】"
  },
  [111800325] = {
    id = 111800325,
    name = "【好了，你该去睡觉了。】"
  },
  [111800326] = {
    id = 111800326,
    name = "【不要，我还想和哥哥说话。】"
  },
  [111800327] = {
    id = 111800327,
    name = "【想说什么？】"
  },
  [111800328] = {
    id = 111800328,
    name = "【想听哥哥多跟我说话......平时哥哥不是在学习剑术，就是在完成父亲布置的课业，很少有时间陪我玩的。】"
  },
  [111800329] = {
    id = 111800329,
    name = "【那......我给你讲个故事好不好？】"
  },
  [111800330] = {
    id = 111800330,
    name = "【好呀！】"
  },
  [111800331] = {
    id = 111800331,
    name = "咚咚咚。仆人在外面提醒你准备好了，你进入浴室，三下五除二跳进了宽大的浴缸中，发出一声喟叹。"
  },
  [111800332] = {
    id = 111800332,
    name = "【你想听什么样的故事？】"
  },
  [111800333] = {
    id = 111800333,
    name = "【只要是哥哥讲的就好。】"
  },
  [111800334] = {
    id = 111800334,
    name = "柯莱特尔的声音更轻了，像只扑朔的蝴蝶。"
  },
  [111800335] = {
    id = 111800335,
    name = "【那我给你讲一个关于魔法的故事吧。】"
  },
  [111800336] = {
    id = 111800336,
    name = "【嗯。】"
  },
  [111800337] = {
    id = 111800337,
    name = "【在很久很久以前，北方有一个冰封的地方，那里住着一个小女孩，她拥有一件魔法斗篷，能将她自己变成想要的任何模样......】"
  },
  [111800338] = {
    id = 111800338,
    name = "【哈欠......什么样子......都可以吗......】"
  },
  [111800339] = {
    id = 111800339,
    name = "【可以的。她还有一个很好的朋友，是隔壁擅长打猎的哥哥，他们一起生活在村庄里，非常地幸福。】"
  },
  [111800340] = {
    id = 111800340,
    name = "你顿了顿，柯莱特尔没有回应，但你继续讲了下去。"
  },
  [111800341] = {
    id = 111800341,
    name = "【可是，恶名昭彰的国王听说了这件事，他想抢走那件魔法斗篷，于是用魔法将村庄冻起来了。】"
  },
  [111800342] = {
    id = 111800342,
    name = "【......】"
  },
  [111800343] = {
    id = 111800343,
    name = "【妹妹，你还在听吗？】"
  },
  [111800344] = {
    id = 111800344,
    name = "【......】"
  },
  [111800345] = {
    id = 111800345,
    name = "回答你的是窗外偶尔的蝉鸣，柯莱特尔睡着了。你微微一笑，对着无人的天花板，轻轻开口。"
  },
  [111800346] = {
    id = 111800346,
    name = "晚安，柯莱特尔。祝你好梦。"
  },
  [111800347] = {
    id = 111800347,
    name = "一夜好眠，精神抖擞。"
  },
  [111800348] = {
    id = 111800348,
    name = "你根据回忆，大概摸清了这个家庭的规律，于是一大早就听管家所陈述的“伯爵的安排”，在书房抄写功课，得到了管家欣慰的眼神——这个废物总算懂事了。"
  },
  [111800349] = {
    id = 111800349,
    name = "（唉，人可不会在一夜之间改变，他在期待些什么？）"
  },
  [111800350] = {
    id = 111800350,
    name = "而后你阅览了众多书籍和信件，大致发现了一件事——现在的时间，是十五年前。"
  },
  [111800351] = {
    id = 111800351,
    name = "这里是普尔兰城，诺维奇帝国下属的领地之一，属普尔兰家族世代所有，离玛蒙之地有上万里的距离。"
  },
  [111800352] = {
    id = 111800352,
    name = "由于地面的暗域还没有扩张得那么快，深渊对普通的帝国居民来说，更多的是探险者的冒险之地。"
  },
  [111800353] = {
    id = 111800353,
    name = "（如果现在能去玛蒙之地，也许能看到十五年前的我也说不定，呵呵。）"
  },
  [111800354] = {
    id = 111800354,
    name = "明知不可能，你也忍不住胡思乱想。就在这时，一封信从书籍中掉了出来。"
  },
  [111800355] = {id = 111800355, name = "嗯？"},
  [111800356] = {
    id = 111800356,
    name = "这是一封带火漆的信，上面印着一个家族的徽章，这是——"
  },
  [111800357] = {
    id = 111800357,
    name = "嗯？诺瓦克？我们还与他们有交流？"
  },
  [111800358] = {
    id = 111800358,
    name = "你想起来那是个与你们有仇的家族，却没在记忆中翻到你们两家反目成仇的原因，你毫不犹豫打开了信封，正打算展开时，一道黑影从你身边缓缓站立。"
  },
  [111800359] = {
    id = 111800359,
    name = "主人，这是昨晚的调查结果。"
  },
  [111800360] = {
    id = 111800360,
    name = "他拿出了一截竹筒，抽出了其中的情报，里面清清楚楚记录了仆人从房间离开后到睡觉的行动轨迹。"
  },
  [111800361] = {
    id = 111800361,
    name = "他去过地下牢房？"
  },
  [111800362] = {id = 111800362, name = "是。"},
  [111800363] = {
    id = 111800363,
    name = "侍卫回答之后一言不发，垂目而站，等待你的下一步指示。"
  },
  [111800364] = {
    id = 111800364,
    name = "你却随意地扔在了一边，在侍卫的欲言又止下打开了那封火漆的信，信上明确地告知，诺瓦克家族会在今晚的宴会上有所行动。"
  },
  [111800365] = {
    id = 111800365,
    name = "不请自来吗？"
  },
  [111800366] = {
    id = 111800366,
    name = "你思考了一阵，想不清楚这个“有所行动”到底是指什么。"
  },
  [111800367] = {
    id = 111800367,
    name = "【妹妹，你有空吗？】"
  },
  [111800368] = {
    id = 111800368,
    name = "【哥哥！我在学小白鸟唱歌，它唱得可真好听！】"
  },
  [111800369] = {
    id = 111800369,
    name = "【你来书房一趟，带哥哥去喝下午茶好不好？】"
  },
  [111800370] = {
    id = 111800370,
    name = "【我知道了，哥哥，你又想偷懒，我才不会帮你呢，要是你又被罚了怎么办？】"
  },
  [111800371] = {
    id = 111800371,
    name = "【不会，我已经完成了今日的课业，只是不方便离开而已。拜托了，妹妹。】"
  },
  [111800372] = {
    id = 111800372,
    name = "【唔，哥哥有点可怜呢，那我就帮哥哥这一次，等我哦，很快就到！】"
  },
  [111800373] = {
    id = 111800373,
    name = "【好。】"
  },
  [111800374] = {id = 111800374, name = "主人？"},
  [111800375] = {
    id = 111800375,
    name = "你回过神，开始安排宴会的布防人员。"
  },
  [111800376] = {
    id = 111800376,
    name = "今日宾客就会逐一到来。总之，这件事就交给你了，务必部署到位。"
  },
  [111800377] = {id = 111800377, name = "是。"},
  [111800378] = {
    id = 111800378,
    name = "对了，尤其是东南角一定要严加防范，那个地方我的魔法陷阱总缺了一块，怎么也布置不好。"
  },
  [111800379] = {id = 111800379, name = "是。"},
  [111800380] = {
    id = 111800380,
    name = "这可是我全家的身家性命，你是我最相信的人了。"
  },
  [111800381] = {
    id = 111800381,
    name = "为主人死不足惜。"
  },
  [111800382] = {
    id = 111800382,
    name = "你摆了摆手，侍卫仍然站立不动。"
  },
  [111800383] = {
    id = 111800383,
    name = "主人的身边需要有人守护。"
  },
  [111800384] = {
    id = 111800384,
    name = "这是我家，不是深渊，要是我家都不安全，要你们去布防做什么？"
  },
  [111800385] = {id = 111800385, name = "哥哥！"},
  [111800386] = {
    id = 111800386,
    name = "柯莱特尔，你来做什么？"
  },
  [111800387] = {
    id = 111800387,
    name = "柯莱特尔错愕地眨了眨眼，瞬间反应了过来。"
  },
  [111800388] = {
    id = 111800388,
    name = "下午茶时间到了哦，哥哥快来呀！"
  },
  [111800389] = {
    id = 111800389,
    name = "好好，别拖我，我跟你走就是了。"
  },
  [111800390] = {
    id = 111800390,
    name = "你顺理成章地被柯莱特尔拉着离开了房间，挺拔的侍卫又重新消失在了阴影中。"
  },
  [111800391] = {id = 111800391, name = "......"},
  [111800392] = {
    id = 111800392,
    name = "少爷，小姐。今天的茶品是薰衣草、圣约翰草与玫瑰的混合红茶，中和了木质与果香的味道。"
  },
  [111800393] = {
    id = 111800393,
    name = "甜点有黑森林浆果制成的果酱塔，陶瓷奶油奶酪布丁和蜂蜜核桃蛋糕。"
  },
  [111800394] = {
    id = 111800394,
    name = "咸点是烤鹌鹑配肉桂、丁香与姜粉；烟熏鹿肉薄片，以及香草酱甜菜根。"
  },
  [111800395] = {
    id = 111800395,
    name = "另外为少爷与小姐准备了黑莓，可以搭配蜂蜜与肉桂粉食用。"
  },
  [111800396] = {id = 111800396, name = "......"},
  [111800397] = {
    id = 111800397,
    name = "（我总算是知道那些贵族瞧不上学院下午茶的原因了......）"
  },
  [111800398] = {
    id = 111800398,
    name = "哥哥，我想尝尝那个......"
  },
  [111800399] = {id = 111800399, name = "嗯？"},
  [111800400] = {
    id = 111800400,
    name = "就是那个......"
  },
  [111800401] = {
    id = 111800401,
    name = "呵呵，小姐说的是蜂蜜奶酒吧？上次不小心尝了一点少爷的，一直闹着要喝呢。"
  },
  [111800402] = {
    id = 111800402,
    name = "不行，小孩子不能喝酒。"
  },
  [111800403] = {id = 111800403, name = "哼。"},
  [111800404] = {
    id = 111800404,
    name = "柯莱特尔转过身，不理你了。"
  },
  [111800405] = {id = 111800405, name = "......"},
  [111800406] = {
    id = 111800406,
    name = "你忍住品尝美食的冲动，好说歹说，柯莱特尔却总是背过身不愿看你，你叹了口气。"
  },
  [111800407] = {
    id = 111800407,
    name = "酒是绝对不可以喝的，但哥哥可以答应你另外一件事行不行？"
  },
  [111800408] = {
    id = 111800408,
    name = "另外一件事？说话算话？"
  },
  [111800409] = {
    id = 111800409,
    name = "不能是喝酒这种小孩子不可以做的事。"
  },
  [111800410] = {
    id = 111800410,
    name = "听到这里，柯莱特尔转过了身。"
  },
  [111800411] = {
    id = 111800411,
    name = "那好吧，我要吃那个布丁。"
  },
  [111800412] = {
    id = 111800412,
    name = "你立刻把布丁放在了她的桌前，这时柯莱特尔慧黠一笑，端起布丁就砸向了你的脸。"
  },
  [111800413] = {id = 111800413, name = "......"},
  [111800414] = {
    id = 111800414,
    name = "哈哈哈哈！"
  },
  [111800415] = {
    id = 111800415,
    name = "你是躲得掉的，但闪避的一瞬间却停了下来，硬生生挨了一脸的奶油。柯莱特尔笑得甜点也不吃了，直在地上打滚。"
  },
  [111800416] = {
    id = 111800416,
    name = "呼，开心了？这就是你说的事，我做到了哦。"
  },
  [111800417] = {
    id = 111800417,
    name = "你接过仆人递来的毛巾，擦了半晌才擦干净。"
  },
  [111800418] = {
    id = 111800418,
    name = "不是哦，这可不是我的要求，是哥哥自己把布丁给我的。"
  },
  [111800419] = {
    id = 111800419,
    name = "（鬼灵精怪。）"
  },
  [111800420] = {
    id = 111800420,
    name = "那你想要我做什么？"
  },
  [111800421] = {
    id = 111800421,
    name = "哥哥帮我挑裙子呀！今晚宴会的裙子，我要哥哥帮我挑！"
  },
  [111800422] = {
    id = 111800422,
    name = "以往不都是母亲大人给你挑的吗？"
  },
  [111800423] = {
    id = 111800423,
    name = "她给我挑的又紧又难受，说要做什么乖乖的淑女，走几步路背上都疼死啦。"
  },
  [111800424] = {
    id = 111800424,
    name = "（难怪列蒂西雅总是从宴会逃走。）"
  },
  [111800425] = {
    id = 111800425,
    name = "你要我做的就是这件事？"
  },
  [111800426] = {id = 111800426, name = "对呀。"},
  [111800427] = {
    id = 111800427,
    name = "好，喝完下午茶，我们就去吧。"
  },
  [111800428] = {id = 111800428, name = "好耶！"},
  [111800429] = {
    id = 111800429,
    name = "不过你也答应我一件事好不好？"
  },
  [111800430] = {id = 111800430, name = "嗯？"},
  [111800431] = {
    id = 111800431,
    name = "今晚宴会的时候，跟好哥哥，不要乱跑。"
  },
  [111800432] = {
    id = 111800432,
    name = "嘻嘻，哥哥每次都这么说。"
  },
  [111800433] = {id = 111800433, name = "？？？"},
  [111800434] = {
    id = 111800434,
    name = "哥哥放心，我一定牵紧你，你就好好带着我去炫耀一番，让他们看看我有多可爱！"
  },
  [111800435] = {id = 111800435, name = "......"},
  [111800436] = {
    id = 111800436,
    name = "（原来你们是如此自信的兄妹。）"
  },
  [111800437] = {
    id = 111800437,
    name = "你在柯莱特尔的笑容里咬下了一口果酱塔。"
  },
  [111800438] = {
    id = 111800438,
    name = "（啧，有点太甜了。）"
  },
  [111800439] = {
    id = 111800439,
    name = "夕阳的余晖渐渐淡去，城堡在暮色中迎来了灯火。"
  },
  [111800440] = {
    id = 111800440,
    name = "宴会厅内灯火辉煌，烛光摇曳，映照出金碧辉煌的装饰，宾客们身着华丽的服饰，妆容精致，步履轻盈地步入宴会厅。"
  },
  [111800441] = {
    id = 111800441,
    name = "哥哥，她们的裙子都没有我的好看。"
  },
  [111800442] = {
    id = 111800442,
    name = "嘘，这种话放在心里说就好了。"
  },
  [111800443] = {
    id = 111800443,
    name = "【这种闹哄哄的场合，谁会听见呀。】"
  },
  [111800444] = {
    id = 111800444,
    name = "正经点。"
  },
  [111800445] = {
    id = 111800445,
    name = "是，父亲大人。"
  },
  [111800446] = {
    id = 111800446,
    name = "柯莱特尔朝你吐了吐舌头，你无奈地笑笑，牵着她跟在普尔兰伯爵与夫人身后，走上主宾席前的舞台，在伯爵一段欢迎说辞后，宴会正式开始了。"
  },
  [111800447] = {
    id = 111800447,
    name = "乐师们奏响了悠扬的乐曲，贵族们围坐在长桌旁，开始享用这场视觉与味觉的双重盛宴。他们或轻声交谈，讨论着时下流行的风尚与文学艺术作品，默契地不谈风雨飘摇的帝国根基。"
  },
  [111800448] = {
    id = 111800448,
    name = "你一边听着众人的谈论，一边享用着珍馐美味。"
  },
  [111800449] = {
    id = 111800449,
    name = "（就算是幻境，这种从未有过的体验也真不错啊。）"
  },
  [111800450] = {
    id = 111800450,
    name = "别躲在这里了，好好去跟你的几个叔父说说话，另外德温特教授的孙女也来了，你们年轻人也多交流交流。"
  },
  [111800451] = {
    id = 111800451,
    name = "......是，父亲大人。"
  },
  [111800452] = {
    id = 111800452,
    name = "【可怜的哥哥。】"
  },
  [111800453] = {
    id = 111800453,
    name = "你回头看一眼柯莱特尔，提醒她别到处乱走，她朝你举了一杯草莓汁，学大人喝酒一般朝你喝了一口，你没好气地走向舞池。"
  },
  [111800454] = {
    id = 111800454,
    name = "你晃了一圈，都是“你”似乎见过的面孔，每个人都有些熟悉，但与名字无法匹配。"
  },
  [111800455] = {
    id = 111800455,
    name = "（啊，完全记不得那个德温特小姐的模样......）"
  },
  [111800456] = {
    id = 111800456,
    name = "正当一曲结束，宾客们心满意足地离开舞池时，你看到几名侍从不自在的脸。"
  },
  [111800457] = {
    id = 111800457,
    name = "时间差不多了。"
  },
  [111800458] = {
    id = 111800458,
    name = "你将杯中的美酒饮尽，然后，松开了手。"
  },
  [111800459] = {
    id = 111800459,
    name = "啪！玻璃碎裂，接着，宴会厅的大门被猛然推开！"
  },
  [111800460] = {
    id = 111800460,
    name = "几个杀气腾腾的不速之客冲了进来。虽然记不住人脸，但你认得那群人身上的花纹，的确是诺瓦克家族。"
  },
  [111800461] = {
    id = 111800461,
    name = "他们一进来就打碎了一座香槟塔，清脆尖锐的声音响彻宴会厅，众人那一瞬都安静了下来。"
  },
  [111800462] = {
    id = 111800462,
    name = "（看来，这就是破局的关键。）"
  },
  [111800463] = {
    id = 111800463,
    name = "诸位贵宾不要慌乱，这几位似乎有重要的事跟我们说，大家先到楼上暂时休息。"
  },
  [111800464] = {
    id = 111800464,
    name = "由于你太过镇定，场上的宾客都被你这句话安抚了，正打算一一离开时，一个阴恻恻的声音在冷笑，冰冷的锐器放在了你的脖颈。"
  },
  [111800465] = {
    id = 111800465,
    name = "谁也跑不了，今晚，你们全部都要死。"
  },
  [111800466] = {id = 111800466, name = "是你。"},
  [111800467] = {
    id = 111800467,
    name = "不错，普尔兰少爷。很可惜，你信错了人。这里里外外都是我们的人。"
  },
  [111800468] = {
    id = 111800468,
    name = "普尔兰伯爵和夫人紧张地看着你，你扫视了一圈，竟然没有看到柯莱特尔。"
  },
  [111800469] = {
    id = 111800469,
    name = "【妹妹？】"
  },
  [111800470] = {
    id = 111800470,
    name = "【柯莱特尔？】"
  },
  [111800471] = {
    id = 111800471,
    name = "柯莱特尔没有回应，你心中有不太好的感觉。"
  },
  [111800472] = {
    id = 111800472,
    name = "呵呵，少爷已经吓傻了，大家别藏了，都出来吧！"
  },
  [111800473] = {
    id = 111800473,
    name = "宾客们左右相顾，除了那几个面有异色的侍从，再也没有其他人出现。"
  },
  [111800474] = {
    id = 111800474,
    name = "人呢？！"
  },
  [111800475] = {
    id = 111800475,
    name = "氛围凝固了一瞬，侍卫抓紧了武器，惊慌地在你脖子上留下一道血迹。"
  },
  [111800476] = {
    id = 111800476,
    name = "你做了什么？！"
  },
  [111800477] = {
    id = 111800477,
    name = "他们进不来的，都在陷阱里吧。说起来，应该是你邀请他们进入的，我猜猜，是不是防守“薄弱”的东南角？"
  },
  [111800478] = {
    id = 111800478,
    name = "你骗我？！我弄死你！"
  },
  [111800479] = {
    id = 111800479,
    name = "短剑即将划破肌肤的那一刻，你猛地一侧身，抓住他的手腕，将短剑的方向巧妙地偏移开来，避免了致命的威胁同时，也卸掉了他的武器，抓在了手中。"
  },
  [111800480] = {
    id = 111800480,
    name = "怎么可能！我一刻不停地在监视你，你不过是个只会偷懒的废物！！"
  },
  [111800481] = {
    id = 111800481,
    name = "也许吧。"
  },
  [111800482] = {
    id = 111800482,
    name = "你眼中露出一丝狠绝，心中涌起一股直觉——如果不解决一切，将会给家族带来无尽的麻烦和威胁，而你，也永远会滞留在这片虚无之地。"
  },
  [111800483] = {
    id = 111800483,
    name = "侍卫见行事彻底失利，脸色大变，忙施展魔法，准备逃进影子里。"
  },
  [111800484] = {
    id = 111800484,
    name = "逃不了的。"
  },
  [111800485] = {
    id = 111800485,
    name = "你翻身一剑插入阴影中，一股光束冲天而起！光与暗在空中碰撞、激荡，发出耀眼的光芒和震耳欲聋的轰鸣。"
  },
  [111800486] = {
    id = 111800486,
    name = "啊啊啊啊！"
  },
  [111800487] = {
    id = 111800487,
    name = "在绝望的挣扎中，他只能眼睁睁地看着自己的身体被一股无形的力量撕裂、吞噬，最终化为一缕青烟，消散在宴会厅的空气中。"
  },
  [111800488] = {
    id = 111800488,
    name = "这一幕让在场的所有人都震惊不已，而这段时间，管家已带来了忠心的侍卫，将背叛家族的人团团围住。"
  },
  [111800489] = {
    id = 111800489,
    name = "所有的不轨者都被押出了宴会厅，而散乱的大厅也重新布置一新，你惊叹仆从们的收拾速度和宾客们的强装镇定，真不愧是见多识广的贵族。"
  },
  [111800490] = {
    id = 111800490,
    name = "让诸位受惊了，这件小事已经解决，大家可以继续这场宴会，不用拘礼。"
  },
  [111800491] = {
    id = 111800491,
    name = "普尔兰伯爵示意宴会继续，音乐再起响起，你见普尔兰夫人朝你笑了笑，又混入了贵妇群中谈笑风生，心里不禁感叹他们面不改色的心理素质。"
  },
  [111800492] = {
    id = 111800492,
    name = "干得不错。"
  },
  [111800493] = {
    id = 111800493,
    name = "普尔兰伯爵走到了你身边，你甚至在他眼中看到了自豪的光。"
  },
  [111800494] = {
    id = 111800494,
    name = "（......那种看废物的眼神不见了。）"
  },
  [111800495] = {
    id = 111800495,
    name = "你是怎么阻止他们会在宴会上行事的？"
  },
  [111800496] = {
    id = 111800496,
    name = "多亏了管家。"
  },
  [111800497] = {
    id = 111800497,
    name = "你将两天发现的线索总结告诉了普尔兰伯爵。"
  },
  [111800498] = {
    id = 111800498,
    name = "管家无意中发现了诺瓦克家族的刺杀行动，来不及告诉外出的伯爵。而你身边随时跟着可疑的侍卫，也不知道庄园中还有多少其他势力的人。他只能以留书信的方式，给你留下关键的信息。"
  },
  [111800499] = {
    id = 111800499,
    name = "你阅览管家为你准备的书籍，梳理了一大批可能的人，并告诉侍卫“东南角”有破绽。"
  },
  [111800500] = {
    id = 111800500,
    name = "他们打算在宴会开始后，大家都松懈的时候再闯入宴会，大开杀戒。"
  },
  [111800501] = {
    id = 111800501,
    name = "可没想到的是，大批杀手已经在进入的那一刻，就掉入你布置的魔法阵中，伤亡惨重，只有零散撞门的几个，不足为惧。"
  },
  [111800502] = {
    id = 111800502,
    name = "少爷足智多谋，胆识过人，才能让主家完好无损地度过这一次危机啊。"
  },
  [111800503] = {
    id = 111800503,
    name = "普尔兰伯爵也赞同地点点头。"
  },
  [111800504] = {
    id = 111800504,
    name = "嗯，你长大了。"
  },
  [111800505] = {
    id = 111800505,
    name = "这都是父亲大人的教导。"
  },
  [111800506] = {
    id = 111800506,
    name = "你有些尴尬地笑了笑。"
  },
  [111800507] = {
    id = 111800507,
    name = "音乐的节奏似乎到了高潮，女士们的裙摆旋转、跳跃，飞扬，热烈又不失文雅，本是一场美好欢乐的宴会，却让你始终不安，总觉得少了些什么。"
  },
  [111800508] = {
    id = 111800508,
    name = "（对了，柯莱特尔！）"
  },
  [111800509] = {
    id = 111800509,
    name = "【妹妹！你在吗？柯莱特尔！】"
  },
  [111800510] = {
    id = 111800510,
    name = "还是没有回应。"
  },
  [111800511] = {
    id = 111800511,
    name = "父亲大人，柯莱特尔不见了！"
  },
  [111800512] = {
    id = 111800512,
    name = "怎么回事？！"
  },
  [111800513] = {
    id = 111800513,
    name = "小姐刚刚还在这里，怎么......"
  },
  [111800514] = {
    id = 111800514,
    name = "刚才抓的那些人里，难道还有漏网之鱼？！"
  },
  [111800515] = {
    id = 111800515,
    name = "主人放心，城堡已经被封锁了，没有人可以逃出去。"
  },
  [111800516] = {
    id = 111800516,
    name = "加派人手去找，无论如何都要找到柯莱特尔！"
  },
  [111800517] = {
    id = 111800517,
    name = "嘈杂的音乐让你此刻的心情突然变得混乱不堪，你不知道是先思考怎么破局离开幻境，还是先找到柯莱特尔。"
  },
  [111800518] = {
    id = 111800518,
    name = "（明明，一切都是假的啊......为什么还要被她牵动情绪......）"
  },
  [111800519] = {
    id = 111800519,
    name = "【......哥哥！！】"
  },
  [111800520] = {
    id = 111800520,
    name = "你猛然听到了一声哭喊，带着难以言喻的绝望与恐惧。"
  },
  [111800521] = {
    id = 111800521,
    name = "【哥哥，救我啊啊啊！！】"
  },
  [111800522] = {
    id = 111800522,
    name = "【柯莱特尔！你在哪里！】"
  },
  [111800523] = {
    id = 111800523,
    name = "【我不知道，我不知道，这里好黑！！】"
  },
  [111800524] = {
    id = 111800524,
    name = "【哥哥，你在哪里，我好冷，这里好湿，你快来救我啊！！】"
  },
  [111800525] = {
    id = 111800525,
    name = "你霍然冲出了宴会厅，心底却以最温柔的语气，安抚着柯莱特尔。"
  },
  [111800526] = {
    id = 111800526,
    name = "【别怕，我来找你了。】"
  },
  [111800527] = {
    id = 111800527,
    name = "你离开宴会厅时，外面的月亮消失了。夏夜的雨说来便来，毫无预兆地冲刷而下。"
  },
  [111800528] = {
    id = 111800528,
    name = "【啊啊啊！】"
  },
  [111800529] = {
    id = 111800529,
    name = "【妹妹，你怎么了？！】"
  },
  [111800530] = {
    id = 111800530,
    name = "【有什么活的东西，从我身上跑过去了！】"
  },
  [111800531] = {
    id = 111800531,
    name = "你脚步一顿，立刻冷静了下来。"
  },
  [111800532] = {
    id = 111800532,
    name = "【你先别慌，仔细听周围的声音，告诉我。】"
  },
  [111800533] = {
    id = 111800533,
    name = "【我......我什么都听不见，呜呜呜，哥哥，我好害怕......呜呜呜......】"
  },
  [111800534] = {
    id = 111800534,
    name = "柯莱特尔听起来快崩溃了，你仿佛听到了幼兽无力地哀鸣。"
  },
  [111800535] = {
    id = 111800535,
    name = "【别着急，柯莱特尔，你是普尔兰家族的小女儿，不要露出怯懦的姿态让坏人看到嘲笑你，哥哥相信，你会像父亲大人一样勇敢的。来跟我念：我是普尔兰的骄傲与荣耀！】"
  },
  [111800536] = {
    id = 111800536,
    name = "【我......我......我是普尔兰家族的小女儿，我不能露出怯懦的姿态，我要像父亲和哥哥一样勇敢！我是普尔兰的骄傲与荣耀，我是普尔兰的骄傲与荣耀，我是普尔兰的骄傲与荣耀......】"
  },
  [111800537] = {
    id = 111800537,
    name = "柯莱特尔学着跟你念了起来，一开始颤抖的声音逐渐变得平和，而在此期间，你把附近角落的黑暗处都找遍了。"
  },
  [111800538] = {
    id = 111800538,
    name = "【柯莱特尔，还害怕吗？】"
  },
  [111800539] = {
    id = 111800539,
    name = "【不怕了。我是普尔兰的骄傲与荣耀，我不能害怕。】【哥哥，你怎么还没找到我......】"
  },
  [111800540] = {
    id = 111800540,
    name = "【你现在告诉我，你能听到什么吗？】"
  },
  [111800541] = {
    id = 111800541,
    name = "【......很......很奇怪的声音。】"
  },
  [111800542] = {
    id = 111800542,
    name = "【奇怪？关着你的地方很小吗？衣柜？箱子？】"
  },
  [111800543] = {
    id = 111800543,
    name = "【不，我摸不到周围的东西，地上有水。那个声音，一会儿有，一会儿又消失了......啊，出现了！吱吱......呀呀......】"
  },
  [111800544] = {
    id = 111800544,
    name = "（奇怪的声音......水，是地下牢房！）"
  },
  [111800545] = {
    id = 111800545,
    name = "你曾在地下牢房中，也听到过奇怪的声音，那里一片漆黑，你无法判断那是什么东西制造出的声响，只会让人在黑暗中无限恐惧，心里崩塌......"
  },
  [111800546] = {
    id = 111800546,
    name = "脑中闪过那个向你表达忠心的仆人，你回想这次宴会，他并没有和往常一样跟在你身后。"
  },
  [111800547] = {
    id = 111800547,
    name = "【柯莱特尔，我想我知道你在哪儿了，等我。】"
  },
  [111800548] = {
    id = 111800548,
    name = "【真的吗！我在这里乖乖等哥哥！】"
  },
  [111800549] = {
    id = 111800549,
    name = "你在雨中奔跑。城堡很大，就算不停歇地奔跑，现在也还剩三分之二的距离。"
  },
  [111800550] = {
    id = 111800550,
    name = "普尔兰是诺维奇帝国赫赫有名的家族，所属的领地堪比其余贵族的好几倍，难免遭遇嫉妒与不满，这次的刺杀安排不像试探，更像是灭门的手段。"
  },
  [111800551] = {
    id = 111800551,
    name = "【对不起，柯莱特尔，是我疏忽了。】"
  },
  [111800552] = {
    id = 111800552,
    name = "你很清楚，是你轻视了对手。轻视的原因，不是小看对手，而是不太在意。"
  },
  [111800553] = {
    id = 111800553,
    name = "比起如何解决宴会的危机，你更在乎的是，该如何离开这个幻境。你将自己划作了局外的人，虽然更清楚地掌握了整个局势的发展，但也忽略了应该在意的人。"
  },
  [111800554] = {
    id = 111800554,
    name = "【哥哥，你没有错哦，是我没有听你的话，我应该一直牵着你。】"
  },
  [111800555] = {
    id = 111800555,
    name = "【你是怎么到那个地方的？】"
  },
  [111800556] = {
    id = 111800556,
    name = "【我不知道，我醒来就在这里了......】"
  },
  [111800557] = {
    id = 111800557,
    name = "【那苏醒之前呢？】"
  },
  [111800558] = {
    id = 111800558,
    name = "【我喝了你的仆人递给我的一杯草莓汁，后来就什么都不知道了。】"
  },
  [111800559] = {
    id = 111800559,
    name = "（那个仆人果然有问题。）"
  },
  [111800560] = {
    id = 111800560,
    name = "你跑到了一处花园，要绕过很大一圈才能到出口的那方，你毫不犹豫踏上泥泞的草坪，穿过花圃，脆弱的鲜花被碾出一片嫣红。"
  },
  [111800561] = {
    id = 111800561,
    name = "【妹妹，怎么不说话了，你还好吗？】"
  },
  [111800562] = {
    id = 111800562,
    name = "【嗯......还是不说了......我怕把吊坠的魔法力用完，你就找不到我了。】"
  },
  [111800563] = {
    id = 111800563,
    name = "你心中一疼，轻声回答。"
  },
  [111800564] = {
    id = 111800564,
    name = "【不会的。我说过，只要你呼唤我，我就一定能找到你。】"
  },
  [111800565] = {
    id = 111800565,
    name = "【......哥哥，我好冷啊。】"
  },
  [111800566] = {
    id = 111800566,
    name = "【我很快就到了！】"
  },
  [111800567] = {
    id = 111800567,
    name = "你停在了地下牢房的门口。"
  },
  [111800568] = {
    id = 111800568,
    name = "地牢的门口有一口井，现在有好几个人正冒着雨从井里打捞金币。"
  },
  [111800569] = {
    id = 111800569,
    name = "是的，金币。麻绳的一端系着一只沉重的木桶，正缓缓降入井中，发出“吱吱呀呀”的声响。"
  },
  [111800570] = {
    id = 111800570,
    name = "而在旁边指挥的，正是那个发誓永不背叛的仆人。"
  },
  [111800571] = {
    id = 111800571,
    name = "藏得真多啊。"
  },
  [111800572] = {
    id = 111800572,
    name = "大雨浇落到你的脸上，你朝他们露出了一个冰冷的笑。"
  },
  [111800573] = {
    id = 111800573,
    name = "仆人猛然转身，脸色惨白，双腿不由自主地发抖。他试图辩解，但喉咙却像被什么堵住了一般，发不出半点声音。"
  },
  [111800574] = {
    id = 111800574,
    name = "我早就说过，希望女神管不了地狱，你的誓言，我不信的。"
  },
  [111800575] = {
    id = 111800575,
    name = "主人！我没有背叛您！您听我说！诺瓦克的阴谋我没参加，我、我是想为您保住这些财富，才出此下策，我——"
  },
  [111800576] = {
    id = 111800576,
    name = "黑暗中剑光弯月般地掠下，鲜血从仆人咽喉中喷溅而出，他向后摔进泥泞里。"
  },
  [111800577] = {
    id = 111800577,
    name = "我不想听，也没时间听。绑架柯莱特尔的，都是普尔兰的背叛者。"
  },
  [111800578] = {
    id = 111800578,
    name = "背叛者，永远只有一个下场。"
  },
  [111800579] = {
    id = 111800579,
    name = "几乎在同一时间，打捞金币的几个人丢下木桶跑得飞快，你将剑扔了出去，又扔出了几个魔法，很快他们都与泥浆融为了一体。"
  },
  [111800580] = {
    id = 111800580,
    name = "【......哥哥，是你在外面吗？我好像听到你的声音了。】"
  },
  [111800581] = {
    id = 111800581,
    name = "【是我，妹妹。】"
  },
  [111800582] = {
    id = 111800582,
    name = "你踹开了平日里避如蛇蝎的大门，径直朝里走去，在一片漆黑处站定，正想打个聚光的魔法，就听到了柯莱特尔的声音。"
  },
  [111800583] = {
    id = 111800583,
    name = "哥哥！我在这里！"
  },
  [111800584] = {
    id = 111800584,
    name = "也许是门外带来的光亮，柯莱特尔的脚步声匆匆地朝你靠近。"
  },
  [111800585] = {id = 111800585, name = "你——"},
  [111800586] = {
    id = 111800586,
    name = "你被她一把抱住了，胸前一股暖暖的绒毛，如同捡到了被遗弃在荒野的小猫。你心中一暖，彻底放下了心。"
  },
  [111800587] = {
    id = 111800587,
    name = "找到你了。"
  },
  [111800588] = {id = 111800588, name = "......"},
  [111800589] = {
    id = 111800589,
    name = "你牵着柯莱特尔回到了地牢门口，在漫天的雨势里，柯莱特尔发现了躺在雨中的尸体。"
  },
  [111800590] = {
    id = 111800590,
    name = "仆人的身体在泥浆里沉沉浮浮，身边散落着发光的金币。"
  },
  [111800591] = {
    id = 111800591,
    name = "坏人死了吗？"
  },
  [111800592] = {
    id = 111800592,
    name = "嗯，都死了，你安全了，别怕。"
  },
  [111800593] = {
    id = 111800593,
    name = "爸爸妈妈呢？"
  },
  [111800594] = {
    id = 111800594,
    name = "他们都没事，在等你回家。"
  },
  [111800595] = {
    id = 111800595,
    name = "柯莱特尔笑了，你却在她眼中看到了泪花。"
  },
  [111800596] = {
    id = 111800596,
    name = "他们不会死了对不对？"
  },
  [111800597] = {
    id = 111800597,
    name = "当然不会。"
  },
  [111800598] = {
    id = 111800598,
    name = "哥哥也不会死了是不是？"
  },
  [111800599] = {
    id = 111800599,
    name = "我不是好端端在你面前吗？"
  },
  [111800600] = {
    id = 111800600,
    name = "柯莱特尔仍然朝你笑着，眼泪却在不停地滚落。"
  },
  [111800601] = {
    id = 111800601,
    name = "怎么了，柯莱特尔？"
  },
  [111800602] = {
    id = 111800602,
    name = "哥哥，那我们和爸爸妈妈永远在一起好不好？"
  },
  [111800603] = {
    id = 111800603,
    name = "柯莱特尔的身后出现了一道空间裂痕，如同一个密封的箱子，被剑锋扎出了一道天光。"
  },
  [111800604] = {
    id = 111800604,
    name = "......对不起，柯莱特尔。"
  },
  [111800605] = {
    id = 111800605,
    name = "你明白了，这是幻境即将破裂的征兆。"
  },
  [111800606] = {
    id = 111800606,
    name = "（这个家很好。出身高贵，生活优渥，有一个即将将领主交予自己的父亲，还有一个无比聪慧可爱的妹妹，也许很多人的一生追求也不过如此了。）"
  },
  [111800607] = {
    id = 111800607,
    name = "（可我有同伴，有无数的回忆和羁绊，也有属于自己的人生。）"
  },
  [111800608] = {
    id = 111800608,
    name = "（我不是普尔兰少爷，我是探险者，我要回到真实的地方，那才是我的世界。所以......）"
  },
  [111800609] = {
    id = 111800609,
    name = "对不起，柯莱特尔，我该走了。"
  },
  [111800610] = {id = 111800610, name = "哥哥！"},
  [111800611] = {
    id = 111800611,
    name = "柯莱特尔死死地抓住了你的衣角，她睁大眼睛看着你，似乎想把你牢牢记在心中。"
  },
  [111800612] = {
    id = 111800612,
    name = "就在你以为她会说些挽留的话时，她却露出了释然和悲伤的笑容。"
  },
  [111800613] = {
    id = 111800613,
    name = "谢谢你，哥哥。"
  },
  [111800614] = {
    id = 111800614,
    name = "谢谢你，给我的这一场美好的梦。"
  },
  [111800615] = {
    id = 111800615,
    name = "咔嚓——"
  },
  [111800616] = {
    id = 111800616,
    name = "天际仿佛被无形的巨手猛然撕开，光明与黑暗在这一刻变得扭曲而狂乱。"
  },
  [111800617] = {
    id = 111800617,
    name = "四周的空间崩塌成碎片，如琉璃般下落，在虚空中旋转、消散，直到世间只剩下了你与柯莱特尔......"
  },
  [111800618] = {
    id = 111800618,
    name = "然后，你睁开了眼睛。"
  },
  [111800619] = {
    id = 111800619,
    name = "醒了，醒了！"
  },
  [111800620] = {
    id = 111800620,
    name = "老师，你怎么样？还好吗？"
  },
  [111800621] = {
    id = 111800621,
    name = "他怎么没反应？那个药是不是有什么后遗症？"
  },
  [111800622] = {
    id = 111800622,
    name = "不可能，我老师的药剂，绝对不可能出问题！"
  },
  [111800623] = {
    id = 111800623,
    name = "我们当然相信维克多大师，只是怕在计量上......"
  },
  [111800624] = {
    id = 111800624,
    name = "你一睁眼就看着四五个脑袋围着你转，左一句右一句的吵闹，本来头疼的脑子更晕了，索性又闭上了眼。"
  },
  [111800625] = {
    id = 111800625,
    name = "哎呀！队长又晕过去了！"
  },
  [111800626] = {
    id = 111800626,
    name = "都说了等等，你们着什么急！我先去看看吉米啊，有事再叫我。"
  },
  [111800627] = {
    id = 111800627,
    name = "你闭着眼听见弗拉克的声音，年纪并不大，脚步也很轻快。"
  },
  [111800628] = {
    id = 111800628,
    name = "鬼侍小姐，你醒了。"
  },
  [111800629] = {id = 111800629, name = "嗯。"},
  [111800630] = {
    id = 111800630,
    name = "有没有哪里不舒服？"
  },
  [111800631] = {id = 111800631, name = "没有。"},
  [111800632] = {
    id = 111800632,
    name = "听着冷淡地回答，你忽然想起，失去意识前，你还将她护在了身下。"
  },
  [111800633] = {
    id = 111800633,
    name = "（怎么，她也晕过去了吗？听声音，其他人好像没事......）"
  },
  [111800634] = {
    id = 111800634,
    name = "你嘴里蔓延着微苦的药剂，很快头疼也逐渐缓解了过来，你重新睁开了眼睛。"
  },
  [111800635] = {
    id = 111800635,
    name = "这次看起来正常了。"
  },
  [111800636] = {
    id = 111800636,
    name = "老师，太好了，你没事。"
  },
  [111800637] = {
    id = 111800637,
    name = "这里是？"
  },
  [111800638] = {
    id = 111800638,
    name = "我们在维克多大师的家里，不过他不在，是他的弟子弗拉克救了你和鬼侍。"
  },
  [111800639] = {
    id = 111800639,
    name = "还记得吗，来我们学院给我们上过课的维克多大师。"
  },
  [111800640] = {
    id = 111800640,
    name = "嗯，是那个有名的技术大师。"
  },
  [111800641] = {
    id = 111800641,
    name = "当时菲尼斯提出建议的时候，我还纳闷呢，你和鬼侍看起来是中了某个魔法诅咒，怎么不去找言灵，反而来找技术大师。"
  },
  [111800642] = {
    id = 111800642,
    name = "是我们孤陋寡闻了，没想到维克多的弟子真的有解开诅咒的能力。"
  },
  [111800643] = {
    id = 111800643,
    name = "老师，你现在感觉怎么样？"
  },
  [111800644] = {
    id = 111800644,
    name = "放心，已经没事了。"
  },
  [111800645] = {
    id = 111800645,
    name = "你朝众人笑笑，忽然察觉到了一道目光。看过去的时候，鬼侍僵滞了一下，只是一瞬，她就移开了眼。"
  },
  [111800646] = {
    id = 111800646,
    name = "啊，都醒了啊。"
  },
  [111800647] = {
    id = 111800647,
    name = "房间里走出了一个少年，看起来只有十三四岁，他手上拿着一只不停挥动翅膀的金属球。"
  },
  [111800648] = {
    id = 111800648,
    name = "你是......维克多大师的弟子？"
  },
  [111800649] = {
    id = 111800649,
    name = "没错，我叫弗拉克！别总叫我维克多的弟子，我还没有师父那么厉害。呜哇，吉米！"
  },
  [111800650] = {
    id = 111800650,
    name = "他手里的金属球突然挣脱，又被他抓了回去。"
  },
  [111800651] = {
    id = 111800651,
    name = "这是我自己做的魔偶，它叫吉米！来，吉米，给他们打个招呼！"
  },
  [111800652] = {
    id = 111800652,
    name = "“喀嚓”金属球的翅膀停下来，缩成了一团，一动不动。"
  },
  [111800653] = {
    id = 111800653,
    name = "别害羞，他们是给你找材料的人，喂！"
  },
  [111800654] = {
    id = 111800654,
    name = "金属球一头冲向里面的房间，弗拉克挪脚踩了踩脚边的机关，“嘭！”大门关闭，吉米整个球撞在门上，在地上滚了两圈，不动了。"
  },
  [111800655] = {
    id = 111800655,
    name = "不好意思，吉米有点怕生。"
  },
  [111800656] = {id = 111800656, name = "......"},
  [111800657] = {
    id = 111800657,
    name = "呃呵呵呵。"
  },
  [111800658] = {
    id = 111800658,
    name = "（小声）安妮斯，你说他脑子是不是有点问题，把魔偶当人呢。"
  },
  [111800659] = {id = 111800659, name = "......"},
  [111800660] = {
    id = 111800660,
    name = "你是傀儡师？"
  },
  [111800661] = {
    id = 111800661,
    name = "操控魔偶的言灵通常被称之为傀儡师，但你看那个奇奇怪怪的金属球，怎么也不是正常魔偶的样子。"
  },
  [111800662] = {
    id = 111800662,
    name = "嗯，虽然大家都这么说，但我不认为吉米是我的傀儡魔偶，它可是我最好的朋友！"
  },
  [111800663] = {
    id = 111800663,
    name = "说起来这次多亏了弗拉克，是他用维克多大师的药剂救了队长，所以，我们也替队长答应了他的合理要求。"
  },
  [111800664] = {
    id = 111800664,
    name = "你瞧着似笑非笑的菲尼斯，总觉得有个坑在等着你跳，决定转移话题。"
  },
  [111800665] = {
    id = 111800665,
    name = "这样啊，那真的很谢谢你们。"
  },
  [111800666] = {
    id = 111800666,
    name = "说起来，我们找到的那面镜子呢？"
  },
  [111800667] = {
    id = 111800667,
    name = "碎掉啦，在你们昏迷之后，我们收起来去检验过，什么都看不出来。"
  },
  [111800668] = {
    id = 111800668,
    name = "那个宝物里的力量，应该在破碎的时候进入到你们的身体里了，才产生了副作用让你们昏迷不醒。"
  },
  [111800669] = {
    id = 111800669,
    name = "由于我擅长的是机械不是魔法，只能通过解析血液找到解决的办法，还好师父给我留了解除特殊魔法的药剂，算你们运气不错。"
  },
  [111800670] = {
    id = 111800670,
    name = "哦，对了，你们昏迷之后，有没有看到什么东西？比如说梦境，幻境，或者一系列不真实的场景？"
  },
  [111800671] = {
    id = 111800671,
    name = "鬼侍不自然地皱了皱眉，你感觉到她全身绷紧了，似乎十分紧张。"
  },
  [111800672] = {
    id = 111800672,
    name = "梦境？幻境？问这个做什么？"
  },
  [111800673] = {
    id = 111800673,
    name = "我好做研究啊！这是我一直思考的问题，如果能把对应的魔法和结构动力用在吉米身上，它也许有一天就能跟我好好交流了啊！"
  },
  [111800674] = {id = 111800674, name = "......"},
  [111800675] = {
    id = 111800675,
    name = "你突然有些赞同尼莉莎的话，年纪小小的，怎么脑子就出了问题。"
  },
  [111800676] = {
    id = 111800676,
    name = "不过你这么回答，那就是见到了，快跟我说说，你看到了什么？"
  },
  [111800677] = {
    id = 111800677,
    name = "唔，只是一场美梦而已。"
  },
  [111800678] = {
    id = 111800678,
    name = "你不愿多说，那不是你的人生。但此刻回想起来，却仍然觉得温暖。"
  },
  [111800679] = {
    id = 111800679,
    name = "那你呢，你有看到什么吗？"
  },
  [111800680] = {
    id = 111800680,
    name = "弗拉克看向了你旁边的鬼侍。"
  },
  [111800681] = {
    id = 111800681,
    name = "......我不记得了。"
  },
  [111800682] = {
    id = 111800682,
    name = "鬼侍起身就要离开，另一处机关被弗拉克随手一拉，大门“嘭”地关上了。"
  },
  [111800683] = {
    id = 111800683,
    name = "在交易没完成之前，你们不能走哦。"
  },
  [111800684] = {
    id = 111800684,
    name = "（小声）菲尼斯，你答应了他什么？"
  },
  [111800685] = {
    id = 111800685,
    name = "队长，原谅我病急乱投医，但至少你醒了不是吗？"
  },
  [111800686] = {
    id = 111800686,
    name = "你要我做什么？"
  },
  [111800687] = {
    id = 111800687,
    name = "你的朋友说，只要能把你们救醒，就答应我的要求。"
  },
  [111800688] = {
    id = 111800688,
    name = "现在，是承诺应该兑现的时候啦。"
  },
  [111800689] = {
    id = 111800689,
    name = "我的要求很简单：帮我找到升级吉米的材料，我这里有一份材料单。对你们几个来说，应该很容易吧？"
  },
  [111800690] = {
    id = 111800690,
    name = "（嗯，这不算什么难事。）"
  },
  [111800691] = {
    id = 111800691,
    name = "鬼侍扫了一眼单子上的材料，立刻朝门口走去。"
  },
  [111800692] = {
    id = 111800692,
    name = "喂喂，你不是打算白喝了我师父的药剂吧？"
  },
  [111800693] = {
    id = 111800693,
    name = "不是说要找材料吗？"
  },
  [111800694] = {
    id = 111800694,
    name = "鬼侍侧过脸，似乎竭力隐藏了某种情绪，淡然地看向了你。"
  },
  [111800695] = {
    id = 111800695,
    name = "那就一起吧。"
  }
}
